INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.001', 'Iri are i qui bi i je ratit u mam i Jesucrísto chi umuk uxiquin i ujer mam David xak i ujer mam Abraham.
', '', '&lt;h2&gt;I ʼUTZ LAJ TZIJ CHIRIJ I JESUCRISTO U TZʼIBAM&lt;/h2&gt;&lt;h1&gt;I SAN MATÉO&lt;/h1&gt;&lt;h2&gt;I qui bi i je ratit u mam i Jesucrísto&lt;/h2&gt;&lt;span class="verse" id="Matt_1_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Iri are i qui bi i je ratit u mam i Jesucrísto chi umuk uxiquin i ujer mam David xak i ujer mam Abraham.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.002', 'I mam Abraham-le are u kajaw i mam Isaac; xoʼon panok, i mam Isaac-le are u kajaw i mam Jacob; teʼuri i mam Jacob-le are i qui kajaw i mam Juda cuʼ i ratz u chaʼ. ', '', '&lt;span class="verse" id="Matt_1_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I mam Abraham-le are u kajaw i mam Isaac; xoʼon panok, i mam Isaac-le are u kajaw i mam Jacob; teʼuri i mam Jacob-le are i qui kajaw i mam Juda cuʼ i ratz u chaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.003', 'Teʼuri i mam Juda are qui kajaw i mam Fáres xak i mam Zar, i qui chuch are i ati Támar. Xoʼon panok, i mam Fáres are u kajaw i mam Esrom, teʼuri i mam Esrom are u kajaw i mam Aram. ', '', '&lt;span class="verse" id="Matt_1_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Teʼuri i mam Juda are qui kajaw i mam Fáres xak i mam Zar, i qui chuch are i ati Támar. Xoʼon panok, i mam Fáres are u kajaw i mam Esrom, teʼuri i mam Esrom are u kajaw i mam Aram. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.004', 'Xoʼon panok, i mam Aram are u kajaw i mam Aminadab; y teʼuri i mam Aminadab are u kajaw i mam Naason; i mam Naason-le are u kajaw i mam Salmon. ', '', '&lt;span class="verse" id="Matt_1_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i mam Aram are u kajaw i mam Aminadab; y teʼuri i mam Aminadab are u kajaw i mam Naason; i mam Naason-le are u kajaw i mam Salmon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.005', 'Xoʼon panok, i mam Salmon are u kajaw i mam Booz, y u chuch are i ati Rahab. Teʼuri i mam Booz are u kajaw i mam Obed, y u chuch are i ati Rut. Teʼuri i mam Obed-le are u kajaw i mam Isai. ', '', '&lt;span class="verse" id="Matt_1_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i mam Salmon are u kajaw i mam Booz, y u chuch are i ati Rahab. Teʼuri i mam Booz are u kajaw i mam Obed, y u chuch are i ati Rut. Teʼuri i mam Obed-le are u kajaw i mam Isai. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.006', 'Y xoʼon panok, i mam Isai are u kajaw i mam Rey David. I mam David-le are u kajaw i mam Salomon, i u chuch i mam Salomon are i rixokil can i ʼetz mam Urías.
', '', '&lt;span class="verse" id="Matt_1_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Y xoʼon panok, i mam Isai are u kajaw i mam Rey David. I mam David-le are u kajaw i mam Salomon, i u chuch i mam Salomon are i rixokil can i ʼetz mam Urías.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.007', 'Xoʼon panok, i mam Salomon-le are u kajaw i mam Roboam; teʼuri i mam Roboam are u kajaw i mam Abías; i mam Abías-le are u kajaw i mam Asa. ', '', '&lt;span class="verse" id="Matt_1_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i mam Salomon-le are u kajaw i mam Roboam; teʼuri i mam Roboam are u kajaw i mam Abías; i mam Abías-le are u kajaw i mam Asa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.008', 'Xoʼon panok, i mam Asa are u kajaw i mam Josafat, teʼuri i mam Josafat are u kajaw i mam Joram; i mam Joram are u kajaw i mam Uzías. ', '', '&lt;span class="verse" id="Matt_1_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i mam Asa are u kajaw i mam Josafat, teʼuri i mam Josafat are u kajaw i mam Joram; i mam Joram are u kajaw i mam Uzías. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.009', 'Teʼuri i mam Uzías are u kajaw i mam Jotam; i mam Jotam are u kajaw i mam Acaz; i mam Acaz-le are u kajaw i mam Ezequías. ', '', '&lt;span class="verse" id="Matt_1_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Teʼuri i mam Uzías are u kajaw i mam Jotam; i mam Jotam are u kajaw i mam Acaz; i mam Acaz-le are u kajaw i mam Ezequías. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.010', 'I mam Ezequías are u kajaw i mam Manases, i mam Manases are u kajaw i mam Amon; i mam Amon are u kajaw i mam Josías. ', '', '&lt;span class="verse" id="Matt_1_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I mam Ezequías are u kajaw i mam Manases, i mam Manases are u kajaw i mam Amon; i mam Amon are u kajaw i mam Josías. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.011', 'I mam Josías are qui kajaw i mam Jeconías cuʼ i ratz u chaʼ. Are je cʼas ique, are ʼuri xe chap i aj Israel winak y xe cʼam bi pa tinimit Babilónia.
', '', '&lt;span class="verse" id="Matt_1_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I mam Josías are qui kajaw i mam Jeconías cuʼ i ratz u chaʼ. Are je cʼas ique, are ʼuri xe chap i aj Israel winak y xe cʼam bi pa tinimit Babilónia.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.012', 'Teʼuri, xoʼon panok i mam Jeconías are u kajaw i mam Salatiel; teʼuri i mam Salatiel are u kajaw i mam Zorobabel. ', '', '&lt;span class="verse" id="Matt_1_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri, xoʼon panok i mam Jeconías are u kajaw i mam Salatiel; teʼuri i mam Salatiel are u kajaw i mam Zorobabel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.013', 'I mam Zorobabel are u kajaw i mam Abiud; i mam Abiud are u kajaw i mam Eliaquim; i mam Eliaquim are u kajaw i mam Azor; ', '', '&lt;span class="verse" id="Matt_1_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I mam Zorobabel are u kajaw i mam Abiud; i mam Abiud are u kajaw i mam Eliaquim; i mam Eliaquim are u kajaw i mam Azor; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.014', 'i mam Azor are u kajaw i mam Sadoc; i mam Sadoc are u kajaw i mam Aquim; i mam Aquim are u kajaw i mam Eliud. ', '', '&lt;span class="verse" id="Matt_1_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;i mam Azor are u kajaw i mam Sadoc; i mam Sadoc are u kajaw i mam Aquim; i mam Aquim are u kajaw i mam Eliud. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.015', 'I mam Eliud are u kajaw i mam Eleázar; i mam Eleázar are u kajaw i mam Matan; i mam Matan are u kajaw i mam Jacob. ', '', '&lt;span class="verse" id="Matt_1_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I mam Eliud are u kajaw i mam Eleázar; i mam Eleázar are u kajaw i mam Matan; i mam Matan are u kajaw i mam Jacob. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.016', 'I mam Jacob are u kajaw i mam Jose; i mam Jose-le are i rachijil i al-Mariy. I al-Mariy are u chuch i Jesus chi Tolke.
', '', '&lt;span class="verse" id="Matt_1_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I mam Jacob are u kajaw i mam Jose; i mam Jose-le are i rachijil i al-Mariy. I al-Mariy are u chuch i Jesus chi Tolke.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.017', 'Chi cakil wi je cajlajuj lemaj chi qui kajawim re can i mam Abraham cʼa ruʼ i mam David; teʼuri xak je cajlajuj tan chic lemaj chi qui kajawim tan chic chi rijil i mam David cʼa xe cʼam bi i aj Israel winak pa tinimit Babilónia. Xak je cajlajuj lemaj tan chic chi rijil i qui ʼenam i aj Israel winak pa Babilónia cʼa xu rik i ʼij chi xalax i Crísto chi Tolke.
', '', '&lt;span class="verse" id="Matt_1_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Chi cakil wi je cajlajuj lemaj chi qui kajawim re can i mam Abraham cʼa ruʼ i mam David; teʼuri xak je cajlajuj tan chic lemaj chi qui kajawim tan chic chi rijil i mam David cʼa xe cʼam bi i aj Israel winak pa tinimit Babilónia. Xak je cajlajuj lemaj tan chic chi rijil i qui ʼenam i aj Israel winak pa Babilónia cʼa xu rik i ʼij chi xalax i Crísto chi Tolke.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.018', 'I ralaxbal i Jesucrísto, queje iri xoʼono: i al-Mariy, chi u chuch, ya u bim chic che i ma Jose chi que cʼuliʼic. Xui-ri, are maja cacucʼaj quib, ire iwab winak chic rumal u choʼab u Tewal i Dios chi petnak puwi. ', '', '&lt;h2&gt;Wach i ralaxbal i Jesucrísto&lt;/h2&gt;&lt;span class="verse" id="Matt_1_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I ralaxbal i Jesucrísto, queje iri xoʼono: i al-Mariy, chi u chuch, ya u bim chic che i ma Jose chi que cʼuliʼic. Xui-ri, are maja cacucʼaj quib, ire iwab winak chic rumal u choʼab u Tewal i Dios chi petnak puwi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.019', 'I ma Jose xu bij pu cʼux chi xa quiqui jach quib ruʼ i al-Mariy aliʼal. ʼUtz laj winak ire, n-craj ta cu ya u qʼuix che chiquiwach conojel i winak. ', '', '&lt;span class="verse" id="Matt_1_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I ma Jose xu bij pu cʼux chi xa quiqui jach quib ruʼ i al-Mariy aliʼal. ʼUtz laj winak ire, n-craj ta cu ya u qʼuix che chiquiwach conojel i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.020', 'Are ca tijin chu chʼobic ile, jun ángel re i Kajwal xupon ruʼ. I ma Jose xrachicʼaj pu waram chi xu bij i ángel che:
—A-Jose, chi at umuk uxiquin can i mam David, ma xij awib che i al-Mariy; cha cʼulaj na awuʼ, lic are awixokil, man i acʼal chi calax na ruʼ, i rajaw are i u Tewal i Dios. ', '', '&lt;span class="verse" id="Matt_1_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are ca tijin chu chʼobic ile, jun ángel re i Kajwal xupon ruʼ. I ma Jose xrachicʼaj pu waram chi xu bij i ángel che:&lt;p&gt; &lt;/p&gt;—A-Jose, chi at umuk uxiquin can i mam David, ma xij awib che i al-Mariy; cha cʼulaj na awuʼ, lic are awixokil, man i acʼal chi calax na ruʼ, i rajaw are i u Tewal i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.021', 'I acʼal-le chi calax na, are jun ala. Cha ya pu bi A-JESUS. Queje ni u bi ile, man que resaj u winak chupam i qui mac. (Queje ile xu bij i ángel che.)
', '', '&lt;span class="verse" id="Matt_1_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I acʼal-le chi calax na, are jun ala. Cha ya pu bi A-JESUS. Queje ni u bi ile, man que resaj u winak chupam i qui mac. (Queje ile xu bij i ángel che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.022', 'Queje xe icʼaw wi ique ile; y xeʼelok pacha u bim can i Kajwal ujer. Ire u bim che jun ajbil u tzij:
', '', '&lt;span class="verse" id="Matt_1_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Queje xe icʼaw wi ique ile; y xeʼelok pacha u bim can i Kajwal ujer. Ire u bim che jun ajbil u tzij:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.023', 'I apoj ali ca qʼuiji ni yobil chirij y calax ni jun ala ruʼ;
i ala-le, Emanuel ca bix che.
Queje i tzij ile xu tzʼibaj i ajbil u tzij i Dios ujer. I “Emanuel” queʼelok: Dios ʼo kuʼ.
', '', '&lt;span class="verse" id="Matt_1_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I apoj ali ca qʼuiji ni yobil chirij y calax ni jun ala ruʼ;
i ala-le, Emanuel ca bix che.
Queje i tzij ile xu tzʼibaj i ajbil u tzij i Dios ujer. I “Emanuel” queʼelok: Dios ʼo kuʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.024', 'I ma Jose, are xcʼun sak chuwach, ʼis xoʼon pacha xbix che rumal i ángel chi taktal li ruʼ rumal i Kajwal Dios; xrucʼaj i al-Mariy chi lic rixokil. ', '', '&lt;span class="verse" id="Matt_1_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I ma Jose, are xcʼun sak chuwach, ʼis xoʼon pacha xbix che rumal i ángel chi taktal li ruʼ rumal i Kajwal Dios; xrucʼaj i al-Mariy chi lic rixokil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '1.025', 'Xui-ri, ire toʼ xu chajij i al-Mariy, n-xqʼuiji ta tan ruʼ, cʼa xqʼuiji ni ral acʼal; y xu ya pu bi A-JESUS.
', '', '&lt;span class="verse" id="Matt_1_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Xui-ri, ire toʼ xu chajij i al-Mariy, n-xqʼuiji ta tan ruʼ, cʼa xqʼuiji ni ral acʼal; y xu ya pu bi A-JESUS.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.001', 'Are xalax i Jesus pa tinimit Belen re i jyub Judéa, i ʼatol tzij are i mam Heródes. I mam-le ʼo pu patan pa tinimit Jerusalen; je ʼo juban winak je aj ʼij, je petnak cʼa naj pa oriénte. ', '', '&lt;h2&gt;I nimak tak aj ʼij que upon ruʼ i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_2_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xalax i Jesus pa tinimit Belen re i jyub Judéa, i ʼatol tzij are i mam Heródes. I mam-le ʼo pu patan pa tinimit Jerusalen; je ʼo juban winak je aj ʼij, je petnak cʼa naj pa oriénte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.002', 'Are xe upon pa Jerusalen, xqui tzʼonoj chique i winak:
—¿Paʼowi i nim laj ʼatol tzij que i aj Israel winak chi xalaxic? Ya xkil i u chʼumil are oj ʼo chila pa oriénte, rumal-i xoj cʼun chu cojic u ʼij —xqui bij.
', '', '&lt;span class="verse" id="Matt_2_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are xe upon pa Jerusalen, xqui tzʼonoj chique i winak:&lt;p&gt; &lt;/p&gt;—¿Paʼowi i nim laj ʼatol tzij que i aj Israel winak chi xalaxic? Ya xkil i u chʼumil are oj ʼo chila pa oriénte, rumal-i xoj cʼun chu cojic u ʼij —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.003', 'I nim laj ʼatol tzij, mam Heródes, are xu ta rason ile, lic xcʼachir rumal; xak conojel i winak pa tinimit Jerusalen xe cʼachir rumal. ', '', '&lt;span class="verse" id="Matt_2_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I nim laj ʼatol tzij, mam Heródes, are xu ta rason ile, lic xcʼachir rumal; xak conojel i winak pa tinimit Jerusalen xe cʼachir rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.004', 'Teʼuri i ʼatol tzij xe u siqʼuij i nimak tak sacerdóte que i aj Israel, tak i tijonel re i tzij tzʼibtal can rumal i mam Moises ujer. Are xe moltajic, xu tzʼonoj chique pa calax na wi i Crísto chi tolque coyʼem. ', '', '&lt;span class="verse" id="Matt_2_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼatol tzij xe u siqʼuij i nimak tak sacerdóte que i aj Israel, tak i tijonel re i tzij tzʼibtal can rumal i mam Moises ujer. Are xe moltajic, xu tzʼonoj chique pa calax na wi i Crísto chi tolque coyʼem. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.005', 'Ique xqui bij ʼuri che:
—Calax pa tinimit Belen, u cwenta i jyub Judéa. Queje ile queʼelok, man queje ile tzʼibtal can rumal i ajbil u tzij i Dios ujer. Queje iri xu bij:
', '', '&lt;span class="verse" id="Matt_2_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Ique xqui bij ʼuri che:&lt;p&gt; &lt;/p&gt;—Calax pa tinimit Belen, u cwenta i jyub Judéa. Queje ile queʼelok, man queje ile tzʼibtal can rumal i ajbil u tzij i Dios ujer. Queje iri xu bij:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.006', 'Yet chi at tinimit Belen,
at u cwenta i jyub Juda,
ma chʼobo mok n-ti a ʼij yet;
ʼo ni a ʼij man chiquixol awacʼal yet
queʼelok ʼo ni jun qui cʼambeal;
cucʼaw ni u tzam chique in winak, aj Israel.
Queje ile tzʼibtal canok —xqui bij i mamʼib.
', '', '&lt;span class="verse" id="Matt_2_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Yet chi at tinimit Belen,
at u cwenta i jyub Juda,
ma chʼobo mok n-ti a ʼij yet;
ʼo ni a ʼij man chiquixol awacʼal yet
queʼelok ʼo ni jun qui cʼambeal;
cucʼaw ni u tzam chique in winak, aj Israel.
Queje ile tzʼibtal canok —xqui bij i mamʼib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.007', 'Teʼuri i mam Heródes aliʼal xe u siqʼuij i nimak tak aj ʼij chi je petnak pa oriénte y xu tzʼonoj chique wach xoʼon i icʼ, wach xoʼon i ʼij are xwinakir i chʼumil. ', '', '&lt;span class="verse" id="Matt_2_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Teʼuri i mam Heródes aliʼal xe u siqʼuij i nimak tak aj ʼij chi je petnak pa oriénte y xu tzʼonoj chique wach xoʼon i icʼ, wach xoʼon i ʼij are xwinakir i chʼumil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.008', 'Teʼuri, xe u takbi pa Belen. Xu bij chique:
—Jix chu tzucuxic i ral acʼal-le; naʼal chi tzucuj. We xi riko, col i bij chwe, man xak yin quin ʼe chu cojic u ʼij —xu bij i mam Heródes.
', '', '&lt;span class="verse" id="Matt_2_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Teʼuri, xe u takbi pa Belen. Xu bij chique:&lt;p&gt; &lt;/p&gt;—Jix chu tzucuxic i ral acʼal-le; naʼal chi tzucuj. We xi riko, col i bij chwe, man xak yin quin ʼe chu cojic u ʼij —xu bij i mam Heródes.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.009', 'I nimak tak aj ʼij, are xutzin u bixquil ile chique, xqui maj chubi qui be. I chʼumil chi xnabeaj chiquiwach cʼa pa oriénte, xbin chic chiquiwach cʼa xupon pa yatal wi i ral acʼal. Chila xtaqʼui na puwi i ja paʼowi i Jesus. ', '', '&lt;span class="verse" id="Matt_2_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I nimak tak aj ʼij, are xutzin u bixquil ile chique, xqui maj chubi qui be. I chʼumil chi xnabeaj chiquiwach cʼa pa oriénte, xbin chic chiquiwach cʼa xupon pa yatal wi i ral acʼal. Chila xtaqʼui na puwi i ja paʼowi i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.010', 'Are xquil i nimak tak aj ʼij chi xtaqʼui i chʼumil, lic xe quicotic. ', '', '&lt;span class="verse" id="Matt_2_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are xquil i nimak tak aj ʼij chi xtaqʼui i chʼumil, lic xe quicotic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.011', 'Y xe ʼoc ruʼ ja, xqui rik i acʼal ruʼ u chuch, ixok Mariy, xe xuquiʼic, xqui coj u ʼij i acʼal. Teʼuri, xqui tor i quekan, xquesaj li qui sipon chi cucʼam y xqui ya che. Ile are óro, xak inciénso, xak i mírra. ', '', '&lt;span class="verse" id="Matt_2_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Y xe ʼoc ruʼ ja, xqui rik i acʼal ruʼ u chuch, ixok Mariy, xe xuquiʼic, xqui coj u ʼij i acʼal. Teʼuri, xqui tor i quekan, xquesaj li qui sipon chi cucʼam y xqui ya che. Ile are óro, xak inciénso, xak i mírra. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.012', 'Are xe tzalij chic pa qui tinimit, junwi chic be xqui majo man xbix chique rumal i Dios piqui waram chi me tzalij chic ruʼ i mam Heródes.
', '', '&lt;span class="verse" id="Matt_2_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Are xe tzalij chic pa qui tinimit, junwi chic be xqui majo man xbix chique rumal i Dios piqui waram chi me tzalij chic ruʼ i mam Heródes.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.013', 'Je ʼenak chic i nimak tak aj ʼij, teʼuri jun ángel re i Kajwal xwinakir pu waram i ma Jose. Xu bij che:
—Chat walijok, cha cʼama bi awacʼal, xak i u chuch, y chat ʼanmajok, jat cʼa Egípto. Chat ʼol chila cʼa quin bij na chawe umpa cat petic, man i mam Heródes cu tzucuj ni acʼal, craj cu camsaj —xu bij i ángel che.
', '', '&lt;h2&gt;I qui camsaxic i acʼalab rumal i mam Heródes&lt;/h2&gt;&lt;span class="verse" id="Matt_2_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Je ʼenak chic i nimak tak aj ʼij, teʼuri jun ángel re i Kajwal xwinakir pu waram i ma Jose. Xu bij che:&lt;p&gt; &lt;/p&gt;—Chat walijok, cha cʼama bi awacʼal, xak i u chuch, y chat ʼanmajok, jat cʼa Egípto. Chat ʼol chila cʼa quin bij na chawe umpa cat petic, man i mam Heródes cu tzucuj ni acʼal, craj cu camsaj —xu bij i ángel che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.014', 'I ma Jose xwalijic, xu cʼambi i acʼal, xak i u chuch, y xqui majbi qui be chaʼab, xe ʼe cʼa pa Egípto. ', '', '&lt;span class="verse" id="Matt_2_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I ma Jose xwalijic, xu cʼambi i acʼal, xak i u chuch, y xqui majbi qui be chaʼab, xe ʼe cʼa pa Egípto. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.015', 'Chila xe qʼuiji wi cʼa are xcam i mam Heródes. Queje ile xeʼelok xak jun ruʼ pacha u bim can i Kajwal; man i ajbil u tzij i Dios u tzʼibam can i tzij-i chi u bim i Kajwal: “Pa Egípto quin siqʼuij wi in Cʼojol.” (Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Matt_2_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Chila xe qʼuiji wi cʼa are xcam i mam Heródes. Queje ile xeʼelok xak jun ruʼ pacha u bim can i Kajwal; man i ajbil u tzij i Dios u tzʼibam can i tzij-i chi u bim i Kajwal: “Pa Egípto quin siqʼuij wi in Cʼojol.” (Queje ile tzʼibtal canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.016', 'I mam Heródes, are xu nabej chi xtʼor can cumal i nimak tak aj ʼij, lic xpe roywal che. Rumal ʼuri, xu tak qui camsaxic conojel i ral tak alabom we maja quiqui chap oxib junab, man qui bim che chi colo ʼo chic queb junab che u winakiric i chʼumil. Queje xu tak u ʼanic ile pa tinimit Belen, xak ronojel i jyub chirij chuwach. ', '', '&lt;span class="verse" id="Matt_2_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I mam Heródes, are xu nabej chi xtʼor can cumal i nimak tak aj ʼij, lic xpe roywal che. Rumal ʼuri, xu tak qui camsaxic conojel i ral tak alabom we maja quiqui chap oxib junab, man qui bim che chi colo ʼo chic queb junab che u winakiric i chʼumil. Queje xu tak u ʼanic ile pa tinimit Belen, xak ronojel i jyub chirij chuwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.017', 'Queje ile xeʼelok pacha xu bij i tzij tzʼibtal can rumal i mam Jeremías, i ajbil u tzij i Dios ujer. Xu bij ire:
', '', '&lt;span class="verse" id="Matt_2_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Queje ile xeʼelok pacha xu bij i tzij tzʼibtal can rumal i mam Jeremías, i ajbil u tzij i Dios ujer. Xu bij ire:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.018', 'Ca tataj jun nim laj oʼej pa tinimit Rama,
Are i ixokib aj Israel que siqʼuinic, que oʼic,
Man i cacʼal, ya xe camic, ya n-ta chic;
Lic n-ca pactaj ti qui bis rumal.
(Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Matt_2_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Ca tataj jun nim laj oʼej pa tinimit Rama,
Are i ixokib aj Israel que siqʼuinic, que oʼic,
Man i cacʼal, ya xe camic, ya n-ta chic;
Lic n-ca pactaj ti qui bis rumal.
(Queje ile tzʼibtal canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.019', 'Xoʼon panok xcam i mam Heródes. Teʼuri, chila pa Egípto, jun ángel re i Kajwal xwinakir ruʼ i ma Jose pu waram. Xu bij che:
', '', '&lt;span class="verse" id="Matt_2_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xoʼon panok xcam i mam Heródes. Teʼuri, chila pa Egípto, jun ángel re i Kajwal xwinakir ruʼ i ma Jose pu waram. Xu bij che:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.020', '—Chat walijok, cha cʼama bi acʼal y u chuch, chix tzalij che i jyub Israel; man ya xe cam ique chi cacaj u camsaxic i acʼal. (Queje ile xu bij i ángel che.)
', '', '&lt;span class="verse" id="Matt_2_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;—Chat walijok, cha cʼama bi acʼal y u chuch, chix tzalij che i jyub Israel; man ya xe cam ique chi cacaj u camsaxic i acʼal. (Queje ile xu bij i ángel che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.021', 'Teʼuri i ma Jose xwalijic, xu cʼam bi racʼal, i rixokil y xe tzalij che i jyub Israel. ', '', '&lt;span class="verse" id="Matt_2_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Jose xwalijic, xu cʼam bi racʼal, i rixokil y xe tzalij che i jyub Israel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.022', 'Are que tijin chi be, i ma Jose xu ta rason chi u qʼuexel i mam Heródes are i u cʼojol, ma Arqueláo u bi. Ire xu xij rib che, n-xraj ta xa ʼe jeki pa jyub Judéa rumal. Teʼuri xbix chic che rumal i Dios pu waram wach i cu ʼano; rumal-i, xa ʼe pa jyub Galiléa. ', '', '&lt;span class="verse" id="Matt_2_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are que tijin chi be, i ma Jose xu ta rason chi u qʼuexel i mam Heródes are i u cʼojol, ma Arqueláo u bi. Ire xu xij rib che, n-xraj ta xa ʼe jeki pa jyub Judéa rumal. Teʼuri xbix chic che rumal i Dios pu waram wach i cu ʼano; rumal-i, xa ʼe pa jyub Galiléa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '2.023', 'Are xe upon chila, xa ʼe jeki pa tinimit Nazaret. Queje ile xeʼelok, pacha qui bim i ajbil u tzij i Dios ujer, chi Jesus “aj Nazaret” ca bix na che.
', '', '&lt;span class="verse" id="Matt_2_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are xe upon chila, xa ʼe jeki pa tinimit Nazaret. Queje ile xeʼelok, pacha qui bim i ajbil u tzij i Dios ujer, chi Jesus “aj Nazaret” ca bix na che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.001', 'Xoʼon panok, i ma Wan, aj kajsanel ya, xupon che i chakij jyub tzʼinilic, chila pa jyub Judéa. Are xuponic, xoc chu bixquil u tzij i Dios chique i winak chi xe moltaj ruʼ.
', '', '&lt;h2&gt;I ma Wan, aj kajsanel ya, coc chi chac&lt;/h2&gt;&lt;span class="verse" id="Matt_3_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i ma Wan, aj kajsanel ya, xupon che i chakij jyub tzʼinilic, chila pa jyub Judéa. Are xuponic, xoc chu bixquil u tzij i Dios chique i winak chi xe moltaj ruʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.002', '—Chi jalwachij i nojbal chuwach i Dios —xu bij chique—. Man i ʼatbal tzij chi ca pe chicaj ya ca jekanic —xu bij.
', '', '&lt;span class="verse" id="Matt_3_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—Chi jalwachij i nojbal chuwach i Dios —xu bij chique—. Man i ʼatbal tzij chi ca pe chicaj ya ca jekanic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.003', 'I ma Wan-le are i jun chi ʼax bital wi chi ca cʼunic. Are i bin-nak can ile are i ujer ajbil u tzij i Dios mam Isaías. Ire u tzʼibam canok:
Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.”
(Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Matt_3_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I ma Wan-le are i jun chi ʼax bital wi chi ca cʼunic. Are i bin-nak can ile are i ujer ajbil u tzij i Dios mam Isaías. Ire u tzʼibam canok:
Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.”
(Queje ile tzʼibtal canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.004', 'I ma Wan, i u ʼuʼ ire xa are i rismal camey ʼantal u ʼuʼ che; i ximbal u pam xa are chʼakap tzʼum. Wach i xu tijo xa are wach i xu rik pa tak ʼes: xa xu tij u waʼal cab, xak xe u tij i ral tak sacʼ. ', '', '&lt;span class="verse" id="Matt_3_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I ma Wan, i u ʼuʼ ire xa are i rismal camey ʼantal u ʼuʼ che; i ximbal u pam xa are chʼakap tzʼum. Wach i xu tijo xa are wach i xu rik pa tak ʼes: xa xu tij u waʼal cab, xak xe u tij i ral tak sacʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.005', 'I winak chi xe upon ruʼ chu tayic u tzij, ʼo i je petnak cʼa pa tinimit Jerusalen, xak ʼo i je petnak pa ronojel tak lugar re i jyub Judéa, xak ʼo i je petnak chu nakaj i ya Jordan. ', '', '&lt;span class="verse" id="Matt_3_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I winak chi xe upon ruʼ chu tayic u tzij, ʼo i je petnak cʼa pa tinimit Jerusalen, xak ʼo i je petnak pa ronojel tak lugar re i jyub Judéa, xak ʼo i je petnak chu nakaj i ya Jordan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.006', 'Ique, are xqui tzʼonoj ni cuybal mac che i Dios, teʼuri i ma Wan xu kajsaj ya piquiwi pa ya Jordan.
', '', '&lt;span class="verse" id="Matt_3_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Ique, are xqui tzʼonoj ni cuybal mac che i Dios, teʼuri i ma Wan xu kajsaj ya piquiwi pa ya Jordan.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.007', 'Xui-ri, xril ire je qʼui i aj Fariséo winak, xak i aj Saducéo, chi cacaj ique toʼ ca kajsax ya piquiwi. Are ʼuri xu bij chique:
—¡Yix xa ix racʼal cumatz! ¿Pachin xbin chiwe yix chi quix totaj chuwach i tojpen chi ca tijin nali piwi? ', '', '&lt;span class="verse" id="Matt_3_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xui-ri, xril ire je qʼui i aj Fariséo winak, xak i aj Saducéo, chi cacaj ique toʼ ca kajsax ya piquiwi. Are ʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—¡Yix xa ix racʼal cumatz! ¿Pachin xbin chiwe yix chi quix totaj chuwach i tojpen chi ca tijin nali piwi? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.008', 'Chi ʼalijinsaj ʼut ruʼ wach tak i qui ʼano we i jalwachim chic i nojbal chuwach i Dios. ', '', '&lt;span class="verse" id="Matt_3_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Chi ʼalijinsaj ʼut ruʼ wach tak i qui ʼano we i jalwachim chic i nojbal chuwach i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.009', 'Mi chʼob yix mok xa n-ca pe tu tojbalil piwi yix xa rumal ix aj Israel winak, xa rumal ix umuk uxiquin can i mam Abraham. Mi chʼob yix chi lic quix totaj na rumal. Quin bij chiwe chi Dios, che i abaj-i, ca tiqui chu ʼonquil uqʼuial aj Israel winak che, we ta craj —xu bij i ma Wan chique. ', '', '&lt;span class="verse" id="Matt_3_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Mi chʼob yix mok xa n-ca pe tu tojbalil piwi yix xa rumal ix aj Israel winak, xa rumal ix umuk uxiquin can i mam Abraham. Mi chʼob yix chi lic quix totaj na rumal. Quin bij chiwe chi Dios, che i abaj-i, ca tiqui chu ʼonquil uqʼuial aj Israel winak che, we ta craj —xu bij i ma Wan chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.010', 'Xak xu bij:
—I tojpen ya ca pe piwi, pacha jun icaj yij chic che jun cheʼ, cʼatzbal re i raʼ. Ronojel tak i cheʼ chi n-cu ya tu wach chom, ca cʼatzixic; teʼuri ca porox pa ʼaʼ. ', '', '&lt;span class="verse" id="Matt_3_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xak xu bij:&lt;p&gt; &lt;/p&gt;—I tojpen ya ca pe piwi, pacha jun icaj yij chic che jun cheʼ, cʼatzbal re i raʼ. Ronojel tak i cheʼ chi n-cu ya tu wach chom, ca cʼatzixic; teʼuri ca porox pa ʼaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.011', 'Yin, katzij, quin kajsaj ya piwi yix, cʼutbal-re we i jalwachim chic i nojbal chuwach i Dios. Xui-ri, i Jun chi ca cʼun chwijil yin cu kajsaj i u Tewal i Dios piquiwi niʼpa i qui jalwachim qui cʼaslemal; xak cu kajsaj li ʼaʼ piquiwi i je mal. Ire lic nim ni u ʼij chinwach yin; n-ca majaw ta chwe we quin ʼan mocom che wib chirij, mi tene cwucʼaj u xajab, rumal ni u ʼij nim. ', '', '&lt;span class="verse" id="Matt_3_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Yin, katzij, quin kajsaj ya piwi yix, cʼutbal-re we i jalwachim chic i nojbal chuwach i Dios. Xui-ri, i Jun chi ca cʼun chwijil yin cu kajsaj i u Tewal i Dios piquiwi niʼpa i qui jalwachim qui cʼaslemal; xak cu kajsaj li ʼaʼ piquiwi i je mal. Ire lic nim ni u ʼij chinwach yin; n-ca majaw ta chwe we quin ʼan mocom che wib chirij, mi tene cwucʼaj u xajab, rumal ni u ʼij nim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.012', 'Col u ʼata tzij piquiwi i winak, pachin i je ʼutz, xak pachin i ne te ʼutz. Ire yij chic pacha coʼon jun ruʼ u pala are cu pu u wach u trígo. Are i ʼutz quel che u trígo, cu molo y cu ya pu cʼolbal. I rij, chi n-ʼus taj, ca porox pa ʼaʼ chi n-tu chupic —xu bij i ma Wan chique.
', '', '&lt;span class="verse" id="Matt_3_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Col u ʼata tzij piquiwi i winak, pachin i je ʼutz, xak pachin i ne te ʼutz. Ire yij chic pacha coʼon jun ruʼ u pala are cu pu u wach u trígo. Are i ʼutz quel che u trígo, cu molo y cu ya pu cʼolbal. I rij, chi n-ʼus taj, ca porox pa ʼaʼ chi n-tu chupic —xu bij i ma Wan chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.013', 'Che tak i ʼij-le, i Jesus xel pa Galiléa, xupon chiya Jordan pa ʼo wi i ma Wan; xupon chu kajsaxic ya puwi rumal ire. ', '', '&lt;h2&gt;U kajsaxic ya puwi i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_3_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le, i Jesus xel pa Galiléa, xupon chiya Jordan pa ʼo wi i ma Wan; xupon chu kajsaxic ya puwi rumal ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.014', 'I ma Wan xu chʼobo chi n-cu maj taj we are ire ca kajsaw ya puwi i Jesus. Xu bij:
—Yet, mas ca majaw chawe chi ca kajsaj ya pinwi yin. N-ca majaw taj we yin quin kajsaw ya pawi yet —xu bij.
', '', '&lt;span class="verse" id="Matt_3_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I ma Wan xu chʼobo chi n-cu maj taj we are ire ca kajsaw ya puwi i Jesus. Xu bij:&lt;p&gt; &lt;/p&gt;—Yet, mas ca majaw chawe chi ca kajsaj ya pinwi yin. N-ca majaw taj we yin quin kajsaw ya pawi yet —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.015', 'I Jesus xu bij:
—N-ta coʼono. Queje cha ʼana ni ile woʼor, man ca majawic caka ʼan ni ronojel pacha craj i Dios —xu bij.
Teʼuri i ma Wan xu ya u wach che, wach i xbix che. ', '', '&lt;span class="verse" id="Matt_3_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—N-ta coʼono. Queje cha ʼana ni ile woʼor, man ca majawic caka ʼan ni ronojel pacha craj i Dios —xu bij.&lt;p&gt; &lt;/p&gt;Teʼuri i ma Wan xu ya u wach che, wach i xbix che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.016', 'I Jesus, are xutzin u kajsaxic ya puwi, xel pa ya. Xak teʼet, xjaktaj i caj, xa ʼiltaj i u Tewal i Dios chi xkaj lok pacha jun palomax sucʼulic puwi Jesus, ', '', '&lt;span class="verse" id="Matt_3_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I Jesus, are xutzin u kajsaxic ya puwi, xel pa ya. Xak teʼet, xjaktaj i caj, xa ʼiltaj i u Tewal i Dios chi xkaj lok pacha jun palomax sucʼulic puwi Jesus, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '3.017', 'y xtataj jun chʼawbal chicaj chi xu bij:
—Iri lic are i wacʼal chi cʼax quin naʼo; lic quin quicot che, wach u ʼanom.
', '', '&lt;span class="verse" id="Matt_3_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;y xtataj jun chʼawbal chicaj chi xu bij:&lt;p&gt; &lt;/p&gt;—Iri lic are i wacʼal chi cʼax quin naʼo; lic quin quicot che, wach u ʼanom.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.001', 'Are xoʼon ile, i Jesus xcʼam bi rumal i u Tewal i Dios che jun jyub tzʼinilic. Xcʼam bi chila, ʼilbal-re we ca tzaksax chupam u mac rumal itzel. ', '', '&lt;h2&gt;Xraj itzel cu tzaksaj i Jesus chupam u mac&lt;/h2&gt;&lt;span class="verse" id="Matt_4_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xoʼon ile, i Jesus xcʼam bi rumal i u Tewal i Dios che jun jyub tzʼinilic. Xcʼam bi chila, ʼilbal-re we ca tzaksax chupam u mac rumal itzel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.002', 'Chila xu chʼij cuarénta ʼij, cuarénta aʼab, lic n-ta xu tijo; teʼuri xpe i numic che. ', '', '&lt;span class="verse" id="Matt_4_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Chila xu chʼij cuarénta ʼij, cuarénta aʼab, lic n-ta xu tijo; teʼuri xpe i numic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.003', 'Are ʼuri, xupon itzel ruʼ, tzaksabal-re chupam u mac. Xu bij che:
—We katzij at u Cʼojol i Dios, cha bij che i abaj-le chi coʼon wa che rib —xu bij che.
', '', '&lt;span class="verse" id="Matt_4_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are ʼuri, xupon itzel ruʼ, tzaksabal-re chupam u mac. Xu bij che:&lt;p&gt; &lt;/p&gt;—We katzij at u Cʼojol i Dios, cha bij che i abaj-le chi coʼon wa che rib —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.004', 'I Jesus xu laʼ u wach che itzel y xu bij:
—N-quin ʼan taj, man tzʼibtalic chupam u wuj i Dios: “N-xui ta rumal i wa je cʼas i winak; are rumal ronojel u tzij i Dios je cʼaslic.”
', '', '&lt;span class="verse" id="Matt_4_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach che itzel y xu bij:&lt;p&gt; &lt;/p&gt;—N-quin ʼan taj, man tzʼibtalic chupam u wuj i Dios: “N-xui ta rumal i wa je cʼas i winak; are rumal ronojel u tzij i Dios je cʼaslic.”&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.005', 'Teʼuri itzel xu cʼam bi i Jesus che i loʼlaj tinimit, Jerusalen. Xe u ya chila cʼa chu tzam i rachoch i Dios. ', '', '&lt;span class="verse" id="Matt_4_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri itzel xu cʼam bi i Jesus che i loʼlaj tinimit, Jerusalen. Xe u ya chila cʼa chu tzam i rachoch i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.006', 'Teʼuri xu bij che:
—We at u Cʼojol i Dios, cha cʼaka bi awib cʼa pulew. N-cat cam taj, man chupam u wuj i Dios, queje iri tzʼibtalic:
I Dios cu bij na chique i ángel chi cat qui chajij.
Ique lic cat cucʼaj che qui ʼab, mi tene quiqui ya chawe que ʼana cʼax che awakan che abaj.
(Queje ile xu bij itzel che.)
', '', '&lt;span class="verse" id="Matt_4_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij che:&lt;p&gt; &lt;/p&gt;—We at u Cʼojol i Dios, cha cʼaka bi awib cʼa pulew. N-cat cam taj, man chupam u wuj i Dios, queje iri tzʼibtalic:
I Dios cu bij na chique i ángel chi cat qui chajij.
Ique lic cat cucʼaj che qui ʼab, mi tene quiqui ya chawe que ʼana cʼax che awakan che abaj.
(Queje ile xu bij itzel che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.007', 'I Jesus xu laʼ chic u wach che:
—Xak tzʼibtal chupam u wuj i Dios chi maka cʼam u pam i Kajwal Dios —xu bij che.
', '', '&lt;span class="verse" id="Matt_4_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ chic u wach che:&lt;p&gt; &lt;/p&gt;—Xak tzʼibtal chupam u wuj i Dios chi maka cʼam u pam i Kajwal Dios —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.008', 'Teʼuri itzel xu cʼam chubi i Jesus che jun jyub, naj u wi. Chila xe u cʼutu wi ronojel i tinimit ʼo chuwach i jyub taʼaj, ruʼ tak i qui chomal. ', '', '&lt;span class="verse" id="Matt_4_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Teʼuri itzel xu cʼam chubi i Jesus che jun jyub, naj u wi. Chila xe u cʼutu wi ronojel i tinimit ʼo chuwach i jyub taʼaj, ruʼ tak i qui chomal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.009', 'Teʼuri xu bij che:
—Ronojel iri ʼis quin sipaj chawe, xui-ri we yet cat xuqui chinwach chu cojic in ʼij. (Queje ile xu bij itzel che.)
', '', '&lt;span class="verse" id="Matt_4_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij che:&lt;p&gt; &lt;/p&gt;—Ronojel iri ʼis quin sipaj chawe, xui-ri we yet cat xuqui chinwach chu cojic in ʼij. (Queje ile xu bij itzel che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.010', 'I Jesus xu laʼ chic u wach che itzel:
—¡Jat, at Satanas! Man chupam u wuj i Dios, queje iri tzʼibtalic: “Xui che i Kajwal Dios cha cojo u ʼij; xui che ire ca ʼan wi a patan,” —xu bij che.
', '', '&lt;span class="verse" id="Matt_4_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ chic u wach che itzel:&lt;p&gt; &lt;/p&gt;—¡Jat, at Satanas! Man chupam u wuj i Dios, queje iri tzʼibtalic: “Xui che i Kajwal Dios cha cojo u ʼij; xui che ire ca ʼan wi a patan,” —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.011', 'Teʼuri itzel xel chuwach i Jesus; are ʼuri xe upon juban ángel ruʼ, xol qui to wach i rajwaxic che.
', '', '&lt;span class="verse" id="Matt_4_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Teʼuri itzel xel chuwach i Jesus; are ʼuri xe upon juban ángel ruʼ, xol qui to wach i rajwaxic che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.012', 'Xoʼon panok, xu ta rason i Jesus chi ma wan tzʼapil pa cárcel. ', '', '&lt;h2&gt;I Jesus xu jek u chac pa jyub Galiléa&lt;/h2&gt;&lt;span class="verse" id="Matt_4_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xu ta rason i Jesus chi ma wan tzʼapil pa cárcel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.013', 'Are xu ta ile, xa ʼe tan chic pa jyub Galiléa, xel pa Nazaret, xa ʼe jekel pa tinimit Capernaum. I Capernaum ʼo chu chiʼ i alagun; ʼo che i jyub Zabulon, tak i Neftali. ', '', '&lt;span class="verse" id="Matt_4_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Are xu ta ile, xa ʼe tan chic pa jyub Galiléa, xel pa Nazaret, xa ʼe jekel pa tinimit Capernaum. I Capernaum ʼo chu chiʼ i alagun; ʼo che i jyub Zabulon, tak i Neftali. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.014', 'Are xupon i Jesus pa jyub-le, xeʼelok pacha u tzʼibam can i mam Isaías chi ajbil u tzij i Dios ujer. U tzʼibam can ire i tzij-i:
', '', '&lt;span class="verse" id="Matt_4_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Are xupon i Jesus pa jyub-le, xeʼelok pacha u tzʼibam can i mam Isaías chi ajbil u tzij i Dios ujer. U tzʼibam can ire i tzij-i:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.015', 'Quin bij chiwe wach quiquil na
chila pa jyub Zabulon tak pa Neftali,
chila pa jyub pa ca ʼe wi i be ruʼ i mar,
chila pa jyub chʼaka ya re i Jordan,
chila ʼut pa Galiléa, pa je ʼo wi i ne te aj Israel, chi n-quetaʼam ti Dios.
', '', '&lt;span class="verse" id="Matt_4_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Quin bij chiwe wach quiquil na
chila pa jyub Zabulon tak pa Neftali,
chila pa jyub pa ca ʼe wi i be ruʼ i mar,
chila pa jyub chʼaka ya re i Jordan,
chila ʼut pa Galiléa, pa je ʼo wi i ne te aj Israel, chi n-quetaʼam ti Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.016', 'I winak chila chi je ʼo pa ʼekumal, quiquil na jun nim laj sak.
Ca pe u sakirbal piquiwi niʼpa i toʼ je ʼo chupam qui mac, toʼ je ʼo chuwach i camic.
(Queje ile xu tzʼibaj i mam Isaías ujer.)
', '', '&lt;span class="verse" id="Matt_4_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I winak chila chi je ʼo pa ʼekumal, quiquil na jun nim laj sak.
Ca pe u sakirbal piquiwi niʼpa i toʼ je ʼo chupam qui mac, toʼ je ʼo chuwach i camic.
(Queje ile xu tzʼibaj i mam Isaías ujer.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.017', 'Are xupon i Jesus chila, are ʼuri xu jek u bixquil chique i winak:
—Chi jalwachij i noʼoj chuwach i Dios, man i u ʼatbal tzij ya ca jekanic. (Queje ile xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_4_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are xupon i Jesus chila, are ʼuri xu jek u bixquil chique i winak:&lt;p&gt; &lt;/p&gt;—Chi jalwachij i noʼoj chuwach i Dios, man i u ʼatbal tzij ya ca jekanic. (Queje ile xu bij i Jesus chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.018', 'Che tak i ʼij-le, are ca tijin i Jesus chi be chu chiʼ i alagun Galiléa, xe ril queb achiab, catz qui chaʼ quib. I jun are i ma Simon chi xak ma Pédro quiqui bij che; i jun chic are i ma Andres. Ique-le je aj chapol car, y que tijin chu cʼakic i cataraya pa ya. ', '', '&lt;h2&gt;I Jesus xe u siqʼuij cajib achiab, aj chapol car&lt;/h2&gt;&lt;span class="verse" id="Matt_4_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le, are ca tijin i Jesus chi be chu chiʼ i alagun Galiléa, xe ril queb achiab, catz qui chaʼ quib. I jun are i ma Simon chi xak ma Pédro quiqui bij che; i jun chic are i ma Andres. Ique-le je aj chapol car, y que tijin chu cʼakic i cataraya pa ya. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.019', 'I Jesus xu bij chique:
—Chix terej chwij y quin ʼan na chiwe ix aj molol que i winak pacha qui ʼan chique i car —xu bij chique.
', '', '&lt;span class="verse" id="Matt_4_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chix terej chwij y quin ʼan na chiwe ix aj molol que i winak pacha qui ʼan chique i car —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.020', 'Are ʼuri, ique xcoʼtaj can i cataraya, y xe ʼe ruʼ.
', '', '&lt;span class="verse" id="Matt_4_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are ʼuri, ique xcoʼtaj can i cataraya, y xe ʼe ruʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.021', 'Xoʼon chupan jubiʼ, i Jesus xe ril chic queb achiab catz qui chaʼ quib. Are i ma Jacóbo, xak i ma Wan, je u cʼojol i mam Zebedéo. Ique je ʼo pa bárco ruʼ i qui kajaw, que tijin chu yijbaxic i cataraya are xe u siqʼuij i Jesus ruʼ; ', '', '&lt;span class="verse" id="Matt_4_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, i Jesus xe ril chic queb achiab catz qui chaʼ quib. Are i ma Jacóbo, xak i ma Wan, je u cʼojol i mam Zebedéo. Ique je ʼo pa bárco ruʼ i qui kajaw, que tijin chu yijbaxic i cataraya are xe u siqʼuij i Jesus ruʼ; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.022', 'juntir xqui canaj can i qui bárco, xqui canaj can i qui kajaw, y xe terej chirij.
', '', '&lt;span class="verse" id="Matt_4_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;juntir xqui canaj can i qui bárco, xqui canaj can i qui kajaw, y xe terej chirij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.023', 'Teʼuri i Jesus xu jek qui solixic ronojel tak i tinimit pa jyub re i Galiléa. Xoc ruʼ tak ja re qui molbalʼib, y xe u nawsaj i winak chi que moltaj chila. Xu paxsaj i ʼutz laj tzij re u ʼatbal tzij i Dios, xak xe rutzirsaj iwabib, bi chi cʼaxil ʼo chique. ', '', '&lt;h2&gt;I Jesus xe u nawsaj je uqʼuial winak&lt;/h2&gt;&lt;span class="verse" id="Matt_4_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu jek qui solixic ronojel tak i tinimit pa jyub re i Galiléa. Xoc ruʼ tak ja re qui molbalʼib, y xe u nawsaj i winak chi que moltaj chila. Xu paxsaj i ʼutz laj tzij re u ʼatbal tzij i Dios, xak xe rutzirsaj iwabib, bi chi cʼaxil ʼo chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.024', 'Xak ronojel i jyub Síria xak xqui ta rason wach ca tijin i Jesus chu ʼonquil pa Galiléa. Xqui jek li qui cʼamic iwabib ruʼ: niʼpa ʼo tak i yobil chique, niʼpa ʼo tak cʼax chique. Xe qui cʼam li ʼo itzel tew chique, xak niʼpa i quiqui tij i camic rumal i atac, xak niʼpa i je sic. I Jesus, niʼpa ile xe rutzirsaj. ', '', '&lt;span class="verse" id="Matt_4_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xak ronojel i jyub Síria xak xqui ta rason wach ca tijin i Jesus chu ʼonquil pa Galiléa. Xqui jek li qui cʼamic iwabib ruʼ: niʼpa ʼo tak i yobil chique, niʼpa ʼo tak cʼax chique. Xe qui cʼam li ʼo itzel tew chique, xak niʼpa i quiqui tij i camic rumal i atac, xak niʼpa i je sic. I Jesus, niʼpa ile xe rutzirsaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '4.025', 'Je uqʼuial winak xe terej chirij: xe cʼun i aj Galiléa, xe cʼun i aj Decápolis, xe cʼun i aj pa Jerusalen, xak ronojel i Judéa, xak xe cʼun aj chʼakap re i ya Jordan.
', '', '&lt;span class="verse" id="Matt_4_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Je uqʼuial winak xe terej chirij: xe cʼun i aj Galiléa, xe cʼun i aj Decápolis, xe cʼun i aj pa Jerusalen, xak ronojel i Judéa, xak xe cʼun aj chʼakap re i ya Jordan.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.001', 'I Jesus, are xe ril je uqʼuial winak je teren chirij, xel bi chuwi jun jyub, y xcubi chila. Niʼpa i que tijin qui nawic che u tzij xqui mol quib ruʼ. ', '', '&lt;h2&gt;I Jesus xu bij pachin i ca pe ni ʼutz piquiwi&lt;/h2&gt;&lt;span class="verse" id="Matt_5_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus, are xe ril je uqʼuial winak je teren chirij, xel bi chuwi jun jyub, y xcubi chila. Niʼpa i que tijin qui nawic che u tzij xqui mol quib ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.002', 'Teʼuri xu jek qui nawsaxic ruʼ i tzij-i:
', '', '&lt;span class="verse" id="Matt_5_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Teʼuri xu jek qui nawsaxic ruʼ i tzij-i:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.003', '—ʼUtz que ique chi quetaʼam ʼo rajwaxic piqui cʼux chuwach i Dios, man que oc na ʼuri pa cu ʼat wi tzij i Dios.
', '', '&lt;span class="verse" id="Matt_5_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;—ʼUtz que ique chi quetaʼam ʼo rajwaxic piqui cʼux chuwach i Dios, man que oc na ʼuri pa cu ʼat wi tzij i Dios.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.004', 'ʼUtz que ique chi que bisonic, man i Dios cu cubsaj ni qui cʼux.
', '', '&lt;span class="verse" id="Matt_5_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi que bisonic, man i Dios cu cubsaj ni qui cʼux.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.005', 'ʼUtz que ique chi ʼo u nimal qui cʼux, man ca yaʼ ni jyub taʼaj chique chi u sujum i Dios.
', '', '&lt;span class="verse" id="Matt_5_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi ʼo u nimal qui cʼux, man ca yaʼ ni jyub taʼaj chique chi u sujum i Dios.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.006', 'ʼUtz que ique chi lic quiqui rayij u ʼanic wach tak craj i Dios, man ire lic que u to chu ʼonquil.
', '', '&lt;span class="verse" id="Matt_5_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi lic quiqui rayij u ʼanic wach tak craj i Dios, man ire lic que u to chu ʼonquil.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.007', 'ʼUtz que ique chi quewi ʼo i cʼaxnabal qui cʼux, man i Dios xak cʼax que u na ni ique.
', '', '&lt;span class="verse" id="Matt_5_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi quewi ʼo i cʼaxnabal qui cʼux, man i Dios xak cʼax que u na ni ique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.008', 'ʼUtz que ique chi chʼam i piqui cʼux, man ique quiquil nu wach i Dios.
', '', '&lt;span class="verse" id="Matt_5_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi chʼam i piqui cʼux, man ique quiquil nu wach i Dios.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.009', 'ʼUtz que ique chi que qui chomarsaj i que chʼoʼojinic, man “je wacʼal” cu bij na ʼuri i Dios chique.
', '', '&lt;span class="verse" id="Matt_5_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi que qui chomarsaj i que chʼoʼojinic, man “je wacʼal” cu bij na ʼuri i Dios chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.010', 'ʼUtz que ique chi caʼani cʼax chique xa rumal u ʼanic wach craj i Dios chique, man que oc na ʼuri pa cu ʼat wi tzij ire.
', '', '&lt;span class="verse" id="Matt_5_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;ʼUtz que ique chi caʼani cʼax chique xa rumal u ʼanic wach craj i Dios chique, man que oc na ʼuri pa cu ʼat wi tzij ire.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.011', 'ʼUtz iwe yix we cʼax que chʼaw ni winak chiwe xa wumal yin. ʼUtz iwe yix we quix qui yoʼya, xak we quiqui bij i mentir chiwij xa rumal quix terej chwij. ', '', '&lt;span class="verse" id="Matt_5_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;ʼUtz iwe yix we cʼax que chʼaw ni winak chiwe xa wumal yin. ʼUtz iwe yix we quix qui yoʼya, xak we quiqui bij i mentir chiwij xa rumal quix terej chwij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.012', 'Mix bisonic, xa chix quicotok, man qui rikbej ʼuri jun nim laj sipon chila chicaj. Cha cʼun chi cʼux, xak queje xqui ʼan li winak ile chique i ajbil u tzij i Dios ujer.
', '', '&lt;span class="verse" id="Matt_5_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Mix bisonic, xa chix quicotok, man qui rikbej ʼuri jun nim laj sipon chila chicaj. Cha cʼun chi cʼux, xak queje xqui ʼan li winak ile chique i ajbil u tzij i Dios ujer.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.013', 'Yix ʼut lic qui rik i chac chique i winak waral chuwach i jyub taʼaj; ix pacha i atzʼam chi lic cu rik u chac chique. Xui-ri, i atzʼam, we xsach u jelal, ¿wach ʼuri u yijbaxic chic u jelal ca ʼanic? N-taj. Ile n-cu rik ta chic u chac; xa ca cʼak bic; toʼ ca wachʼalbex chic cumal i winak.
', '', '&lt;h2&gt;I je re chic i Dios je pacha atzʼam, xak je pacha u tzunbal ʼaʼ&lt;/h2&gt;&lt;span class="verse" id="Matt_5_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Yix ʼut lic qui rik i chac chique i winak waral chuwach i jyub taʼaj; ix pacha i atzʼam chi lic cu rik u chac chique. Xui-ri, i atzʼam, we xsach u jelal, ¿wach ʼuri u yijbaxic chic u jelal ca ʼanic? N-taj. Ile n-cu rik ta chic u chac; xa ca cʼak bic; toʼ ca wachʼalbex chic cumal i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.014', 'Yix ix pacha u tzunbal ʼaʼ waral chuwach i jyub taʼaj. Iwilom ʼut wach oʼonom jun tinimit we ʼo tzam jyub; lic ʼalaj, n-ruwam ta rib. ', '', '&lt;span class="verse" id="Matt_5_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Yix ix pacha u tzunbal ʼaʼ waral chuwach i jyub taʼaj. Iwilom ʼut wach oʼonom jun tinimit we ʼo tzam jyub; lic ʼalaj, n-ruwam ta rib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.015', 'Xak iwetaʼam, we jun cu tzij jun cantil, n-que u mina ta chuxeʼ jun cajon; n-ʼus taj we cruwaj. Are i cu ʼano, cu ya ʼan chicaj paja, man conojel que tzun na rumal. ', '', '&lt;span class="verse" id="Matt_5_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xak iwetaʼam, we jun cu tzij jun cantil, n-que u mina ta chuxeʼ jun cajon; n-ʼus taj we cruwaj. Are i cu ʼano, cu ya ʼan chicaj paja, man conojel que tzun na rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.016', 'Xak queje chi ʼana yix, chi ix pacha u tzunbal ʼaʼ waral chuwach i jyub taʼaj, chi ya che i tzunbal ca ʼe chiquiwach i winak. Queje ile quiquil na wach tak i ʼutz qui ʼano. Rumal ʼuri, quiqui coj u ʼij i Kajaw yix chila chicaj.
', '', '&lt;span class="verse" id="Matt_5_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xak queje chi ʼana yix, chi ix pacha u tzunbal ʼaʼ waral chuwach i jyub taʼaj, chi ya che i tzunbal ca ʼe chiquiwach i winak. Queje ile quiquil na wach tak i ʼutz qui ʼano. Rumal ʼuri, quiqui coj u ʼij i Kajaw yix chila chicaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.017', 'Mi chʼob yix mok col in sacha u wach i ʼatbal tzij tzʼibtal can rumal i mam Moises ujer; mi tene ile, xak mi tene i qui cʼutunic can i ujer ajbil u tzij i Dios. Yin n-in ta aj sachol re ile. Xa in aj ʼanol re i usucʼ i tzij-le chiwach yix. ', '', '&lt;h2&gt;I Jesus aj ʼanol re i mer usucʼ i ujer ʼatbal tzij&lt;/h2&gt;&lt;span class="verse" id="Matt_5_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Mi chʼob yix mok col in sacha u wach i ʼatbal tzij tzʼibtal can rumal i mam Moises ujer; mi tene ile, xak mi tene i qui cʼutunic can i ujer ajbil u tzij i Dios. Yin n-in ta aj sachol re ile. Xa in aj ʼanol re i usucʼ i tzij-le chiwach yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.018', 'Katzij iri: tzʼakat cʼo i caj, i jyub taʼaj, n-ca sachsax ti u wach i ujer ʼatbal tzij, mi ta jubiʼ, mi ta jun tzʼib, mi ta pichʼ che jun ral tzʼib. Ile cu chʼij na cʼa ca qʼuis ni ronojel u ʼanic pacha u chʼobom i Dios chi ca ʼani na. ', '', '&lt;span class="verse" id="Matt_5_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Katzij iri: tzʼakat cʼo i caj, i jyub taʼaj, n-ca sachsax ti u wach i ujer ʼatbal tzij, mi ta jubiʼ, mi ta jun tzʼib, mi ta pichʼ che jun ral tzʼib. Ile cu chʼij na cʼa ca qʼuis ni ronojel u ʼanic pacha u chʼobom i Dios chi ca ʼani na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.019', 'N-ca sachsax taj; rumal-i, ʼo u chac qui ʼan na pacha i cu bij ronojel i ʼatbal tzij. We ʼo jun n-xcojon ta che i ral cu jupaj tzij pichʼ re i ʼatbal tzij, xak we xu cʼut ʼuri chique i winak queje quiqui ʼan ile, ire ʼut xa pichʼ queʼelok chiquiwach conojel i juban pa cu ʼat wi tzij i Dios. Xui-ri, we jun xcojon che i mer usucʼ i ʼatbal tzij, xak xu cʼut ʼuri chique i juban chic, ire ʼuri lic nim queʼel na, pa cu ʼat wi tzij i Dios. ', '', '&lt;span class="verse" id="Matt_5_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;N-ca sachsax taj; rumal-i, ʼo u chac qui ʼan na pacha i cu bij ronojel i ʼatbal tzij. We ʼo jun n-xcojon ta che i ral cu jupaj tzij pichʼ re i ʼatbal tzij, xak we xu cʼut ʼuri chique i winak queje quiqui ʼan ile, ire ʼut xa pichʼ queʼelok chiquiwach conojel i juban pa cu ʼat wi tzij i Dios. Xui-ri, we jun xcojon che i mer usucʼ i ʼatbal tzij, xak xu cʼut ʼuri chique i juban chic, ire ʼuri lic nim queʼel na, pa cu ʼat wi tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.020', 'Lic quin bij chiwe, ʼo u chac chix cojon na che ronojel pacha u bim can i Dios; ʼo u chac mas ʼutz u ʼanic chi ʼana ni yix chiquiwach i je tijonel re i ʼatbal tzij; xak mas ʼutz u ʼanic chi ʼana na chiquiwach i aj Fariséo. We n-qui ʼan taj, n-quix oc ta puʼab i Dios.
', '', '&lt;span class="verse" id="Matt_5_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Lic quin bij chiwe, ʼo u chac chix cojon na che ronojel pacha u bim can i Dios; ʼo u chac mas ʼutz u ʼanic chi ʼana ni yix chiquiwach i je tijonel re i ʼatbal tzij; xak mas ʼutz u ʼanic chi ʼana na chiquiwach i aj Fariséo. We n-qui ʼan taj, n-quix oc ta puʼab i Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.021', 'I tom yix i ʼatbal tzij bital chique iwatit imam ujer. “Mix camsanic,” bital chique, “man pachin jun ca camsanic, caʼat ni tzij ʼuri puwi,” xbix chique. ', '', '&lt;h2&gt;I Jesus xu bij chi n-ʼus taj ʼo awoywal che i jun chic&lt;/h2&gt;&lt;span class="verse" id="Matt_5_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I tom yix i ʼatbal tzij bital chique iwatit imam ujer. “Mix camsanic,” bital chique, “man pachin jun ca camsanic, caʼat ni tzij ʼuri puwi,” xbix chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.022', 'Xui-ri, yin quin bij ni usucʼ chiwe: pachin jun xa ca pe roywal che jun rachalal, xak ire caʼat ni tzij puwi. Xak pachin jun cu yoʼya i rachalal, ca yaʼ ni ire chiquiwach i nim laj nucʼbal tʼisbal. Xak we ʼo jun cu bij mal laj tzij che i rachalal, chu ʼana cwent ʼuri che rib, mok xa ca ʼe pa ʼaʼ rumal. ', '', '&lt;span class="verse" id="Matt_5_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: pachin jun xa ca pe roywal che jun rachalal, xak ire caʼat ni tzij puwi. Xak pachin jun cu yoʼya i rachalal, ca yaʼ ni ire chiquiwach i nim laj nucʼbal tʼisbal. Xak we ʼo jun cu bij mal laj tzij che i rachalal, chu ʼana cwent ʼuri che rib, mok xa ca ʼe pa ʼaʼ rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.023', 'Rumal-i, we yet a cʼamom li jun a sipon col a ya chuwach i Dios, teʼuri we ca cʼun pa cʼux chi ʼo jun awachalal cu chʼobo ʼo a mac che, ', '', '&lt;span class="verse" id="Matt_5_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Rumal-i, we yet a cʼamom li jun a sipon col a ya chuwach i Dios, teʼuri we ca cʼun pa cʼux chi ʼo jun awachalal cu chʼobo ʼo a mac che, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.024', 'cha canaj can ʼuri a sipon chila chuwach i altar, y jat ruʼ awachalal. Chi chomaj iwib chiwach. Teʼuri chat tzalij chic y cha jacha a sipon chuwach i Dios. ', '', '&lt;span class="verse" id="Matt_5_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;cha canaj can ʼuri a sipon chila chuwach i altar, y jat ruʼ awachalal. Chi chomaj iwib chiwach. Teʼuri chat tzalij chic y cha jacha a sipon chuwach i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.025', 'We ʼo jun u cojom a mac, y craj cat u cʼam bi chuwach i ʼatbal tzij, chi chomaj iwib chiwach pa be. Queje chi ʼana ile, man ire n-cat u ya ta chuwach i ʼatbal tzij. Man i ʼatbal tzij cat u jach na piquiʼab i policía; teʼuri i policía cat u tzʼapij pa cárcel. ', '', '&lt;span class="verse" id="Matt_5_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;We ʼo jun u cojom a mac, y craj cat u cʼam bi chuwach i ʼatbal tzij, chi chomaj iwib chiwach pa be. Queje chi ʼana ile, man ire n-cat u ya ta chuwach i ʼatbal tzij. Man i ʼatbal tzij cat u jach na piquiʼab i policía; teʼuri i policía cat u tzʼapij pa cárcel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.026', 'Katzij iri: n-cat el ta lok, cʼa ca toj ni ronojel a mult. (Rumal-i, weʼ chi chomaj iwib chiwach.)
', '', '&lt;span class="verse" id="Matt_5_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Katzij iri: n-cat el ta lok, cʼa ca toj ni ronojel a mult. (Rumal-i, weʼ chi chomaj iwib chiwach.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.027', 'I tom yix wach cu bij i ʼatbal tzij re i mam Moises: “Ma kajsaj a ʼij ruʼ jun ixok, we n-awixokil taj,” cu bij. ', '', '&lt;h2&gt;I pixbanic xoʼon i Jesus chique i je cachijil quib&lt;/h2&gt;&lt;span class="verse" id="Matt_5_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I tom yix wach cu bij i ʼatbal tzij re i mam Moises: “Ma kajsaj a ʼij ruʼ jun ixok, we n-awixokil taj,” cu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.028', 'Xui-ri, yin quin bij ni usucʼ chiwe: we ʼo jun lic cu takej jun ixok, man cu rayij u wach, xeʼel ʼuri pu cʼux xak jun ruʼ pacha i xmacun ruʼ. ', '', '&lt;span class="verse" id="Matt_5_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: we ʼo jun lic cu takej jun ixok, man cu rayij u wach, xeʼel ʼuri pu cʼux xak jun ruʼ pacha i xmacun ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.029', 'Rumal-i, quin bij i tzij-i chiwe: we yet toʼ ca takej jun ixok y toʼ ca rayij u wach, mejor chawesaj a baʼwach y cha cʼaka bic Mas ʼutz queʼelok we xa are a baʼwach ca sacho, chuwach i ronojel a chʼacul ca ʼe pa ʼaʼ. ', '', '&lt;span class="verse" id="Matt_5_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Rumal-i, quin bij i tzij-i chiwe: we yet toʼ ca takej jun ixok y toʼ ca rayij u wach, mejor chawesaj a baʼwach y cha cʼaka bic Mas ʼutz queʼelok we xa are a baʼwach ca sacho, chuwach i ronojel a chʼacul ca ʼe pa ʼaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.030', 'We xak are a ʼab wikabim ca ʼanaw chawe cat macunic, cha cʼatzij y cha cʼaka bic. ʼUtz queʼelok we xa are a ʼab ca sacho, chuwach i ronojel a chʼacul ca ʼe pa ʼaʼ.
', '', '&lt;span class="verse" id="Matt_5_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;We xak are a ʼab wikabim ca ʼanaw chawe cat macunic, cha cʼatzij y cha cʼaka bic. ʼUtz queʼelok we xa are a ʼab ca sacho, chuwach i ronojel a chʼacul ca ʼe pa ʼaʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.031', 'Xak i tom yix iri chi ca bixic: “Jun we cu jach can i rixokil, ʼo u chac cu tzʼibaj jun wuj, cu ya che i rixokil, re i jachbal quib.” ca bixic. ', '', '&lt;span class="verse" id="Matt_5_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xak i tom yix iri chi ca bixic: “Jun we cu jach can i rixokil, ʼo u chac cu tzʼibaj jun wuj, cu ya che i rixokil, re i jachbal quib.” ca bixic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.032', 'Xui-ri, yin quin bij ni usucʼ chiwe: jun, we cu jach can i rixokil, i ixok, we n-tu mac u ʼanom ruʼ jun chic achi, n-ʼus ta xu ʼan ʼuri i rachijil che. Queʼel ʼuri xa are u mac i rachijil che, we i ixok xcʼuli ruʼ jun chic achi; i u mac i ixok ʼo ʼuri chukul i achi chi jachawnak bic. Xak we ʼo jun achi ca cʼuli ruʼ jun ixok chi jachtal bi rumal i rachijil, i achi-le xak ca macun ʼuri chuwach i Dios.
', '', '&lt;span class="verse" id="Matt_5_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: jun, we cu jach can i rixokil, i ixok, we n-tu mac u ʼanom ruʼ jun chic achi, n-ʼus ta xu ʼan ʼuri i rachijil che. Queʼel ʼuri xa are u mac i rachijil che, we i ixok xcʼuli ruʼ jun chic achi; i u mac i ixok ʼo ʼuri chukul i achi chi jachawnak bic. Xak we ʼo jun achi ca cʼuli ruʼ jun ixok chi jachtal bi rumal i rachijil, i achi-le xak ca macun ʼuri chuwach i Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.033', 'Xak i tom yix iri chi bital chique iwatit imam ujer: “ʼO u chac ca ʼan na pacha a sujum che jun winak, we a cuxtam u bi i Kajwal puwi a tzij-le.” (Queje ile tzʼibtal canok.) ', '', '&lt;h2&gt;Ma cuxtaj u bi i Dios puwi a tzij&lt;/h2&gt;&lt;span class="verse" id="Matt_5_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xak i tom yix iri chi bital chique iwatit imam ujer: “ʼO u chac ca ʼan na pacha a sujum che jun winak, we a cuxtam u bi i Kajwal puwi a tzij-le.” (Queje ile tzʼibtal canok.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.034', 'Xui-ri, yin quin bij ni usucʼ chiwe: n-ʼus taj qui cuxtaj u bi i Dios puwi i tzij che jun winak. Xak mi cuxtaj u bi i caj, man are u qʼuijibal i Dios, pa cu ʼat wi tzij. ', '', '&lt;span class="verse" id="Matt_5_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: n-ʼus taj qui cuxtaj u bi i Dios puwi i tzij che jun winak. Xak mi cuxtaj u bi i caj, man are u qʼuijibal i Dios, pa cu ʼat wi tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.035', 'Xak mi cuxtaj u bi i ulew, man are pa cu ya wi i rakan. Xak mi cuxtaj u bi i tinimit Jerusalen, man are u tinimit i Dios chi ka nim laj ʼAtol Tzij. ', '', '&lt;span class="verse" id="Matt_5_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Xak mi cuxtaj u bi i ulew, man are pa cu ya wi i rakan. Xak mi cuxtaj u bi i tinimit Jerusalen, man are u tinimit i Dios chi ka nim laj ʼAtol Tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.036', 'Xak mi cuxtaj u bi i jolom, man n-quix tiqui ta chu yijbaxic wach ca tzun jun ral ʼis re i wi, we ʼek ʼo we sak quiwaj che. ', '', '&lt;span class="verse" id="Matt_5_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Xak mi cuxtaj u bi i jolom, man n-quix tiqui ta chu yijbaxic wach ca tzun jun ral ʼis re i wi, we ʼek ʼo we sak quiwaj che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.037', 'Xui chi bij “are”; o we n-are taj, chi bij “n-are taj”. We ʼo mas tzij qui bij puwi ile, xa ruʼ itzel petnak wi.
', '', '&lt;span class="verse" id="Matt_5_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Xui chi bij “are”; o we n-are taj, chi bij “n-are taj”. We ʼo mas tzij qui bij puwi ile, xa ruʼ itzel petnak wi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.038', 'Xak i tom yix chi i ujer ʼatbal tzij cu bij: “We ʼo jun u puchʼum a baʼwach yet, xak ca puchʼ ni jun u baʼwach ire ʼuri; xak we u bokom jun a wawe, xak ire ca bok ni jun u ware ʼuri.” Queje ile cu bij i u ʼatbal tzij i mam Moises. ', '', '&lt;h2&gt;Jun, we cu ʼan jun mal chiwe, mi tzalij u wach che&lt;/h2&gt;&lt;span class="verse" id="Matt_5_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Xak i tom yix chi i ujer ʼatbal tzij cu bij: “We ʼo jun u puchʼum a baʼwach yet, xak ca puchʼ ni jun u baʼwach ire ʼuri; xak we u bokom jun a wawe, xak ire ca bok ni jun u ware ʼuri.” Queje ile cu bij i u ʼatbal tzij i mam Moises. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.039', 'Xui-ri, yin quin bij ni usucʼ chiwe: mi tzalij u wach che, wach cu ʼan chiwe. Are i chi ʼana, we ʼo jun cat u chʼay che juperaj a palaj, cha ya i juperaj chic che. ', '', '&lt;span class="verse" id="Matt_5_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: mi tzalij u wach che, wach cu ʼan chiwe. Are i chi ʼana, we ʼo jun cat u chʼay che juperaj a palaj, cha ya i juperaj chic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.040', 'We ʼo jun ca ʼe pa ʼatbal tzij, man craj cu maj a camix, cha ya na ʼuri che, xak cha ya bi a chaquet che. ', '', '&lt;span class="verse" id="Matt_5_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;We ʼo jun ca ʼe pa ʼatbal tzij, man craj cu maj a camix, cha ya na ʼuri che, xak cha ya bi a chaquet che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.041', 'We ʼo jun coʼon pen chawe ca cʼam bi jun rekan pa jun kilómetro, yet ja ya che cʼa pa queb kilómetro. ', '', '&lt;span class="verse" id="Matt_5_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;We ʼo jun coʼon pen chawe ca cʼam bi jun rekan pa jun kilómetro, yet ja ya che cʼa pa queb kilómetro. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.042', 'We ʼo tak ca tzʼonox chawe, cha ya wach i ca tzʼonoxic. Y we ʼo i ca chiʼmax chawe, cha ya wach i rajwaxic.
', '', '&lt;span class="verse" id="Matt_5_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;We ʼo tak ca tzʼonox chawe, cha ya wach i ca tzʼonoxic. Y we ʼo i ca chiʼmax chawe, cha ya wach i rajwaxic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.043', 'I tom yix i tzij-i chi ca bixic: “Cʼax cha na i cʼax cat nawic,” quiqui bij, “xak tzel chawila i tzel cat ilawic,” quiqui bij. ', '', '&lt;h2&gt;Cʼax che i na i je aj chʼoʼoj chiwij&lt;/h2&gt;&lt;span class="verse" id="Matt_5_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;I tom yix i tzij-i chi ca bixic: “Cʼax cha na i cʼax cat nawic,” quiqui bij, “xak tzel chawila i tzel cat ilawic,” quiqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.044', 'Xui-ri, yin quin bij ni usucʼ chiwe: cʼax che i na i je aj chʼoʼoj chiwij; chi tzʼonoj i ʼutz piquiwi i que tzʼonow mal piwi yix; chi ʼana ʼutz chique i tzel quix ilawic; chi tzʼonoj che i Dios piquiwi niʼpa i quix qui yoʼya, xak piquiwi niʼpa i cʼax que chʼaw chiwe. ', '', '&lt;span class="verse" id="Matt_5_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij ni usucʼ chiwe: cʼax che i na i je aj chʼoʼoj chiwij; chi tzʼonoj i ʼutz piquiwi i que tzʼonow mal piwi yix; chi ʼana ʼutz chique i tzel quix ilawic; chi tzʼonoj che i Dios piquiwi niʼpa i quix qui yoʼya, xak piquiwi niʼpa i cʼax que chʼaw chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.045', 'Queje ile, quix ʼel na lic ix racʼal i Kajaw yix chila chicaj. Ire ʼut cu ʼan che i ʼij cu tzunsaj piquiwi i je mal, xak jun ruʼ pacha cu ʼan chique i je ʼutz. Xak cu tak li jab piquiwi i je ʼutz, xak piquiwi i je mal. ', '', '&lt;span class="verse" id="Matt_5_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Queje ile, quix ʼel na lic ix racʼal i Kajaw yix chila chicaj. Ire ʼut cu ʼan che i ʼij cu tzunsaj piquiwi i je mal, xak jun ruʼ pacha cu ʼan chique i je ʼutz. Xak cu tak li jab piquiwi i je ʼutz, xak piquiwi i je mal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.046', 'Yix ʼut, we xui cʼax que i na ique chi xak cʼax quix qui na yix, n-ta qui chʼac na ʼuri ruʼ i Dios. Xak i je mal laj tak aj alcawal queje quiqui ʼan ile. ', '', '&lt;span class="verse" id="Matt_5_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Yix ʼut, we xui cʼax que i na ique chi xak cʼax quix qui na yix, n-ta qui chʼac na ʼuri ruʼ i Dios. Xak i je mal laj tak aj alcawal queje quiqui ʼan ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.047', 'Yix, we xui chique iwamig qui ya rutzil qui wach, ¿pa ʼutz wi ʼuri ile? Xak queje quiqui ʼan i winak chi n-quetaʼam tu wach i Dios. ', '', '&lt;span class="verse" id="Matt_5_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Yix, we xui chique iwamig qui ya rutzil qui wach, ¿pa ʼutz wi ʼuri ile? Xak queje quiqui ʼan i winak chi n-quetaʼam tu wach i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '5.048', 'Yix, chom ix ʼolok, ronojel niʼpa i qui ʼano, xak jun ruʼ pacha cu ʼan i Kajaw chi ʼo chicaj, chi lic n-ta cʼax che ronojel niʼpa i cu ʼano.
', '', '&lt;span class="verse" id="Matt_5_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Yix, chom ix ʼolok, ronojel niʼpa i qui ʼano, xak jun ruʼ pacha cu ʼan i Kajaw chi ʼo chicaj, chi lic n-ta cʼax che ronojel niʼpa i cu ʼano.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.001', 'We ʼo tak i ʼutz qui ʼano, cojbal-re u ʼij i Dios, mi ʼan ile chiquiwach i winak we xa quiwaj quix quilo. We queje qui ʼan ile, n-ta qui chʼac na ʼuri ruʼ i Ta chicaj.
', '', '&lt;h2&gt;I tak i ʼutz qui ʼano, mi ʼano xa sicʼbal que i winak&lt;/h2&gt;&lt;span class="verse" id="Matt_6_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;We ʼo tak i ʼutz qui ʼano, cojbal-re u ʼij i Dios, mi ʼan ile chiquiwach i winak we xa quiwaj quix quilo. We queje qui ʼan ile, n-ta qui chʼac na ʼuri ruʼ i Ta chicaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.002', 'Rumal-i, are que i to i nibaʼib, aliʼal chi ʼana, man n-sicʼbal ta que i winak qui ʼano. Xa are sicʼbal que i winak quiqui ʼan jujun, chi lic je re i Dios quiwil yix, xui-ri, n-katzij taj. Queje quiqui ʼan ique ile pa tak molbalʼib, xak pa tak be. Are quiqui ʼan tak i ʼutz, xa are cacaj ca bix ni ʼutz chiquij. Chitapeʼ, xa are ile quiqui chʼaco, chi que ʼiltaj cumal i winak. ', '', '&lt;span class="verse" id="Matt_6_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Rumal-i, are que i to i nibaʼib, aliʼal chi ʼana, man n-sicʼbal ta que i winak qui ʼano. Xa are sicʼbal que i winak quiqui ʼan jujun, chi lic je re i Dios quiwil yix, xui-ri, n-katzij taj. Queje quiqui ʼan ique ile pa tak molbalʼib, xak pa tak be. Are quiqui ʼan tak i ʼutz, xa are cacaj ca bix ni ʼutz chiquij. Chitapeʼ, xa are ile quiqui chʼaco, chi que ʼiltaj cumal i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.003', 'Yix, are que i to i nibaʼib, chi ʼana che chi mi jun retaʼam wach i ʼanom, xak mi ne iwachalal. ', '', '&lt;span class="verse" id="Matt_6_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Yix, are que i to i nibaʼib, chi ʼana che chi mi jun retaʼam wach i ʼanom, xak mi ne iwachalal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.004', 'Aliʼal chi ʼana; teʼuri i Kajaw yix chi xrilo wach xi ʼano aliʼal, cu ya ni ʼutz piwi.
', '', '&lt;span class="verse" id="Matt_6_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Aliʼal chi ʼana; teʼuri i Kajaw yix chi xrilo wach xi ʼano aliʼal, cu ya ni ʼutz piwi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.005', 'Yix, are qui tzʼonoj che i Dios, mi ʼano pacha quiqui ʼan i jujun chi lic je re i Dios quiwil yix, xui-ri n-katzij taj. Ique, kus quiquilo we je tacʼal chiquiwach i winak are quiqui tzʼonoj che i Dios. Queje quiqui ʼan ile pa tak ja re i molbalʼib, xak i xol tak be, man cacaj ique que ʼiltaj na cumal i winak. Chitapeʼ, xa are ile quiqui chʼaco, chi que ʼiltaj cumal i winak. ', '', '&lt;h2&gt;I Jesus xu cʼutu wach u tzʼonoxic che i Dios caka ʼano&lt;/h2&gt;&lt;span class="verse" id="Matt_6_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Yix, are qui tzʼonoj che i Dios, mi ʼano pacha quiqui ʼan i jujun chi lic je re i Dios quiwil yix, xui-ri n-katzij taj. Ique, kus quiquilo we je tacʼal chiquiwach i winak are quiqui tzʼonoj che i Dios. Queje quiqui ʼan ile pa tak ja re i molbalʼib, xak i xol tak be, man cacaj ique que ʼiltaj na cumal i winak. Chitapeʼ, xa are ile quiqui chʼaco, chi que ʼiltaj cumal i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.006', 'Yet, are ca tzʼonoj che i Dios, chat oc bi pa ja y cha tzʼapij uchija. Teʼuri cha tzʼonoj che a Kajaw chi ʼo chila awuʼ, xui-ri, n-ca ʼiltaj tu wach. Y a Kajaw chi xrilo wach xa ʼano atuquel, cu ya ni ʼutz pawi.
', '', '&lt;span class="verse" id="Matt_6_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Yet, are ca tzʼonoj che i Dios, chat oc bi pa ja y cha tzʼapij uchija. Teʼuri cha tzʼonoj che a Kajaw chi ʼo chila awuʼ, xui-ri, n-ca ʼiltaj tu wach. Y a Kajaw chi xrilo wach xa ʼano atuquel, cu ya ni ʼutz pawi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.007', 'Yix, are qui tzʼonoj che i Dios, mi bij uqʼuial tzij chi toʼ tzij, n-tu chac. Queje quiqui ʼan ique ile chi n-quetaʼam tu wach i Dios. Quiqui bij ique chi cu ta i Dios man qʼui i tzij quiqui bij che. ', '', '&lt;span class="verse" id="Matt_6_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Yix, are qui tzʼonoj che i Dios, mi bij uqʼuial tzij chi toʼ tzij, n-tu chac. Queje quiqui ʼan ique ile chi n-quetaʼam tu wach i Dios. Quiqui bij ique chi cu ta i Dios man qʼui i tzij quiqui bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.008', 'Mi ʼan yix pacha quiqui ʼan ique; i Ta chicaj retaʼam wach i rajwaxic chiwe are maja qui tzʼonoj che. ', '', '&lt;span class="verse" id="Matt_6_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Mi ʼan yix pacha quiqui ʼan ique; i Ta chicaj retaʼam wach i rajwaxic chiwe are maja qui tzʼonoj che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.009', 'Rumal-i, queje iri chi tzʼonoj che:
Ka Kajaw chicaj, chocsax u ʼij a bi.
', '', '&lt;span class="verse" id="Matt_6_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Rumal-i, queje iri chi tzʼonoj che:
Ka Kajaw chicaj, chocsax u ʼij a bi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.010', 'Chapetsax a ʼatbal tzij,
cha ʼana na waral chuwach i jyub taʼaj, xak jun ruʼ pacha i ca ʼani chila chicaj.
', '', '&lt;span class="verse" id="Matt_6_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Chapetsax a ʼatbal tzij,
cha ʼana na waral chuwach i jyub taʼaj, xak jun ruʼ pacha i ca ʼani chila chicaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.011', 'Cha ya na chake wach i caka tijo, wach i rajwaxic chake.
', '', '&lt;span class="verse" id="Matt_6_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Cha ya na chake wach i caka tijo, wach i rajwaxic chake.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.012', 'Cha cuyu ni ka mac, xak jun ruʼ pacha ka ʼanom yoj, chi ka cuyum ni qui mac niʼpa i je macun-nak chikij.
', '', '&lt;span class="verse" id="Matt_6_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Cha cuyu ni ka mac, xak jun ruʼ pacha ka ʼanom yoj, chi ka cuyum ni qui mac niʼpa i je macun-nak chikij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.013', 'Ma ya chake coj tzak chupam i mac atzalal, xui-ri choj awesaj puʼab itzel.
Man yet ʼo i ʼatbal tzij paʼab; nim a choʼab, xak nim a ʼij a chomal.
At queje ile queʼe ʼij sak chawij. Amen.
', '', '&lt;span class="verse" id="Matt_6_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ma ya chake coj tzak chupam i mac atzalal, xui-ri choj awesaj puʼab itzel.
Man yet ʼo i ʼatbal tzij paʼab; nim a choʼab, xak nim a ʼij a chomal.
At queje ile queʼe ʼij sak chawij. Amen.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.014', '(Queje ile chi tzʼonoj che i Dios.) Man yix, we i cuyum chic qui mac i juban chi qui ʼanom qui mac chiwe, xak i Kajaw yix chila chicaj xak cu cuy na i mac yix. ', '', '&lt;span class="verse" id="Matt_6_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;(Queje ile chi tzʼonoj che i Dios.) Man yix, we i cuyum chic qui mac i juban chi qui ʼanom qui mac chiwe, xak i Kajaw yix chila chicaj xak cu cuy na i mac yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.015', 'Xui-ri, we yix n-qui cuy ti qui mac i juban, xak ire n-cu cuy ta i mac yix.
', '', '&lt;span class="verse" id="Matt_6_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xui-ri, we yix n-qui cuy ti qui mac i juban, xak ire n-cu cuy ta i mac yix.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.016', 'Yix, ʼolic quiwoʼtaj i waʼim qui yabej-re iwib chu tzʼonoxíc che i Dios. Are qui ʼan ile, mi ʼan che i palaj lic ca bisonic pacha quiqui ʼan i winak chi queb qui palaj. Queje quiqui ʼan ique ile, man quiquil i winak chi quiqui tij uyej rumal qui tzʼonoxic che i Dios. Chitapeʼ, xa are ile quiqui chʼaco chi que ʼiltaj cumal i winak.
', '', '&lt;h2&gt;I Jesus xu cʼutu wach i roʼtaxic ka waʼim caka ʼano&lt;/h2&gt;&lt;span class="verse" id="Matt_6_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Yix, ʼolic quiwoʼtaj i waʼim qui yabej-re iwib chu tzʼonoxíc che i Dios. Are qui ʼan ile, mi ʼan che i palaj lic ca bisonic pacha quiqui ʼan i winak chi queb qui palaj. Queje quiqui ʼan ique ile, man quiquil i winak chi quiqui tij uyej rumal qui tzʼonoxic che i Dios. Chitapeʼ, xa are ile quiqui chʼaco chi que ʼiltaj cumal i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.017', 'Xui-ri, yix, are quiwoʼtaj i waʼim chu tzʼonoxic che i Dios, chi chʼaja i palaj, chi xiya i wi, ', '', '&lt;span class="verse" id="Matt_6_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xui-ri, yix, are quiwoʼtaj i waʼim chu tzʼonoxic che i Dios, chi chʼaja i palaj, chi xiya i wi, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.018', 'man n-quiquil ti winak wach quix tijin chu ʼanic chuwach i Dios. I Kajaw chi n-caʼiltaj tu wach, ire crilo wach quix tijin chu ʼanic; cu ya na ʼuri i ʼutz piwi.
', '', '&lt;span class="verse" id="Matt_6_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;man n-quiquil ti winak wach quix tijin chu ʼanic chuwach i Dios. I Kajaw chi n-caʼiltaj tu wach, ire crilo wach quix tijin chu ʼanic; cu ya na ʼuri i ʼutz piwi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.019', 'Mi rayij i beyomal waral chuwach i jyub taʼaj, man xa ca pocʼaric, ca pusiric, xak caquelʼaj iliʼomab chiwe. ', '', '&lt;h2&gt;Chaka tzucuj ka beyomal chila chicaj&lt;/h2&gt;&lt;span class="verse" id="Matt_6_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Mi rayij i beyomal waral chuwach i jyub taʼaj, man xa ca pocʼaric, ca pusiric, xak caquelʼaj iliʼomab chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.020', 'Are i chi ʼana, chi molo u chiʼ i beyomal chila chicaj, man i chila, n-ca pocʼar taj, xak n-ca pusir taj, xak n-que oc ti iliʼom ruʼ quiqui maj chiwe. ', '', '&lt;span class="verse" id="Matt_6_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are i chi ʼana, chi molo u chiʼ i beyomal chila chicaj, man i chila, n-ca pocʼar taj, xak n-ca pusir taj, xak n-que oc ti iliʼom ruʼ quiqui maj chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.021', 'Chi cʼolo i beyomal chila chicaj, man pa i cʼolom wi i beyomal, chila ʼuri ʼo wi u cubibal i cʼux.
', '', '&lt;span class="verse" id="Matt_6_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Chi cʼolo i beyomal chila chicaj, man pa i cʼolom wi i beyomal, chila ʼuri ʼo wi u cubibal i cʼux.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.022', '(Chi ta u be i tzij-i:) Rumal i baʼwach, coc i sak che i chʼacul. We ʼutz i baʼwach, ʼo ʼuri i chʼacul pa sak. ', '', '&lt;span class="verse" id="Matt_6_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;(Chi ta u be i tzij-i:) Rumal i baʼwach, coc i sak che i chʼacul. We ʼutz i baʼwach, ʼo ʼuri i chʼacul pa sak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.023', 'Xui-ri, we ix potzʼ, ʼo ʼuri i chʼacul pa ekum. We qui bij yix chi ʼo i sak pi cʼux, xui-ri, we i sak chi ʼo pi cʼux xa ʼekum, lic ʼek ʼuri i ʼekumal-le.
', '', '&lt;span class="verse" id="Matt_6_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xui-ri, we ix potzʼ, ʼo ʼuri i chʼacul pa ekum. We qui bij yix chi ʼo i sak pi cʼux, xui-ri, we i sak chi ʼo pi cʼux xa ʼekum, lic ʼek ʼuri i ʼekumal-le.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.024', 'Mi jun ca tiqui che, we je ʼo queb u patron ca chacun cuʼ. Queʼelok xa jun cʼax cu naʼo, y jun tzel crilo. Xa jun, ʼutz u ʼanic u chac coʼon che; y che i jun, n-kas ta coʼon cas che. N-quix tiqui ta che, we qui ya iwib chu ʼonquil u chac i Dios, xak qui ya iwib chu molic i mer.
', '', '&lt;h2&gt;N-qui rik ti Dios we xa are qui tzucuj re u wach i jyub taʼaj&lt;/h2&gt;&lt;span class="verse" id="Matt_6_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Mi jun ca tiqui che, we je ʼo queb u patron ca chacun cuʼ. Queʼelok xa jun cʼax cu naʼo, y jun tzel crilo. Xa jun, ʼutz u ʼanic u chac coʼon che; y che i jun, n-kas ta coʼon cas che. N-quix tiqui ta che, we qui ya iwib chu ʼonquil u chac i Dios, xak qui ya iwib chu molic i mer.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.025', 'Rumal-i, mix cʼachir che i cʼaslemal, wach i qui tijo, wach i qui tuʼu; xak mix cʼachir che i ʼuʼ, wach i qui cojo, chʼukbal re iwij. I cʼaslemal, ¿xataba xui i wa cajwax che? Xak i chʼacul, ¿xataba xui i ʼuʼ cajwax che? N-xui taj. ', '', '&lt;span class="verse" id="Matt_6_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Rumal-i, mix cʼachir che i cʼaslemal, wach i qui tijo, wach i qui tuʼu; xak mix cʼachir che i ʼuʼ, wach i qui cojo, chʼukbal re iwij. I cʼaslemal, ¿xataba xui i wa cajwax che? Xak i chʼacul, ¿xataba xui i ʼuʼ cajwax che? N-xui taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.026', 'Chi chʼobo ʼut wach quiqui ʼan i tzʼiquin chi que bin pa tew. Ique, n-ti quiqui tico, xak n-ti quiqui molo, xak n-ti quiqui cʼol pu cʼolbal-re. Tupu n-quiqui ʼan taj, i Kajaw yix chi ʼo chicaj que u tzuku. Y yix lic nim ni i ʼij chiquiwach ique. ', '', '&lt;span class="verse" id="Matt_6_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Chi chʼobo ʼut wach quiqui ʼan i tzʼiquin chi que bin pa tew. Ique, n-ti quiqui tico, xak n-ti quiqui molo, xak n-ti quiqui cʼol pu cʼolbal-re. Tupu n-quiqui ʼan taj, i Kajaw yix chi ʼo chicaj que u tzuku. Y yix lic nim ni i ʼij chiquiwach ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.027', 'We toʼ qui cʼachirsaj iwib, n-ta cu chacuj. Mi jun chiwe ca tiqui chu nimarsaxic u najtil rij, tupu lic ca cʼachir che.
', '', '&lt;span class="verse" id="Matt_6_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;We toʼ qui cʼachirsaj iwib, n-ta cu chacuj. Mi jun chiwe ca tiqui chu nimarsaxic u najtil rij, tupu lic ca cʼachir che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.028', '¿Wuchac quix cʼachir chu tzucuxic i ʼuʼ? Chi chʼobo ʼut wach quiqui ʼan i cotzʼij pa tak ʼes. Ique n-que cʼachir ta chu chʼaquic qui ʼuʼ, xak mi ne u yijbaxic. ', '', '&lt;span class="verse" id="Matt_6_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;¿Wuchac quix cʼachir chu tzucuxic i ʼuʼ? Chi chʼobo ʼut wach quiqui ʼan i cotzʼij pa tak ʼes. Ique n-que cʼachir ta chu chʼaquic qui ʼuʼ, xak mi ne u yijbaxic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.029', 'Tupu n-quiqui ʼan taj, lic chom qui ʼuʼ. Quin bij chiwe, chom ni qui ʼuʼ ique chuwach i u ʼuʼ i nim laj ʼatol tzij mam Salomon ujer chi lic beyom. ', '', '&lt;span class="verse" id="Matt_6_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Tupu n-quiqui ʼan taj, lic chom qui ʼuʼ. Quin bij chiwe, chom ni qui ʼuʼ ique chuwach i u ʼuʼ i nim laj ʼatol tzij mam Salomon ujer chi lic beyom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.030', 'Queje coʼon i Dios ile che i cotzʼij pa tak ʼes, chi xa queb oxib ʼij ʼolic: woʼor cʼaslic, y chwek cʼatnak chic pa ʼaʼ. We ire cu ya i chom laj u ʼuʼ i ral cu cotzʼij-le, ¿xataba n-cu ya ta ʼuri iwe yix? Cu yaʼo pues. ¡Yix cʼo i rajwaxic che u cubibal i cʼux che i Dios! ', '', '&lt;span class="verse" id="Matt_6_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Queje coʼon i Dios ile che i cotzʼij pa tak ʼes, chi xa queb oxib ʼij ʼolic: woʼor cʼaslic, y chwek cʼatnak chic pa ʼaʼ. We ire cu ya i chom laj u ʼuʼ i ral cu cotzʼij-le, ¿xataba n-cu ya ta ʼuri iwe yix? Cu yaʼo pues. ¡Yix cʼo i rajwaxic che u cubibal i cʼux che i Dios! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.031', 'We queje coʼon i Dios ile, mix cʼachir ʼuri; mi bij ʼut, “¿Wach tak caka tijo? o ¿wach tak caka tuʼu? o ¿wach tak u cojic ka ʼuʼ?” ', '', '&lt;span class="verse" id="Matt_6_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;We queje coʼon i Dios ile, mix cʼachir ʼuri; mi bij ʼut, “¿Wach tak caka tijo? o ¿wach tak caka tuʼu? o ¿wach tak u cojic ka ʼuʼ?” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.032', 'Niʼpa tak ile, are i quiqui tzucuj i toʼ winak. Péro yix, ʼo jun i Kajaw chicaj lic retaʼam wach i rajwaxic chiwe. ', '', '&lt;span class="verse" id="Matt_6_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Niʼpa tak ile, are i quiqui tzucuj i toʼ winak. Péro yix, ʼo jun i Kajaw chicaj lic retaʼam wach i rajwaxic chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.033', 'Rumal-i, chi ya iwib che i ʼutz laj ʼatbal tzij re i Dios; chi ya iwib chu ʼonquil wach craj ire; teʼuri ire cu ya na chiwe wach tak i rajwaxic chiwe. ', '', '&lt;span class="verse" id="Matt_6_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Rumal-i, chi ya iwib che i ʼutz laj ʼatbal tzij re i Dios; chi ya iwib chu ʼonquil wach craj ire; teʼuri ire cu ya na chiwe wach tak i rajwaxic chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '6.034', 'Mix cʼachir tan rumal wach i qui rik na chwek. Katzij, chwek mok ʼo ni pen caka riko, xui-ri, mix cʼachir tan chu chʼobic woʼor; xui chi chʼobo i re woʼor.
', '', '&lt;span class="verse" id="Matt_6_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Mix cʼachir tan rumal wach i qui rik na chwek. Katzij, chwek mok ʼo ni pen caka riko, xui-ri, mix cʼachir tan chu chʼobic woʼor; xui chi chʼobo i re woʼor.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.001', 'Mi ʼat tzij piquiwi iwachiʼil, queje ile, i Dios n-cu ʼat ti tzij ʼuri piwi yix. ', '', '&lt;h2&gt;Ma ʼat tzij chirij jun chic&lt;/h2&gt;&lt;span class="verse" id="Matt_7_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Mi ʼat tzij piquiwi iwachiʼil, queje ile, i Dios n-cu ʼat ti tzij ʼuri piwi yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.002', 'Mi ʼano, man we cʼax u ʼatic tzij qui ʼano, xak cʼax ʼuri u ʼatic tzij coʼon i Dios piwi yix; ire xak jun u ʼonquil coʼono, pacha u ʼonquil xi ʼan yix. ', '', '&lt;span class="verse" id="Matt_7_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Mi ʼano, man we cʼax u ʼatic tzij qui ʼano, xak cʼax ʼuri u ʼatic tzij coʼon i Dios piwi yix; ire xak jun u ʼonquil coʼono, pacha u ʼonquil xi ʼan yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.003', '¿Wuchac cat cʼachiric rumal i ral tzʼil ca takej chupam u baʼwach awachiʼil, y n-ca na taj ʼo jun nim laj ʼet chupam a baʼwach yet? ', '', '&lt;span class="verse" id="Matt_7_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;¿Wuchac cat cʼachiric rumal i ral tzʼil ca takej chupam u baʼwach awachiʼil, y n-ca na taj ʼo jun nim laj ʼet chupam a baʼwach yet? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.004', '¿Wuchac ʼuri ca bij che awachiʼil: “Cha ya a baʼwach chwe, cwesaj i tzʼil ʼo chupam,” cat cha, we ʼo jun ʼet chupam a baʼwach yet? ', '', '&lt;span class="verse" id="Matt_7_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;¿Wuchac ʼuri ca bij che awachiʼil: “Cha ya a baʼwach chwe, cwesaj i tzʼil ʼo chupam,” cat cha, we ʼo jun ʼet chupam a baʼwach yet? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.005', 'Yet, ¡n-katzij ti ca ʼano! Nabe chawesaj ni ʼet pa baʼwach yet, teʼuri cat tzun bien cawesaj ni tzʼil ʼo chupam u baʼwach awachiʼil.
', '', '&lt;span class="verse" id="Matt_7_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Yet, ¡n-katzij ti ca ʼano! Nabe chawesaj ni ʼet pa baʼwach yet, teʼuri cat tzun bien cawesaj ni tzʼil ʼo chupam u baʼwach awachiʼil.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.006', 'Mi ya chique i tzʼi wach tak i ʼo u ʼij, man mokxa i tzʼi quiqui cʼak quib chiwij y quix qui raxij. Xak mi ya jun chom laj uwaj chiquiwach i ak, mokxa que oc chu yaʼyaxic.
', '', '&lt;span class="verse" id="Matt_7_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Mi ya chique i tzʼi wach tak i ʼo u ʼij, man mokxa i tzʼi quiqui cʼak quib chiwij y quix qui raxij. Xak mi ya jun chom laj uwaj chiquiwach i ak, mokxa que oc chu yaʼyaxic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.007', 'We ʼo rajwaxic chiwe, chi tzʼonoj che i Dios y Ire cu ya na chiwe; chi tzucuj, y qui rik na; chi cʼojcʼa i porta, y ca jaki na chiwe. ', '', '&lt;h2&gt;Dios cu ya ni ʼutz chique i racʼal chi lic qui tzʼonom che&lt;/h2&gt;&lt;span class="verse" id="Matt_7_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;We ʼo rajwaxic chiwe, chi tzʼonoj che i Dios y Ire cu ya na chiwe; chi tzucuj, y qui rik na; chi cʼojcʼa i porta, y ca jaki na chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.008', 'Queje ile chi ʼana, man we jun lic cu tzʼonoj, ca yaʼ che; we lic cu tzucuj, cu rik na; we lic cu cʼojcʼa i porta, ca jaki na che. ', '', '&lt;span class="verse" id="Matt_7_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Queje ile chi ʼana, man we jun lic cu tzʼonoj, ca yaʼ che; we lic cu tzucuj, cu rik na; we lic cu cʼojcʼa i porta, ca jaki na che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.009', 'Yix chi ix kajawxel, we ʼo jun iwacʼal cu tzʼonoj u wa chiwe, ¿xataba qui ya jun abaj che, pu qʼuexel u wa? N-qui ya taj. ', '', '&lt;span class="verse" id="Matt_7_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Yix chi ix kajawxel, we ʼo jun iwacʼal cu tzʼonoj u wa chiwe, ¿xataba qui ya jun abaj che, pu qʼuexel u wa? N-qui ya taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.010', 'We xak cu tzʼonoj car chiwe, ¿xataba qui ya jun cumatz che, pu qʼuexel? Xak n-qui ya taj. ', '', '&lt;span class="verse" id="Matt_7_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;We xak cu tzʼonoj car chiwe, ¿xataba qui ya jun cumatz che, pu qʼuexel? Xak n-qui ya taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.011', 'Yix, chi toʼ ix winak, tupu n-qui ʼan ti ʼutz, xui-ri, lic qui ya ni ʼutz chique iwacʼal. I Kajaw chicaj, mas ʼutz coʼon na chuwach ile; lic cu ya ni ʼutz chique pachin i que tzʼonow che.
', '', '&lt;span class="verse" id="Matt_7_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Yix, chi toʼ ix winak, tupu n-qui ʼan ti ʼutz, xui-ri, lic qui ya ni ʼutz chique iwacʼal. I Kajaw chicaj, mas ʼutz coʼon na chuwach ile; lic cu ya ni ʼutz chique pachin i que tzʼonow che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.012', 'Chi ʼana chique i winak pacha i quiwaj yix quiqui ʼan na ique chiwe. Are ile are usucʼ i ʼatbal tzij u tzʼibam can i mam Moises, xak are i usucʼ i tzij qui bim can i ajbil u tzij i Dios ujer.
', '', '&lt;span class="verse" id="Matt_7_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Chi ʼana chique i winak pacha i quiwaj yix quiqui ʼan na ique chiwe. Are ile are usucʼ i ʼatbal tzij u tzʼibam can i mam Moises, xak are i usucʼ i tzij qui bim can i ajbil u tzij i Dios ujer.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.013', 'Je queb i be re i cʼaslemal: jun, xa pichʼ u wach y jun chic, nim u wach. Chix oc yix che i uchibe chi pichʼ u wach, man i uchibe chi nim u wach, xa pa ʼaʼ ca ʼe wi. Lic je qʼui que bin na che i be-le. ', '', '&lt;h2&gt;Chix oc che uchija chi xa pichʼ u wach&lt;/h2&gt;&lt;span class="verse" id="Matt_7_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Je queb i be re i cʼaslemal: jun, xa pichʼ u wach y jun chic, nim u wach. Chix oc yix che i uchibe chi pichʼ u wach, man i uchibe chi nim u wach, xa pa ʼaʼ ca ʼe wi. Lic je qʼui que bin na che i be-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.014', 'I uchibe, xak i be, chi coj u cʼam bi che i cʼaslemal sakil, xa pichʼ qui wach; ne te qʼui quiqui riko.
', '', '&lt;span class="verse" id="Matt_7_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I uchibe, xak i be, chi coj u cʼam bi che i cʼaslemal sakil, xa pichʼ qui wach; ne te qʼui quiqui riko.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.015', 'Chi ʼana cwent chique i que upon iwuʼ chi quiqui bij je ajbil u tzij i Dios; pent ne te are. ʼOlic xa je pacha mal laj tak utiw chi qui ʼanom che quib pacha je chij; lic je ʼutz quiwil yix. ', '', '&lt;h2&gt;Que iwetamaj na pachin je aj tʼoronel, rumal wach tak quiqui ʼano&lt;/h2&gt;&lt;span class="verse" id="Matt_7_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Chi ʼana cwent chique i que upon iwuʼ chi quiqui bij je ajbil u tzij i Dios; pent ne te are. ʼOlic xa je pacha mal laj tak utiw chi qui ʼanom che quib pacha je chij; lic je ʼutz quiwil yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.016', 'Que iwetamaj na, je bi chi winakil rumal wach tak i quiqui ʼano. Que iwetamaj na pacha retamaxic u wach i qʼuix. Iwetaʼam chi jun qʼuix n-ta úva cu wachij; xak i jun tzum-tzerek n-ti hígo  cu wachij. ', '', '&lt;span class="verse" id="Matt_7_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Que iwetamaj na, je bi chi winakil rumal wach tak i quiqui ʼano. Que iwetamaj na pacha retamaxic u wach i qʼuix. Iwetaʼam chi jun qʼuix n-ta úva cu wachij; xak i jun tzum-tzerek n-ti hígo  cu wachij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.017', 'Quiwil na bi chi winakil, man jun ʼutz laj cheʼ, niʼpa i u wach cu yaʼo ʼis ʼutz; xak jun cheʼ n-ʼus taj, n-ti u wach ʼutz cu yaʼo. ', '', '&lt;span class="verse" id="Matt_7_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Quiwil na bi chi winakil, man jun ʼutz laj cheʼ, niʼpa i u wach cu yaʼo ʼis ʼutz; xak jun cheʼ n-ʼus taj, n-ti u wach ʼutz cu yaʼo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.018', 'I jun cheʼ ʼutz, n-cu ya ti u wach chi n-ʼus taj; xak jun cheʼ n-ʼus taj n-ca tiqui ta chu yaʼic u wach ʼutz. ', '', '&lt;span class="verse" id="Matt_7_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I jun cheʼ ʼutz, n-cu ya ti u wach chi n-ʼus taj; xak jun cheʼ n-ʼus taj n-ca tiqui ta chu yaʼic u wach ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.019', 'Niʼpa i cheʼ chi n-ti u wach ʼutz cu yaʼo, ca cʼatzix na, y ca porox na pa ʼaʼ. ', '', '&lt;span class="verse" id="Matt_7_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Niʼpa i cheʼ chi n-ti u wach ʼutz cu yaʼo, ca cʼatzix na, y ca porox na pa ʼaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.020', 'Yix ʼut que iwetamaj na pachin i xa je aj tʼoronel; que iwetamaj na rumal wach quiqui ʼano.
', '', '&lt;span class="verse" id="Matt_7_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Yix ʼut que iwetamaj na pachin i xa je aj tʼoronel; que iwetamaj na rumal wach quiqui ʼano.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.021', 'Mok ʼo jun cu bij na, “Wajwal, Wajwal,” cu bij chwe, ire n-coc ta chicaj pa cu ʼat wi tzij i Dios we xa rumal ni ile chi cu bij ruʼ u chiʼ. Xui niʼpa i qui ʼanom pacha craj i Ta chicaj, xui ique que oc ruʼ. ', '', '&lt;h2&gt;Jun, n-rumal ta wach cu bij ruʼ u chiʼ cu rikbej na re i rocbal chicaj&lt;/h2&gt;&lt;span class="verse" id="Matt_7_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Mok ʼo jun cu bij na, “Wajwal, Wajwal,” cu bij chwe, ire n-coc ta chicaj pa cu ʼat wi tzij i Dios we xa rumal ni ile chi cu bij ruʼ u chiʼ. Xui niʼpa i qui ʼanom pacha craj i Ta chicaj, xui ique que oc ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.022', 'Are cu rik i qʼuisbal ʼij, je qʼui na quiqui bij chwe: “Kajwal, Kajwal, choj a to, man are yoj xoj cuxtaw a bi are xka bij u tzij i Dios chique i winak; are yoj xoj cuxtaw a bi are xe kesaj bi itzel tak tew chique i winak; are yoj xoj cuxtaw a bi are xka ʼan uqʼuial milágro.” (Queje ni ile quiqui bij chwe.) ', '', '&lt;span class="verse" id="Matt_7_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are cu rik i qʼuisbal ʼij, je qʼui na quiqui bij chwe: “Kajwal, Kajwal, choj a to, man are yoj xoj cuxtaw a bi are xka bij u tzij i Dios chique i winak; are yoj xoj cuxtaw a bi are xe kesaj bi itzel tak tew chique i winak; are yoj xoj cuxtaw a bi are xka ʼan uqʼuial milágro.” (Queje ni ile quiqui bij chwe.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.023', 'Yin quin bij na ʼuri chique: “Lic n-wetaʼam ti wach; ¡chix el chin wach, yix chi ix aj ʼanol mal!” (Queje ile quin bij na chique.)
', '', '&lt;span class="verse" id="Matt_7_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Yin quin bij na ʼuri chique: “Lic n-wetaʼam ti wach; ¡chix el chin wach, yix chi ix aj ʼanol mal!” (Queje ile quin bij na chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.024', 'Je niʼpa i que taw in tzij-i, we que cojon che, ʼo qui noʼoj ʼuri. Queje pacha jun achi, ʼo u noʼoj, xu cuba i rachoch pujaʼ abaj. ', '', '&lt;h2&gt;I queb ja: jun cubam puwi abaj y jun cubam puwi seneb&lt;/h2&gt;&lt;span class="verse" id="Matt_7_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Je niʼpa i que taw in tzij-i, we que cojon che, ʼo qui noʼoj ʼuri. Queje pacha jun achi, ʼo u noʼoj, xu cuba i rachoch pujaʼ abaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.025', 'Xoʼon panok, xpe i jab nim, lic xnimar i ya, xak xpe i tew, craj cu tzaksaj i ja pulew péro n-xtzak taj man cul pujaʼ abaj. ', '', '&lt;span class="verse" id="Matt_7_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xpe i jab nim, lic xnimar i ya, xak xpe i tew, craj cu tzaksaj i ja pulew péro n-xtzak taj man cul pujaʼ abaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.026', 'Xui-ri, je niʼpa i que taw in tzij, we n-que cojon ta che, n-ta qui noʼoj ʼuri. Queje pacha jun achi, n-tu noʼoj, xu cuba i rachoch xa puwi i seneb. ', '', '&lt;span class="verse" id="Matt_7_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Xui-ri, je niʼpa i que taw in tzij, we n-que cojon ta che, n-ta qui noʼoj ʼuri. Queje pacha jun achi, n-tu noʼoj, xu cuba i rachoch xa puwi i seneb. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.027', 'Xoʼon panok, xpe i jab; xnimar i ya; xak xpe i tew chirij i rachoch, y xtzak pulew. ¡Lic xoʼon mal!
', '', '&lt;span class="verse" id="Matt_7_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xpe i jab; xnimar i ya; xak xpe i tew chirij i rachoch, y xtzak pulew. ¡Lic xoʼon mal!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.028', 'Are xutzin i Jesus chu bixquil chique, i winak lic xqui bisoj wach i cʼutunic xoʼono. ', '', '&lt;span class="verse" id="Matt_7_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are xutzin i Jesus chu bixquil chique, i winak lic xqui bisoj wach i cʼutunic xoʼono. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '7.029', 'Are ca chʼaʼat cuʼ, ʼalaj ʼuri chi lic retaʼam usucʼ wach i xu bij; n-are ta pacha quiqui ʼan i toʼ je tijonel re i ʼatbal tzij.
', '', '&lt;span class="verse" id="Matt_7_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are ca chʼaʼat cuʼ, ʼalaj ʼuri chi lic retaʼam usucʼ wach i xu bij; n-are ta pacha quiqui ʼan i toʼ je tijonel re i ʼatbal tzij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.001', 'Are xkaj li Jesus tzam jyub, je qʼui i winak je teren chirij. ', '', '&lt;h2&gt;I Jesus xrutzirsaj jun achi ʼo yobil lépra che&lt;/h2&gt;&lt;span class="verse" id="Matt_8_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xkaj li Jesus tzam jyub, je qʼui i winak je teren chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.002', 'Are ʼuri xcʼun li jun achi, ʼo i tzel laj yobil lépra che. Ire xupon chuwach i Jesus y xuquiʼic. Xu bij che:
—Kajwal, we ta cawaj, cawesaj ni itzel cʼax chwe —xu bij.
', '', '&lt;span class="verse" id="Matt_8_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ʼuri xcʼun li jun achi, ʼo i tzel laj yobil lépra che. Ire xupon chuwach i Jesus y xuquiʼic. Xu bij che:&lt;p&gt; &lt;/p&gt;—Kajwal, we ta cawaj, cawesaj ni itzel cʼax chwe —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.003', 'I Jesus xu chap ruʼ u ʼab, y xu bij che:
—Cwaj. Chat utzirok.
Are xu bij ile che, i achi xutziric. ', '', '&lt;span class="verse" id="Matt_8_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I Jesus xu chap ruʼ u ʼab, y xu bij che:&lt;p&gt; &lt;/p&gt;—Cwaj. Chat utzirok.&lt;p&gt; &lt;/p&gt;Are xu bij ile che, i achi xutziric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.004', 'I Jesus xu bij che:
—Chatapeʼ, mi jun ma lap wi. Xui-ri, jat, ja cʼutu awib che i sacerdóte chi at ʼutz chic; cha sujuj a sipon chuwach i Dios pacha u bim can i mam Moises. Queje cha ʼana ile man caquetamaj conojel chi at utzirnak chic, chi ya n-ta chic i chʼac chawij —xu bij che.
', '', '&lt;span class="verse" id="Matt_8_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Chatapeʼ, mi jun ma lap wi. Xui-ri, jat, ja cʼutu awib che i sacerdóte chi at ʼutz chic; cha sujuj a sipon chuwach i Dios pacha u bim can i mam Moises. Queje cha ʼana ile man caquetamaj conojel chi at utzirnak chic, chi ya n-ta chic i chʼac chawij —xu bij che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.005', 'Xoʼon chupanok, are xoc i Jesus pa tinimit Capernaum, jun capitan aj Róma xa ʼe chu cʼulaxic y xu tzʼonoj u tobal che.
', '', '&lt;h2&gt;I Jesus xrutzirsaj i u mocom jun capitan n-aj Israel taj&lt;/h2&gt;&lt;span class="verse" id="Matt_8_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, are xoc i Jesus pa tinimit Capernaum, jun capitan aj Róma xa ʼe chu cʼulaxic y xu tzʼonoj u tobal che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.006', '—Nim laj winak —xu bij che—, ʼo jun in mocom ruʼ ja, lic iwab. ʼO chuwi chʼat, lic siquirnak chic; lic cu tij uyej rumal u cʼaxcʼol —xu bij che.
', '', '&lt;span class="verse" id="Matt_8_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;—Nim laj winak —xu bij che—, ʼo jun in mocom ruʼ ja, lic iwab. ʼO chuwi chʼat, lic siquirnak chic; lic cu tij uyej rumal u cʼaxcʼol —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.007', 'I Jesus xu bij che i achi:
—Quin ʼe wutzirsaj —xu bij.
', '', '&lt;span class="verse" id="Matt_8_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che i achi:&lt;p&gt; &lt;/p&gt;—Quin ʼe wutzirsaj —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.008', '—Nim laj winak —xu bij i capitan che—, yet n-ca majaw ta chawe cat oc pa wachoch; n-in ti aj Israel winak. Xui cha bij panok: ¡Chutzirok! Teʼuri in mocom cutzir na. Wetaʼam ʼo paʼab yet u ʼanic ile. ', '', '&lt;span class="verse" id="Matt_8_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;—Nim laj winak —xu bij i capitan che—, yet n-ca majaw ta chawe cat oc pa wachoch; n-in ti aj Israel winak. Xui cha bij panok: ¡Chutzirok! Teʼuri in mocom cutzir na. Wetaʼam ʼo paʼab yet u ʼanic ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.009', 'Wetaʼam ile man xak yin in ʼo piquiʼab i je wajʼatzil; xak je ʼo jujun soldádo pinʼab yin. We “¡Jat!” quin bij che jun chique, ca ʼec; y we “¡Saʼaj!” quin bij che jun chic, ca petic. We xak quin tak bi wajchac, ire cu ʼan na wach i quin bij che. Wetaʼam chi xak ʼo paʼab yet cawesaj na bi itzel cʼax-le —xu bij i capitan.
', '', '&lt;span class="verse" id="Matt_8_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Wetaʼam ile man xak yin in ʼo piquiʼab i je wajʼatzil; xak je ʼo jujun soldádo pinʼab yin. We “¡Jat!” quin bij che jun chique, ca ʼec; y we “¡Saʼaj!” quin bij che jun chic, ca petic. We xak quin tak bi wajchac, ire cu ʼan na wach i quin bij che. Wetaʼam chi xak ʼo paʼab yet cawesaj na bi itzel cʼax-le —xu bij i capitan.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.010', 'Are xu ta i Jesus ile, lic xu bisoj.
—Chitapeʼ —xu bij chique i je teren chirij—, mi tene jun chique i aj Israel winak cul u cʼux chwe pacha oʼonom i achi-le. ', '', '&lt;span class="verse" id="Matt_8_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are xu ta i Jesus ile, lic xu bisoj.&lt;p&gt; &lt;/p&gt;—Chitapeʼ —xu bij chique i je teren chirij—, mi tene jun chique i aj Israel winak cul u cʼux chwe pacha oʼonom i achi-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.011', 'Lic quin bij chiwe, je qʼui i ne te aj Israel winak chi lic qui cubam qui cʼux chwe, que pe pa quel wi i ʼij, que pe pa ca kaj wi i ʼij, que oc na ʼuri pa cu ʼat wi tzij i Dios; quiqui tij ni ʼutz chila junam cuʼ i mam Abraham, i mam Isaac, i mam Jacob, i catit qui mam i aj Israel winak. ', '', '&lt;span class="verse" id="Matt_8_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Lic quin bij chiwe, je qʼui i ne te aj Israel winak chi lic qui cubam qui cʼux chwe, que pe pa quel wi i ʼij, que pe pa ca kaj wi i ʼij, que oc na ʼuri pa cu ʼat wi tzij i Dios; quiqui tij ni ʼutz chila junam cuʼ i mam Abraham, i mam Isaac, i mam Jacob, i catit qui mam i aj Israel winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.012', 'Xui-ri, je qʼui i aj Israel winak n-que oc ta chila cuʼ, tupu que ique i qʼuijibal-le. Ique-le, xa que yaʼ na pa ʼekumal; chila que oʼ wi, lic quiqui kachʼachʼej ni qui waque rumal qui bis —xu bij i Jesus chique i winak.
', '', '&lt;span class="verse" id="Matt_8_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xui-ri, je qʼui i aj Israel winak n-que oc ta chila cuʼ, tupu que ique i qʼuijibal-le. Ique-le, xa que yaʼ na pa ʼekumal; chila que oʼ wi, lic quiqui kachʼachʼej ni qui waque rumal qui bis —xu bij i Jesus chique i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.013', 'Are ʼuri xu bij che i capitan:
—ʼUtz cat ʼe che awachoch, y ca ʼani na che a mocom pacha a tzʼonom; queje ile queʼel na rumal u cubibal a cʼux chwe —xu bij i Jesus che.
Lic are ʼor ʼuri xutzir i u mocom i capitan.
', '', '&lt;span class="verse" id="Matt_8_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij che i capitan:&lt;p&gt; &lt;/p&gt;—ʼUtz cat ʼe che awachoch, y ca ʼani na che a mocom pacha a tzʼonom; queje ile queʼel na rumal u cubibal a cʼux chwe —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;Lic are ʼor ʼuri xutzir i u mocom i capitan.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.014', 'Xoʼon panok, xa ʼe i Jesus che i rachoch i ma Pédro. Are xoc paja, xrilo chi iwab u chuch u jiʼ i ma Pédro; ire cotzʼol chuwi chʼat, ʼo ʼaʼ chirij. ', '', '&lt;h2&gt;I Jesus xrutzirsaj u chuch u jiʼ i ma Pédro&lt;/h2&gt;&lt;span class="verse" id="Matt_8_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xa ʼe i Jesus che i rachoch i ma Pédro. Are xoc paja, xrilo chi iwab u chuch u jiʼ i ma Pédro; ire cotzʼol chuwi chʼat, ʼo ʼaʼ chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.015', 'I Jesus xa ʼe u yina u ʼab; are ʼuri, xchup i ʼaʼ chirij, xwalijic y xoc chu yijbaxic wach i quiqui tijo.
', '', '&lt;span class="verse" id="Matt_8_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I Jesus xa ʼe u yina u ʼab; are ʼuri, xchup i ʼaʼ chirij, xwalijic y xoc chu yijbaxic wach i quiqui tijo.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.016', 'Are xoc aʼab, je qʼui i winak xe qui cʼam li iwabib chuwach i Jesus. ʼO jujun ʼo itzel tew chique; i Jesus, xa ruʼ jun tzij xu bij, xe resaj bic; xak xe rutzirsaj niʼpa i iwabib. ', '', '&lt;h2&gt;Cʼax i terejic chirij i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_8_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are xoc aʼab, je qʼui i winak xe qui cʼam li iwabib chuwach i Jesus. ʼO jujun ʼo itzel tew chique; i Jesus, xa ruʼ jun tzij xu bij, xe resaj bic; xak xe rutzirsaj niʼpa i iwabib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.017', 'Are xoʼon ire ile, xeʼelok pacha u bim can i mam Isaías, chi ajbil u tzij i Dios ujer. Ire u bim canok chi i Jesus “Lic are ire aj ʼelsanel bi yobil chake; are ire aj ʼelsanel re i cʼax chake.” (Queje ile u tzʼibam canok.)
', '', '&lt;span class="verse" id="Matt_8_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are xoʼon ire ile, xeʼelok pacha u bim can i mam Isaías, chi ajbil u tzij i Dios ujer. Ire u bim canok chi i Jesus “Lic are ire aj ʼelsanel bi yobil chake; are ire aj ʼelsanel re i cʼax chake.” (Queje ile u tzʼibam canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.018', 'I Jesus, are xrilo je qʼui i winak je ʼo chirij chuwach, xu bij chique u tijoxelab:
—Joʼ, choj ʼax chʼakap re i alagun —xu bij.
', '', '&lt;span class="verse" id="Matt_8_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I Jesus, are xrilo je qʼui i winak je ʼo chirij chuwach, xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Joʼ, choj ʼax chʼakap re i alagun —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.019', 'Are ʼuri, ʼo jun tijonel re i ʼatbal tzij xtejeb ruʼ i Jesus.
—Mayes —xu bij che—, cwaj quin ʼe awuʼ xa ipa cat ʼe wi —xu bij.
', '', '&lt;span class="verse" id="Matt_8_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Are ʼuri, ʼo jun tijonel re i ʼatbal tzij xtejeb ruʼ i Jesus.&lt;p&gt; &lt;/p&gt;—Mayes —xu bij che—, cwaj quin ʼe awuʼ xa ipa cat ʼe wi —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.020', 'I Jesus xu bij che:
—Cʼax i terejic chwij. I yac ʼo qui jul, xak i tzʼiquin ʼo qui soc, xui-ri yin chi in Achi aj Chicaj, n-ta pa quin cotzʼba wi in jolom are quin waric —xu bij che.
', '', '&lt;span class="verse" id="Matt_8_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Cʼax i terejic chwij. I yac ʼo qui jul, xak i tzʼiquin ʼo qui soc, xui-ri yin chi in Achi aj Chicaj, n-ta pa quin cotzʼba wi in jolom are quin waric —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.021', 'Teʼuri ʼo jun chique i je teren chirij xu bij:
—Nim laj winak, cʼa te quin terej chawij we xcam ni in kajaw —xu bij.
', '', '&lt;span class="verse" id="Matt_8_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Teʼuri ʼo jun chique i je teren chirij xu bij:&lt;p&gt; &lt;/p&gt;—Nim laj winak, cʼa te quin terej chawij we xcam ni in kajaw —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.022', 'I Jesus xu bij che:
—Suʼ woʼor chat terej chwij. Niʼpa i je camnak, cha ya can piquiʼab ique quiqui chajij i que camic —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Matt_8_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Suʼ woʼor chat terej chwij. Niʼpa i je camnak, cha ya can piquiʼab ique quiqui chajij i que camic —xu bij i Jesus che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.023', 'Teʼuri i Jesus xoc pa bárco, je rachiʼil u tijoxelab. ', '', '&lt;h2&gt;I Jesus xu ʼatij i tew-jab&lt;/h2&gt;&lt;span class="verse" id="Matt_8_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc pa bárco, je rachiʼil u tijoxelab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.024', 'Xak teʼet xpe jun nim laj tew puwi i alagun; i u wi ya, lic quel chicaj, cʼa puwi i bárco. I Jesus, lic cwaric. ', '', '&lt;span class="verse" id="Matt_8_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xak teʼet xpe jun nim laj tew puwi i alagun; i u wi ya, lic quel chicaj, cʼa puwi i bárco. I Jesus, lic cwaric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.025', 'Are ʼuri i u tijoxelab xe ʼe qui cʼastaj.
—Kajwal —xqui bij che—, ¡Choj a to! ¡Ya coj muktaj pa ya!
', '', '&lt;span class="verse" id="Matt_8_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are ʼuri i u tijoxelab xe ʼe qui cʼastaj.&lt;p&gt; &lt;/p&gt;—Kajwal —xqui bij che—, ¡Choj a to! ¡Ya coj muktaj pa ya!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.026', 'I Jesus xu bij chique:
—¿Wuchac lic qui xij iwib? ¿N-ta u cubibal i cʼux chwe? —xu bij.
Are ʼuri, xtaqʼui ʼanok y xu jek u ʼatixic i tew, tak i ya chi quel chicaj; y xe uxlanic, n-que silab ta chic. ', '', '&lt;span class="verse" id="Matt_8_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac lic qui xij iwib? ¿N-ta u cubibal i cʼux chwe? —xu bij.&lt;p&gt; &lt;/p&gt;Are ʼuri, xtaqʼui ʼanok y xu jek u ʼatixic i tew, tak i ya chi quel chicaj; y xe uxlanic, n-que silab ta chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.027', 'I rachiʼil lic xqui bisoj wach i xoʼono.
—¿Bi chi achial i achi-le chi ʼo i milágro puʼab? —xqui bij chiquiwach—. Ire, i tew, xak i ya, que cojon che, wach i cu bij —que cha.
', '', '&lt;span class="verse" id="Matt_8_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I rachiʼil lic xqui bisoj wach i xoʼono.&lt;p&gt; &lt;/p&gt;—¿Bi chi achial i achi-le chi ʼo i milágro puʼab? —xqui bij chiquiwach—. Ire, i tew, xak i ya, que cojon che, wach i cu bij —que cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.028', 'Are ya xe ʼax chʼaka alagun, xe upon pa jyub Gadára. Are ʼuri xe ʼel li queb achiab pa tak mukbal que i camnak y xe upon ruʼ i Jesus. I queb-le, ʼo itzel tew chique, lic je chʼuj rumal; i winak lic quiqui xij quib chique, rumal-i, mi jun quicʼaw che i be-le. ', '', '&lt;h2&gt;I Jesus xe rutzirsaj queb achiab ʼo itzel tew chique&lt;/h2&gt;&lt;span class="verse" id="Matt_8_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are ya xe ʼax chʼaka alagun, xe upon pa jyub Gadára. Are ʼuri xe ʼel li queb achiab pa tak mukbal que i camnak y xe upon ruʼ i Jesus. I queb-le, ʼo itzel tew chique, lic je chʼuj rumal; i winak lic quiqui xij quib chique, rumal-i, mi jun quicʼaw che i be-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.029', 'Ique-le xqui jek i sicʼ y xqui bij:
—¿Wach col a tzucuj chi kuʼ yoj, Jesus, chi at u Cʼojol i Dios? ¿Xat cʼun na col oj a ya pa cʼax? ¡Maja na cu rik i ʼij! —xqui bij che.
', '', '&lt;span class="verse" id="Matt_8_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Ique-le xqui jek i sicʼ y xqui bij:&lt;p&gt; &lt;/p&gt;—¿Wach col a tzucuj chi kuʼ yoj, Jesus, chi at u Cʼojol i Dios? ¿Xat cʼun na col oj a ya pa cʼax? ¡Maja na cu rik i ʼij! —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.030', 'Nakaj che i lugar-le, je ʼo juban uqʼuial ak que tijin chi waʼim. ', '', '&lt;span class="verse" id="Matt_8_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Nakaj che i lugar-le, je ʼo juban uqʼuial ak que tijin chi waʼim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.031', 'Are ʼuri i itzel tew queje iri xqui tzʼonoj che i Jesus:
—We coj awesaj na chique i achiab, cha ya chake coj oc chique i ak-le —xe cha.
', '', '&lt;span class="verse" id="Matt_8_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Are ʼuri i itzel tew queje iri xqui tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;—We coj awesaj na chique i achiab, cha ya chake coj oc chique i ak-le —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.032', 'I Jesus xu bij:
—Jix ba chique.
Teʼuri i itzel tew xe ʼel chique achiab y xe oc chiquipam i ak. Xak teʼet conojel i ak xe kaj bi paʼanem, xe ʼe tzak pa ya y xe jiʼic.
', '', '&lt;span class="verse" id="Matt_8_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Jix ba chique.&lt;p&gt; &lt;/p&gt;Teʼuri i itzel tew xe ʼel chique achiab y xe oc chiquipam i ak. Xak teʼet conojel i ak xe kaj bi paʼanem, xe ʼe tzak pa ya y xe jiʼic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.033', 'Are xqui ʼan ile, i que chajiw i ak lic xqui bisoj. Xe ʼe paʼanem cʼa pa tinimit. Are xe upon chila, xe oc chu bixquil ronojel wach i xquilo; xqui bij wach i xa ʼani chique i queb achiab chi ʼo itzel tew chique. ', '', '&lt;span class="verse" id="Matt_8_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Are xqui ʼan ile, i que chajiw i ak lic xqui bisoj. Xe ʼe paʼanem cʼa pa tinimit. Are xe upon chila, xe oc chu bixquil ronojel wach i xquilo; xqui bij wach i xa ʼani chique i queb achiab chi ʼo itzel tew chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '8.034', 'Teʼuri conojel i winak re i tinimit xe ʼe chila paʼowi i Jesus. Are xquil u wach, lic xqui tzʼonoj che chi ca ʼec; n-cacaj ta ca canaj ire pa qui jyub ique.
', '', '&lt;span class="verse" id="Matt_8_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri conojel i winak re i tinimit xe ʼe chila paʼowi i Jesus. Are xquil u wach, lic xqui tzʼonoj che chi ca ʼec; n-cacaj ta ca canaj ire pa qui jyub ique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.001', 'Teʼuri i Jesus xoc chupam jun bárco y xa ʼax chic chʼaka alagun y xupon chic pu tinimit. ', '', '&lt;h2&gt;I Jesus xrutzirsaj jun achi sic&lt;/h2&gt;&lt;span class="verse" id="Matt_9_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chupam jun bárco y xa ʼax chic chʼaka alagun y xupon chic pu tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.002', 'Are ʼuri cʼamtal bi jun achi ruʼ, siquirnak u chʼacul, ʼo chuwa chʼat. Niʼpa i je cʼamawnak lok, lic cul qui cʼux che i Jesus. Ire, are xril ile, xu bij che i iwab:
—Wacʼal, cha cowij a cʼux; niʼpa i a mac ya cuytajnak chic —xu bij che.
', '', '&lt;span class="verse" id="Matt_9_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ʼuri cʼamtal bi jun achi ruʼ, siquirnak u chʼacul, ʼo chuwa chʼat. Niʼpa i je cʼamawnak lok, lic cul qui cʼux che i Jesus. Ire, are xril ile, xu bij che i iwab:&lt;p&gt; &lt;/p&gt;—Wacʼal, cha cowij a cʼux; niʼpa i a mac ya cuytajnak chic —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.003', 'Je ʼo jujun aj tijonel re i ʼatbal tzij je ʼo chila. Ique xqui bij chiquiwach: “I achi-ri cu kajsaj u ʼij i Dios ruʼ u tzij,” xqui bij. ', '', '&lt;span class="verse" id="Matt_9_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Je ʼo jujun aj tijonel re i ʼatbal tzij je ʼo chila. Ique xqui bij chiquiwach: “I achi-ri cu kajsaj u ʼij i Dios ruʼ u tzij,” xqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.004', 'I Jesus retaʼam chic wach que tijin chu chʼobic, rumal-i xu bij chique:
—¿Wuchac n-ʼus ta qui chʼobo? —xu bij chique—. ', '', '&lt;span class="verse" id="Matt_9_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus retaʼam chic wach que tijin chu chʼobic, rumal-i xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac n-ʼus ta qui chʼobo? —xu bij chique—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.005', 'Ile, n-cʼax tu bixquil chi “Ya xcuytaj a mac.” Are i cʼax u bixquil are iri: “Chat walijok, y chat binok,” are ile cʼax u ʼonquil che jun winak. ', '', '&lt;span class="verse" id="Matt_9_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Ile, n-cʼax tu bixquil chi “Ya xcuytaj a mac.” Are i cʼax u bixquil are iri: “Chat walijok, y chat binok,” are ile cʼax u ʼonquil che jun winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.006', 'Xui-ri, yin chi in Achi aj Chicaj, quin ʼalijinsaj ʼut chi ʼo pinʼab quin cuy ni qui mac i winak waral chuwach i jyub taʼaj —xu bij i Jesus chique. Teʼuri xu bij che i achi chi siquirnak:
—Chat walijok, cha yaca bi a soc, y jat che awachoch.
', '', '&lt;span class="verse" id="Matt_9_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xui-ri, yin chi in Achi aj Chicaj, quin ʼalijinsaj ʼut chi ʼo pinʼab quin cuy ni qui mac i winak waral chuwach i jyub taʼaj —xu bij i Jesus chique. Teʼuri xu bij che i achi chi siquirnak:&lt;p&gt; &lt;/p&gt;—Chat walijok, cha yaca bi a soc, y jat che awachoch.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.007', 'I achi sic xwalijic y xa ʼe che rachoch. ', '', '&lt;span class="verse" id="Matt_9_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I achi sic xwalijic y xa ʼe che rachoch. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.008', 'I winak, are xquilo, xe oc ʼil che y xqui coj u ʼij i Dios man u yom jun chique chi ʼo puʼab que rutzirsaj i winak, xak cresaj i qui mac.
', '', '&lt;span class="verse" id="Matt_9_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I winak, are xquilo, xe oc ʼil che y xqui coj u ʼij i Dios man u yom jun chique chi ʼo puʼab que rutzirsaj i winak, xak cresaj i qui mac.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.009', 'I Jesus xel chila; are ca tijin chi be, xril jun aj tzʼonol alcawal, ma Matéo u bi, chi cul pu qʼuijibal.
—Chat terej chwij —xu bij i Jesus che.
Ire xwalijic y xterej chirij.
', '', '&lt;h2&gt;I Jesus xu siqʼuij i ma Matéo&lt;/h2&gt;&lt;span class="verse" id="Matt_9_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I Jesus xel chila; are ca tijin chi be, xril jun aj tzʼonol alcawal, ma Matéo u bi, chi cul pu qʼuijibal.&lt;p&gt; &lt;/p&gt;—Chat terej chwij —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;Ire xwalijic y xterej chirij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.010', 'I Jesus xe ʼe junam cuʼ u tijoxelab pa rachoch i ma Matéo. Are que tijin chi waʼim, xak xe upon juban chic aj tzʼonol alcawal, xak juban chic ajmaquib, que ʼe qui tija qui wa cuʼ. ', '', '&lt;span class="verse" id="Matt_9_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I Jesus xe ʼe junam cuʼ u tijoxelab pa rachoch i ma Matéo. Are que tijin chi waʼim, xak xe upon juban chic aj tzʼonol alcawal, xak juban chic ajmaquib, que ʼe qui tija qui wa cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.011', 'ʼO jujun aj Fariséo winak xe quilo, y xqui bij chique u tijoxelab i Jesus:
—¿Wuchac i tijonel yix cu tij u wa cuʼ i mal laj tak aj tzʼonol alcawal, xak cuʼ ajmaquib? —xqui bij.
', '', '&lt;span class="verse" id="Matt_9_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;ʼO jujun aj Fariséo winak xe quilo, y xqui bij chique u tijoxelab i Jesus:&lt;p&gt; &lt;/p&gt;—¿Wuchac i tijonel yix cu tij u wa cuʼ i mal laj tak aj tzʼonol alcawal, xak cuʼ ajmaquib? —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.012', 'I Jesus xu ta ile chi xqui bij, y xu bij chique:
—Quin ʼan iri man je pacha iwabib. Are i je iwab cajwax jun ajcun chique; i ʼutz qui wach n-tu chac ajcun chique. ', '', '&lt;span class="verse" id="Matt_9_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I Jesus xu ta ile chi xqui bij, y xu bij chique:&lt;p&gt; &lt;/p&gt;—Quin ʼan iri man je pacha iwabib. Are i je iwab cajwax jun ajcun chique; i ʼutz qui wach n-tu chac ajcun chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.013', 'Je i ta u be bien wach usucʼ i tzij-i chi i tzʼibtal can chupam u wuj i Dios chi cu bij: “Are i cwaj yin are quiwil toʼbal qui wach i loʼcʼanij; n-are ti u sujuxic i chicop chinwach.” Queje ile u bim can i Dios —xu bij i Jesus chique—. Yin in cʼun-nak in aj siqʼuil que i ajmaquib, man quiqui jalwachij qui cʼaslemal. N-in ta cʼun-nak cumal i je ʼutz chuwach i Dios —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_9_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Je i ta u be bien wach usucʼ i tzij-i chi i tzʼibtal can chupam u wuj i Dios chi cu bij: “Are i cwaj yin are quiwil toʼbal qui wach i loʼcʼanij; n-are ti u sujuxic i chicop chinwach.” Queje ile u bim can i Dios —xu bij i Jesus chique—. Yin in cʼun-nak in aj siqʼuil que i ajmaquib, man quiqui jalwachij qui cʼaslemal. N-in ta cʼun-nak cumal i je ʼutz chuwach i Dios —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.014', 'Teʼuri i u tijoxelab i ma Wan aj kajsanel ya, xe upon ruʼ i Jesus y xqui bij che:
—Yoj, nic are wi caka ʼano, caka chʼij jun ʼij queb ʼij chuwach i Dios, n-ta wa caka tijo; xak queje quiqui ʼan i aj Fariséo. ¿Wuchac ʼuri n-queje ta quiqui ʼan i a tijoxelab yet? —xqui bij che.
', '', '&lt;h2&gt;I costúmbre re ujer n-cumaj ta rib ruʼ u cʼutunic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_9_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab i ma Wan aj kajsanel ya, xe upon ruʼ i Jesus y xqui bij che:&lt;p&gt; &lt;/p&gt;—Yoj, nic are wi caka ʼano, caka chʼij jun ʼij queb ʼij chuwach i Dios, n-ta wa caka tijo; xak queje quiqui ʼan i aj Fariséo. ¿Wuchac ʼuri n-queje ta quiqui ʼan i a tijoxelab yet? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.015', 'I Jesus xu bij ʼuri i tijojbal tzij-i chique:
—I winak, we que ʼe pa nimaʼij re jun achi ya ca cʼuliʼic, ¿xataba que bisonic? ¿xataba n-quiqui tij ti wa ruʼ? Quiqui tijo pues, tzʼakat ire ʼo cuʼ. Xui-ri, are cu rik i ʼij chi ʼelsam chic i achi chiquixol, teʼuri quiqui mayij qui nimaʼij.
', '', '&lt;span class="verse" id="Matt_9_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I Jesus xu bij ʼuri i tijojbal tzij-i chique:&lt;p&gt; &lt;/p&gt;—I winak, we que ʼe pa nimaʼij re jun achi ya ca cʼuliʼic, ¿xataba que bisonic? ¿xataba n-quiqui tij ti wa ruʼ? Quiqui tijo pues, tzʼakat ire ʼo cuʼ. Xui-ri, are cu rik i ʼij chi ʼelsam chic i achi chiquixol, teʼuri quiqui mayij qui nimaʼij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.016', '—Mi jun cu cʼojoj jun u ʼuʼ ʼel ruʼ jun chʼakap cʼojbal cʼacʼ, man i cʼojbal cʼacʼ xa ca tzajic y cu rakchʼij i cʼul ʼel; ruʼ ile coʼon mas nim i rakchʼijnak. ', '', '&lt;span class="verse" id="Matt_9_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;—Mi jun cu cʼojoj jun u ʼuʼ ʼel ruʼ jun chʼakap cʼojbal cʼacʼ, man i cʼojbal cʼacʼ xa ca tzajic y cu rakchʼij i cʼul ʼel; ruʼ ile coʼon mas nim i rakchʼijnak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.017', 'Xak queje ile, mi jun cu cʼol i víno cʼacʼ chupam i ʼel tak surum, man we queje quiqui ʼan ile, xa ca rakchʼij i surum. Queʼelok, coʼon mal i surum, y ca sach i víno. Rumal-i, ʼo u chac cocsax i víno cʼacʼ chupam i surum cʼacʼ; queje ile ʼutz queʼelic che queb —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_9_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xak queje ile, mi jun cu cʼol i víno cʼacʼ chupam i ʼel tak surum, man we queje quiqui ʼan ile, xa ca rakchʼij i surum. Queʼelok, coʼon mal i surum, y ca sach i víno. Rumal-i, ʼo u chac cocsax i víno cʼacʼ chupam i surum cʼacʼ; queje ile ʼutz queʼelic che queb —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.018', 'I Jesus, are ca tijin chu bixquil ile chique, xak teʼet xupon jun achi ruʼ, chi nim u patan chiquixol i aj Israel. Xuqui chuwach i Jesus cʼa chuwach ulew y xu bij che: —Cuymac, Ta, jun in miʼal cʼa te xcamic —coʼono—. Xui-ri, we ta cat ʼe yet wuʼ, xui ca ya a ʼab puwi y ca cʼun chic sak chuwach —xu bij.
', '', '&lt;h2&gt;I Jesus xu walijsaj jun ali camnak, xak xrutzirsaj jun ixok&lt;/h2&gt;&lt;span class="verse" id="Matt_9_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I Jesus, are ca tijin chu bixquil ile chique, xak teʼet xupon jun achi ruʼ, chi nim u patan chiquixol i aj Israel. Xuqui chuwach i Jesus cʼa chuwach ulew y xu bij che: —Cuymac, Ta, jun in miʼal cʼa te xcamic —coʼono—. Xui-ri, we ta cat ʼe yet wuʼ, xui ca ya a ʼab puwi y ca cʼun chic sak chuwach —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.019', 'I Jesus xwalijic y xe ʼe ruʼ, junam cuʼ u tijoxelab. ', '', '&lt;span class="verse" id="Matt_9_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Jesus xwalijic y xe ʼe ruʼ, junam cuʼ u tijoxelab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.020', 'Are que tijin chi be, ʼo jun ixok chiquixol i winak chi je teren chirij. I ixok-le, ʼax iwab wi; cablajuj junab ʼo chic che chi cril i riqʼuil congan; n-ca tani taj. Ire xtejeb pan chirij i Jesus y xu yin pan u chiʼ u ʼuʼ, ', '', '&lt;span class="verse" id="Matt_9_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are que tijin chi be, ʼo jun ixok chiquixol i winak chi je teren chirij. I ixok-le, ʼax iwab wi; cablajuj junab ʼo chic che chi cril i riqʼuil congan; n-ca tani taj. Ire xtejeb pan chirij i Jesus y xu yin pan u chiʼ u ʼuʼ, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.021', 'man xu chʼob pu cʼux: “Quin utzir na we tene quin yin pichʼ u ʼuʼ,” xu bij. ', '', '&lt;span class="verse" id="Matt_9_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;man xu chʼob pu cʼux: “Quin utzir na we tene quin yin pichʼ u ʼuʼ,” xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.022', 'Are xu chap u ʼuʼ, i Jesus xu naʼo; xu pisquilij rib, xu takej pan i ixok y xu bij che:
—Wacʼal, cha cowirsaj a cʼux; ya xat utziric rumal u cubibal a cʼux chwe —coʼono. Are ʼor ʼuri xutzir tan.
', '', '&lt;span class="verse" id="Matt_9_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are xu chap u ʼuʼ, i Jesus xu naʼo; xu pisquilij rib, xu takej pan i ixok y xu bij che:&lt;p&gt; &lt;/p&gt;—Wacʼal, cha cowirsaj a cʼux; ya xat utziric rumal u cubibal a cʼux chwe —coʼono. Are ʼor ʼuri xutzir tan.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.023', 'Xqui maj chubi qui be, y xe upon ruʼ rachoch i achi chi nim u patan. Xril i Jesus chi je yij chic i aj bixanel re i mukunic, xak niʼpa i winak ʼis que siqʼuin che oʼej, ', '', '&lt;span class="verse" id="Matt_9_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xqui maj chubi qui be, y xe upon ruʼ rachoch i achi chi nim u patan. Xril i Jesus chi je yij chic i aj bixanel re i mukunic, xak niʼpa i winak ʼis que siqʼuin che oʼej, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.024', 'xu bij ʼuri chique:
—Chix el chi, man i ali n-camnak taj, xa cwaric —xu bij chique.
I winak xa xpe qui tzej che. ', '', '&lt;span class="verse" id="Matt_9_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;xu bij ʼuri chique:&lt;p&gt; &lt;/p&gt;—Chix el chi, man i ali n-camnak taj, xa cwaric —xu bij chique.&lt;p&gt; &lt;/p&gt;I winak xa xpe qui tzej che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.025', 'N-ta coʼono, ire xe resaj bic; teʼuri xoc bi pa ja, y xu chap che u ʼab i ali; are ʼuri ire xwalijic. ', '', '&lt;span class="verse" id="Matt_9_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;N-ta coʼono, ire xe resaj bic; teʼuri xoc bi pa ja, y xu chap che u ʼab i ali; are ʼuri ire xwalijic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.026', 'Rumal i u ʼanom-le, weʼ xqui ta rason i winak ronojel i jyub-le, chi Jesus u walijsam i ali.
', '', '&lt;span class="verse" id="Matt_9_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Rumal i u ʼanom-le, weʼ xqui ta rason i winak ronojel i jyub-le, chi Jesus u walijsam i ali.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.027', 'Are xel i Jesus chila, je ʼo queb mawach xe terej chirij; xe oc chi sicʼ che.
—¡Nim laj winak, yet at umuk uxiquin i mam David, chawila toʼbal ka wach! —xqui bij.
', '', '&lt;h2&gt;I Jesus xe rutzirsaj je queb mawach&lt;/h2&gt;&lt;span class="verse" id="Matt_9_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are xel i Jesus chila, je ʼo queb mawach xe terej chirij; xe oc chi sicʼ che.&lt;p&gt; &lt;/p&gt;—¡Nim laj winak, yet at umuk uxiquin i mam David, chawila toʼbal ka wach! —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.028', 'Are xoc i Jesus ruʼ ja, xak i mawach xe tejeb pan ruʼ. Are ʼuri xu bij chique:
—¿Cul i cʼux chwe chi quin tiqui na che ile? —coʼono.
—Katzij cat tiquiʼic, nim laj winak —xe cha.
', '', '&lt;span class="verse" id="Matt_9_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are xoc i Jesus ruʼ ja, xak i mawach xe tejeb pan ruʼ. Are ʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Cul i cʼux chwe chi quin tiqui na che ile? —coʼono.&lt;p&gt; &lt;/p&gt;—Katzij cat tiquiʼic, nim laj winak —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.029', 'Teʼuri i Jesus xu yin i qui baʼwach, y xu bij chique,
—We qui na pi cʼux chi quix wutzirsaj, queje ca ʼan ni ile chiwe —xu bij chique.
', '', '&lt;span class="verse" id="Matt_9_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu yin i qui baʼwach, y xu bij chique,&lt;p&gt; &lt;/p&gt;—We qui na pi cʼux chi quix wutzirsaj, queje ca ʼan ni ile chiwe —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.030', 'Teʼuri i qui baʼwach xutzir na. I Jesus lic xu bij chique:
—Chi ʼana cwent, mi lap iri chique i winak, mi xa ta jubiʼ —coʼono.
', '', '&lt;span class="verse" id="Matt_9_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Teʼuri i qui baʼwach xutzir na. I Jesus lic xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ʼana cwent, mi lap iri chique i winak, mi xa ta jubiʼ —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.031', 'Xui-ri, are xe ʼel bic, xqui jek u bixquil chique conojel i winak re i jyub-le.
', '', '&lt;span class="verse" id="Matt_9_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xui-ri, are xe ʼel bic, xqui jek u bixquil chique conojel i winak re i jyub-le.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.032', 'Are xe ʼe ique, xe cʼun chal juban winak, cucʼam li jun achi mem. Memernak na rumal itzel tew ʼo che. ', '', '&lt;h2&gt;I Jesus xrutzirsaj jun mem&lt;/h2&gt;&lt;span class="verse" id="Matt_9_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Are xe ʼe ique, xe cʼun chal juban winak, cucʼam li jun achi mem. Memernak na rumal itzel tew ʼo che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.033', 'I Jesus xresaj bi itzel tew che, i achi juntir xu jek chʼawic. I winak lic xqui bisoj rumal. Xqui bij:
—Lic n-kilom ta pacha ile, mi tene ronojel i jyub Israel —xe cha.
', '', '&lt;span class="verse" id="Matt_9_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I Jesus xresaj bi itzel tew che, i achi juntir xu jek chʼawic. I winak lic xqui bisoj rumal. Xqui bij:&lt;p&gt; &lt;/p&gt;—Lic n-kilom ta pacha ile, mi tene ronojel i jyub Israel —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.034', 'Xui-ri, i aj Fariséo xqui bij:
—I achi-le cresaj i itzel tew chique i winak xa rumal u choʼab itzel ʼo ruʼ; i choʼab ʼo ruʼ-le, are i cajʼatzil itzel tew yawnak che —xe cha.
', '', '&lt;span class="verse" id="Matt_9_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Xui-ri, i aj Fariséo xqui bij:&lt;p&gt; &lt;/p&gt;—I achi-le cresaj i itzel tew chique i winak xa rumal u choʼab itzel ʼo ruʼ; i choʼab ʼo ruʼ-le, are i cajʼatzil itzel tew yawnak che —xe cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.035', 'I Jesus xe u solij ronojel tak i tinimit, ronojel tak i aldéa. ʼIs xoc pa tak molbalʼib que i aj Israel winak, xa ʼe u tijojbej que i winak chila; xa ʼe u paxsaj i ʼutz laj tzij chique i winak, xu bij chi petnak chic i u ʼatbal tzij i Dios chique. Xak xe rutzirsaj bi chi cʼaxil, bi chi yobil, ʼo chique. ', '', '&lt;h2&gt;I Jesus cʼax que u na i winak chi n-ta ca ucʼaw u tzam chique&lt;/h2&gt;&lt;span class="verse" id="Matt_9_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I Jesus xe u solij ronojel tak i tinimit, ronojel tak i aldéa. ʼIs xoc pa tak molbalʼib que i aj Israel winak, xa ʼe u tijojbej que i winak chila; xa ʼe u paxsaj i ʼutz laj tzij chique i winak, xu bij chi petnak chic i u ʼatbal tzij i Dios chique. Xak xe rutzirsaj bi chi cʼaxil, bi chi yobil, ʼo chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.036', 'Are xe ril i winak, lic cʼax xu na pu cʼux, man que cʼachiric, lic n-ta jun que towic; xe rilo pacha juban chij, n-ta que ucʼaw u tzam chique. ', '', '&lt;span class="verse" id="Matt_9_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Are xe ril i winak, lic cʼax xu na pu cʼux, man que cʼachiric, lic n-ta jun que towic; xe rilo pacha juban chij, n-ta que ucʼaw u tzam chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.037', 'Are ʼuri xu bij chique u tijoxelab:
—Lic ʼo i jachʼic craj; xui-ri, i ajchac ne te qʼui. ', '', '&lt;span class="verse" id="Matt_9_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Lic ʼo i jachʼic craj; xui-ri, i ajchac ne te qʼui. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '9.038', 'Rumal-i, chi tzʼonoj che i Rajaw i chac chi que u tak bi mas mocom pa jachʼ —xu bij chique.
', '', '&lt;span class="verse" id="Matt_9_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Rumal-i, chi tzʼonoj che i Rajaw i chac chi que u tak bi mas mocom pa jachʼ —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.001', 'Teʼuri i Jesus xe u siqʼuij i cablajuj tijoxelab ruʼ y xu ya piquiʼab que tiqui chi relsaxic itzel laj tak tew, xak que tiqui chi rutzirsaxic bi chi cʼaxil, bi chi yobil.
', '', '&lt;h2&gt;I cablajuj apostelab&lt;/h2&gt;&lt;span class="verse" id="Matt_10_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u siqʼuij i cablajuj tijoxelab ruʼ y xu ya piquiʼab que tiqui chi relsaxic itzel laj tak tew, xak que tiqui chi rutzirsaxic bi chi cʼaxil, bi chi yobil.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.002', 'Are i qui bi i cablajuj tijoxelab are iri: nabe, are i ma Simon chi xak quiqui bij “ma Pédro” che; teʼuri i u chaʼ, u bi ma Andres; teʼuri, je queb u cʼojol i mam Zebedéo: i ma Jacóbo, chilam i u chaʼ chi ma Wan; ', '', '&lt;span class="verse" id="Matt_10_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are i qui bi i cablajuj tijoxelab are iri: nabe, are i ma Simon chi xak quiqui bij “ma Pédro” che; teʼuri i u chaʼ, u bi ma Andres; teʼuri, je queb u cʼojol i mam Zebedéo: i ma Jacóbo, chilam i u chaʼ chi ma Wan; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.003', 'teʼuri i ma Felípe, xak i ma Bartolome; teʼuri i ma Tomas, xak i ma Matéo chi aj tzʼonol alcawal; teʼuri i ma Jacóbo chi u cʼojol i mam Alféo; i ma Lebéo chi xak ma Tadéo quiqui bij che; ', '', '&lt;span class="verse" id="Matt_10_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;teʼuri i ma Felípe, xak i ma Bartolome; teʼuri i ma Tomas, xak i ma Matéo chi aj tzʼonol alcawal; teʼuri i ma Jacóbo chi u cʼojol i mam Alféo; i ma Lebéo chi xak ma Tadéo quiqui bij che; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.004', 'teʼuri i jun chic ma Simon, are i jun chi re i partído cananísta; teʼuri i ma Júdas Iscarióte chi xoʼon panok xcʼayiw i Jesus.
', '', '&lt;span class="verse" id="Matt_10_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;teʼuri i jun chic ma Simon, are i jun chi re i partído cananísta; teʼuri i ma Júdas Iscarióte chi xoʼon panok xcʼayiw i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.005', 'I cablajuj-le, Jesus xe u tak bic, y xu bij bi chique:
—Are que i solij i tinimit, mix ʼe pa i je jekel wi i ne te aj Israel winak; xak mix oc pa tak tinimit re i Samária. ', '', '&lt;h2&gt;I Jesus xe u tak bi i rapostelab chu bixquil u tzij i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_10_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I cablajuj-le, Jesus xe u tak bic, y xu bij bi chique:&lt;p&gt; &lt;/p&gt;—Are que i solij i tinimit, mix ʼe pa i je jekel wi i ne te aj Israel winak; xak mix oc pa tak tinimit re i Samária. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.006', 'Xui cuʼ i aj Israel winak quix ʼe wi; je pacha juban chij, xa je sachnak. ', '', '&lt;span class="verse" id="Matt_10_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xui cuʼ i aj Israel winak quix ʼe wi; je pacha juban chij, xa je sachnak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.007', 'Are quix ʼec, chi paxsaj u tzijol i ʼatbal tzij aj chicaj; chi bij chique chi ya ca jekanic. ', '', '&lt;span class="verse" id="Matt_10_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are quix ʼec, chi paxsaj u tzijol i ʼatbal tzij aj chicaj; chi bij chique chi ya ca jekanic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.008', 'Che iwutzirsaj iwabib; che i walijsaj i je camnak; chiwesaj i cʼax chique niʼpa ʼo i yobil lépra chique; xak che iwesaj bi itzel tew chique i winak. Queje ile chi ʼana na. Lic sipam chiwe niʼpa quix tiqui yix chu ʼonquil, ni loʼom taj. Xak mi tzʼonoj i rajil, we xi ʼan i pawor chique i winak.
', '', '&lt;span class="verse" id="Matt_10_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Che iwutzirsaj iwabib; che i walijsaj i je camnak; chiwesaj i cʼax chique niʼpa ʼo i yobil lépra chique; xak che iwesaj bi itzel tew chique i winak. Queje ile chi ʼana na. Lic sipam chiwe niʼpa quix tiqui yix chu ʼonquil, ni loʼom taj. Xak mi tzʼonoj i rajil, we xi ʼan i pawor chique i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.009', 'Are quix ʼec, n-ti mer nimak rajil qui cʼam bic, xak mi ne chʼutik rajil. ', '', '&lt;span class="verse" id="Matt_10_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Are quix ʼec, n-ti mer nimak rajil qui cʼam bic, xak mi ne chʼutik rajil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.010', 'Xak mi cʼam bi i teb, xak mi ne jun chic i camix, xak mi ne jumolaj chic i xajab, xak mi ne i chʼamiy. Mi cʼam bi tak ile, man jun ajchac ʼo re che, wach i cajwax che; i winak quiqui ya na chiwe wach rajwaxic chiwe. ', '', '&lt;span class="verse" id="Matt_10_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xak mi cʼam bi i teb, xak mi ne jun chic i camix, xak mi ne jumolaj chic i xajab, xak mi ne i chʼamiy. Mi cʼam bi tak ile, man jun ajchac ʼo re che, wach i cajwax che; i winak quiqui ya na chiwe wach rajwaxic chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.011', 'Yix, are quix upon che jun tinimit, are quix upon che jun aldéa, chi tzucuj jun winak chi quix u cʼulaj che rachoch; chix jekel chila, cʼa te qui canaj canok are quix el chila. ', '', '&lt;span class="verse" id="Matt_10_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Yix, are quix upon che jun tinimit, are quix upon che jun aldéa, chi tzucuj jun winak chi quix u cʼulaj che rachoch; chix jekel chila, cʼa te qui canaj canok are quix el chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.012', 'Are quix oc ruʼ ja, chi tzʼonoj che i Dios cu ya i uxlambal qui cʼux. ', '', '&lt;span class="verse" id="Matt_10_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Are quix oc ruʼ ja, chi tzʼonoj che i Dios cu ya i uxlambal qui cʼux. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.013', 'I winak chi rajaw ja, we ʼutz i cʼulaxic xqui ʼan chiwe, ca qʼuiji i ʼutz-le piquiwi. We n-ʼus ta i cʼulaxic xqui ʼano, mi tzʼonoj i ʼutz re i Dios piquiwi are quix el ruʼ ja. ', '', '&lt;span class="verse" id="Matt_10_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I winak chi rajaw ja, we ʼutz i cʼulaxic xqui ʼan chiwe, ca qʼuiji i ʼutz-le piquiwi. We n-ʼus ta i cʼulaxic xqui ʼano, mi tzʼonoj i ʼutz re i Dios piquiwi are quix el ruʼ ja. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.014', 'Ique-le, we n-xix qui cʼulaj taj, xak n-cacaj ti u tayic i tzij chi qui bij chique, chix el ʼuri chila. Are quix el che i ja-le, o we xak che i tinimit-le, chi pu can i ulew che iwakan (cʼutbal-re chique chi cʼo qui mac chiquij). ', '', '&lt;span class="verse" id="Matt_10_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Ique-le, we n-xix qui cʼulaj taj, xak n-cacaj ti u tayic i tzij chi qui bij chique, chix el ʼuri chila. Are quix el che i ja-le, o we xak che i tinimit-le, chi pu can i ulew che iwakan (cʼutbal-re chique chi cʼo qui mac chiquij). &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.015', 'Katzij i quin bij chiwe: pa qʼuisbal ʼij, are ca ʼat tzij piquiwi tak i tinimit, mas nim na i tojpen ca pe piquiwi i tinimit-le chuwach ile chi xpe piquiwi i queb tinimit Sodóma y Gomórra ujer.
', '', '&lt;span class="verse" id="Matt_10_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe: pa qʼuisbal ʼij, are ca ʼat tzij piquiwi tak i tinimit, mas nim na i tojpen ca pe piquiwi i tinimit-le chuwach ile chi xpe piquiwi i queb tinimit Sodóma y Gomórra ujer.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.016', 'Chitapeʼ, yin quix in tak bi chiquixol i je aj chʼoʼoj chiwij; ix pacha chij ʼuri chiquixol i utiw. Chi ʼana cwent che iwib, pacha coʼon jun cumatz, n-cu ya ta rib che i jun. Xui-ri, chi ʼana che iwib pacha quiqui ʼan i palomax, lic n-ta jun mal quiqui ʼan che jun chic. ', '', '&lt;h2&gt;Ca pe chʼoʼoj chiquij i je re i Crísto&lt;/h2&gt;&lt;span class="verse" id="Matt_10_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Chitapeʼ, yin quix in tak bi chiquixol i je aj chʼoʼoj chiwij; ix pacha chij ʼuri chiquixol i utiw. Chi ʼana cwent che iwib, pacha coʼon jun cumatz, n-cu ya ta rib che i jun. Xui-ri, chi ʼana che iwib pacha quiqui ʼan i palomax, lic n-ta jun mal quiqui ʼan che jun chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.017', 'Chi ʼana cwent, man ʼo ni winak quix qui jach na piquiʼab i ʼatbal tzij, xak quiqui jic i loʼxic pa tak molbalʼib. ', '', '&lt;span class="verse" id="Matt_10_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Chi ʼana cwent, man ʼo ni winak quix qui jach na piquiʼab i ʼatbal tzij, xak quiqui jic i loʼxic pa tak molbalʼib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.018', 'Xak quix qui cʼam bi chiquiwach i gobernador, xak chiquiwach i nimak tak ʼatol tzij xa wumal yin. Xui-ri, queje ile quin i lap na chiquiwach ique, xak chiquiwach i ne te aj Israel. ', '', '&lt;span class="verse" id="Matt_10_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xak quix qui cʼam bi chiquiwach i gobernador, xak chiquiwach i nimak tak ʼatol tzij xa wumal yin. Xui-ri, queje ile quin i lap na chiquiwach ique, xak chiquiwach i ne te aj Israel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.019', 'Are quix jach na piquiʼab i ʼatbal tzij, mix cʼachir chu chʼobic wach i qui bij chique, xak mix cʼachir chu chʼobic wach u bixquil qui bij na. Are cu rik i ʼor-le, Dios cu bij na chiwe wach i qui bij chiquiwach. ', '', '&lt;span class="verse" id="Matt_10_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Are quix jach na piquiʼab i ʼatbal tzij, mix cʼachir chu chʼobic wach i qui bij chique, xak mix cʼachir chu chʼobic wach u bixquil qui bij na. Are cu rik i ʼor-le, Dios cu bij na chiwe wach i qui bij chiquiwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.020', 'Man yix, are quix chʼawic, n-are ti yix quix chʼawic, are u Tewal i Ta chicaj ca chʼaw chique.
', '', '&lt;span class="verse" id="Matt_10_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Man yix, are quix chʼawic, n-are ti yix quix chʼawic, are u Tewal i Ta chicaj ca chʼaw chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.021', 'Quiwil na, ʼo i winak que qui jach ni catz qui chaʼ piquiʼab i je aj chʼoʼoj chiquij, camsabal na que; xak je ʼo ni kajawxel queje quiqui ʼan cuʼ i cacʼal. Xak queje i acʼalab, quiqui ʼan che quib je aj chʼoʼoj chiquij i qui kajaw i qui chuch, que qui jach na chique i que camsawic. ', '', '&lt;span class="verse" id="Matt_10_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Quiwil na, ʼo i winak que qui jach ni catz qui chaʼ piquiʼab i je aj chʼoʼoj chiquij, camsabal na que; xak je ʼo ni kajawxel queje quiqui ʼan cuʼ i cacʼal. Xak queje i acʼalab, quiqui ʼan che quib je aj chʼoʼoj chiquij i qui kajaw i qui chuch, que qui jach na chique i que camsawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.022', 'Conojel i winak tzel quix quil na xa wumal yin: xui-ri pachin i cu chʼijo cʼa pa qʼuisbal ie, que ʼelsax na ʼuri pa cʼax. ', '', '&lt;span class="verse" id="Matt_10_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Conojel i winak tzel quix quil na xa wumal yin: xui-ri pachin i cu chʼijo cʼa pa qʼuisbal ie, que ʼelsax na ʼuri pa cʼax. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.023', 'We ix ʼo che jun tinimit y quiqui ʼan chʼoʼoj chiwij, chix ʼanmajok; jix chic che jun tinimit. Katzij i quin bij chiwe, i Achi aj Chicaj ca cʼun na, are maja quix tzalij li che i qui solixic ronojel tak i tinimit pa Israel.
', '', '&lt;span class="verse" id="Matt_10_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;We ix ʼo che jun tinimit y quiqui ʼan chʼoʼoj chiwij, chix ʼanmajok; jix chic che jun tinimit. Katzij i quin bij chiwe, i Achi aj Chicaj ca cʼun na, are maja quix tzalij li che i qui solixic ronojel tak i tinimit pa Israel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.024', '(Qui tij ni cʼax cumal, pacha quiqui ʼan chwe yin.) Jun tijoxel, na nim ti u ʼij chuwach u ʼij u mayes; xak queje jun ajchac, na nim ti u ʼij chuwach u ʼij u patron. ', '', '&lt;span class="verse" id="Matt_10_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;(Qui tij ni cʼax cumal, pacha quiqui ʼan chwe yin.) Jun tijoxel, na nim ti u ʼij chuwach u ʼij u mayes; xak queje jun ajchac, na nim ti u ʼij chuwach u ʼij u patron. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.025', 'Jun tijoxel, ca majaw che chi ca quicot ruʼ, we queʼel na che pacha oʼonom u tijonel; xak queje jun ajchac, ca majaw che chi ca quicot ruʼ we queʼel na che pacha oʼonom u patron. Jun rajaw ja, we quiqui bij ni winak chi ire itzel, xak jun ʼuri quiqui bij na ique chiquij i ralcʼwal. (We queje xqui bij ile chwe yin, xak queje quiqui bij na ʼuri chiwe yix.)
', '', '&lt;span class="verse" id="Matt_10_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Jun tijoxel, ca majaw che chi ca quicot ruʼ, we queʼel na che pacha oʼonom u tijonel; xak queje jun ajchac, ca majaw che chi ca quicot ruʼ we queʼel na che pacha oʼonom u patron. Jun rajaw ja, we quiqui bij ni winak chi ire itzel, xak jun ʼuri quiqui bij na ique chiquij i ralcʼwal. (We queje xqui bij ile chwe yin, xak queje quiqui bij na ʼuri chiwe yix.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.026', 'Rumal-i, mi xij iwib chique i winak, wach quiqui ʼan chiwe. (Chi ʼana co che i cʼux chu bixquil in tzij chique.) Niʼpa i n-ʼalijinsam taj, ca ʼalijinsax na; niʼpa i uwam u wach, ca ʼiltaj ni u wach. ', '', '&lt;h2&gt;Mi xij iwib chique i winak; are, chi xija iwib che i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_10_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Rumal-i, mi xij iwib chique i winak, wach quiqui ʼan chiwe. (Chi ʼana co che i cʼux chu bixquil in tzij chique.) Niʼpa i n-ʼalijinsam taj, ca ʼalijinsax na; niʼpa i uwam u wach, ca ʼiltaj ni u wach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.027', 'Niʼpa i quin bij chiwe chaʼab, chi paxsaj yix pa ʼij; niʼpa i quin bij chiwe aliʼal, chix siqʼuin chu bixquil chique i winak puwi tak ja. ', '', '&lt;span class="verse" id="Matt_10_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Niʼpa i quin bij chiwe chaʼab, chi paxsaj yix pa ʼij; niʼpa i quin bij chiwe aliʼal, chix siqʼuin chu bixquil chique i winak puwi tak ja. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.028', 'Mi xij iwib chique i que tiqui chu camsaxic i chʼacul, xui-ri i iwanima n-que tiqui ta che. Are, chi xija iwib che i Jun, chi ʼo puʼab cu sachsaj u wach i chʼacul xak i iwanima pa ʼaʼ. (Xui i Dios ʼo puʼab cu ʼan ile.)
', '', '&lt;span class="verse" id="Matt_10_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Mi xij iwib chique i que tiqui chu camsaxic i chʼacul, xui-ri i iwanima n-que tiqui ta che. Are, chi xija iwib che i Jun, chi ʼo puʼab cu sachsaj u wach i chʼacul xak i iwanima pa ʼaʼ. (Xui i Dios ʼo puʼab cu ʼan ile.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.029', 'Yix, i Dios lic quix rilo. Iwetaʼam chi are qui loʼ queb tzʼiquin xa jun centávo i cajil. N-ta kas cajil chiwach yix. Xui-ri, i Dios lic que u rilo; mi jun chique ca tzak li pulew we i Kajaw yix chicaj n-cu ya ta che. ', '', '&lt;span class="verse" id="Matt_10_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Yix, i Dios lic quix rilo. Iwetaʼam chi are qui loʼ queb tzʼiquin xa jun centávo i cajil. N-ta kas cajil chiwach yix. Xui-ri, i Dios lic que u rilo; mi jun chique ca tzak li pulew we i Kajaw yix chicaj n-cu ya ta che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.030', 'Xak queje yix, i Dios lic quix rilo. Ire xak retaʼam neri niʼpa i wi ʼo che i jolom chi jujunal. ', '', '&lt;span class="verse" id="Matt_10_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xak queje yix, i Dios lic quix rilo. Ire xak retaʼam neri niʼpa i wi ʼo che i jolom chi jujunal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.031', 'Mi xij iwib ʼuri; yix ʼut, mas ʼo ni ʼij chiquiwach uqʼuial tzʼiquin.
', '', '&lt;span class="verse" id="Matt_10_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Mi xij iwib ʼuri; yix ʼut, mas ʼo ni ʼij chiquiwach uqʼuial tzʼiquin.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.032', 'Conojel niʼpa i quiqui bij chiquiwach i winak chi ique qui yom qui wach wuʼ, xak queje yin ʼuri, quin bij na che in Kajaw chicaj chi je we yin. ', '', '&lt;h2&gt;ʼO u chac caka bij na chique i winak we oj re i Crísto&lt;/h2&gt;&lt;span class="verse" id="Matt_10_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Conojel niʼpa i quiqui bij chiquiwach i winak chi ique qui yom qui wach wuʼ, xak queje yin ʼuri, quin bij na che in Kajaw chicaj chi je we yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.033', 'Xak niʼpa i quiqui bij chiquiwach i winak chi n-qui yom ti qui wach wuʼ, xak queje yin ʼuri, quin bij na che in Kajaw chicaj chi ne te we yin.
', '', '&lt;span class="verse" id="Matt_10_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xak niʼpa i quiqui bij chiquiwach i winak chi n-qui yom ti qui wach wuʼ, xak queje yin ʼuri, quin bij na che in Kajaw chicaj chi ne te we yin.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.034', 'Mi chʼob yix chi in cʼun-nak xa que in chomarsaj ni winak chuwach i jyub taʼaj. N-rumal ta ile in cʼun-nak; in cʼun-nak chu petsaxic chʼoʼoj chiquixol i winak. ', '', '&lt;h2&gt;I u cʼutic i sak laj tzij xa cu petsaj i chʼoʼoj&lt;/h2&gt;&lt;span class="verse" id="Matt_10_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Mi chʼob yix chi in cʼun-nak xa que in chomarsaj ni winak chuwach i jyub taʼaj. N-rumal ta ile in cʼun-nak; in cʼun-nak chu petsaxic chʼoʼoj chiquixol i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.035', 'Rumal in cʼunbal, quiqui ʼan ni chʼoʼoj jun achi ruʼ u kajaw, jun ali ruʼ u chuch, jun alibaxel ruʼ u chuch ralib. ', '', '&lt;span class="verse" id="Matt_10_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Rumal in cʼunbal, quiqui ʼan ni chʼoʼoj jun achi ruʼ u kajaw, jun ali ruʼ u chuch, jun alibaxel ruʼ u chuch ralib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.036', 'Queʼelok ʼo ni jujun chique i winak je are quixokil calcʼwal je aj chʼoʼoj chic chiquij. ', '', '&lt;span class="verse" id="Matt_10_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Queʼelok ʼo ni jujun chique i winak je are quixokil calcʼwal je aj chʼoʼoj chic chiquij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.037', 'Xui-ri, jun chi we-yin, ʼo u chac cʼax chin u na ni yin chiquiwach u chuch u kajaw. Xak ʼo u chac cʼax chin u na ni yin chiquiwach u miʼal u cʼojol. We n-queje ta coʼon ire ile, lic n-ca majaw ta che ʼuri we we-yin. ', '', '&lt;span class="verse" id="Matt_10_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Xui-ri, jun chi we-yin, ʼo u chac cʼax chin u na ni yin chiquiwach u chuch u kajaw. Xak ʼo u chac cʼax chin u na ni yin chiquiwach u miʼal u cʼojol. We n-queje ta coʼon ire ile, lic n-ca majaw ta che ʼuri we we-yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.038', 'Jun we-yin ʼo u chac ca terej chwij, tupu ca cam na rumal. Pachin n-craj ti u ʼanic ile, n-ca majaw ta ʼuri che chi we-yin. ', '', '&lt;span class="verse" id="Matt_10_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Jun we-yin ʼo u chac ca terej chwij, tupu ca cam na rumal. Pachin n-craj ti u ʼanic ile, n-ca majaw ta ʼuri che chi we-yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.039', 'Pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, we ʼo jun ca sachsax nu wach u cʼaslemal xa wumal yin, ca yaʼ ʼuri u cʼaslemal sakil.
', '', '&lt;span class="verse" id="Matt_10_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, we ʼo jun ca sachsax nu wach u cʼaslemal xa wumal yin, ca yaʼ ʼuri u cʼaslemal sakil.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.040', 'Pachin jun quix u cʼulaj ruʼ, queʼel na ʼuri pacha yin quin u cʼulaj na ruʼ. Xak pachin i quin u cʼulaj na yin ruʼ, queʼel na ʼuri chi cu cʼulaj ni Jun chi in takawnak lok. ', '', '&lt;h2&gt;Pachin quiqui rik ni qui sipon chuwach i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_10_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Pachin jun quix u cʼulaj ruʼ, queʼel na ʼuri pacha yin quin u cʼulaj na ruʼ. Xak pachin i quin u cʼulaj na yin ruʼ, queʼel na ʼuri chi cu cʼulaj ni Jun chi in takawnak lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.041', 'Iwetaʼam chi jun ajbil u tzij i Dios, coʼon panok ca yaʼ ni sipon che rumal i Dios. Xak pachin i cu cʼulaj jun ajbil u tzij i Dios ruʼ, we xa rumal u takon i Dios, ire ca yaʼ ni sipon ʼuri che xak jun ruʼ pacha i ca yaʼ che i ajbil u tzij i Dios. Xak pachin jun cu cʼulaj jun ʼutz laj winak ruʼ, xa rumal ʼutz i jun chuwach i Dios, xak ire ʼuri i Dios cu ya ni ʼutz che pacha i cu ya che i winak jun chi lic ʼutz. ', '', '&lt;span class="verse" id="Matt_10_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Iwetaʼam chi jun ajbil u tzij i Dios, coʼon panok ca yaʼ ni sipon che rumal i Dios. Xak pachin i cu cʼulaj jun ajbil u tzij i Dios ruʼ, we xa rumal u takon i Dios, ire ca yaʼ ni sipon ʼuri che xak jun ruʼ pacha i ca yaʼ che i ajbil u tzij i Dios. Xak pachin jun cu cʼulaj jun ʼutz laj winak ruʼ, xa rumal ʼutz i jun chuwach i Dios, xak ire ʼuri i Dios cu ya ni ʼutz che pacha i cu ya che i winak jun chi lic ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '10.042', 'Pachin jun cu ya jubiʼ u yaʼ che jun ral in tijoxel yin, we xu ya na che xa rumal teren na chwij, katzij ʼuri xak ca yaʼ ni ʼutz puwi rumal i Dios —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_10_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Pachin jun cu ya jubiʼ u yaʼ che jun ral in tijoxel yin, we xu ya na che xa rumal teren na chwij, katzij ʼuri xak ca yaʼ ni ʼutz puwi rumal i Dios —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.001', 'I Jesus queje ile xutzin chu bixquil chique i cablajuj u tijoxelab wach quiqui ʼano. Teʼuri xa ʼe pa tak tinimit re i jyub-le, xa ʼe chi qui tijoxic i winak chu bixquil u tzij i Dios chique.
', '', '&lt;h2&gt;Wach i xu bij i Jesus chique i u tijoxelab i ma Wan&lt;/h2&gt;&lt;span class="verse" id="Matt_11_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus queje ile xutzin chu bixquil chique i cablajuj u tijoxelab wach quiqui ʼano. Teʼuri xa ʼe pa tak tinimit re i jyub-le, xa ʼe chi qui tijoxic i winak chu bixquil u tzij i Dios chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.002', 'Are ca tijin i Jesus che u chac-le, are ʼuri i ma Wan, aj kajsanel ya, tzʼapim chic pa cárcel. Ire, are xu ta rason wach ca tijin i Crísto chu ʼanic, xe u tak li jujun chique u tijoxelab ruʼ. ', '', '&lt;span class="verse" id="Matt_11_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ca tijin i Jesus che u chac-le, are ʼuri i ma Wan, aj kajsanel ya, tzʼapim chic pa cárcel. Ire, are xu ta rason wach ca tijin i Crísto chu ʼanic, xe u tak li jujun chique u tijoxelab ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.003', 'Are xe upon ruʼ i Jesus, xqui tzʼonoj che:
—¿Mok yet i Tolke chi u bim i ma Wan chi cat cʼunic? ¿O xa cakoyʼej chic jun chic? —xqui bij che.
', '', '&lt;span class="verse" id="Matt_11_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are xe upon ruʼ i Jesus, xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Mok yet i Tolke chi u bim i ma Wan chi cat cʼunic? ¿O xa cakoyʼej chic jun chic? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.004', 'I Jesus xu bij chique:
—Chix tzalij chic ruʼ i ma Wan; chi bij che wach i xi ta, wach i xiwilo chi quin tijin yin chu ʼonquil. ', '', '&lt;span class="verse" id="Matt_11_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chix tzalij chic ruʼ i ma Wan; chi bij che wach i xi ta, wach i xiwilo chi quin tijin yin chu ʼonquil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.005', 'Chi bij che chi i mawach que tzun chic; xak i je sic, que binic; xak i iwabib chi ʼo itzel yobil lépra chique, que utziric; xak i je tac, que tanic; xak i camnak que walijsaxic; xak i nibaʼib qui tom i ʼutz laj tzij re i quelbal pa cʼax. ', '', '&lt;span class="verse" id="Matt_11_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Chi bij che chi i mawach que tzun chic; xak i je sic, que binic; xak i iwabib chi ʼo itzel yobil lépra chique, que utziric; xak i je tac, que tanic; xak i camnak que walijsaxic; xak i nibaʼib qui tom i ʼutz laj tzij re i quelbal pa cʼax. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.006', 'Xak chi bij che chi ʼutz re ire pachin n-cu sach tu cubibal u cʼux chwe —xu bij i Jesus chique u tijoxelab i ma Wan.
', '', '&lt;span class="verse" id="Matt_11_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xak chi bij che chi ʼutz re ire pachin n-cu sach tu cubibal u cʼux chwe —xu bij i Jesus chique u tijoxelab i ma Wan.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.007', 'Are xe ʼe ique, i Jesus xu jek u bixquil chique i winak wach oʼonom i ma Wan.
—Yix, are xix ʼe che i jyub tzʼinilic chu tayquil i ma Wan, ¿wach xiwoyʼej rilic? ¿Xataba xa xix ʼe chi rilic jun ʼes ca silab rumal i tew? N-are taj. ', '', '&lt;h2&gt;I Jesus xu bij chique i winak wach u patan i ma Wan&lt;/h2&gt;&lt;span class="verse" id="Matt_11_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are xe ʼe ique, i Jesus xu jek u bixquil chique i winak wach oʼonom i ma Wan.&lt;p&gt; &lt;/p&gt;—Yix, are xix ʼe che i jyub tzʼinilic chu tayquil i ma Wan, ¿wach xiwoyʼej rilic? ¿Xataba xa xix ʼe chi rilic jun ʼes ca silab rumal i tew? N-are taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.008', '¿Wach ʼu i xix ʼe chi rilic? ¿Xataba xix ʼe chi rilic jun achi, chom u wikic u ʼanom che rib? Xak n-are taj. Niʼpa i ʼo qui ʼuʼ chom pakalic, ne te ʼo pa taʼaj tzʼinilic, xa je jekel pa rachoch i ʼatol tzij. ', '', '&lt;span class="verse" id="Matt_11_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;¿Wach ʼu i xix ʼe chi rilic? ¿Xataba xix ʼe chi rilic jun achi, chom u wikic u ʼanom che rib? Xak n-are taj. Niʼpa i ʼo qui ʼuʼ chom pakalic, ne te ʼo pa taʼaj tzʼinilic, xa je jekel pa rachoch i ʼatol tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.009', '¿Wach ʼu xix ʼe chi rilic? ¿Jun ajbil u tzij i Dios? Katzij, are. I jun chi xiwilo-le, lic nim ni u ʼij chiquiwach i je ajbil u tzij i Dios. ', '', '&lt;span class="verse" id="Matt_11_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;¿Wach ʼu xix ʼe chi rilic? ¿Jun ajbil u tzij i Dios? Katzij, are. I jun chi xiwilo-le, lic nim ni u ʼij chiquiwach i je ajbil u tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.010', 'Man i ma Wan are i jun chi u bim can i Dios chi cu tak na lok. I Dios u bim can chupam u wuj:
Yin nabe quin tak nubi jun ajbil in tzij chawach yet, man cu yijba ni a be chawach.
(Queje ile u bim can i Dios chupam u wuj.)
', '', '&lt;span class="verse" id="Matt_11_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Man i ma Wan are i jun chi u bim can i Dios chi cu tak na lok. I Dios u bim can chupam u wuj:
Yin nabe quin tak nubi jun ajbil in tzij chawach yet, man cu yijba ni a be chawach.
(Queje ile u bim can i Dios chupam u wuj.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.011', 'Katzij i quin bij chiwe: i ma Wan aj kajsanel ya, nim ni u ʼij ire chiquiwach conojel i winak, cʼa chu xebal ʼij sak. Xui-ri, woʼor conojel niʼpa i que oc na pa ʼatbal tzij re i Dios, mas nim ni qui ʼij ique chuwach ire.
', '', '&lt;span class="verse" id="Matt_11_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe: i ma Wan aj kajsanel ya, nim ni u ʼij ire chiquiwach conojel i winak, cʼa chu xebal ʼij sak. Xui-ri, woʼor conojel niʼpa i que oc na pa ʼatbal tzij re i Dios, mas nim ni qui ʼij ique chuwach ire.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.012', 'I ʼatbal tzij re i Dios, je qʼui quiqui ʼan chʼoʼoj chirij. Queje qui ʼanom li ile cʼa are xu jek u chac i ma Wan, cʼa are ʼo wi woʼor. Lic quiqui ʼat qui pen chu ʼatixic. ', '', '&lt;span class="verse" id="Matt_11_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I ʼatbal tzij re i Dios, je qʼui quiqui ʼan chʼoʼoj chirij. Queje qui ʼanom li ile cʼa are xu jek u chac i ma Wan, cʼa are ʼo wi woʼor. Lic quiqui ʼat qui pen chu ʼatixic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.013', 'Queje ile quiqui ʼano, man conojel i ajbil u tzij i Dios ujer, xak i ʼatbal tzij re i mam Moises ʼis xqui bij chi ca cʼun ni u ʼatbal tzij i Dios. Queje qui bim ile conojel, xak queje xu bij i ma Wan are xcʼun ire. ', '', '&lt;span class="verse" id="Matt_11_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Queje ile quiqui ʼano, man conojel i ajbil u tzij i Dios ujer, xak i ʼatbal tzij re i mam Moises ʼis xqui bij chi ca cʼun ni u ʼatbal tzij i Dios. Queje qui bim ile conojel, xak queje xu bij i ma Wan are xcʼun ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.014', 'Y we quiwaj quiwetamaj i sak laj tzij, i ma Wan are i mam Elías chi xqui bij ique ca cʼun na. ', '', '&lt;span class="verse" id="Matt_11_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Y we quiwaj quiwetamaj i sak laj tzij, i ma Wan are i mam Elías chi xqui bij ique ca cʼun na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.015', 'We yix ʼo i xiquin chu tayic, chi ta u be iri.
', '', '&lt;span class="verse" id="Matt_11_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;We yix ʼo i xiquin chu tayic, chi ta u be iri.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.016', 'I winak woʼor, ¿wach nawi que in junamsaj ruʼ? Xa je pacha acʼalab je cul xol tak be; jutzobaj que siqʼuin chique cachiʼil: “Yix lic n-ta ʼo quiwaj,” quiqui bij. ', '', '&lt;span class="verse" id="Matt_11_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I winak woʼor, ¿wach nawi que in junamsaj ruʼ? Xa je pacha acʼalab je cul xol tak be; jutzobaj que siqʼuin chique cachiʼil: “Yix lic n-ta ʼo quiwaj,” quiqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.017', '“Yoj xka xutʼij i suʼ chiwe, péro n-xiwaj ta xix xajawic. Xak xka bixaj jun bix re i bisonbal, péro n-xix oʼ taj,” quiqui bij. ', '', '&lt;span class="verse" id="Matt_11_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;“Yoj xka xutʼij i suʼ chiwe, péro n-xiwaj ta xix xajawic. Xak xka bixaj jun bix re i bisonbal, péro n-xix oʼ taj,” quiqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.018', 'Queje que tijin i winak chu ʼanic woʼor; n-ta ʼo cacaj, man xcʼun i ma Wan chi n-ta kas xu tijo, xak n-ta víno xu tijo, y qui bij yix xa ʼo itzel tew ruʼ. ', '', '&lt;span class="verse" id="Matt_11_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Queje que tijin i winak chu ʼanic woʼor; n-ta ʼo cacaj, man xcʼun i ma Wan chi n-ta kas xu tijo, xak n-ta víno xu tijo, y qui bij yix xa ʼo itzel tew ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.019', 'Are xin cʼun yin chi in Achi aj Chicaj, ʼis ronojel quin tijo y quin tuʼu, xui-ri qui bij yix chi lic in aj numel, xak in aj ʼabarel, qui bij yix. Xak qui coj in mac chi yin in cachiʼil i ajmaquib, xak i mal laj tak aj tzʼonol alcawal. Xui-ri, we te xi ta u be wach xoʼon i ma Wan, xak wach i quin ʼan yin, quiwetamaj ʼuri chi Dios lic ʼo u noʼoj.
', '', '&lt;span class="verse" id="Matt_11_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Are xin cʼun yin chi in Achi aj Chicaj, ʼis ronojel quin tijo y quin tuʼu, xui-ri qui bij yix chi lic in aj numel, xak in aj ʼabarel, qui bij yix. Xak qui coj in mac chi yin in cachiʼil i ajmaquib, xak i mal laj tak aj tzʼonol alcawal. Xui-ri, we te xi ta u be wach xoʼon i ma Wan, xak wach i quin ʼan yin, quiwetamaj ʼuri chi Dios lic ʼo u noʼoj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.020', 'Niʼpa i tinimit pa oʼonom wi uqʼuial milágro i Jesus, i winak chila lic n-xqui jalwachij ti qui cʼaslemal. Rumal-i, xoc ire che i qui yajic. Xu bij:
', '', '&lt;h2&gt;I tinimit chi n-xqui coj ti Jesus, nim ni tojpen ca pe piquiwi&lt;/h2&gt;&lt;span class="verse" id="Matt_11_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Niʼpa i tinimit pa oʼonom wi uqʼuial milágro i Jesus, i winak chila lic n-xqui jalwachij ti qui cʼaslemal. Rumal-i, xoc ire che i qui yajic. Xu bij:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.021', '—¡Toʼ i wach yix, ix aj tinimit Corazin! Toʼ i wach yix, ix aj tinimit Betsáida. Nic are wi qui coj qui mac i queb tinimit Tíro y Sidon man n-ta Dios cuʼ qui bij yix. Xui-ri, we te in ʼanom uqʼuial milágro chila pacha in ʼanom iwuʼ yix, ique ʼuri ya qui jalwachim tene chic-i qui cʼaslemal. Xak que bison tene-ri rumal i qui mac. ', '', '&lt;span class="verse" id="Matt_11_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;—¡Toʼ i wach yix, ix aj tinimit Corazin! Toʼ i wach yix, ix aj tinimit Betsáida. Nic are wi qui coj qui mac i queb tinimit Tíro y Sidon man n-ta Dios cuʼ qui bij yix. Xui-ri, we te in ʼanom uqʼuial milágro chila pacha in ʼanom iwuʼ yix, ique ʼuri ya qui jalwachim tene chic-i qui cʼaslemal. Xak que bison tene-ri rumal i qui mac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.022', 'Xui-ri yix, are ca cʼun i qʼuisbal ʼij, nim ni tojpen ca pe piwi yix chuwach i tojpen chi ca pe piquiwi ique chi je aj Tíro, je aj Sidon. ', '', '&lt;span class="verse" id="Matt_11_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xui-ri yix, are ca cʼun i qʼuisbal ʼij, nim ni tojpen ca pe piwi yix chuwach i tojpen chi ca pe piquiwi ique chi je aj Tíro, je aj Sidon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.023', 'Xak yix, chi ix aj tinimit Capernaum, qui chʼob yix lic ix ʼutz, ya mer quix oc chila chicaj, qui bij yix. Xui-ri, ya mer quix kaj pa ʼaʼ. Man i uqʼuial milágro ʼantal chiwach yix, we tene ʼantal ile chiquiwach i aj Sodóma ujer, ique xqui jalwachij tene qui cʼaslemal-i; i qui tinimit-le cʼa ʼo tene-ri woʼor. ', '', '&lt;span class="verse" id="Matt_11_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xak yix, chi ix aj tinimit Capernaum, qui chʼob yix lic ix ʼutz, ya mer quix oc chila chicaj, qui bij yix. Xui-ri, ya mer quix kaj pa ʼaʼ. Man i uqʼuial milágro ʼantal chiwach yix, we tene ʼantal ile chiquiwach i aj Sodóma ujer, ique xqui jalwachij tene qui cʼaslemal-i; i qui tinimit-le cʼa ʼo tene-ri woʼor. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.024', 'Xui-ri yix, are ca cʼun i qʼuisbal ʼij, nim ni tojpen ca pe piwi, chuwach i tojpen chi ca pe piquiwi ique chi je aj Sodóma —xu bij chique.
', '', '&lt;span class="verse" id="Matt_11_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xui-ri yix, are ca cʼun i qʼuisbal ʼij, nim ni tojpen ca pe piwi, chuwach i tojpen chi ca pe piquiwi ique chi je aj Sodóma —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.025', 'Are ʼuri i Jesus xu coj u ʼij i Dios; xu bij che:
—Quin tioxij chawe Ta, chi at rajaw i caj ulew. Quin tioxij chawe, man a yom chique i n-ti qui ʼij chi quiqui ta u be wach a chʼobom yet. Xui-ri, awuwam u wach chique i ʼo qui noʼoj, chique i nimak qui nojbal. ', '', '&lt;h2&gt;“Chix pet wuʼ; ʼo iwuxlambal” xu bij i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_11_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu coj u ʼij i Dios; xu bij che:&lt;p&gt; &lt;/p&gt;—Quin tioxij chawe Ta, chi at rajaw i caj ulew. Quin tioxij chawe, man a yom chique i n-ti qui ʼij chi quiqui ta u be wach a chʼobom yet. Xui-ri, awuwam u wach chique i ʼo qui noʼoj, chique i nimak qui nojbal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.026', 'Katzij, Ta, queje a ʼanom ile man queje ile cawaj yet —xu bij che.
', '', '&lt;span class="verse" id="Matt_11_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Katzij, Ta, queje a ʼanom ile man queje ile cawaj yet —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.027', 'Teʼuri xu bij chique i winak:
—Ronojel niʼpa i ʼolic, in Kajaw u yom pinʼab yin. Mi jun retaʼam wach in patan yin chi in u Cʼojol i Dios, xui i Ta chicaj retaʼam. Xak mi jun retaʼam u wach i Dios, xui yin, chi in u Cʼojol wetaʼam u wach; xak niʼpa i winak chi in ʼalijinsam chique, ique ʼut xak quetaʼam u wach. ', '', '&lt;span class="verse" id="Matt_11_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique i winak:&lt;p&gt; &lt;/p&gt;—Ronojel niʼpa i ʼolic, in Kajaw u yom pinʼab yin. Mi jun retaʼam wach in patan yin chi in u Cʼojol i Dios, xui i Ta chicaj retaʼam. Xak mi jun retaʼam u wach i Dios, xui yin, chi in u Cʼojol wetaʼam u wach; xak niʼpa i winak chi in ʼalijinsam chique, ique ʼut xak quetaʼam u wach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.028', 'Chix pet na wuʼ yin, iwonojel yix chi ix cosnak na, iwonojel yix chi ʼo iwekan; yin quin ya i uxlanem chiwe. ', '', '&lt;span class="verse" id="Matt_11_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Chix pet na wuʼ yin, iwonojel yix chi ix cosnak na, iwonojel yix chi ʼo iwekan; yin quin ya i uxlanem chiwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.029', 'Chi ya iwib che in yucu chi quin coj chiwe; chix naw na chwe yin, man yin nim in cʼux, xak ʼutz in cʼux chiwe. Qui rik ni uxlambal i cʼux ʼuri. ', '', '&lt;span class="verse" id="Matt_11_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Chi ya iwib che in yucu chi quin coj chiwe; chix naw na chwe yin, man yin nim in cʼux, xak ʼutz in cʼux chiwe. Qui rik ni uxlambal i cʼux ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '11.030', 'I yucu chi quin coj yin, n-cʼax taj quix bin ruʼ, xak iwekan quin yaʼo, n-ʼal taj. (Queje ile xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_11_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;I yucu chi quin coj yin, n-cʼax taj quix bin ruʼ, xak iwekan quin yaʼo, n-ʼal taj. (Queje ile xu bij i Jesus chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.001', 'Xoʼon chupan jubiʼ, pa uxlambal ʼij, i Jesus ca bin cuʼ u tijoxelab pa ticon re i trígo. I u tijoxelab, xpe numic chique; xe oc chu chʼupic u wach i trígo; xe oc chu pochʼic, y xqui tijo. ', '', '&lt;h2&gt;I u tijoxelab i Jesus xqui chʼup u wach i trígo pa uxlambal ʼij&lt;/h2&gt;&lt;span class="verse" id="Matt_12_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, pa uxlambal ʼij, i Jesus ca bin cuʼ u tijoxelab pa ticon re i trígo. I u tijoxelab, xpe numic chique; xe oc chu chʼupic u wach i trígo; xe oc chu pochʼic, y xqui tijo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.002', 'I aj Fariséo winak, are xquil ile, xqui bij che i Jesus:
—Chawilapeʼ, i a tijoxelab, n-ʼus taj que chacun woʼor pa uxlambal ʼij. I ʼatbal tzij tzʼibtal can rumal i mam Moises n-cu ya ta chique quiqui mol cosech woʼor —xe cha.
', '', '&lt;span class="verse" id="Matt_12_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I aj Fariséo winak, are xquil ile, xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—Chawilapeʼ, i a tijoxelab, n-ʼus taj que chacun woʼor pa uxlambal ʼij. I ʼatbal tzij tzʼibtal can rumal i mam Moises n-cu ya ta chique quiqui mol cosech woʼor —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.003', '—¿Katzij nawi? —xu bij i Jesus—. ¿Xataba n-iwilom ti yix chupam u wuj i Dios wach xu ʼan i mam David ujer? ', '', '&lt;span class="verse" id="Matt_12_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;—¿Katzij nawi? —xu bij i Jesus—. ¿Xataba n-iwilom ti yix chupam u wuj i Dios wach xu ʼan i mam David ujer? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.004', 'Ire, cuʼ i rachiʼil are xpe numic chique, xoc pa rachoch i Dios y xu tij i loʼlaj caxlanwa, chi yatal chuwach i Dios. I caxlanwa-le, xui yatal chique i sacerdóte quiqui tijo; n-yatal ta chique i toʼ winak quiqui tijo —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_12_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ire, cuʼ i rachiʼil are xpe numic chique, xoc pa rachoch i Dios y xu tij i loʼlaj caxlanwa, chi yatal chuwach i Dios. I caxlanwa-le, xui yatal chique i sacerdóte quiqui tijo; n-yatal ta chique i toʼ winak quiqui tijo —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.005', '¿Xataba xak ni tom ti yix wach i tzʼibtal can chupam i ʼatbal tzij re i mam Moises? Cu bij chi ʼo u chac que chacun i sacerdóte pa rachoch i Dios pa uxlambal ʼij. N-mac ta ile, tupu n-que uxlan taj. ', '', '&lt;span class="verse" id="Matt_12_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;¿Xataba xak ni tom ti yix wach i tzʼibtal can chupam i ʼatbal tzij re i mam Moises? Cu bij chi ʼo u chac que chacun i sacerdóte pa rachoch i Dios pa uxlambal ʼij. N-mac ta ile, tupu n-que uxlan taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.006', 'Bay, chi ʼo wi Jun chiwach yix chi nim ni u ʼij chuwach i rachoch i Dios. ', '', '&lt;span class="verse" id="Matt_12_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Bay, chi ʼo wi Jun chiwach yix chi nim ni u ʼij chuwach i rachoch i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.007', 'Yix n-qui ta tu be wach u bim i Dios chupam u wuj. Xu bij iri: “Cwaj yin ʼo ni cʼaxnabal i cʼux; n-are ti toʼ u sujuxic chicop chinwach.” (Queje ile tzʼibtal canok.) We tene qui ta u be ile, n-qui coj ta ʼuri qui mac i winak chi n-ta jun qui mac. (In tijoxelab-le n-ti qui mac que tijin chu ʼanic.) ', '', '&lt;span class="verse" id="Matt_12_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Yix n-qui ta tu be wach u bim i Dios chupam u wuj. Xu bij iri: “Cwaj yin ʼo ni cʼaxnabal i cʼux; n-are ti toʼ u sujuxic chicop chinwach.” (Queje ile tzʼibtal canok.) We tene qui ta u be ile, n-qui coj ta ʼuri qui mac i winak chi n-ta jun qui mac. (In tijoxelab-le n-ti qui mac que tijin chu ʼanic.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.008', 'Yin, chi in Achi aj Chicaj xak in ʼo puwi i uxlambal ʼij —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_12_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Yin, chi in Achi aj Chicaj xak in ʼo puwi i uxlambal ʼij —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.009', 'Teʼuri i Jesus xel pa ticon, xa ʼe pa molbal quib i aj Israel. Are ʼo chila, xril jun achi lic chakijnak u ʼab. ', '', '&lt;h2&gt;I Jesus xrutzirsaj u ʼab jun achi pa uxlambal ʼij&lt;/h2&gt;&lt;span class="verse" id="Matt_12_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xel pa ticon, xa ʼe pa molbal quib i aj Israel. Are ʼo chila, xril jun achi lic chakijnak u ʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.010', 'Chila xak je ʼo juban achiab, quiqui tzucuj u mac i Jesus; xe oc ʼuri chu cʼamic u pam.
—¿ʼUtz nawi cutzirsax jun iwab pa uxlambal ʼij? —xqui bij che.
', '', '&lt;span class="verse" id="Matt_12_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Chila xak je ʼo juban achiab, quiqui tzucuj u mac i Jesus; xe oc ʼuri chu cʼamic u pam.&lt;p&gt; &lt;/p&gt;—¿ʼUtz nawi cutzirsax jun iwab pa uxlambal ʼij? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.011', 'I Jesus xu bij chique:
—Yix, we ʼo jun i chij, we ca tzak chupam jun siwan che i uxlambal ʼij, ¿xataba n-quiwesaj ta lok, we pa uxlambal ʼij? Quiwesaj pues. ', '', '&lt;span class="verse" id="Matt_12_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Yix, we ʼo jun i chij, we ca tzak chupam jun siwan che i uxlambal ʼij, ¿xataba n-quiwesaj ta lok, we pa uxlambal ʼij? Quiwesaj pues. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.012', '¿Xataba jun achi n-tu ʼij chuwach jun chij? Jun achi mas ʼo u ʼij pues. Rumal-i, yatal ʼuri chake caka ʼan i ʼutz che jun winak pa uxlambal ʼij —xu bij chique.
', '', '&lt;span class="verse" id="Matt_12_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;¿Xataba jun achi n-tu ʼij chuwach jun chij? Jun achi mas ʼo u ʼij pues. Rumal-i, yatal ʼuri chake caka ʼan i ʼutz che jun winak pa uxlambal ʼij —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.013', 'Teʼuri xu bij che i achi:
—Cha sucʼba i a ʼab —coʼon che.
Are ʼuri i achi xu ji pan u ʼab, y xutziric; xcanajic pacha u ʼanom i jun chi ʼutz. ', '', '&lt;span class="verse" id="Matt_12_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij che i achi:&lt;p&gt; &lt;/p&gt;—Cha sucʼba i a ʼab —coʼon che.&lt;p&gt; &lt;/p&gt;Are ʼuri i achi xu ji pan u ʼab, y xutziric; xcanajic pacha u ʼanom i jun chi ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.014', 'Teʼuri i aj Fariséo winak xe ʼec; xe oc ʼuri chu chʼobic chiquiwach wach u camsaxic i Jesus quiqui ʼano.
', '', '&lt;span class="verse" id="Matt_12_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri i aj Fariséo winak xe ʼec; xe oc ʼuri chu chʼobic chiquiwach wach u camsaxic i Jesus quiqui ʼano.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.015', 'I Jesus, are xu ta wach cacaj quiqui ʼan che, xel chila; y je qʼui i winak xe terej bi chirij. I Jesus ʼis xe u cunaj iwabib, xui-ri, xu bij chique chi n-quiqui bij taj pachin ire. ', '', '&lt;h2&gt;Wach xoʼon i Jesus xeʼelok pacha u bim i Dios ujer&lt;/h2&gt;&lt;span class="verse" id="Matt_12_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I Jesus, are xu ta wach cacaj quiqui ʼan che, xel chila; y je qʼui i winak xe terej bi chirij. I Jesus ʼis xe u cunaj iwabib, xui-ri, xu bij chique chi n-quiqui bij taj pachin ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.017', 'Queje ile xeʼelok xak jun ruʼ pacha u bim can i Dios chupam i wuj tzʼibtal can rumal i mam Isaías. Xu bij:
', '', '&lt;span class="verse" id="Matt_12_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Queje ile xeʼelok xak jun ruʼ pacha u bim can i Dios chupam i wuj tzʼibtal can rumal i mam Isaías. Xu bij:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.018', 'Are iri are i wajchac chi in chom na;
ire lic cʼax quin naʼo, lic quin quicot rumal.
Quin tak bi in Tewal puwi ire,
y que u bij chique conojel i tinimit chi ʼo ni tobal-que chuwach i tojpen chi ca pe na.
', '', '&lt;span class="verse" id="Matt_12_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Are iri are i wajchac chi in chom na;
ire lic cʼax quin naʼo, lic quin quicot rumal.
Quin tak bi in Tewal puwi ire,
y que u bij chique conojel i tinimit chi ʼo ni tobal-que chuwach i tojpen chi ca pe na.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.019', 'Ire n-craj ta quiqui chap quib chi tzij, xak n-ca siqʼuin taj;
xak n-ca taqʼui ta pa tak be, cu ʼanbej re nimak tak u bixquil chique i winak.
', '', '&lt;span class="verse" id="Matt_12_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Ire n-craj ta quiqui chap quib chi tzij, xak n-ca siqʼuin taj;
xak n-ca taqʼui ta pa tak be, cu ʼanbej re nimak tak u bixquil chique i winak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.020', 'Ire n-coʼon ta cʼax che jun chi n-tu choʼab:
we ʼo jun winak pacha jun ajij cotquic, ire n-cu qʼuis ti u patzʼic;
xak we ʼo jun winak pacha jun cantil cu rayij ca chupic, ire n-cu qʼuis ti u chupic.
Queje coʼon ire ile, cʼa ca pe ni ʼutz sucʼulic chuwach i jyub taʼaj.
', '', '&lt;span class="verse" id="Matt_12_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Ire n-coʼon ta cʼax che jun chi n-tu choʼab:
we ʼo jun winak pacha jun ajij cotquic, ire n-cu qʼuis ti u patzʼic;
xak we ʼo jun winak pacha jun cantil cu rayij ca chupic, ire n-cu qʼuis ti u chupic.
Queje coʼon ire ile, cʼa ca pe ni ʼutz sucʼulic chuwach i jyub taʼaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.021', 'I ronojel tak i tinimit, quiqui cuba ni qui cʼux che ire, chi rumal ire que ʼel ni ique pa cʼax.
(Queje ile u tzij i Dios chi u tzʼibam can i mam Isaías.)
', '', '&lt;span class="verse" id="Matt_12_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I ronojel tak i tinimit, quiqui cuba ni qui cʼux che ire, chi rumal ire que ʼel ni ique pa cʼax.
(Queje ile u tzij i Dios chi u tzʼibam can i mam Isaías.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.022', 'Teʼuri ʼo jun achi mawach, cʼamtal bi chuwach i Jesus. Ire xak mem, man ʼo jun itzel tew che. I Jesus xu rutzirsaj, teʼuri i achi chi mawach xtzunic, xak xchʼawic. ', '', '&lt;h2&gt;Xqui bij i aj Fariséo chi u choʼab i Jesus xa ruʼ itzel petnak wi&lt;/h2&gt;&lt;span class="verse" id="Matt_12_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Teʼuri ʼo jun achi mawach, cʼamtal bi chuwach i Jesus. Ire xak mem, man ʼo jun itzel tew che. I Jesus xu rutzirsaj, teʼuri i achi chi mawach xtzunic, xak xchʼawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.023', 'Conojel i winak lic xqui bisoj che, y xqui bij:
—¿Mokxa are i Tolke chi koyʼem? —xe cha.
', '', '&lt;span class="verse" id="Matt_12_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Conojel i winak lic xqui bisoj che, y xqui bij:&lt;p&gt; &lt;/p&gt;—¿Mokxa are i Tolke chi koyʼem? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.024', 'Xui-ri, i je aj Fariséo are xqui ta, xqui bij:
—N-are taj. I achi-le, i choʼab chi ʼo ruʼ xa ruʼ itzel ca pe wi. Are i rumal ʼuri ca tiqui chi relsaxic bi itzel tew. I choʼab chi ʼo ruʼ, xa ruʼ i Beelzebu ca pe wi, chi ʼo piquiwi itzel tak tew —xqui bij.
', '', '&lt;span class="verse" id="Matt_12_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xui-ri, i je aj Fariséo are xqui ta, xqui bij:&lt;p&gt; &lt;/p&gt;—N-are taj. I achi-le, i choʼab chi ʼo ruʼ xa ruʼ itzel ca pe wi. Are i rumal ʼuri ca tiqui chi relsaxic bi itzel tew. I choʼab chi ʼo ruʼ, xa ruʼ i Beelzebu ca pe wi, chi ʼo piquiwi itzel tak tew —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.025', 'I Jesus retaʼam wach quiqui chʼobo, y xu bij jun tijojbal tzij chique, cʼutbal-re chi n-katzij taj wach que tijin chu bixquil. Xu bij:
—I ʼatbal tzij re jun tinimit, we quiqui ʼan tak tzobajil che quib man quiqui ʼanbej chʼoʼoj chiquiwach, xa quiqui sach qui wach ʼuri. Xak queje jun tinimit, xak queje i je alal; we n-junam ti qui wach, we quiqui jach quib chiquiwach, n-que najtin ta ʼuri. ', '', '&lt;span class="verse" id="Matt_12_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I Jesus retaʼam wach quiqui chʼobo, y xu bij jun tijojbal tzij chique, cʼutbal-re chi n-katzij taj wach que tijin chu bixquil. Xu bij:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re jun tinimit, we quiqui ʼan tak tzobajil che quib man quiqui ʼanbej chʼoʼoj chiquiwach, xa quiqui sach qui wach ʼuri. Xak queje jun tinimit, xak queje i je alal; we n-junam ti qui wach, we quiqui jach quib chiquiwach, n-que najtin ta ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.026', 'Xak queje queʼel ile che i Satanas, man we ʼo jujun itzel quiqui ʼan chʼoʼoj chiquiwach je itzel, n-que najtin ta ʼuri. ', '', '&lt;span class="verse" id="Matt_12_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Xak queje queʼel ile che i Satanas, man we ʼo jujun itzel quiqui ʼan chʼoʼoj chiquiwach je itzel, n-que najtin ta ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.027', 'Qui bij yix chi ruʼ u choʼab i Beelzebu xwesaj bi itzel tew. Bay, xak i tijoxelab yix xak caquesaj bi itzel tew chique i winak; ¿pachin yawnak i qui choʼab ique ʼut? Ile quiqui ʼan i tijoxelab yix; ca ʼalijin ʼuri chiwe chi n-usucʼ taj wach i xi bij. ', '', '&lt;span class="verse" id="Matt_12_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Qui bij yix chi ruʼ u choʼab i Beelzebu xwesaj bi itzel tew. Bay, xak i tijoxelab yix xak caquesaj bi itzel tew chique i winak; ¿pachin yawnak i qui choʼab ique ʼut? Ile quiqui ʼan i tijoxelab yix; ca ʼalijin ʼuri chiwe chi n-usucʼ taj wach i xi bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.028', 'Xui-ri, yin que wesaj bi itzel tak tew rumal u Tewal i Dios chi ʼo wuʼ. Ile ʼut cʼutbal-re chiwe chi i ʼatbal tzij re i Dios ya ʼo chic chi.
', '', '&lt;span class="verse" id="Matt_12_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Xui-ri, yin que wesaj bi itzel tak tew rumal u Tewal i Dios chi ʼo wuʼ. Ile ʼut cʼutbal-re chiwe chi i ʼatbal tzij re i Dios ya ʼo chic chi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.029', 'Qui bij yix chi ruʼ ni u choʼab itzel, xwesaj bi itzel tew-le. Xui-ri, mi jun ca tiqui che oquic pa cu ʼat wi tzij jun (pacha itzel), chi relsaxic wach i ʼo puʼab, we maja u ximom i jun-le. We u ximom chic, teʼuri ca tiqui chi relsaxic wach i ʼo puʼab —xu bij chique. Xak xu bij:
', '', '&lt;span class="verse" id="Matt_12_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Qui bij yix chi ruʼ ni u choʼab itzel, xwesaj bi itzel tew-le. Xui-ri, mi jun ca tiqui che oquic pa cu ʼat wi tzij jun (pacha itzel), chi relsaxic wach i ʼo puʼab, we maja u ximom i jun-le. We u ximom chic, teʼuri ca tiqui chi relsaxic wach i ʼo puʼab —xu bij chique. Xak xu bij:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.030', '—Pachin n-cu ya tu wach wuʼ, ire aj chʼoʼoj ʼuri chwij. Xak pachin jun n-quin u to ta che i molonic, ire xa cu quichersaj bic.
', '', '&lt;span class="verse" id="Matt_12_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;—Pachin n-cu ya tu wach wuʼ, ire aj chʼoʼoj ʼuri chwij. Xak pachin jun n-quin u to ta che i molonic, ire xa cu quichersaj bic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.031', 'Rumal-i quin bij chiwe, chi ʼana cwent xak manal mi bij chirij u Tewal i Dios. I winak, ca cuytaj ni qui mac chique, xak niʼpa i n-ʼus taj chi quiqui bij; xui-ri, we quiqui bij mal laj tak tzij chirij u Tewal i Dios, ile n-ca cuytaj ta chic chique. ', '', '&lt;span class="verse" id="Matt_12_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Rumal-i quin bij chiwe, chi ʼana cwent xak manal mi bij chirij u Tewal i Dios. I winak, ca cuytaj ni qui mac chique, xak niʼpa i n-ʼus taj chi quiqui bij; xui-ri, we quiqui bij mal laj tak tzij chirij u Tewal i Dios, ile n-ca cuytaj ta chic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.032', 'I winak, we cʼax que chʼaw chwij yin, ile ca cuytaj na chique; xui-ri we cʼax que chʼaw na chirij u Tewal i Dios, i mac-le lic n-ta u cuytajic. N-ca cuytaj ta woʼor, xak mi ne coʼon panok, are cu jek u ʼatbal tzij i Dios.
', '', '&lt;span class="verse" id="Matt_12_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I winak, we cʼax que chʼaw chwij yin, ile ca cuytaj na chique; xui-ri we cʼax que chʼaw na chirij u Tewal i Dios, i mac-le lic n-ta u cuytajic. N-ca cuytaj ta woʼor, xak mi ne coʼon panok, are cu jek u ʼatbal tzij i Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.033', 'Jun cheʼ, we ʼutz laj cheʼ, cu ya u wach ʼutz; xak we n-ʼus ta laj cheʼ, i u wach xak n-ʼus taj. Jun cheʼ ca ʼiltaj na che u wach we ʼutz. ', '', '&lt;h2&gt;Jun ʼutz laj winak, ʼutz i cu bij, man ʼutz i ʼo pa ranima&lt;/h2&gt;&lt;span class="verse" id="Matt_12_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Jun cheʼ, we ʼutz laj cheʼ, cu ya u wach ʼutz; xak we n-ʼus ta laj cheʼ, i u wach xak n-ʼus taj. Jun cheʼ ca ʼiltaj na che u wach we ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.034', '¡Yix ix racʼal cumatz! ¿Wach nawi u bixquil i ʼutz qui ʼan na? man lic ix mal. Jun, are ca chʼawic, wach i quel pu chiʼ, pu cʼux petnak wi, niʼpa u molom rib chila. ', '', '&lt;span class="verse" id="Matt_12_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;¡Yix ix racʼal cumatz! ¿Wach nawi u bixquil i ʼutz qui ʼan na? man lic ix mal. Jun, are ca chʼawic, wach i quel pu chiʼ, pu cʼux petnak wi, niʼpa u molom rib chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.035', 'Jun ʼutz laj winak, ʼutz i cu bij, man ʼutz i ʼo pa ranima; xak jun mal laj winak, n-ʼus ti cu bij, man n-ʼus ti ʼo pa ranima. ', '', '&lt;span class="verse" id="Matt_12_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Jun ʼutz laj winak, ʼutz i cu bij, man ʼutz i ʼo pa ranima; xak jun mal laj winak, n-ʼus ti cu bij, man n-ʼus ti ʼo pa ranima. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.036', 'Y yin quin bij chiwe, are ca cʼun i qʼuisbal ʼij, conojel ʼis quiqui bij na chuwach i Dios wach tak i tzij qui bim chi n-ʼus taj. ', '', '&lt;span class="verse" id="Matt_12_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Y yin quin bij chiwe, are ca cʼun i qʼuisbal ʼij, conojel ʼis quiqui bij na chuwach i Dios wach tak i tzij qui bim chi n-ʼus taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.037', 'Rumal wach i a bim, ca ʼat ni tzij pawi, ca ʼiltaj ʼuri we ʼo i a mac, o we n-taj.
', '', '&lt;span class="verse" id="Matt_12_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Rumal wach i a bim, ca ʼat ni tzij pawi, ca ʼiltaj ʼuri we ʼo i a mac, o we n-taj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.038', 'Je ʼo jujun aj Fariséo winak, tak jujun aj tijonel re i ʼatbal tzij, xqui bij che i Jesus:
—Tijonel —xe cha—, cakaj cakil jun milágro, cʼutbal-re chake chi Dios ʼo awuʼ.
', '', '&lt;h2&gt;I winak xa cacaj rilic jun milágro&lt;/h2&gt;&lt;span class="verse" id="Matt_12_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Je ʼo jujun aj Fariséo winak, tak jujun aj tijonel re i ʼatbal tzij, xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—Tijonel —xe cha—, cakaj cakil jun milágro, cʼutbal-re chake chi Dios ʼo awuʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.039', 'I Jesus xu laʼ u wach chique:
—I winak woʼor, lic je mal na, lic n-quiqui coj ta chic i Dios. Yix qui rayij ca ʼan ni jun milágro chiwach, cʼutbal re u choʼab i Dios wuʼ; xui-ri, n-ta jun milágro ca ʼan ni chiwach. I tom chic wach i milágro xa ʼani che i ma Jonas, chi ajbil u tzij i Dios ujer. Xui i milágro-le quiwil na. ', '', '&lt;span class="verse" id="Matt_12_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—I winak woʼor, lic je mal na, lic n-quiqui coj ta chic i Dios. Yix qui rayij ca ʼan ni jun milágro chiwach, cʼutbal re u choʼab i Dios wuʼ; xui-ri, n-ta jun milágro ca ʼan ni chiwach. I tom chic wach i milágro xa ʼani che i ma Jonas, chi ajbil u tzij i Dios ujer. Xui i milágro-le quiwil na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.040', 'Ire xu chʼij oxib ʼij, oxib aʼab, chupam i nim laj car; bay, xak queje quin ʼan ni yin, chi in Achi aj Chicaj; quin chʼij ni oxib ʼij, oxib aʼab, quin muktaj na. ', '', '&lt;span class="verse" id="Matt_12_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Ire xu chʼij oxib ʼij, oxib aʼab, chupam i nim laj car; bay, xak queje quin ʼan ni yin, chi in Achi aj Chicaj; quin chʼij ni oxib ʼij, oxib aʼab, quin muktaj na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.041', 'Are ca pe i ʼij chi ca ʼat ni tzij piquiwi i winak, i aj Nínive chi xe u pixbaj i ma Jonas, quiqui coj na i mac yix ix winak re woʼor. Queje quiqui ʼan ni ile, man i winak aj Nínive xqui jalwachij qui cʼaslemal are xupon i ma Jonas chu bixquil u tzij i Dios chique. Xui-ri, yix lic n-qui jalwachij ti cʼaslemal tupu ʼo Jun chi, mas nim ni u ʼij chuwach i ma Jonas; rumal-i quiqui coj na i mac. ', '', '&lt;span class="verse" id="Matt_12_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Are ca pe i ʼij chi ca ʼat ni tzij piquiwi i winak, i aj Nínive chi xe u pixbaj i ma Jonas, quiqui coj na i mac yix ix winak re woʼor. Queje quiqui ʼan ni ile, man i winak aj Nínive xqui jalwachij qui cʼaslemal are xupon i ma Jonas chu bixquil u tzij i Dios chique. Xui-ri, yix lic n-qui jalwachij ti cʼaslemal tupu ʼo Jun chi, mas nim ni u ʼij chuwach i ma Jonas; rumal-i quiqui coj na i mac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.042', 'Xak i ixok, chi ujer ʼatol tzij re i pa Sur, xak ire cu coj ni i mac yix ix winak re woʼor, are ca pe i ʼij chi ca ʼat ni tzij piquiwi i winak. Queje cu ʼan na ire-le, man yix n-quiwaj ta qui ta i Jun chi ʼo u noʼoj. I ixok-le, lic mas naj xpe wi chu tayic u nojbal i ma Salomon, chi mas ʼo u noʼoj. Y woʼor ʼo Jun chi, nim ni u ʼij chuwach i ma Salomon, péro yix n-quiwaj tu tayic wach i cu bij —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_12_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Xak i ixok, chi ujer ʼatol tzij re i pa Sur, xak ire cu coj ni i mac yix ix winak re woʼor, are ca pe i ʼij chi ca ʼat ni tzij piquiwi i winak. Queje cu ʼan na ire-le, man yix n-quiwaj ta qui ta i Jun chi ʼo u noʼoj. I ixok-le, lic mas naj xpe wi chu tayic u nojbal i ma Salomon, chi mas ʼo u noʼoj. Y woʼor ʼo Jun chi, nim ni u ʼij chuwach i ma Salomon, péro yix n-quiwaj tu tayic wach i cu bij —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.043', 'I Jesus xak xu bij i tzij-i chique:
—Jun itzel tew, we xel che jun winak, xa ca bin pa chakij jyub chu tzucuxic pa cuxlan wi, y n-cu rik taj. Teʼuri cu chʼob pu jolom: ', '', '&lt;h2&gt;Jun itzel tew cu tzucuj tan chic u tzalijbal che jun winak&lt;/h2&gt;&lt;span class="verse" id="Matt_12_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;I Jesus xak xu bij i tzij-i chique:&lt;p&gt; &lt;/p&gt;—Jun itzel tew, we xel che jun winak, xa ca bin pa chakij jyub chu tzucuxic pa cuxlan wi, y n-cu rik taj. Teʼuri cu chʼob pu jolom: &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.044', '“Quin tzalij chic che i wachoch pa i xin el wi” cu bij; y ca tzalij chic ʼuri. Are cupon tan chic ruʼ achi, cu riko pacha jun ja n-tu pam, lic josʼim, lic yijbam na che jun rajaw cʼacʼ. ', '', '&lt;span class="verse" id="Matt_12_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;“Quin tzalij chic che i wachoch pa i xin el wi” cu bij; y ca tzalij chic ʼuri. Are cupon tan chic ruʼ achi, cu riko pacha jun ja n-tu pam, lic josʼim, lic yijbam na che jun rajaw cʼacʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.045', 'Teʼuri i itzel tew que u tzucuj bi rachiʼil, y que u rik je wukub chic, mas je mal chuwach ire. Que u cʼam bi ruʼ, y conojel que oc che achi. Chila que jeki wi. Teʼuri i achi-le, queʼelok mas mal na chuwach li ujer. Xak queje ile queʼel chique i winak re woʼor (man yix n-ta jun cʼutunic ʼutz qui ya chique, quiqui cʼolbej piqui cʼux —xu bij i Jesus chique i aj Fariséo).
', '', '&lt;span class="verse" id="Matt_12_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Teʼuri i itzel tew que u tzucuj bi rachiʼil, y que u rik je wukub chic, mas je mal chuwach ire. Que u cʼam bi ruʼ, y conojel que oc che achi. Chila que jeki wi. Teʼuri i achi-le, queʼelok mas mal na chuwach li ujer. Xak queje ile queʼel chique i winak re woʼor (man yix n-ta jun cʼutunic ʼutz qui ya chique, quiqui cʼolbej piqui cʼux —xu bij i Jesus chique i aj Fariséo).
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.046', 'I Jesus cʼa ca chʼaʼat cuʼ i winak are xe upon u chaʼ, xak i u chuch. Ique je ʼo chi sak, cacaj que lapan ruʼ. ', '', '&lt;h2&gt;Je pachin i u chuch i u chaʼ i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_12_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;I Jesus cʼa ca chʼaʼat cuʼ i winak are xe upon u chaʼ, xak i u chuch. Ique je ʼo chi sak, cacaj que lapan ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.047', 'Are ʼuri ʼo jun achi xu bij che i Jesus:
—I a chuch cuʼ i a chaʼ je ʼo chi sak, cacaj que lapan awuʼ —xu bij che.
', '', '&lt;span class="verse" id="Matt_12_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Are ʼuri ʼo jun achi xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—I a chuch cuʼ i a chaʼ je ʼo chi sak, cacaj que lapan awuʼ —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.048', 'Xui-ri, i Jesus xu bij che i achi:
—¿Je pachin in chuch, je pachin in chaʼ? —coʼono.
', '', '&lt;span class="verse" id="Matt_12_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Xui-ri, i Jesus xu bij che i achi:&lt;p&gt; &lt;/p&gt;—¿Je pachin in chuch, je pachin in chaʼ? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.049', 'Are ʼuri, xu cʼut che u ʼab niʼpa i u tijoxelab je ʼo ruʼ, y xu bij:
—Ique-le je are in chuch, je in chaʼ —xu bij—. ', '', '&lt;span class="verse" id="Matt_12_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;Are ʼuri, xu cʼut che u ʼab niʼpa i u tijoxelab je ʼo ruʼ, y xu bij:&lt;p&gt; &lt;/p&gt;—Ique-le je are in chuch, je in chaʼ —xu bij—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '12.050', 'Pachin i que tijin chu ʼanic pacha craj in Kajaw chicaj, je are in chuch ʼuri, je are in chaʼ, je are i wanab —xu bij.
', '', '&lt;span class="verse" id="Matt_12_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Pachin i que tijin chu ʼanic pacha craj in Kajaw chicaj, je are in chuch ʼuri, je are in chaʼ, je are i wanab —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.001', 'Che i ʼij-le, i Jesus xel ruʼ ja, xa ʼe chu chiʼ i alagun, y xcubi chila. ', '', '&lt;h2&gt;Jun tijojbal tzij re i ticon&lt;/h2&gt;&lt;span class="verse" id="Matt_13_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Che i ʼij-le, i Jesus xel ruʼ ja, xa ʼe chu chiʼ i alagun, y xcubi chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.002', 'Are ʼuri xe moltaj tan chic uqʼuial winak ruʼ. Rumal-i, i Jesus xa ʼan chupam jun bárco, y xcubi chupam. Je niʼpa i winak ʼis xe canaj chi tak seneb. ', '', '&lt;span class="verse" id="Matt_13_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ʼuri xe moltaj tan chic uqʼuial winak ruʼ. Rumal-i, i Jesus xa ʼan chupam jun bárco, y xcubi chupam. Je niʼpa i winak ʼis xe canaj chi tak seneb. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.003', 'Teʼuri xu jek u cʼutic juban tak tijojbal tzij chique. Xu bij:
—ʼO jun achi ca ʼe chu tiquic u ticon. ', '', '&lt;span class="verse" id="Matt_13_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Teʼuri xu jek u cʼutic juban tak tijojbal tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;—ʼO jun achi ca ʼe chu tiquic u ticon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.004', 'Are xe u quirij i rijaʼ, ʼo jubiʼ xa ʼe tzak pa be. Teʼuri xe cʼun i tzʼiquin y xqui tij bic. ', '', '&lt;span class="verse" id="Matt_13_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Are xe u quirij i rijaʼ, ʼo jubiʼ xa ʼe tzak pa be. Teʼuri xe cʼun i tzʼiquin y xqui tij bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.005', 'Xak ʼo jubiʼ chic xa ʼe tzak pa tak abaj, chi n-ta kas ulew che. I ija-le, juntir xalax lok, man n-muktajnak ta mas. ', '', '&lt;span class="verse" id="Matt_13_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xak ʼo jubiʼ chic xa ʼe tzak pa tak abaj, chi n-ta kas ulew che. I ija-le, juntir xalax lok, man n-muktajnak ta mas. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.006', 'Xui-ri, are xel li ʼij, xchakij na, man n-ta raʼ ʼenak pulew. ', '', '&lt;span class="verse" id="Matt_13_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xui-ri, are xel li ʼij, xchakij na, man n-ta raʼ ʼenak pulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.007', 'Xak juban chic xa ʼe tzak pa qʼuix; i qʼuix xqʼuiyic, y xu camsaj i ticon. ', '', '&lt;span class="verse" id="Matt_13_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xak juban chic xa ʼe tzak pa qʼuix; i qʼuix xqʼuiyic, y xu camsaj i ticon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.008', 'Xak ʼo ni ija xa ʼe tzak pa ʼutz laj ulew; lic qʼui ni u wach xu yaʼo. ʼO ni rakan xu ya jun ciénto u wach; xak ʼo i xu ya sesénta; xak ʼo i xu ya tréinta —xu bij. Teʼuri xu bij chic:
', '', '&lt;span class="verse" id="Matt_13_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xak ʼo ni ija xa ʼe tzak pa ʼutz laj ulew; lic qʼui ni u wach xu yaʼo. ʼO ni rakan xu ya jun ciénto u wach; xak ʼo i xu ya sesénta; xak ʼo i xu ya tréinta —xu bij. Teʼuri xu bij chic:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.009', '—We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xcha.
', '', '&lt;span class="verse" id="Matt_13_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;—We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xcha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.010', 'I u tijoxelab i Jesus, are xu bij ire ile, xe tejeb ruʼ. Xqui tzʼonoj che:
—¿Wuchac xa tijojbal tzij ca bij are cat tijin chu cʼutic chique i winak? —xqui bij che.
', '', '&lt;h2&gt;Wach u chac i tak tijojbal tzij xu bij i Jesus chique i winak&lt;/h2&gt;&lt;span class="verse" id="Matt_13_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I u tijoxelab i Jesus, are xu bij ire ile, xe tejeb ruʼ. Xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wuchac xa tijojbal tzij ca bij are cat tijin chu cʼutic chique i winak? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.011', 'I Jesus xu bij ʼuri chique:
—Yix ʼut, i Dios u yom chiwe qui ta u be wach usucʼ i ʼatbal tzij aj chicaj. I juban winak (chi n-que terej ta chwij) n-quiqui ta tu be. ', '', '&lt;span class="verse" id="Matt_13_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I Jesus xu bij ʼuri chique:&lt;p&gt; &lt;/p&gt;—Yix ʼut, i Dios u yom chiwe qui ta u be wach usucʼ i ʼatbal tzij aj chicaj. I juban winak (chi n-que terej ta chwij) n-quiqui ta tu be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.012', 'Queje oʼonom i Dios ile, man niʼpa i quiqui cʼam na wach cu sujuj ire chique, mas cu ya ʼuri chique; queje ile nim ni ʼo cuʼ. Niʼpa i n-cacaj ti u cʼamic wach cu sujuj i Dios chique, quelsax na ʼuri i jubiʼ chi ʼo cuʼ. ', '', '&lt;span class="verse" id="Matt_13_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Queje oʼonom i Dios ile, man niʼpa i quiqui cʼam na wach cu sujuj ire chique, mas cu ya ʼuri chique; queje ile nim ni ʼo cuʼ. Niʼpa i n-cacaj ti u cʼamic wach cu sujuj i Dios chique, quelsax na ʼuri i jubiʼ chi ʼo cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.013', 'Rumal ʼuri, xa ruʼ tijojbal tzij quin cʼut chique i winak, man ique, tupu que tzunic, n-ta quiquilo; tupu que tanic, n-quiqui ta taj, xak n-quiqui ta tu be. ', '', '&lt;span class="verse" id="Matt_13_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Rumal ʼuri, xa ruʼ tijojbal tzij quin cʼut chique i winak, man ique, tupu que tzunic, n-ta quiquilo; tupu que tanic, n-quiqui ta taj, xak n-quiqui ta tu be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.014', 'Queʼel chique pacha u bim i Dios chupam u wuj u tzʼibam can i mam Isaías chi ajbil u tzij i Dios ujer; ire xu bij:
Yix, katzij neri, qui ta na, péro n-qui ta tu be;
katzij neri, quix taken na, péro n-ta quiwil na.
', '', '&lt;span class="verse" id="Matt_13_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Queʼel chique pacha u bim i Dios chupam u wuj u tzʼibam can i mam Isaías chi ajbil u tzij i Dios ujer; ire xu bij:
Yix, katzij neri, qui ta na, péro n-qui ta tu be;
katzij neri, quix taken na, péro n-ta quiwil na.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.015', 'Man i winak-i, n-cacaj ta quiqui ta u be,
xa qui tzʼapim i qui xiquin,
xak qui mutzʼum i qui baʼwach,
man n-que tzun ta ruʼ qui baʼwach,
xak n-que tan ta ruʼ qui xiquin.
N-ta quiqui ta u be piqui cʼux,
xak n-que cʼun ta wuʼ, col qui cunbej quib.
(Queje ile u bim i Dios.)
', '', '&lt;span class="verse" id="Matt_13_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Man i winak-i, n-cacaj ta quiqui ta u be,
xa qui tzʼapim i qui xiquin,
xak qui mutzʼum i qui baʼwach,
man n-que tzun ta ruʼ qui baʼwach,
xak n-que tan ta ruʼ qui xiquin.
N-ta quiqui ta u be piqui cʼux,
xak n-que cʼun ta wuʼ, col qui cunbej quib.
(Queje ile u bim i Dios.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.016', 'Xui-ri yix, n-ix ta queje ile. Lic ʼutz iwe, man i baʼwach yix que tzunic, xak i xiquin que tanic. ', '', '&lt;span class="verse" id="Matt_13_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xui-ri yix, n-ix ta queje ile. Lic ʼutz iwe, man i baʼwach yix que tzunic, xak i xiquin que tanic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.017', 'Katzij i quin bij chiwe, je qʼui i ajbil u tzij i Dios ujer, xak je qʼui i je terejnak chirij, xcaj te rilic wach quix tijin yix chi rilic woʼor, péro n-xquil taj; xak xcaj u tayic teʼek pacha i quix tijin yix chu tayic woʼor, péro n-xqui ta taj.
', '', '&lt;span class="verse" id="Matt_13_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, je qʼui i ajbil u tzij i Dios ujer, xak je qʼui i je terejnak chirij, xcaj te rilic wach quix tijin yix chi rilic woʼor, péro n-xquil taj; xak xcaj u tayic teʼek pacha i quix tijin yix chu tayic woʼor, péro n-xqui ta taj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.018', 'Teʼuri Jesus xu bij chique:
—Chi ta ʼuri u be wach usucʼ i tijojbal tzij chi xin bij mier re i aj ticol ticon. ', '', '&lt;h2&gt;I Jesus xu sakij wach i usucʼ i tijojbal tzij u bim&lt;/h2&gt;&lt;span class="verse" id="Matt_13_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Teʼuri Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ta ʼuri u be wach usucʼ i tijojbal tzij chi xin bij mier re i aj ticol ticon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.019', 'Niʼpa i winak chi quiqui ta i tzij re i ʼatbal tzij aj chicaj, we n-quiqui ta tu be, queʼelok pacha xoʼon ija chi xtzak pa be. Queje i winak-le, man ca cʼun itzel, y cresaj i ʼutz laj tzij chi tictal piqui cʼux. ', '', '&lt;span class="verse" id="Matt_13_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Niʼpa i winak chi quiqui ta i tzij re i ʼatbal tzij aj chicaj, we n-quiqui ta tu be, queʼelok pacha xoʼon ija chi xtzak pa be. Queje i winak-le, man ca cʼun itzel, y cresaj i ʼutz laj tzij chi tictal piqui cʼux. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.020', 'Xak i ija chi xtzak pa abaj, ile pacha i winak chi quiqui ta i ʼutz laj tzij, y que quicotic xqui cojo. ', '', '&lt;span class="verse" id="Matt_13_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xak i ija chi xtzak pa abaj, ile pacha i winak chi quiqui ta i ʼutz laj tzij, y que quicotic xqui cojo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.021', 'Xui-ri, je pacha jun ticon chi n-naj taj ʼenak i raʼ; n-que najtin taj. Are ca pe i cʼax piquiwi rumal i ʼutz laj tzij qui cojom, xak ca pe i qui yoʼyaxic cumal i winak, xa cacoʼtaj canok. ', '', '&lt;span class="verse" id="Matt_13_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xui-ri, je pacha jun ticon chi n-naj taj ʼenak i raʼ; n-que najtin taj. Are ca pe i cʼax piquiwi rumal i ʼutz laj tzij qui cojom, xak ca pe i qui yoʼyaxic cumal i winak, xa cacoʼtaj canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.022', 'Xak i ija chi xtzak pa qʼuix, ile pacha i winak chi quiqui ta i ʼutz laj tzij, xui-ri, xa que cʼachir rumal i qui cʼaslemal waral chuwach i jyub taʼaj, xak i qui rayijbal re mas ubitak que; ile xa cu chup i ʼutz laj tzij. Rumal-i n-ta ca wachin chique. ', '', '&lt;span class="verse" id="Matt_13_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xak i ija chi xtzak pa qʼuix, ile pacha i winak chi quiqui ta i ʼutz laj tzij, xui-ri, xa que cʼachir rumal i qui cʼaslemal waral chuwach i jyub taʼaj, xak i qui rayijbal re mas ubitak que; ile xa cu chup i ʼutz laj tzij. Rumal-i n-ta ca wachin chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.023', 'Are ija chi xe ʼe tzak pa ʼutz laj ulew, ile pacha i winak chi quiqui ta i ʼutz laj tzij, xak quiqui ta u be. Ique lic que wachinic; ʼo jujun chique je pacha i rakan chi xu ya cien u wach; xak ʼo jujun quiqui ya sesénta u wach; xak ʼo jujun quiqui ya tréinta. (Queje ile xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_13_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are ija chi xe ʼe tzak pa ʼutz laj ulew, ile pacha i winak chi quiqui ta i ʼutz laj tzij, xak quiqui ta u be. Ique lic que wachinic; ʼo jujun chique je pacha i rakan chi xu ya cien u wach; xak ʼo jujun quiqui ya sesénta u wach; xak ʼo jujun quiqui ya tréinta. (Queje ile xu bij i Jesus chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.024', 'I Jesus xu bij chic jun tijojbal tzij chique, are iri:
—I ʼatbal tzij re aj chicaj, queʼelok pacha i tzij-i: ʼO jun achi xu tic i trígo pa rulew; chom laj ija xu tico. ', '', '&lt;h2&gt;Wach i xoʼon i mal laj ʼes chuxol i trígo&lt;/h2&gt;&lt;span class="verse" id="Matt_13_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chic jun tijojbal tzij chique, are iri:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj, queʼelok pacha i tzij-i: ʼO jun achi xu tic i trígo pa rulew; chom laj ija xu tico. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.025', 'Xui-ri, are ʼis que war conojel, xcʼun jun aj chʼoʼoj chirij i rajaw, y xu tic can mal laj ticon ruʼ i trígo (pacha trígo ca tzunic, péro n-are taj). ', '', '&lt;span class="verse" id="Matt_13_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Xui-ri, are ʼis que war conojel, xcʼun jun aj chʼoʼoj chirij i rajaw, y xu tic can mal laj ticon ruʼ i trígo (pacha trígo ca tzunic, péro n-are taj). &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.026', 'Are xe poʼic, junam xe qʼuiyic; xui-ri, are xpe u wach i trígo, are ʼuri xaʼalijin i juban chi n-trígo taj. ', '', '&lt;span class="verse" id="Matt_13_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Are xe poʼic, junam xe qʼuiyic; xui-ri, are xpe u wach i trígo, are ʼuri xaʼalijin i juban chi n-trígo taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.027', 'Teʼuri i ajchaquib xe ʼe chu bixquil ruʼ i rajaw: “Nim laj winak, i ija chi a ticom na, lic chom, ¿pa xpe wi i mal laj ʼes chi ʼo ruʼ?” xe cha. ', '', '&lt;span class="verse" id="Matt_13_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri i ajchaquib xe ʼe chu bixquil ruʼ i rajaw: “Nim laj winak, i ija chi a ticom na, lic chom, ¿pa xpe wi i mal laj ʼes chi ʼo ruʼ?” xe cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.028', 'I rajaw xu bij chique: “Xa are jun aj chʼoʼoj chwij xa ʼanaw ile.” Teʼuri i ajchaquib xqui bij che: “¿Cawaj ca ʼe ka michʼa i mal laj ʼes?” xe cha. ', '', '&lt;span class="verse" id="Matt_13_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;I rajaw xu bij chique: “Xa are jun aj chʼoʼoj chwij xa ʼanaw ile.” Teʼuri i ajchaquib xqui bij che: “¿Cawaj ca ʼe ka michʼa i mal laj ʼes?” xe cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.029', '“Mi michʼo,” xu bij i rajaw, “man we qui michʼ i mal laj ʼes, mokxa ca michʼtaj i trígo ruʼ,” xu bij. ', '', '&lt;span class="verse" id="Matt_13_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;“Mi michʼo,” xu bij i rajaw, “man we qui michʼ i mal laj ʼes, mokxa ca michʼtaj i trígo ruʼ,” xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.030', '“ʼUtz chi ya che ca qʼuiy na, junam ruʼ i trígo. Are cakaj caka jek nu chʼupic u wach i trígo, are ʼuri que in tak bi i aj molonel chi relsaxic i mal laj ʼes. Teʼuri ique quiqui ʼan manoj che, man ca porox na. We xutzin ile, caka mol i trígo y caka ya pu cʼolbal,” xu bij i rajaw. (Are ile are i tijojbal tzij xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_13_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;“ʼUtz chi ya che ca qʼuiy na, junam ruʼ i trígo. Are cakaj caka jek nu chʼupic u wach i trígo, are ʼuri que in tak bi i aj molonel chi relsaxic i mal laj ʼes. Teʼuri ique quiqui ʼan manoj che, man ca porox na. We xutzin ile, caka mol i trígo y caka ya pu cʼolbal,” xu bij i rajaw. (Are ile are i tijojbal tzij xu bij i Jesus chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.031', 'I Jesus xak xu bij jun chic tijojbal tzij chique. Xu bij:
—I ʼatbal tzij re aj chicaj queʼelok pacha coʼon jun ija re i mostas, are jun achi cu tic pa rulew. ', '', '&lt;h2&gt;I tijojbal tzij re u qʼuiyic i ija re i mostas&lt;/h2&gt;&lt;span class="verse" id="Matt_13_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I Jesus xak xu bij jun chic tijojbal tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj queʼelok pacha coʼon jun ija re i mostas, are jun achi cu tic pa rulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.032', 'I ija-le, lic chʼutin na chiquiwach i ronojel ija. Xui-ri, we xpoʼic, mas nim na queʼel chiquiwach i ronojel i ticon. Lic nim queʼelok, pacha jun cheʼ; teʼuri i tzʼiquin que cʼunic y quiqui ʼan qui soc che tak u ʼab.
', '', '&lt;span class="verse" id="Matt_13_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I ija-le, lic chʼutin na chiquiwach i ronojel ija. Xui-ri, we xpoʼic, mas nim na queʼel chiquiwach i ronojel i ticon. Lic nim queʼelok, pacha jun cheʼ; teʼuri i tzʼiquin que cʼunic y quiqui ʼan qui soc che tak u ʼab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.033', 'I Jesus xak xu bij jun chic tijojbal tzij chique. Xu bij:
—I ʼatbal tzij re aj chicaj coʼon pacha coʼon i cunbal re i caxlanwa. Ile, we jun ixok cu chapo, y cu coj oxib almul arin ruʼ, tupu xa jubiʼ i cunbal ʼo ruʼ, cwalij u wach i ʼor ronojel. (Xak queje ile coʼon i ʼatbal tzij re aj chicaj.)
', '', '&lt;h2&gt;I tijojbal tzij re i cunbal re i caxlanwa&lt;/h2&gt;&lt;span class="verse" id="Matt_13_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I Jesus xak xu bij jun chic tijojbal tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj coʼon pacha coʼon i cunbal re i caxlanwa. Ile, we jun ixok cu chapo, y cu coj oxib almul arin ruʼ, tupu xa jubiʼ i cunbal ʼo ruʼ, cwalij u wach i ʼor ronojel. (Xak queje ile coʼon i ʼatbal tzij re aj chicaj.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.034', 'I Jesus, ronojel i cʼutunic-le, xu bij chique i winak ruʼ tijojbal tzij; n-ta tzij xu bij chique, we n-are ti tijojbal tzij. ', '', '&lt;h2&gt;I Jesus, niʼpa i cʼutunic xoʼono, xa ruʼ i tijojbal tzij&lt;/h2&gt;&lt;span class="verse" id="Matt_13_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus, ronojel i cʼutunic-le, xu bij chique i winak ruʼ tijojbal tzij; n-ta tzij xu bij chique, we n-are ti tijojbal tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.035', 'Xeʼelok pacha u bim can jun ajbil u tzij i Dios ujer. Xu bij:
Ruʼ i tijojbal tzij quin ʼan wi i cʼutunic chique;
quin sakirsaj niʼpa tak i uwam u wach, cʼa chu xebal ʼij sak.
(Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Matt_13_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Xeʼelok pacha u bim can jun ajbil u tzij i Dios ujer. Xu bij:
Ruʼ i tijojbal tzij quin ʼan wi i cʼutunic chique;
quin sakirsaj niʼpa tak i uwam u wach, cʼa chu xebal ʼij sak.
(Queje ile tzʼibtal canok.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.036', 'Teʼuri i Jesus xe u ʼijla bi i winak y xoc pa ja. Are ʼuri i u tijoxelab xe tejeb pan ruʼ y xqui tzʼonoj che:
—Cha sakij chake wach usucʼ i tijojbal tzij xa bij, che i mal laj ʼes tictal can pa ticon.
', '', '&lt;h2&gt;Xu sakij wach usucʼ i tijojbal tzij re i mal laj ʼes&lt;/h2&gt;&lt;span class="verse" id="Matt_13_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u ʼijla bi i winak y xoc pa ja. Are ʼuri i u tijoxelab xe tejeb pan ruʼ y xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—Cha sakij chake wach usucʼ i tijojbal tzij xa bij, che i mal laj ʼes tictal can pa ticon.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.037', 'Teʼuri i Jesus xu bij:
—Are i ca ticaw i ʼutz laj ija, are yin chi in Achi aj Chicaj. ', '', '&lt;span class="verse" id="Matt_13_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Are i ca ticaw i ʼutz laj ija, are yin chi in Achi aj Chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.038', 'Are i pa quin tic wi, are i jyub taʼaj. Are i ʼutz laj ija, are i winak chi qui jachom quib pa ʼatbal tzij re i Dios; are i mal laj tak ʼes, are i winak ʼuri chi je re itzel. ', '', '&lt;span class="verse" id="Matt_13_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Are i pa quin tic wi, are i jyub taʼaj. Are i ʼutz laj ija, are i winak chi qui jachom quib pa ʼatbal tzij re i Dios; are i mal laj tak ʼes, are i winak ʼuri chi je re itzel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.039', 'Are i aj chʼoʼoj chirij i rajaw i ticon, are itzel chi ca ticaw i mal laj ija. Are i molonic re i ticon, are i qui molic i winak pa qʼuisbal ʼij. I ajchaquib chi quiqui mol i ticon, je are i ángel. ', '', '&lt;span class="verse" id="Matt_13_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Are i aj chʼoʼoj chirij i rajaw i ticon, are itzel chi ca ticaw i mal laj ija. Are i molonic re i ticon, are i qui molic i winak pa qʼuisbal ʼij. I ajchaquib chi quiqui mol i ticon, je are i ángel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.040', 'I mal laj tak ʼes, ca mol u chiʼ, y ca cʼak pa ʼaʼ porobal-re. Xak queje ca ʼan ni ile pa qʼuisbal re u wach i jyub taʼaj. ', '', '&lt;span class="verse" id="Matt_13_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;I mal laj tak ʼes, ca mol u chiʼ, y ca cʼak pa ʼaʼ porobal-re. Xak queje ca ʼan ni ile pa qʼuisbal re u wach i jyub taʼaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.041', 'Yin, chi in Achi aj Chicaj, que in tak li in ángel ruʼ in ticon, col qui molbej-que niʼpa i je qui tacpum i cachiʼil pa mal, xak conojel niʼpa i que ʼanaw i mal. ', '', '&lt;span class="verse" id="Matt_13_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Yin, chi in Achi aj Chicaj, que in tak li in ángel ruʼ in ticon, col qui molbej-que niʼpa i je qui tacpum i cachiʼil pa mal, xak conojel niʼpa i que ʼanaw i mal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.042', 'Teʼuri que cʼak na pa ʼaʼ pa orno. Chila que oʼ na; xak quiqui kachʼachʼej uwaque rumal ni uyej quiqui tijo. ', '', '&lt;span class="verse" id="Matt_13_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Teʼuri que cʼak na pa ʼaʼ pa orno. Chila que oʼ na; xak quiqui kachʼachʼej uwaque rumal ni uyej quiqui tijo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.043', 'Teʼuri, niʼpa i je ʼutz chuwach i Dios, que qʼuiji pa cu ʼat wi tzij ire. I qui ʼij qui chomal ique, pacha ca tzun i ʼij. We yix ʼo i xiquin chu tayic, chi ta u be ʼuri. (Queje ile xu bij i Jesus chique u tijoxelab.)
', '', '&lt;span class="verse" id="Matt_13_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Teʼuri, niʼpa i je ʼutz chuwach i Dios, que qʼuiji pa cu ʼat wi tzij ire. I qui ʼij qui chomal ique, pacha ca tzun i ʼij. We yix ʼo i xiquin chu tayic, chi ta u be ʼuri. (Queje ile xu bij i Jesus chique u tijoxelab.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.044', '(Teʼuri xu bij jun chic tijojbal tzij. Xu bij:)
—I ʼatbal tzij re aj chicaj queje pacha uqʼuial mer muktal chupam juban ulew. Ca cʼun jun achi y cu riko. N-xresaj ta lok, xa cu muk chic can u wi, y lic ca quicotic ca ʼec; ʼis que u cʼayij na niʼpa i ʼo ruʼ. Teʼuri ca petic y cu loʼ i ulew man ʼo i beyomal chupam. (Queje ile i ʼatbal tzij re aj chicaj.)
', '', '&lt;h2&gt;I tijojbal tzij re i mer muktalic&lt;/h2&gt;&lt;span class="verse" id="Matt_13_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;(Teʼuri xu bij jun chic tijojbal tzij. Xu bij:)&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj queje pacha uqʼuial mer muktal chupam juban ulew. Ca cʼun jun achi y cu riko. N-xresaj ta lok, xa cu muk chic can u wi, y lic ca quicotic ca ʼec; ʼis que u cʼayij na niʼpa i ʼo ruʼ. Teʼuri ca petic y cu loʼ i ulew man ʼo i beyomal chupam. (Queje ile i ʼatbal tzij re aj chicaj.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.045', '(ʼO jun chic tijojbal tzij xu bij chique, are iri:)
—I ʼatbal tzij re aj chicaj are pacha jun chom laj ral abaj, lic pakal rajil, pérla ca bix che. Jun aj cʼay pérla, ca ʼe chu tzucuxic i tak i pérla, y cu loʼo. ', '', '&lt;h2&gt;I tijojbal tzij re i chom laj ral abaj, pérla ca bix che&lt;/h2&gt;&lt;span class="verse" id="Matt_13_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;(ʼO jun chic tijojbal tzij xu bij chique, are iri:)&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj are pacha jun chom laj ral abaj, lic pakal rajil, pérla ca bix che. Jun aj cʼay pérla, ca ʼe chu tzucuxic i tak i pérla, y cu loʼo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.046', 'Ire, we xu rik jun chi lic chom, lic pakal rajil, ca ʼe chu cʼayixic ronojel i ubitak-re, loʼbal-re. (Queje coʼon ire ile man lic chom; xak queje ile i ʼatbal tzij re aj chicaj.)
', '', '&lt;span class="verse" id="Matt_13_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Ire, we xu rik jun chi lic chom, lic pakal rajil, ca ʼe chu cʼayixic ronojel i ubitak-re, loʼbal-re. (Queje coʼon ire ile man lic chom; xak queje ile i ʼatbal tzij re aj chicaj.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.047', '(Teʼuri xu bij i Jesus chique:)
—I ʼatbal tzij re aj chicaj queje pacha jun ataraya. I je aj chapol car, are quiqui cʼak bi i ataraya pa ya, ʼis bi chi caril cu chap lok. ', '', '&lt;h2&gt;I tijojbal tzij re i ataraya&lt;/h2&gt;&lt;span class="verse" id="Matt_13_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;(Teʼuri xu bij i Jesus chique:)&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj queje pacha jun ataraya. I je aj chapol car, are quiqui cʼak bi i ataraya pa ya, ʼis bi chi caril cu chap lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.048', 'Are nojnak chic i ataraya, caquesaj li ʼuri chu chiʼ i ya. Teʼuri que cubi chi qui tzucuxic i ʼutz chique i car. Niʼpa i ʼutz, que qui cʼol na chupam jun chicach; i ne te ʼutz, que qui cʼak bic. ', '', '&lt;span class="verse" id="Matt_13_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Are nojnak chic i ataraya, caquesaj li ʼuri chu chiʼ i ya. Teʼuri que cubi chi qui tzucuxic i ʼutz chique i car. Niʼpa i ʼutz, que qui cʼol na chupam jun chicach; i ne te ʼutz, que qui cʼak bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.049', 'Queje coʼon ni ile pa qʼuisbal ʼij; que ʼel ni ángel chi qui chayic i ne te ʼutz chiquixol i je ʼutz. ', '', '&lt;span class="verse" id="Matt_13_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;Queje coʼon ni ile pa qʼuisbal ʼij; que ʼel ni ángel chi qui chayic i ne te ʼutz chiquixol i je ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.050', 'Teʼuri que cʼak na pa ʼaʼ pa orno. Chila que oʼ na; xak quiqui kachʼachʼej uwaque rumal qui bis. (Queje ile i tijojbal tzij xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_13_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Teʼuri que cʼak na pa ʼaʼ pa orno. Chila que oʼ na; xak quiqui kachʼachʼej uwaque rumal qui bis. (Queje ile i tijojbal tzij xu bij i Jesus chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.051', 'Teʼuri i Jesus xu tzʼonoj chique:
—¿Xi ta u be ronojel iri? —coʼono.
Katzij —xqui bij—, xka ta.
', '', '&lt;h2&gt;ʼO i cʼutunic cʼacʼ, xak ʼo i cʼutunic re ujer&lt;/h2&gt;&lt;span class="verse" id="Matt_13_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Xi ta u be ronojel iri? —coʼono.&lt;p&gt; &lt;/p&gt;Katzij —xqui bij—, xka ta.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.052', 'Teʼuri xu bij chique:
—Jun tijonel re i ʼatbal tzij tzʼibtal can rumal i mam Moises, we ire nawnak che i tzij chi quin bij yin re i ʼatbal tzij aj chicaj, ʼutz re ire ʼuri. Queʼel ire pacha jun rajaw ja chi ʼo ubitak-re cʼacʼ, xak ʼo i re ujer. Che i queb-le ʼis cu chapo. (Queje ile xu bij chique u tijoxelab.)
', '', '&lt;span class="verse" id="Matt_13_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—Jun tijonel re i ʼatbal tzij tzʼibtal can rumal i mam Moises, we ire nawnak che i tzij chi quin bij yin re i ʼatbal tzij aj chicaj, ʼutz re ire ʼuri. Queʼel ire pacha jun rajaw ja chi ʼo ubitak-re cʼacʼ, xak ʼo i re ujer. Che i queb-le ʼis cu chapo. (Queje ile xu bij chique u tijoxelab.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.053', 'I Jesus, are xutzin chu bixquil i tak tijojbal tzij-le, xel chila pa Capernaum y xa ʼe pa Nazaret. ', '', '&lt;h2&gt;I Jesus n-xajwax ta cumal i rach aj tinimit&lt;/h2&gt;&lt;span class="verse" id="Matt_13_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;I Jesus, are xutzin chu bixquil i tak tijojbal tzij-le, xel chila pa Capernaum y xa ʼe pa Nazaret. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.054', 'Chila xoc chu cʼutic chique i aj Israel winak pa qui molbalʼib. Ique, are xqui ta, lic xqui bisoj che. Xqui bij ʼuri:
—¿Ipa xa ʼe u nawsaj wi rib i achi-le, chi lic ʼo u nojbal? ¿Wach u tiquilem che i milágro-le? ', '', '&lt;span class="verse" id="Matt_13_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;Chila xoc chu cʼutic chique i aj Israel winak pa qui molbalʼib. Ique, are xqui ta, lic xqui bisoj che. Xqui bij ʼuri:&lt;p&gt; &lt;/p&gt;—¿Ipa xa ʼe u nawsaj wi rib i achi-le, chi lic ʼo u nojbal? ¿Wach u tiquilem che i milágro-le? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.055', 'Ire xa are racʼal i mam chi aj ʼanol mexa, xak i u chuch xa are ati Mariy. I je u chaʼ xa are i a-Jacóbo, i a-Jose, xak i a-Simon, i a-Júdas. ', '', '&lt;span class="verse" id="Matt_13_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;Ire xa are racʼal i mam chi aj ʼanol mexa, xak i u chuch xa are ati Mariy. I je u chaʼ xa are i a-Jacóbo, i a-Jose, xak i a-Simon, i a-Júdas. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.056', 'Xak i ranab je jekel chi kuʼ yoj. ¿Ipa xretamaj wi ʼut ronojel ile? —xqui bij.
', '', '&lt;span class="verse" id="Matt_13_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Xak i ranab je jekel chi kuʼ yoj. ¿Ipa xretamaj wi ʼut ronojel ile? —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.057', 'Rumal-i, lic n-xcaj taj wach i xu bij, wach i xu ʼano. Are ʼuri i Jesus xu bij chique:
—Jun ajbil u tzij i Dios, conojel i winak quiqui coj u ʼij, xui i je rach aj tinimit n-quiqui coj tu ʼij, xak queje i je aj ruʼ rachoch —xu bij.
', '', '&lt;span class="verse" id="Matt_13_57"&gt;&lt;strong class="verseno"&gt;57&lt;/strong&gt;&amp;nbsp;Rumal-i, lic n-xcaj taj wach i xu bij, wach i xu ʼano. Are ʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Jun ajbil u tzij i Dios, conojel i winak quiqui coj u ʼij, xui i je rach aj tinimit n-quiqui coj tu ʼij, xak queje i je aj ruʼ rachoch —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '13.058', 'Ire n-ta kas milágro ʼuri xu ʼan chila, man ique n-ta u cubibal qui cʼux che.
', '', '&lt;span class="verse" id="Matt_13_58"&gt;&lt;strong class="verseno"&gt;58&lt;/strong&gt;&amp;nbsp;Ire n-ta kas milágro ʼuri xu ʼan chila, man ique n-ta u cubibal qui cʼux che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.001', 'Che tak i ʼij-le, i ma Heródes, chi ʼatol tzij re i jyub Galiléa, xu ta rason wach coʼon i Jesus. ', '', '&lt;h2&gt;Wach u camsaxic i ma Wan aj kajsanel ya&lt;/h2&gt;&lt;span class="verse" id="Matt_14_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le, i ma Heródes, chi ʼatol tzij re i jyub Galiléa, xu ta rason wach coʼon i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.002', 'Xu bij ire chique i rachiʼil:
—Ire-le, are i ʼetz ma Wan, aj kajsanel ya, ya walijnak chic che i camic —xu bij—. Are i rumal ʼuri chi ʼo i choʼab ruʼ chu ʼonquil i milágro —xcha.
', '', '&lt;span class="verse" id="Matt_14_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Xu bij ire chique i rachiʼil:&lt;p&gt; &lt;/p&gt;—Ire-le, are i ʼetz ma Wan, aj kajsanel ya, ya walijnak chic che i camic —xu bij—. Are i rumal ʼuri chi ʼo i choʼab ruʼ chu ʼonquil i milágro —xcha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.003', 'I u camic i ma Wan, queje iri xicʼaw wi: i ma Heródes xu tak u chapic i ma Wan y xqui ximo, y xqui cʼam bi pa cárcel. Queje xoʼon ire ile rumal i rixokil. I ixok-le, ati Erodiys u bi, are i rixokil i ma Felípe chi catz qui chaʼ quib ruʼ i ma Heródes. ', '', '&lt;span class="verse" id="Matt_14_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I u camic i ma Wan, queje iri xicʼaw wi: i ma Heródes xu tak u chapic i ma Wan y xqui ximo, y xqui cʼam bi pa cárcel. Queje xoʼon ire ile rumal i rixokil. I ixok-le, ati Erodiys u bi, are i rixokil i ma Felípe chi catz qui chaʼ quib ruʼ i ma Heródes. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.004', 'I ma Wan u bim ujer che i ma Heródes:
—N-ʼus taj chi ca ʼan awixokil che i ixok-le —xu bij.
', '', '&lt;span class="verse" id="Matt_14_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I ma Wan u bim ujer che i ma Heródes:&lt;p&gt; &lt;/p&gt;—N-ʼus taj chi ca ʼan awixokil che i ixok-le —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.005', 'Rumal-i, i ma Heródes craj cu camsaj i ma Wan, xui-ri xu xij rib chique i winak. Conojel i winak qui cojom chi i ma Wan lic ajbil u tzij i Dios.
', '', '&lt;span class="verse" id="Matt_14_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Rumal-i, i ma Heródes craj cu camsaj i ma Wan, xui-ri xu xij rib chique i winak. Conojel i winak qui cojom chi i ma Wan lic ajbil u tzij i Dios.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.006', 'Are xcʼun i nimaʼij re u ʼij i ma Heródes, xcʼun i ralit i ati Erodiys, xa xajaw chiquiwach conojel i je ʼo ruʼ. I ma Heródes lic kus xrilo. ', '', '&lt;span class="verse" id="Matt_14_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are xcʼun i nimaʼij re u ʼij i ma Heródes, xcʼun i ralit i ati Erodiys, xa xajaw chiquiwach conojel i je ʼo ruʼ. I ma Heródes lic kus xrilo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.007', 'Rumal-i xu bij che i ali chi cu ya na che pachique tak ca tzʼonox che, y xu cuxtaj u bi i Dios puwi wach i xu bij.
', '', '&lt;span class="verse" id="Matt_14_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Rumal-i xu bij che i ali chi cu ya na che pachique tak ca tzʼonox che, y xu cuxtaj u bi i Dios puwi wach i xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.008', 'I ali, ya bital chic che rumal u chuch wach i cu tzʼonoj; are ʼuri xu bij che i ma Heródes:
—Cha ya li u jolom i ma Wan aj kajsanel ya; waral chupam jun plato cha ya chwe —xu bij.
', '', '&lt;span class="verse" id="Matt_14_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I ali, ya bital chic che rumal u chuch wach i cu tzʼonoj; are ʼuri xu bij che i ma Heródes:&lt;p&gt; &lt;/p&gt;—Cha ya li u jolom i ma Wan aj kajsanel ya; waral chupam jun plato cha ya chwe —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.009', 'I ma ʼatol tzij lic xbisonic are xu ta ile. Xui-ri, xu tak u ʼanic pacha xbix che, man ire xu cuxtaj u bi i Dios puwi wach i xu sujuj che i ali. Xak niʼpa i je ʼo chila ruʼ ʼis xqui ta wach i xu bij; rumal-i, xu ʼano. ', '', '&lt;span class="verse" id="Matt_14_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I ma ʼatol tzij lic xbisonic are xu ta ile. Xui-ri, xu tak u ʼanic pacha xbix che, man ire xu cuxtaj u bi i Dios puwi wach i xu sujuj che i ali. Xak niʼpa i je ʼo chila ruʼ ʼis xqui ta wach i xu bij; rumal-i, xu ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.010', 'Are ʼuri xu tak i relsaxic u jolom i ma Wan pa cárcel. ', '', '&lt;span class="verse" id="Matt_14_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are ʼuri xu tak i relsaxic u jolom i ma Wan pa cárcel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.011', 'Teʼuri xqui cʼam li u jolom chupam jun plato, y xqui ya che i ali; ire xa ʼe u jacha che u chuch.
', '', '&lt;span class="verse" id="Matt_14_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Teʼuri xqui cʼam li u jolom chupam jun plato, y xqui ya che i ali; ire xa ʼe u jacha che u chuch.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.012', 'Teʼuri xe upon i u tijoxelab i ma Wan chu cʼamic i u chʼacul; xqui cʼam bic y xqui muku. Teʼuri xe ʼe qui bij che i Jesus chi xcamsax i ma Wan.
', '', '&lt;span class="verse" id="Matt_14_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri xe upon i u tijoxelab i ma Wan chu cʼamic i u chʼacul; xqui cʼam bic y xqui muku. Teʼuri xe ʼe qui bij che i Jesus chi xcamsax i ma Wan.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.013', 'I Jesus, are xu ta ile, xel chila utuquel. Xoc bi chupam jun bárco, xa ʼe che jun jyub tzʼinilic. Xui-ri, i winak lic xqui ta pa xa ʼe wi. Ique xe ʼel pa tak qui tinimit, xe sutin chu chiʼ i ya, xe ʼe chu cʼulaxic i Jesus. ', '', '&lt;h2&gt;Wach i qui tzukic joʼob mil winak xoʼon i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_14_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I Jesus, are xu ta ile, xel chila utuquel. Xoc bi chupam jun bárco, xa ʼe che jun jyub tzʼinilic. Xui-ri, i winak lic xqui ta pa xa ʼe wi. Ique xe ʼel pa tak qui tinimit, xe sutin chu chiʼ i ya, xe ʼe chu cʼulaxic i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.014', 'Ire, are xel pa bárco, xe ril tan chic uqʼuial winak je ʼo chila. Lic xril toʼbal qui wach, y xe rutzirsaj i iwabib. ', '', '&lt;span class="verse" id="Matt_14_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Ire, are xel pa bárco, xe ril tan chic uqʼuial winak je ʼo chila. Lic xril toʼbal qui wach, y xe rutzirsaj i iwabib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.015', 'Are ya coc aʼab, i u tijoxelab xe tejeb pan ruʼ y xqui bij che:
—Ya xkaj i ʼij; i waral-i n-ta winak jekel chi. Che a ʼijla bi i winak; cha ya chique que ʼe pa tak aldéa chu loʼic wach i quiqui tijo —xqui bij.
', '', '&lt;span class="verse" id="Matt_14_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are ya coc aʼab, i u tijoxelab xe tejeb pan ruʼ y xqui bij che:&lt;p&gt; &lt;/p&gt;—Ya xkaj i ʼij; i waral-i n-ta winak jekel chi. Che a ʼijla bi i winak; cha ya chique que ʼe pa tak aldéa chu loʼic wach i quiqui tijo —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.016', 'I Jesus xu bij chique:
—N-tu chac que ʼec; chi ya yix chique wach quiqui tij na —xu bij.
', '', '&lt;span class="verse" id="Matt_14_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—N-tu chac que ʼec; chi ya yix chique wach quiqui tij na —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.017', '—N-ta kuʼ yoj wach caka ya chique; xui joʼob caxlanwa ruʼ queb car —xe cha.
', '', '&lt;span class="verse" id="Matt_14_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;—N-ta kuʼ yoj wach caka ya chique; xui joʼob caxlanwa ruʼ queb car —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.018', '—Chi cʼama li waral —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_14_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;—Chi cʼama li waral —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.019', 'Teʼuri xu bij chique i winak chi que cubi chuwach tak qʼuim. Ire xu chap i joʼob caxlanwa xak i queb car, xtzun chicaj y xu tioxij che i Dios. Teʼuri xu pir i caxlanwa y xu ya chique u tijoxelab; ique xqui jach chique i winak. ', '', '&lt;span class="verse" id="Matt_14_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique i winak chi que cubi chuwach tak qʼuim. Ire xu chap i joʼob caxlanwa xak i queb car, xtzun chicaj y xu tioxij che i Dios. Teʼuri xu pir i caxlanwa y xu ya chique u tijoxelab; ique xqui jach chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.020', 'Conojel ʼis xqui tijo, y xe nojic. Xak xsobrin can cablajuj coban. ', '', '&lt;span class="verse" id="Matt_14_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Conojel ʼis xqui tijo, y xe nojic. Xak xsobrin can cablajuj coban. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.021', 'I uqʼuial winak chi xe tzukic, xui achiab ʼo joʼob mil chique, y mas puwi ile je ʼo juban ixokib, tak i acʼalab.
', '', '&lt;span class="verse" id="Matt_14_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I uqʼuial winak chi xe tzukic, xui achiab ʼo joʼob mil chique, y mas puwi ile je ʼo juban ixokib, tak i acʼalab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.022', 'Teʼuri Jesus xu bij chique u tijoxelab chi que oc pa bárco, que ʼax chic chʼakap. Xu bij chique que nabeaj nubi chuwach ire, tzʼakat que u ʼijla bi i winak. ', '', '&lt;h2&gt;Wach u binic i Jesus chuwi ya&lt;/h2&gt;&lt;span class="verse" id="Matt_14_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Teʼuri Jesus xu bij chique u tijoxelab chi que oc pa bárco, que ʼax chic chʼakap. Xu bij chique que nabeaj nubi chuwach ire, tzʼakat que u ʼijla bi i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.023', 'Xutzin ile, xel bi Jesus chuwi jun jyub utuquel chu tzʼonoxic che i Dios. Are xoc aʼab, utuquel ʼo chila chuwi jyub. ', '', '&lt;span class="verse" id="Matt_14_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xutzin ile, xel bi Jesus chuwi jun jyub utuquel chu tzʼonoxic che i Dios. Are xoc aʼab, utuquel ʼo chila chuwi jyub. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.024', 'Are ʼuri i u tijoxelab je ʼo pa bárco pa nicʼaj ya. I uwi ya lic quel chicaj, quel ʼan chirij i bárco rumal i tew chi lic xpe chirij. ', '', '&lt;span class="verse" id="Matt_14_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Are ʼuri i u tijoxelab je ʼo pa bárco pa nicʼaj ya. I uwi ya lic quel chicaj, quel ʼan chirij i bárco rumal i tew chi lic xpe chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.025', 'Are ya ca pe u sakiric, i Jesus ca tijin chi be chuwi ya, ca tijin u tejebic cuʼ. ', '', '&lt;span class="verse" id="Matt_14_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are ya ca pe u sakiric, i Jesus ca tijin chi be chuwi ya, ca tijin u tejebic cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.026', 'I u tijoxelab, are xquilo chi ire ca bin chuwi ya, lic xqui xij quib che. Rumal ʼuri, xe siqʼuin chu bixquil:
—¡Ile, jun xibinel! —xe cha.
', '', '&lt;span class="verse" id="Matt_14_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xquilo chi ire ca bin chuwi ya, lic xqui xij quib che. Rumal ʼuri, xe siqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Ile, jun xibinel! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.027', 'Are ʼuri i Jesus xu bij chique:
—¡Mi xij iwib! Xa yin. ¡Chi cowirsaj i cʼux!
', '', '&lt;span class="verse" id="Matt_14_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¡Mi xij iwib! Xa yin. ¡Chi cowirsaj i cʼux!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.028', 'Teʼuri i ma Pédro xu ʼijla y xu bij che:
—Kajwal, we lic katzij yet, cha ya na chwe quin bin awuʼ puwi i ya —xu bij.
', '', '&lt;span class="verse" id="Matt_14_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Pédro xu ʼijla y xu bij che:&lt;p&gt; &lt;/p&gt;—Kajwal, we lic katzij yet, cha ya na chwe quin bin awuʼ puwi i ya —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.029', '—Saʼaj wuʼ —xu bij i Jesus che.
I ma Pédro teʼuri xel chupam i bárco y xu jek bi binic chuwi ya, ca ʼe ruʼ i Jesus. ', '', '&lt;span class="verse" id="Matt_14_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;—Saʼaj wuʼ —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;I ma Pédro teʼuri xel chupam i bárco y xu jek bi binic chuwi ya, ca ʼe ruʼ i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.030', 'Xak teʼet xrilo chi lic nim i tew, y xu xij rib rumal. Are ʼuri xu ticba sachic pa ya; rumal-i, xsiqʼuin chu bixquil:
—¡Kajwal, chin a to!
', '', '&lt;span class="verse" id="Matt_14_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xak teʼet xrilo chi lic nim i tew, y xu xij rib rumal. Are ʼuri xu ticba sachic pa ya; rumal-i, xsiqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Kajwal, chin a to!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.031', 'I Jesus juntir xu chap pan che u ʼab. Xu bij che:
—A-Pédro, ¿n-tu cubibal a cʼux chwe? ¿Wuchac xa xij awib? —xcha.
', '', '&lt;span class="verse" id="Matt_14_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I Jesus juntir xu chap pan che u ʼab. Xu bij che:&lt;p&gt; &lt;/p&gt;—A-Pédro, ¿n-tu cubibal a cʼux chwe? ¿Wuchac xa xij awib? —xcha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.032', 'Are xe oc bi ique pa bárco, i tew xuxlanic. ', '', '&lt;span class="verse" id="Matt_14_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Are xe oc bi ique pa bárco, i tew xuxlanic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.033', 'Are ʼuri niʼpa i je ʼo pa bárco, xe xuqui chuwach i Jesus.
—Katzij yet at u Cʼojol i Dios —xe cha.
', '', '&lt;span class="verse" id="Matt_14_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Are ʼuri niʼpa i je ʼo pa bárco, xe xuqui chuwach i Jesus.&lt;p&gt; &lt;/p&gt;—Katzij yet at u Cʼojol i Dios —xe cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.034', 'Teʼuri xqui maj chubi qui be puwi ya, y xe upon chu chiʼ i taʼaj Genesaret. ', '', '&lt;h2&gt;I Jesus xe u cunaj iwabib pa tinimit Genesaret&lt;/h2&gt;&lt;span class="verse" id="Matt_14_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri xqui maj chubi qui be puwi ya, y xe upon chu chiʼ i taʼaj Genesaret. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.035', 'I winak chila, are xquetamaj are i Jesus xuponic, xqui paxsaj rason ronojel i taʼaj-le. Teʼuri xe qui cʼam li niʼpa i iwabib ruʼ i Jesus. ', '', '&lt;span class="verse" id="Matt_14_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I winak chila, are xquetamaj are i Jesus xuponic, xqui paxsaj rason ronojel i taʼaj-le. Teʼuri xe qui cʼam li niʼpa i iwabib ruʼ i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '14.036', 'Lic xqui tzʼonoj che, chi cu ya na chique i iwabib quiqui yin na jubiʼ u chiʼ u ʼuʼ; y niʼpa i xqui chap ni u ʼuʼ, ʼis xe utzir na.
', '', '&lt;span class="verse" id="Matt_14_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Lic xqui tzʼonoj che, chi cu ya na chique i iwabib quiqui yin na jubiʼ u chiʼ u ʼuʼ; y niʼpa i xqui chap ni u ʼuʼ, ʼis xe utzir na.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.001', 'Teʼuri ʼo jujun chique i aj Fariséo winak xe upon ruʼ i Jesus, xak juban tijonel re i ʼatbal tzij re i mam Moises. Ique je petnak cʼa pa tinimit Jerusalen. Are ʼuri xqui tzʼonoj che i Jesus:
', '', '&lt;h2&gt;Wach ca ʼanaw che jun winak chi n-chʼam taj&lt;/h2&gt;&lt;span class="verse" id="Matt_15_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri ʼo jujun chique i aj Fariséo winak xe upon ruʼ i Jesus, xak juban tijonel re i ʼatbal tzij re i mam Moises. Ique je petnak cʼa pa tinimit Jerusalen. Are ʼuri xqui tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.002', '—I a tijoxelab yet, ¿wuchac n-quiqui ʼan ti ka costúmbre chi qui cʼutum can i katit kamam? N-quiqui coj ta retalil che i ujer ʼatbal tzij re u chʼajic qui ʼab are quiqui tij i qui wa —xqui bij che.
', '', '&lt;span class="verse" id="Matt_15_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—I a tijoxelab yet, ¿wuchac n-quiqui ʼan ti ka costúmbre chi qui cʼutum can i katit kamam? N-quiqui coj ta retalil che i ujer ʼatbal tzij re u chʼajic qui ʼab are quiqui tij i qui wa —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.003', 'I Jesus xu laʼ u wach chique:
—Yix ʼut, ¿wuchac xa quix terej chirij i costúmbre? ¿Wuchac ni cojom ti retalil che i méro tzij u bim can i Dios? Xa i canam can u ʼonquil. ', '', '&lt;span class="verse" id="Matt_15_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Yix ʼut, ¿wuchac xa quix terej chirij i costúmbre? ¿Wuchac ni cojom ti retalil che i méro tzij u bim can i Dios? Xa i canam can u ʼonquil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.004', 'I Dios u bim na: “Cha cojo qui ʼij a kajaw a chuch,” xak u bim na: “Pachin cʼax ca chʼaw chiquij u kajaw u chuch, cha camsaxok.” Queje ile u bim i Dios. ', '', '&lt;span class="verse" id="Matt_15_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Dios u bim na: “Cha cojo qui ʼij a kajaw a chuch,” xak u bim na: “Pachin cʼax ca chʼaw chiquij u kajaw u chuch, cha camsaxok.” Queje ile u bim i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.005', 'Xui-ri, yix qui ya na che jun achi cu bij che u kajaw, cu bij che u chuch: “N-cat in to taj,” cu bij che, “man niʼpa i ʼo wuʼ in sujum chic che i Dios,” cu bij che. ', '', '&lt;span class="verse" id="Matt_15_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xui-ri, yix qui ya na che jun achi cu bij che u kajaw, cu bij che u chuch: “N-cat in to taj,” cu bij che, “man niʼpa i ʼo wuʼ in sujum chic che i Dios,” cu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.006', 'Yix, we queje cu bij i jun ile, qui bij na ʼuri, n-ta chic chukul que u to u kajaw u chuch. Queʼel ʼut chi i kajsam u ʼij u tzij u pixab i Dios; xa quix terej chirij i costúmbre yix. ', '', '&lt;span class="verse" id="Matt_15_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Yix, we queje cu bij i jun ile, qui bij na ʼuri, n-ta chic chukul que u to u kajaw u chuch. Queʼel ʼut chi i kajsam u ʼij u tzij u pixab i Dios; xa quix terej chirij i costúmbre yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.007', 'Yix ¡xa queb i palaj! Lic katzij wach u tzʼibam can i ujer ajbil u tzij i Dios, mam Isaías. Lic u tzʼibam canok wach quix tijin yix chu ʼanic woʼor. Xu bij canok chi Dios u bim na:
', '', '&lt;span class="verse" id="Matt_15_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Yix ¡xa queb i palaj! Lic katzij wach u tzʼibam can i ujer ajbil u tzij i Dios, mam Isaías. Lic u tzʼibam canok wach quix tijin yix chu ʼanic woʼor. Xu bij canok chi Dios u bim na:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.008', 'I winak-i lic quiqui coj in ʼij ruʼ qui chiʼ,
xui-ri, ruʼ qui cʼux n-quiqui ʼan taj.
', '', '&lt;span class="verse" id="Matt_15_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I winak-i lic quiqui coj in ʼij ruʼ qui chiʼ,
xui-ri, ruʼ qui cʼux n-quiqui ʼan taj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.009', 'I u cojic in ʼij quiqui ʼano, lic n-ta cu chacuj,
man i tzij que tijin chu cʼutic,
xa cuʼ winak petnak wi.
(Queje ile u bim i Dios —xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_15_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I u cojic in ʼij quiqui ʼano, lic n-ta cu chacuj,
man i tzij que tijin chu cʼutic,
xa cuʼ winak petnak wi.
(Queje ile u bim i Dios —xu bij i Jesus chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.010', 'Teʼuri xe u siqʼuij i winak ruʼ y xu bij chique:
—Chi ta na wach quin bij; chi bisoj u tayic u be wach ca ʼanaw che jun winak chi n-chʼam taj. ', '', '&lt;span class="verse" id="Matt_15_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri xe u siqʼuij i winak ruʼ y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ta na wach quin bij; chi bisoj u tayic u be wach ca ʼanaw che jun winak chi n-chʼam taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.011', 'N-are ti wach coc pu chiʼ ca ʼanaw che chi n-chʼam taj, xa are i tzij chi quel li pu chiʼ; are ca ʼanaw ʼuri che chi n-chʼam taj —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_15_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;N-are ti wach coc pu chiʼ ca ʼanaw che chi n-chʼam taj, xa are i tzij chi quel li pu chiʼ; are ca ʼanaw ʼuri che chi n-chʼam taj —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.012', 'Teʼuri i u tijoxelab i Jesus xe tejeb pan ruʼ, y xqui bij che:
—¿Xawil yet chi xpe coywal i aj Fariséo che i tzij-le? —xqui bij che.
', '', '&lt;span class="verse" id="Matt_15_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab i Jesus xe tejeb pan ruʼ, y xqui bij che:&lt;p&gt; &lt;/p&gt;—¿Xawil yet chi xpe coywal i aj Fariséo che i tzij-le? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.013', '(—N-ta quin chʼob yin che ile. I qui patan ique, n-ruʼ ti in Kajaw petnak wi.) Xu bij i Jesus: —Niʼpa i ticon chi nu ticom ti in Kajaw chicaj, xa que michʼ na ruʼ i raʼ. ', '', '&lt;span class="verse" id="Matt_15_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;(—N-ta quin chʼob yin che ile. I qui patan ique, n-ruʼ ti in Kajaw petnak wi.) Xu bij i Jesus: —Niʼpa i ticon chi nu ticom ti in Kajaw chicaj, xa que michʼ na ruʼ i raʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.014', 'Mi coj retalil chique ique; xa je mawach laj tak cʼambeal chique i juban chic mawach. Jun mawach, we cu cʼam bi jun rach mawach, junam ʼuri que ʼe tzak pa siwan —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_15_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Mi coj retalil chique ique; xa je mawach laj tak cʼambeal chique i juban chic mawach. Jun mawach, we cu cʼam bi jun rach mawach, junam ʼuri que ʼe tzak pa siwan —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.015', 'Teʼuri i ma Pédro xu bij che:
—Cha sakij ni tijojbal tzij chake chi xa bij-le —coʼono.
', '', '&lt;span class="verse" id="Matt_15_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Pédro xu bij che:&lt;p&gt; &lt;/p&gt;—Cha sakij ni tijojbal tzij chake chi xa bij-le —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.016', 'Are ʼuri i Jesus xu bij:
—¿Yix, maja bari qui ta u be wach i quin bij? ', '', '&lt;span class="verse" id="Matt_15_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—¿Yix, maja bari qui ta u be wach i quin bij? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.017', '¿N-qui ta ta bari u be ile, chi wach tak coc chupam jun winak n-are ti ca ʼanaw che chi n-chʼam taj? Xa coc chupam u chiʼ, y ca ʼe che u pam; xa quicʼawic y quel tan chic. ', '', '&lt;span class="verse" id="Matt_15_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;¿N-qui ta ta bari u be ile, chi wach tak coc chupam jun winak n-are ti ca ʼanaw che chi n-chʼam taj? Xa coc chupam u chiʼ, y ca ʼe che u pam; xa quicʼawic y quel tan chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.018', 'Xui-ri, wach i quel pu chiʼ, pu cʼux ʼuri petnak wi. Are ile are ca ʼanaw chique i winak chi ne te chʼam. ', '', '&lt;span class="verse" id="Matt_15_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xui-ri, wach i quel pu chiʼ, pu cʼux ʼuri petnak wi. Are ile are ca ʼanaw chique i winak chi ne te chʼam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.019', 'Man xa piqui cʼux ca pe wi ronojel tak n-ʼus taj. Are rumal ʼuri ca pe i qui nojbal chi n-ʼus taj; are rumal ʼuri ʼo i camsanic, xak ʼo i je cʼulanic quiqui kajsaj qui ʼij cuʼ i n-cachijil ta quib; xak ʼo i ne te cʼulanic xak quiqui kajsaj qui ʼij chiquiwach; are ile chi ca ʼanaw chique chi ne te chʼam, y piqui cʼux petnak wi. Xak queje iliʼomab, xak queje i je aj ʼanol mentir, xak queje i je aj nucʼul tzij. ', '', '&lt;span class="verse" id="Matt_15_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Man xa piqui cʼux ca pe wi ronojel tak n-ʼus taj. Are rumal ʼuri ca pe i qui nojbal chi n-ʼus taj; are rumal ʼuri ʼo i camsanic, xak ʼo i je cʼulanic quiqui kajsaj qui ʼij cuʼ i n-cachijil ta quib; xak ʼo i ne te cʼulanic xak quiqui kajsaj qui ʼij chiquiwach; are ile chi ca ʼanaw chique chi ne te chʼam, y piqui cʼux petnak wi. Xak queje iliʼomab, xak queje i je aj ʼanol mentir, xak queje i je aj nucʼul tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.020', 'Are i niʼpa ile are ca ʼanaw chique i winak chi ne te chʼam. We jun n-cu chʼaj tu ʼab are cu tij u wa, ile ʼut n-are ti ca ʼanaw che chi n-chʼam tu pu cʼux. (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Matt_15_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are i niʼpa ile are ca ʼanaw chique i winak chi ne te chʼam. We jun n-cu chʼaj tu ʼab are cu tij u wa, ile ʼut n-are ti ca ʼanaw che chi n-chʼam tu pu cʼux. (Queje xu bij i Jesus ile chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.021', 'Xoʼon chupanok, i Jesus xel chila, xa ʼe cʼa pa jyub pa ʼo wi i queb tinimit, Tíro y Sidon. ', '', '&lt;h2&gt;Wach rutzirsaxic i ralit jun ixok chi n-aj Israel taj&lt;/h2&gt;&lt;span class="verse" id="Matt_15_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, i Jesus xel chila, xa ʼe cʼa pa jyub pa ʼo wi i queb tinimit, Tíro y Sidon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.022', 'Chila jekel wi jun ixok n-aj Israel taj; ire xcʼun ruʼ i Jesus y co xu bij:
—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David! ¡Chawila toʼbal in wach! Jun walit lic ʼo itzel tew che, mas cu tij uyej rumal —coʼono.
', '', '&lt;span class="verse" id="Matt_15_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Chila jekel wi jun ixok n-aj Israel taj; ire xcʼun ruʼ i Jesus y co xu bij:&lt;p&gt; &lt;/p&gt;—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David! ¡Chawila toʼbal in wach! Jun walit lic ʼo itzel tew che, mas cu tij uyej rumal —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.023', 'I Jesus n-ta xu bij che. I u tijoxelab xe tejeb ruʼ y lic xqui tzʼonoj che:
—Cha taka bi ixok-le, man toʼ ca siqʼuinic teren li chikij —xe cha.
', '', '&lt;span class="verse" id="Matt_15_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I Jesus n-ta xu bij che. I u tijoxelab xe tejeb ruʼ y lic xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—Cha taka bi ixok-le, man toʼ ca siqʼuinic teren li chikij —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.024', 'I Jesus n-xu laʼ tu wach chique, xa xu bij jun moxbal tzij che ixok:
—I Dios in u takom na lok xui chique i je aj Israel winak, chi pacha je chij je sachnak —coʼono.
', '', '&lt;span class="verse" id="Matt_15_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I Jesus n-xu laʼ tu wach chique, xa xu bij jun moxbal tzij che ixok:&lt;p&gt; &lt;/p&gt;—I Dios in u takom na lok xui chique i je aj Israel winak, chi pacha je chij je sachnak —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.025', 'I ixok xtejeb ruʼ y xuqui chuwach.
—Nim laj winak, ¡chin a to! —xu bij.
', '', '&lt;span class="verse" id="Matt_15_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I ixok xtejeb ruʼ y xuqui chuwach.&lt;p&gt; &lt;/p&gt;—Nim laj winak, ¡chin a to! —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.026', 'I Jesus xu bij jun chic moxbal tzij che ixok:
—N-ʼus taj ca maji ni qui wa i acʼalab, we xa ca yaʼ chique i tzʼi —coʼono.
', '', '&lt;span class="verse" id="Matt_15_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I Jesus xu bij jun chic moxbal tzij che ixok:&lt;p&gt; &lt;/p&gt;—N-ʼus taj ca maji ni qui wa i acʼalab, we xa ca yaʼ chique i tzʼi —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.027', '—Katzij ile, nim laj winak —xu bij i ixok—, xui-ri, xak i tzʼi xak quiqui mol ni ucʼaj i wa chi ca puk chuxeʼ i mexa re i cajaw —xu bij.
', '', '&lt;span class="verse" id="Matt_15_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;—Katzij ile, nim laj winak —xu bij i ixok—, xui-ri, xak i tzʼi xak quiqui mol ni ucʼaj i wa chi ca puk chuxeʼ i mexa re i cajaw —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.028', 'I Jesus xu bij ʼuri:
—Ixok, ¡lic nim u cubibal a cʼux chwe! Quin ʼan na che awacʼal pacha i cawaj —xu bij.
Are xu bij i Jesus ile, juntir xutzir i ralit i ixok.
', '', '&lt;span class="verse" id="Matt_15_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;I Jesus xu bij ʼuri:&lt;p&gt; &lt;/p&gt;—Ixok, ¡lic nim u cubibal a cʼux chwe! Quin ʼan na che awacʼal pacha i cawaj —xu bij.&lt;p&gt; &lt;/p&gt;Are xu bij i Jesus ile, juntir xutzir i ralit i ixok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.029', 'Xoʼon chupanok, i Jesus xel chila y xu tzalej u chiʼ i alagun Galiléa. Teʼuri xel bi puwi jun jyub y xcubi chila. ', '', '&lt;h2&gt;Wach cutzirsaxic uqʼuial winak chi ne te aj Israel&lt;/h2&gt;&lt;span class="verse" id="Matt_15_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, i Jesus xel chila y xu tzalej u chiʼ i alagun Galiléa. Teʼuri xel bi puwi jun jyub y xcubi chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.030', 'Lic je qʼui i winak xe upon ruʼ. Xqui cʼam bi i je sic, xak i je mawach, xak i je mem, xak i je clis, xak juban chic iwabib, je qʼui. Niʼpa i xe qui ya chuwach i Jesus, conojel xe rutzirsaj. ', '', '&lt;span class="verse" id="Matt_15_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Lic je qʼui i winak xe upon ruʼ. Xqui cʼam bi i je sic, xak i je mawach, xak i je mem, xak i je clis, xak juban chic iwabib, je qʼui. Niʼpa i xe qui ya chuwach i Jesus, conojel xe rutzirsaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.031', 'Rumal-i, i winak lic xqui bisoj, man i mem xe chʼaw na; xak i je clis, xe utzir na; xak i je sic, xe binic; xak i mawach, xe tzun na. ʼIs xe oc chu cojic u ʼij i Dios, chi qui Dios i aj Israel.
', '', '&lt;span class="verse" id="Matt_15_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Rumal-i, i winak lic xqui bisoj, man i mem xe chʼaw na; xak i je clis, xe utzir na; xak i je sic, xe binic; xak i mawach, xe tzun na. ʼIs xe oc chu cojic u ʼij i Dios, chi qui Dios i aj Israel.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.032', 'Xoʼon chupanok, i Jesus xe u siqʼuij i u tijoxelab ruʼ, y xu bij chique:
—Toʼ qui wach i winak-i, man are oxib ʼij iri chi je ʼo chi wuʼ; n-ta chic wach quiqui tijo. N-cwaj ta que in tak bi che cachoch we n-ti wa qui tijom bic, mokxa que tzak pa be —xu bij chique.
', '', '&lt;h2&gt;Wach i qui tzukic cajib mil winak xoʼon i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_15_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, i Jesus xe u siqʼuij i u tijoxelab ruʼ, y xu bij chique:&lt;p&gt; &lt;/p&gt;—Toʼ qui wach i winak-i, man are oxib ʼij iri chi je ʼo chi wuʼ; n-ta chic wach quiqui tijo. N-cwaj ta que in tak bi che cachoch we n-ti wa qui tijom bic, mokxa que tzak pa be —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.033', 'I u tijoxelab xqui bij:
—¿Pa coj ʼe wi chu cʼamic i wa waral, tzukbal-que uqʼuial winak-i? N-ta jun jekel waral —xe cha.
', '', '&lt;span class="verse" id="Matt_15_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I u tijoxelab xqui bij:&lt;p&gt; &lt;/p&gt;—¿Pa coj ʼe wi chu cʼamic i wa waral, tzukbal-que uqʼuial winak-i? N-ta jun jekel waral —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.034', 'I Jesus xu tzʼonoj chique:
¿Niʼpa i caxlanwa ʼo iwuʼ?
—Wukub —xe cha—, ruʼ jubiʼ ral tak car.
', '', '&lt;span class="verse" id="Matt_15_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;¿Niʼpa i caxlanwa ʼo iwuʼ?&lt;p&gt; &lt;/p&gt;—Wukub —xe cha—, ruʼ jubiʼ ral tak car.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.035', 'Teʼuri i Jesus xu bij chique i winak chi che cubi chuwach ulew. ', '', '&lt;span class="verse" id="Matt_15_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique i winak chi che cubi chuwach ulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.036', 'Ire xu chap i wukub caxlanwa, tak i car. Xu tioxij che i Dios, xu piro y xu ya chique u tijoxelab. Ique xqui jach bi chique i winak. ', '', '&lt;span class="verse" id="Matt_15_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Ire xu chap i wukub caxlanwa, tak i car. Xu tioxij che i Dios, xu piro y xu ya chique u tijoxelab. Ique xqui jach bi chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.037', 'Conojel ʼis xqui tijo y xe nojic. Are xquelej ile, i u tijoxelab xqui mol niʼpa i chʼakatak chi xsobrinic; xnoj wukub chicach che. ', '', '&lt;span class="verse" id="Matt_15_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Conojel ʼis xqui tijo y xe nojic. Are xquelej ile, i u tijoxelab xqui mol niʼpa i chʼakatak chi xsobrinic; xnoj wukub chicach che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.038', 'I uqʼuial winak chi xe tzukic, xui achiab ʼo cajib mil chique, y mas puwi ile je ʼo ni ixokib, xak i acʼalab. ', '', '&lt;span class="verse" id="Matt_15_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;I uqʼuial winak chi xe tzukic, xui achiab ʼo cajib mil chique, y mas puwi ile je ʼo ni ixokib, xak i acʼalab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '15.039', 'Are ʼuri i Jesus xe u ʼijla bi i winak, xoc pa bárco, y xa ʼe che i jyub Magdála.
', '', '&lt;span class="verse" id="Matt_15_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xe u ʼijla bi i winak, xoc pa bárco, y xa ʼe che i jyub Magdála.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.001', 'Xoʼon panok, ʼo jujun aj Fariséo winak cucʼam quib cuʼ juban aj Saducéo xe upon ruʼ i Jesus. Cacaj ique quiqui cʼam u pam i Jesus, rumal-i xqui bij che chi cu cʼut jun milágro chique, cʼutbal-re chi Dios ʼo ruʼ. ', '', '&lt;h2&gt;I aj Fariséo xak i aj Saducéo cacaj rilic jun milágro&lt;/h2&gt;&lt;span class="verse" id="Matt_16_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok, ʼo jujun aj Fariséo winak cucʼam quib cuʼ juban aj Saducéo xe upon ruʼ i Jesus. Cacaj ique quiqui cʼam u pam i Jesus, rumal-i xqui bij che chi cu cʼut jun milágro chique, cʼutbal-re chi Dios ʼo ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.002', 'Ire xu laʼ u wach chique:
—Yix lic iwetaʼam usucʼ u wach i caj, wach i coʼono. Are ca kaj i ʼij, we cak, qui bij yix chi “Chwek, chom laj ʼij, man cak i uwacaj,” qui bij. ', '', '&lt;span class="verse" id="Matt_16_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Ire xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Yix lic iwetaʼam usucʼ u wach i caj, wach i coʼono. Are ca kaj i ʼij, we cak, qui bij yix chi “Chwek, chom laj ʼij, man cak i uwacaj,” qui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.003', 'Xak we ʼo jun u sakirbal, i uwacaj lic cak, xak ʼekmuj, qui bij yix ʼuri chi “N-chom ti ʼij woʼor, man i uwacaj xa cak y ʼekmuj,” qui bij. Yix lic quiwil nu be i uwacaj, wach i coʼono; xui-ri, n-quiwil tu be wach ca tijin i Dios chu ʼonquil che tak i ʼij-i. ', '', '&lt;span class="verse" id="Matt_16_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xak we ʼo jun u sakirbal, i uwacaj lic cak, xak ʼekmuj, qui bij yix ʼuri chi “N-chom ti ʼij woʼor, man i uwacaj xa cak y ʼekmuj,” qui bij. Yix lic quiwil nu be i uwacaj, wach i coʼono; xui-ri, n-quiwil tu be wach ca tijin i Dios chu ʼonquil che tak i ʼij-i. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.004', 'I winak woʼor, lic je mal na, lic n-quiqui coj ta chic i Dios. Yix quiwaj ca ʼan ni jun milágro chiwach, cʼutbal re u choʼab i Dios wuʼ. Xui-ri, n-ta jun milágro ca ʼan ni chiwach, xui pacha i xa ʼan che i ma Jonas ujer. (Queje ile xu bij i Jesus chique.) Teʼuri xe u canaj canok y xa ʼec.
', '', '&lt;span class="verse" id="Matt_16_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I winak woʼor, lic je mal na, lic n-quiqui coj ta chic i Dios. Yix quiwaj ca ʼan ni jun milágro chiwach, cʼutbal re u choʼab i Dios wuʼ. Xui-ri, n-ta jun milágro ca ʼan ni chiwach, xui pacha i xa ʼan che i ma Jonas ujer. (Queje ile xu bij i Jesus chique.) Teʼuri xe u canaj canok y xa ʼec.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.005', 'Teʼuri i Jesus cuʼ u tijoxelab, xe ʼax chic chʼaka alagun. Ique, xsach chiqui cʼux n-ta qui wa xqui cʼam bic. ', '', '&lt;span class="verse" id="Matt_16_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus cuʼ u tijoxelab, xe ʼax chic chʼaka alagun. Ique, xsach chiqui cʼux n-ta qui wa xqui cʼam bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.006', 'Are ʼuri i Jesus xu bij chique:
—Chitapeʼ, chi ʼana cwent chique i aj Fariséo xak i aj Saducéo; xa je pacha cunbal re i caxlanwa —xu bij chique.
', '', '&lt;span class="verse" id="Matt_16_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chitapeʼ, chi ʼana cwent chique i aj Fariséo xak i aj Saducéo; xa je pacha cunbal re i caxlanwa —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.007', 'I u tijoxelab n-xqui ta tu be; xe oc chu bixquil chiquiwach:
—Xu bij ire ile man yoj n-ta ka caxlanwa ka cʼamom lok —xqui bij.
', '', '&lt;span class="verse" id="Matt_16_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I u tijoxelab n-xqui ta tu be; xe oc chu bixquil chiquiwach:&lt;p&gt; &lt;/p&gt;—Xu bij ire ile man yoj n-ta ka caxlanwa ka cʼamom lok —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.008', 'I Jesus xu naʼo wach quiqui bij.
—¿Wuchac qui bij ile chi n-ta ka caxlanwa? —xu bij chique. ¡Yix n-ta kas u cubibal i cʼux chwe! ', '', '&lt;span class="verse" id="Matt_16_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I Jesus xu naʼo wach quiqui bij.&lt;p&gt; &lt;/p&gt;—¿Wuchac qui bij ile chi n-ta ka caxlanwa? —xu bij chique. ¡Yix n-ta kas u cubibal i cʼux chwe! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.009', '¿Maja qui ta u be ʼuri? ¿N-ca cʼun ta bawa chi cʼux chi yin, che i joʼob caxlanwa, xe in tzuk ni joʼob mil achiab? ¿I niʼpa i coban xsobrinic? ', '', '&lt;span class="verse" id="Matt_16_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;¿Maja qui ta u be ʼuri? ¿N-ca cʼun ta bawa chi cʼux chi yin, che i joʼob caxlanwa, xe in tzuk ni joʼob mil achiab? ¿I niʼpa i coban xsobrinic? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.010', '¿Xak n-ca cʼun ta chic chi cʼux chi yin, che i wukub caxlanwa, xe in tzuk ni cajib mil achiab? ¿I niʼpa chicach xsobrin ʼuri? ', '', '&lt;span class="verse" id="Matt_16_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;¿Xak n-ca cʼun ta chic chi cʼux chi yin, che i wukub caxlanwa, xe in tzuk ni cajib mil achiab? ¿I niʼpa chicach xsobrin ʼuri? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.011', '¿Wuchac ʼuri n-qui ta tu be chi n-are ta rumal i caxlanwa xin bij yin ile? ¡Chi ʼana cwent che i cunbal re i caxlanwa que i aj Fariséo xak i aj Saducéo! —xu bij chique.
', '', '&lt;span class="verse" id="Matt_16_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;¿Wuchac ʼuri n-qui ta tu be chi n-are ta rumal i caxlanwa xin bij yin ile? ¡Chi ʼana cwent che i cunbal re i caxlanwa que i aj Fariséo xak i aj Saducéo! —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.012', 'Teʼuri i u tijoxelab xqui ta u be chi ire n-are ti cu bij i cunbal re i caxlanwa. Are i usucʼ i xu bij, are chi qui ʼana cwent che i qui cʼutunic i aj Fariséo xak i aj Saducéo.
', '', '&lt;span class="verse" id="Matt_16_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab xqui ta u be chi ire n-are ti cu bij i cunbal re i caxlanwa. Are i usucʼ i xu bij, are chi qui ʼana cwent che i qui cʼutunic i aj Fariséo xak i aj Saducéo.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.013', 'I Jesus, are xe upon pa jyub Cesaréa re i Filípo, xu bij chique u tijoxelab:
—¿Wach quiqui bij i winak chi in pachin yin chi in Achi aj Chiaj? ¿Wach quiqui bij ique? —coʼono.
', '', '&lt;h2&gt;Xu bij i ma Pédro chi Jesus are i Tolque chi coyʼem&lt;/h2&gt;&lt;span class="verse" id="Matt_16_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I Jesus, are xe upon pa jyub Cesaréa re i Filípo, xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—¿Wach quiqui bij i winak chi in pachin yin chi in Achi aj Chiaj? ¿Wach quiqui bij ique? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.014', 'I u tijoxelab xqui bij:
—ʼO jujun quiqui bij chi yet at i ʼetz ma Wan aj kajsanel ya, quiqui bij. Xak ʼo jujun quiqui bij chi yet at i ujer mam Elías; xak ʼo jujun quiqui bij chi at i ujer mam Jeremías, o mok at jun chic ujer ajbil u tzij i Dios. (Queje ile xqui bij i tijoxelab che i Jesus.)
', '', '&lt;span class="verse" id="Matt_16_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I u tijoxelab xqui bij:&lt;p&gt; &lt;/p&gt;—ʼO jujun quiqui bij chi yet at i ʼetz ma Wan aj kajsanel ya, quiqui bij. Xak ʼo jujun quiqui bij chi yet at i ujer mam Elías; xak ʼo jujun quiqui bij chi at i ujer mam Jeremías, o mok at jun chic ujer ajbil u tzij i Dios. (Queje ile xqui bij i tijoxelab che i Jesus.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.015', 'Are ʼuri xu bij chique:
—Y yix, ¿wach qui bij chi in pachin yin? —coʼono.
', '', '&lt;span class="verse" id="Matt_16_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—Y yix, ¿wach qui bij chi in pachin yin? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.016', 'I ma Simon Pédro xu bij:
—Yet at Crísto (i Tolke chi at koyʼem); at u Cʼojol i Dios cʼaslic —coʼono.
', '', '&lt;span class="verse" id="Matt_16_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I ma Simon Pédro xu bij:&lt;p&gt; &lt;/p&gt;—Yet at Crísto (i Tolke chi at koyʼem); at u Cʼojol i Dios cʼaslic —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.017', '—¡ʼUtz awe yet! —xu bij i Jesus che—. ʼUtz i xa bij, a-Simon Bar-Jonas. N-ruʼ ta winak petnak wi i tzij-le; lic ruʼ in Kajaw chicaj petnak wi. ', '', '&lt;span class="verse" id="Matt_16_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;—¡ʼUtz awe yet! —xu bij i Jesus che—. ʼUtz i xa bij, a-Simon Bar-Jonas. N-ruʼ ta winak petnak wi i tzij-le; lic ruʼ in Kajaw chicaj petnak wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.018', 'Rumal-i, quin bij chawe: yet at a-Pédro, i usucʼ i a bi queʼelok “abaj”; puwi i abaj-le quin yac i wachoch. Niʼpa i je jutzobaj re i Dios, lic n-ca sachsax tu wach i tzobajil-le; mi tene i u choʼab i camic ca tique che. ', '', '&lt;span class="verse" id="Matt_16_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Rumal-i, quin bij chawe: yet at a-Pédro, i usucʼ i a bi queʼelok “abaj”; puwi i abaj-le quin yac i wachoch. Niʼpa i je jutzobaj re i Dios, lic n-ca sachsax tu wach i tzobajil-le; mi tene i u choʼab i camic ca tique che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.019', 'Yin quin ya i lawe chawe, chi jakbal-re i ocbal pa cu ʼat wi tzij i Dios. Wach tak i ca ʼatij yet waral chuwach i jyub taʼaj, i Dios cu ya u wach awuʼ. Xak wach tak ca ya na yet chique i je re i Dios chi quiqui ʼan waral chuwach i jyub taʼaj, xak ca yaʼ na chique rumal i Dios —xu bij che i ma Pédro.
', '', '&lt;span class="verse" id="Matt_16_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Yin quin ya i lawe chawe, chi jakbal-re i ocbal pa cu ʼat wi tzij i Dios. Wach tak i ca ʼatij yet waral chuwach i jyub taʼaj, i Dios cu ya u wach awuʼ. Xak wach tak ca ya na yet chique i je re i Dios chi quiqui ʼan waral chuwach i jyub taʼaj, xak ca yaʼ na chique rumal i Dios —xu bij che i ma Pédro.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.020', 'Teʼuri xu bij chique u tijoxelab mi jun quiqui lap wi, we are ire i Tolque chi coyʼem.
', '', '&lt;span class="verse" id="Matt_16_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique u tijoxelab mi jun quiqui lap wi, we are ire i Tolque chi coyʼem.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.021', 'Teʼuri i Jesus xoc chu sakixic chique u tijoxelab chi ʼo u chac ca ʼe ire pa Jerusalen, xui-ri i nimak tak mamʼib chila, junam cuʼ i cajʼatzil i sacerdóte, tak i je tijonel re i ujer ʼatbal tzij, ire lic cu tij uyej cumal. Xu bij chique chi ca camsax na, y ca walij churox ʼij.
', '', '&lt;h2&gt;I Jesus xu bij chi ca camsax na&lt;/h2&gt;&lt;span class="verse" id="Matt_16_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chu sakixic chique u tijoxelab chi ʼo u chac ca ʼe ire pa Jerusalen, xui-ri i nimak tak mamʼib chila, junam cuʼ i cajʼatzil i sacerdóte, tak i je tijonel re i ujer ʼatbal tzij, ire lic cu tij uyej cumal. Xu bij chique chi ca camsax na, y ca walij churox ʼij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.022', 'Are ʼuri i ma Pédro xu siqʼuij i Jesus ruʼ, y xu bij che:
—¡Ay, Kajwal! ¡Ma bij ile! I Dios chu ʼatij ile chi xa bij —coʼon che.
', '', '&lt;span class="verse" id="Matt_16_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xu siqʼuij i Jesus ruʼ, y xu bij che:&lt;p&gt; &lt;/p&gt;—¡Ay, Kajwal! ¡Ma bij ile! I Dios chu ʼatij ile chi xa bij —coʼon che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.023', 'I Jesus xu pisquilij rib; xu bij che i ma Pédro:
—¡Chat el na chin wach, a-Satanas! Ma ʼan latzʼ che in be. A nojbal yet n-queje ta pacha u chʼobom i Dios. Ca chʼob yet pacha quiqui chʼob i toʼ winak —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Matt_16_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I Jesus xu pisquilij rib; xu bij che i ma Pédro:&lt;p&gt; &lt;/p&gt;—¡Chat el na chin wach, a-Satanas! Ma ʼan latzʼ che in be. A nojbal yet n-queje ta pacha u chʼobom i Dios. Ca chʼob yet pacha quiqui chʼob i toʼ winak —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.024', 'Teʼuri i Jesus xu bij chique u tijoxelab:
—Pachin jun craj ca terej chwij, ʼo u chac mu chʼob chic rib, xui-ri cha terej na chwij, tupu ca cam na rumal. ', '', '&lt;span class="verse" id="Matt_16_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Pachin jun craj ca terej chwij, ʼo u chac mu chʼob chic rib, xui-ri cha terej na chwij, tupu ca cam na rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.025', 'Pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, we ʼo jun ca sachsax nu wach u cʼaslemal waral xa wumal yin, ca yaʼ ʼuri u cʼaslemal sakil. ', '', '&lt;span class="verse" id="Matt_16_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, we ʼo jun ca sachsax nu wach u cʼaslemal waral xa wumal yin, ca yaʼ ʼuri u cʼaslemal sakil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.026', 'Jun, we cu chʼac ronojel i beyomal re u wach i jyub taʼaj, xui-ri we cu sach u cʼaslemal sakil, n-ta queʼel wi ʼuri, n-ta u chʼacom ʼuri. ¿Wach u tojic ʼuri coʼon na, chʼacbal re u cʼaslemal sakil, we u sachom? N-taj. ', '', '&lt;span class="verse" id="Matt_16_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Jun, we cu chʼac ronojel i beyomal re u wach i jyub taʼaj, xui-ri we cu sach u cʼaslemal sakil, n-ta queʼel wi ʼuri, n-ta u chʼacom ʼuri. ¿Wach u tojic ʼuri coʼon na, chʼacbal re u cʼaslemal sakil, we u sachom? N-taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.027', 'Man yin, chi in Achi aj Chicaj, quin cʼun na ruʼ u ʼij u chomal in Kajaw, xak cuʼ in ángel. Are ʼuri, quin ya na chique i winak chiqui jujunal wach i tojbal-re niʼpa qui ʼanom na. ', '', '&lt;span class="verse" id="Matt_16_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Man yin, chi in Achi aj Chicaj, quin cʼun na ruʼ u ʼij u chomal in Kajaw, xak cuʼ in ángel. Are ʼuri, quin ya na chique i winak chiqui jujunal wach i tojbal-re niʼpa qui ʼanom na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '16.028', 'Sak laj tzij i quin bij chiwe, je ʼo jujun waral chi n-que cam ta tan, cʼa quin quil na are col in ʼana ni in ʼatbal tzij waral —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_16_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Sak laj tzij i quin bij chiwe, je ʼo jujun waral chi n-que cam ta tan, cʼa quin quil na are col in ʼana ni in ʼatbal tzij waral —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.001', 'Wakib ʼij ricʼawic ile, i Jesus xe u cʼam bi i ma Pédro, i ma Jacóbo, xak i ma Wan chi u chaʼ i ma Jacóbo, y xe ʼe quituquel che jun nim laj jyub naj u wi. ', '', '&lt;h2&gt;Wach u yojtajic u tzunbal i Jesus rumal u chomal i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_17_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Wakib ʼij ricʼawic ile, i Jesus xe u cʼam bi i ma Pédro, i ma Jacóbo, xak i ma Wan chi u chaʼ i ma Jacóbo, y xe ʼe quituquel che jun nim laj jyub naj u wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.002', 'Are je ʼo chila, i u tzunbal i Jesus lic xyojtajic; i u palaj lic pacha u tzunbal ʼij ca walchʼinic; xak i u ʼuʼ lic xoʼon sak, pacha i sak re i ʼij. ', '', '&lt;span class="verse" id="Matt_17_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are je ʼo chila, i u tzunbal i Jesus lic xyojtajic; i u palaj lic pacha u tzunbal ʼij ca walchʼinic; xak i u ʼuʼ lic xoʼon sak, pacha i sak re i ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.003', 'Xak teʼet, xquil i ʼetz mam Moises, xak i ʼetz mam Elías, que chʼaʼat ruʼ i Jesus. ', '', '&lt;span class="verse" id="Matt_17_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xak teʼet, xquil i ʼetz mam Moises, xak i ʼetz mam Elías, que chʼaʼat ruʼ i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.004', 'Are ʼuri i ma Pédro xu bij che i Jesus:
—¡Mayes, ʼutz xeʼelok chi oj ʼo chi! We cawaj, caka ʼan oxib mujbal waral, jun awe yet, jun re i mam Moises, xak jun re i mam Elías —coʼono.
', '', '&lt;span class="verse" id="Matt_17_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¡Mayes, ʼutz xeʼelok chi oj ʼo chi! We cawaj, caka ʼan oxib mujbal waral, jun awe yet, jun re i mam Moises, xak jun re i mam Elías —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.005', 'Are ca tijin i ma Pédro chu bixquil ile, xkaj li jutzobaj sutzʼ piquiwi, lic ca walchʼinic, y xe sach chupam. Chupam i sutzʼ-le xqui ta jun chʼawbal:
—Iri are in Cʼojol chi lic cʼax quin naʼo —xu bij—, lic quin quicot rumal ire. Chi ta ʼut wach i cu bij —coʼono.
', '', '&lt;span class="verse" id="Matt_17_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Are ca tijin i ma Pédro chu bixquil ile, xkaj li jutzobaj sutzʼ piquiwi, lic ca walchʼinic, y xe sach chupam. Chupam i sutzʼ-le xqui ta jun chʼawbal:&lt;p&gt; &lt;/p&gt;—Iri are in Cʼojol chi lic cʼax quin naʼo —xu bij—, lic quin quicot rumal ire. Chi ta ʼut wach i cu bij —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.006', 'I u tijoxelab i Jesus, are xqui ta ile, lic xqui xij quib; xqui jupij bi quib chuwach ulew. ', '', '&lt;span class="verse" id="Matt_17_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I u tijoxelab i Jesus, are xqui ta ile, lic xqui xij quib; xqui jupij bi quib chuwach ulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.007', 'Are ʼuri i Jesus xtejeb pan cuʼ; xe u yino y xu bij chique:
—Chix walijok; mi xij iwib —coʼono.
', '', '&lt;span class="verse" id="Matt_17_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xtejeb pan cuʼ; xe u yino y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chix walijok; mi xij iwib —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.008', 'Ique, are xe tzun ʼanok, n-ta chic xquilo; xui chic i Jesus ʼo cuʼ.
', '', '&lt;span class="verse" id="Matt_17_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Ique, are xe tzun ʼanok, n-ta chic xquilo; xui chic i Jesus ʼo cuʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.009', 'Are que tijin li qui kajic tzam jyub, i Jesus xu bij chique:
—Niʼpa i xiwil yix, mi lap tan chique i winak, cʼa cu rik niʼ ij are in walijnak chic yin, chi in Achi aj Chicaj —coʼono.
', '', '&lt;span class="verse" id="Matt_17_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Are que tijin li qui kajic tzam jyub, i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Niʼpa i xiwil yix, mi lap tan chique i winak, cʼa cu rik niʼ ij are in walijnak chic yin, chi in Achi aj Chicaj —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.010', 'Teʼuri i u tijoxelab xqui tzʼonoj che i Jesus:
¿Wuchac quiqui bij i tijonel re i ʼatbal tzij chi nabe ca cʼun i mam Elías? Nabe na cakil u cʼunic i mam Elías chuwach i u cʼunic i ʼatbal tzij aj chicaj, quiqui bij.
', '', '&lt;span class="verse" id="Matt_17_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab xqui tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;¿Wuchac quiqui bij i tijonel re i ʼatbal tzij chi nabe ca cʼun i mam Elías? Nabe na cakil u cʼunic i mam Elías chuwach i u cʼunic i ʼatbal tzij aj chicaj, quiqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.011', 'I Jesus xu bij:
—Katzij i quiqui bij chi mam Elías nabe ca cʼun na, col u yijba ni ronojel. ', '', '&lt;span class="verse" id="Matt_17_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij i quiqui bij chi mam Elías nabe ca cʼun na, col u yijba ni ronojel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.012', 'Xui-ri, yin quin bij chiwe chi mam Elías ya cʼun-nak chic, péro ique n-xquetamaj tu wach. Xa xqui ʼan che niʼpa i xcaj u ʼanic ique. Xak queje yin, chi in Achi aj Chicaj, quin tij ni uyej piquiʼab ique —coʼono.
', '', '&lt;span class="verse" id="Matt_17_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij chiwe chi mam Elías ya cʼun-nak chic, péro ique n-xquetamaj tu wach. Xa xqui ʼan che niʼpa i xcaj u ʼanic ique. Xak queje yin, chi in Achi aj Chicaj, quin tij ni uyej piquiʼab ique —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.013', 'Teʼuri i u tijoxelab xqui ta u be chi are i ma Wan aj Kajsanel ya chi ca tijin i Jesus chu lapic chique. U sakirsam chique chi ma Wan pacha i mam Elías ya xcʼunic.
', '', '&lt;span class="verse" id="Matt_17_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab xqui ta u be chi are i ma Wan aj Kajsanel ya chi ca tijin i Jesus chu lapic chique. U sakirsam chique chi ma Wan pacha i mam Elías ya xcʼunic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.014', 'Teʼuri xe kaj li che i jyub y xe upon chic cuʼ uqʼuial winak chi je moltajnak. Are ʼuri ʼo jun achi xtejeb ruʼ i Jesus y xuqui chuwach.
', '', '&lt;h2&gt;I Jesus xrutzirsaj jun ala chi ʼo atac che&lt;/h2&gt;&lt;span class="verse" id="Matt_17_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri xe kaj li che i jyub y xe upon chic cuʼ uqʼuial winak chi je moltajnak. Are ʼuri ʼo jun achi xtejeb ruʼ i Jesus y xuqui chuwach.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.015', '—Nim laj winak —xu bij che—, chawila toʼbal u wach i wacʼal. Ca pe i atac che, y lic cu tij i camic rumal. Qʼuial mul ca tzak pa ʼaʼ, ca tzak pa ya rumal. ', '', '&lt;span class="verse" id="Matt_17_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;—Nim laj winak —xu bij che—, chawila toʼbal u wach i wacʼal. Ca pe i atac che, y lic cu tij i camic rumal. Qʼuial mul ca tzak pa ʼaʼ, ca tzak pa ya rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.016', 'In cʼamom na li waral cuʼ a tijoxelab, xui-ri n-que tiqui ta chi rutzirsaxic —coʼono.
', '', '&lt;span class="verse" id="Matt_17_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;In cʼamom na li waral cuʼ a tijoxelab, xui-ri n-que tiqui ta chi rutzirsaxic —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.017', 'Teʼuri i Jesus xu bij:
—¡Ay, yix, n-tu cubibal i cʼux che i Dios; lic ix sachnak na! ¡ʼO tan chi ʼij in ʼo iwuʼ, y yix n-ta i nawic! ¿Niʼpa nawi chic ʼij cʼa craj? —xu bij—. Chi cʼama li acʼal waral —coʼono.
', '', '&lt;span class="verse" id="Matt_17_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—¡Ay, yix, n-tu cubibal i cʼux che i Dios; lic ix sachnak na! ¡ʼO tan chi ʼij in ʼo iwuʼ, y yix n-ta i nawic! ¿Niʼpa nawi chic ʼij cʼa craj? —xu bij—. Chi cʼama li acʼal waral —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.018', 'Teʼuri i Jesus xu yaj i itzel tew chi ʼo che ala, y xa ʼec. I acʼal juntir xutziric.
', '', '&lt;span class="verse" id="Matt_17_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu yaj i itzel tew chi ʼo che ala, y xa ʼec. I acʼal juntir xutziric.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.019', 'Teʼuri i u tijoxelab i Jesus, are quituquel chic, xqui tzʼonoj che:
—¿Wuchac n-xoj tiqui ti yoj chi relsaxic bi itzel tew-le? —xqui bij.
', '', '&lt;span class="verse" id="Matt_17_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab i Jesus, are quituquel chic, xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wuchac n-xoj tiqui ti yoj chi relsaxic bi itzel tew-le? —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.020', '—N-xix tiqui ta che man n-ta kas u cubibal i cʼux che i Dios —xu bij i Jesus chique—. Katzij i quin bij chiwe: i cubibal i cʼux che i Dios, we lic katzij, quix tiqui chu ʼanic milágro ruʼ. N-ta coʼono we xa pichʼ pacha u nimal jun ija re mostas, we lic katzij, quix tiqui ʼuri chi relsaxic jun nim laj jyub rumal. We tene qui bij che i jyub-i: “Chat el chi; jat chila,” qui bij ne che, pues quel ʼuri. ʼIs quix tiqui chu ʼonquil ʼuri. ', '', '&lt;span class="verse" id="Matt_17_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;—N-xix tiqui ta che man n-ta kas u cubibal i cʼux che i Dios —xu bij i Jesus chique—. Katzij i quin bij chiwe: i cubibal i cʼux che i Dios, we lic katzij, quix tiqui chu ʼanic milágro ruʼ. N-ta coʼono we xa pichʼ pacha u nimal jun ija re mostas, we lic katzij, quix tiqui ʼuri chi relsaxic jun nim laj jyub rumal. We tene qui bij che i jyub-i: “Chat el chi; jat chila,” qui bij ne che, pues quel ʼuri. ʼIs quix tiqui chu ʼonquil ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.021', 'Xui-ri, we quiwaj quiwesaj itzel tew pacha i jun-le, ʼo u chac quix tijin chu tzʼonoxic che i Dios, xak chi mayij ni waʼim rumal —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_17_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xui-ri, we quiwaj quiwesaj itzel tew pacha i jun-le, ʼo u chac quix tijin chu tzʼonoxic che i Dios, xak chi mayij ni waʼim rumal —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.022', 'Xoʼon panok, are que tijin chi be pa jyub Galiléa, i Jesus xu bij chique u tijoxelab chi ire, chi Achi aj Chicaj, ya ca jachtaj na piquiʼab i winak. ', '', '&lt;h2&gt;I Jesus xu bij tan chic chi ca camsax na&lt;/h2&gt;&lt;span class="verse" id="Matt_17_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xoʼon panok, are que tijin chi be pa jyub Galiléa, i Jesus xu bij chique u tijoxelab chi ire, chi Achi aj Chicaj, ya ca jachtaj na piquiʼab i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.023', 'Ique quiqui camsaj na; xui-ri churox ʼij ca walij na, xu bij. I u tijoxelab, are xqui ta i tzij-le, lic xe oc chi bis.
', '', '&lt;span class="verse" id="Matt_17_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Ique quiqui camsaj na; xui-ri churox ʼij ca walij na, xu bij. I u tijoxelab, are xqui ta i tzij-le, lic xe oc chi bis.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.024', 'I Jesus cuʼ u tijoxelab, are xe upon pa tinimit Capernaum, ʼo jujun aj tzʼonol alcawal xe upon ruʼ i ma Pédro. Que tijin chu tzʼonoxic i alcawal re i rachoch i Dios. Xqui bij che i ma Pédro:
—I tijonel yix, ¿xataba n-cu toj ti impuésto re i rachoch i Dios? —xe cha.
', '', '&lt;h2&gt;Wach u tojic i impuésto re i rachoch i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_17_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I Jesus cuʼ u tijoxelab, are xe upon pa tinimit Capernaum, ʼo jujun aj tzʼonol alcawal xe upon ruʼ i ma Pédro. Que tijin chu tzʼonoxic i alcawal re i rachoch i Dios. Xqui bij che i ma Pédro:&lt;p&gt; &lt;/p&gt;—I tijonel yix, ¿xataba n-cu toj ti impuésto re i rachoch i Dios? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.025', '—Cu tojo pues —xu bij i ma Pédro.
Are xoc i ma Pédro paja, i Jesus juntir xu bij che:
—¿Wach ca chʼob yet, a-Simon? I ʼatol tzij re u wach i jyub taʼaj, are quiqui tzʼonoj alcawal chique i winak, ¿pachin cuʼ quiqui tzʼonoj wi? ¿Xataba quiqui tzʼonoj chique i je que ique? N-taj. Xui quiqui tzʼonoj chique i ne te cach aj tinimit chi je ʼo chiquixol.
', '', '&lt;span class="verse" id="Matt_17_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;—Cu tojo pues —xu bij i ma Pédro.&lt;p&gt; &lt;/p&gt;Are xoc i ma Pédro paja, i Jesus juntir xu bij che:&lt;p&gt; &lt;/p&gt;—¿Wach ca chʼob yet, a-Simon? I ʼatol tzij re u wach i jyub taʼaj, are quiqui tzʼonoj alcawal chique i winak, ¿pachin cuʼ quiqui tzʼonoj wi? ¿Xataba quiqui tzʼonoj chique i je que ique? N-taj. Xui quiqui tzʼonoj chique i ne te cach aj tinimit chi je ʼo chiquixol.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.026', '—Katzij —xu bij i ma Pédro—. Xui chique i ne te cach aj tinimit quiqui tzʼonoj wi.
Are ʼuri i Jesus xu bij:
—Entons i je re i ʼatol tzij n-quiqui toj taj. Bay, yoj oj re i ʼatol tzij chi lic katzij —xu bij—. ', '', '&lt;span class="verse" id="Matt_17_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;—Katzij —xu bij i ma Pédro—. Xui chique i ne te cach aj tinimit quiqui tzʼonoj wi.&lt;p&gt; &lt;/p&gt;Are ʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Entons i je re i ʼatol tzij n-quiqui toj taj. Bay, yoj oj re i ʼatol tzij chi lic katzij —xu bij—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '17.027', 'Xui-ri, n-cakaj ta caka petsaj i coywal; ʼutz jat chiya, ja cʼaka awansuélo pa ya. Chawesaj i nabe car we xchaptaj rumal awansuélo. Pu chiʼ, ca rik wi jun sak pwak. Coʼon ile ca tojbej re i impuésto we yin, xak i awe yet. Cha cʼama bic y cha tojo canok —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Matt_17_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xui-ri, n-cakaj ta caka petsaj i coywal; ʼutz jat chiya, ja cʼaka awansuélo pa ya. Chawesaj i nabe car we xchaptaj rumal awansuélo. Pu chiʼ, ca rik wi jun sak pwak. Coʼon ile ca tojbej re i impuésto we yin, xak i awe yet. Cha cʼama bic y cha tojo canok —xu bij i Jesus che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.001', 'Are ʼuri xe tejeb pan i u tijoxelab i Jesus ruʼ, y xqui bij che:
—I ʼatbal tzij aj chicaj, are ca pe pakawi, ¿pachin ʼuri naj u ʼij queʼelok? —xqui bij che.
', '', '&lt;h2&gt;Pachin mas naj u ʼij pa cu ʼat wi tzij i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_18_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are ʼuri xe tejeb pan i u tijoxelab i Jesus ruʼ, y xqui bij che:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij aj chicaj, are ca pe pakawi, ¿pachin ʼuri naj u ʼij queʼelok? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.002', 'Are ʼuri i Jesus xu siqʼuij jun acʼal ruʼ, y xu ya chiquiwach. ', '', '&lt;span class="verse" id="Matt_18_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu siqʼuij jun acʼal ruʼ, y xu ya chiquiwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.003', 'Xu bij chique:
—Katzij i quin bij chiwe: yix we n-qui jalwachij ta i nojbal, we n-qui ʼan ta che iwib pacha u noʼoj jun acʼal, n-quix oc ta ʼuri pa ca ʼat wi i tzij chi ca pe chicaj. ', '', '&lt;span class="verse" id="Matt_18_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe: yix we n-qui jalwachij ta i nojbal, we n-qui ʼan ta che iwib pacha u noʼoj jun acʼal, n-quix oc ta ʼuri pa ca ʼat wi i tzij chi ca pe chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.004', 'Queʼel ʼuri chi pachin coʼon che rib pacha u noʼoj i acʼal-i, pachin coʼon chʼutin che rib, ire ʼut are i mas naj u ʼij pa cu ʼat wi tzij i Dios.
', '', '&lt;span class="verse" id="Matt_18_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Queʼel ʼuri chi pachin coʼon che rib pacha u noʼoj i acʼal-i, pachin coʼon chʼutin che rib, ire ʼut are i mas naj u ʼij pa cu ʼat wi tzij i Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.005', 'We ʼo jun cu cʼulaj jun acʼal ruʼ pacha iri, rumal i cʼaxnabal u cʼux chwe yin, quel ʼuri pacha yin quin u cʼulaj. ', '', '&lt;h2&gt;Toʼ u wach i jun we xa cu tzaksaj jun chʼutin&lt;/h2&gt;&lt;span class="verse" id="Matt_18_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;We ʼo jun cu cʼulaj jun acʼal ruʼ pacha iri, rumal i cʼaxnabal u cʼux chwe yin, quel ʼuri pacha yin quin u cʼulaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.006', 'Jun chʼutin chi u cubam u cʼux chwe, we ʼo jun coʼon che chi ca tzak chupam u mac, nim laj mac ʼuri u ʼanom ire. Mas ʼutz queʼel che, we tene ca xim jun caʼ rij u kul y ca cʼak bi pa ya, jiʼbal-re. I camic-le, mas ʼutz chuwach i mac chi u ʼanom che. ', '', '&lt;span class="verse" id="Matt_18_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Jun chʼutin chi u cubam u cʼux chwe, we ʼo jun coʼon che chi ca tzak chupam u mac, nim laj mac ʼuri u ʼanom ire. Mas ʼutz queʼel che, we tene ca xim jun caʼ rij u kul y ca cʼak bi pa ya, jiʼbal-re. I camic-le, mas ʼutz chuwach i mac chi u ʼanom che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.007', '¡Toʼ qui wach i winak waral chuwach i jyub taʼaj, man lic ʼo ni tzaksabal-que! Lic ʼo na; xui-ri toʼ u wach i jun chi cu tzaksaj jun chic chupam u mac. ¡Mi ʼan yix ile!
', '', '&lt;span class="verse" id="Matt_18_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;¡Toʼ qui wach i winak waral chuwach i jyub taʼaj, man lic ʼo ni tzaksabal-que! Lic ʼo na; xui-ri toʼ u wach i jun chi cu tzaksaj jun chic chupam u mac. ¡Mi ʼan yix ile!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.008', 'We are a ʼab, we are awakan ca ʼanaw chawe cat macunic, suʼ cha cʼupij y cha cʼaka bic. Mas ʼutz ile, ca rikbej re a cʼaslemal, tupu xa jun a ʼab, xa jun awakan. Mas ʼutz ile chuwach i queb a ʼab, queb awakan we xa cat ʼe pa ʼaʼ rumal, i ʼaʼ chi n-tu qʼuisic. ', '', '&lt;span class="verse" id="Matt_18_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;We are a ʼab, we are awakan ca ʼanaw chawe cat macunic, suʼ cha cʼupij y cha cʼaka bic. Mas ʼutz ile, ca rikbej re a cʼaslemal, tupu xa jun a ʼab, xa jun awakan. Mas ʼutz ile chuwach i queb a ʼab, queb awakan we xa cat ʼe pa ʼaʼ rumal, i ʼaʼ chi n-tu qʼuisic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.009', 'Xak we are a baʼwach ca ʼanaw chawe cat macunic, ile suʼ chawesaj y cha cʼaka bic. Mas ʼutz ile, ca rikbej re a cʼaslemal, tupu xa jun a baʼwach. Mas ʼutz ile chuwach i queb a baʼwach we xa cat ʼe pa ʼaʼ re i tojpen rumal.
', '', '&lt;span class="verse" id="Matt_18_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak we are a baʼwach ca ʼanaw chawe cat macunic, ile suʼ chawesaj y cha cʼaka bic. Mas ʼutz ile, ca rikbej re a cʼaslemal, tupu xa jun a baʼwach. Mas ʼutz ile chuwach i queb a baʼwach we xa cat ʼe pa ʼaʼ re i tojpen rumal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.010', 'Jun chʼutin, mi chʼob yix chi n-ta kas u ʼij. Quin bij ʼu chiwe, i ángel chi je chajal-que nic are wi je ʼo chuwach in Kajaw chicaj. ', '', '&lt;h2&gt;I tijojbal tzij chirij i chij sachnak&lt;/h2&gt;&lt;span class="verse" id="Matt_18_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Jun chʼutin, mi chʼob yix chi n-ta kas u ʼij. Quin bij ʼu chiwe, i ángel chi je chajal-que nic are wi je ʼo chuwach in Kajaw chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.011', 'Yin chi in Achi aj Chicaj in cʼun-nak chi qui toʼic niʼpa i je sachnak.
', '', '&lt;span class="verse" id="Matt_18_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Yin chi in Achi aj Chicaj in cʼun-nak chi qui toʼic niʼpa i je sachnak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.012', '¿Wach i qui bij yix chi cu ʼan jun achi, we je ʼo cien u chij, we xsach jun chique? ¿Wach coʼon na ire ʼuri? ¿Xataba n-que u canaj ta can i novénta y nuéve che i jyub pa i que wa wi, que u tzucbej re i jun chi sachnak? Que u tzucuj na pues. ', '', '&lt;span class="verse" id="Matt_18_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;¿Wach i qui bij yix chi cu ʼan jun achi, we je ʼo cien u chij, we xsach jun chique? ¿Wach coʼon na ire ʼuri? ¿Xataba n-que u canaj ta can i novénta y nuéve che i jyub pa i que wa wi, que u tzucbej re i jun chi sachnak? Que u tzucuj na pues. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.013', 'Y we xu riko, mas ca quicot ruʼ i jun-le chiquiwach i novénta y nuéve chi n-xe sach taj. ', '', '&lt;span class="verse" id="Matt_18_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Y we xu riko, mas ca quicot ruʼ i jun-le chiquiwach i novénta y nuéve chi n-xe sach taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.014', 'Xak queje coʼon i Kajaw yix chi ʼo chicaj. Craj ire chi mi jun chʼutin pacha iri ca sachic.
', '', '&lt;span class="verse" id="Matt_18_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xak queje coʼon i Kajaw yix chi ʼo chicaj. Craj ire chi mi jun chʼutin pacha iri ca sachic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.015', 'We ʼo jun awachalal u ʼanom mal chawe, cha ʼijla ʼuri are utuquel; queje ile cha sakirsaj che wach u mac chawe. Ire, we xu ta u be wach i xa bij che, quix canaj chic can ʼuri iwatz i chaʼ iwib. ', '', '&lt;h2&gt;Wach u cuyic u mac jun awachalal&lt;/h2&gt;&lt;span class="verse" id="Matt_18_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;We ʼo jun awachalal u ʼanom mal chawe, cha ʼijla ʼuri are utuquel; queje ile cha sakirsaj che wach u mac chawe. Ire, we xu ta u be wach i xa bij che, quix canaj chic can ʼuri iwatz i chaʼ iwib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.016', 'Ire, we n-coʼon ta cas wach i ca bij che, teʼuri che i siqʼuij ni jun chic, queb chic, iwuʼ. Queje ile i je queb oxib quiqui ta wach u cojic u mac ca ʼano. ', '', '&lt;span class="verse" id="Matt_18_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Ire, we n-coʼon ta cas wach i ca bij che, teʼuri che i siqʼuij ni jun chic, queb chic, iwuʼ. Queje ile i je queb oxib quiqui ta wach u cojic u mac ca ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.017', 'We xak n-coʼon ta cas ire chique ique, cha bij ʼuri pa molbalʼib. We xak n-coʼon ta cas chique pa molbalʼib, chi ʼana ʼuri che pacha lic n-ta Dios ruʼ, o pacha jun mal laj aj tzʼonol alcawal.
', '', '&lt;span class="verse" id="Matt_18_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;We xak n-coʼon ta cas ire chique ique, cha bij ʼuri pa molbalʼib. We xak n-coʼon ta cas chique pa molbalʼib, chi ʼana ʼuri che pacha lic n-ta Dios ruʼ, o pacha jun mal laj aj tzʼonol alcawal.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.018', 'Katzij i quin bij chiwe: wach tak i qui ʼatij yix waral chuwach i jyub taʼaj, i Dios cu ya u wach iwuʼ, xak cu ʼatij chila chicaj. Xak wach tak qui ya chique i je re i Dios chi quiqui ʼan waral, xak ca yaʼ na li chique rumal i Dios chila chicaj.
', '', '&lt;span class="verse" id="Matt_18_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe: wach tak i qui ʼatij yix waral chuwach i jyub taʼaj, i Dios cu ya u wach iwuʼ, xak cu ʼatij chila chicaj. Xak wach tak qui ya chique i je re i Dios chi quiqui ʼan waral, xak ca yaʼ na li chique rumal i Dios chila chicaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.019', 'Xak quin bij chiwe: we je ʼo queb chixol yix waral chuwach i jyub taʼaj lic xak jun i qui nojbal are quiqui tzʼonoj che i Dios, in Kajaw chila chicaj cu ya na chique wach i quiqui tzʼonoj. ', '', '&lt;span class="verse" id="Matt_18_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xak quin bij chiwe: we je ʼo queb chixol yix waral chuwach i jyub taʼaj lic xak jun i qui nojbal are quiqui tzʼonoj che i Dios, in Kajaw chila chicaj cu ya na chique wach i quiqui tzʼonoj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.020', 'Queje ile coʼono, man pa je ʼo wi je queb je oxib qui molom na quib wumal yin, xak yin in ʼo na cuʼ ique —xu bij chique u tijoxelab.
', '', '&lt;span class="verse" id="Matt_18_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Queje ile coʼono, man pa je ʼo wi je queb je oxib qui molom na quib wumal yin, xak yin in ʼo na cuʼ ique —xu bij chique u tijoxelab.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.021', 'Teʼuri i ma Pédro xtejeb ruʼ i Jesus y xu bij che:
—Wajwal, ¿niʼpa mul quin cuy ni u mac i wachalal we macun-nak chwij? ¿Cʼa wukub mul quin cuy na? —coʼono.
', '', '&lt;span class="verse" id="Matt_18_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Pédro xtejeb ruʼ i Jesus y xu bij che:&lt;p&gt; &lt;/p&gt;—Wajwal, ¿niʼpa mul quin cuy ni u mac i wachalal we macun-nak chwij? ¿Cʼa wukub mul quin cuy na? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.022', 'I Jesus xu bij:
—N-xui ti wukub mul ca cuyu. We a cuyum wukub mul, cha cuyu chic seténta mul i wukub-le —xu bij.
', '', '&lt;span class="verse" id="Matt_18_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—N-xui ti wukub mul ca cuyu. We a cuyum wukub mul, cha cuyu chic seténta mul i wukub-le —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.023', '(Teʼuri xu bij jun chic tijojbal tzij chique): —I ʼatbal tzij aj chicaj are pacha xu ʼan jun ʼatol tzij; xe u siqʼuij u mocom ruʼ y xu bij chique chi quiqui toj ni qui cʼas ruʼ. ', '', '&lt;h2&gt;I mocom chi n-xu cuy tu mac i rachiʼil&lt;/h2&gt;&lt;span class="verse" id="Matt_18_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;(Teʼuri xu bij jun chic tijojbal tzij chique): —I ʼatbal tzij aj chicaj are pacha xu ʼan jun ʼatol tzij; xe u siqʼuij u mocom ruʼ y xu bij chique chi quiqui toj ni qui cʼas ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.024', 'Are xu jek u ʼonquil ile, xqui cʼam li jun ruʼ chi ʼo uqʼuial mil u cʼas u ʼanom ruʼ. ', '', '&lt;span class="verse" id="Matt_18_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Are xu jek u ʼonquil ile, xqui cʼam li jun ruʼ chi ʼo uqʼuial mil u cʼas u ʼanom ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.025', 'I jun-le lic n-ta ca ʼe u cʼama wi, tojbal na re u cʼas u ʼanom. Rumal-i, i ʼatol tzij xu bij chi ca cʼayix na, junam ruʼ i rixokil, tak i racʼal, xak ronojel i ubitak-re, tojbal na re u cʼas. ', '', '&lt;span class="verse" id="Matt_18_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I jun-le lic n-ta ca ʼe u cʼama wi, tojbal na re u cʼas u ʼanom. Rumal-i, i ʼatol tzij xu bij chi ca cʼayix na, junam ruʼ i rixokil, tak i racʼal, xak ronojel i ubitak-re, tojbal na re u cʼas. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.026', 'Teʼuri i u mocom-le xa xuqui chuwach u patron y xu bij che: “Nim laj winak, cha ya u nimal a cʼux chwe; coqʼuil quin toj ni ronojel in cʼas chawe,” coʼono. ', '', '&lt;span class="verse" id="Matt_18_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Teʼuri i u mocom-le xa xuqui chuwach u patron y xu bij che: “Nim laj winak, cha ya u nimal a cʼux chwe; coqʼuil quin toj ni ronojel in cʼas chawe,” coʼono. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.027', 'Are ʼuri u patron xril toʼbal u wach, y xu chup u cʼas; teʼuri xroʼtaj bic. ', '', '&lt;span class="verse" id="Matt_18_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are ʼuri u patron xril toʼbal u wach, y xu chup u cʼas; teʼuri xroʼtaj bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.028', 'Ire xa ʼec; are ʼuri xu cʼulaj jun rach ajchac. I rach ajchac-le, ʼo jubiʼ u cʼas ruʼ; are ʼuri xu chapo y xoc chu chapic pu kul, y xu bij che: “Cha tojo chwe niʼpa a cʼas wuʼ,” coʼon che. ', '', '&lt;span class="verse" id="Matt_18_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Ire xa ʼec; are ʼuri xu cʼulaj jun rach ajchac. I rach ajchac-le, ʼo jubiʼ u cʼas ruʼ; are ʼuri xu chapo y xoc chu chapic pu kul, y xu bij che: “Cha tojo chwe niʼpa a cʼas wuʼ,” coʼon che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.029', 'I rach ajchac xa xuqui chuwach y xu bij che: “Cha ya u nimal a cʼux chwe; coqʼuil quin toj ni ronojel in cʼas chawe,” coʼono. ', '', '&lt;span class="verse" id="Matt_18_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I rach ajchac xa xuqui chuwach y xu bij che: “Cha ya u nimal a cʼux chwe; coqʼuil quin toj ni ronojel in cʼas chawe,” coʼono. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.030', 'I jun n-xraj taj; suʼ xu chapo y xu tzʼapij pa cárcel. Chila xu ya wi cʼa cu toj ni u cʼas che. ', '', '&lt;span class="verse" id="Matt_18_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;I jun n-xraj taj; suʼ xu chapo y xu tzʼapij pa cárcel. Chila xu ya wi cʼa cu toj ni u cʼas che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.031', 'Xak je ʼo juban cach ajchaquib xquilo wach xoʼon i jun che i cachiʼil. Ique lic cʼax xqui naʼo wach i xa ʼani che. Rumal-i, xe ʼe qui bij che i qui patron wach u mac i jun xu ʼano. ', '', '&lt;span class="verse" id="Matt_18_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xak je ʼo juban cach ajchaquib xquilo wach xoʼon i jun che i cachiʼil. Ique lic cʼax xqui naʼo wach i xa ʼani che. Rumal-i, xe ʼe qui bij che i qui patron wach u mac i jun xu ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.032', 'I qui patron xu tak u siqʼuixic, y xu bij che: “¡Lic at mal laj winak! Yin xin bij na chawe chi niʼpa i a cʼas wuʼ, ʼis xin cuy na chawe, man lic xa tzʼonoj cuybal mac chwe. ', '', '&lt;span class="verse" id="Matt_18_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I qui patron xu tak u siqʼuixic, y xu bij che: “¡Lic at mal laj winak! Yin xin bij na chawe chi niʼpa i a cʼas wuʼ, ʼis xin cuy na chawe, man lic xa tzʼonoj cuybal mac chwe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.033', 'Rumal-i, ʼo chakul yet queje cha ʼana ni ile, cha cuyu ni u mac i jun we ʼo u cʼas awuʼ. ʼO u chac chawila toʼbal u wach, pacha xin ʼan yin chawe yet, xwil toʼbal a wach.” Queje ile xu bij i ʼatol tzij che. ', '', '&lt;span class="verse" id="Matt_18_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Rumal-i, ʼo chakul yet queje cha ʼana ni ile, cha cuyu ni u mac i jun we ʼo u cʼas awuʼ. ʼO u chac chawila toʼbal u wach, pacha xin ʼan yin chawe yet, xwil toʼbal a wach.” Queje ile xu bij i ʼatol tzij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.034', 'Y lic xpe roywal che. Rumal-i, xu tak u tzʼapixic pa cárcel, cʼa cu toj ni ronojel u cʼas che. (Queje ile i tijojbal tzij xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_18_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Y lic xpe roywal che. Rumal-i, xu tak u tzʼapixic pa cárcel, cʼa cu toj ni ronojel u cʼas che. (Queje ile i tijojbal tzij xu bij i Jesus chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '18.035', 'Pa qʼuisbal-re xu bij chique:
—Xak queje ca ʼani chiwe yix rumal in Kajaw aj chicaj, we yix chi jujunal n-qui cuy ti qui mac iwachalal ronojel i cʼux —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_18_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Pa qʼuisbal-re xu bij chique:&lt;p&gt; &lt;/p&gt;—Xak queje ca ʼani chiwe yix rumal in Kajaw aj chicaj, we yix chi jujunal n-qui cuy ti qui mac iwachalal ronojel i cʼux —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.001', 'Are xutzin chu bixquil ronojel ile, i Jesus xel pa Galiléa y xa ʼe pa Judéa. Chila xu ʼaxuj i ya re i Jordan. ', '', '&lt;h2&gt;N-ʼus taj quiqui jach quib je cʼulanic winak&lt;/h2&gt;&lt;span class="verse" id="Matt_19_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xutzin chu bixquil ronojel ile, i Jesus xel pa Galiléa y xa ʼe pa Judéa. Chila xu ʼaxuj i ya re i Jordan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.002', 'Je qʼui i winak je teren chirij; chila xe rutzirsaj iwabib.
', '', '&lt;span class="verse" id="Matt_19_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Je qʼui i winak je teren chirij; chila xe rutzirsaj iwabib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.003', 'Are ʼuri xe upon juban aj Fariséo ruʼ; cacaj quiqui cʼam u pam, tzaksabal-re quiqui ʼano. Y xqui tzʼonoj che:
—Jun achi, we craj cu jach can i rixokil, ¿wach cu bij i ʼatbal tzij tzʼibtal can rumal i mam Moises? ¿Xa bi chi maquil ʼutz nawi cu jacho? Queje ile xqui bij che i Jesus.
', '', '&lt;span class="verse" id="Matt_19_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are ʼuri xe upon juban aj Fariséo ruʼ; cacaj quiqui cʼam u pam, tzaksabal-re quiqui ʼano. Y xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—Jun achi, we craj cu jach can i rixokil, ¿wach cu bij i ʼatbal tzij tzʼibtal can rumal i mam Moises? ¿Xa bi chi maquil ʼutz nawi cu jacho? Queje ile xqui bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.004', 'Ire xu laʼ u wach chique:
—¿Xataba n-iwetaʼam taj wach i tzʼibtal can chupam u wuj i Dios? Cu bij chi i Dios chu xebal ʼij sak, are xe u ʼan i winak, “jun achi, jun ixok xe u yijba.” ', '', '&lt;span class="verse" id="Matt_19_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ire xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—¿Xataba n-iwetaʼam taj wach i tzʼibtal can chupam u wuj i Dios? Cu bij chi i Dios chu xebal ʼij sak, are xe u ʼan i winak, “jun achi, jun ixok xe u yijba.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.005', 'Xak xu bij i Dios chi “are i rumal ʼuri chi jun achi ʼo u chac quel cuʼ u kajaw u chuch man cacucʼaj quib ruʼ i rixokil. I je queb-le queʼelok xa je jun chi winak.” (Queje ile tzʼibtal canok.) ', '', '&lt;span class="verse" id="Matt_19_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xak xu bij i Dios chi “are i rumal ʼuri chi jun achi ʼo u chac quel cuʼ u kajaw u chuch man cacucʼaj quib ruʼ i rixokil. I je queb-le queʼelok xa je jun chi winak.” (Queje ile tzʼibtal canok.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.006', 'Rumal-i queʼelok ne te queb chic, ique xa je jun xeʼelok. Rumal-i i winak, n-ta piquiʼab quiqui yoj ile chi u yijbam i Dios xa jun u ʼanom chique. (Queje ile xu bij i Jesus chique i aj Fariséo.)
', '', '&lt;span class="verse" id="Matt_19_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Rumal-i queʼelok ne te queb chic, ique xa je jun xeʼelok. Rumal-i i winak, n-ta piquiʼab quiqui yoj ile chi u yijbam i Dios xa jun u ʼanom chique. (Queje ile xu bij i Jesus chique i aj Fariséo.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.007', 'Teʼuri xqui tzʼonoj chic che i Jesus:
—¿Wuchac ʼuri xu bij i mam Moises chique i achiab chi ʼo u chac quiqui ya jun wuj chique i quixokil, jachbal-que quiqui ʼano? Teʼuri quiqui jach ʼu bic. ¿Wuchac ʼut queje xu bij ire ile, we n-queje ta craj i Dios ile? —xqui bij che i Jesus.
', '', '&lt;span class="verse" id="Matt_19_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Teʼuri xqui tzʼonoj chic che i Jesus:&lt;p&gt; &lt;/p&gt;—¿Wuchac ʼuri xu bij i mam Moises chique i achiab chi ʼo u chac quiqui ya jun wuj chique i quixokil, jachbal-que quiqui ʼano? Teʼuri quiqui jach ʼu bic. ¿Wuchac ʼut queje xu bij ire ile, we n-queje ta craj i Dios ile? —xqui bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.008', 'Ire xu laʼ u wach chique:
—Queje xu ya i mam Moises ile chiwe man yix lic ix buy. Are rumal ʼuri chi xu ya chiwe qui jach ni iwixokil. Xui-ri, chu xebal ʼij sak, n-queje ti ile u chʼobom li Dios. ', '', '&lt;span class="verse" id="Matt_19_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Ire xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Queje xu ya i mam Moises ile chiwe man yix lic ix buy. Are rumal ʼuri chi xu ya chiwe qui jach ni iwixokil. Xui-ri, chu xebal ʼij sak, n-queje ti ile u chʼobom li Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.009', 'Quin bij iri chiwe: we ʼo jun cu jach nubi i rixokil, y we i rixokil n-tu mac u ʼanom ruʼ jun chic achi, i rachijil teʼek ʼo u mac ʼuri chuwach i Dios we xcucʼaj chic quib ruʼ jun chic ixok. Xak we jun achi ca qʼuiji ruʼ jun ixok we xa jachtal can rumal i rachijil, lic ʼo ni u mac ʼuri i ucab achi chuwach i Dios. (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Matt_19_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Quin bij iri chiwe: we ʼo jun cu jach nubi i rixokil, y we i rixokil n-tu mac u ʼanom ruʼ jun chic achi, i rachijil teʼek ʼo u mac ʼuri chuwach i Dios we xcucʼaj chic quib ruʼ jun chic ixok. Xak we jun achi ca qʼuiji ruʼ jun ixok we xa jachtal can rumal i rachijil, lic ʼo ni u mac ʼuri i ucab achi chuwach i Dios. (Queje xu bij i Jesus ile chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.010', 'Teʼuri i u tijoxelab xqui bij che:
—We queje queʼel ile, jun achi ruʼ jun ixok, mas ʼutz ʼuri we n-cu tzucuj ta rixokil —xe cha.
', '', '&lt;span class="verse" id="Matt_19_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri i u tijoxelab xqui bij che:&lt;p&gt; &lt;/p&gt;—We queje queʼel ile, jun achi ruʼ jun ixok, mas ʼutz ʼuri we n-cu tzucuj ta rixokil —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.011', 'I Jesus xu bij chique:
—Colo katzij ile, xui-ri n-conojel taj quiqui chʼijo chi n-ta quixokil; xui ique quiqui chʼijo chi yatal u choʼab i Dios cuʼ. ', '', '&lt;span class="verse" id="Matt_19_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Colo katzij ile, xui-ri n-conojel taj quiqui chʼijo chi n-ta quixokil; xui ique quiqui chʼijo chi yatal u choʼab i Dios cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.012', 'Je ʼo i achiab chi n-quiqui tzucuj ta quixokil: ʼo jujun chique chi n-tzʼakat ti qui chʼacul chi calaxic; xak ʼo jujun chique chi n-tzʼakat ti qui chʼacul man xa ril ʼantal chique; xak ʼo jujun chi n-quiqui tzucuj ta quixokil rumal u chac i Dios, u chac i ʼatbal tzij aj chicaj. We ʼo jun ca tiqui chu chʼijic ile, chu ʼana na —xu bij.
', '', '&lt;span class="verse" id="Matt_19_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Je ʼo i achiab chi n-quiqui tzucuj ta quixokil: ʼo jujun chique chi n-tzʼakat ti qui chʼacul chi calaxic; xak ʼo jujun chique chi n-tzʼakat ti qui chʼacul man xa ril ʼantal chique; xak ʼo jujun chi n-quiqui tzucuj ta quixokil rumal u chac i Dios, u chac i ʼatbal tzij aj chicaj. We ʼo jun ca tiqui chu chʼijic ile, chu ʼana na —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.013', 'Je ʼo jujun winak xe qui cʼam li acʼalab ruʼ i Jesus, cu yabej na re u ʼab piquiwi, xak cu tzʼonbej i ʼutz re i Dios piquiwi. Xui-ri, i u tijoxelab xqui jek qui ʼatixic i je ucʼawnak i acʼalab. ', '', '&lt;h2&gt;I ʼatbal tzij aj chicaj, que ique chi je pacha acʼalab&lt;/h2&gt;&lt;span class="verse" id="Matt_19_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Je ʼo jujun winak xe qui cʼam li acʼalab ruʼ i Jesus, cu yabej na re u ʼab piquiwi, xak cu tzʼonbej i ʼutz re i Dios piquiwi. Xui-ri, i u tijoxelab xqui jek qui ʼatixic i je ucʼawnak i acʼalab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.014', 'I Jesus, are xrilo, xu bij chique:
—Chi ya chique i acʼalab que cʼun wuʼ; me i ʼatij, man niʼpa i je pacha acʼalab piqui cʼux, ʼo-que che i ʼatbal tzij aj chicaj —xu bij chique u tijoxelab.
', '', '&lt;span class="verse" id="Matt_19_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I Jesus, are xrilo, xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ya chique i acʼalab que cʼun wuʼ; me i ʼatij, man niʼpa i je pacha acʼalab piqui cʼux, ʼo-que che i ʼatbal tzij aj chicaj —xu bij chique u tijoxelab.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.015', 'Are ʼuri xu coj u ʼab piquiwi i acʼalab; teʼuri xel chila.
', '', '&lt;span class="verse" id="Matt_19_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are ʼuri xu coj u ʼab piquiwi i acʼalab; teʼuri xel chila.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.016', 'ʼO jun achi, cʼa ala, xupon ruʼ i Jesus y xu bij che:
—Nim laj winak, at ʼutz laj tijonel, ¿wach tak i ʼutz quin ʼan na, quin rikbej re in cʼaslemal chi n-tu qʼuisic? —xu bij.
', '', '&lt;h2&gt;Jun achi beyom xu tzucuj u cʼaslemal chi n-tu qʼuisic&lt;/h2&gt;&lt;span class="verse" id="Matt_19_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;ʼO jun achi, cʼa ala, xupon ruʼ i Jesus y xu bij che:&lt;p&gt; &lt;/p&gt;—Nim laj winak, at ʼutz laj tijonel, ¿wach tak i ʼutz quin ʼan na, quin rikbej re in cʼaslemal chi n-tu qʼuisic? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.017', 'I Jesus xu bij che:
—¿Wuchac ca bij “ʼutz” chwe yin? N-ta jun chi ʼutz, xui Dios —xu bij—. Xui-ri we cawaj cat oc che i cʼaslemal katzij, cha ʼana pacha cu bij u tzij u pixab i Dios —xu bij che.
', '', '&lt;span class="verse" id="Matt_19_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—¿Wuchac ca bij “ʼutz” chwe yin? N-ta jun chi ʼutz, xui Dios —xu bij—. Xui-ri we cawaj cat oc che i cʼaslemal katzij, cha ʼana pacha cu bij u tzij u pixab i Dios —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.018', '—¿Pachique che? —xu bij i achi.
I Jesus xu bij che:
—Mat camsanic; ma kajsaj a ʼij ruʼ jun ixok chi n-awixokil taj; ma ʼan elaʼ; mat tʼoronic; ', '', '&lt;span class="verse" id="Matt_19_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;—¿Pachique che? —xu bij i achi.&lt;p&gt; &lt;/p&gt;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Mat camsanic; ma kajsaj a ʼij ruʼ jun ixok chi n-awixokil taj; ma ʼan elaʼ; mat tʼoronic; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.019', 'cha cojo qui ʼij a kajaw a chuch; y cʼax cha na a loʼcʼanij pacha cʼax u naʼic awib yet —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Matt_19_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;cha cojo qui ʼij a kajaw a chuch; y cʼax cha na a loʼcʼanij pacha cʼax u naʼic awib yet —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.020', '—Ronojel ile ʼis quin tijin chu ʼanic; in ʼanom na lok cʼa are in chʼutin —xu bij i achi che—. ¿Wach cʼa craj quin ʼano? —xu bij.
', '', '&lt;span class="verse" id="Matt_19_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;—Ronojel ile ʼis quin tijin chu ʼanic; in ʼanom na lok cʼa are in chʼutin —xu bij i achi che—. ¿Wach cʼa craj quin ʼano? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.021', 'I Jesus xu bij che:
—We cawaj lic n-ta chic rajwaxic chawe chuwach i Dios, jat, ja cʼayij niʼpa i ʼo awuʼ, y cha jacha chique i nibaʼib. Ca rik ʼuri a beyomal chicaj. Teʼuri chat petok y chat terej chwij —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Matt_19_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—We cawaj lic n-ta chic rajwaxic chawe chuwach i Dios, jat, ja cʼayij niʼpa i ʼo awuʼ, y cha jacha chique i nibaʼib. Ca rik ʼuri a beyomal chicaj. Teʼuri chat petok y chat terej chwij —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.022', 'I achi, are xu ta ile, lic ca bisonic, man lic beyom; xu maj bi u be, y xa ʼec. ', '', '&lt;span class="verse" id="Matt_19_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I achi, are xu ta ile, lic ca bisonic, man lic beyom; xu maj bi u be, y xa ʼec. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.023', 'Are ʼuri i Jesus xu bij chique i u tijoxelab:
—Katzij i quin bij chiwe: i beyomab, cʼax coquic puʼab i ʼatbal tzij aj chicaj. ', '', '&lt;span class="verse" id="Matt_19_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique i u tijoxelab:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe: i beyomab, cʼax coquic puʼab i ʼatbal tzij aj chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.024', 'Quin camulij chic u bixquil: cʼax roquic jun beyom, coc puʼab i Dios; cʼax ni ile chuwach i ricʼawic jun camey che i tel re jun acuxa —xu bij chique.
', '', '&lt;span class="verse" id="Matt_19_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Quin camulij chic u bixquil: cʼax roquic jun beyom, coc puʼab i Dios; cʼax ni ile chuwach i ricʼawic jun camey che i tel re jun acuxa —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.025', 'I u tijoxelab, are xqui ta ile, lic xqui bisoj. Teʼuri xe oc chu bixquil chiquiwach:
—¿Pachin quelsax na ʼuri chupam u mac? —xe cha.
', '', '&lt;span class="verse" id="Matt_19_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xqui ta ile, lic xqui bisoj. Teʼuri xe oc chu bixquil chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Pachin quelsax na ʼuri chupam u mac? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.026', 'I Jesus xe u takej y xu bij chique:
—I winak n-que tiqui ta chu ʼanic ile; xui-ri i Dios ronojel ʼis ca tiqui che —xu bij.
', '', '&lt;span class="verse" id="Matt_19_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I Jesus xe u takej y xu bij chique:&lt;p&gt; &lt;/p&gt;—I winak n-que tiqui ta chu ʼanic ile; xui-ri i Dios ronojel ʼis ca tiqui che —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.027', 'Are ʼuri i ma Pédro xu bij che:
—Kajwal, yoj, ronojel niʼpa i ʼo kuʼ, xka canaj canok y oj terejnak chawij. ¿Wach i ca yaʼ chake ʼut? —coʼono.
', '', '&lt;span class="verse" id="Matt_19_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xu bij che:&lt;p&gt; &lt;/p&gt;—Kajwal, yoj, ronojel niʼpa i ʼo kuʼ, xka canaj canok y oj terejnak chawij. ¿Wach i ca yaʼ chake ʼut? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.028', 'I Jesus xu bij chique:
—Katzij i quin bij chiwe, are cu rik i ʼij chi ca jaltaj ronojel niʼpa ʼo chuwach i jyub taʼaj, are ʼuri ca yaʼ i ʼij yix. Are ʼo chic i Achi aj Chicaj pa nim laj u ʼatbal tzij, xak yix chi ix terejnak chwij xak ca yaʼ i patan nim u ʼij. Yix ʼuri qui ʼat ni tzij piquiwi i je cablajuj tzobaj aj Israel winak. ', '', '&lt;span class="verse" id="Matt_19_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, are cu rik i ʼij chi ca jaltaj ronojel niʼpa ʼo chuwach i jyub taʼaj, are ʼuri ca yaʼ i ʼij yix. Are ʼo chic i Achi aj Chicaj pa nim laj u ʼatbal tzij, xak yix chi ix terejnak chwij xak ca yaʼ i patan nim u ʼij. Yix ʼuri qui ʼat ni tzij piquiwi i je cablajuj tzobaj aj Israel winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.029', 'Conojel niʼpa i coʼtam can ubitak-que wumal yin, nim na quiqui chʼac puwi ile. We ʼo jun u canam can i rachoch, o we i ratz u chaʼ, o we i ranab, o we u chuch u kajaw, o we u canam can rixokil, o we i racʼal, o we i rulew xa wumal yin, nim i cu chʼac na. Ca yaʼ na ʼuri jun ciénto che, puwi niʼpa ile chi xa jun ʼo teʼek ruʼ, xak ca yaʼ jun u cʼaslemal chi n-tu qʼuisic. ', '', '&lt;span class="verse" id="Matt_19_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Conojel niʼpa i coʼtam can ubitak-que wumal yin, nim na quiqui chʼac puwi ile. We ʼo jun u canam can i rachoch, o we i ratz u chaʼ, o we i ranab, o we u chuch u kajaw, o we u canam can rixokil, o we i racʼal, o we i rulew xa wumal yin, nim i cu chʼac na. Ca yaʼ na ʼuri jun ciénto che, puwi niʼpa ile chi xa jun ʼo teʼek ruʼ, xak ca yaʼ jun u cʼaslemal chi n-tu qʼuisic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '19.030', 'Xui-ri, niʼpa i je nabe woʼor, je qʼui na que canaj can pa qʼuisbal-re; xak je qʼui chi je ʼo can pa qʼuisbal-re woʼor, are ique are i que nabeaj na —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_19_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xui-ri, niʼpa i je nabe woʼor, je qʼui na que canaj can pa qʼuisbal-re; xak je qʼui chi je ʼo can pa qʼuisbal-re woʼor, are ique are i que nabeaj na —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.001', '(Are ʼuri i Jesus xu bij jun chic tijojbal tzij chique, xu bij:)
—I ʼatbal tzij aj chicaj coʼon pacha jun rajaw ticon chi que u tzucuj rajchaquib quiqui mol ni u wach i úva. Ire weʼ xa ʼec, que u tzucuj u mocom. ', '', '&lt;h2&gt;I tijojbal tzij re i ajchaquib&lt;/h2&gt;&lt;span class="verse" id="Matt_20_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;(Are ʼuri i Jesus xu bij jun chic tijojbal tzij chique, xu bij:)&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij aj chicaj coʼon pacha jun rajaw ticon chi que u tzucuj rajchaquib quiqui mol ni u wach i úva. Ire weʼ xa ʼec, que u tzucuj u mocom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.002', 'Xu rik u mocom y xu bij chique chi are jun sak pwak i cajil re jun ʼij. Teʼuri xe u tak bi pa chac pa rulew. ', '', '&lt;span class="verse" id="Matt_20_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Xu rik u mocom y xu bij chique chi are jun sak pwak i cajil re jun ʼij. Teʼuri xe u tak bi pa chac pa rulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.003', 'Xoʼon panok, colo a las nuéve re i raʼanbal ʼij, xa ʼe pa qʼuebal. Xe u rika juban achiab n-ta quiqui ʼano. ', '', '&lt;span class="verse" id="Matt_20_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xoʼon panok, colo a las nuéve re i raʼanbal ʼij, xa ʼe pa qʼuebal. Xe u rika juban achiab n-ta quiqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.004', 'Xu bij chique: “We quiwaj i chac, jix pa wulew, je i molo in cosech; y yin quin toj na pacha ca majaw chiwe,” xu bij chique. Ique xe ʼec. ', '', '&lt;span class="verse" id="Matt_20_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xu bij chique: “We quiwaj i chac, jix pa wulew, je i molo in cosech; y yin quin toj na pacha ca majaw chiwe,” xu bij chique. Ique xe ʼec. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.005', 'Xak queje xoʼon paʼij, xak queje xoʼon a las tres re u kajbal ʼij. ', '', '&lt;span class="verse" id="Matt_20_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xak queje xoʼon paʼij, xak queje xoʼon a las tres re u kajbal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.006', 'Xak queje tan chic xoʼono are xkaj i ʼij, colo a las cínco: xa ʼe pa qʼuebal, xe u rika juban achiab n-ta quiqui ʼano. Y xu bij chique: “¿Wach qui ʼolba yix chi, jun ʼij? ¿N-ta ba i chac-i?” coʼono. ', '', '&lt;span class="verse" id="Matt_20_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xak queje tan chic xoʼono are xkaj i ʼij, colo a las cínco: xa ʼe pa qʼuebal, xe u rika juban achiab n-ta quiqui ʼano. Y xu bij chique: “¿Wach qui ʼolba yix chi, jun ʼij? ¿N-ta ba i chac-i?” coʼono. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.007', 'Y xqui bij ique che: “Mi jun u sujum chac chake,” xe cha che. “Jix pa wulew,” xu bij i achi, “je i molo in cosech; yin quin toj na pacha ca majaw chiwe,” xu bij chique. ', '', '&lt;span class="verse" id="Matt_20_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Y xqui bij ique che: “Mi jun u sujum chac chake,” xe cha che. “Jix pa wulew,” xu bij i achi, “je i molo in cosech; yin quin toj na pacha ca majaw chiwe,” xu bij chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.008', 'Are ya xoc aʼab, i rajaw i chac xu bij che i ucʼawnak i mocom: “Che a siqʼuij i ajchaquib y che a tojo. Nabe che a tojo bi niʼpa i xe cʼun qʼuisbal-re. Are i je ocnak nabe, qʼuisbal-re che a tojo bic,” xu bij che. ', '', '&lt;span class="verse" id="Matt_20_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are ya xoc aʼab, i rajaw i chac xu bij che i ucʼawnak i mocom: “Che a siqʼuij i ajchaquib y che a tojo. Nabe che a tojo bi niʼpa i xe cʼun qʼuisbal-re. Are i je ocnak nabe, qʼuisbal-re che a tojo bic,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.009', 'Xeʼelok chi are i xe oc a las cínco re i kajbal ʼij, xyaʼ bi jun sak pwak chique. ', '', '&lt;span class="verse" id="Matt_20_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xeʼelok chi are i xe oc a las cínco re i kajbal ʼij, xyaʼ bi jun sak pwak chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.010', 'Are i je ocnak nabe, quiqui bij ique mas nim ni quiqui chʼac ique chiquiwach i je ocnak qʼuisbal-re, quiqui bij ique. Xui-ri, xak xa jun sak pwak xyaʼ chique chiqui jujunal. ', '', '&lt;span class="verse" id="Matt_20_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are i je ocnak nabe, quiqui bij ique mas nim ni quiqui chʼac ique chiquiwach i je ocnak qʼuisbal-re, quiqui bij ique. Xui-ri, xak xa jun sak pwak xyaʼ chique chiqui jujunal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.011', 'Are xqui chapo, xe oc chu bixquil che i rajaw i chac chi n-ʼus ta xoʼon che i cajil. ', '', '&lt;span class="verse" id="Matt_20_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Are xqui chapo, xe oc chu bixquil che i rajaw i chac chi n-ʼus ta xoʼon che i cajil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.012', 'Xqui bij: “Are i je cʼun-nak qʼuisbal-re, xak jun i qui tojic xa ʼan kuʼ yoj. Ique, xa jun ʼor xe chacunic, y yoj jun ʼij entera xka chʼij i chac, xka chʼij i ʼij,” xqui bij che i rajaw. ', '', '&lt;span class="verse" id="Matt_20_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xqui bij: “Are i je cʼun-nak qʼuisbal-re, xak jun i qui tojic xa ʼan kuʼ yoj. Ique, xa jun ʼor xe chacunic, y yoj jun ʼij entera xka chʼij i chac, xka chʼij i ʼij,” xqui bij che i rajaw. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.013', 'Ire xu bij che i jun, “Chatapeʼ, yin n-xix in tʼor taj. ¿Xataba n-xka bij ta wa, chi quix chacun jun ʼij chuwach jun sak pwak? Xka bij pues. ', '', '&lt;span class="verse" id="Matt_20_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ire xu bij che i jun, “Chatapeʼ, yin n-xix in tʼor taj. ¿Xataba n-xka bij ta wa, chi quix chacun jun ʼij chuwach jun sak pwak? Xka bij pues. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.014', 'Cha chapa i awajil y jat. Are i cwaj yin, quin ya na che i jun pacha i xin ya chawe yet; n-ta coʼono we xa pa qʼuisbal-re xoquic. ', '', '&lt;span class="verse" id="Matt_20_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Cha chapa i awajil y jat. Are i cwaj yin, quin ya na che i jun pacha i xin ya chawe yet; n-ta coʼono we xa pa qʼuisbal-re xoquic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.015', '¿Xataba n-ta we yin che in mer, wach cwaj quin ʼan che? ʼOlic pues. ¿O xa ca titat a pam che, chi ʼutz xin ʼan yin chique i juban?” Queje ile xu bij i rajaw. ', '', '&lt;span class="verse" id="Matt_20_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;¿Xataba n-ta we yin che in mer, wach cwaj quin ʼan che? ʼOlic pues. ¿O xa ca titat a pam che, chi ʼutz xin ʼan yin chique i juban?” Queje ile xu bij i rajaw. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.016', 'Are queʼel ʼuri, niʼpa i je pa qʼuisbal-re, queʼelok je nabe; y xak i je nabe, que ʼelok que canaj cʼa pa qʼuisbal-re. Queje queʼel ni ile, man tupu je qʼui i je sicʼtal na, ne te qʼui je chatalic —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_20_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are queʼel ʼuri, niʼpa i je pa qʼuisbal-re, queʼelok je nabe; y xak i je nabe, que ʼelok que canaj cʼa pa qʼuisbal-re. Queje queʼel ni ile, man tupu je qʼui i je sicʼtal na, ne te qʼui je chatalic —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.017', 'Xoʼon panok, i Jesus ca tijin chi be cuʼ u tijoxelab, ʼenam-que pa Jerusalen. Are ʼuri xe u siqʼuij i cablajuj ruʼ, y xe u ʼijla quituquel:
', '', '&lt;h2&gt;I Jesus xu bij tan chic chi ca camsax na&lt;/h2&gt;&lt;span class="verse" id="Matt_20_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus ca tijin chi be cuʼ u tijoxelab, ʼenam-que pa Jerusalen. Are ʼuri xe u siqʼuij i cablajuj ruʼ, y xe u ʼijla quituquel:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.018', '—Yix iwetaʼam ya xka maj bi ka be pa Jerusalen. Are coj upon chila, yin chi in Achi aj Chicaj, quin jachtaj na piquiʼab i cajʼatzil i sacerdóte, piquiʼab i tijonel re i ʼatbal tzij. Ique quiqui ʼat na tzij pinwi y quiqui bij na chi ʼo u chac quin camsaxic. ', '', '&lt;span class="verse" id="Matt_20_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;—Yix iwetaʼam ya xka maj bi ka be pa Jerusalen. Are coj upon chila, yin chi in Achi aj Chicaj, quin jachtaj na piquiʼab i cajʼatzil i sacerdóte, piquiʼab i tijonel re i ʼatbal tzij. Ique quiqui ʼat na tzij pinwi y quiqui bij na chi ʼo u chac quin camsaxic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.019', 'Teʼuri quin qui jach na piquiʼab i ne te aj Israel winak: ique ʼut quin qui yoʼya, quin qui chʼay na, quin qui camsaj chuwach cruz. Xui-ri, churox ʼij, quin walijsax tan chic —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_20_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Teʼuri quin qui jach na piquiʼab i ne te aj Israel winak: ique ʼut quin qui yoʼya, quin qui chʼay na, quin qui camsaj chuwach cruz. Xui-ri, churox ʼij, quin walijsax tan chic —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.020', 'Teʼuri i qui chuch i ma Jacóbo i ma Wan, chi je u cʼojol i mam Zebedéo, xtejeb pan ruʼ i Jesus je rachiʼil i ralab. Xuqui chuwach i Jesus, tzʼonbal-re jun pawor che. ', '', '&lt;h2&gt;Wach i xu tzʼonoj i qui chuch i ma Jacóbo, i ma Wan&lt;/h2&gt;&lt;span class="verse" id="Matt_20_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Teʼuri i qui chuch i ma Jacóbo i ma Wan, chi je u cʼojol i mam Zebedéo, xtejeb pan ruʼ i Jesus je rachiʼil i ralab. Xuqui chuwach i Jesus, tzʼonbal-re jun pawor che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.021', 'I Jesus xu tzʼonoj che:
—¿Wach cawaj yet? —coʼono.
I ixok xu bij:
—Are at ʼo chic che a ʼatbal tzij, cwaj yin cha ya chique i queb wacʼal-i que cubi jujun pa tak a tzal, jun pa wikabim, y jun pa moxim —xu bij i ixok che.
', '', '&lt;span class="verse" id="Matt_20_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus xu tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wach cawaj yet? —coʼono.&lt;p&gt; &lt;/p&gt;I ixok xu bij:&lt;p&gt; &lt;/p&gt;—Are at ʼo chic che a ʼatbal tzij, cwaj yin cha ya chique i queb wacʼal-i que cubi jujun pa tak a tzal, jun pa wikabim, y jun pa moxim —xu bij i ixok che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.022', '—Yix n-iwetaʼam taj wach i qui tzʼonoj —xu bij i Jesus chique—. Niʼpa i cʼax quin icʼaw na wi yin, ¿quix tiqui yix ʼuri chu chʼijic ile? Niʼpa i uyej chi quin icʼaw na wi yin, ¿quix tiqui yix che ile? —xu bij chique.
—Coj tiquiʼic —xe cha.
', '', '&lt;span class="verse" id="Matt_20_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;—Yix n-iwetaʼam taj wach i qui tzʼonoj —xu bij i Jesus chique—. Niʼpa i cʼax quin icʼaw na wi yin, ¿quix tiqui yix ʼuri chu chʼijic ile? Niʼpa i uyej chi quin icʼaw na wi yin, ¿quix tiqui yix che ile? —xu bij chique.&lt;p&gt; &lt;/p&gt;—Coj tiquiʼic —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.023', 'Are ʼuri i Jesus xu bij:
—Katzij, yix ʼis quix icʼaw na chupam i cʼax-i, xak qui tij ni uyej pacha quin icʼaw wi yin. Xui-ri, n-ta pinʼab yin quin bij pachin ca cubi pin wikabim xak pin moxim. Xui in Kajaw ʼo puʼab; ire u yijbam chic i cʼolbal-le, ire retaʼam pachin que qʼuiji che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_20_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij, yix ʼis quix icʼaw na chupam i cʼax-i, xak qui tij ni uyej pacha quin icʼaw wi yin. Xui-ri, n-ta pinʼab yin quin bij pachin ca cubi pin wikabim xak pin moxim. Xui in Kajaw ʼo puʼab; ire u yijbam chic i cʼolbal-le, ire retaʼam pachin que qʼuiji che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.024', 'I lajuj chic u tijoxelab, are xqui ta wach i xqui bij i je queb-le chi je catz qui chaʼ quib, ique xpe coywal chique. ', '', '&lt;span class="verse" id="Matt_20_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I lajuj chic u tijoxelab, are xqui ta wach i xqui bij i je queb-le chi je catz qui chaʼ quib, ique xpe coywal chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.025', 'Are ʼuri i Jesus xe u siqʼuij y xu bij chique:
—Yix iwetaʼam chi i nimak tak ʼatol tzij quiqui ʼan che quib pacha je cajaw i winak chi je ʼo piquiʼab; i winak chi nimak qui patan que rajawin piquiwi i cach winak. ', '', '&lt;span class="verse" id="Matt_20_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xe u siqʼuij y xu bij chique:&lt;p&gt; &lt;/p&gt;—Yix iwetaʼam chi i nimak tak ʼatol tzij quiqui ʼan che quib pacha je cajaw i winak chi je ʼo piquiʼab; i winak chi nimak qui patan que rajawin piquiwi i cach winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.026', 'Xui-ri; n-ix ta queje yix ile. Are i qui ʼan ni yix, pachin craj queʼelok nim u ʼij, cha chacun ʼuri chiquij i rachiʼil. ', '', '&lt;span class="verse" id="Matt_20_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Xui-ri; n-ix ta queje yix ile. Are i qui ʼan ni yix, pachin craj queʼelok nim u ʼij, cha chacun ʼuri chiquij i rachiʼil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.027', 'Are pachin craj cu ya rib chu nabeal, chu ʼana ʼuri che rib pacha mocom chiquij i rachiʼil. ', '', '&lt;span class="verse" id="Matt_20_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are pachin craj cu ya rib chu nabeal, chu ʼana ʼuri che rib pacha mocom chiquij i rachiʼil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.028', 'Chu ʼana ʼuri che rib pacha quin ʼan yin chi in Achi aj Chicaj; in cʼun-nak na, pacha mocom chiquij i winak; n-are ti ique je in mocom yin. In cʼun-nak man quin ya na wib chi camic, tojbal-re i quelbal pa cʼax chique uqʼuial winak —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_20_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Chu ʼana ʼuri che rib pacha quin ʼan yin chi in Achi aj Chicaj; in cʼun-nak na, pacha mocom chiquij i winak; n-are ti ique je in mocom yin. In cʼun-nak man quin ya na wib chi camic, tojbal-re i quelbal pa cʼax chique uqʼuial winak —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.029', 'Xoʼon chupanok, ique que tijin quelic pa tinimit Jerico, je qʼui i winak je teren chirij i Jesus. ', '', '&lt;h2&gt;I Jesus xe rutzirsaj je queb mawach&lt;/h2&gt;&lt;span class="verse" id="Matt_20_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, ique que tijin quelic pa tinimit Jerico, je qʼui i winak je teren chirij i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.030', 'Chila je ʼo wi queb mawach je chʼuquernak pa be. Are xqui ta chi ya quicʼaw i Jesus, ique xe siqʼuin chu bixquil:
—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal ka wach! —xqui bij.
', '', '&lt;span class="verse" id="Matt_20_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Chila je ʼo wi queb mawach je chʼuquernak pa be. Are xqui ta chi ya quicʼaw i Jesus, ique xe siqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal ka wach! —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.031', 'I winak chi je teren bi chirij i Jesus xe qui yajo y xe qui ʼatij. Xui-ri, ique xa xqui cowij mas u bixquil che i Jesus:
—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal ka wach! —xe cha.
', '', '&lt;span class="verse" id="Matt_20_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I winak chi je teren bi chirij i Jesus xe qui yajo y xe qui ʼatij. Xui-ri, ique xa xqui cowij mas u bixquil che i Jesus:&lt;p&gt; &lt;/p&gt;—¡Nim laj winak! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal ka wach! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.032', 'Teʼuri i Jesus xtacʼ canic y xe u siqʼuij ruʼ. Xu bij chique:
—¿Wach quiwaj quin ʼan chiwe? —xu bij.
', '', '&lt;span class="verse" id="Matt_20_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xtacʼ canic y xe u siqʼuij ruʼ. Xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wach quiwaj quin ʼan chiwe? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.033', 'Ique xqui bij che:
—Nim laj winak, cakaj coj tzunic —xe cha.
', '', '&lt;span class="verse" id="Matt_20_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Ique xqui bij che:&lt;p&gt; &lt;/p&gt;—Nim laj winak, cakaj coj tzunic —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '20.034', 'Are ʼuri i Jesus cʼax xe u naʼo. Xu chap qui baʼwach y juntir xe tzunic. Teʼuri xak ique xe terej bi chirij i Jesus.
', '', '&lt;span class="verse" id="Matt_20_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus cʼax xe u naʼo. Xu chap qui baʼwach y juntir xe tzunic. Teʼuri xak ique xe terej bi chirij i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.001', 'Are nakaj chic je ʼo wi pa Jerusalen, xe upon pa tinimit Betfage, pa ʼo wi i ral jyub Olívos. Are ʼuri i Jesus xe u tak bi queb u tijoxelab, ', '', '&lt;h2&gt;Wach u cojic u ʼij i Jesus are xoc pa Jerusalen&lt;/h2&gt;&lt;span class="verse" id="Matt_21_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are nakaj chic je ʼo wi pa Jerusalen, xe upon pa tinimit Betfage, pa ʼo wi i ral jyub Olívos. Are ʼuri i Jesus xe u tak bi queb u tijoxelab, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.002', 'xu bij chique:
—Jix che i aldéa-le chi ʼo pan chakawach. Chila qui rik wi jun ati asna yukulic, ʼo jun racʼal ruʼ. Chi toro lok, y che i cʼama lok. ', '', '&lt;span class="verse" id="Matt_21_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;xu bij chique:&lt;p&gt; &lt;/p&gt;—Jix che i aldéa-le chi ʼo pan chakawach. Chila qui rik wi jun ati asna yukulic, ʼo jun racʼal ruʼ. Chi toro lok, y che i cʼama lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.003', 'Y we ʼo ca tzʼonow chiwe wach u chac quix tijin chu toric, chi bij che chi cu rik u chac che i Kajwal, y ire juntir cu tzalij chal chawe, chix chok che —xu bij Jesus chique.
', '', '&lt;span class="verse" id="Matt_21_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Y we ʼo ca tzʼonow chiwe wach u chac quix tijin chu toric, chi bij che chi cu rik u chac che i Kajwal, y ire juntir cu tzalij chal chawe, chix chok che —xu bij Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.004', 'Queje xqui ʼan ile, y xeʼel ʼuri pacha u tzʼibam can jun ajbil u tzij i Dios ujer. Ire u tzʼibam canok:
', '', '&lt;span class="verse" id="Matt_21_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Queje xqui ʼan ile, y xeʼel ʼuri pacha u tzʼibam can jun ajbil u tzij i Dios ujer. Ire u tzʼibam canok:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.005', 'Cha bij iri chique i aj tinimit Sion:
“I ʼatol tzij yix ya ca cʼun iwuʼ.
Ire nu ʼanom ta nim che rib,
xa cul chirij jun buru ca cʼunic;
ca cʼunic cul chirij jun ral buru, racʼal jun ati asna aj ekan.”
(Queje ile u tzʼibam can i ajbil u tzij i Dios ujer.)
', '', '&lt;span class="verse" id="Matt_21_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Cha bij iri chique i aj tinimit Sion:
“I ʼatol tzij yix ya ca cʼun iwuʼ.
Ire nu ʼanom ta nim che rib,
xa cul chirij jun buru ca cʼunic;
ca cʼunic cul chirij jun ral buru, racʼal jun ati asna aj ekan.”
(Queje ile u tzʼibam can i ajbil u tzij i Dios ujer.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.006', 'I u tijoxelab i Jesus xe ʼec y xqui ʼan na pacha i bital chique.
', '', '&lt;span class="verse" id="Matt_21_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I u tijoxelab i Jesus xe ʼec y xqui ʼan na pacha i bital chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.007', 'Xqui cʼam li ati asna ruʼ i racʼal. Teʼuri xqui coj qui ʼuʼ chiquij; i Jesus xa ʼan chirij. ', '', '&lt;span class="verse" id="Matt_21_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xqui cʼam li ati asna ruʼ i racʼal. Teʼuri xqui coj qui ʼuʼ chiquij; i Jesus xa ʼan chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.008', 'Je qʼui i winak je rachiʼil pa be; ique xqui rip i qui ʼuʼ chuwach, wikbal re i pu be. Xak je ʼo jujun xqui chʼup u xak cheʼ y xqui coj pa be. ', '', '&lt;span class="verse" id="Matt_21_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Je qʼui i winak je rachiʼil pa be; ique xqui rip i qui ʼuʼ chuwach, wikbal re i pu be. Xak je ʼo jujun xqui chʼup u xak cheʼ y xqui coj pa be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.009', 'I winak chi je nabe chuwach, xak i je teren lok, conojel xe oc chi sicʼ:
—¡Nim u ʼij ire chi umuk uxiquin i ka rey mam David! ¡Dios chu ya ni ʼutz puwi i jun-i chi u takom na lok! ¡Chocsax ni u ʼij i Dios! (Queje ile xe siqʼuin chu bixquil.)
', '', '&lt;span class="verse" id="Matt_21_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I winak chi je nabe chuwach, xak i je teren lok, conojel xe oc chi sicʼ:&lt;p&gt; &lt;/p&gt;—¡Nim u ʼij ire chi umuk uxiquin i ka rey mam David! ¡Dios chu ya ni ʼutz puwi i jun-i chi u takom na lok! ¡Chocsax ni u ʼij i Dios! (Queje ile xe siqʼuin chu bixquil.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.010', 'Are xoc bi i Jesus pa Jerusalen, i winak conojel xe chʼujer na rumal i quicotemal. Je qʼui xe tzʼonowic:
—¿Pachin i achi-le? —xe cha.
', '', '&lt;span class="verse" id="Matt_21_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are xoc bi i Jesus pa Jerusalen, i winak conojel xe chʼujer na rumal i quicotemal. Je qʼui xe tzʼonowic:&lt;p&gt; &lt;/p&gt;—¿Pachin i achi-le? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.011', '—Are i Jesus, chi ajbil u tzij i Dios; ile chi aj Nazaret, u cwenta i Galiléa —xqui bij i winak.
', '', '&lt;span class="verse" id="Matt_21_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;—Are i Jesus, chi ajbil u tzij i Dios; ile chi aj Nazaret, u cwenta i Galiléa —xqui bij i winak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.012', 'Teʼuri i Jesus xoc pa rachoch i Dios; are ʼuri ʼis xe resaj niʼpa i aj cʼayinel xak i aj loʼonel je ʼo xe ja. Xe u balʼatij qui mexa i je aj jalwachil u wach qui mer i winak, xak queje i qui tem i je aj cʼay palomax. ', '', '&lt;h2&gt;I Jesus xe resaj i je aj cʼay pa rachoch i Dios&lt;/h2&gt;&lt;span class="verse" id="Matt_21_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc pa rachoch i Dios; are ʼuri ʼis xe resaj niʼpa i aj cʼayinel xak i aj loʼonel je ʼo xe ja. Xe u balʼatij qui mexa i je aj jalwachil u wach qui mer i winak, xak queje i qui tem i je aj cʼay palomax. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.013', 'Xu bij chique:
—I Dios, chupam u wuj, u bim can i tzij-i: “I wachoch, lic jun ja re qui tzʼonbal i winak chwe”; xui-ri yix i ʼanom qui jul iliʼomab che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_21_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—I Dios, chupam u wuj, u bim can i tzij-i: “I wachoch, lic jun ja re qui tzʼonbal i winak chwe”; xui-ri yix i ʼanom qui jul iliʼomab che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.014', 'Chila pa rachoch i Dios je ʼo jujun mawach, xak je ʼo jujun je clis je ʼo chila. Ique xe tejeb pan ruʼ i Jesus, y ire xe rutzirsaj. ', '', '&lt;span class="verse" id="Matt_21_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Chila pa rachoch i Dios je ʼo jujun mawach, xak je ʼo jujun je clis je ʼo chila. Ique xe tejeb pan ruʼ i Jesus, y ire xe rutzirsaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.015', 'Xak je ʼo jujun acʼalab je ʼo chila que tijin chi sicʼ: “¡Nim u ʼij ire chi umuk uxiquin i ka rey mam David!” xqui bij. I ajwab sacerdóte, xak i je tijonel re i tzij pixab, are xquilo wach xoʼon i Jesus, are xqui ta qui tzij i acʼalab, ique lic xpe coywal. ', '', '&lt;span class="verse" id="Matt_21_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun acʼalab je ʼo chila que tijin chi sicʼ: “¡Nim u ʼij ire chi umuk uxiquin i ka rey mam David!” xqui bij. I ajwab sacerdóte, xak i je tijonel re i tzij pixab, are xquilo wach xoʼon i Jesus, are xqui ta qui tzij i acʼalab, ique lic xpe coywal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.016', 'Are ʼuri xqui bij che i Jesus:
—¿N-ca ta ti yet wach que tijin chu bixquil? ¿ʼUtz nawi ile, ca bij yet? —xe cha.
—Katzij xin ta —xu bij i Jesus chique—. Ique que tijin chu ʼonquil pacha u tzʼibam can jun ajbil u tzij i Dios ujer. Ire xu bij che i Dios:
Yet a ʼanom chique i acʼalab,
xak chique i cʼa que tzʼumanic
lic quiqui bixaj a ʼij (—xu bij).
', '', '&lt;span class="verse" id="Matt_21_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are ʼuri xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¿N-ca ta ti yet wach que tijin chu bixquil? ¿ʼUtz nawi ile, ca bij yet? —xe cha.&lt;p&gt; &lt;/p&gt;—Katzij xin ta —xu bij i Jesus chique—. Ique que tijin chu ʼonquil pacha u tzʼibam can jun ajbil u tzij i Dios ujer. Ire xu bij che i Dios:
Yet a ʼanom chique i acʼalab,
xak chique i cʼa que tzʼumanic
lic quiqui bixaj a ʼij (—xu bij).&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.017', 'Are ʼuri i Jesus xe roʼtaj canok y xa ʼe cʼa Betánia, que u ʼana jun aʼab chila.
', '', '&lt;span class="verse" id="Matt_21_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xe roʼtaj canok y xa ʼe cʼa Betánia, que u ʼana jun aʼab chila.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.018', 'Are xsakiric, xu maj chubi u be, xa ʼe pa Jerusalen. Are ca tijin chi be, i Jesus xpe u numic. ', '', '&lt;h2&gt;Wach u chakijsaxic jun cheʼ xoʼon i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_21_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Are xsakiric, xu maj chubi u be, xa ʼe pa Jerusalen. Are ca tijin chi be, i Jesus xpe u numic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.019', 'Xak teʼet xril jun cheʼ, u cheʼlal hígo, ʼo xucut be. Ire xa ʼe pan ruʼ i cheʼ; lic n-ti u wach xu rik che, xui u xak. Are ʼuri i Jesus xu bij jun cʼokbal tzij che i cheʼ:
—¡Yet n-cat wachin ta chic! —xu bij che.
Are ʼuri i cheʼ juntir xchakijic. ', '', '&lt;span class="verse" id="Matt_21_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xak teʼet xril jun cheʼ, u cheʼlal hígo, ʼo xucut be. Ire xa ʼe pan ruʼ i cheʼ; lic n-ti u wach xu rik che, xui u xak. Are ʼuri i Jesus xu bij jun cʼokbal tzij che i cheʼ:&lt;p&gt; &lt;/p&gt;—¡Yet n-cat wachin ta chic! —xu bij che.&lt;p&gt; &lt;/p&gt;Are ʼuri i cheʼ juntir xchakijic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.020', 'I u tijoxelab, are xquil ile, lic xqui bisoj. Xqui bij che i Jesus:
—¿Wach xa ʼani che i cheʼ-le, chi juntir xchakijic? —xe cha.
', '', '&lt;span class="verse" id="Matt_21_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xquil ile, lic xqui bisoj. Xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¿Wach xa ʼani che i cheʼ-le, chi juntir xchakijic? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.021', 'I Jesus xu bij chique:
—Katzij i quin bij chiwe, we lic ʼo u cubibal i cʼux, we lic n-ta cu bij i cʼux, xak yix quix tiqui chu ʼanic pacha i xin ʼan yin che i che-i. Y n-xui ta la ile, xak quix tiqui che, we qui bij che i jyub-i, “Chat el chi, ja cʼaka awib pa mar,” qui bij che, ile lic coʼon na. ', '', '&lt;span class="verse" id="Matt_21_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, we lic ʼo u cubibal i cʼux, we lic n-ta cu bij i cʼux, xak yix quix tiqui chu ʼanic pacha i xin ʼan yin che i che-i. Y n-xui ta la ile, xak quix tiqui che, we qui bij che i jyub-i, “Chat el chi, ja cʼaka awib pa mar,” qui bij che, ile lic coʼon na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.022', 'Yix, wach i qui tzʼonoj che i Dios, we lic qui cojo chi quiwil na, ca yaʼ na ʼuri chiwe —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_21_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Yix, wach i qui tzʼonoj che i Dios, we lic qui cojo chi quiwil na, ca yaʼ na ʼuri chiwe —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.023', 'Teʼuri i Jesus xoc chic pa rachoch i Dios y xoc chi qui tijoxic chila. Are ʼuri xe tejeb pan i cajʼatzil i sacerdóte ruʼ, xak i nimak tak mamʼib que i aj Israel, y xqui tzʼonoj che:
—¿Pachin xyaw ile paʼab chi cat tijin chu ʼonquil? —xqui bij che—. ¿Bi chi ʼatbal tzijal ʼo paʼab, chi queje ca ʼan ile? —xe cha.
', '', '&lt;h2&gt;Xtzʼonox che i Jesus bi chi ʼatbal tzijal ʼo puʼab&lt;/h2&gt;&lt;span class="verse" id="Matt_21_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chic pa rachoch i Dios y xoc chi qui tijoxic chila. Are ʼuri xe tejeb pan i cajʼatzil i sacerdóte ruʼ, xak i nimak tak mamʼib que i aj Israel, y xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Pachin xyaw ile paʼab chi cat tijin chu ʼonquil? —xqui bij che—. ¿Bi chi ʼatbal tzijal ʼo paʼab, chi queje ca ʼan ile? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.024', 'I Jesus xu bij chique:
—Xak yin quin tzʼonoj jun tzij chiwe. We qui bij usucʼ chwe, teʼuri xak quin bij yin chiwe bi chi ʼatbal tzijal ʼo pinʼab. ', '', '&lt;span class="verse" id="Matt_21_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Xak yin quin tzʼonoj jun tzij chiwe. We qui bij usucʼ chwe, teʼuri xak quin bij yin chiwe bi chi ʼatbal tzijal ʼo pinʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.025', 'Chi bij chwe pachin xyaw puʼab i ma Wan cu kajsaj ya piquiwi i winak. ¿Are i Dios yawnak che, o xa winak yawnak che? —xu bij i Jesus chique.
Ique xe oc chu bixquil chiquiwach:
—¿Wach caka bij na che? —que cha—. We caka bij chi are i Dios yawnak ni ile puʼab i ma Wan, ire cu bij na ʼuri chake: “¿Wuchac ʼuri n-xi coj taj wach i xu bij?” ', '', '&lt;span class="verse" id="Matt_21_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Chi bij chwe pachin xyaw puʼab i ma Wan cu kajsaj ya piquiwi i winak. ¿Are i Dios yawnak che, o xa winak yawnak che? —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;Ique xe oc chu bixquil chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Wach caka bij na che? —que cha—. We caka bij chi are i Dios yawnak ni ile puʼab i ma Wan, ire cu bij na ʼuri chake: “¿Wuchac ʼuri n-xi coj taj wach i xu bij?” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.026', 'We xak caka bij chi xa ruʼ winak petnak wi wach i xu bij i ma Wan, lic caka xij kib chique i winak; man conojel i winak quiqui bij chi ma Wan lic ruʼ i Dios petnak wi wach i u bim —xqui bij chiquiwach.
', '', '&lt;span class="verse" id="Matt_21_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;We xak caka bij chi xa ruʼ winak petnak wi wach i xu bij i ma Wan, lic caka xij kib chique i winak; man conojel i winak quiqui bij chi ma Wan lic ruʼ i Dios petnak wi wach i u bim —xqui bij chiquiwach.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.027', 'Rumal-i xqui bij che i Jesus:
—N-ketaʼam taj —xe cha che.
—Xak yin n-quin bij ta ʼuri chiwe wach i ʼatbal tzij ʼo pinʼab, chi quin ʼan tak iri —xu bij chique.
', '', '&lt;span class="verse" id="Matt_21_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Rumal-i xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—N-ketaʼam taj —xe cha che.&lt;p&gt; &lt;/p&gt;—Xak yin n-quin bij ta ʼuri chiwe wach i ʼatbal tzij ʼo pinʼab, chi quin ʼan tak iri —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.028', 'Teʼuri i Jesus xu bij chique:
—¿Wach qui chʼob yix che i tzij-i? ʼO jun achi, je ʼo queb u cʼojol. I achi xu bij che i jun: “Wacʼal, jat woʼor pa chac chupam in ticon,” xu bij che. ', '', '&lt;h2&gt;Queb alabom chi xe tak rumal qui kajaw&lt;/h2&gt;&lt;span class="verse" id="Matt_21_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wach qui chʼob yix che i tzij-i? ʼO jun achi, je ʼo queb u cʼojol. I achi xu bij che i jun: “Wacʼal, jat woʼor pa chac chupam in ticon,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.029', 'I racʼal xu laʼ u wach che: “N-cwaj taj,” xu bij. Xui-ri, xu jalwachij u noʼoj y xa ʼe pa chac. ', '', '&lt;span class="verse" id="Matt_21_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I racʼal xu laʼ u wach che: “N-cwaj taj,” xu bij. Xui-ri, xu jalwachij u noʼoj y xa ʼe pa chac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.030', 'Teʼuri i qui kajaw xak xu ʼijla i jun chic, pacha xoʼon che i nabe. Ire xu bij chi ca ʼec; “Ey,” coʼono, xui-ri, n-xa ʼe taj. ', '', '&lt;span class="verse" id="Matt_21_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Teʼuri i qui kajaw xak xu ʼijla i jun chic, pacha xoʼon che i nabe. Ire xu bij chi ca ʼec; “Ey,” coʼono, xui-ri, n-xa ʼe taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.031', '¿Pachique chique i je queb u cʼojol xoʼono pacha craj i qui kajaw? —xu bij i Jesus chique i nimak tak mamʼib.
—Are i nabe —xe cha.
—Katzij i xi bij —xu bij i Jesus chique—. Xak quin bij na yin chiwe chi ʼo i je mal laj tak aj tzʼonol alcawal, xak ʼo ixokib ajmaquib que tijin coquic puʼab i Dios, y yix n-taj. Ique je pacha i racʼal i achi chi xoʼono pacha craj u kajaw. ', '', '&lt;span class="verse" id="Matt_21_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;¿Pachique chique i je queb u cʼojol xoʼono pacha craj i qui kajaw? —xu bij i Jesus chique i nimak tak mamʼib.&lt;p&gt; &lt;/p&gt;—Are i nabe —xe cha.&lt;p&gt; &lt;/p&gt;—Katzij i xi bij —xu bij i Jesus chique—. Xak quin bij na yin chiwe chi ʼo i je mal laj tak aj tzʼonol alcawal, xak ʼo ixokib ajmaquib que tijin coquic puʼab i Dios, y yix n-taj. Ique je pacha i racʼal i achi chi xoʼono pacha craj u kajaw. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.032', 'Xui-ri yix ix pacha i ucab u cʼojol man n-xix cojon ta che u tzij i ma Wan aj kajsanel ya. Xcʼun ire chu cʼutic chiwe wach craj te i cʼaslemal, xui-ri n-xix cojon taj. I je aj tzʼonol alcawal, xak i ixokib ajmaquib, ique xqui cojo. Tupu xiwil yix ile, n-xi jalwachij ti nojbal, cojbal-re u tzij —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_21_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xui-ri yix ix pacha i ucab u cʼojol man n-xix cojon ta che u tzij i ma Wan aj kajsanel ya. Xcʼun ire chu cʼutic chiwe wach craj te i cʼaslemal, xui-ri n-xix cojon taj. I je aj tzʼonol alcawal, xak i ixokib ajmaquib, ique xqui cojo. Tupu xiwil yix ile, n-xi jalwachij ti nojbal, cojbal-re u tzij —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.033', 'Teʼuri xu bij chique:
—Chi ta chic jun tijojbal tzij quin bij na chiwe. ʼO jun achi nim i rulew, u ticom úva chuwach. Teʼuri xu coj coral chirij; xu yijba pa quiqui yitzʼ wi u waʼal i úva, xak xu yijba jun ral ja tacʼtic chupam i ticon, chajbal-re ronojel. Teʼuri xe u coj jujun ajchaquib, je aj a média ruʼ; y xa ʼe ire naj che jun tinimit. ', '', '&lt;h2&gt;Wach xqui ʼan i aj mediant che u cʼojol i rajaw ulew&lt;/h2&gt;&lt;span class="verse" id="Matt_21_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ta chic jun tijojbal tzij quin bij na chiwe. ʼO jun achi nim i rulew, u ticom úva chuwach. Teʼuri xu coj coral chirij; xu yijba pa quiqui yitzʼ wi u waʼal i úva, xak xu yijba jun ral ja tacʼtic chupam i ticon, chajbal-re ronojel. Teʼuri xe u coj jujun ajchaquib, je aj a média ruʼ; y xa ʼe ire naj che jun tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.034', 'Are xu rik i ʼij re i cosech, xe u tak li u mocom chu tzʼonoxic i re ire chique i raj mediant. ', '', '&lt;span class="verse" id="Matt_21_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Are xu rik i ʼij re i cosech, xe u tak li u mocom chu tzʼonoxic i re ire chique i raj mediant. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.035', 'Are xe upon i mocom, xe qui chapo. I jun, xqui chʼayo; i jun chic, xqui camsaj; i jun chic, xqui coj abaj che. ', '', '&lt;span class="verse" id="Matt_21_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Are xe upon i mocom, xe qui chapo. I jun, xqui chʼayo; i jun chic, xqui camsaj; i jun chic, xqui coj abaj che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.036', 'Teʼuri i rajaw ticon xe u tak chali juban chic u mocom, mas je qʼui na chiquiwach i je nabe. Are xe uponic, i je raj mediant xqui ʼan chic chique pacha xqui ʼan chique i juban chi je nabe.
', '', '&lt;span class="verse" id="Matt_21_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri i rajaw ticon xe u tak chali juban chic u mocom, mas je qʼui na chiquiwach i je nabe. Are xe uponic, i je raj mediant xqui ʼan chic chique pacha xqui ʼan chique i juban chi je nabe.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.037', 'Pa qʼuisbal-re, xu tak li i mer u cʼojol. Xu chʼob ire: “Ique lic que cojon na che i wacʼal-i,” xu chʼob ire. ', '', '&lt;span class="verse" id="Matt_21_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Pa qʼuisbal-re, xu tak li i mer u cʼojol. Xu chʼob ire: “Ique lic que cojon na che i wacʼal-i,” xu chʼob ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.038', 'Xui-ri, are xquil i u cʼojol i rajaw ulew, xqui bij chiquiwach: “Are ire-le are i rajaw ulew coʼon panok; chaka camsaj,” xe cha, “man yoj coj canajic oj rajaw,” xe cha. ', '', '&lt;span class="verse" id="Matt_21_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Xui-ri, are xquil i u cʼojol i rajaw ulew, xqui bij chiquiwach: “Are ire-le are i rajaw ulew coʼon panok; chaka camsaj,” xe cha, “man yoj coj canajic oj rajaw,” xe cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.039', 'Rumal-i xqui chapo, y xquesaj li chuwach ulew, y xqui camsaj. (Queje ile i tijojbal tzij xu bij i Jesus chique.)
', '', '&lt;span class="verse" id="Matt_21_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Rumal-i xqui chapo, y xquesaj li chuwach ulew, y xqui camsaj. (Queje ile i tijojbal tzij xu bij i Jesus chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.040', 'Teʼuri xu tzʼonoj chique:
—Bay, are ca cʼun ni rajaw i ulew, ¿wach coʼon ni ire chique i ajchaquib-le? —xu bij.
', '', '&lt;span class="verse" id="Matt_21_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Teʼuri xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—Bay, are ca cʼun ni rajaw i ulew, ¿wach coʼon ni ire chique i ajchaquib-le? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.041', 'Ique xqui bij:
—Ire lic que u camsaj ni je aj ʼanol mal-le; lic n-cril ta toʼbal qui wach. Teʼuri cu ya i rulew chique juban chic aj mediant, chi quiqui jach i re ire che, are cu rik i ʼij —xe cha i mamʼib che i Jesus.
', '', '&lt;span class="verse" id="Matt_21_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Ique xqui bij:&lt;p&gt; &lt;/p&gt;—Ire lic que u camsaj ni je aj ʼanol mal-le; lic n-cril ta toʼbal qui wach. Teʼuri cu ya i rulew chique juban chic aj mediant, chi quiqui jach i re ire che, are cu rik i ʼij —xe cha i mamʼib che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.042', 'Teʼuri i Jesus xak xu bij chique:
—¿Xataba n-iwilom taj wach tzʼibtal can chupam u wuj i Dios? Queje iri cu bij:
I abaj chi n-xcaj ti xqui coj i je aj ʼanol ja,
are i abaj-le chi xeʼelok lic nim ni u chac che i cubibal ja.
Are i xeʼelok-le, are i Kajwal Dios xa ʼanawic,
y cakil yoj, lic ʼutz ile chi u ʼanom ire.
(Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Matt_21_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xak xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Xataba n-iwilom taj wach tzʼibtal can chupam u wuj i Dios? Queje iri cu bij:
I abaj chi n-xcaj ti xqui coj i je aj ʼanol ja,
are i abaj-le chi xeʼelok lic nim ni u chac che i cubibal ja.
Are i xeʼelok-le, are i Kajwal Dios xa ʼanawic,
y cakil yoj, lic ʼutz ile chi u ʼanom ire.
(Queje ile tzʼibtal canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.043', 'Rumal-i, quin bij chiwe chi Dios cresaj ni u chac piʼab yix y cu ya na piquiʼab juban chic ajchaquib je ʼutz, chi quiqui ya ni cosech che i Dios pacha ca majawic. ', '', '&lt;span class="verse" id="Matt_21_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Rumal-i, quin bij chiwe chi Dios cresaj ni u chac piʼab yix y cu ya na piquiʼab juban chic ajchaquib je ʼutz, chi quiqui ya ni cosech che i Dios pacha ca majawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.044', 'I abaj-le chi xin bij chiwe, we ʼo jun ca ʼe tzak puwi, xa ca paxic; xak i abaj-le, we ca tzak chirij jun winak, lic coʼon na cʼajcʼaj che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_21_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;I abaj-le chi xin bij chiwe, we ʼo jun ca ʼe tzak puwi, xa ca paxic; xak i abaj-le, we ca tzak chirij jun winak, lic coʼon na cʼajcʼaj che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.045', 'I je ajwab sacerdóte, xak i je aj Fariséo, are xqui ta wach i tijojbal tzij xbix chique, ya xquetamaj chi xa are ique que u lap na. ', '', '&lt;span class="verse" id="Matt_21_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;I je ajwab sacerdóte, xak i je aj Fariséo, are xqui ta wach i tijojbal tzij xbix chique, ya xquetamaj chi xa are ique que u lap na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '21.046', 'Rumal-i cacaj ique quiqui chapo, xui-ri xqui xij quib chique i uqʼuial winak, man i winak quiqui bij chi Jesus ajbil u tzij i Dios.
', '', '&lt;span class="verse" id="Matt_21_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Rumal-i cacaj ique quiqui chapo, xui-ri xqui xij quib chique i uqʼuial winak, man i winak quiqui bij chi Jesus ajbil u tzij i Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.001', 'I Jesus xu jek chic u bixquil i tijojbal tzij chique. Xu bij:
', '', '&lt;h2&gt;Wach xqui ʼan i je sicʼtal che jun cʼulanquil&lt;/h2&gt;&lt;span class="verse" id="Matt_22_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus xu jek chic u bixquil i tijojbal tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.002', '—I ʼatbal tzij re aj chicaj, pacha iri: ʼO jun nim laj ʼatol tzij xu yijba i nimaʼij re u cʼulanquil i u cʼojol. ', '', '&lt;span class="verse" id="Matt_22_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—I ʼatbal tzij re aj chicaj, pacha iri: ʼO jun nim laj ʼatol tzij xu yijba i nimaʼij re u cʼulanquil i u cʼojol. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.003', 'Are xu rik i ʼij, xe u tak bi u mocom cuʼ i je siqʼuim chic, queʼe qui bij chique chi ya yij chic i waʼim. Xui-ri, ique n-xcaj ta xe cʼunic. ', '', '&lt;span class="verse" id="Matt_22_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are xu rik i ʼij, xe u tak bi u mocom cuʼ i je siqʼuim chic, queʼe qui bij chique chi ya yij chic i waʼim. Xui-ri, ique n-xcaj ta xe cʼunic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.004', 'Teʼuri xe u tak chubi juban chic u mocom chi qui siqʼuixic. Xu bij chique: “Ji bij chique niʼpa i je in siqʼuim na, chi ya yij chic i waʼim. Chi bij chique chi je in camsam chic i chicop chi je in tiʼojersam. Ronojel ʼis yij chic; ique che petok, chix chok chique.” ', '', '&lt;span class="verse" id="Matt_22_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Teʼuri xe u tak chubi juban chic u mocom chi qui siqʼuixic. Xu bij chique: “Ji bij chique niʼpa i je in siqʼuim na, chi ya yij chic i waʼim. Chi bij chique chi je in camsam chic i chicop chi je in tiʼojersam. Ronojel ʼis yij chic; ique che petok, chix chok chique.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.005', 'Xui-ri, i je siqʼuim na, n-xqui ʼan ta cas chique i takon. ʼO jun chique xa ʼe pa rulew, xak jun chic xa ʼe chi cʼayinic. ', '', '&lt;span class="verse" id="Matt_22_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xui-ri, i je siqʼuim na, n-xqui ʼan ta cas chique i takon. ʼO jun chique xa ʼe pa rulew, xak jun chic xa ʼe chi cʼayinic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.006', 'Xak je ʼo jujun xe qui chap i takon y xqui ʼan cʼax chique y xe qui camsaj. ', '', '&lt;span class="verse" id="Matt_22_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun xe qui chap i takon y xqui ʼan cʼax chique y xe qui camsaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.007', 'Rumal-i, i ʼatol tzij lic xpe roywal che; xe u tak bi u soldádo chi qui camsaxic niʼpa i xe ʼanaw i camic-le, xak chu poroxic i qui tinimit. ', '', '&lt;span class="verse" id="Matt_22_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Rumal-i, i ʼatol tzij lic xpe roywal che; xe u tak bi u soldádo chi qui camsaxic niʼpa i xe ʼanaw i camic-le, xak chu poroxic i qui tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.008', 'Teʼuri xu bij chique u mocom: “Ronojel ʼis yij chic re i nimaʼij, xui-ri ique chi je in siqʼuim na, n-xmajaw ta chique chi que petic. ', '', '&lt;span class="verse" id="Matt_22_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique u mocom: “Ronojel ʼis yij chic re i nimaʼij, xui-ri ique chi je in siqʼuim na, n-xmajaw ta chique chi que petic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.009', 'Rumal-i, jix yix pa nimak tak be, y che i siqʼuij che i nimaʼij niʼpa i que i rik chila,” xu bij chique u mocom. ', '', '&lt;span class="verse" id="Matt_22_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Rumal-i, jix yix pa nimak tak be, y che i siqʼuij che i nimaʼij niʼpa i que i rik chila,” xu bij chique u mocom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.010', 'Ique ʼis xe ʼel pa tak be, y xe qui mol niʼpa i xe qui cʼulaj. Xe qui mol i je ʼutz laj tak winak, xak i ne te ʼutz. Queje ile xqui nojsaj i u paja.
', '', '&lt;span class="verse" id="Matt_22_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Ique ʼis xe ʼel pa tak be, y xe qui mol niʼpa i xe qui cʼulaj. Xe qui mol i je ʼutz laj tak winak, xak i ne te ʼutz. Queje ile xqui nojsaj i u paja.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.011', 'Teʼuri xoc i ʼatol tzij pa je moltajnak wi i je u siqʼuim ruʼ. Are ʼuri xril jun achi cuʼ chi nu cojom ti u ʼuʼ re i nimaʼij. ', '', '&lt;span class="verse" id="Matt_22_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Teʼuri xoc i ʼatol tzij pa je moltajnak wi i je u siqʼuim ruʼ. Are ʼuri xril jun achi cuʼ chi nu cojom ti u ʼuʼ re i nimaʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.012', 'Y xu bij che: “Yet, ¿wach awoquic chi, chi na cojom ti a ʼuʼ re i nimaʼij?” xu bij. I jun n-xu rik taj wach cu bij che. ', '', '&lt;span class="verse" id="Matt_22_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Y xu bij che: “Yet, ¿wach awoquic chi, chi na cojom ti a ʼuʼ re i nimaʼij?” xu bij. I jun n-xu rik taj wach cu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.013', 'Teʼuri i ʼatol tzij xe u siqʼuij i u mocom, xu bij chique: “Chi xima i u ʼab, i rakan, y chiwesaj bi rija pa ʼekum. Chila coʼ na, xak cu kachʼachʼej ni uware rumal u bis,” xu bij chique. ', '', '&lt;span class="verse" id="Matt_22_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼatol tzij xe u siqʼuij i u mocom, xu bij chique: “Chi xima i u ʼab, i rakan, y chiwesaj bi rija pa ʼekum. Chila coʼ na, xak cu kachʼachʼej ni uware rumal u bis,” xu bij chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.014', 'Queje ile queʼelok man tupu je qʼui i je sicʼtal na, ne te qʼui je chatalic. (Queje i tijojbal tzij ile xu bij i Jesus.)
', '', '&lt;span class="verse" id="Matt_22_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Queje ile queʼelok man tupu je qʼui i je sicʼtal na, ne te qʼui je chatalic. (Queje i tijojbal tzij ile xu bij i Jesus.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.015', 'Are ʼuri i aj Fariséo xe ʼec y xe oc chu chʼobic chiquiwach wach jun tzij quiqui tzʼonoj che i Jesus, tzaksabal-re quiqui ʼano. ', '', '&lt;h2&gt;Chi ya che i ʼatol tzij wach ʼo re che; xak queje i Dios, chi ya che wach ʼo re che&lt;/h2&gt;&lt;span class="verse" id="Matt_22_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are ʼuri i aj Fariséo xe ʼec y xe oc chu chʼobic chiquiwach wach jun tzij quiqui tzʼonoj che i Jesus, tzaksabal-re quiqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.016', 'Teʼuri xe qui cha jujun je ʼo cuʼ, xak juban chic chi je teren chirij i ma Heródes, y xe qui tak bi ruʼ i Jesus.
—Nim laj winak —xqui bij che, are xe upon ruʼ—, ketaʼam chi yet xui i sak laj tzij ca bij. Ketaʼam chi yet katzij ca cʼut i ʼutz laj be re i Dios, lic n-ca ʼan ta cas che wach quiqui bij i winak chawij. Yet n-ca coj ta retalil che, tupu je nimak tak winak. ', '', '&lt;span class="verse" id="Matt_22_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Teʼuri xe qui cha jujun je ʼo cuʼ, xak juban chic chi je teren chirij i ma Heródes, y xe qui tak bi ruʼ i Jesus.&lt;p&gt; &lt;/p&gt;—Nim laj winak —xqui bij che, are xe upon ruʼ—, ketaʼam chi yet xui i sak laj tzij ca bij. Ketaʼam chi yet katzij ca cʼut i ʼutz laj be re i Dios, lic n-ca ʼan ta cas che wach quiqui bij i winak chawij. Yet n-ca coj ta retalil che, tupu je nimak tak winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.017', 'Cha bij ʼu chake, wach i ca bij yet, ¿ʼUtz nawi, caka toj impuésto che i ʼatol tzij, o n-ʼus taj? —xqui bij che.
', '', '&lt;span class="verse" id="Matt_22_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Cha bij ʼu chake, wach i ca bij yet, ¿ʼUtz nawi, caka toj impuésto che i ʼatol tzij, o n-ʼus taj? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.018', 'I Jesus xretamaj chi n-ʼus ta quiqui chʼobo are xqui bij ile. Xu bij chique:
—Yix, ¡xa queb i palaj! ¿Wuchac xa tzaksabal-we qui ʼano? ', '', '&lt;span class="verse" id="Matt_22_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I Jesus xretamaj chi n-ʼus ta quiqui chʼobo are xqui bij ile. Xu bij chique:&lt;p&gt; &lt;/p&gt;—Yix, ¡xa queb i palaj! ¿Wuchac xa tzaksabal-we qui ʼano? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.019', 'Chi cʼutu jun sak pwak chwe, chi tojbal re impuésto —coʼono.
Y xe qui cʼama jun sak pwak. ', '', '&lt;span class="verse" id="Matt_22_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Chi cʼutu jun sak pwak chwe, chi tojbal re impuésto —coʼono.&lt;p&gt; &lt;/p&gt;Y xe qui cʼama jun sak pwak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.020', 'Are ʼuri i Jesus xu tzʼonoj chique:
—¿Pachin u qʼuexwach iri ʼo chuwach? ¿Pachin u bi iri tzʼibtal chuwach? —xu bij chique.
', '', '&lt;span class="verse" id="Matt_22_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Pachin u qʼuexwach iri ʼo chuwach? ¿Pachin u bi iri tzʼibtal chuwach? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.021', '—Re i ʼatol tzij —xe cha ique.
—Bay, chi ya ʼuri che i ʼatol tzij wach ʼo re ire che, xak chi ya che i Dios wach ʼo re ire che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_22_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;—Re i ʼatol tzij —xe cha ique.&lt;p&gt; &lt;/p&gt;—Bay, chi ya ʼuri che i ʼatol tzij wach ʼo re ire che, xak chi ya che i Dios wach ʼo re ire che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.022', 'Ique, are xqui ta ile, lic xqui bisoj che. Are ʼuri xqui canaj canok y xe ʼec.
', '', '&lt;span class="verse" id="Matt_22_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Ique, are xqui ta ile, lic xqui bisoj che. Are ʼuri xqui canaj canok y xe ʼec.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.023', 'Xak are i ʼij-le, je ʼo jujun aj Saducéo xe upon ruʼ. (I aj Saducéo quiqui bij chi i je camnak n-que walij ta chic.) Xqui bij che:
', '', '&lt;h2&gt;I Jesus xu sakirsaj chi ʼo ni qui walijbal i camnak&lt;/h2&gt;&lt;span class="verse" id="Matt_22_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xak are i ʼij-le, je ʼo jujun aj Saducéo xe upon ruʼ. (I aj Saducéo quiqui bij chi i je camnak n-que walij ta chic.) Xqui bij che:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.024', '—Nim laj winak, i mam Moises u bim canok chi jun achi, we xcamic, we n-ta racʼal ruʼ can i rixokil, ʼo u chac ʼuri ca cʼuli i ratz u chaʼ i achi ruʼ ixok malcan, pu qʼuexel i camnak. Queje ile ca qʼuiji na can racʼal i camnak ruʼ ixok. Je ile u bim canok. ', '', '&lt;span class="verse" id="Matt_22_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;—Nim laj winak, i mam Moises u bim canok chi jun achi, we xcamic, we n-ta racʼal ruʼ can i rixokil, ʼo u chac ʼuri ca cʼuli i ratz u chaʼ i achi ruʼ ixok malcan, pu qʼuexel i camnak. Queje ile ca qʼuiji na can racʼal i camnak ruʼ ixok. Je ile u bim canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.025', 'Bay, oʼonom lok, je ʼo wukub kuʼ yoj, je catz qui chaʼ quib. I nabe chique xcʼuliʼic, y xoʼon panok xcamic, n-ta racʼal. I ixok malcan xcanaj can ruʼ ucab chaʼaxel. ', '', '&lt;span class="verse" id="Matt_22_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Bay, oʼonom lok, je ʼo wukub kuʼ yoj, je catz qui chaʼ quib. I nabe chique xcʼuliʼic, y xoʼon panok xcamic, n-ta racʼal. I ixok malcan xcanaj can ruʼ ucab chaʼaxel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.026', 'Teʼuri i achi-le xcam chic, i ixok xcʼuli chic ruʼ urox chaʼaxel. I achi-le xak xcamic. Queje xqui ʼan i wukub achiab-le. ', '', '&lt;span class="verse" id="Matt_22_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Teʼuri i achi-le xcam chic, i ixok xcʼuli chic ruʼ urox chaʼaxel. I achi-le xak xcamic. Queje xqui ʼan i wukub achiab-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.027', 'Xak xoʼon panok, xcam i ixok. ', '', '&lt;span class="verse" id="Matt_22_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xak xoʼon panok, xcam i ixok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.028', 'Bay, we ta que walij chic i camnak, ¿pachique chique i je wukub-le, chi mer rachijil queʼelok? Man ʼis xcʼuli cuʼ i je wukub achiab-le —xe cha.
', '', '&lt;span class="verse" id="Matt_22_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Bay, we ta que walij chic i camnak, ¿pachique chique i je wukub-le, chi mer rachijil queʼelok? Man ʼis xcʼuli cuʼ i je wukub achiab-le —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.029', 'I Jesus xu laʼ u wach chique:
—Yix xa ix sachnak na; n-qui ta tu be wach cu bij u tzij i Dios chi tzʼibtal canok; xak n-iwetaʼam taj wach i nim laj u choʼab i Dios. ', '', '&lt;span class="verse" id="Matt_22_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Yix xa ix sachnak na; n-qui ta tu be wach cu bij u tzij i Dios chi tzʼibtal canok; xak n-iwetaʼam taj wach i nim laj u choʼab i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.030', 'Are que walij ni camnak, que ʼelok pacha i u ángel i Dios aj chicaj; n-que cʼuli taj. I achiab, i ixokib n-que cʼuli ta chic. ', '', '&lt;span class="verse" id="Matt_22_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Are que walij ni camnak, que ʼelok pacha i u ángel i Dios aj chicaj; n-que cʼuli taj. I achiab, i ixokib n-que cʼuli ta chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.031', 'I qui walijbal i camnak, katzij ʼolic. ¿Xataba n-iwilom ti yix wach u bim can i Dios chiwe? U bim can ire: ', '', '&lt;span class="verse" id="Matt_22_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I qui walijbal i camnak, katzij ʼolic. ¿Xataba n-iwilom ti yix wach u bim can i Dios chiwe? U bim can ire: &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.032', '“Cʼa in u Dios i mam Abraham, xak i mam Isaac, xak i mam Jacob,” xu bij. I oxib-le je camnak chic are xu bij i Dios ile. Bay, chuwach i Dios, c a je cʼas na; i Dios n-are ti qui Dios i je camnak, are i qui Dios i je cʼaslic —xu bij i Jesus chique. (Queje ile xu sakirsaj chique chi ʼo ni qui walijbal i camnak). ', '', '&lt;span class="verse" id="Matt_22_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;“Cʼa in u Dios i mam Abraham, xak i mam Isaac, xak i mam Jacob,” xu bij. I oxib-le je camnak chic are xu bij i Dios ile. Bay, chuwach i Dios, c a je cʼas na; i Dios n-are ti qui Dios i je camnak, are i qui Dios i je cʼaslic —xu bij i Jesus chique. (Queje ile xu sakirsaj chique chi ʼo ni qui walijbal i camnak). &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.033', 'I winak, are xqui ta ile, lic xqui bisoj che i cʼutunic xoʼon i Jesus.
', '', '&lt;span class="verse" id="Matt_22_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I winak, are xqui ta ile, lic xqui bisoj che i cʼutunic xoʼon i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.034', 'I aj Fariséo, are xqui ta rason chi Jesus xtiqui chu laʼic u wach i qui chiʼ i je aj Saducéo, xqui mol la quib. ', '', '&lt;h2&gt;Wach i tzij ʼatz u ʼij, re i tzij pixab&lt;/h2&gt;&lt;span class="verse" id="Matt_22_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I aj Fariséo, are xqui ta rason chi Jesus xtiqui chu laʼic u wach i qui chiʼ i je aj Saducéo, xqui mol la quib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.035', 'ʼO jun chique chi tijonel re i ʼatbal tzij u tzʼibam can i mam Moises. Xraj ire xu tzʼonoj jun tzij che i Jesus, tzaksabal-re coʼono. Xu bij che:
', '', '&lt;span class="verse" id="Matt_22_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;ʼO jun chique chi tijonel re i ʼatbal tzij u tzʼibam can i mam Moises. Xraj ire xu tzʼonoj jun tzij che i Jesus, tzaksabal-re coʼono. Xu bij che:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.036', '—Nim laj winak, ¿pachique tzij re i ʼatbal tzij mas ʼo u chac chi caka cojo? —xu bij.
', '', '&lt;span class="verse" id="Matt_22_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;—Nim laj winak, ¿pachique tzij re i ʼatbal tzij mas ʼo u chac chi caka cojo? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.037', 'I Jesus xu bij che:
—“Chawaj Awajwal Dios ronojel a cʼux, ronojel awanima, ronojel a nojbal.” ', '', '&lt;span class="verse" id="Matt_22_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—“Chawaj Awajwal Dios ronojel a cʼux, ronojel awanima, ronojel a nojbal.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.038', 'Are i jun-le chi mas ʼo u chac coj cojon che; are mas ʼo u ʼij ile chiquiwach i juban. ', '', '&lt;span class="verse" id="Matt_22_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Are i jun-le chi mas ʼo u chac coj cojon che; are mas ʼo u ʼij ile chiquiwach i juban. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.039', 'Teʼuri i ucab tzij (chi ʼo u chac coj cojon che) laj cu maj i jun-le. Cu bij: “Cʼax che a na a loʼcʼanij pacha cʼax u naʼic awib yet ca ʼano,” cu bij. ', '', '&lt;span class="verse" id="Matt_22_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Teʼuri i ucab tzij (chi ʼo u chac coj cojon che) laj cu maj i jun-le. Cu bij: “Cʼax che a na a loʼcʼanij pacha cʼax u naʼic awib yet ca ʼano,” cu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.040', 'I queb tzij-le are mer u xebal ronojel tak i ʼatbal tzij u tzʼibam can i mam Moises, xak are u xebal ronojel tak i cʼutunic qui canam can i ajbil u tzij i Dios. (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Matt_22_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;I queb tzij-le are mer u xebal ronojel tak i ʼatbal tzij u tzʼibam can i mam Moises, xak are u xebal ronojel tak i cʼutunic qui canam can i ajbil u tzij i Dios. (Queje xu bij i Jesus ile chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.041', 'Tzʼakat cʼa qui molom quib i je aj Fariséo, ', '', '&lt;h2&gt;I Jesus lic Dios; n-toʼ ta umuk uxiquin can i mam David&lt;/h2&gt;&lt;span class="verse" id="Matt_22_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Tzʼakat cʼa qui molom quib i je aj Fariséo, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.042', 'i Jesus xu tzʼonoj chique:
—¿Wach qui chʼob yix chirij i Crísto, chi Toliwe iwoyʼem? ¿Pachin i ratit u mam? —xu bij chique.
Ique xqui bij:
—Ire are ni umuk uxiquin can i mam David —xe cha.
', '', '&lt;span class="verse" id="Matt_22_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;i Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Wach qui chʼob yix chirij i Crísto, chi Toliwe iwoyʼem? ¿Pachin i ratit u mam? —xu bij chique.&lt;p&gt; &lt;/p&gt;Ique xqui bij:&lt;p&gt; &lt;/p&gt;—Ire are ni umuk uxiquin can i mam David —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.043', 'Are ʼuri i Jesus xu bij chique:
—¿Wuchac ʼuri i mam David xu bij “Wajwal” chirij i Crísto are xu lapo? Lic rumal ni u Tewal i Dios xu bij ile. I mam David xu bij i tzij-i:
', '', '&lt;span class="verse" id="Matt_22_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac ʼuri i mam David xu bij “Wajwal” chirij i Crísto are xu lapo? Lic rumal ni u Tewal i Dios xu bij ile. I mam David xu bij i tzij-i:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.044', 'Xu bij i Kajwal Dios che i Wajwal Crísto:
“Chat cul na pin wikabim, cʼa cu rik ni ʼij
que in ya na pawakan niʼpa i je aj chʼoʼoj chawij,” xu bij.
Queje ile u tzʼibam can i mam David ujer. ', '', '&lt;span class="verse" id="Matt_22_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Xu bij i Kajwal Dios che i Wajwal Crísto:
“Chat cul na pin wikabim, cʼa cu rik ni ʼij
que in ya na pawakan niʼpa i je aj chʼoʼoj chawij,” xu bij.
Queje ile u tzʼibam can i mam David ujer. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.045', '¿Wach u ʼonquil ʼuri chi Crísto xa umuk uxiquin i mam David? ¿Wach u ʼonquil ile man lic are i mam David xu bij “Wajwal” che i Crísto? (Queje xu bij i Jesus ile chique i aj Fariséo, cu sakijbej-re chique chi Crísto n-toʼ ta winak, lic Dios.)
', '', '&lt;span class="verse" id="Matt_22_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;¿Wach u ʼonquil ʼuri chi Crísto xa umuk uxiquin i mam David? ¿Wach u ʼonquil ile man lic are i mam David xu bij “Wajwal” che i Crísto? (Queje xu bij i Jesus ile chique i aj Fariséo, cu sakijbej-re chique chi Crísto n-toʼ ta winak, lic Dios.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '22.046', 'Mi jun chique xu rik chic u laʼic u wach che, mi jupaj. Are xoʼon ile, teʼuri mi jun xu cowij chic u cʼux chu tzʼonoxic mas tzij che i Jesus.
', '', '&lt;span class="verse" id="Matt_22_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Mi jun chique xu rik chic u laʼic u wach che, mi jupaj. Are xoʼon ile, teʼuri mi jun xu cowij chic u cʼux chu tzʼonoxic mas tzij che i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.001', 'Teʼuri i Jesus xoc chu bixquil i tzij-i chique i winak, xak chique u tijoxelab:
', '', '&lt;h2&gt;I Jesus cu coj qui mac i aj Fariséo, xak i nimak tak aj tijonel&lt;/h2&gt;&lt;span class="verse" id="Matt_23_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chu bixquil i tzij-i chique i winak, xak chique u tijoxelab:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.002', '—I je tijonel re i ʼatbal tzij, xak i aj Fariséo, ʼo chiquikul quiqui sakij chiwe wach usucʼ i ʼatbal tzij chi u tzʼibam can i mam Moises. ', '', '&lt;span class="verse" id="Matt_23_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—I je tijonel re i ʼatbal tzij, xak i aj Fariséo, ʼo chiquikul quiqui sakij chiwe wach usucʼ i ʼatbal tzij chi u tzʼibam can i mam Moises. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.003', 'Rumal-i chix cojon na che, lic chi ʼana cas che wach i quiqui bij; xui-ri, mi ʼan yix pacha quiqui ʼan ique. Mi ʼan na, man ique n-quiqui ʼan taj pacha tak i quiqui bij. ', '', '&lt;span class="verse" id="Matt_23_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Rumal-i chix cojon na che, lic chi ʼana cas che wach i quiqui bij; xui-ri, mi ʼan yix pacha quiqui ʼan ique. Mi ʼan na, man ique n-quiqui ʼan taj pacha tak i quiqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.004', 'Ique, nim i ekan quiqui ya chiquij i winak, cʼax u chʼijawic; xak n-que ton ta ruʼ, mi xa ta jubiʼ. ', '', '&lt;span class="verse" id="Matt_23_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ique, nim i ekan quiqui ya chiquij i winak, cʼax u chʼijawic; xak n-que ton ta ruʼ, mi xa ta jubiʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.005', 'Ronojel wach i quiqui ʼano, quiqui ʼan na man xa cacaj que ʼiltaj cumal i winak. Kus quiquil u cʼamic bi jupaj u tzij u pixab i Dios pasim chupam jun ral tzʼum chuwach qui palaj. Xak kus quiquil u batzʼcatixic tzʼum rij tak qui ʼab. I tzʼum-le xak tzʼibtal jutakpaj u tzij i Dios chuwach; kus quiquil ique we nim u wach. Xak i u chiʼ i qui ʼuʼ, lic nimak i rismachiʼ ʼo che, mas nimak chuwach i que i toʼ winak. Queje quiqui ʼan ique ile, quiquilbej na re i winak chi lic je re i Dios, quiqui bij ique. ', '', '&lt;span class="verse" id="Matt_23_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Ronojel wach i quiqui ʼano, quiqui ʼan na man xa cacaj que ʼiltaj cumal i winak. Kus quiquil u cʼamic bi jupaj u tzij u pixab i Dios pasim chupam jun ral tzʼum chuwach qui palaj. Xak kus quiquil u batzʼcatixic tzʼum rij tak qui ʼab. I tzʼum-le xak tzʼibtal jutakpaj u tzij i Dios chuwach; kus quiquil ique we nim u wach. Xak i u chiʼ i qui ʼuʼ, lic nimak i rismachiʼ ʼo che, mas nimak chuwach i que i toʼ winak. Queje quiqui ʼan ique ile, quiquilbej na re i winak chi lic je re i Dios, quiqui bij ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.006', 'Are que ʼe pa tak nimaʼij, xa quiqui tzucuj i chomik laj tak qʼuijibal. Are que ʼe pa molbalʼib, xak quiqui tzucuj i qui cubibal chi ʼo u ʼij. ', '', '&lt;span class="verse" id="Matt_23_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are que ʼe pa tak nimaʼij, xa quiqui tzucuj i chomik laj tak qʼuijibal. Are que ʼe pa molbalʼib, xak quiqui tzucuj i qui cubibal chi ʼo u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.007', 'Are que ʼe pa qʼuebal, lic kus quiqui ta are que tioxix cumal i winak, ocsabal re qui ij. Xak kus quiqui ta are ca bix “nim laj tijonel” chique.
', '', '&lt;span class="verse" id="Matt_23_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are que ʼe pa qʼuebal, lic kus quiqui ta are que tioxix cumal i winak, ocsabal re qui ij. Xak kus quiqui ta are ca bix “nim laj tijonel” chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.008', 'Mi ʼan yix pacha ique-le; mi ya chique i winak chi quiqui bij “nim laj tijonel” chiwe. Man iwonojel yix, iwatz i chaʼ iwib; xa jun i Tijonel yix, are i Crísto. ', '', '&lt;span class="verse" id="Matt_23_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Mi ʼan yix pacha ique-le; mi ya chique i winak chi quiqui bij “nim laj tijonel” chiwe. Man iwonojel yix, iwatz i chaʼ iwib; xa jun i Tijonel yix, are i Crísto. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.009', 'Xak mi bij i kajaw che jun winak waral chuwach i jyub taʼaj, cojbal re u ʼij, man xa jun i Kajaw ʼolic, are i Ta chicaj. ', '', '&lt;span class="verse" id="Matt_23_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak mi bij i kajaw che jun winak waral chuwach i jyub taʼaj, cojbal re u ʼij, man xa jun i Kajaw ʼolic, are i Ta chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.010', 'Xak mi ya chique i winak quiqui bij “kajʼatzil” chiwe, man xui i Crísto are Iwajʼatzil. ', '', '&lt;span class="verse" id="Matt_23_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xak mi ya chique i winak quiqui bij “kajʼatzil” chiwe, man xui i Crísto are Iwajʼatzil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.011', 'Pachin coʼon mocom che rib chiquij i juban, are i mer nim ʼuri chixol. ', '', '&lt;span class="verse" id="Matt_23_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Pachin coʼon mocom che rib chiquij i juban, are i mer nim ʼuri chixol. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.012', 'Man pachin coʼon nim che rib, ire ʼut xa ca kajsax u ʼij; xak pachin coʼon chʼutin che rib, ire ca nimarsax na ʼuri u ʼij.
', '', '&lt;span class="verse" id="Matt_23_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Man pachin coʼon nim che rib, ire ʼut xa ca kajsax u ʼij; xak pachin coʼon chʼutin che rib, ire ca nimarsax na ʼuri u ʼij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.013', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! N-qui cʼut ti sak laj tzij chique i winak; xa qui tzʼapij i uchija re i ʼatbal tzij aj chicaj are ʼo i cacaj que oc bic. Yix n-quiwaj ta quix oc bic, xak n-qui ya ta chique ique que oc bic.
', '', '&lt;span class="verse" id="Matt_23_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! N-qui cʼut ti sak laj tzij chique i winak; xa qui tzʼapij i uchija re i ʼatbal tzij aj chicaj are ʼo i cacaj que oc bic. Yix n-quiwaj ta quix oc bic, xak n-qui ya ta chique ique que oc bic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.014', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Xa qui maj i cachoch ixokib malcanib, teʼuri qui ʼan nimak tak i tzʼonbal che i Dios, xa sachsabal-re chiquiwach i winak, wach i ʼanom. Queje ile i nimarsam ni tojpen chi ca pe na piwi.
', '', '&lt;span class="verse" id="Matt_23_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Xa qui maj i cachoch ixokib malcanib, teʼuri qui ʼan nimak tak i tzʼonbal che i Dios, xa sachsabal-re chiquiwach i winak, wach i ʼanom. Queje ile i nimarsam ni tojpen chi ca pe na piwi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.015', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Lic quix ʼe jela, quix ʼe jewa, che i cʼutunic qui ʼano, chʼacbal na re jun i tijoxel. Xui-ri, we xix tiqui che, queʼel ire ʼuri lic mas mal chiwach yix; ca ʼe pa ʼaʼ nabe chiwach yix, xa rumal i cʼutum che.
', '', '&lt;span class="verse" id="Matt_23_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Lic quix ʼe jela, quix ʼe jewa, che i cʼutunic qui ʼano, chʼacbal na re jun i tijoxel. Xui-ri, we xix tiqui che, queʼel ire ʼuri lic mas mal chiwach yix; ca ʼe pa ʼaʼ nabe chiwach yix, xa rumal i cʼutum che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.016', 'Toʼ i wach yix chi ix aj cʼambeal chi xa ix mawach. Qui bij yix chi “We jun winak cu cuxtaj u bi i rachoch i Dios puwi i u tzij, n-chukul ta ʼo wi ʼuri u ʼonquil pacha u bim,” qui bij yix. “Péro we cu cuxtaj u bi i óro re i rachoch i Dios, ʼo chukul ʼuri coʼon na pacha i u bim.” Queje qui bij yix ile. ', '', '&lt;span class="verse" id="Matt_23_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix chi ix aj cʼambeal chi xa ix mawach. Qui bij yix chi “We jun winak cu cuxtaj u bi i rachoch i Dios puwi i u tzij, n-chukul ta ʼo wi ʼuri u ʼonquil pacha u bim,” qui bij yix. “Péro we cu cuxtaj u bi i óro re i rachoch i Dios, ʼo chukul ʼuri coʼon na pacha i u bim.” Queje qui bij yix ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.017', '¡Lic ix sachnak na; lic ix mawach! ¿Pachique mas ʼo i u ʼij? ¿Xataba are i óro? N-are taj. Are i rachoch i Dios chi mas ʼo u ʼij, y coʼon che i óro chi xak ʼo u ʼij. ', '', '&lt;span class="verse" id="Matt_23_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;¡Lic ix sachnak na; lic ix mawach! ¿Pachique mas ʼo i u ʼij? ¿Xataba are i óro? N-are taj. Are i rachoch i Dios chi mas ʼo u ʼij, y coʼon che i óro chi xak ʼo u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.018', 'Xak qui bij yix chi “We jun winak cu cuxtaj u bi i altar puwi i u tzij, n-chukul ta ʼo wi ʼuri i u ʼonquil pacha u bim,” qui bij yix. “Péro we cu cuxtaj u bi i sipon chi ʼo puwi i altar, ʼo chukul ʼuri coʼon na pacha i u bim.” Queje qui bij yix ile. ', '', '&lt;span class="verse" id="Matt_23_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xak qui bij yix chi “We jun winak cu cuxtaj u bi i altar puwi i u tzij, n-chukul ta ʼo wi ʼuri i u ʼonquil pacha u bim,” qui bij yix. “Péro we cu cuxtaj u bi i sipon chi ʼo puwi i altar, ʼo chukul ʼuri coʼon na pacha i u bim.” Queje qui bij yix ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.019', '¡Lic ix sachnak na; lic ix mawach! ¿Pachique mas ʼo i u ʼij? ¿Xataba are i sipon? N-are taj. Are i altar chi mas ʼo u ʼij, y coʼon che i sipon chi xak ʼo u ʼij. ', '', '&lt;span class="verse" id="Matt_23_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;¡Lic ix sachnak na; lic ix mawach! ¿Pachique mas ʼo i u ʼij? ¿Xataba are i sipon? N-are taj. Are i altar chi mas ʼo u ʼij, y coʼon che i sipon chi xak ʼo u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.020', 'We ʼo jun cu cuxtaj u bi i altar, xeʼel ʼuri xak xu cuxtaj u bi wach tak i ʼo puwi i altar. ', '', '&lt;span class="verse" id="Matt_23_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;We ʼo jun cu cuxtaj u bi i altar, xeʼel ʼuri xak xu cuxtaj u bi wach tak i ʼo puwi i altar. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.021', 'Xak we jun cu cuxtaj u bi i rachoch i Dios, xeʼel ʼuri xak xu cuxtaj u bi i Dios chi ʼo chila. ', '', '&lt;span class="verse" id="Matt_23_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xak we jun cu cuxtaj u bi i rachoch i Dios, xeʼel ʼuri xak xu cuxtaj u bi i Dios chi ʼo chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.022', 'Xak we jun cu cuxtaj u bi i caj, xeʼel ʼuri xu cuxtaj i u cubibal i Dios, pa cu ʼat wi tzij, xak xu cuxtaj i Dios chi cul chila.
', '', '&lt;span class="verse" id="Matt_23_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xak we jun cu cuxtaj u bi i caj, xeʼel ʼuri xu cuxtaj i u cubibal i Dios, pa cu ʼat wi tzij, xak xu cuxtaj i Dios chi cul chila.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.023', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! (Qui ʼan pen i cojonic che i ral tzij re i ʼatbal tzij chi n-ta kas ropis.) Niʼpa i ʼo iwuʼ chi n-ta kas u chac, pacha i ral tak arawen, i anix, tak i comin, ile ʼut qui ʼan pen i relsaxic jun tant che i lajuj, y qui ya na che i Dios. Xui-ri, n-qui ʼan ti pen i cojonic che i tzij re i ʼatbal tzij chi lic ʼo u chac qui ʼan na. N-qui ʼan ta pen u ʼanic i sucʼulic cuʼ i winak; n-qui ʼan ta pen rilic tobal qui wach; n-kas ta quix terej chirij i Dios. Are i niʼpa iri, are ʼo u chac qui ʼan na; xak ʼo u chac qui jach i nabe tant che i Dios che i qui chʼaco. ', '', '&lt;span class="verse" id="Matt_23_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! (Qui ʼan pen i cojonic che i ral tzij re i ʼatbal tzij chi n-ta kas ropis.) Niʼpa i ʼo iwuʼ chi n-ta kas u chac, pacha i ral tak arawen, i anix, tak i comin, ile ʼut qui ʼan pen i relsaxic jun tant che i lajuj, y qui ya na che i Dios. Xui-ri, n-qui ʼan ti pen i cojonic che i tzij re i ʼatbal tzij chi lic ʼo u chac qui ʼan na. N-qui ʼan ta pen u ʼanic i sucʼulic cuʼ i winak; n-qui ʼan ta pen rilic tobal qui wach; n-kas ta quix terej chirij i Dios. Are i niʼpa iri, are ʼo u chac qui ʼan na; xak ʼo u chac qui jach i nabe tant che i Dios che i qui chʼaco. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.024', '¡Yix xa ix aj cʼambeal lic ix mawach! ¡We ʼo jun ral ʼus chupam i yaʼ re i qui tijo, quiwesaj la ʼut; xui-ri we ʼo jun camey chupam, xa qui tuʼ la bic!
', '', '&lt;span class="verse" id="Matt_23_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;¡Yix xa ix aj cʼambeal lic ix mawach! ¡We ʼo jun ral ʼus chupam i yaʼ re i qui tijo, quiwesaj la ʼut; xui-ri we ʼo jun camey chupam, xa qui tuʼ la bic!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.025', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Lic qui ʼan pen u chʼajic i bas, tak i lak, xui-ri n-qui chʼaj ti i pi cʼux yix. I ubitak iwe, toʼ iwelʼam na, xa quix tijin chu molic u chiʼ, xui ile qui chʼobo. Queʼelok pacha xui i rij i bas, xui i rij i lak i chʼajom na, péro i u pam lic tzʼil. ', '', '&lt;span class="verse" id="Matt_23_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Lic qui ʼan pen u chʼajic i bas, tak i lak, xui-ri n-qui chʼaj ti i pi cʼux yix. I ubitak iwe, toʼ iwelʼam na, xa quix tijin chu molic u chiʼ, xui ile qui chʼobo. Queʼelok pacha xui i rij i bas, xui i rij i lak i chʼajom na, péro i u pam lic tzʼil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.026', 'Ix mawach laj tak Fariséo, nabe chi chʼaja i u pam i bas, i u pam i lak, man queʼel ʼuri chom na ronojel.
', '', '&lt;span class="verse" id="Matt_23_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Ix mawach laj tak Fariséo, nabe chi chʼaja i u pam i bas, i u pam i lak, man queʼel ʼuri chom na ronojel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.027', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Ix queje pacha jun pantion, lic chom, sak i rij, péro i u pam n-chʼam taj, xa ʼo can u bakil camnak chupam, xa ʼo ca ʼay chupam. ', '', '&lt;span class="verse" id="Matt_23_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Ix queje pacha jun pantion, lic chom, sak i rij, péro i u pam n-chʼam taj, xa ʼo can u bakil camnak chupam, xa ʼo ca ʼay chupam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.028', 'Yix ix queje ile; lic ʼutz quix ʼanaw chiquiwach i winak xui-ri i pi cʼux lic n-chʼam taj, xa ix nojnak che i mal; xa queb i palaj i ʼanom che iwib.
', '', '&lt;span class="verse" id="Matt_23_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Yix ix queje ile; lic ʼutz quix ʼanaw chiquiwach i winak xui-ri i pi cʼux lic n-chʼam taj, xa ix nojnak che i mal; xa queb i palaj i ʼanom che iwib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.029', 'Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Qui ʼan pen u yijbaxic qui pantion i ujer ajbil u tzij i Dios, cojbal re qui ʼij. Qui ʼan pen u wikic qui mukbal i je re i Dios ujer. ', '', '&lt;span class="verse" id="Matt_23_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Toʼ i wach yix, ix tijonel re i ʼatbal tzij, xak yix chi ix aj Fariséo. ¡Xa queb i palaj! Qui ʼan pen u yijbaxic qui pantion i ujer ajbil u tzij i Dios, cojbal re qui ʼij. Qui ʼan pen u wikic qui mukbal i je re i Dios ujer. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.030', 'Teʼuri qui bij ni yix, “We tene yoj oj ʼo che i ʼij-le que i katit kamam, yoj ʼut n-queje tene que ka camsaj-i i ajbil u tzij i Dios,” quix cha. ', '', '&lt;span class="verse" id="Matt_23_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Teʼuri qui bij ni yix, “We tene yoj oj ʼo che i ʼij-le que i katit kamam, yoj ʼut n-queje tene que ka camsaj-i i ajbil u tzij i Dios,” quix cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.031', 'Xui-ri, ruʼ i n-ʼus taj qui ʼano, i ʼalijinsam chi lic ix xak jun cuʼ ique. Lic ix qui muk qui xiquin can ique chi xe qui camsaj i je ajbil u tzij i Dios ujer; xa ix qui cwenta ique. ', '', '&lt;span class="verse" id="Matt_23_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xui-ri, ruʼ i n-ʼus taj qui ʼano, i ʼalijinsam chi lic ix xak jun cuʼ ique. Lic ix qui muk qui xiquin can ique chi xe qui camsaj i je ajbil u tzij i Dios ujer; xa ix qui cwenta ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.032', 'Chi qʼuisa u ʼanic ʼuri wach i xqui jek u ʼanic iwatit i mam.', '', '&lt;span class="verse" id="Matt_23_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Chi qʼuisa u ʼanic ʼuri wach i xqui jek u ʼanic iwatit i mam.&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.033', '¡Yix xa ix cumatz! ¡Xa ix racʼal cumatz! N-quix tiqui ta chu jeqʼuic iwib chuwach i tojpen pa ʼaʼ. ', '', '&lt;span class="verse" id="Matt_23_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;¡Yix xa ix cumatz! ¡Xa ix racʼal cumatz! N-quix tiqui ta chu jeqʼuic iwib chuwach i tojpen pa ʼaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.034', 'Que in tak nubi je ajbil u tzij i Dios chiwe, que in tak bi ʼo qui noʼoj, xak i je tijonel que in tak bi iwuʼ. Yix qui camsaj ni jujun chique, xak ʼo jujun que i camsaj chuwach cruz, xak ʼo jujun chic qui ʼan ni cʼax chique pa tak molbalʼib, xak ʼo jujun chic que iwoktaj, n-quix uxlan ta chi coktaxic pa tak tinimit. ', '', '&lt;span class="verse" id="Matt_23_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Que in tak nubi je ajbil u tzij i Dios chiwe, que in tak bi ʼo qui noʼoj, xak i je tijonel que in tak bi iwuʼ. Yix qui camsaj ni jujun chique, xak ʼo jujun que i camsaj chuwach cruz, xak ʼo jujun chic qui ʼan ni cʼax chique pa tak molbalʼib, xak ʼo jujun chic que iwoktaj, n-quix uxlan ta chi coktaxic pa tak tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.035', 'I mal laj tak ʼanic chi xqui ʼan iwatit i mam ujer, xak are quix tijin yix chu ʼanic woʼor, xak ca pe ni tojpen piwi yix rumal. Lic ix xak jun cuʼ ique chi xe qui camsaj i winak je ʼutz. Are u jekanic, are u camsaxic i a-Abel chi n-tu mac, y queje ile petnak cʼa are ʼo wi chi xi camsaj i mam Zacarías pa rachoch i Dios. ', '', '&lt;span class="verse" id="Matt_23_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I mal laj tak ʼanic chi xqui ʼan iwatit i mam ujer, xak are quix tijin yix chu ʼanic woʼor, xak ca pe ni tojpen piwi yix rumal. Lic ix xak jun cuʼ ique chi xe qui camsaj i winak je ʼutz. Are u jekanic, are u camsaxic i a-Abel chi n-tu mac, y queje ile petnak cʼa are ʼo wi chi xi camsaj i mam Zacarías pa rachoch i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.036', 'Katzij i quin bij chiwe, niʼpa i tojpen re iri ca pe na piquiwi i winak aj woʼor.
', '', '&lt;span class="verse" id="Matt_23_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, niʼpa i tojpen re iri ca pe na piquiwi i winak aj woʼor.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.037', '¡Ay, yix, ix aj Jerusalen! ¡Yix chi que i camsaj i ajbil u tzij i Dios; yix chi toʼ qui coj abaj chique i je u takon i Dios! ¡Qʼuialmul lic in rayim quix in mol wuʼ, pacha coʼon jun ati acʼ cuʼ i racʼal, que u ya chuxeʼ u xicʼ! ¡Queje ile xwaj te yin, y yix n-xiwaj taj! ', '', '&lt;h2&gt;Xoʼ i Jesus cumal i aj Jerusalen man n-xcaj ti ʼutz&lt;/h2&gt;&lt;span class="verse" id="Matt_23_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;¡Ay, yix, ix aj Jerusalen! ¡Yix chi que i camsaj i ajbil u tzij i Dios; yix chi toʼ qui coj abaj chique i je u takon i Dios! ¡Qʼuialmul lic in rayim quix in mol wuʼ, pacha coʼon jun ati acʼ cuʼ i racʼal, que u ya chuxeʼ u xicʼ! ¡Queje ile xwaj te yin, y yix n-xiwaj taj! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.038', 'Chitapeʼ, i tinimit yix coʼtax na can rumal i Dios. ', '', '&lt;span class="verse" id="Matt_23_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Chitapeʼ, i tinimit yix coʼtax na can rumal i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '23.039', 'Quin bij chiwe, queje ile quix eʼel na; n-quiwil ta chic in wach cʼa cu rik ni ʼij chi quin i cʼulaj na iwuʼ. Qui bij na: “¡Chu ya ni ʼutz i Kajwal Dios puwi i Jun chi u takom lok!” quix cha. (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Matt_23_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Quin bij chiwe, queje ile quix eʼel na; n-quiwil ta chic in wach cʼa cu rik ni ʼij chi quin i cʼulaj na iwuʼ. Qui bij na: “¡Chu ya ni ʼutz i Kajwal Dios puwi i Jun chi u takom lok!” quix cha. (Queje xu bij i Jesus ile chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.001', 'I Jesus xel pa rachoch i Dios; ya cu maj u be. Are ʼuri xe tejeb i u tijoxelab ruʼ, y xe oc chu cʼutic niʼpa i ja re i rachoch i Dios. ', '', '&lt;h2&gt;Wach i retamaxic caka ʼano are ya ca cʼun i qʼuisbal ʼij&lt;/h2&gt;&lt;span class="verse" id="Matt_24_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus xel pa rachoch i Dios; ya cu maj u be. Are ʼuri xe tejeb i u tijoxelab ruʼ, y xe oc chu cʼutic niʼpa i ja re i rachoch i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.002', 'I Jesus xu bij chique:
—¿Y yix cʼa qui takej niʼpa ile? Katzij i quin bij chiwe: ronojel iri ca wulijsax na. Mi jun abaj ca canaj chic puwi i rachiʼil —xu bij chique.
', '', '&lt;span class="verse" id="Matt_24_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Y yix cʼa qui takej niʼpa ile? Katzij i quin bij chiwe: ronojel iri ca wulijsax na. Mi jun abaj ca canaj chic puwi i rachiʼil —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.003', 'Teʼuri xe ʼe chuwi jyub Olívos i Jesus xcubi chila. Are ʼuri i u tijoxelab xe tejeb pan ruʼ. Xe oc chu tzʼonoxic che, aliʼal:
—Cakaj caketamaj umpa ca ʼani ile chi xa bij. ¿Wach cakilo are ya cat cʼunic? ¿Wach cakilo are ya ca cʼun i qʼuisbal re tak i ʼij-i? —xe cha che.
', '', '&lt;span class="verse" id="Matt_24_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Teʼuri xe ʼe chuwi jyub Olívos i Jesus xcubi chila. Are ʼuri i u tijoxelab xe tejeb pan ruʼ. Xe oc chu tzʼonoxic che, aliʼal:&lt;p&gt; &lt;/p&gt;—Cakaj caketamaj umpa ca ʼani ile chi xa bij. ¿Wach cakilo are ya cat cʼunic? ¿Wach cakilo are ya ca cʼun i qʼuisbal re tak i ʼij-i? —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.004', 'I Jesus xu bij:
—Chi ʼana cwent iwib, mokxa ʼo quix qui tʼoro. ', '', '&lt;span class="verse" id="Matt_24_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Chi ʼana cwent iwib, mokxa ʼo quix qui tʼoro. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.005', 'Man je qʼui que cʼun na, y quiqui ʼan che quib pacha yin. “Yin in i Toliwe,” que cha; cumal ique-le, je qʼui que tʼortaj na. ', '', '&lt;span class="verse" id="Matt_24_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Man je qʼui que cʼun na, y quiqui ʼan che quib pacha yin. “Yin in i Toliwe,” que cha; cumal ique-le, je qʼui que tʼortaj na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.006', 'Yix xak qui ta ni rason chi ʼo nimak tak chʼoʼoj jela, xak nimak tak chʼoʼoj jewa. Mix cʼachir rumal, man ʼo ni u chac queje ile coʼono; xui-ri, are qui ta i tzij-le, cʼa maja ca cʼun ni qʼuisbal re tak i ʼij-i. ', '', '&lt;span class="verse" id="Matt_24_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Yix xak qui ta ni rason chi ʼo nimak tak chʼoʼoj jela, xak nimak tak chʼoʼoj jewa. Mix cʼachir rumal, man ʼo ni u chac queje ile coʼono; xui-ri, are qui ta i tzij-le, cʼa maja ca cʼun ni qʼuisbal re tak i ʼij-i. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.007', 'ʼO ni tinimit quiqui ʼan chʼoʼoj che jun chic. I winak re jun ʼatol tzij xak quiqui ʼan chʼoʼoj che jun chic nim laj ʼatol tzij. Ca pe waʼjal, ca pe yobil, ca pe i ucabrakan pa tak tinimit. ', '', '&lt;span class="verse" id="Matt_24_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;ʼO ni tinimit quiqui ʼan chʼoʼoj che jun chic. I winak re jun ʼatol tzij xak quiqui ʼan chʼoʼoj che jun chic nim laj ʼatol tzij. Ca pe waʼjal, ca pe yobil, ca pe i ucabrakan pa tak tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.008', 'Lic ca cʼun na ile, xui-ri xa are i jekbal re i cʼax chi ca petic.
', '', '&lt;span class="verse" id="Matt_24_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Lic ca cʼun na ile, xui-ri xa are i jekbal re i cʼax chi ca petic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.009', 'Yix quix chaptaj na, quix yaʼ na pa tojpen, y quix camsax na. Conojel i winak tzel na quix quilo xa wumal yin. ', '', '&lt;span class="verse" id="Matt_24_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Yix quix chaptaj na, quix yaʼ na pa tojpen, y quix camsax na. Conojel i winak tzel na quix quilo xa wumal yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.010', 'Che i ʼij-le, je qʼui na quiqui sach u cubibal qui cʼux che i Dios; xak je qʼui na, tzel na quiquil quib; xak ʼo ni quiqui jach i cachiʼil piquiʼab i tzel que ʼilawic. ', '', '&lt;span class="verse" id="Matt_24_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Che i ʼij-le, je qʼui na quiqui sach u cubibal qui cʼux che i Dios; xak je qʼui na, tzel na quiquil quib; xak ʼo ni quiqui jach i cachiʼil piquiʼab i tzel que ʼilawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.011', 'Xak je qʼui ni aj tʼoronel que cʼun na, quiqui bij chi je ajbil u tzij i Dios; je qʼui ni winak que qui tʼor na. ', '', '&lt;span class="verse" id="Matt_24_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xak je qʼui ni aj tʼoronel que cʼun na, quiqui bij chi je ajbil u tzij i Dios; je qʼui ni winak que qui tʼor na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.012', 'Niʼpa i ca pe ruʼ itzel lic ca qʼuiar na, je qʼui na ʼuri i winak quiqui sach i cʼaxnabal qui cʼux chiquiwach. ', '', '&lt;span class="verse" id="Matt_24_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Niʼpa i ca pe ruʼ itzel lic ca qʼuiar na, je qʼui na ʼuri i winak quiqui sach i cʼaxnabal qui cʼux chiquiwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.013', 'Xui-ri, pachin i quiqui chʼijo cʼa pa qʼuisbal-re, que ʼel na ʼuri pa cʼax. ', '', '&lt;span class="verse" id="Matt_24_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xui-ri, pachin i quiqui chʼijo cʼa pa qʼuisbal-re, que ʼel na ʼuri pa cʼax. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.014', 'Xak i ʼutz laj tzij re i ʼatbal tzij re i Dios ca paxsax na, ronojel u wach i jyub taʼaj, man caquetamaj na conojel i tinimit; teʼuri ca pe i qʼuisbal re u wach i jyub taʼaj.
', '', '&lt;span class="verse" id="Matt_24_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xak i ʼutz laj tzij re i ʼatbal tzij re i Dios ca paxsax na, ronojel u wach i jyub taʼaj, man caquetamaj na conojel i tinimit; teʼuri ca pe i qʼuisbal re u wach i jyub taʼaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.015', 'I ajbil u tzij i Dios, mam Daniel, u tzʼibam canok wach ca cʼunic; xu bij ire chi cocsax na jun tzel laj kajsabal re u ʼij i rachoch i Dios. Are quiwil yix ile tacʼal chila che i lugar ʼo u ʼij pa rachoch i Dios, niʼpa i je ʼo pa jyub Judéa, che ʼanmaj ʼuri chuwa tak jyub. Pachin crajlaj i tzij-i, chu ta usucʼ. ', '', '&lt;span class="verse" id="Matt_24_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I ajbil u tzij i Dios, mam Daniel, u tzʼibam canok wach ca cʼunic; xu bij ire chi cocsax na jun tzel laj kajsabal re u ʼij i rachoch i Dios. Are quiwil yix ile tacʼal chila che i lugar ʼo u ʼij pa rachoch i Dios, niʼpa i je ʼo pa jyub Judéa, che ʼanmaj ʼuri chuwa tak jyub. Pachin crajlaj i tzij-i, chu ta usucʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.017', 'Niʼpa je ʼo ʼan puwija re i cachoch, suʼ che kaj la lok; me oc chic chupam, quiqui cʼambej re ubitak-que. ', '', '&lt;span class="verse" id="Matt_24_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Niʼpa je ʼo ʼan puwija re i cachoch, suʼ che kaj la lok; me oc chic chupam, quiqui cʼambej re ubitak-que. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.018', 'Xak niʼpa i je ʼo pa tak ticon, me tzalij lok que qui cʼambej re qui ʼuʼ. ¡Paʼanem che ʼanmajok! ', '', '&lt;span class="verse" id="Matt_24_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xak niʼpa i je ʼo pa tak ticon, me tzalij lok que qui cʼambej re qui ʼuʼ. ¡Paʼanem che ʼanmajok! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.019', 'Chupam i ʼij-le, toʼ qui wach ixokib chi ʼo i yobil chiquij, xak ique chi ʼo cacʼal que tzʼumanic, cʼax i qui ʼanmajic ique. ', '', '&lt;span class="verse" id="Matt_24_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Chupam i ʼij-le, toʼ qui wach ixokib chi ʼo i yobil chiquij, xak ique chi ʼo cacʼal que tzʼumanic, cʼax i qui ʼanmajic ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.020', 'Chi tzʼonoj che i Dios chi n-joron ti tew are quix ʼanmaj chuwach ile, xak n-are ta chupam i uxlambal ʼij. ', '', '&lt;span class="verse" id="Matt_24_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Chi tzʼonoj che i Dios chi n-joron ti tew are quix ʼanmaj chuwach ile, xak n-are ta chupam i uxlambal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.021', 'Man i uyej che tak i ʼij-le, lic cʼax. Cʼa chu xebal ʼij sak, n-ta jun cʼax petnak lic cʼax chuwach ile; xak n-ta chic jun chic ca petic, lic cʼax chuwach ile. ', '', '&lt;span class="verse" id="Matt_24_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Man i uyej che tak i ʼij-le, lic cʼax. Cʼa chu xebal ʼij sak, n-ta jun cʼax petnak lic cʼax chuwach ile; xak n-ta chic jun chic ca petic, lic cʼax chuwach ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.022', 'Y we i Dios n-coʼon ta che, chi xa ʼatal ʼij ʼo ni cʼax-le, ʼis que cam na conojel. Xui-ri, ire cu ʼatij na; queje coʼon ire ile rumal i cʼaxnabal u cʼux chique i je re ire.
', '', '&lt;span class="verse" id="Matt_24_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Y we i Dios n-coʼon ta che, chi xa ʼatal ʼij ʼo ni cʼax-le, ʼis que cam na conojel. Xui-ri, ire cu ʼatij na; queje coʼon ire ile rumal i cʼaxnabal u cʼux chique i je re ire.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.023', 'Rumal-i, che tak i ʼij-le, we ʼo i que bin chiwe: “Chiwilapeʼ, chi ʼo wi i Tolke,” que cha, o we quiqui bij: “Le i ʼolic-le,” que cha, mi cojo. ', '', '&lt;span class="verse" id="Matt_24_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Rumal-i, che tak i ʼij-le, we ʼo i que bin chiwe: “Chiwilapeʼ, chi ʼo wi i Tolke,” que cha, o we quiqui bij: “Le i ʼolic-le,” que cha, mi cojo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.024', 'Man que cʼun ni aj tʼoronel chi quiqui ʼan che quib pacha je toliwe, xak que cʼun jujun quiqui ʼan che quib pacha je ajbil u tzij i Dios. Quiqui ʼan ni milágro, xak nimak tak ʼanic, ʼilbal-re we que tiqui chi qui tʼoric niʼpa i je re i Dios. ', '', '&lt;span class="verse" id="Matt_24_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Man que cʼun ni aj tʼoronel chi quiqui ʼan che quib pacha je toliwe, xak que cʼun jujun quiqui ʼan che quib pacha je ajbil u tzij i Dios. Quiqui ʼan ni milágro, xak nimak tak ʼanic, ʼilbal-re we que tiqui chi qui tʼoric niʼpa i je re i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.025', 'Ya in bim chic iri chiwe wach i qui cʼulmaj coʼon panok. ', '', '&lt;span class="verse" id="Matt_24_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Ya in bim chic iri chiwe wach i qui cʼulmaj coʼon panok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.026', 'Rumal-i, we ca bix chiwe: “Chiwilapeʼ chila ʼo wi i Tolke pa taʼaj tzʼinilic,” que cha, mix ʼe chila; xak we ca bix chiwe: “Chiwilapeʼ chi ʼo wi paja,” quiqui bij chiwe, mi cojo. ', '', '&lt;span class="verse" id="Matt_24_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Rumal-i, we ca bix chiwe: “Chiwilapeʼ chila ʼo wi i Tolke pa taʼaj tzʼinilic,” que cha, mix ʼe chila; xak we ca bix chiwe: “Chiwilapeʼ chi ʼo wi paja,” quiqui bij chiwe, mi cojo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.027', 'Mi cojo, man i Achi aj Chicaj n-aliʼal taj ca cʼunic. I u cʼunic ire coʼono pacha coʼon jun nim laj u rep ʼaʼ ca repcanic, chi coʼon sak ronojel u wach i caj. ', '', '&lt;span class="verse" id="Matt_24_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Mi cojo, man i Achi aj Chicaj n-aliʼal taj ca cʼunic. I u cʼunic ire coʼono pacha coʼon jun nim laj u rep ʼaʼ ca repcanic, chi coʼon sak ronojel u wach i caj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.028', '(I tojpen lic ca cʼun-na.) Pa ʼo wi jun chʼaculaj camnak, chila que moltaj wi i cʼuch.
', '', '&lt;span class="verse" id="Matt_24_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;(I tojpen lic ca cʼun-na.) Pa ʼo wi jun chʼaculaj camnak, chila que moltaj wi i cʼuch.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.029', 'Are quicʼaw niʼpa i cʼax re i ʼij-le, are ʼuri i ʼij ca ʼekumaric; i icʼ n-ca tzun ta chic; i chʼumil que tzak na li chicaj; xak wach tak ʼo chicaj que sicsax na. ', '', '&lt;h2&gt;Wach i u cʼunic i Achi aj Chicaj&lt;/h2&gt;&lt;span class="verse" id="Matt_24_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are quicʼaw niʼpa i cʼax re i ʼij-le, are ʼuri i ʼij ca ʼekumaric; i icʼ n-ca tzun ta chic; i chʼumil que tzak na li chicaj; xak wach tak ʼo chicaj que sicsax na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.030', 'Are ʼuri ca ʼiltaj jun séña chicaj, re in cʼunbal yin chi in Achi aj Chicaj. Y conojel i tinimit chuwach i jyub taʼaj que oʼ na. Y quin quil na chi quin cʼun pa sutzʼ, nim na in choʼab, in chomal. ', '', '&lt;span class="verse" id="Matt_24_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Are ʼuri ca ʼiltaj jun séña chicaj, re in cʼunbal yin chi in Achi aj Chicaj. Y conojel i tinimit chuwach i jyub taʼaj que oʼ na. Y quin quil na chi quin cʼun pa sutzʼ, nim na in choʼab, in chomal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.031', 'Ca tataj jun nim laj trompet, y que in tak bi in ángel cʼa pa que ʼel wi li i cajib tew cʼulala; que in tak li jewa, xak que in tak bi jela, quiqui molbej-que niʼpa i je in chom, ronojel u wach i jyub taʼaj.
', '', '&lt;span class="verse" id="Matt_24_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Ca tataj jun nim laj trompet, y que in tak bi in ángel cʼa pa que ʼel wi li i cajib tew cʼulala; que in tak li jewa, xak que in tak bi jela, quiqui molbej-que niʼpa i je in chom, ronojel u wach i jyub taʼaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.032', 'Yix iwetaʼam wach i séña quiwil che u cheʼlal hígo are ya ca cʼun i ʼalaj; chix naw che i cheʼ-le. Ile, are quiwilo chi ca pe tan chic u tux, y coʼon rax, iwetaʼam ʼuri ya ca miʼ tan chic i ulew. ', '', '&lt;span class="verse" id="Matt_24_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Yix iwetaʼam wach i séña quiwil che u cheʼlal hígo are ya ca cʼun i ʼalaj; chix naw che i cheʼ-le. Ile, are quiwilo chi ca pe tan chic u tux, y coʼon rax, iwetaʼam ʼuri ya ca miʼ tan chic i ulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.033', 'Xak queje quiwetamaj are ya ca cʼun i qʼuisbal re tak i ʼij-i. Are quiwil yix pacha tak in bim chiwe, chi ta u be ʼuri ya ca pe i ʼij in bim chiwe; ya jekanic cu ʼano. ', '', '&lt;span class="verse" id="Matt_24_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xak queje quiwetamaj are ya ca cʼun i qʼuisbal re tak i ʼij-i. Are quiwil yix pacha tak in bim chiwe, chi ta u be ʼuri ya ca pe i ʼij in bim chiwe; ya jekanic cu ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.034', 'Katzij i quin bij chiwe, ya ca cʼun ronojel niʼpa in bim-le; i winak je re woʼor quiquil na. ', '', '&lt;span class="verse" id="Matt_24_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, ya ca cʼun ronojel niʼpa in bim-le; i winak je re woʼor quiquil na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.035', 'I caj, i ulew ca sachic, xui-ri in tzij yin n-ca sach taj, ca ʼantaj na ronojel niʼpa in bim chiwe.
', '', '&lt;span class="verse" id="Matt_24_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I caj, i ulew ca sachic, xui-ri in tzij yin n-ca sach taj, ca ʼantaj na ronojel niʼpa in bim chiwe.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.036', 'Mi jun retaʼam pachique ʼij, pachique ʼor, coʼon ile. Xak mi ne i ángel aj chicaj, xak mi ne u Cʼojol i Dios n-retaʼam taj; xui i Ta chicaj retaʼam.
', '', '&lt;span class="verse" id="Matt_24_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Mi jun retaʼam pachique ʼij, pachique ʼor, coʼon ile. Xak mi ne i ángel aj chicaj, xak mi ne u Cʼojol i Dios n-retaʼam taj; xui i Ta chicaj retaʼam.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.037', 'I winak n-quiqui yijba ta quib che i ʼij-le are ca cʼun i Achi aj Chicaj. Xa quiqui ʼan na pacha xqui ʼan i winak re tak i ʼij re i mam Noe ujer. ', '', '&lt;span class="verse" id="Matt_24_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;I winak n-quiqui yijba ta quib che i ʼij-le are ca cʼun i Achi aj Chicaj. Xa quiqui ʼan na pacha xqui ʼan i winak re tak i ʼij re i mam Noe ujer. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.038', 'Ique ujer, are maja ca pe i nim laj jab chi xu nojsaj u wach i jyub taʼaj che i ya, n-xqui yijba ta quib. Toʼ xqui tzuk quib, toʼ xe tuʼunic, toʼ xqui bisoj i qui cʼulanic; queje xqui ʼan ile tzʼakat xcʼun i ʼij chi xoc i mam Noe pa nim laj bárco. ', '', '&lt;span class="verse" id="Matt_24_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Ique ujer, are maja ca pe i nim laj jab chi xu nojsaj u wach i jyub taʼaj che i ya, n-xqui yijba ta quib. Toʼ xqui tzuk quib, toʼ xe tuʼunic, toʼ xqui bisoj i qui cʼulanic; queje xqui ʼan ile tzʼakat xcʼun i ʼij chi xoc i mam Noe pa nim laj bárco. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.039', 'Toʼ queje ile xqui ʼano, y n-xqui na taj, cʼa are xpe i nim laj ya chi ʼis xe u cʼam bic. Xak queje quiqui ʼan na ile are quin cʼun yin chi in Achi aj Chicaj. ', '', '&lt;span class="verse" id="Matt_24_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Toʼ queje ile xqui ʼano, y n-xqui na taj, cʼa are xpe i nim laj ya chi ʼis xe u cʼam bic. Xak queje quiqui ʼan na ile are quin cʼun yin chi in Achi aj Chicaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.040', 'Chupam i ʼij-le, je ʼo ni queb achiab, que chacun junam pa qui ticon; jun chique ca cʼam nubic; i jun, ca canax canok. ', '', '&lt;span class="verse" id="Matt_24_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Chupam i ʼij-le, je ʼo ni queb achiab, que chacun junam pa qui ticon; jun chique ca cʼam nubic; i jun, ca canax canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.041', 'Xak je ʼo ni queb ixokib que tijin chi queʼem. Jun chique ca cʼam nubic; i jun ca canax canok.
', '', '&lt;span class="verse" id="Matt_24_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Xak je ʼo ni queb ixokib que tijin chi queʼem. Jun chique ca cʼam nubic; i jun ca canax canok.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.042', 'Rumal-i, chix cʼascʼat na, man n-iwetaʼam taj wach oʼonom ʼor ca cʼun i Iwajwal. ', '', '&lt;span class="verse" id="Matt_24_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Rumal-i, chix cʼascʼat na, man n-iwetaʼam taj wach oʼonom ʼor ca cʼun i Iwajwal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.043', 'Cha cʼun chi cʼux wach coʼon jun rajaw ja. Ire we te retaʼam chi chaʼab ca cʼun jun iliʼom ruʼ, we te retaʼam wach oʼonom ʼor ca cʼunic, ire ʼut ca cʼascʼat na. N-cu ya ta che coc paja, crelʼajbej re ubitak-re. ', '', '&lt;span class="verse" id="Matt_24_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Cha cʼun chi cʼux wach coʼon jun rajaw ja. Ire we te retaʼam chi chaʼab ca cʼun jun iliʼom ruʼ, we te retaʼam wach oʼonom ʼor ca cʼunic, ire ʼut ca cʼascʼat na. N-cu ya ta che coc paja, crelʼajbej re ubitak-re. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.044', 'Xak queje chi ʼana yix, lic chi yijba iwib chin iwoyʼej na; are n-ti qui chʼob che, are ʼuri quin cʼun yin chi in Achi aj Chicaj.
', '', '&lt;span class="verse" id="Matt_24_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Xak queje chi ʼana yix, lic chi yijba iwib chin iwoyʼej na; are n-ti qui chʼob che, are ʼuri quin cʼun yin chi in Achi aj Chicaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.045', '(I Jesus xu bij chic jun tijojbal tzij chique. Xu bij):
—Iwetaʼam wach ca ʼani che jun mocom lic ʼutz, lic ʼo u noʼoj. Ire ca yaʼ na can piquiwi i juban mocom rumal u patron. Chukul ire ʼo wi u yaʼic i qui wa are cu rik i ʼor. ', '', '&lt;h2&gt;Jun ajchac, chu ʼana i u chac pusucʼ, tupu naj ʼo wi u patron&lt;/h2&gt;&lt;span class="verse" id="Matt_24_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;(I Jesus xu bij chic jun tijojbal tzij chique. Xu bij):&lt;p&gt; &lt;/p&gt;—Iwetaʼam wach ca ʼani che jun mocom lic ʼutz, lic ʼo u noʼoj. Ire ca yaʼ na can piquiwi i juban mocom rumal u patron. Chukul ire ʼo wi u yaʼic i qui wa are cu rik i ʼor. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.046', 'ʼUtz re i achi-le, we ca tijin chu ʼonquil wach i rajwaxic chique i mocom are ca cʼun chali i u patron. ', '', '&lt;span class="verse" id="Matt_24_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;ʼUtz re i achi-le, we ca tijin chu ʼonquil wach i rajwaxic chique i mocom are ca cʼun chali i u patron. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.047', 'Katzij i quin bij chiwe, ire ca canax na ʼuri puwi ronojel niʼpa i ubitak-re u patron. ', '', '&lt;span class="verse" id="Matt_24_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, ire ca canax na ʼuri puwi ronojel niʼpa i ubitak-re u patron. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.048', 'Xui-ri, we xa mal laj mocom, xa cu chʼob na: “In patron, n-weʼ taj ca tzalij chalok,” mok cu bij ire. ', '', '&lt;span class="verse" id="Matt_24_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Xui-ri, we xa mal laj mocom, xa cu chʼob na: “In patron, n-weʼ taj ca tzalij chalok,” mok cu bij ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.049', 'Are ʼuri cu jek qui chʼayic i rach ajchac, xak ca ʼe cuʼ i je ʼabarel laj tak winak: toʼ cuʼ ique ca wa wi, xak ca tuʼun wi. ', '', '&lt;span class="verse" id="Matt_24_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;Are ʼuri cu jek qui chʼayic i rach ajchac, xak ca ʼe cuʼ i je ʼabarel laj tak winak: toʼ cuʼ ique ca wa wi, xak ca tuʼun wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.050', 'Ire n-retaʼam taj bi chi ʼoril ca cʼun u patron. Are n-ta cu chʼob che, xak teʼet ca cʼun chalok. ', '', '&lt;span class="verse" id="Matt_24_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Ire n-retaʼam taj bi chi ʼoril ca cʼun u patron. Are n-ta cu chʼob che, xak teʼet ca cʼun chalok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '24.051', 'I u patron cu ya na ʼuri pa nim laj tojpen. Xak jun i tojpen ca yaʼ che pacha ca yaʼ chique i je queb qui palaj. Chila coʼ wi, y cu kachʼachʼej ni uware.
', '', '&lt;span class="verse" id="Matt_24_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;I u patron cu ya na ʼuri pa nim laj tojpen. Xak jun i tojpen ca yaʼ che pacha ca yaʼ chique i je queb qui palaj. Chila coʼ wi, y cu kachʼachʼej ni uware.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.001', '(I Jesus xu bij chic jun tijojbal tzij chique.) Xu bij: I ʼatbal tzij aj chicaj queje pacha iri: Je ʼo lajuj altom xe ʼe che jun cʼulanquil. Xqui cʼam bi qui cantil man que ʼe chu cʼulaxic i achi are ca cʼunic. ', '', '&lt;h2&gt;I tijojbal tzij chiquij i lajuj altom&lt;/h2&gt;&lt;span class="verse" id="Matt_25_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;(I Jesus xu bij chic jun tijojbal tzij chique.) Xu bij: I ʼatbal tzij aj chicaj queje pacha iri: Je ʼo lajuj altom xe ʼe che jun cʼulanquil. Xqui cʼam bi qui cantil man que ʼe chu cʼulaxic i achi are ca cʼunic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.002', 'Je joʼob chique n-ta kas qui noʼoj; i je joʼob chic ʼo qui noʼoj. ', '', '&lt;span class="verse" id="Matt_25_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Je joʼob chique n-ta kas qui noʼoj; i je joʼob chic ʼo qui noʼoj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.003', 'I joʼob chi n-ta kas qui noʼoj, n-xqui cʼam tubi acéite re i qui cantil we xqʼuis u pam. ', '', '&lt;span class="verse" id="Matt_25_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I joʼob chi n-ta kas qui noʼoj, n-xqui cʼam tubi acéite re i qui cantil we xqʼuis u pam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.004', 'I joʼob chi ʼo qui noʼoj xqui cʼam bi jujun qui botey acéite, n-xui ti ʼo chupam i qui cantil xqui cʼam bic. ', '', '&lt;span class="verse" id="Matt_25_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I joʼob chi ʼo qui noʼoj xqui cʼam bi jujun qui botey acéite, n-xui ti ʼo chupam i qui cantil xqui cʼam bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.005', 'I achi chi ca cʼuliʼic xbaytajic. Ique xe u chap waram, y xe waric. ', '', '&lt;span class="verse" id="Matt_25_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I achi chi ca cʼuliʼic xbaytajic. Ique xe u chap waram, y xe waric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.006', 'Are tiqʼuil aʼab, ʼo i xe siqʼuin chu bixquil: “¡Ya xcʼun i achi; jix chu cʼulaxic!” xqui bij. ', '', '&lt;span class="verse" id="Matt_25_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are tiqʼuil aʼab, ʼo i xe siqʼuin chu bixquil: “¡Ya xcʼun i achi; jix chu cʼulaxic!” xqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.007', 'I lajuj altom xe walijic, xe oc chu yijbaxic qui cantil. ', '', '&lt;span class="verse" id="Matt_25_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I lajuj altom xe walijic, xe oc chu yijbaxic qui cantil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.008', 'Are ʼuri i joʼob chi n-ta qui noʼoj xqui bij chique i je joʼob cachiʼil: “Chi sipaj pichʼ ka acéite re i ka cantil, man ya ca chupic,” xe cha chique. ', '', '&lt;span class="verse" id="Matt_25_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are ʼuri i joʼob chi n-ta qui noʼoj xqui bij chique i je joʼob cachiʼil: “Chi sipaj pichʼ ka acéite re i ka cantil, man ya ca chupic,” xe cha chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.009', 'I altom chi ʼo qui noʼoj xqui bij: “N-caka ya taj,” xe cha. “Man we caka yaʼo, n-coʼon ti ke yoj, xak mi ne iwe yix,” xe cha. “Jix chila pa ca cʼayix wi; chi loʼo na iwe yix,” xe cha. ', '', '&lt;span class="verse" id="Matt_25_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I altom chi ʼo qui noʼoj xqui bij: “N-caka ya taj,” xe cha. “Man we caka yaʼo, n-coʼon ti ke yoj, xak mi ne iwe yix,” xe cha. “Jix chila pa ca cʼayix wi; chi loʼo na iwe yix,” xe cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.010', 'Teʼuri ique xe ʼe chu loʼic; are je ʼenak chu loʼic, xcʼun i achi. Niʼpa i altom chi je yijilic, xe oc ruʼ ire pa cʼulanquil, are ʼuri xtzʼapix i uchija. ', '', '&lt;span class="verse" id="Matt_25_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri ique xe ʼe chu loʼic; are je ʼenak chu loʼic, xcʼun i achi. Niʼpa i altom chi je yijilic, xe oc ruʼ ire pa cʼulanquil, are ʼuri xtzʼapix i uchija. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.011', 'Teʼuri xe upon i joʼob chic, y xqui bij: “¡Nim laj winak! ¡Nim laj winak! ¡Cha jaka i uchija chake!” xe cha che. ', '', '&lt;span class="verse" id="Matt_25_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Teʼuri xe upon i joʼob chic, y xqui bij: “¡Nim laj winak! ¡Nim laj winak! ¡Cha jaka i uchija chake!” xe cha che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.012', 'Ire xu laʼ u wach chique: “N-quin ʼan taj; n-wetaʼam ta i wach,” xu bij.
', '', '&lt;span class="verse" id="Matt_25_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Ire xu laʼ u wach chique: “N-quin ʼan taj; n-wetaʼam ta i wach,” xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.013', 'Teʼuri i Jesus xu bij chique u tijoxelab:
—Yix, chix cʼascʼat na, man n-iwetaʼam taj pachique ʼij, pachique ʼor, quin cʼun yin chi in Achi aj Chicaj —xu bij chique.
', '', '&lt;span class="verse" id="Matt_25_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Yix, chix cʼascʼat na, man n-iwetaʼam taj pachique ʼij, pachique ʼor, quin cʼun yin chi in Achi aj Chicaj —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.014', '(Teʼuri i Jesus xu bij jun chic tijojbal tzij chique. Xu bij):
—I ʼatbal tzij re aj chicaj, queje pacha iri: ʼO jun achi ca ʼe che jun tinimit cʼa naj; xe u siqʼuij u mocom y xrokxanij can u mer cuʼ. ', '', '&lt;h2&gt;I tijojbal tzij chiquij i oxib mocom&lt;/h2&gt;&lt;span class="verse" id="Matt_25_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;(Teʼuri i Jesus xu bij jun chic tijojbal tzij chique. Xu bij):&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re aj chicaj, queje pacha iri: ʼO jun achi ca ʼe che jun tinimit cʼa naj; xe u siqʼuij u mocom y xrokxanij can u mer cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.015', 'Jun chique xrokxanij joʼob mil sak pwak ruʼ. Y jun chic, xu ya chic can queb mil sak pwak ruʼ; y jun chic, xu ya can jun mil ruʼ. Queje u yaʼic ile xoʼon chique, pacha i ca majaw chique chi qui jujunal. Teʼuri xu maj u be. ', '', '&lt;span class="verse" id="Matt_25_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Jun chique xrokxanij joʼob mil sak pwak ruʼ. Y jun chic, xu ya chic can queb mil sak pwak ruʼ; y jun chic, xu ya can jun mil ruʼ. Queje u yaʼic ile xoʼon chique, pacha i ca majaw chique chi qui jujunal. Teʼuri xu maj u be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.016', 'I mocom chi xyaʼ can joʼob mil sak pwak che, ire xoc chi chac ruʼ i mer. Queje ile xu chʼac joʼob mil chirij. ', '', '&lt;span class="verse" id="Matt_25_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I mocom chi xyaʼ can joʼob mil sak pwak che, ire xoc chi chac ruʼ i mer. Queje ile xu chʼac joʼob mil chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.017', 'Xak queje xoʼon i jun chi xyaʼ queb mil che: xak xu chʼac queb mil chirij. ', '', '&lt;span class="verse" id="Matt_25_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xak queje xoʼon i jun chi xyaʼ queb mil che: xak xu chʼac queb mil chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.018', 'I jun chi yatal jun mil che, xa xu ruwaj u mer i u patron; xoʼon jun jul y xu muk pulew.
', '', '&lt;span class="verse" id="Matt_25_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I jun chi yatal jun mil che, xa xu ruwaj u mer i u patron; xoʼon jun jul y xu muk pulew.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.019', 'I patron xnajtinic; teʼuri xcʼun chic, y xoc chi rajlaxic u mer cuʼ u mocom. ', '', '&lt;span class="verse" id="Matt_25_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I patron xnajtinic; teʼuri xcʼun chic, y xoc chi rajlaxic u mer cuʼ u mocom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.020', 'Nabe xupon i jun chi u yom can joʼob mil che. Ire xu jach i joʼob mil chi yatal che, xak xu jach i joʼob mil chi u chʼacom. Xu bij: “Nim laj winak, joʼob mil xa ya chwe, xak joʼob mil chic xin chʼac chirij,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Nabe xupon i jun chi u yom can joʼob mil che. Ire xu jach i joʼob mil chi yatal che, xak xu jach i joʼob mil chi u chʼacom. Xu bij: “Nim laj winak, joʼob mil xa ya chwe, xak joʼob mil chic xin chʼac chirij,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.021', 'I u patron xu bij: “Lic ʼutz i a ʼanom. Yet at ʼutz laj mocom; lic a yom a cʼux chu ʼonquil pacha i cwaj yin. Lic ʼutz a chac xa ʼan ruʼ i jubiʼ-le, rumal-i, quin ya na mas paʼab puwi ile. Chat oc lok, y chat quicot wuʼ,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I u patron xu bij: “Lic ʼutz i a ʼanom. Yet at ʼutz laj mocom; lic a yom a cʼux chu ʼonquil pacha i cwaj yin. Lic ʼutz a chac xa ʼan ruʼ i jubiʼ-le, rumal-i, quin ya na mas paʼab puwi ile. Chat oc lok, y chat quicot wuʼ,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.022', 'Teʼuri xupon i jun chi yatal queb mil che, Xu bij: “Nim laj winak, queb mil a yom chwe, xak ri i queb mil-i xin chʼac chirij,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Teʼuri xupon i jun chi yatal queb mil che, Xu bij: “Nim laj winak, queb mil a yom chwe, xak ri i queb mil-i xin chʼac chirij,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.023', 'I u patron xu bij: “Lic ʼutz i a ʼanom. Yet at ʼutz laj mocom; lic a yom a cʼux chu ʼonquil pacha i cwaj yin. Lic ʼutz a chac xa ʼan ruʼ i jubiʼ-le, rumal-i, quin ya na mas paʼab puwi ile. Chat oc lok, y chat quicot wuʼ,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I u patron xu bij: “Lic ʼutz i a ʼanom. Yet at ʼutz laj mocom; lic a yom a cʼux chu ʼonquil pacha i cwaj yin. Lic ʼutz a chac xa ʼan ruʼ i jubiʼ-le, rumal-i, quin ya na mas paʼab puwi ile. Chat oc lok, y chat quicot wuʼ,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.024', 'Xak xupon chic i jun chi yatal jun mil che. Xu bij che u patron: “Nim laj winak, wetaʼam chi yet at cʼan che a chac; ca jachʼ i ticon chi na ticom taj, xak ca mol u wach i ticon chi n-yet ti a mulim. ', '', '&lt;span class="verse" id="Matt_25_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xak xupon chic i jun chi yatal jun mil che. Xu bij che u patron: “Nim laj winak, wetaʼam chi yet at cʼan che a chac; ca jachʼ i ticon chi na ticom taj, xak ca mol u wach i ticon chi n-yet ti a mulim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.025', 'Rumal-i xin xij wib; xeʼe wuwaj a mer pulew. Chawila, ri a mer-i chi a yom chwe,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Rumal-i xin xij wib; xeʼe wuwaj a mer pulew. Chawila, ri a mer-i chi a yom chwe,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.026', '“Yet n-at ta ʼutz laj mocom; lic ca cʼayo,” xu bij u patron che. “We yet awetaʼam chi in cʼan che in chac, ', '', '&lt;span class="verse" id="Matt_25_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;“Yet n-at ta ʼutz laj mocom; lic ca cʼayo,” xu bij u patron che. “We yet awetaʼam chi in cʼan che in chac, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.027', '¿wuchac ʼuri n-xa ya ti in mer pa bánco, cu chʼacbej na mas? We ta queje ile xa ʼano, quin chʼac ni yin-i niʼpa i xu chʼaco junam ruʼ ile chi in yom chawe,” xu bij che. ', '', '&lt;span class="verse" id="Matt_25_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;¿wuchac ʼuri n-xa ya ti in mer pa bánco, cu chʼacbej na mas? We ta queje ile xa ʼano, quin chʼac ni yin-i niʼpa i xu chʼaco junam ruʼ ile chi in yom chawe,” xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.028', 'Teʼuri xu bij chique i juban je ʼo chila, “Chi cʼama i jun mil-le che; chi ya na che i jun chi ʼo lajuj mil ruʼ. ', '', '&lt;span class="verse" id="Matt_25_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique i juban je ʼo chila, “Chi cʼama i jun mil-le che; chi ya na che i jun chi ʼo lajuj mil ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.029', 'Man jun, we ʼo jubiʼ ruʼ, ca yaʼ na mas che; we jun n-ca chacun ta ruʼ wach ʼo puʼab, niʼpa i ʼo ruʼ quelsax na che. ', '', '&lt;span class="verse" id="Matt_25_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Man jun, we ʼo jubiʼ ruʼ, ca yaʼ na mas che; we jun n-ca chacun ta ruʼ wach ʼo puʼab, niʼpa i ʼo ruʼ quelsax na che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.030', 'I n-ʼus ta laj mocom-le, chiwesaj bi rija; ʼoj pa ʼekum. Chila coʼ wi, y cu kachʼachʼej ni uware,” xu bij i qui patron chique.
', '', '&lt;span class="verse" id="Matt_25_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;I n-ʼus ta laj mocom-le, chiwesaj bi rija; ʼoj pa ʼekum. Chila coʼ wi, y cu kachʼachʼej ni uware,” xu bij i qui patron chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.031', '(I Jesus xu bij:) I Achi aj Chicaj, are ca cʼun tan chic, coc pu patan che ʼatol tzij; junam cuʼ u ángel ca cʼunic. Ca qʼuiji na ʼuri pu cubibal chi nim u ʼij. ', '', '&lt;h2&gt;Wach i u jachic quixol i winak ca ʼani pa qʼuisbal ʼij&lt;/h2&gt;&lt;span class="verse" id="Matt_25_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;(I Jesus xu bij:) I Achi aj Chicaj, are ca cʼun tan chic, coc pu patan che ʼatol tzij; junam cuʼ u ángel ca cʼunic. Ca qʼuiji na ʼuri pu cubibal chi nim u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.032', 'I winak re ronojel tak tinimit chuwach i jyub taʼaj que moltaj na chuwach. Teʼuri coc chi qui jachic qui pam, pacha jun aj chajil chij que u resaj u chij chiquixol i chabat. ', '', '&lt;span class="verse" id="Matt_25_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I winak re ronojel tak tinimit chuwach i jyub taʼaj que moltaj na chuwach. Teʼuri coc chi qui jachic qui pam, pacha jun aj chajil chij que u resaj u chij chiquixol i chabat. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.033', 'Que u ya i u chij pu wikabim; i tak i chabat que u ya pu moxim. ', '', '&lt;span class="verse" id="Matt_25_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Que u ya i u chij pu wikabim; i tak i chabat que u ya pu moxim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.034', 'Teʼuri i ʼAtol Tzij cu bij na chique i je ʼo pu wikabim: “ʼUtz iwe yix; u yom ni utzil chomal in Kajaw piwi. Chix petok, chix oc che i qʼuijibal chi u yijbam ire chiwe cʼa chu xebal ʼij sak. ', '', '&lt;span class="verse" id="Matt_25_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼAtol Tzij cu bij na chique i je ʼo pu wikabim: “ʼUtz iwe yix; u yom ni utzil chomal in Kajaw piwi. Chix petok, chix oc che i qʼuijibal chi u yijbam ire chiwe cʼa chu xebal ʼij sak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.035', 'Queje cu ya ire ile chiwe man yix, are xin tij numic, xi ya na chwe wach i quin tijo; are xin cʼatic, xi ya ni in yaʼ; are xin tzucuj in posar, xin i cʼulaj iwuʼ. ', '', '&lt;span class="verse" id="Matt_25_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Queje cu ya ire ile chiwe man yix, are xin tij numic, xi ya na chwe wach i quin tijo; are xin cʼatic, xi ya ni in yaʼ; are xin tzucuj in posar, xin i cʼulaj iwuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.036', 'Are n-ta chic in ʼuʼ, xi ya i chʼukbal wij; are in iwab, xin i chajij; are in tzʼapil pa cárcel, xin i solij na.” Queje ile cu bij ni ʼAtol Tzij chique. ', '', '&lt;span class="verse" id="Matt_25_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Are n-ta chic in ʼuʼ, xi ya i chʼukbal wij; are in iwab, xin i chajij; are in tzʼapil pa cárcel, xin i solij na.” Queje ile cu bij ni ʼAtol Tzij chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.037', 'Teʼuri, i je ʼutz-le quiqui bij na: “Nim laj winak, ¿umpa xa ʼan ile? ¿Umpa xka ʼano chi cat numic, y xka ya chawe wach ca tijo? ¿Umpa xka ʼano chi cat cʼatic, y xka ya a yaʼ? ', '', '&lt;span class="verse" id="Matt_25_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri, i je ʼutz-le quiqui bij na: “Nim laj winak, ¿umpa xa ʼan ile? ¿Umpa xka ʼano chi cat numic, y xka ya chawe wach ca tijo? ¿Umpa xka ʼano chi cat cʼatic, y xka ya a yaʼ? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.038', '¿Umpa xka ʼano chi ca tzucuj a posar y xka ya chawe? ¿Umpa xka ʼano chi n-ta chic a ʼuʼ, y xka ya chʼukbal awij? ', '', '&lt;span class="verse" id="Matt_25_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;¿Umpa xka ʼano chi ca tzucuj a posar y xka ya chawe? ¿Umpa xka ʼano chi n-ta chic a ʼuʼ, y xka ya chʼukbal awij? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.039', '¿Umpa xka ʼano chi at iwab, xak at ʼo pa cárcel, y xat ka solij?” Queje ile quiqui bij na che i ʼAtol Tzij. ', '', '&lt;span class="verse" id="Matt_25_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;¿Umpa xka ʼano chi at iwab, xak at ʼo pa cárcel, y xat ka solij?” Queje ile quiqui bij na che i ʼAtol Tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.040', 'Teʼuri i ʼAtol Tzij cu bij: “Katzij i quin bij chiwe, niʼpa i utzil chomal i ʼanom che jun wachalal, tupu n-ta kas u ʼij, xeʼel ʼuri lic chwe yin i ʼanom wi,” cu bij na chique.
', '', '&lt;span class="verse" id="Matt_25_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼAtol Tzij cu bij: “Katzij i quin bij chiwe, niʼpa i utzil chomal i ʼanom che jun wachalal, tupu n-ta kas u ʼij, xeʼel ʼuri lic chwe yin i ʼanom wi,” cu bij na chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.041', 'Teʼuri i ʼAtol Tzij cu bij na chique i je ʼo pu moxim: “Chix el chi chinwach, yix chi ix cʼoktal rumal i Dios. Jix pa ʼaʼ chi n-tu qʼuisic, chi yijtalic re itzel junam cuʼ i u ángel. ', '', '&lt;span class="verse" id="Matt_25_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼAtol Tzij cu bij na chique i je ʼo pu moxim: “Chix el chi chinwach, yix chi ix cʼoktal rumal i Dios. Jix pa ʼaʼ chi n-tu qʼuisic, chi yijtalic re itzel junam cuʼ i u ángel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.042', 'Yin, are xin tij numic, yix n-xi ya ta chwe wach i quin tijo; are xin tij cʼatic, n-xi ya ta in yaʼ. ', '', '&lt;span class="verse" id="Matt_25_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Yin, are xin tij numic, yix n-xi ya ta chwe wach i quin tijo; are xin tij cʼatic, n-xi ya ta in yaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.043', 'Are xin tzucuj in posar, n-xi ya ta chwe. Are n-ta chic in ʼuʼ, xak n-ta in ʼuʼ xi yaʼo. Are in iwab, xak are in ʼo pa cárcel, yix n-xin i solij taj.” Queje cu bij ni ʼAtol Tzij ile chique i je ʼo pu moxim. ', '', '&lt;span class="verse" id="Matt_25_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Are xin tzucuj in posar, n-xi ya ta chwe. Are n-ta chic in ʼuʼ, xak n-ta in ʼuʼ xi yaʼo. Are in iwab, xak are in ʼo pa cárcel, yix n-xin i solij taj.” Queje cu bij ni ʼAtol Tzij ile chique i je ʼo pu moxim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.044', 'Teʼuri ique quiqui bij na: “Nim laj winak, ¿umpa xat kilo cat numic, o cat cʼatic, o are ca tzucuj a posar, o xak are n-ta a ʼuʼ, o are at iwab, o xak are at ʼo pa cárcel? ¿Umpa xa ʼan ile chi n-xat ka to taj?” quiqui bij na che. ', '', '&lt;span class="verse" id="Matt_25_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Teʼuri ique quiqui bij na: “Nim laj winak, ¿umpa xat kilo cat numic, o cat cʼatic, o are ca tzucuj a posar, o xak are n-ta a ʼuʼ, o are at iwab, o xak are at ʼo pa cárcel? ¿Umpa xa ʼan ile chi n-xat ka to taj?” quiqui bij na che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.045', 'I ʼAtol Tzij cu bij na ʼuri chique: “Katzij i quin bij chiwe: yix are xiwil jun we yin chi ʼo rajwaxic che, y n-xi to taj, tupu n-ta kas u ʼij, xeʼel ʼuri chiwe lic are ni yin chi n-xin i to taj,” cu bij na chique. ', '', '&lt;span class="verse" id="Matt_25_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;I ʼAtol Tzij cu bij na ʼuri chique: “Katzij i quin bij chiwe: yix are xiwil jun we yin chi ʼo rajwaxic che, y n-xi to taj, tupu n-ta kas u ʼij, xeʼel ʼuri chiwe lic are ni yin chi n-xin i to taj,” cu bij na chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '25.046', 'Teʼuri ique que ʼe pa tojpen chi n-tu qʼuisic; xak i je ʼutz que oc na che i qui cʼaslemal chi n-tu qʼuisic. (Queje ile xu bij i Jesus chique u tijoxelab.)
', '', '&lt;span class="verse" id="Matt_25_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Teʼuri ique que ʼe pa tojpen chi n-tu qʼuisic; xak i je ʼutz que oc na che i qui cʼaslemal chi n-tu qʼuisic. (Queje ile xu bij i Jesus chique u tijoxelab.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.001', 'I Jesus, are xutzin u bixquil ile chique, teʼuri xu bij chique u tijoxelab:
', '', '&lt;h2&gt;Wach u chapic i Jesus xqui chʼob i nimak tak mamʼib&lt;/h2&gt;&lt;span class="verse" id="Matt_26_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus, are xutzin u bixquil ile chique, teʼuri xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.002', '—Iwetaʼam yix, xa queb ʼij chic craj, ca cʼun i nimaʼij páscua. Are ʼuri yin, chi in Achi aj Chicaj quin jachtaj na piquiʼab i je aj chʼoʼoj chwij man quin qui camsaj chuwach cruz —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—Iwetaʼam yix, xa queb ʼij chic craj, ca cʼun i nimaʼij páscua. Are ʼuri yin, chi in Achi aj Chicaj quin jachtaj na piquiʼab i je aj chʼoʼoj chwij man quin qui camsaj chuwach cruz —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.003', 'Xak che i ʼij-le, xqui mol quib i ajwab sacerdóte, xak i je tijonel re i ʼatbal tzij, xak i nimak tak mamʼib que i aj Israel. Xqui mol quib chuwaja re i nim laj ja pa ʼo wi i mam Caifas chi ʼatz laj sacerdóte. ', '', '&lt;span class="verse" id="Matt_26_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xak che i ʼij-le, xqui mol quib i ajwab sacerdóte, xak i je tijonel re i ʼatbal tzij, xak i nimak tak mamʼib que i aj Israel. Xqui mol quib chuwaja re i nim laj ja pa ʼo wi i mam Caifas chi ʼatz laj sacerdóte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.004', 'Chila xqui yijba wi wach u chapic i Jesus quiqui ʼano aliʼal, xak wach i camsabal-re. ', '', '&lt;span class="verse" id="Matt_26_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Chila xqui yijba wi wach u chapic i Jesus quiqui ʼano aliʼal, xak wach i camsabal-re. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.005', 'Xui-ri, xqui bij chiquiwach:
—N-ʼus taj we caka ʼano tzʼakat je qʼui i winak waral re i nimaʼij, mokxa que oc i winak chi chʼokowic —xe cha.
', '', '&lt;span class="verse" id="Matt_26_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xui-ri, xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—N-ʼus taj we caka ʼano tzʼakat je qʼui i winak waral re i nimaʼij, mokxa que oc i winak chi chʼokowic —xe cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.006', 'I Jesus ʼo pa aldéa Betánia ruʼ i rachoch i achi Simon, chi “Lepróso” ca bix che. ', '', '&lt;h2&gt;Jun ixok xu coj cunbal muy che u jolom i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_26_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I Jesus ʼo pa aldéa Betánia ruʼ i rachoch i achi Simon, chi “Lepróso” ca bix che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.007', 'Are ʼuri xtejeb jun ixok ruʼ, rucʼam jun chom laj baxo nojnak che acéite lic muy, lic pakal rajil. I Jesus ca tijin chi waʼim, are ʼuri i ixok xu jam bi i cunbal puwi i Jesus. ', '', '&lt;span class="verse" id="Matt_26_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are ʼuri xtejeb jun ixok ruʼ, rucʼam jun chom laj baxo nojnak che acéite lic muy, lic pakal rajil. I Jesus ca tijin chi waʼim, are ʼuri i ixok xu jam bi i cunbal puwi i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.008', 'I u tijoxelab, are xquil ile, xpe coywal.
—¿Wuchac i ixok-le toʼ xoʼon cʼax che i cunbal muy? —xe cha chiquiwach—. ', '', '&lt;span class="verse" id="Matt_26_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xquil ile, xpe coywal.&lt;p&gt; &lt;/p&gt;—¿Wuchac i ixok-le toʼ xoʼon cʼax che i cunbal muy? —xe cha chiquiwach—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.009', 'We ta xu cʼayij, nim ni mer cu chap na che, cu tobej que i nibaʼib —xe cha.
', '', '&lt;span class="verse" id="Matt_26_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;We ta xu cʼayij, nim ni mer cu chap na che, cu tobej que i nibaʼib —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.010', 'Xu ta i Jesus wach quiqui bij y xu bij chique:
—¿Wuchac queje qui bij ile chirij i ixok-le? I xoʼon ire-le chwe, lic ʼutz. ', '', '&lt;span class="verse" id="Matt_26_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xu ta i Jesus wach quiqui bij y xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac queje qui bij ile chirij i ixok-le? I xoʼon ire-le chwe, lic ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.011', 'I nibaʼib, queʼe ʼij sak je ʼo na iwuʼ; yin n-naj ta chic in ʼo iwuʼ. ', '', '&lt;span class="verse" id="Matt_26_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I nibaʼib, queʼe ʼij sak je ʼo na iwuʼ; yin n-naj ta chic in ʼo iwuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.012', 'I ixok-i, are xu jam i cunbal muy che in chʼacul, ʼutz i xu ʼano, man xu cunaj bi in chʼacul re i mukbal-we. ', '', '&lt;span class="verse" id="Matt_26_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I ixok-i, are xu jam i cunbal muy che in chʼacul, ʼutz i xu ʼano, man xu cunaj bi in chʼacul re i mukbal-we. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.013', 'Katzij i quin bij chiwe, xa ipa ca paxsax wi i tzij re i ʼelsabal re i qui mac i winak xak ca tataj ni rason wach u ʼanom i ixok-i chwe. Queje coʼon ile ronojel u wach i jyub taʼaj, cʼunsabal re i ixok-i chi qui cʼux i winak —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Matt_26_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, xa ipa ca paxsax wi i tzij re i ʼelsabal re i qui mac i winak xak ca tataj ni rason wach u ʼanom i ixok-i chwe. Queje coʼon ile ronojel u wach i jyub taʼaj, cʼunsabal re i ixok-i chi qui cʼux i winak —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.014', 'Teʼuri, jun chique i cablajuj u tijoxelab, i jun chi ma Júdas Iscarióte u bi, ire xa ʼe lapan cuʼ i ajwab sacerdóte. ', '', '&lt;h2&gt;Wach u cʼayixic i Jesus xoʼon i ma Júdas&lt;/h2&gt;&lt;span class="verse" id="Matt_26_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri, jun chique i cablajuj u tijoxelab, i jun chi ma Júdas Iscarióte u bi, ire xa ʼe lapan cuʼ i ajwab sacerdóte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.015', 'Xu bij chique:
—¿Niʼpa i qui ya chwe we quin jach i ma Jesus piʼab? —xu bij chique.
Ique xqui ya tréinta sak pwak che. ', '', '&lt;span class="verse" id="Matt_26_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Niʼpa i qui ya chwe we quin jach i ma Jesus piʼab? —xu bij chique.&lt;p&gt; &lt;/p&gt;Ique xqui ya tréinta sak pwak che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.016', 'Are ʼuri i ma Júdas xu jek u tzucuxic wach i jachbal re i Jesus coʼon piquiʼab.
', '', '&lt;span class="verse" id="Matt_26_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Júdas xu jek u tzucuxic wach i jachbal re i Jesus coʼon piquiʼab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.017', 'Xcʼun i nabe ʼij re i nimaʼij chi ca tijaw i caxlanwa chi n-ti cunbal che. I u tijoxelab i Jesus xe tejeb ruʼ y xqui bij che:
—¿Pa cawaj wi yet queʼe ka yijba wi i a waʼim re i nimaʼij páscua? —xe cha che.
', '', '&lt;h2&gt;I qʼuisbal waʼim xu tij i Jesus cuʼ u tijoxelab&lt;/h2&gt;&lt;span class="verse" id="Matt_26_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xcʼun i nabe ʼij re i nimaʼij chi ca tijaw i caxlanwa chi n-ti cunbal che. I u tijoxelab i Jesus xe tejeb ruʼ y xqui bij che:&lt;p&gt; &lt;/p&gt;—¿Pa cawaj wi yet queʼe ka yijba wi i a waʼim re i nimaʼij páscua? —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.018', '—Jix pa tinimit —xu bij chique—. Jix ruʼ jun achi ʼo chila y queje iri chi bij che: “Xu bij li ka tijonel: Ya cu rik i ʼor chwij re in camic, cu bij. Chawachoch yet caka ʼan wi ka waʼim re i nimaʼij páscua, yin cuʼ in tijoxelab, cu bij.” Queje ile chix chok che —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;—Jix pa tinimit —xu bij chique—. Jix ruʼ jun achi ʼo chila y queje iri chi bij che: “Xu bij li ka tijonel: Ya cu rik i ʼor chwij re in camic, cu bij. Chawachoch yet caka ʼan wi ka waʼim re i nimaʼij páscua, yin cuʼ in tijoxelab, cu bij.” Queje ile chix chok che —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.019', 'I u tijoxelab xqui ʼano pacha i xbix chique. Xe ʼe chu yijbaxic i waʼim re i nimaʼij.
', '', '&lt;span class="verse" id="Matt_26_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I u tijoxelab xqui ʼano pacha i xbix chique. Xe ʼe chu yijbaxic i waʼim re i nimaʼij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.020', 'Are xoc aʼab, i Jesus ʼo cuʼ i cablajuj u tijoxelab chi mexa. ', '', '&lt;span class="verse" id="Matt_26_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are xoc aʼab, i Jesus ʼo cuʼ i cablajuj u tijoxelab chi mexa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.021', 'Are que tijin chi waʼim, xu bij chique:
—Katzij i quin bij chiwe, ʼo jun chiwe yix quin u jach na piquiʼab i je aj chʼoʼoj chwij —xu bij.
', '', '&lt;span class="verse" id="Matt_26_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Are que tijin chi waʼim, xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, ʼo jun chiwe yix quin u jach na piquiʼab i je aj chʼoʼoj chwij —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.022', 'Ique, are xqui ta, lic xe oc chi bis. Chiqui jujunal xe oc chu tzʼonoxic che:
—Kajwal, ¿mokxa yin? —xqui bij che.
', '', '&lt;span class="verse" id="Matt_26_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Ique, are xqui ta, lic xe oc chi bis. Chiqui jujunal xe oc chu tzʼonoxic che:&lt;p&gt; &lt;/p&gt;—Kajwal, ¿mokxa yin? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.023', 'Ire xu bij:
—Pachin cu mu u wa pa lak wuʼ yin, are ni ire ʼuri quin jachaw na. ', '', '&lt;span class="verse" id="Matt_26_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Ire xu bij:&lt;p&gt; &lt;/p&gt;—Pachin cu mu u wa pa lak wuʼ yin, are ni ire ʼuri quin jachaw na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.024', 'Katzij, yin chi in Achi aj chicaj, quin camsax na, pacha tzʼibtal can chupam u wuj i Dios, xui-ri toʼwach i achi chi quin jachaw na piquiʼab i aj chʼoʼoj chwij. Mas ʼutz queʼel che i achi-le ma ta xalaxic —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Katzij, yin chi in Achi aj chicaj, quin camsax na, pacha tzʼibtal can chupam u wuj i Dios, xui-ri toʼwach i achi chi quin jachaw na piquiʼab i aj chʼoʼoj chwij. Mas ʼutz queʼel che i achi-le ma ta xalaxic —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.025', 'Teʼuri xchʼaw i ma Júdas, chi ca jachaw ni Jesus:
—Tijonel, ¿xataba yin quin ʼanaw ile? —xu bij.
I Jesus xu bij che:
—Ruʼ a chiʼ xa bij —coʼono.
', '', '&lt;span class="verse" id="Matt_26_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Teʼuri xchʼaw i ma Júdas, chi ca jachaw ni Jesus:&lt;p&gt; &lt;/p&gt;—Tijonel, ¿xataba yin quin ʼanaw ile? —xu bij.&lt;p&gt; &lt;/p&gt;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Ruʼ a chiʼ xa bij —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.026', 'Are que tijin chi waʼim, i Jesus xu chap jun caxlanwa, xu tioxij che i Dios, teʼuri xu piro. Xu ya chique u tijoxelab, y xu bij chique:
—Chi tija, are iri are in chʼacul.
', '', '&lt;span class="verse" id="Matt_26_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Are que tijin chi waʼim, i Jesus xu chap jun caxlanwa, xu tioxij che i Dios, teʼuri xu piro. Xu ya chique u tijoxelab, y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi tija, are iri are in chʼacul.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.027', 'Teʼuri xu chap jun tuʼbal víno, xu tioxij che i Dios, y xu ya chique.
—Chi tuʼu yix iri, iwonojel —xu bij chique—. ', '', '&lt;span class="verse" id="Matt_26_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri xu chap jun tuʼbal víno, xu tioxij che i Dios, y xu ya chique.&lt;p&gt; &lt;/p&gt;—Chi tuʼu yix iri, iwonojel —xu bij chique—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.028', 'Are iri are in quiqʼuel chi ca yijbaw ni trat cʼacʼ ruʼ i Dios. Ca turuw na, sachsabal re i qui mac uqʼuial winak. ', '', '&lt;span class="verse" id="Matt_26_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are iri are in quiqʼuel chi ca yijbaw ni trat cʼacʼ ruʼ i Dios. Ca turuw na, sachsabal re i qui mac uqʼuial winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.029', 'Katzij quin bij chiwe, n-quin tuʼ ta chic u waʼal úva, cʼa cu rik ni ʼij quin tuʼ julaj chic junam iwuʼ yix, pa cu ʼat wi tzij in Kajaw —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Katzij quin bij chiwe, n-quin tuʼ ta chic u waʼal úva, cʼa cu rik ni ʼij quin tuʼ julaj chic junam iwuʼ yix, pa cu ʼat wi tzij in Kajaw —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.030', 'Teʼuri xqui bixaj jun bix, cojbal re u ʼij i Dios; are xe utzinic, xe ʼe chuwi jyub Olívos.
', '', '&lt;span class="verse" id="Matt_26_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Teʼuri xqui bixaj jun bix, cojbal re u ʼij i Dios; are xe utzinic, xe ʼe chuwi jyub Olívos.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.031', 'I Jesus xu bij chique:
—Woʼor chaʼab, iwonojel yix quin iwoʼtaj na canok. Wetaʼam queje ile queʼelok, man queje iri u bim can i Dios chupam u wuj: “Quin ya na, chi ca camsax ni aj yuʼul chij,” xu bij, “teʼuri i chij que jacher bic.” Queje ile tzʼibtal canok. ', '', '&lt;h2&gt;I Jesus xu bij che i ma Pédro chi cruwaj ni u wach&lt;/h2&gt;&lt;span class="verse" id="Matt_26_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Woʼor chaʼab, iwonojel yix quin iwoʼtaj na canok. Wetaʼam queje ile queʼelok, man queje iri u bim can i Dios chupam u wuj: “Quin ya na, chi ca camsax ni aj yuʼul chij,” xu bij, “teʼuri i chij que jacher bic.” Queje ile tzʼibtal canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.032', 'Xui-ri, are in walijnak chic che i camic, quin nabeaj na chiwach pa Galiléa —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xui-ri, are in walijnak chic che i camic, quin nabeaj na chiwach pa Galiléa —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.033', 'Are ʼuri xu bij i ma Pédro:
—We conojel ʼis cat coʼtaj canok, yin lic n-cat woʼtaj taj —xu bij.
', '', '&lt;span class="verse" id="Matt_26_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij i ma Pédro:&lt;p&gt; &lt;/p&gt;—We conojel ʼis cat coʼtaj canok, yin lic n-cat woʼtaj taj —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.034', 'I Jesus xu bij che:
—Katzij i quin bij chawe, che i aʼab-i, are maja ca chʼaw i acʼ re u sakirbal, oxmul ca bij na, chi n-awetaʼam ta in wach —coʼono.
', '', '&lt;span class="verse" id="Matt_26_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chawe, che i aʼab-i, are maja ca chʼaw i acʼ re u sakirbal, oxmul ca bij na, chi n-awetaʼam ta in wach —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.035', '—Yin lic n-cat wuwaj taj —xu bij i ma Pédro— n-ta coʼono we junam awuʼ coj camsaxic.
Xak queje ile xqui bij conojel i u tijoxelab.
', '', '&lt;span class="verse" id="Matt_26_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;—Yin lic n-cat wuwaj taj —xu bij i ma Pédro— n-ta coʼono we junam awuʼ coj camsaxic.&lt;p&gt; &lt;/p&gt;Xak queje ile xqui bij conojel i u tijoxelab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.036', 'Teʼuri xe ʼe chic che jun lugar, Getsemani u bi. I Jesus xu bij chique u tijoxelab:
—Chix cul na can chi, tzʼakat quin ʼe na yin jela chu tzʼonoxic che i Dios —xu bij.
', '', '&lt;h2&gt;Wach u tzʼonoxic xoʼon i Jesus pa Getsemani&lt;/h2&gt;&lt;span class="verse" id="Matt_26_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri xe ʼe chic che jun lugar, Getsemani u bi. I Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Chix cul na can chi, tzʼakat quin ʼe na yin jela chu tzʼonoxic che i Dios —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.037', 'Teʼuri xe u cʼam bi i ma Pédro, xak i queb u cʼojol i mam Zebedéo ruʼ, y xpe jun nim laj bis, jun nim laj cʼax pu cʼux. ', '', '&lt;span class="verse" id="Matt_26_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri xe u cʼam bi i ma Pédro, xak i queb u cʼojol i mam Zebedéo ruʼ, y xpe jun nim laj bis, jun nim laj cʼax pu cʼux. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.038', 'Xu bij chique:
—I bis ʼo pa wanima, quin cam rumal, quin na yin. Chix canaj yix waral y chix cʼascʼatok —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—I bis ʼo pa wanima, quin cam rumal, quin na yin. Chix canaj yix waral y chix cʼascʼatok —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.039', 'I Jesus xa ʼe chupan jubiʼ, xu jupij rib pulew, y xoc chu tzʼonoxic che i Dios:
—Ta, cwaj yin, we ta ʼo módo, cawesaj i cʼax ʼo chinwach. Xui-ri, ma ya chwe pacha i toʼ cwaj yin; are, cha ʼana pacha i cawaj yet —xu bij.
', '', '&lt;span class="verse" id="Matt_26_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;I Jesus xa ʼe chupan jubiʼ, xu jupij rib pulew, y xoc chu tzʼonoxic che i Dios:&lt;p&gt; &lt;/p&gt;—Ta, cwaj yin, we ta ʼo módo, cawesaj i cʼax ʼo chinwach. Xui-ri, ma ya chwe pacha i toʼ cwaj yin; are, cha ʼana pacha i cawaj yet —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.040', 'Teʼuri xtzalij chic cuʼ i u tijoxelab; xe u rika que waric. Xu ʼijla i ma Pédro:
—¿N-xi chʼij ta bari xix cʼascʼat jun ʼor wuʼ? —xu bij—. ', '', '&lt;span class="verse" id="Matt_26_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Teʼuri xtzalij chic cuʼ i u tijoxelab; xe u rika que waric. Xu ʼijla i ma Pédro:&lt;p&gt; &lt;/p&gt;—¿N-xi chʼij ta bari xix cʼascʼat jun ʼor wuʼ? —xu bij—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.041', 'Chix cʼascʼat na, y chi tzʼonoj che i Dios man n-quix tzak ta chupam i mac. Katzij, yix piwanima quiwaj quix cʼascʼatic, xui-ri i chʼacul n-tu choʼab —xu bij.
', '', '&lt;span class="verse" id="Matt_26_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Chix cʼascʼat na, y chi tzʼonoj che i Dios man n-quix tzak ta chupam i mac. Katzij, yix piwanima quiwaj quix cʼascʼatic, xui-ri i chʼacul n-tu choʼab —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.042', 'Are ʼuri xtzalij chic ucamul chu tzʼonoxic che i Dios. Xu bij:
—Ta, we n-ʼus taj chi quelsax ni cʼax chi ʼo chinwach, we lic ʼo ni u chac quin chʼijo, ʼutz ʼuri ca ʼani chwe pacha i cawaj yet —xu bij.
', '', '&lt;span class="verse" id="Matt_26_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are ʼuri xtzalij chic ucamul chu tzʼonoxic che i Dios. Xu bij:&lt;p&gt; &lt;/p&gt;—Ta, we n-ʼus taj chi quelsax ni cʼax chi ʼo chinwach, we lic ʼo ni u chac quin chʼijo, ʼutz ʼuri ca ʼani chwe pacha i cawaj yet —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.043', 'Are xtzalij chalok, xe ʼe u rika tan chic u tijoxelab que war tan chic; i qui baʼwach ique lic waram craj. ', '', '&lt;span class="verse" id="Matt_26_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Are xtzalij chalok, xe ʼe u rika tan chic u tijoxelab que war tan chic; i qui baʼwach ique lic waram craj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.044', 'Ire xe u canaj canok, y xa ʼe chic; are uroxmul xa ʼe chu tzʼonoxic che i Dios, xak jun chi tzij xu bij chic che. ', '', '&lt;span class="verse" id="Matt_26_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Ire xe u canaj canok, y xa ʼe chic; are uroxmul xa ʼe chu tzʼonoxic che i Dios, xak jun chi tzij xu bij chic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.045', 'Teʼuri xa ʼe chic chila cuʼ u tijoxelab.
—Bay, chix warok; chix uxlanok —xu bij chique—. Ya xu rik i ʼor chi yin, chi in Achi aj Chicaj, quin jachtaj na piquiʼab i ajmaquib. ', '', '&lt;span class="verse" id="Matt_26_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Teʼuri xa ʼe chic chila cuʼ u tijoxelab.&lt;p&gt; &lt;/p&gt;—Bay, chix warok; chix uxlanok —xu bij chique—. Ya xu rik i ʼor chi yin, chi in Achi aj Chicaj, quin jachtaj na piquiʼab i ajmaquib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.046', 'Chix walijok. Joʼ. Ya xcʼun ire chi quin jachaw na piquiʼab i aj chʼoʼoj chwij —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Chix walijok. Joʼ. Ya xcʼun ire chi quin jachaw na piquiʼab i aj chʼoʼoj chwij —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.047', 'I Jesus cʼa ca tijin chi tzij are xupon i ma Júdas, chi jun chique i cablajuj u tijoxelab. Ire je rucʼam li uqʼuial winak; ʼo i cucʼam chʼichʼ, ʼo i cucʼam cheʼ. Ique je taktal li cumal i ajwab sacerdóte xak i nimak tak mamʼib que i aj Israel. ', '', '&lt;h2&gt;Wach u chapic i Jesus xqui ʼano&lt;/h2&gt;&lt;span class="verse" id="Matt_26_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;I Jesus cʼa ca tijin chi tzij are xupon i ma Júdas, chi jun chique i cablajuj u tijoxelab. Ire je rucʼam li uqʼuial winak; ʼo i cucʼam chʼichʼ, ʼo i cucʼam cheʼ. Ique je taktal li cumal i ajwab sacerdóte xak i nimak tak mamʼib que i aj Israel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.048', 'I ma Júdas, chi aj jachanel re i Jesus, u bim can chique wach i séña coʼon chique, cʼutbal re i Jesus: “Are i jun chi quin tzʼubaj, are ile,” u bim chique. “Chi chapa ʼuri,” u bim can chique.
', '', '&lt;span class="verse" id="Matt_26_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;I ma Júdas, chi aj jachanel re i Jesus, u bim can chique wach i séña coʼon chique, cʼutbal re i Jesus: “Are i jun chi quin tzʼubaj, are ile,” u bim chique. “Chi chapa ʼuri,” u bim can chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.049', 'I ma Júdas, are xuponic, xtejeb pan ruʼ i Jesus.
—Naʼab, in Tijonel —xu bij che. Y xa ʼe u tzʼubaj.
', '', '&lt;span class="verse" id="Matt_26_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;I ma Júdas, are xuponic, xtejeb pan ruʼ i Jesus.&lt;p&gt; &lt;/p&gt;—Naʼab, in Tijonel —xu bij che. Y xa ʼe u tzʼubaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.050', 'I Jesus xu bij che:
—Achi, ¿Wach cawaj chwe? —xu bij.
Are ʼuri i juban xe tejeb ruʼ i Jesus y xqui chapo; lic co i u chapic xqui ʼano.
', '', '&lt;span class="verse" id="Matt_26_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Achi, ¿Wach cawaj chwe? —xu bij.&lt;p&gt; &lt;/p&gt;Are ʼuri i juban xe tejeb ruʼ i Jesus y xqui chapo; lic co i u chapic xqui ʼano.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.051', 'Are ʼuri, jun chique i rachiʼil i Jesus xresaj li u chʼichʼ y xu cʼatzij u xiquin i aj takon re i ʼatz laj sacerdóte. ', '', '&lt;span class="verse" id="Matt_26_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Are ʼuri, jun chique i rachiʼil i Jesus xresaj li u chʼichʼ y xu cʼatzij u xiquin i aj takon re i ʼatz laj sacerdóte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.052', 'I Jesus xu bij che:
—Cha cʼolo a chʼichʼ pa rachoch, man niʼpa i que ʼanaw chʼoʼoj ruʼ chʼichʼ, xak ruʼ chʼichʼ que camsax wi —xu bij che—. ', '', '&lt;span class="verse" id="Matt_26_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Cha cʼolo a chʼichʼ pa rachoch, man niʼpa i que ʼanaw chʼoʼoj ruʼ chʼichʼ, xak ruʼ chʼichʼ que camsax wi —xu bij che—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.053', '¿N-awetaʼam ta bari, chi we ta cwaj yin, quin tzʼonoj che in Kajaw y cu tak li uqʼuial ángel chin toʼic? ¡Mas ne chuwach i seténta mil que u tak neli-ri wuʼ! ', '', '&lt;span class="verse" id="Matt_26_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;¿N-awetaʼam ta bari, chi we ta cwaj yin, quin tzʼonoj che in Kajaw y cu tak li uqʼuial ángel chin toʼic? ¡Mas ne chuwach i seténta mil que u tak neli-ri wuʼ! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.054', 'Xui-ri, we ta quin ʼan yin ile, n-queʼel ta ʼuri pacha tzʼibtal can chupam u wuj i Dios. Lic ʼo ni u chac queʼel na pacha i tzʼibtal canok —xu bij che.
', '', '&lt;span class="verse" id="Matt_26_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;Xui-ri, we ta quin ʼan yin ile, n-queʼel ta ʼuri pacha tzʼibtal can chupam u wuj i Dios. Lic ʼo ni u chac queʼel na pacha i tzʼibtal canok —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.055', 'Teʼuri xu bij chique i winak:
—¿Wuchac xix cʼun yix ruʼ i chʼichʼ xak ruʼ i cheʼ, qui cʼambej bi we, pacha qui ʼan che jun iliʼom? Yin ʼij ʼij in cul chixol yix pa rachoch i Dios, quin tijin chu cʼutic chique i winak, y n-xin i chap taj. ', '', '&lt;span class="verse" id="Matt_26_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique i winak:&lt;p&gt; &lt;/p&gt;—¿Wuchac xix cʼun yix ruʼ i chʼichʼ xak ruʼ i cheʼ, qui cʼambej bi we, pacha qui ʼan che jun iliʼom? Yin ʼij ʼij in cul chixol yix pa rachoch i Dios, quin tijin chu cʼutic chique i winak, y n-xin i chap taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.056', 'Xui-ri, ʼo ni u chac ca ʼani iri chwe, pacha qui tzʼibam can i ajbil u tzij i Dios ujer —xu bij chique.
Teʼuri, niʼpa i u tijoxelab i Jesus xqui canaj canok y xe ʼanmajic.
', '', '&lt;span class="verse" id="Matt_26_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Xui-ri, ʼo ni u chac ca ʼani iri chwe, pacha qui tzʼibam can i ajbil u tzij i Dios ujer —xu bij chique.&lt;p&gt; &lt;/p&gt;Teʼuri, niʼpa i u tijoxelab i Jesus xqui canaj canok y xe ʼanmajic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.057', 'Niʼpa i qui chapom i Jesus, xqui cʼam bi che i rachoch i mam Caifas, chi ʼatz laj sacerdóte. Chila qui molom wi quib i je tijonel re i ʼatbal tzij xak i nimak tak mamʼib. ', '', '&lt;h2&gt;Wach i cʼax xu tij i Jesus piquiʼab i nucʼbal tʼisbal&lt;/h2&gt;&lt;span class="verse" id="Matt_26_57"&gt;&lt;strong class="verseno"&gt;57&lt;/strong&gt;&amp;nbsp;Niʼpa i qui chapom i Jesus, xqui cʼam bi che i rachoch i mam Caifas, chi ʼatz laj sacerdóte. Chila qui molom wi quib i je tijonel re i ʼatbal tzij xak i nimak tak mamʼib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.058', 'I ma Pédro, toʼ chi naj xterej bi chiquij, cʼa xupon chuwaja re i rachoch i mam Caifas. Xoc bi chuwaja, chila xcubi wi cuʼ i policía, cu takejbej-re wach ca ʼani che i Jesus.
', '', '&lt;span class="verse" id="Matt_26_58"&gt;&lt;strong class="verseno"&gt;58&lt;/strong&gt;&amp;nbsp;I ma Pédro, toʼ chi naj xterej bi chiquij, cʼa xupon chuwaja re i rachoch i mam Caifas. Xoc bi chuwaja, chila xcubi wi cuʼ i policía, cu takejbej-re wach ca ʼani che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.059', 'I cajʼatzil i sacerdóte, xak i nimak tak mamʼib, xak ronojel i nucʼbal tʼisbal, ʼis xqui tzucuj u mac i Jesus. Cacaj ique quiqui coj u mac chuwach i ʼatbal tzij, tupu n-katzij taj, xa camsabel-re cacaj ique. ', '', '&lt;span class="verse" id="Matt_26_59"&gt;&lt;strong class="verseno"&gt;59&lt;/strong&gt;&amp;nbsp;I cajʼatzil i sacerdóte, xak i nimak tak mamʼib, xak ronojel i nucʼbal tʼisbal, ʼis xqui tzucuj u mac i Jesus. Cacaj ique quiqui coj u mac chuwach i ʼatbal tzij, tupu n-katzij taj, xa camsabel-re cacaj ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.060', 'Lic n-ta jun u mac xqui rik chirij, tupu je qʼui xe cʼunic y xqui bij mentir chirij. Pa qʼuisbal-re, xe upon chic queb testígo chi xa mentir quiqui bij. ', '', '&lt;span class="verse" id="Matt_26_60"&gt;&lt;strong class="verseno"&gt;60&lt;/strong&gt;&amp;nbsp;Lic n-ta jun u mac xqui rik chirij, tupu je qʼui xe cʼunic y xqui bij mentir chirij. Pa qʼuisbal-re, xe upon chic queb testígo chi xa mentir quiqui bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.061', 'Xqui bij ique:
—I achi-ri xu bij: “Yin quin tiqui chu wulijsaxic i rachoch i Dios, teʼuri che oxib ʼij quin walijsaj wi chic,” xu bij —xe cha.
', '', '&lt;span class="verse" id="Matt_26_61"&gt;&lt;strong class="verseno"&gt;61&lt;/strong&gt;&amp;nbsp;Xqui bij ique:&lt;p&gt; &lt;/p&gt;—I achi-ri xu bij: “Yin quin tiqui chu wulijsaxic i rachoch i Dios, teʼuri che oxib ʼij quin walijsaj wi chic,” xu bij —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.062', 'Are ʼuri i ʼatz laj sacerdóte xwalijic, y xu bij che i Jesus:
—Yet, ¿lic n-ca laʼ tu wach chique? ¿Wach usucʼ ile quiqui bij chawij? —xu bij.
', '', '&lt;span class="verse" id="Matt_26_62"&gt;&lt;strong class="verseno"&gt;62&lt;/strong&gt;&amp;nbsp;Are ʼuri i ʼatz laj sacerdóte xwalijic, y xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—Yet, ¿lic n-ca laʼ tu wach chique? ¿Wach usucʼ ile quiqui bij chawij? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.063', 'I Jesus lic n-xchʼaw taj. Are ʼuri i ʼatz laj sacerdóte xu bij:
—Yin, chuwach i Dios cʼaslic, quin ya chakul: cha bij i sak laj tzij chake; cha bij ʼuri we yet at Crísto, chi u Cʼojol i Dios —xu bij che.
', '', '&lt;span class="verse" id="Matt_26_63"&gt;&lt;strong class="verseno"&gt;63&lt;/strong&gt;&amp;nbsp;I Jesus lic n-xchʼaw taj. Are ʼuri i ʼatz laj sacerdóte xu bij:&lt;p&gt; &lt;/p&gt;—Yin, chuwach i Dios cʼaslic, quin ya chakul: cha bij i sak laj tzij chake; cha bij ʼuri we yet at Crísto, chi u Cʼojol i Dios —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.064', 'I Jesus xu bij:
—Katzij i xa bij. Xak quin iwil ni yin chi in Achi aj Chicaj, in cul na pu wikabim i Dios chi ʼatz u choʼab; xak quin iwil na quin cʼun chali pa sutzʼ chicaj —xu bij chique.
', '', '&lt;span class="verse" id="Matt_26_64"&gt;&lt;strong class="verseno"&gt;64&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij i xa bij. Xak quin iwil ni yin chi in Achi aj Chicaj, in cul na pu wikabim i Dios chi ʼatz u choʼab; xak quin iwil na quin cʼun chali pa sutzʼ chicaj —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.065', 'Are ʼuri i ʼatz laj sacerdóte xu rakchʼij u ʼuʼ, cʼutbal-re i roywal. Xu bij:
—¡Tzel cu ta i Dios ile! ¡Ya n-ta chic u chac we ʼo juban chic col qui bij u mac! Ya xi ta yix waral wach i itzel tzij xu bij. ', '', '&lt;span class="verse" id="Matt_26_65"&gt;&lt;strong class="verseno"&gt;65&lt;/strong&gt;&amp;nbsp;Are ʼuri i ʼatz laj sacerdóte xu rakchʼij u ʼuʼ, cʼutbal-re i roywal. Xu bij:&lt;p&gt; &lt;/p&gt;—¡Tzel cu ta i Dios ile! ¡Ya n-ta chic u chac we ʼo juban chic col qui bij u mac! Ya xi ta yix waral wach i itzel tzij xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.066', '¿Wach qui chʼob yix che ile? —xu bij chique i rachiʼil.
I juban je ʼo ruʼ xqui bij:
—Ire lic ʼo u mac; cu maj che chi ca camsaxic —xe cha.
', '', '&lt;span class="verse" id="Matt_26_66"&gt;&lt;strong class="verseno"&gt;66&lt;/strong&gt;&amp;nbsp;¿Wach qui chʼob yix che ile? —xu bij chique i rachiʼil.&lt;p&gt; &lt;/p&gt;I juban je ʼo ruʼ xqui bij:&lt;p&gt; &lt;/p&gt;—Ire lic ʼo u mac; cu maj che chi ca camsaxic —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.067', 'Are ʼuri xqui chubaj qui cʼaxaj chu palaj y xqui chʼayo. ʼO juban chic xqui chʼay chu palaj ', '', '&lt;span class="verse" id="Matt_26_67"&gt;&lt;strong class="verseno"&gt;67&lt;/strong&gt;&amp;nbsp;Are ʼuri xqui chubaj qui cʼaxaj chu palaj y xqui chʼayo. ʼO juban chic xqui chʼay chu palaj &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.068', 'y xqui bij che:
—Yet chi at Crísto, cha bij ʼu chake pachin xat chʼayawic —xe cha che.
', '', '&lt;span class="verse" id="Matt_26_68"&gt;&lt;strong class="verseno"&gt;68&lt;/strong&gt;&amp;nbsp;y xqui bij che:&lt;p&gt; &lt;/p&gt;—Yet chi at Crísto, cha bij ʼu chake pachin xat chʼayawic —xe cha che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.069', 'Tzʼakat are xqui ʼan ile, i ma Pédro cul chuwaja. Are ʼuri, xupon jun ali, aj chac pa rachoch i cajʼatzil i sacerdóte; xu bij che:
—Xak yet at rachiʼil i ma Jesus aj Galiléa —xu bij.
', '', '&lt;h2&gt;I ma Pédro xruwaj u wach i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_26_69"&gt;&lt;strong class="verseno"&gt;69&lt;/strong&gt;&amp;nbsp;Tzʼakat are xqui ʼan ile, i ma Pédro cul chuwaja. Are ʼuri, xupon jun ali, aj chac pa rachoch i cajʼatzil i sacerdóte; xu bij che:&lt;p&gt; &lt;/p&gt;—Xak yet at rachiʼil i ma Jesus aj Galiléa —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.070', 'Xui-ri, i ma Pédro xruwaj u wach chiquiwach conojel:
—N-wetaʼam ti yin wach i ca bij —xu bij che i ali.
', '', '&lt;span class="verse" id="Matt_26_70"&gt;&lt;strong class="verseno"&gt;70&lt;/strong&gt;&amp;nbsp;Xui-ri, i ma Pédro xruwaj u wach chiquiwach conojel:&lt;p&gt; &lt;/p&gt;—N-wetaʼam ti yin wach i ca bij —xu bij che i ali.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.071', 'I ma Pédro ya ca ʼec, ʼo chic che uchibe. Are ʼuri xa ʼiltaj chic rumal jun chic. Xu bij ʼuri chique i je ʼo chila:
—I achi-le, rachiʼil i ma Jesus chi aj Nazaret —coʼono.
', '', '&lt;span class="verse" id="Matt_26_71"&gt;&lt;strong class="verseno"&gt;71&lt;/strong&gt;&amp;nbsp;I ma Pédro ya ca ʼec, ʼo chic che uchibe. Are ʼuri xa ʼiltaj chic rumal jun chic. Xu bij ʼuri chique i je ʼo chila:&lt;p&gt; &lt;/p&gt;—I achi-le, rachiʼil i ma Jesus chi aj Nazaret —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.072', 'I ma Pédro xu bij chic chi n-retaʼam tu wach. Xu cuxtaj u bi i Dios puwi u tzij y xu bij:
—Lic n-wetaʼam tu wach i achi-le —xcha.
', '', '&lt;span class="verse" id="Matt_26_72"&gt;&lt;strong class="verseno"&gt;72&lt;/strong&gt;&amp;nbsp;I ma Pédro xu bij chic chi n-retaʼam tu wach. Xu cuxtaj u bi i Dios puwi u tzij y xu bij:&lt;p&gt; &lt;/p&gt;—Lic n-wetaʼam tu wach i achi-le —xcha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.073', 'Xoʼon chupan jubiʼ, niʼpa i je ʼo chila xe tejeb ruʼ y xqui bij:
—Katzij, xak yet at cachiʼil ique. Ca ʼalijin che a chʼawbal —xqui bij che.
', '', '&lt;span class="verse" id="Matt_26_73"&gt;&lt;strong class="verseno"&gt;73&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, niʼpa i je ʼo chila xe tejeb ruʼ y xqui bij:&lt;p&gt; &lt;/p&gt;—Katzij, xak yet at cachiʼil ique. Ca ʼalijin che a chʼawbal —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.074', 'Teʼuri i ma Pédro xu bij chique:
—Chu ya ni tojpen i Dios pinwi, we n-katzij ti quin bij. Lic n-wetaʼam tu wach i achi-le —xu bij.
Are ʼuri xchʼaw jun teren. ', '', '&lt;span class="verse" id="Matt_26_74"&gt;&lt;strong class="verseno"&gt;74&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Pédro xu bij chique:&lt;p&gt; &lt;/p&gt;—Chu ya ni tojpen i Dios pinwi, we n-katzij ti quin bij. Lic n-wetaʼam tu wach i achi-le —xu bij.&lt;p&gt; &lt;/p&gt;Are ʼuri xchʼaw jun teren. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '26.075', 'Juntir xcʼun chu cʼux wach i bital che rumal i Jesus, man u bim che: “Are maja ca chʼaw i acʼ re u sakirbal, quin awuwaj na oxmul,” u bim i Jesus che. Teʼuri i ma Pédro xel chila, lic cʼax xu na u cʼux, lic xoʼ rumal.
', '', '&lt;span class="verse" id="Matt_26_75"&gt;&lt;strong class="verseno"&gt;75&lt;/strong&gt;&amp;nbsp;Juntir xcʼun chu cʼux wach i bital che rumal i Jesus, man u bim che: “Are maja ca chʼaw i acʼ re u sakirbal, quin awuwaj na oxmul,” u bim i Jesus che. Teʼuri i ma Pédro xel chila, lic cʼax xu na u cʼux, lic xoʼ rumal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.001', 'Are xsakiric, i cajʼatzil i sacerdóte xak i nimak tak mamʼib que i aj Israel, ique xjunamtaj qui noʼoj wach u camsaxic i Jesus quiqui ʼano. ', '', '&lt;h2&gt;Wach u jachic i Jesus puʼab i ma Piláto&lt;/h2&gt;&lt;span class="verse" id="Matt_27_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xsakiric, i cajʼatzil i sacerdóte xak i nimak tak mamʼib que i aj Israel, ique xjunamtaj qui noʼoj wach u camsaxic i Jesus quiqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.002', 'Xqui xim i Jesus y xqui cʼam bi ruʼ i ma Piláto chi gobernador, y xqui jach puʼab.
', '', '&lt;span class="verse" id="Matt_27_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Xqui xim i Jesus y xqui cʼam bi ruʼ i ma Piláto chi gobernador, y xqui jach puʼab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.003', 'I ma Júdas, chi cʼayiwnak i Jesus, are xrilo chi ca camsax na, lic xbison che wach u ʼanom. Are ʼuri xa ʼe u tzalij chic i tréinta sak pwak chique i cajʼatzil i sacerdóte, tak i nimak tak mamʼib. ', '', '&lt;h2&gt;Wach u camic i ma Júdas&lt;/h2&gt;&lt;span class="verse" id="Matt_27_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I ma Júdas, chi cʼayiwnak i Jesus, are xrilo chi ca camsax na, lic xbison che wach u ʼanom. Are ʼuri xa ʼe u tzalij chic i tréinta sak pwak chique i cajʼatzil i sacerdóte, tak i nimak tak mamʼib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.004', 'Xu bij chique:
—Lic in macun-nak; in jachom jun achi che i camic chi n-tu mac —xu bij.
Ique xqui laʼ u wach che:
—¿Bi ke-yoj che? —xe cha—. ¡Lic awe yet pa wach wi ile! —xe cha che.
', '', '&lt;span class="verse" id="Matt_27_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—Lic in macun-nak; in jachom jun achi che i camic chi n-tu mac —xu bij.&lt;p&gt; &lt;/p&gt;Ique xqui laʼ u wach che:&lt;p&gt; &lt;/p&gt;—¿Bi ke-yoj che? —xe cha—. ¡Lic awe yet pa wach wi ile! —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.005', 'Teʼuri i ma Júdas xu jopij i sak pwak pa rachoch i Dios y xel bic. Are ʼuri xa ʼe u jitzʼaj rib.
', '', '&lt;span class="verse" id="Matt_27_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Júdas xu jopij i sak pwak pa rachoch i Dios y xel bic. Are ʼuri xa ʼe u jitzʼaj rib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.006', 'I cajʼatzil i sacerdóte xqui mol i mer y xqui bij:
—I mer-i, n-ʼus taj caka ya ruʼ i re i Dios; i ʼatbal tzij re i mam Moises n-cu ya taj, man i mer-i are i rajil u quiqʼuel jun winak —xqui bij.
', '', '&lt;span class="verse" id="Matt_27_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I cajʼatzil i sacerdóte xqui mol i mer y xqui bij:&lt;p&gt; &lt;/p&gt;—I mer-i, n-ʼus taj caka ya ruʼ i re i Dios; i ʼatbal tzij re i mam Moises n-cu ya taj, man i mer-i are i rajil u quiqʼuel jun winak —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.007', 'Teʼuri xjunamtaj qui noʼoj quiqui loʼ jun ulew re i qui camposant niʼpa i je aj naj que cam pa Jerusalen. I ulew chi xqui loʼo, xa are pa ca ʼan wi boj ujer; “Ulew re ʼAnbal Boj” u bi. ', '', '&lt;span class="verse" id="Matt_27_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Teʼuri xjunamtaj qui noʼoj quiqui loʼ jun ulew re i qui camposant niʼpa i je aj naj que cam pa Jerusalen. I ulew chi xqui loʼo, xa are pa ca ʼan wi boj ujer; “Ulew re ʼAnbal Boj” u bi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.008', 'Xui-ri, xqui jalwachij u bi i ulew; “Ulew Quicʼ” xqui bij che, y cʼa are ʼo wi woʼor. ', '', '&lt;span class="verse" id="Matt_27_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xui-ri, xqui jalwachij u bi i ulew; “Ulew Quicʼ” xqui bij che, y cʼa are ʼo wi woʼor. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.009', 'Queje ile xeʼelok pacha u bim can i mam Jeremías, chi ajbil u tzij i Dios ujer. Ire u bim canok: “Xqui cʼam bi i tréinta sak pwak chi are i rajil bital che cumal i aj Israel; ', '', '&lt;span class="verse" id="Matt_27_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Queje ile xeʼelok pacha u bim can i mam Jeremías, chi ajbil u tzij i Dios ujer. Ire u bim canok: “Xqui cʼam bi i tréinta sak pwak chi are i rajil bital che cumal i aj Israel; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.010', 'y ruʼ i mer-le xqui loʼ i ulew pa ca ʼan wi boj ujer. Xeʼelok pacha u bim can i Kajwal chwe.” Queje ile tzʼibtal canok.
', '', '&lt;span class="verse" id="Matt_27_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;y ruʼ i mer-le xqui loʼ i ulew pa ca ʼan wi boj ujer. Xeʼelok pacha u bim can i Kajwal chwe.” Queje ile tzʼibtal canok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.011', 'I Jesus xcʼam bi chuwach i gobernador. Ire xu tzʼonoj che i Jesus:
—¿Yet at ʼatol tzij que i aj Israel winak? —coʼono.
I Jesus xu bij:
—Are ile, pacha i xa bij yet —coʼono.
', '', '&lt;h2&gt;I u tzʼonbal i ma Piláto che i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_27_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I Jesus xcʼam bi chuwach i gobernador. Ire xu tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;—¿Yet at ʼatol tzij que i aj Israel winak? —coʼono.&lt;p&gt; &lt;/p&gt;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Are ile, pacha i xa bij yet —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.012', 'Xui-ri, are xocsax u mac cumal i ajwab sacerdóte, xak i nimak tak mamʼib, i Jesus n-ta xu bij. ', '', '&lt;span class="verse" id="Matt_27_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xui-ri, are xocsax u mac cumal i ajwab sacerdóte, xak i nimak tak mamʼib, i Jesus n-ta xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.013', 'Are ʼuri i ma Piláto xu bij che i Jesus:
—¿N-ca ta ta-bari wach tak quiqui bij chawij? —coʼono.
', '', '&lt;span class="verse" id="Matt_27_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Piláto xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¿N-ca ta ta-bari wach tak quiqui bij chawij? —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.014', 'Xui-ri, i Jesus xak n-ta xu bij. Are ʼuri, i gobernador lic xu bisoj.
', '', '&lt;span class="verse" id="Matt_27_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xui-ri, i Jesus xak n-ta xu bij. Are ʼuri, i gobernador lic xu bisoj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.015', 'Junab chi junab, are ʼo i nimaʼij páscua, i gobernador ʼo puʼab croʼtaj jun prex, pachique quiqui cha i winak. ', '', '&lt;h2&gt;I winak lic xqui tzʼonoj u camsaxic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_27_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Junab chi junab, are ʼo i nimaʼij páscua, i gobernador ʼo puʼab croʼtaj jun prex, pachique quiqui cha i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.016', 'ʼO jun prex, ma Barabas u bi, ire lic ʼetamam u mac cumal conojel i winak. ', '', '&lt;span class="verse" id="Matt_27_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;ʼO jun prex, ma Barabas u bi, ire lic ʼetamam u mac cumal conojel i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.017', 'Are je moltajnak chic i winak, i ma Piláto xu tzʼonoj chique:
—¿Pachique quiwaj yix cwoʼtaj bi chiwe? ¿Quiwaj i ma Barabas? ¿O quiwaj i ma Jesus chi “Crísto” ca bix che? —xu bij chique.
', '', '&lt;span class="verse" id="Matt_27_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are je moltajnak chic i winak, i ma Piláto xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Pachique quiwaj yix cwoʼtaj bi chiwe? ¿Quiwaj i ma Barabas? ¿O quiwaj i ma Jesus chi “Crísto” ca bix che? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.018', 'Ire retaʼam chic chi xa tzel quiquil i Jesus; xa rumal ne ile qui cʼamom lo ruʼ.
', '', '&lt;span class="verse" id="Matt_27_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Ire retaʼam chic chi xa tzel quiquil i Jesus; xa rumal ne ile qui cʼamom lo ruʼ.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.019', 'I ma Piláto, are cul pu qʼuijibal, i rixokil xu tak li u bixquil ruʼ; xu bij che: “Ma min awib che u camsaxic i achi-le, man ire n-tu mac. Yin chaʼab xin ʼan jun achicʼ lic xibinʼib rumal ni ire-le y quin cʼachir rumal.” (Queje ile xbix che.)
', '', '&lt;span class="verse" id="Matt_27_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I ma Piláto, are cul pu qʼuijibal, i rixokil xu tak li u bixquil ruʼ; xu bij che: “Ma min awib che u camsaxic i achi-le, man ire n-tu mac. Yin chaʼab xin ʼan jun achicʼ lic xibinʼib rumal ni ire-le y quin cʼachir rumal.” (Queje ile xbix che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.020', 'Xui-ri i cajʼatzil i sacerdóte, xak i nimak tak mamʼib, xe qui tun i winak chi quiqui tzʼonoj i roʼtaxic i ma Barabas, xak quiqui tzʼonoj u camsaxic i Jesus. Queje i qui tunic ile xqui ʼano. ', '', '&lt;span class="verse" id="Matt_27_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xui-ri i cajʼatzil i sacerdóte, xak i nimak tak mamʼib, xe qui tun i winak chi quiqui tzʼonoj i roʼtaxic i ma Barabas, xak quiqui tzʼonoj u camsaxic i Jesus. Queje i qui tunic ile xqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.021', 'I gobernador xu tzʼonoj chic chique i winak:
—¿Pachique chique i je queb quiwaj yix quin tak nubi roʼtaxic? —xu bij.
—A-Barabas —xe cha.
', '', '&lt;span class="verse" id="Matt_27_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I gobernador xu tzʼonoj chic chique i winak:&lt;p&gt; &lt;/p&gt;—¿Pachique chique i je queb quiwaj yix quin tak nubi roʼtaxic? —xu bij.&lt;p&gt; &lt;/p&gt;—A-Barabas —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.022', 'I ma Piláto xu tzʼonoj chique:
—¿Wach quin ʼan ʼuri che i Jesus-i chi “Cristo” ca bix che? —coʼono.
Conojel xqui bij:
—¡Cha camsaj chuwach cruz! —xe cha.
', '', '&lt;span class="verse" id="Matt_27_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I ma Piláto xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Wach quin ʼan ʼuri che i Jesus-i chi “Cristo” ca bix che? —coʼono.&lt;p&gt; &lt;/p&gt;Conojel xqui bij:&lt;p&gt; &lt;/p&gt;—¡Cha camsaj chuwach cruz! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.023', '—¿Wach u mac ʼuri u ʼanom? —coʼono.
Ique xa xe siqʼuin tan chic; xqui bij:
—¡Cha camsaj chuwach cruz! —xe cha.
', '', '&lt;span class="verse" id="Matt_27_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;—¿Wach u mac ʼuri u ʼanom? —coʼono.&lt;p&gt; &lt;/p&gt;Ique xa xe siqʼuin tan chic; xqui bij:&lt;p&gt; &lt;/p&gt;—¡Cha camsaj chuwach cruz! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.024', 'Xril i ma Piláto ʼuri chi n-tu chac cu tzʼonoj mas tzij chique. Ique toʼ chʼokowic quiqui ʼano. Rumal-i ire xresaj rib chupam; xu tak u cʼamic jun palangan ya, y xu chʼaj u ʼab chiquiwach. Xu bij chique:
—N-in mac ti yin we qui camsaj i achi-ri chi n-tu mac. Chiwach yix ʼo wi wach qui ʼan che —xu bij chique.
', '', '&lt;span class="verse" id="Matt_27_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xril i ma Piláto ʼuri chi n-tu chac cu tzʼonoj mas tzij chique. Ique toʼ chʼokowic quiqui ʼano. Rumal-i ire xresaj rib chupam; xu tak u cʼamic jun palangan ya, y xu chʼaj u ʼab chiquiwach. Xu bij chique:&lt;p&gt; &lt;/p&gt;—N-in mac ti yin we qui camsaj i achi-ri chi n-tu mac. Chiwach yix ʼo wi wach qui ʼan che —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.025', 'Conojel i winak xqui laʼ u wach che:
—¡Pakawi yoj ca pe wi i tojpen che u camic i achi-ri! ¡Xak piquiwi i kacʼal! —xqui bij.
', '', '&lt;span class="verse" id="Matt_27_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Conojel i winak xqui laʼ u wach che:&lt;p&gt; &lt;/p&gt;—¡Pakawi yoj ca pe wi i tojpen che u camic i achi-ri! ¡Xak piquiwi i kacʼal! —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.026', 'Are ʼuri i ma Piláto xroʼtaj bi a-Barabas; teʼuri xu tak u yaʼic u loʼxic i Jesus y xu jach bi piquiʼab i que camsaw chuwach cruz.
', '', '&lt;span class="verse" id="Matt_27_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Piláto xroʼtaj bi a-Barabas; teʼuri xu tak u yaʼic u loʼxic i Jesus y xu jach bi piquiʼab i que camsaw chuwach cruz.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.027', 'Teʼuri i u soldádo i gobernador xqui cʼam bi i Jesus pa palácio. Chila conojel i cachiʼil xqui mol quib chirij chuwach. ', '', '&lt;h2&gt;Wach u yoʼyaxic i Jesus xqui ʼan i soldádo&lt;/h2&gt;&lt;span class="verse" id="Matt_27_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri i u soldádo i gobernador xqui cʼam bi i Jesus pa palácio. Chila conojel i cachiʼil xqui mol quib chirij chuwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.028', 'Xquesaj u ʼuʼ y xqui coj jun chom laj u ʼuʼ cak. ', '', '&lt;span class="verse" id="Matt_27_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Xquesaj u ʼuʼ y xqui coj jun chom laj u ʼuʼ cak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.029', 'Teʼuri xqui coj jun sotosic qʼuix che u jolom, y xqui ya jun cheʼ puʼab wikabim. Teʼuri xe xuqui chuwach, y xe oc chu yoʼyaxic; xqui bij:
—¡Naj u ʼij i ʼatol tzij que i aj Israel! —xqui bij.
', '', '&lt;span class="verse" id="Matt_27_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Teʼuri xqui coj jun sotosic qʼuix che u jolom, y xqui ya jun cheʼ puʼab wikabim. Teʼuri xe xuqui chuwach, y xe oc chu yoʼyaxic; xqui bij:&lt;p&gt; &lt;/p&gt;—¡Naj u ʼij i ʼatol tzij que i aj Israel! —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.030', 'Xqui chubaj qui cʼaxaj chirij, xak xqui maj i cheʼ che, chi qui yom puʼab, y xqui chʼay u jolom ruʼ. ', '', '&lt;span class="verse" id="Matt_27_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xqui chubaj qui cʼaxaj chirij, xak xqui maj i cheʼ che, chi qui yom puʼab, y xqui chʼay u jolom ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.031', 'Are xe utzin chu yoʼyaxic, xquesaj i cʼul cak chi qui cojom che, y xqui coj chic i u ʼuʼ ire. Teʼuri xqui cʼam bic, xe ʼe chu camsaxic chuwach cruz.
', '', '&lt;span class="verse" id="Matt_27_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Are xe utzin chu yoʼyaxic, xquesaj i cʼul cak chi qui cojom che, y xqui coj chic i u ʼuʼ ire. Teʼuri xqui cʼam bic, xe ʼe chu camsaxic chuwach cruz.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.032', 'Are que tijin chi be, xqui cʼulaj jun achi aj Ciréne, chi ma Simon u bi. Xqui ʼan pen che, xu cʼam bi u cruz i Jesus. ', '', '&lt;h2&gt;Wach u ripic i Jesus chuwach cruz&lt;/h2&gt;&lt;span class="verse" id="Matt_27_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Are que tijin chi be, xqui cʼulaj jun achi aj Ciréne, chi ma Simon u bi. Xqui ʼan pen che, xu cʼam bi u cruz i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.033', 'Queje ile xe upon che jun jyub, Gólgota u bi; i usucʼ i tzij-le queʼelok: “jyub pacha u jolom anma.” ', '', '&lt;span class="verse" id="Matt_27_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Queje ile xe upon che jun jyub, Gólgota u bi; i usucʼ i tzij-le queʼelok: “jyub pacha u jolom anma.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.034', 'Are xe upon chila, xqui sujuj jubiʼ víno che i Jesus, chi ʼo cunbal cʼa che, tobal na re u cʼaxcʼol. I Jesus, are xu naʼo, n-xraj taj.
', '', '&lt;span class="verse" id="Matt_27_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Are xe upon chila, xqui sujuj jubiʼ víno che i Jesus, chi ʼo cunbal cʼa che, tobal na re u cʼaxcʼol. I Jesus, are xu naʼo, n-xraj taj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.035', 'Are qui ripom chic chuwach i cruz, i soldádo xe oc chi saquinic chirij u ʼuʼ i Jesus, ʼilbal-re pachin ca chʼacaw bi chique. Xeʼel ʼuri pacha u bim can i ajbil u tzij i Dios ujer. Xu bij ire: “Xqui jach na in ʼuʼ chiquiwach; xe saquin chirij in ʼuʼ, pachin ca chʼacaw nubic.” Queje ile u tzʼibam canok. ', '', '&lt;span class="verse" id="Matt_27_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Are qui ripom chic chuwach i cruz, i soldádo xe oc chi saquinic chirij u ʼuʼ i Jesus, ʼilbal-re pachin ca chʼacaw bi chique. Xeʼel ʼuri pacha u bim can i ajbil u tzij i Dios ujer. Xu bij ire: “Xqui jach na in ʼuʼ chiquiwach; xe saquin chirij in ʼuʼ, pachin ca chʼacaw nubic.” Queje ile u tzʼibam canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.036', 'Teʼuri xe cubi chila chu chajixic, tzʼakat ca camic. ', '', '&lt;span class="verse" id="Matt_27_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri xe cubi chila chu chajixic, tzʼakat ca camic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.037', 'Puwi ʼan i cruz xqui tzʼibaj juban tzij, ʼalijinsabal-re wach u mac. Cu bij i tzʼib: “Are iri, are i Jesus, chi ʼatol tzij que i aj Israel winak,” coʼono.
', '', '&lt;span class="verse" id="Matt_27_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Puwi ʼan i cruz xqui tzʼibaj juban tzij, ʼalijinsabal-re wach u mac. Cu bij i tzʼib: “Are iri, are i Jesus, chi ʼatol tzij que i aj Israel winak,” coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.038', 'Xak je ʼo queb iliʼomab xe qui rip chuwach qui cruz, junam ruʼ i Jesus. Jun xqʼuiji pu wikabim, y jun pu moxim. ', '', '&lt;span class="verse" id="Matt_27_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Xak je ʼo queb iliʼomab xe qui rip chuwach qui cruz, junam ruʼ i Jesus. Jun xqʼuiji pu wikabim, y jun pu moxim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.039', 'Niʼpa i que icʼaw chila, xqui yoʼya. Quiqui sutsa qui jolom ', '', '&lt;span class="verse" id="Matt_27_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Niʼpa i que icʼaw chila, xqui yoʼya. Quiqui sutsa qui jolom &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.040', 'y quiqui bij:
—Yet a bim na chi ca wulijsaj ni rachoch i Dios, y ca yac chic churox ʼij. ¡Bay, cha to ʼuri awib! We yet at u Cʼojol i Dios, cha kajsaj li awib chuwach i cruz —xe cha.
', '', '&lt;span class="verse" id="Matt_27_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;y quiqui bij:&lt;p&gt; &lt;/p&gt;—Yet a bim na chi ca wulijsaj ni rachoch i Dios, y ca yac chic churox ʼij. ¡Bay, cha to ʼuri awib! We yet at u Cʼojol i Dios, cha kajsaj li awib chuwach i cruz —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.041', 'Xak queje xqui ʼan i ajwab sacerdóte, xak i tijonel re i ʼatbal tzij, xak queje i aj Fariséo, xak i nimak tak mamʼib, lic xqui yoʼya na. Ique xqui bij chiquiwach:
', '', '&lt;span class="verse" id="Matt_27_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Xak queje xqui ʼan i ajwab sacerdóte, xak i tijonel re i ʼatbal tzij, xak queje i aj Fariséo, xak i nimak tak mamʼib, lic xqui yoʼya na. Ique xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.042', '—Ire-le je qʼui xe u to na, xui-ri n-ca tiqui ta chu toʼic rib. Ire, we lic are i ka ʼatol tzij yoj chi oj aj Israel, cha kaj lo ʼuri chuwach i cruz, teʼuri caka coj na. ', '', '&lt;span class="verse" id="Matt_27_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;—Ire-le je qʼui xe u to na, xui-ri n-ca tiqui ta chu toʼic rib. Ire, we lic are i ka ʼatol tzij yoj chi oj aj Israel, cha kaj lo ʼuri chuwach i cruz, teʼuri caka coj na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.043', 'Ire u cubam u cʼux che i Dios, xak u bim chi ire u Cʼojol i Dios; bay, i Dios ʼut chu to na we cʼax cu naʼo —xe cha.
', '', '&lt;span class="verse" id="Matt_27_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Ire u cubam u cʼux che i Dios, xak u bim chi ire u Cʼojol i Dios; bay, i Dios ʼut chu to na we cʼax cu naʼo —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.044', 'Xak i iliʼomab chi je riptal ruʼ ire, xak ique quiqui yoʼya panok.
', '', '&lt;span class="verse" id="Matt_27_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Xak i iliʼomab chi je riptal ruʼ ire, xak ique quiqui yoʼya panok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.045', 'Are tiqʼuil ʼij, xoʼon ʼekum ronojel u wach i jyub taʼaj; queje xoʼon ile cʼa a las tres re u kajbal ʼij. ', '', '&lt;h2&gt;Wach u camic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_27_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Are tiqʼuil ʼij, xoʼon ʼekum ronojel u wach i jyub taʼaj; queje xoʼon ile cʼa a las tres re u kajbal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.046', 'Are ʼuri, co xchʼaw i Jesus, xu bij:
—Eli, Eli, ¿láma sabactáni? —xu bij.
I usucʼ i tzij-i queʼelok: Wajwal Dios, Wajwal Dios, ¿wuchac in awoʼtam canok? Queje usucʼ u tzij ile.
', '', '&lt;span class="verse" id="Matt_27_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Are ʼuri, co xchʼaw i Jesus, xu bij:&lt;p&gt; &lt;/p&gt;—Eli, Eli, ¿láma sabactáni? —xu bij.&lt;p&gt; &lt;/p&gt;I usucʼ i tzij-i queʼelok: Wajwal Dios, Wajwal Dios, ¿wuchac in awoʼtam canok? Queje usucʼ u tzij ile.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.047', 'Je ʼo jujun chila xqui ta; ique xqui bij:
—I achi-le cu siqʼuij i ujer ajbil u tzij i Dios, mam Elías —xe cha.
', '', '&lt;span class="verse" id="Matt_27_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Je ʼo jujun chila xqui ta; ique xqui bij:&lt;p&gt; &lt;/p&gt;—I achi-le cu siqʼuij i ujer ajbil u tzij i Dios, mam Elías —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.048', 'Juntir, ʼo jun chique xa ʼe paʼanem, xu mu jun espónja chupam juban víno chʼam. Teʼuri xu chʼap tzam cheʼ y xu ya ʼan che, man cu tzʼub-bej re. ', '', '&lt;span class="verse" id="Matt_27_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Juntir, ʼo jun chique xa ʼe paʼanem, xu mu jun espónja chupam juban víno chʼam. Teʼuri xu chʼap tzam cheʼ y xu ya ʼan che, man cu tzʼub-bej re. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.049', 'I juban xqui bij:
—Chawoyʼej na, we kas ca cʼun i mam Elías chu toʼic —xe cha che.
', '', '&lt;span class="verse" id="Matt_27_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;I juban xqui bij:&lt;p&gt; &lt;/p&gt;—Chawoyʼej na, we kas ca cʼun i mam Elías chu toʼic —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.050', 'Teʼuri i Jesus xsiqʼuin julaj chic y xel u cʼux.
', '', '&lt;span class="verse" id="Matt_27_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xsiqʼuin julaj chic y xel u cʼux.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.051', 'Are ʼuri i nim laj cortin pim, pa rachoch i Dios, xel pa nicʼaj, xmajan li pujaʼ, cʼa pulew. I ulew lic xa sicsatic; i tak i abaj xpax u pam. ', '', '&lt;span class="verse" id="Matt_27_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Are ʼuri i nim laj cortin pim, pa rachoch i Dios, xel pa nicʼaj, xmajan li pujaʼ, cʼa pulew. I ulew lic xa sicsatic; i tak i abaj xpax u pam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.052', 'Niʼpa i qui mukbal i camnak xe jaktaj na; je qʼui i creyent je camnak xe walij na. ', '', '&lt;span class="verse" id="Matt_27_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;Niʼpa i qui mukbal i camnak xe jaktaj na; je qʼui i creyent je camnak xe walij na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.053', 'Xe ʼel ʼuri chi sak; y chirijil nu walijic i Jesus, xe oc pa tinimit Jerusalen. Chila xe ʼiltaj wi cumal uqʼuial winak.
', '', '&lt;span class="verse" id="Matt_27_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;Xe ʼel ʼuri chi sak; y chirijil nu walijic i Jesus, xe oc pa tinimit Jerusalen. Chila xe ʼiltaj wi cumal uqʼuial winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.054', 'ʼO jun capitan que tijin chu chajixic i Jesus cuʼ u soldádo. Are xqui na i ucabrakan, xak xquil niʼpa i ʼanic-le, lic xqui xij quib. Xqui bij:
—Katzij i achi-ri, lic are u Cʼojol i Dios —xqui bij.
', '', '&lt;span class="verse" id="Matt_27_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;ʼO jun capitan que tijin chu chajixic i Jesus cuʼ u soldádo. Are xqui na i ucabrakan, xak xquil niʼpa i ʼanic-le, lic xqui xij quib. Xqui bij:&lt;p&gt; &lt;/p&gt;—Katzij i achi-ri, lic are u Cʼojol i Dios —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.055', 'Je ʼo uqʼuial ixokib que taken li cʼa naj chi je terejnak chirij i Jesus. Je terejnak li chirij, quiqui tobej na re; cʼa pa Galiléa je terejnak lok. ', '', '&lt;span class="verse" id="Matt_27_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;Je ʼo uqʼuial ixokib que taken li cʼa naj chi je terejnak chirij i Jesus. Je terejnak li chirij, quiqui tobej na re; cʼa pa Galiléa je terejnak lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.056', 'Chiquixol ique je ʼo i ati Mariy Magdaléna, xak i ati Mariy chi qui chuch i ma Jacóbo y ma Jose, xak i qui chuch i u cʼojol i mam Zebedéo.
', '', '&lt;span class="verse" id="Matt_27_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Chiquixol ique je ʼo i ati Mariy Magdaléna, xak i ati Mariy chi qui chuch i ma Jacóbo y ma Jose, xak i qui chuch i u cʼojol i mam Zebedéo.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.057', 'Are xoc aʼab, xcʼun jun achi beyom aj Arimatéa, ma Jose u bi. I achi-le xak terejnak chirij u cʼutunic i Jesus. ', '', '&lt;h2&gt;Wach u mukic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_27_57"&gt;&lt;strong class="verseno"&gt;57&lt;/strong&gt;&amp;nbsp;Are xoc aʼab, xcʼun jun achi beyom aj Arimatéa, ma Jose u bi. I achi-le xak terejnak chirij u cʼutunic i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.058', 'Xoc ire ruʼ i ma Piláto y xu tzʼonoj i u chʼacul i Jesus. I ma Piláto xu tak u jachic che. ', '', '&lt;span class="verse" id="Matt_27_58"&gt;&lt;strong class="verseno"&gt;58&lt;/strong&gt;&amp;nbsp;Xoc ire ruʼ i ma Piláto y xu tzʼonoj i u chʼacul i Jesus. I ma Piláto xu tak u jachic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.059', 'Teʼuri i ma Jose xu cʼam bic y xu pasij bi chupam jun sawan lic chʼam. ', '', '&lt;span class="verse" id="Matt_27_59"&gt;&lt;strong class="verseno"&gt;59&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Jose xu cʼam bic y xu pasij bi chupam jun sawan lic chʼam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.060', 'Teʼuri xe u mina chupam jun pantion re ire; are jun jul chi cʼa te u takom u woric chupam jun péña. Xu tzʼapij can u chiʼ i jul ruʼ jun nim laj abaj, y xa ʼec. ', '', '&lt;span class="verse" id="Matt_27_60"&gt;&lt;strong class="verseno"&gt;60&lt;/strong&gt;&amp;nbsp;Teʼuri xe u mina chupam jun pantion re ire; are jun jul chi cʼa te u takom u woric chupam jun péña. Xu tzʼapij can u chiʼ i jul ruʼ jun nim laj abaj, y xa ʼec. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.061', 'I ati Mariy Magdaléna, xak i ati Mariy jun, xe cubi can chila chuwach i jul.
', '', '&lt;span class="verse" id="Matt_27_61"&gt;&lt;strong class="verseno"&gt;61&lt;/strong&gt;&amp;nbsp;I ati Mariy Magdaléna, xak i ati Mariy jun, xe cubi can chila chuwach i jul.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.062', 'Are xa ʼani ile, are i ʼij chi yijbal na re i uxlambal ʼij. Chucab ʼij ʼuri, i ajwab sacerdóte, xak i aj Fariséo, xe ʼe ruʼ i ma Piláto ', '', '&lt;h2&gt;Lic xqui ʼan pen n-ta quelʼaw u chʼacul i Jesus&lt;/h2&gt;&lt;span class="verse" id="Matt_27_62"&gt;&lt;strong class="verseno"&gt;62&lt;/strong&gt;&amp;nbsp;Are xa ʼani ile, are i ʼij chi yijbal na re i uxlambal ʼij. Chucab ʼij ʼuri, i ajwab sacerdóte, xak i aj Fariséo, xe ʼe ruʼ i ma Piláto &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.063', 'y xqui bij che:
—Nim laj winak, xcʼun chaka cʼux chi i achi aj tʼoronel-le u bim na chi ca cʼun sak chuwach churox ʼij. Queje ile u bim na are cʼa cʼaslic. ', '', '&lt;span class="verse" id="Matt_27_63"&gt;&lt;strong class="verseno"&gt;63&lt;/strong&gt;&amp;nbsp;y xqui bij che:&lt;p&gt; &lt;/p&gt;—Nim laj winak, xcʼun chaka cʼux chi i achi aj tʼoronel-le u bim na chi ca cʼun sak chuwach churox ʼij. Queje ile u bim na are cʼa cʼaslic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.064', 'Rumal-i cha taka u chajixic i jul pa muktal wi, cʼa churox ʼij. Mokxa que cʼun i u tijoxelab chaʼab, caquelʼaj bi u chʼacul, teʼuri quiqui bij chique i winak chi walijnak chic. We queje quiqui ʼan ni ique ile, i mentir-le mas nim na chuwach i-jun chi u bim can i achi. (Queje ile xqui bij che i ma Piláto.)
', '', '&lt;span class="verse" id="Matt_27_64"&gt;&lt;strong class="verseno"&gt;64&lt;/strong&gt;&amp;nbsp;Rumal-i cha taka u chajixic i jul pa muktal wi, cʼa churox ʼij. Mokxa que cʼun i u tijoxelab chaʼab, caquelʼaj bi u chʼacul, teʼuri quiqui bij chique i winak chi walijnak chic. We queje quiqui ʼan ni ique ile, i mentir-le mas nim na chuwach i-jun chi u bim can i achi. (Queje ile xqui bij che i ma Piláto.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.065', 'Teʼuri ire xu bij chique:
—Che i cʼama bi i soldádo; che i ya chila chu chajixic i jul. ʼUtz u chajixic chi ʼana che —xu bij chique.
', '', '&lt;span class="verse" id="Matt_27_65"&gt;&lt;strong class="verseno"&gt;65&lt;/strong&gt;&amp;nbsp;Teʼuri ire xu bij chique:&lt;p&gt; &lt;/p&gt;—Che i cʼama bi i soldádo; che i ya chila chu chajixic i jul. ʼUtz u chajixic chi ʼana che —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '27.066', 'Ique xe ʼec, que ʼe qui yijba u chiʼ i jul man n-ta jun ca ʼe yinawok. I abaj chi ʼo che u chiʼ i jul, xqui coj cʼam chuwach, xqui nacʼ ixcab che tak u tzam. Queje xqui ʼan ile, ʼilbal-re we n-ta jun ca ʼe yinawok. Xak xe qui canaj can i soldádo chi je chajinel re u chiʼ i jul.
', '', '&lt;span class="verse" id="Matt_27_66"&gt;&lt;strong class="verseno"&gt;66&lt;/strong&gt;&amp;nbsp;Ique xe ʼec, que ʼe qui yijba u chiʼ i jul man n-ta jun ca ʼe yinawok. I abaj chi ʼo che u chiʼ i jul, xqui coj cʼam chuwach, xqui nacʼ ixcab che tak u tzam. Queje xqui ʼan ile, ʼilbal-re we n-ta jun ca ʼe yinawok. Xak xe qui canaj can i soldádo chi je chajinel re u chiʼ i jul.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.001', 'Are xicʼaw i ʼij re i uxlambal ʼij, are ya ca pe i u sakiric re i domíngo, are ʼuri i ati Mariy Magdaléna, xak i ati Mariy jun, xe ʼe chu rilic i jul. ', '', '&lt;h2&gt;U walijic i Jesus che u camic&lt;/h2&gt;&lt;span class="verse" id="Matt_28_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xicʼaw i ʼij re i uxlambal ʼij, are ya ca pe i u sakiric re i domíngo, are ʼuri i ati Mariy Magdaléna, xak i ati Mariy jun, xe ʼe chu rilic i jul. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.002', 'Are maja que uponic, xpe jun ucabrakan, man ʼo jun ángel re i Kajwal xkaj li chicaj, xresaj i abaj chu chiʼ i jul, y xcubi puwi. ', '', '&lt;span class="verse" id="Matt_28_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are maja que uponic, xpe jun ucabrakan, man ʼo jun ángel re i Kajwal xkaj li chicaj, xresaj i abaj chu chiʼ i jul, y xcubi puwi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.003', 'I u tzunbal ire ca walchʼinic pacha u rep ʼaʼ; i u ʼuʼ lic sak, sak. ', '', '&lt;span class="verse" id="Matt_28_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I u tzunbal ire ca walchʼinic pacha u rep ʼaʼ; i u ʼuʼ lic sak, sak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.004', 'I soldádo, are xquil u wach, lic que sicsat rumal qui xibriquil, pacha laj xe cam rumal.
', '', '&lt;span class="verse" id="Matt_28_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I soldádo, are xquil u wach, lic que sicsat rumal qui xibriquil, pacha laj xe cam rumal.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.005', 'Are xe upon i ixokib, i ángel xu bij chique:
—Mi xij iwib. Yin wetaʼam chi xix cʼun chu tzucuxic i Jesus chi xcamsax chuwach cruz. ', '', '&lt;span class="verse" id="Matt_28_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Are xe upon i ixokib, i ángel xu bij chique:&lt;p&gt; &lt;/p&gt;—Mi xij iwib. Yin wetaʼam chi xix cʼun chu tzucuxic i Jesus chi xcamsax chuwach cruz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.006', 'Ire n-ta waral; walijnak chubic pacha i u bim. Chix petok, chol iwila u qʼuijibal pa yatal wi canok. ', '', '&lt;span class="verse" id="Matt_28_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Ire n-ta waral; walijnak chubic pacha i u bim. Chix petok, chol iwila u qʼuijibal pa yatal wi canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.007', 'Chi bisoj u yaʼic rason chique i u tijoxelab. Chi bij chique: “Ire walijnak chic chiquixol i camnak; ya ʼenak chic pa Galiléa, nabe na chiwach yix; chila quiwil ni u wach,” chix chok chique. Are iri col in bij chiwe —xu bij i ángel chique.
', '', '&lt;span class="verse" id="Matt_28_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Chi bisoj u yaʼic rason chique i u tijoxelab. Chi bij chique: “Ire walijnak chic chiquixol i camnak; ya ʼenak chic pa Galiléa, nabe na chiwach yix; chila quiwil ni u wach,” chix chok chique. Are iri col in bij chiwe —xu bij i ángel chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.008', 'Teʼuri i ixokib juntir xe ʼel ruʼ i jul. Lic xqui xij quib xe ʼec, xui-ri xak que quicotic. Paʼanem xe ʼe chu yaʼic rason chique u tijoxelab. ', '', '&lt;span class="verse" id="Matt_28_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Teʼuri i ixokib juntir xe ʼel ruʼ i jul. Lic xqui xij quib xe ʼec, xui-ri xak que quicotic. Paʼanem xe ʼe chu yaʼic rason chique u tijoxelab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.009', 'Xak teʼet i Jesus xu cʼut rib chique, y xu ya rutzil qui wach. Ique xe tejeb ruʼ y xe xuqui chuwach, cojbal re u ʼij, y xqui chap i rakan.
', '', '&lt;span class="verse" id="Matt_28_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak teʼet i Jesus xu cʼut rib chique, y xu ya rutzil qui wach. Ique xe tejeb ruʼ y xe xuqui chuwach, cojbal re u ʼij, y xqui chap i rakan.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.010', '—Mi xij iwib —xu bij i Jesus chique—. Ji bij chique i wachalal che ʼoj ique pa Galiléa; chila quiquil ni in wach —xu bij chique.
', '', '&lt;span class="verse" id="Matt_28_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;—Mi xij iwib —xu bij i Jesus chique—. Ji bij chique i wachalal che ʼoj ique pa Galiléa; chila quiquil ni in wach —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.011', 'I ixokib xqui maj bi qui be. Are que tijin chi be, xak ʼo jujun chique i soldádo xe ʼe pa tinimit chu bixquil chique i ajwab sacerdóte wach i xquilo. ', '', '&lt;h2&gt;Xyaʼ mer chique i soldádo, ʼanbal re chique chi quiqui ʼan i mentir&lt;/h2&gt;&lt;span class="verse" id="Matt_28_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;I ixokib xqui maj bi qui be. Are que tijin chi be, xak ʼo jujun chique i soldádo xe ʼe pa tinimit chu bixquil chique i ajwab sacerdóte wach i xquilo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.012', 'Teʼuri i ajwab sacerdóte xe ʼe chu bixquil chique i nimak tak mamʼib. Xqui junamsaj i qui noʼoj chiquiwach, wach i quiqui ʼano. Teʼuri xqui ya uqʼuial mer chique i soldádo, ', '', '&lt;span class="verse" id="Matt_28_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i ajwab sacerdóte xe ʼe chu bixquil chique i nimak tak mamʼib. Xqui junamsaj i qui noʼoj chiquiwach, wach i quiqui ʼano. Teʼuri xqui ya uqʼuial mer chique i soldádo, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.013', 'y xqui bij chique:
—Chi bij yix chi xix war chaʼab, y are ʼuri xe cʼun i u tijoxelab i Jesus y xquelʼaj bi u chʼacul ire. Queje ile chi bij na. ', '', '&lt;span class="verse" id="Matt_28_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;y xqui bij chique:&lt;p&gt; &lt;/p&gt;—Chi bij yix chi xix war chaʼab, y are ʼuri xe cʼun i u tijoxelab i Jesus y xquelʼaj bi u chʼacul ire. Queje ile chi bij na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.014', 'We xu ta rason i gobernador, mi xij iwib; yoj caka kajsaj ni u cʼux ire; ire n-ta cu ʼan chiwe. (Queje xqui bij i ajwab sacerdóte ile chique i soldádo.)
', '', '&lt;span class="verse" id="Matt_28_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;We xu ta rason i gobernador, mi xij iwib; yoj caka kajsaj ni u cʼux ire; ire n-ta cu ʼan chiwe. (Queje xqui bij i ajwab sacerdóte ile chique i soldádo.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.015', 'Are ʼuri i soldádo xqui chap i mer, y xqui bij na pacha bital chique. Y queje quiqui bij ni aj Israel winak ile, cʼa are ʼo wi woʼor.
', '', '&lt;span class="verse" id="Matt_28_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are ʼuri i soldádo xqui chap i mer, y xqui bij na pacha bital chique. Y queje quiqui bij ni aj Israel winak ile, cʼa are ʼo wi woʼor.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.016', 'I julajuj u tijoxelab xe ʼe pa Galiléa; xe ʼe che jun jyub pacha bital chique rumal i Jesus. ', '', '&lt;h2&gt;Wach u cʼutic rib xoʼon i Jesus chique u tijoxelab&lt;/h2&gt;&lt;span class="verse" id="Matt_28_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I julajuj u tijoxelab xe ʼe pa Galiléa; xe ʼe che jun jyub pacha bital chique rumal i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.017', 'Chila xqui rik wi i Jesus, y are xquilo, xe oc chu cojic u ʼij, tupu ʼo jujun chique n-kas ta quiqui cojo we are i Jesus. ', '', '&lt;span class="verse" id="Matt_28_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Chila xqui rik wi i Jesus, y are xquilo, xe oc chu cojic u ʼij, tupu ʼo jujun chique n-kas ta quiqui cojo we are i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.018', 'I Jesus xtejeb pan cuʼ y xu bij:
—Yatal chic pinʼab u ʼatic tzij puwi ronojel niʼpa i ʼolic. ʼO chic pinʼab ronojel i aj chicaj, xak ronojel i aj chuwach i jyub taʼaj. ', '', '&lt;span class="verse" id="Matt_28_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I Jesus xtejeb pan cuʼ y xu bij:&lt;p&gt; &lt;/p&gt;—Yatal chic pinʼab u ʼatic tzij puwi ronojel niʼpa i ʼolic. ʼO chic pinʼab ronojel i aj chicaj, xak ronojel i aj chuwach i jyub taʼaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.019', 'Jix yix cuʼ i winak ronojel u wach i jyub taʼaj. Chi bij in tzij chique man xak ique que u ʼon ni in tijoxelab yin. Chi kajsaj ni ya piquiwi, cʼutbal-re chi je re chic i Ta chicaj, xak je re i u Cʼojol, xak je re i u Tewal i Dios. ', '', '&lt;span class="verse" id="Matt_28_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Jix yix cuʼ i winak ronojel u wach i jyub taʼaj. Chi bij in tzij chique man xak ique que u ʼon ni in tijoxelab yin. Chi kajsaj ni ya piquiwi, cʼutbal-re chi je re chic i Ta chicaj, xak je re i u Cʼojol, xak je re i u Tewal i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Matt', '28.020', 'Chi cʼutu chique chi ʼo u chac que cojon na che ronojel niʼpa in cʼutum yin chiwe yix. Mi sach pi cʼux chi yin in ʼo na iwuʼ ronojel ʼij sak, cʼa pa qʼuisbal re u wach i jyub taʼaj.
Amen (katzij).
', '', '&lt;span class="verse" id="Matt_28_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Chi cʼutu chique chi ʼo u chac que cojon na che ronojel niʼpa in cʼutum yin chiwe yix. Mi sach pi cʼux chi yin in ʼo na iwuʼ ronojel ʼij sak, cʼa pa qʼuisbal re u wach i jyub taʼaj.&lt;p&gt; &lt;/p&gt;Amen (katzij).
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.001', 'Are iri are i jekbal re i ʼutz laj tzij chirij i Jesucrísto, chi u Cʼojol i Dios. ', '', '&lt;h2&gt;I ʼUTZ LAJ TZIJ CHIRIJ I JESUCRISTO U TZʼIBAM&lt;/h2&gt;&lt;h1&gt;I SAN MARCOS&lt;/h1&gt;&lt;h2&gt;I ma Wan, aj kajsanel ya, xe u pixbaj i winak&lt;/h2&gt;&lt;span class="verse" id="Mark_1_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are iri are i jekbal re i ʼutz laj tzij chirij i Jesucrísto, chi u Cʼojol i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.002', 'I jekbal re u chac i Jesus, xak jun ruʼ pacha u tzʼibam can i ujer ajbil u tzij i Dios, mam Isaías. Ire xu tzʼibaj i tzij-i chi Dios xu bij che u Cʼojol:
Chatapeʼ —xu bij che— quin tak bi jun in takon ca nabeaj bi chawach, cu yijba a be chawach.
', '', '&lt;span class="verse" id="Mark_1_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I jekbal re u chac i Jesus, xak jun ruʼ pacha u tzʼibam can i ujer ajbil u tzij i Dios, mam Isaías. Ire xu tzʼibaj i tzij-i chi Dios xu bij che u Cʼojol:
Chatapeʼ —xu bij che— quin tak bi jun in takon ca nabeaj bi chawach, cu yijba a be chawach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.003', 'Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.”
(Queje ile tzʼibtal canok.)
', '', '&lt;span class="verse" id="Mark_1_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.”
(Queje ile tzʼibtal canok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.004', 'Xoʼon panok, queje ile xeʼelok. ʼO jun achi, ma Wan u bi, xoc che jun chakij jyub tzʼinilic. Chila xe u pixbaj wi i winak, xak xu kajsaj ya piquiwi. Xu bij chique: “Chi jalwachij i nojbal y chi tzʼonoj u kajsaxic ya piwi, man i Dios cu cuy na i mac.” (Queje ile xu bij.) ', '', '&lt;span class="verse" id="Mark_1_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xoʼon panok, queje ile xeʼelok. ʼO jun achi, ma Wan u bi, xoc che jun chakij jyub tzʼinilic. Chila xe u pixbaj wi i winak, xak xu kajsaj ya piquiwi. Xu bij chique: “Chi jalwachij i nojbal y chi tzʼonoj u kajsaxic ya piwi, man i Dios cu cuy na i mac.” (Queje ile xu bij.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.005', 'Che tak i ʼij-le, je qʼui i winak xe upon ruʼ chila, chu tayiquil u tzij; xe upon i je aj Jerusalen, xak ronojel i juban tinimit re i jyub Judéa. Chila quiqui bij ni qui mac chiquiwach i winak, teʼuri i ma Wan xu kajsaj ya piquiwi chiya Jordan.
', '', '&lt;span class="verse" id="Mark_1_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le, je qʼui i winak xe upon ruʼ chila, chu tayiquil u tzij; xe upon i je aj Jerusalen, xak ronojel i juban tinimit re i jyub Judéa. Chila quiqui bij ni qui mac chiquiwach i winak, teʼuri i ma Wan xu kajsaj ya piquiwi chiya Jordan.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.006', 'I ma Wan, i u ʼuʼ ire xa are i rismal camey ʼantal u ʼuʼ che; i ximbal u pam xa are chʼakap tzʼum. Xak i u wa cu tijo xa wach tak i sacʼ, wach tak i u waʼal cab cu rik pa tak ʼes. ', '', '&lt;span class="verse" id="Mark_1_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I ma Wan, i u ʼuʼ ire xa are i rismal camey ʼantal u ʼuʼ che; i ximbal u pam xa are chʼakap tzʼum. Xak i u wa cu tijo xa wach tak i sacʼ, wach tak i u waʼal cab cu rik pa tak ʼes. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.007', 'Are xe u pixbaj i winak, xu bij chique:
—ʼO jun ca pe chwijil yin chi nim ni u ʼij chinwach yin. N-ca majaw ta chwe we quin ʼan mocom che wib chirij, rumal u ʼij nim; mi te ne quin chaqui kaj chuwach chu toric u wach u xajab che. ', '', '&lt;span class="verse" id="Mark_1_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are xe u pixbaj i winak, xu bij chique:&lt;p&gt; &lt;/p&gt;—ʼO jun ca pe chwijil yin chi nim ni u ʼij chinwach yin. N-ca majaw ta chwe we quin ʼan mocom che wib chirij, rumal u ʼij nim; mi te ne quin chaqui kaj chuwach chu toric u wach u xajab che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.008', 'Yin, xa ya quin kajsaj piwi, xui-ri ire cu kajsaj ni u Tewal i Dios piwi. (Queje ile xu bij i ma Wan chique i winak.)
', '', '&lt;span class="verse" id="Mark_1_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Yin, xa ya quin kajsaj piwi, xui-ri ire cu kajsaj ni u Tewal i Dios piwi. (Queje ile xu bij i ma Wan chique i winak.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.009', 'Che tak i ʼij-le i Jesus xel pu tinimit Nazaret, u cwenta i Galiléa, y xa ʼe chiya Jordan. Chila, i ma Wan xu kajsaj ya puwi chiya. ', '', '&lt;h2&gt;Wach u yijbaxic rib i Jesus che u chac&lt;/h2&gt;&lt;span class="verse" id="Mark_1_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le i Jesus xel pu tinimit Nazaret, u cwenta i Galiléa, y xa ʼe chiya Jordan. Chila, i ma Wan xu kajsaj ya puwi chiya. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.010', 'Xak teʼet, are xel i Jesus pa ya, xril i caj xjaktajic, xak xril i u Tewal i Dios xkaj lok pacha jun palomax. Xcʼunic, y xkaj puwi ire. ', '', '&lt;span class="verse" id="Mark_1_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xak teʼet, are xel i Jesus pa ya, xril i caj xjaktajic, xak xril i u Tewal i Dios xkaj lok pacha jun palomax. Xcʼunic, y xkaj puwi ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.011', 'Are xa ʼantaj ile, ʼo jun chʼawbal xchʼaw chicaj chi xu bij:
—Yet at Wacʼal chi lic cʼax cat in naʼo. Lic quin quicot awumal —xu bij.
', '', '&lt;span class="verse" id="Mark_1_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Are xa ʼantaj ile, ʼo jun chʼawbal xchʼaw chicaj chi xu bij:&lt;p&gt; &lt;/p&gt;—Yet at Wacʼal chi lic cʼax cat in naʼo. Lic quin quicot awumal —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.012', 'Teʼuri i u Tewal i Dios xu cʼam bi i Jesus che jun chakij jyub tzʼinilic. ', '', '&lt;h2&gt;I itzel xu tzucuj wach tzaksabal re i Jesus cu ʼano&lt;/h2&gt;&lt;span class="verse" id="Mark_1_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i u Tewal i Dios xu cʼam bi i Jesus che jun chakij jyub tzʼinilic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.013', 'Chila xqʼuiji cuarénta ʼij chiquixol itzel chicop. I itzel, chi Satanas, xupon ruʼ, xraj te ire cu tzaksaj i Jesus chupam jun u mac. Xui-ri, i Jesus n-xtzak taj. Are xicʼaw ile, xe upon juban ángel ruʼ, y xqui to wach i rajwaxic che.
', '', '&lt;span class="verse" id="Mark_1_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Chila xqʼuiji cuarénta ʼij chiquixol itzel chicop. I itzel, chi Satanas, xupon ruʼ, xraj te ire cu tzaksaj i Jesus chupam jun u mac. Xui-ri, i Jesus n-xtzak taj. Are xicʼaw ile, xe upon juban ángel ruʼ, y xqui to wach i rajwaxic che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.014', 'Xoʼon panok, i ma Wan aj kajsanel ya, xtzʼapix pa cárcel. Are ʼuri xa ʼe i Jesus pa Galiléa chu bixquil i ʼutz laj tzij re i Dios chila, wach u ʼatbal tzij i Dios piquiwi i winak. ', '', '&lt;h2&gt;I jekbal re u chac i Jesus pa Galiléa&lt;/h2&gt;&lt;span class="verse" id="Mark_1_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i ma Wan aj kajsanel ya, xtzʼapix pa cárcel. Are ʼuri xa ʼe i Jesus pa Galiléa chu bixquil i ʼutz laj tzij re i Dios chila, wach u ʼatbal tzij i Dios piquiwi i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.015', 'Xu bij chique:
—Ya xu rik i ʼij chi u chʼicom i Dios; ya ca jekan ʼuri i ʼutz laj ʼatbal tzij re ire. ¡Chi jalwachij i nojbal chi n-ʼus taj! ¡Chi cuba i cʼux che i tzij re iwelbal chi sak! —xu bij chique.
', '', '&lt;span class="verse" id="Mark_1_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—Ya xu rik i ʼij chi u chʼicom i Dios; ya ca jekan ʼuri i ʼutz laj ʼatbal tzij re ire. ¡Chi jalwachij i nojbal chi n-ʼus taj! ¡Chi cuba i cʼux che i tzij re iwelbal chi sak! —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.016', 'Xoʼon chupan jubiʼ, i Jesus ca malcat chu chiʼ i alagun, Galiléa u bi. Chila xe ril i ma Simon ruʼ i ma Andres, chi je catz qui chaʼ quib. Que tijin chu cʼakic cataraya pa ya, man je aj chapol car. ', '', '&lt;h2&gt;I Jesus xe u siqʼuij je cajib aj chapol car&lt;/h2&gt;&lt;span class="verse" id="Mark_1_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, i Jesus ca malcat chu chiʼ i alagun, Galiléa u bi. Chila xe ril i ma Simon ruʼ i ma Andres, chi je catz qui chaʼ quib. Que tijin chu cʼakic cataraya pa ya, man je aj chapol car. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.017', 'I Jesus xu bij chique:
—Chix pet wuʼ, y quin cʼut chiwe wach qui molic i winak qui ʼano, pacha qui ʼan chique uqʼuial car are qui chapo —xu bij chique.
', '', '&lt;span class="verse" id="Mark_1_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chix pet wuʼ, y quin cʼut chiwe wach qui molic i winak qui ʼano, pacha qui ʼan chique uqʼuial car are qui chapo —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.018', 'Are xqui ta wach xbix chique, juntir xqui canaj can i cataraya, y xe terej chirij.
', '', '&lt;span class="verse" id="Mark_1_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Are xqui ta wach xbix chique, juntir xqui canaj can i cataraya, y xe terej chirij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.019', 'Xbin chupan jubiʼ, xak xe ril i ma Jacóbo ruʼ i ma Wan chi catz qui chaʼ quib; ique je racʼal i mam Zebedéo. Je ʼo chupam jun bárco, que tijin chu yijbaxic tak i cataraya. ', '', '&lt;span class="verse" id="Mark_1_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xbin chupan jubiʼ, xak xe ril i ma Jacóbo ruʼ i ma Wan chi catz qui chaʼ quib; ique je racʼal i mam Zebedéo. Je ʼo chupam jun bárco, que tijin chu yijbaxic tak i cataraya. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.020', 'I Jesus, are xe rilo, juntir xe u siqʼuij ruʼ. Ique-le, xqui canaj can i qui kajaw ruʼ i qui bárco, xak i qui mocom, y xe terej chirij i Jesus.
', '', '&lt;span class="verse" id="Mark_1_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I Jesus, are xe rilo, juntir xe u siqʼuij ruʼ. Ique-le, xqui canaj can i qui kajaw ruʼ i qui bárco, xak i qui mocom, y xe terej chirij i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.021', 'Xoʼon chupan jubiʼ, xe upon pa tinimit Capernaum. Are xcʼun i uxlambal ʼij, i Jesus xoc pa molbalʼib que i aj Israel winak, y xoc chu cʼutic chique. ', '', '&lt;h2&gt;I rutzirsaxic jun achi, ʼo itzel tew che&lt;/h2&gt;&lt;span class="verse" id="Mark_1_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, xe upon pa tinimit Capernaum. Are xcʼun i uxlambal ʼij, i Jesus xoc pa molbalʼib que i aj Israel winak, y xoc chu cʼutic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.022', 'Lic xqui bisoj i winak wach i cʼutunic xu ʼan chique, man ire, are ca chʼaʼat cuʼ, ʼalaj ʼuri chi lic retaʼam wach i cu bij; n-are ta pacha xqui ʼan i toʼ je tijonel re i ʼatbal tzij. ', '', '&lt;span class="verse" id="Mark_1_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Lic xqui bisoj i winak wach i cʼutunic xu ʼan chique, man ire, are ca chʼaʼat cuʼ, ʼalaj ʼuri chi lic retaʼam wach i cu bij; n-are ta pacha xqui ʼan i toʼ je tijonel re i ʼatbal tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.023', 'Chupam i molbalʼib-le, ʼo jun achi ʼo jun itzel tew che. Ire ca siqʼuin chu bixquil:
', '', '&lt;span class="verse" id="Mark_1_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Chupam i molbalʼib-le, ʼo jun achi ʼo jun itzel tew che. Ire ca siqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.024', '—Yet, Jesus aj Nazaret ¿wuchac col a mina awib kuʼ yoj? ¿Mok xat cʼun na, col a sachbej ka wach? Yin wetaʼam a wach: at are i Jun petnak ruʼ i Dios; ¡lic ʼo re i Dios chawe! (Queje ile ca siqʼuin chu bixquil.)
', '', '&lt;span class="verse" id="Mark_1_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;—Yet, Jesus aj Nazaret ¿wuchac col a mina awib kuʼ yoj? ¿Mok xat cʼun na, col a sachbej ka wach? Yin wetaʼam a wach: at are i Jun petnak ruʼ i Dios; ¡lic ʼo re i Dios chawe! (Queje ile ca siqʼuin chu bixquil.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.025', 'I Jesus xu ʼatij wach i xu bij:
—¡Mat chʼawic! —xu bij che—. ¡Chawoʼtaj can i achi-le! —coʼon che.
', '', '&lt;span class="verse" id="Mark_1_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I Jesus xu ʼatij wach i xu bij:&lt;p&gt; &lt;/p&gt;—¡Mat chʼawic! —xu bij che—. ¡Chawoʼtaj can i achi-le! —coʼon che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.026', 'Are ʼuri i achi co xsicsax rumal itzel tew, teʼuri i itzel tew ca siqʼuinic xel che i achi. ', '', '&lt;span class="verse" id="Mark_1_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Are ʼuri i achi co xsicsax rumal itzel tew, teʼuri i itzel tew ca siqʼuinic xel che i achi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.027', 'Conojel i winak lic xqui bisoj, y xe oc chu chʼobic chiquiwach:
—¿Wach u ʼonquil ile? ¿Wach usucʼ i cʼacʼ laj cʼutunic coʼon ire-le? I achi-le lic ʼo u choʼab; ʼo puʼab que u tak bi itzel tew, y que cojon che —xqui bij i winak.
', '', '&lt;span class="verse" id="Mark_1_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Conojel i winak lic xqui bisoj, y xe oc chu chʼobic chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Wach u ʼonquil ile? ¿Wach usucʼ i cʼacʼ laj cʼutunic coʼon ire-le? I achi-le lic ʼo u choʼab; ʼo puʼab que u tak bi itzel tew, y que cojon che —xqui bij i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.028', 'Y juntir xpax u tzijol i Jesus ronojel i jyub re i Galiléa.
', '', '&lt;span class="verse" id="Mark_1_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Y juntir xpax u tzijol i Jesus ronojel i jyub re i Galiléa.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.029', 'Are xel pa molbalʼib, xe ʼe che i cachoch i ma Simon y ma Andres, xak xe ʼe i ma Jacóbo y ma Wan cuʼ. ', '', '&lt;h2&gt;I rutzirsaxic u chuch u jiʼ i ma Pédro&lt;/h2&gt;&lt;span class="verse" id="Mark_1_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are xel pa molbalʼib, xe ʼe che i cachoch i ma Simon y ma Andres, xak xe ʼe i ma Jacóbo y ma Wan cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.030', 'Are xe upon chila, i u chuch u jiʼ i ma Simon iwab, cotzʼol chuwi u chʼat, ʼo ʼaʼ chirij. I je ʼo chila xqui bij che i Jesus chi iwab i ixok. ', '', '&lt;span class="verse" id="Mark_1_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Are xe upon chila, i u chuch u jiʼ i ma Simon iwab, cotzʼol chuwi u chʼat, ʼo ʼaʼ chirij. I je ʼo chila xqui bij che i Jesus chi iwab i ixok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.031', 'Are ʼuri, ire xtejeb ruʼ ixok; xu chap che u ʼab, y xu walijsaj. Juntir xchup i ʼaʼ chirij. Teʼuri xoc chi qui tzukic.
', '', '&lt;span class="verse" id="Mark_1_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Are ʼuri, ire xtejeb ruʼ ixok; xu chap che u ʼab, y xu walijsaj. Juntir xchup i ʼaʼ chirij. Teʼuri xoc chi qui tzukic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.032', 'Are xkaj i ʼij, ya xoc aʼab, teʼuri i winak xqui ticba li u cʼamic iwabib ruʼ i Jesus. Xak xqui cʼam li niʼpa i ʼo itzel tew chique. ', '', '&lt;h2&gt;I Jesus, je qʼui iwabib xe rutzirsaj&lt;/h2&gt;&lt;span class="verse" id="Mark_1_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Are xkaj i ʼij, ya xoc aʼab, teʼuri i winak xqui ticba li u cʼamic iwabib ruʼ i Jesus. Xak xqui cʼam li niʼpa i ʼo itzel tew chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.033', 'Conojel i winak re i tinimit xqui rik quib pa ocbal bic ruʼ ja, pa ʼo wi i Jesus. ', '', '&lt;span class="verse" id="Mark_1_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Conojel i winak re i tinimit xqui rik quib pa ocbal bic ruʼ ja, pa ʼo wi i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.034', 'Je qʼui xe rutzirsaj, xa bi chi cʼaxil ʼo chique, xak xe resaj niʼpa itzel tew ʼo chique. Xui-ri, n-xu ya ta chique itzel tew que chʼawic, man ique-le quetaʼam u wach.
', '', '&lt;span class="verse" id="Mark_1_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Je qʼui xe rutzirsaj, xa bi chi cʼaxil ʼo chique, xak xe resaj niʼpa itzel tew ʼo chique. Xui-ri, n-xu ya ta chique itzel tew que chʼawic, man ique-le quetaʼam u wach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.035', 'Sakir chucab ʼij, cʼa ʼekum na, i Jesus xwalijic, xel ruʼ ja, xa ʼe che jun lugar tzʼinilic. Chila xu tzʼonoj wi che i Dios. ', '', '&lt;h2&gt;I Jesus xel chu bixquil u tzij i Dios pa tak tinimit re i jyub Galiléa&lt;/h2&gt;&lt;span class="verse" id="Mark_1_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Sakir chucab ʼij, cʼa ʼekum na, i Jesus xwalijic, xel ruʼ ja, xa ʼe che jun lugar tzʼinilic. Chila xu tzʼonoj wi che i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.036', 'Teʼuri i ma Simon cuʼ i rachiʼil, xe ʼe chu tzucuxic. ', '', '&lt;span class="verse" id="Mark_1_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Simon cuʼ i rachiʼil, xe ʼe chu tzucuxic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.037', 'Are xqui riko, xqui bij che:
—Conojel i winak que tijin cha tzucuxic —xqui bij.
', '', '&lt;span class="verse" id="Mark_1_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Are xqui riko, xqui bij che:&lt;p&gt; &lt;/p&gt;—Conojel i winak que tijin cha tzucuxic —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.038', 'Xui-ri, i Jesus xu bij chique:
—Coj ʼe chic che juban tak aldéa; ʼo u chac xak quin bij u tzij i Dios chique i winak chila. Are i rumal ile xin petic —xu bij chique.
', '', '&lt;span class="verse" id="Mark_1_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Xui-ri, i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Coj ʼe chic che juban tak aldéa; ʼo u chac xak quin bij u tzij i Dios chique i winak chila. Are i rumal ile xin petic —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.039', 'Teʼuri, xe ʼe che ronojel i jyub re i Galiléa, chu bixquil u tzij i Dios chique i winak pa tak molbalʼib que i aj Israel. N-xui ta la ile xu ʼano, xak xe resaj itzel tak tew chique i je niʼpa je chaptajnak cumal.
', '', '&lt;span class="verse" id="Mark_1_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Teʼuri, xe ʼe che ronojel i jyub re i Galiléa, chu bixquil u tzij i Dios chique i winak pa tak molbalʼib que i aj Israel. N-xui ta la ile xu ʼano, xak xe resaj itzel tak tew chique i je niʼpa je chaptajnak cumal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.040', 'Xoʼon panok, ʼo jun achi xupon ruʼ i Jesus, ʼo yobil che, lépra ca bixic. Lic xu tzʼonoj utzil chomal che. Xuqui chuwach y xu bij che:
—We ta cawaj, cat tiqui chi rutzirsaxic i yobil ʼo chwe —coʼono.
', '', '&lt;h2&gt;I rutzirsaxic jun achi ʼo yobil lépra che&lt;/h2&gt;&lt;span class="verse" id="Mark_1_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Xoʼon panok, ʼo jun achi xupon ruʼ i Jesus, ʼo yobil che, lépra ca bixic. Lic xu tzʼonoj utzil chomal che. Xuqui chuwach y xu bij che:&lt;p&gt; &lt;/p&gt;—We ta cawaj, cat tiqui chi rutzirsaxic i yobil ʼo chwe —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.041', 'I Jesus lic xril toʼbal u wach. Xu yin pan ruʼ u ʼab; xu bij che:
—Cwaj —xu bij—. ¡Chel ni yobil ʼo chawe! —xcha.
', '', '&lt;span class="verse" id="Mark_1_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;I Jesus lic xril toʼbal u wach. Xu yin pan ruʼ u ʼab; xu bij che:&lt;p&gt; &lt;/p&gt;—Cwaj —xu bij—. ¡Chel ni yobil ʼo chawe! —xcha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.042', 'Are xu bij ire ile, juntir xel i yobil che; xcanaj canok chom chic u chʼacul. ', '', '&lt;span class="verse" id="Mark_1_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are xu bij ire ile, juntir xel i yobil che; xcanaj canok chom chic u chʼacul. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.043', 'Teʼuri i Jesus xu ʼijla bic; lic xu pixbaj y xu bij che:
', '', '&lt;span class="verse" id="Mark_1_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu ʼijla bic; lic xu pixbaj y xu bij che:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.044', '—Chatapeʼ, mi jun ma lapla che wach in ʼanom chawe; xui-ri, ja cʼutu awib che i sacerdóte, cʼutbal-re che chi n-ta chic i yobil lépra chawe. Cha jacha can a sipon che, chi cu sujuj chuwach i Dios pacha u bim can i mam Moises ujer. Queje ile caquetamaj i winak chi utzirnak chic i cʼax chawe —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Mark_1_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;—Chatapeʼ, mi jun ma lapla che wach in ʼanom chawe; xui-ri, ja cʼutu awib che i sacerdóte, cʼutbal-re che chi n-ta chic i yobil lépra chawe. Cha jacha can a sipon che, chi cu sujuj chuwach i Dios pacha u bim can i mam Moises ujer. Queje ile caquetamaj i winak chi utzirnak chic i cʼax chawe —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '1.045', 'Xui-ri, i achi, are xa ʼec xoc chu bixquil chique conojel i winak wach i ʼantal che; lic xu paxsaj u tzijol. Rumal ile, lic xe qʼuiar i winak ruʼ i Jesus, xa ipa xa ʼe wi. Rumal-i, ire n-xoc ta chic mer pa tak tinimit, xui xcanaj can pa tak jyub tzʼinilic. Xui-ri, cʼa je qʼui ni winak xe upon ruʼ xa bi chi tinimital.
', '', '&lt;span class="verse" id="Mark_1_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Xui-ri, i achi, are xa ʼec xoc chu bixquil chique conojel i winak wach i ʼantal che; lic xu paxsaj u tzijol. Rumal ile, lic xe qʼuiar i winak ruʼ i Jesus, xa ipa xa ʼe wi. Rumal-i, ire n-xoc ta chic mer pa tak tinimit, xui xcanaj can pa tak jyub tzʼinilic. Xui-ri, cʼa je qʼui ni winak xe upon ruʼ xa bi chi tinimital.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.001', 'Xoʼon chupan jubiʼ chic, i Jesus xtzalij chic pa tinimit Capernaum. Xpax rason chi ire ʼo chic chi rachoch. ', '', '&lt;h2&gt;I rutzirsaxic jun chi siquirnak u chʼacul&lt;/h2&gt;&lt;span class="verse" id="Mark_2_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ chic, i Jesus xtzalij chic pa tinimit Capernaum. Xpax rason chi ire ʼo chic chi rachoch. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.002', 'Juntir xqui mol quib i winak ruʼ, ya n-que oc ta chic paja; que bajbatic cʼa chuxol uchija. I Jesus xoc chu bixquil u tzij i Dios chique. ', '', '&lt;span class="verse" id="Mark_2_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Juntir xqui mol quib i winak ruʼ, ya n-que oc ta chic paja; que bajbatic cʼa chuxol uchija. I Jesus xoc chu bixquil u tzij i Dios chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.003', 'Are ca tijin chu bixquil, xe upon cajib achiab, cucʼam bi jun achi chuwa chʼat, lic siquirnak u chʼacul. ', '', '&lt;span class="verse" id="Mark_2_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are ca tijin chu bixquil, xe upon cajib achiab, cucʼam bi jun achi chuwa chʼat, lic siquirnak u chʼacul. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.004', 'Cacaj ique que oc ruʼ i Jesus; xui-ri, cumal i winak je qʼui, n-xe tiqui ta che. Rumal-i, xe ʼel bi chuwi ja y xe oc chu toric i xot mer puwi i Jesus. Teʼuri i je cajib-le xqui kajsaj bi achi chuwa chʼat chuwach i Jesus. ', '', '&lt;span class="verse" id="Mark_2_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Cacaj ique que oc ruʼ i Jesus; xui-ri, cumal i winak je qʼui, n-xe tiqui ta che. Rumal-i, xe ʼel bi chuwi ja y xe oc chu toric i xot mer puwi i Jesus. Teʼuri i je cajib-le xqui kajsaj bi achi chuwa chʼat chuwach i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.005', 'Ire, are xrilo chi lic qui cubam qui cʼux che, xu bij che i iwab:
—Wacʼal, niʼpa i a mac, ya cuytajnak chic —xu bij che.
', '', '&lt;span class="verse" id="Mark_2_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Ire, are xrilo chi lic qui cubam qui cʼux che, xu bij che i iwab:&lt;p&gt; &lt;/p&gt;—Wacʼal, niʼpa i a mac, ya cuytajnak chic —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.006', 'Xak je ʼo jujun aj tijonel re i u ʼatbal tzij i mam Moises je cul chila. Ique xe oc chu chʼobic pa qui cʼux: ', '', '&lt;span class="verse" id="Mark_2_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun aj tijonel re i u ʼatbal tzij i mam Moises je cul chila. Ique xe oc chu chʼobic pa qui cʼux: &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.007', '“¿Wuchac queje ile cu bij i achi-le? I tzij xu bij ire-le, tzel cu ta i Dios, man mi jun ʼo puʼab cu cuy ni qui mac i winak; xui i Dios ʼo puʼab cu cuy na,” que cha.
', '', '&lt;span class="verse" id="Mark_2_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;“¿Wuchac queje ile cu bij i achi-le? I tzij xu bij ire-le, tzel cu ta i Dios, man mi jun ʼo puʼab cu cuy ni qui mac i winak; xui i Dios ʼo puʼab cu cuy na,” que cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.008', 'Juntir xu na i Jesus pu cʼux wach que tijin chu chʼobic; rumal-i xu bij chique:
—¿Wuchac qui chʼob ile pi cʼux? ', '', '&lt;span class="verse" id="Mark_2_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Juntir xu na i Jesus pu cʼux wach que tijin chu chʼobic; rumal-i xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac qui chʼob ile pi cʼux? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.009', 'Ile, n-cʼax tu bixquil chi “Ya xcuytaj a mac.” Are i cʼax u ʼonquil are iri: “Chat walijok, cha yaca bi a soc, y chat binok.” Are cʼax u ʼonquil ile che jun winak. ', '', '&lt;span class="verse" id="Mark_2_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Ile, n-cʼax tu bixquil chi “Ya xcuytaj a mac.” Are i cʼax u ʼonquil are iri: “Chat walijok, cha yaca bi a soc, y chat binok.” Are cʼax u ʼonquil ile che jun winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.010', 'Xui-ri, yin chi in Achi aj Chicaj, quin ʼalijinsaj na chiwe chi yin ʼo pinʼab quin cuy ni qui mac i winak waral chuwach i jyub taʼaj —xu bij i Jesus chique. Teʼuri xu bij che i achi chi siquirnak:
', '', '&lt;span class="verse" id="Mark_2_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xui-ri, yin chi in Achi aj Chicaj, quin ʼalijinsaj na chiwe chi yin ʼo pinʼab quin cuy ni qui mac i winak waral chuwach i jyub taʼaj —xu bij i Jesus chique. Teʼuri xu bij che i achi chi siquirnak:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.011', '—Yet, chatapeʼ: chat walijok, cha yaca bi a soc, y jat chi awachoch.
', '', '&lt;span class="verse" id="Mark_2_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;—Yet, chatapeʼ: chat walijok, cha yaca bi a soc, y jat chi awachoch.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.012', 'Juntir xwalij i iwab, xu yac bi u soc, y xa ʼec. Xwalij chiquiwach niʼpa i je ʼo chila y xa ʼec. Ique lic xqui bisoj che y xe oc chu cojic u ʼij i Dios:
—Lic n-kilom ta iri —xe cha.
', '', '&lt;span class="verse" id="Mark_2_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Juntir xwalij i iwab, xu yac bi u soc, y xa ʼec. Xwalij chiquiwach niʼpa i je ʼo chila y xa ʼec. Ique lic xqui bisoj che y xe oc chu cojic u ʼij i Dios:&lt;p&gt; &lt;/p&gt;—Lic n-kilom ta iri —xe cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.013', 'Xoʼon panok, i Jesus xa ʼe chic chu chiʼ i nim laj alagun. Are ʼo chila, je mas i winak xe moltaj ruʼ; ire xoc chic chu cʼutic chique. ', '', '&lt;h2&gt;I sisʼbal re i ma Lewi&lt;/h2&gt;&lt;span class="verse" id="Mark_2_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus xa ʼe chic chu chiʼ i nim laj alagun. Are ʼo chila, je mas i winak xe moltaj ruʼ; ire xoc chic chu cʼutic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.014', 'Teʼuri xbin chic jubiʼ y xu rik jun achi, ma Lewi u bi, chi u cʼojol i ma Alféo. Ire cul pa qʼuijibal chila pa cu tzʼonoj wi i alcawal re i ʼatbal tzij. I Jesus xu bij che:
—Chat terej chwij —coʼon che.
I ma Lewi juntir xwalijic y xterej chirij.
', '', '&lt;span class="verse" id="Mark_2_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri xbin chic jubiʼ y xu rik jun achi, ma Lewi u bi, chi u cʼojol i ma Alféo. Ire cul pa qʼuijibal chila pa cu tzʼonoj wi i alcawal re i ʼatbal tzij. I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Chat terej chwij —coʼon che.&lt;p&gt; &lt;/p&gt;I ma Lewi juntir xwalijic y xterej chirij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.015', 'Xoʼon panok, i Jesus cuʼ u tijoxelab que tijin chi waʼim pa rachoch i ma Lewi-le, junam cuʼ juban chic aj tzʼonol alcawal, je qʼui. Xak je ʼo juban winak que tijin chi waʼim cuʼ, chi n-kas ta que cojon che i ʼatbal tzij re i mam Moises; je qʼui je terejnak chirij i Jesus. ', '', '&lt;span class="verse" id="Mark_2_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus cuʼ u tijoxelab que tijin chi waʼim pa rachoch i ma Lewi-le, junam cuʼ juban chic aj tzʼonol alcawal, je qʼui. Xak je ʼo juban winak que tijin chi waʼim cuʼ, chi n-kas ta que cojon che i ʼatbal tzij re i mam Moises; je qʼui je terejnak chirij i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.016', 'Chila je ʼo jujun aj Fariséo junam cuʼ i qui tijonel re i ujer ʼatbal tzij; ique, are xquilo chi i Jesus ca tijin chi waʼim cuʼ i winak-le, xe oc chu bixquil chique i u tijoxelab i Jesus:
¿Wuchac i mayes yix cu tij u wa cuʼ i mal laj tak aj tzʼonol alcawal, xak cuʼ juban ajmaquib? —xqui bij chique.
', '', '&lt;span class="verse" id="Mark_2_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Chila je ʼo jujun aj Fariséo junam cuʼ i qui tijonel re i ujer ʼatbal tzij; ique, are xquilo chi i Jesus ca tijin chi waʼim cuʼ i winak-le, xe oc chu bixquil chique i u tijoxelab i Jesus:&lt;p&gt; &lt;/p&gt;¿Wuchac i mayes yix cu tij u wa cuʼ i mal laj tak aj tzʼonol alcawal, xak cuʼ juban ajmaquib? —xqui bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.017', 'I Jesus, are xu ta wach i quiqui bij, xu bij chique:
—Quin ʼan iri, man are i je iwab craj jun ajcun chique; i ʼutz qui wach n-tu chac ajcun chique. Yin in cʼun-nak chi qui siqʼuixic i je ajmaquib; n-in cʼun-nak ta chi qui siqʼuixic i je ʼutz laj tak winak. (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Mark_2_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I Jesus, are xu ta wach i quiqui bij, xu bij chique:&lt;p&gt; &lt;/p&gt;—Quin ʼan iri, man are i je iwab craj jun ajcun chique; i ʼutz qui wach n-tu chac ajcun chique. Yin in cʼun-nak chi qui siqʼuixic i je ajmaquib; n-in cʼun-nak ta chi qui siqʼuixic i je ʼutz laj tak winak. (Queje xu bij i Jesus ile chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.018', 'Xoʼon panok, i u tijoxelab i ma Wan aj kajsanel ya, xak i je aj Fariséo, que tijin che qui costúmbre, ayun u bi. (Are i usucʼ i ayun-le: quiqui chʼij jun ʼij, queb ʼij chuwach i Dios, n-ta wa quiqui tijo.) Che i ʼij-le, je ʼo juban winak xe upon ruʼ i Jesus; xqui tzʼonoj che:
—¿Wuchac i a tijoxelab yet n-cacoʼtaj ta jun ʼij, queb ʼij i qui waʼim pacha quiqui ʼan i u tijoxelab i ma Wan, xak i que terej chiquij i aj Fariséo? —xqui bij che i Jesus.
', '', '&lt;h2&gt;I costúmbre re ujer n-cumaj taj cocsax ruʼ i cʼutunic cʼacʼ&lt;/h2&gt;&lt;span class="verse" id="Mark_2_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i u tijoxelab i ma Wan aj kajsanel ya, xak i je aj Fariséo, que tijin che qui costúmbre, ayun u bi. (Are i usucʼ i ayun-le: quiqui chʼij jun ʼij, queb ʼij chuwach i Dios, n-ta wa quiqui tijo.) Che i ʼij-le, je ʼo juban winak xe upon ruʼ i Jesus; xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wuchac i a tijoxelab yet n-cacoʼtaj ta jun ʼij, queb ʼij i qui waʼim pacha quiqui ʼan i u tijoxelab i ma Wan, xak i que terej chiquij i aj Fariséo? —xqui bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.019', 'I Jesus xu bij chique:
—I winak, we que ʼe pa nimaʼij re jun achi ya ca cʼuliʼic, ¿xataba n-quiqui tij ta wa ruʼ? Quiqui tijo pues. N-ʼus taj quiqui xutuj i waʼim, tzʼakat ire ʼo cuʼ. ', '', '&lt;span class="verse" id="Mark_2_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—I winak, we que ʼe pa nimaʼij re jun achi ya ca cʼuliʼic, ¿xataba n-quiqui tij ta wa ruʼ? Quiqui tijo pues. N-ʼus taj quiqui xutuj i waʼim, tzʼakat ire ʼo cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.020', 'Xui-ri, we xu rik i ʼij chi ʼelsam chic i achi chiquixol, teʼuri quiqui mayij i waʼim. (Queje i tijojbal tzij ile xu bij i Jesus chique. Xak ʼo jun chic tijojbal tzij xu bij chique. Are iri:)
', '', '&lt;span class="verse" id="Mark_2_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xui-ri, we xu rik i ʼij chi ʼelsam chic i achi chiquixol, teʼuri quiqui mayij i waʼim. (Queje i tijojbal tzij ile xu bij i Jesus chique. Xak ʼo jun chic tijojbal tzij xu bij chique. Are iri:)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.021', '—Mi jun cu cʼojoj jun u ʼuʼ ʼel ruʼ jun chʼakap cʼojbal cʼacʼ, man i cʼojbal cʼacʼ xa ca tzajic, y cu rakchʼij i cʼul ʼel y ruʼ ile cu nimarsaj i rakchʼijnak. ', '', '&lt;span class="verse" id="Mark_2_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;—Mi jun cu cʼojoj jun u ʼuʼ ʼel ruʼ jun chʼakap cʼojbal cʼacʼ, man i cʼojbal cʼacʼ xa ca tzajic, y cu rakchʼij i cʼul ʼel y ruʼ ile cu nimarsaj i rakchʼijnak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.022', 'Xak queje ile, mi jun cu coj i víno cʼacʼ chupam i ʼel tak surum. N-quiqui ʼan taj, man i víno cʼacʼ xa cu rakchʼij ni surum. Queʼelok, coʼon mal i surum, xak ca sach i víno. Rumal-i, ʼo u chac qui cʼol i víno cʼacʼ chupam i surum cʼacʼ —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_2_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xak queje ile, mi jun cu coj i víno cʼacʼ chupam i ʼel tak surum. N-quiqui ʼan taj, man i víno cʼacʼ xa cu rakchʼij ni surum. Queʼelok, coʼon mal i surum, xak ca sach i víno. Rumal-i, ʼo u chac qui cʼol i víno cʼacʼ chupam i surum cʼacʼ —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.023', 'Xoʼon panok, chupam jun uxlambal ʼij, i Jesus ca bin cuʼ u tijoxelab chupam juban ticon. I u tijoxelab xe oc chu chʼupic u wach i trígo are xe icʼawic. ', '', '&lt;h2&gt;I Jesus rajaw i uxlambal ʼij&lt;/h2&gt;&lt;span class="verse" id="Mark_2_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Xoʼon panok, chupam jun uxlambal ʼij, i Jesus ca bin cuʼ u tijoxelab chupam juban ticon. I u tijoxelab xe oc chu chʼupic u wach i trígo are xe icʼawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.024', 'I aj Fariséo xqui bij che i Jesus:
—¡Chawilapeʼ! ¿Wuchac i a tijoxelab quiqui ʼan i chac-le che i uxlambal ʼij-i? Ile nim laj mac u bim i mam Moises —xe cha.
', '', '&lt;span class="verse" id="Mark_2_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I aj Fariséo xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¡Chawilapeʼ! ¿Wuchac i a tijoxelab quiqui ʼan i chac-le che i uxlambal ʼij-i? Ile nim laj mac u bim i mam Moises —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.025', 'Teʼuri, ire xu bij chique:
—¿Xataba n-iwetaʼam ti yix wach i xoʼon i mam David ujer are xu tij numic? Ire cuʼ i rachiʼil lic que numic, lic ʼo rajwaxic chique. ', '', '&lt;span class="verse" id="Mark_2_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Teʼuri, ire xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Xataba n-iwetaʼam ti yix wach i xoʼon i mam David ujer are xu tij numic? Ire cuʼ i rachiʼil lic que numic, lic ʼo rajwaxic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.026', 'Rumal-i, xoc ire pa rachoch i Dios y xu tij i caxlanwa chi sujtal chuwach i Dios. Are xu ʼan ile, are i mam Abiatar ʼatz laj sacerdóte. I caxlanwa-le, n-ta re i mam David che. Man cu bij i ʼatbal tzij tzʼibtal can rumal i mam Moises, xui i sacerdóte que tijaw i caxlanwa-le. Péro i mam David xu tijo, xak xu ya chique i je rachiʼil —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_2_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Rumal-i, xoc ire pa rachoch i Dios y xu tij i caxlanwa chi sujtal chuwach i Dios. Are xu ʼan ile, are i mam Abiatar ʼatz laj sacerdóte. I caxlanwa-le, n-ta re i mam David che. Man cu bij i ʼatbal tzij tzʼibtal can rumal i mam Moises, xui i sacerdóte que tijaw i caxlanwa-le. Péro i mam David xu tijo, xak xu ya chique i je rachiʼil —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.027', 'Xak xu bij chique:
—I uxlambal ʼij, Dios u yijbam cumal i winak, ʼanbal ʼutz chique. Mi ʼan jechʼ che; i uxlambal ʼij n-que u rajawij ta piquiwi i winak. ', '', '&lt;span class="verse" id="Mark_2_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—I uxlambal ʼij, Dios u yijbam cumal i winak, ʼanbal ʼutz chique. Mi ʼan jechʼ che; i uxlambal ʼij n-que u rajawij ta piquiwi i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '2.028', 'Rumal-i, yin chi in Achi aj Chicaj, quin rajawij puwi i uxlambal ʼij —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_2_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Rumal-i, yin chi in Achi aj Chicaj, quin rajawij puwi i uxlambal ʼij —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.001', 'Teʼuri i Jesus xoc chic pa molbalʼib. Chila ʼo wi jun achi, xchakij u ʼab rumal siquirnak. ', '', '&lt;h2&gt;I rutzirsaxic i achi chakijnak u ʼab&lt;/h2&gt;&lt;span class="verse" id="Mark_3_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chic pa molbalʼib. Chila ʼo wi jun achi, xchakij u ʼab rumal siquirnak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.002', 'Xak je ʼo jujun chila que tijin chu takexic i Jesus, we crutzirsaj i achi-le pa uxlambal ʼij; cacaj ique quiqui tzucuj u mac. ', '', '&lt;span class="verse" id="Mark_3_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun chila que tijin chu takexic i Jesus, we crutzirsaj i achi-le pa uxlambal ʼij; cacaj ique quiqui tzucuj u mac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.003', 'I Jesus xu ʼijla i achi chi chakijnak u ʼab. Xu bij che:
—Chat walijok, y chat tacʼal chakaxol —coʼon che.
', '', '&lt;span class="verse" id="Mark_3_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I Jesus xu ʼijla i achi chi chakijnak u ʼab. Xu bij che:&lt;p&gt; &lt;/p&gt;—Chat walijok, y chat tacʼal chakaxol —coʼon che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.004', 'Teʼuri xu bij chique i winak:
—I ʼatbal tzij tzʼibtal can rumal i mam Moises, ¿wach cu bij chake, qui bij yix? Chupam i uxlambal ʼij, ¿ʼutz we caka ʼan jun utzil chomal, o xa caka ʼan i n-ʼus taj? ¿ʼUtz caka to jun chuwach i camic? ¿O xa caka camsaj?
Queje ile xu bij i Jesus chique, xui-ri, mi jun xchʼawic. ', '', '&lt;span class="verse" id="Mark_3_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique i winak:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij tzʼibtal can rumal i mam Moises, ¿wach cu bij chake, qui bij yix? Chupam i uxlambal ʼij, ¿ʼutz we caka ʼan jun utzil chomal, o xa caka ʼan i n-ʼus taj? ¿ʼUtz caka to jun chuwach i camic? ¿O xa caka camsaj?&lt;p&gt; &lt;/p&gt;Queje ile xu bij i Jesus chique, xui-ri, mi jun xchʼawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.005', 'Ire toʼ xe u takej, lic coywaric. Xak lic xbison chique man n-xcaj tu tayic usucʼ. Teʼuri xu bij che achi:
—Cha sucʼba i a ʼab —coʼono.
I achi xu sucʼba, y juntir xutziric. ', '', '&lt;span class="verse" id="Mark_3_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Ire toʼ xe u takej, lic coywaric. Xak lic xbison chique man n-xcaj tu tayic usucʼ. Teʼuri xu bij che achi:&lt;p&gt; &lt;/p&gt;—Cha sucʼba i a ʼab —coʼono.&lt;p&gt; &lt;/p&gt;I achi xu sucʼba, y juntir xutziric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.006', 'Are ʼuri i aj Fariséo xe ʼel bic y xe ʼe cuʼ i jutzobaj je u cwenta i ma Heródes. Junam xe oc chu chʼobic wach u camsaxic i Jesus quiqui ʼano.
', '', '&lt;span class="verse" id="Mark_3_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are ʼuri i aj Fariséo xe ʼel bic y xe ʼe cuʼ i jutzobaj je u cwenta i ma Heródes. Junam xe oc chu chʼobic wach u camsaxic i Jesus quiqui ʼano.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.007', 'I Jesus xa ʼe chic cuʼ u tijoxelab chu chiʼ i alagun; xak je qʼui i winak re i jyub Galiléa xe terej chirij. ', '', '&lt;h2&gt;Lic je qʼui i winak xe upon ruʼ i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_3_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I Jesus xa ʼe chic cuʼ u tijoxelab chu chiʼ i alagun; xak je qʼui i winak re i jyub Galiléa xe terej chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.008', 'Are xqui ta u tzijol wach i nimak tak ʼanic ca tijin i Jesus chu ʼonquil, je qʼui i winak xe cʼun aj pa jyub Judéa, xak aj pa Jerusalen, xak aj pa jyub Iduméa, xak aj chʼakap re i ya Jordan, xak xe cʼun i je re i jyub u cwenta i queb tinimit Tíro y Sidon. ', '', '&lt;span class="verse" id="Mark_3_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are xqui ta u tzijol wach i nimak tak ʼanic ca tijin i Jesus chu ʼonquil, je qʼui i winak xe cʼun aj pa jyub Judéa, xak aj pa Jerusalen, xak aj pa jyub Iduméa, xak aj chʼakap re i ya Jordan, xak xe cʼun i je re i jyub u cwenta i queb tinimit Tíro y Sidon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.009', 'I winak je qʼui, lic quiqui pitzʼ i Jesus, rumal-i, xe u tak bi u tijoxelab chu yijbaxic jun bárco che. ', '', '&lt;span class="verse" id="Mark_3_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I winak je qʼui, lic quiqui pitzʼ i Jesus, rumal-i, xe u tak bi u tijoxelab chu yijbaxic jun bárco che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.010', 'Je qʼui i winak je rutzirsam, rumal-i, conojel i iwabib ʼis quiqui min quib ruʼ, cacaj quiqui chap na jubiʼ. ', '', '&lt;span class="verse" id="Mark_3_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Je qʼui i winak je rutzirsam, rumal-i, conojel i iwabib ʼis quiqui min quib ruʼ, cacaj quiqui chap na jubiʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.011', 'Niʼpa i ʼo itzel tak tew chique are xquil u wach i Jesus, juntir xe xuqui chuwach y xe siqʼuin chu bixquil:
—¡Yet at u Cʼojol i Dios! —xe cha che.
', '', '&lt;span class="verse" id="Mark_3_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Niʼpa i ʼo itzel tak tew chique are xquil u wach i Jesus, juntir xe xuqui chuwach y xe siqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Yet at u Cʼojol i Dios! —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.012', 'Xui-ri, i Jesus xe u yaj i itzel tew; n-xu ya ta chique quiqui bij pachin ire.
', '', '&lt;span class="verse" id="Mark_3_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xui-ri, i Jesus xe u yaj i itzel tew; n-xu ya ta chique quiqui bij pachin ire.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.013', 'Xoʼon panok, i Jesus xel bi chuwi jun jyub, y xe u siqʼuij ruʼ pachin craj ire chi que terej chirij; y xe ʼe ruʼ. ', '', '&lt;h2&gt;Wach qui chayic i cablajuj tijoxelab&lt;/h2&gt;&lt;span class="verse" id="Mark_3_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus xel bi chuwi jun jyub, y xe u siqʼuij ruʼ pachin craj ire chi que terej chirij; y xe ʼe ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.014', 'Chila xe u cha cablajuj chique, chi que terej na chirij, lic rachiʼil na ire. Xu ya qui patan que ʼel chu bixquil u tzij i Dios; (ique “apostelab” ca bix chique). ', '', '&lt;span class="verse" id="Mark_3_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Chila xe u cha cablajuj chique, chi que terej na chirij, lic rachiʼil na ire. Xu ya qui patan que ʼel chu bixquil u tzij i Dios; (ique “apostelab” ca bix chique). &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.015', 'Xak xu ya piquiʼab ique que tiqui chi cutzirsaxic iwabib, xak que quesaj bi itzel tew chiquij i winak. ', '', '&lt;span class="verse" id="Mark_3_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xak xu ya piquiʼab ique que tiqui chi cutzirsaxic iwabib, xak que quesaj bi itzel tew chiquij i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.016', 'I qui bi i cablajuj-le: jun, ma Simon, chi xak ma Pédro xbix che rumal i Jesus; ', '', '&lt;span class="verse" id="Mark_3_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I qui bi i cablajuj-le: jun, ma Simon, chi xak ma Pédro xbix che rumal i Jesus; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.017', 'y queb chic: ma Jacóbo, chilam u chaʼ chi ma Wan, chi je racʼal i mam Zebedéo; i Jesus xu ya pi qui bi “Boanérges” chi queʼelok je achiab pacha u chʼawbal jab. ', '', '&lt;span class="verse" id="Mark_3_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;y queb chic: ma Jacóbo, chilam u chaʼ chi ma Wan, chi je racʼal i mam Zebedéo; i Jesus xu ya pi qui bi “Boanérges” chi queʼelok je achiab pacha u chʼawbal jab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.018', 'Xak i ma Andres, i ma Felípe, xak i ma Bartolome, i ma Matéo, i ma Tomas, y jun chic ma Jacóbo chi u cʼojol i mam Alféo; xak i ma Tadéo, xak i jun chic ma Simon chi re i partído cananísta, ', '', '&lt;span class="verse" id="Mark_3_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xak i ma Andres, i ma Felípe, xak i ma Bartolome, i ma Matéo, i ma Tomas, y jun chic ma Jacóbo chi u cʼojol i mam Alféo; xak i ma Tadéo, xak i jun chic ma Simon chi re i partído cananísta, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.019', 'xak ʼo i ma Júdas Iscarióte chi xoʼon panok xu jach i Jesus piquiʼab i tzel que ʼilawic. (Ile je are i cablajuj apostelab je chatal rumal i Jesus.)
I Jesus xtzalij chali cuʼ u tijoxelab ruʼ ja. ', '', '&lt;span class="verse" id="Mark_3_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;xak ʼo i ma Júdas Iscarióte chi xoʼon panok xu jach i Jesus piquiʼab i tzel que ʼilawic. (Ile je are i cablajuj apostelab je chatal rumal i Jesus.)
&lt;h2&gt;ʼO i xe binic chi Jesus coʼon i milágro man u yom u wach ruʼ itzel&lt;/h2&gt;I Jesus xtzalij chali cuʼ u tijoxelab ruʼ ja. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.020', 'Are xe uponic, xqui mol tan chic quib i winak ruʼ; ique n-xtijtaj ta chic qui wa cumal. ', '', '&lt;span class="verse" id="Mark_3_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are xe uponic, xqui mol tan chic quib i winak ruʼ; ique n-xtijtaj ta chic qui wa cumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.021', 'I rachalal i Jesus, are xqui ta rason wach ca tijin chu ʼanic, ique xe ʼe chu cʼamic, man quiqui bij i winak xa chʼujernak, quiqui bij.
', '', '&lt;span class="verse" id="Mark_3_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I rachalal i Jesus, are xqui ta rason wach ca tijin chu ʼanic, ique xe ʼe chu cʼamic, man quiqui bij i winak xa chʼujernak, quiqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.022', 'Xak xe upon i je aj tijonel re i ujer ʼatbal tzij; cʼa pa Jerusalen je petnak wi. Ique xqui bij:
—I achi-ri ʼo puʼab i cajʼatzil itzel, chi Beelzebu u bi. Ruʼ u choʼab i itzel-le, ca tiqui chi relsaxic itzel tak tew —xqui bij.
', '', '&lt;span class="verse" id="Mark_3_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xak xe upon i je aj tijonel re i ujer ʼatbal tzij; cʼa pa Jerusalen je petnak wi. Ique xqui bij:&lt;p&gt; &lt;/p&gt;—I achi-ri ʼo puʼab i cajʼatzil itzel, chi Beelzebu u bi. Ruʼ u choʼab i itzel-le, ca tiqui chi relsaxic itzel tak tew —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.023', 'Rumal ʼuri, i Jesus xe u siqʼuij ruʼ y xu ya jun tijojbal tzij chique.
—I cajʼatzil itzel ¿xataba que resaj i je u takon ire? N-que resaj taj —xu bij i Jesus chique—. ', '', '&lt;span class="verse" id="Mark_3_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Rumal ʼuri, i Jesus xe u siqʼuij ruʼ y xu ya jun tijojbal tzij chique.&lt;p&gt; &lt;/p&gt;—I cajʼatzil itzel ¿xataba que resaj i je u takon ire? N-que resaj taj —xu bij i Jesus chique—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.024', 'Iwetaʼam yix, jun tinimit we quiqui jach quib pa tak tzobajil, y quiqui ʼan chʼoʼoj chiquiwach, n-que najtin taj. ', '', '&lt;span class="verse" id="Mark_3_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Iwetaʼam yix, jun tinimit we quiqui jach quib pa tak tzobajil, y quiqui ʼan chʼoʼoj chiquiwach, n-que najtin taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.025', 'Xak queje juban alal, we n-junam ta qui wach, we quiqui jach quib chiquiwach, xak n-que najtin ta ʼuri. ', '', '&lt;span class="verse" id="Mark_3_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Xak queje juban alal, we n-junam ta qui wach, we quiqui jach quib chiquiwach, xak n-que najtin ta ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.026', 'Xak queje i itzel cuʼ u takon; we quiqui jach quib chiquiwach, y quiqui ʼan chʼoʼoj chiquiwach, n-que najtin taj, xa que sachic. (Rumal-i, i Satanas, n-que resaj ti u takon ire che jun winak.)
', '', '&lt;span class="verse" id="Mark_3_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Xak queje i itzel cuʼ u takon; we quiqui jach quib chiquiwach, y quiqui ʼan chʼoʼoj chiquiwach, n-que najtin taj, xa que sachic. (Rumal-i, i Satanas, n-que resaj ti u takon ire che jun winak.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.027', 'Jun, we craj coc pa rachoch jun achi chi lic ʼo u choʼab, cresajbej re ubitak-re, ire n-ca tiqui ta che we nu yutʼum ti rajaw ja nabe. We u yutʼum chic, teʼuri ca tiqui chi relsaxic ubitak-re. (Queje xu bij i Jesus ile, cʼutbal-re chique chi ire tiquinak chic che itzel.)
', '', '&lt;span class="verse" id="Mark_3_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Jun, we craj coc pa rachoch jun achi chi lic ʼo u choʼab, cresajbej re ubitak-re, ire n-ca tiqui ta che we nu yutʼum ti rajaw ja nabe. We u yutʼum chic, teʼuri ca tiqui chi relsaxic ubitak-re. (Queje xu bij i Jesus ile, cʼutbal-re chique chi ire tiquinak chic che itzel.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.028', '—Chitapeʼ —xu bij i Jesus chique i nimak qui patan—, ʼo u cuytajic i qui mac i winak, wach tak quiqui ʼano, wach tak quiqui bij, ', '', '&lt;span class="verse" id="Mark_3_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;—Chitapeʼ —xu bij i Jesus chique i nimak qui patan—, ʼo u cuytajic i qui mac i winak, wach tak quiqui ʼano, wach tak quiqui bij, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.029', 'xui-ri, we cʼax quiqui bij chirij i u Tewal i Dios, ile lic n-tu cuytajic. Pachin i que ʼanaw ile, lic ʼo ni qui mac chiquij ronojel ʼij sak. ', '', '&lt;span class="verse" id="Mark_3_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;xui-ri, we cʼax quiqui bij chirij i u Tewal i Dios, ile lic n-tu cuytajic. Pachin i que ʼanaw ile, lic ʼo ni qui mac chiquij ronojel ʼij sak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.030', 'Queje xu bij i Jesus ile chique; man qui bim ique chi xa are u tewal itzel ʼo ruʼ, xui-ri, are i u Tewal i Dios ʼo ruʼ.
', '', '&lt;span class="verse" id="Mark_3_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Queje xu bij i Jesus ile chique; man qui bim ique chi xa are u tewal itzel ʼo ruʼ, xui-ri, are i u Tewal i Dios ʼo ruʼ.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.031', 'Teʼuri xe upon i u chuch xak i u chaʼ i Jesus. Xe taqʼui chuwaja y xqui tak u siqʼuixic cuʼ. ', '', '&lt;h2&gt;Je pachin i ratz u chaʼ i Jesus, xak pachin i u chuch&lt;/h2&gt;&lt;span class="verse" id="Mark_3_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri xe upon i u chuch xak i u chaʼ i Jesus. Xe taqʼui chuwaja y xqui tak u siqʼuixic cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.032', 'Chila pa ja, je ʼo uqʼuial winak je chʼuquernak chu tayic u tzij i Jesus. Ique xqui bij:
—I a chuch cuʼ i a chaʼ je ʼo chi sak; cat qui tzucuj —xqui bij che.
', '', '&lt;span class="verse" id="Mark_3_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Chila pa ja, je ʼo uqʼuial winak je chʼuquernak chu tayic u tzij i Jesus. Ique xqui bij:&lt;p&gt; &lt;/p&gt;—I a chuch cuʼ i a chaʼ je ʼo chi sak; cat qui tzucuj —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.033', 'I Jesus xu bij chique:
—¿Pachin in chuch, tak i je in chaʼ? —coʼon chique.
', '', '&lt;span class="verse" id="Mark_3_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Pachin in chuch, tak i je in chaʼ? —coʼon chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.034', 'Teʼuri cʼula xe u takej niʼpa i je chʼuquernak ruʼ y xu bij:
—Niʼpa i je ʼo chi ʼis je are in chuch, xak je in chaʼ. ', '', '&lt;span class="verse" id="Mark_3_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri cʼula xe u takej niʼpa i je chʼuquernak ruʼ y xu bij:&lt;p&gt; &lt;/p&gt;—Niʼpa i je ʼo chi ʼis je are in chuch, xak je in chaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '3.035', 'Pachin i que tijin chu ʼanic pacha craj i Dios, ique-le je are in chaʼ, xak je are i wanab, xak in chuch —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_3_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Pachin i que tijin chu ʼanic pacha craj i Dios, ique-le je are in chaʼ, xak je are i wanab, xak in chuch —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.001', 'Xoʼon panok, i Jesus ʼo chu chiʼ i alagun, ca tijin tan chic chi qui tijoxic i winak. Xe moltaj uqʼuial winak ruʼ, rumal-i, xoc ire chupam jun bárco y xcubi chila. I bárco, ʼo pan jubiʼ pa ya, i uqʼuial winak je ʼo chu chiʼ i ya. ', '', '&lt;h2&gt;I tijojbal tzij re ulew ʼutz, tak i n-ʼus taj&lt;/h2&gt;&lt;span class="verse" id="Mark_4_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus ʼo chu chiʼ i alagun, ca tijin tan chic chi qui tijoxic i winak. Xe moltaj uqʼuial winak ruʼ, rumal-i, xoc ire chupam jun bárco y xcubi chila. I bárco, ʼo pan jubiʼ pa ya, i uqʼuial winak je ʼo chu chiʼ i ya. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.002', 'Ire, mas tak i cʼutunic xoʼon chila. Are i cʼutunic xu ʼano, xui tak ejémplo xu bij chique, tijojbal que. Jun tijojbal tzij xu bij chique are iri:
', '', '&lt;span class="verse" id="Mark_4_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Ire, mas tak i cʼutunic xoʼon chila. Are i cʼutunic xu ʼano, xui tak ejémplo xu bij chique, tijojbal que. Jun tijojbal tzij xu bij chique are iri:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.003', '—Chitape in tzij-i —xu bij chique—. ʼO jun achi xa ʼe chu tiquic u ticon. ', '', '&lt;span class="verse" id="Mark_4_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;—Chitape in tzij-i —xu bij chique—. ʼO jun achi xa ʼe chu tiquic u ticon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.004', 'Are xe u quirij i rijaʼ, ʼo jubiʼ xeʼe tzak pa be; teʼuri xe cʼun i tzʼiquin y xqui tij bic. ', '', '&lt;span class="verse" id="Mark_4_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Are xe u quirij i rijaʼ, ʼo jubiʼ xeʼe tzak pa be; teʼuri xe cʼun i tzʼiquin y xqui tij bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.005', 'Xak jubiʼ chic i ija xeʼe tzak pa tak abaj chi n-ta kas ulew che. Juntir xalax lok, man n-naj taj ʼenak wi pulew. ', '', '&lt;span class="verse" id="Mark_4_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Xak jubiʼ chic i ija xeʼe tzak pa tak abaj chi n-ta kas ulew che. Juntir xalax lok, man n-naj taj ʼenak wi pulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.006', 'Xui-ri, are xel li ʼij, xchakij na, man n-naj taj ʼenak i raʼ. ', '', '&lt;span class="verse" id="Mark_4_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xui-ri, are xel li ʼij, xchakij na, man n-naj taj ʼenak i raʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.007', 'Xak juban chic xeʼe tzak pa qʼuix. Are xpoʼic, xqʼuiy i qʼuix chirij, y xcamic; n-xwachin taj. ', '', '&lt;span class="verse" id="Mark_4_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xak juban chic xeʼe tzak pa qʼuix. Are xpoʼic, xqʼuiy i qʼuix chirij, y xcamic; n-xwachin taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.008', 'Xak ʼo ni ija xeʼe tzak che ulew chom. Chila xpoʼ wi, y xqʼuiyic, y xu ya u wach chom. ʼO jujun xu ya tréinta u wach; xak ʼo i xu ya sesénta u wach; xak ʼo i xu ya cien u wach —xu bij i Jesus chique. ', '', '&lt;span class="verse" id="Mark_4_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xak ʼo ni ija xeʼe tzak che ulew chom. Chila xpoʼ wi, y xqʼuiyic, y xu ya u wach chom. ʼO jujun xu ya tréinta u wach; xak ʼo i xu ya sesénta u wach; xak ʼo i xu ya cien u wach —xu bij i Jesus chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.009', 'Are xutzin u bixquil ile chique, xu bij:
—We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij chique.
', '', '&lt;span class="verse" id="Mark_4_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Are xutzin u bixquil ile chique, xu bij:&lt;p&gt; &lt;/p&gt;—We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.010', 'Are utuquel chic i Jesus, xe upon i cablajuj u tijoxelab ruʼ, tak juban chic chique i je terejnak chirij, xol qui tzʼonoj che chi ca sakirsax chique wach usucʼ i tijojbal tzij xu bij-le. ', '', '&lt;h2&gt;Wach u chac tak i tijojbal tzij xu bij i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_4_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are utuquel chic i Jesus, xe upon i cablajuj u tijoxelab ruʼ, tak juban chic chique i je terejnak chirij, xol qui tzʼonoj che chi ca sakirsax chique wach usucʼ i tijojbal tzij xu bij-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.011', 'Xu bij ʼuri chique:
—Yix ʼut, i Dios u yom chiwe qui ta u be wach usucʼ i ʼatbal tzij aj chicaj, chi n-xa ʼetamax ta ujer. Xui-ri, i juban winak (chi n-que terej ta chwij), xa ruʼ tijojbal tzij ca ʼani i cʼutunic chique. ', '', '&lt;span class="verse" id="Mark_4_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xu bij ʼuri chique:&lt;p&gt; &lt;/p&gt;—Yix ʼut, i Dios u yom chiwe qui ta u be wach usucʼ i ʼatbal tzij aj chicaj, chi n-xa ʼetamax ta ujer. Xui-ri, i juban winak (chi n-que terej ta chwij), xa ruʼ tijojbal tzij ca ʼani i cʼutunic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.012', '(Queʼel chique pacha i tzʼibtal canok rumal i mam Isaías ujer): Quiqui ta neri, péro n-quiqui ta tu be; que tzun neri, péro n-ta quiquilo. Man we ta quiqui ta u be, quiqui jalwachij ʼuri qui cʼaslemal, y ca cuytaj qui mac ʼuri rumal i Dios —xu bij chique.
', '', '&lt;span class="verse" id="Mark_4_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;(Queʼel chique pacha i tzʼibtal canok rumal i mam Isaías ujer): Quiqui ta neri, péro n-quiqui ta tu be; que tzun neri, péro n-ta quiquilo. Man we ta quiqui ta u be, quiqui jalwachij ʼuri qui cʼaslemal, y ca cuytaj qui mac ʼuri rumal i Dios —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.013', 'Teʼuri i Jesus xu bij chique:
—¿Xak yix n-qui ta tu be i tijojbal tzij-le? We n-xi ta tu be i ral tak tzij-le, ¿wach u tayic ʼuri u be i juban tijojbal tzij quin bij na? ', '', '&lt;h2&gt;Wach usucʼ i tijojbal tzij re i aj ticanel&lt;/h2&gt;&lt;span class="verse" id="Mark_4_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Xak yix n-qui ta tu be i tijojbal tzij-le? We n-xi ta tu be i ral tak tzij-le, ¿wach u tayic ʼuri u be i juban tijojbal tzij quin bij na? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.014', 'I aj ticanel re ija-le are aj paxsal u tzij i Dios. ', '', '&lt;span class="verse" id="Mark_4_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I aj ticanel re ija-le are aj paxsal u tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.015', 'I ija-le chi ca tzak pa be, queʼelok pacha quiqui ʼan i winak chi quiqui ta u tzij i Dios, xui-ri, weʼ ca cʼun i Satanas cuʼ, col resaj i tzij chi tictal piqui cʼux. ', '', '&lt;span class="verse" id="Mark_4_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I ija-le chi ca tzak pa be, queʼelok pacha quiqui ʼan i winak chi quiqui ta u tzij i Dios, xui-ri, weʼ ca cʼun i Satanas cuʼ, col resaj i tzij chi tictal piqui cʼux. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.016', 'Xak je ʼo jujun pacha xoʼon i ija chi xtzak pa abaj: are quiqui ta u tzij i Dios, que quicotic quiqui cʼam pa canima. ', '', '&lt;span class="verse" id="Mark_4_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun pacha xoʼon i ija chi xtzak pa abaj: are quiqui ta u tzij i Dios, que quicotic quiqui cʼam pa canima. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.017', 'Xui-ri, n-naj taj ʼenak i raʼ pa canima, rumal-i, n-quiqui chʼij ta naj. Are ca pe i cʼax piquiwi, xak we ca pe chʼoʼoj chiquij rumal u tzij i Dios, xa cacoʼtaj canok. ', '', '&lt;span class="verse" id="Mark_4_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xui-ri, n-naj taj ʼenak i raʼ pa canima, rumal-i, n-quiqui chʼij ta naj. Are ca pe i cʼax piquiwi, xak we ca pe chʼoʼoj chiquij rumal u tzij i Dios, xa cacoʼtaj canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.018', 'Xak je ʼo jujun pacha ija xtzak pa qʼuix: quiqui ta u tzij i Dios; ', '', '&lt;span class="verse" id="Mark_4_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun pacha ija xtzak pa qʼuix: quiqui ta u tzij i Dios; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.019', 'xui-ri, xa que cʼachir rumal i cʼaslemal waral chuwach i jyub taʼaj; xak i qui rayijbal re mas i mer, mas i ubitak que, xak u rayijbal re i qui chʼacul, niʼpa tak ile xa cu chup i ʼutz laj tzij. Rumal-i, n-ta ca wachin chique. ', '', '&lt;span class="verse" id="Mark_4_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;xui-ri, xa que cʼachir rumal i cʼaslemal waral chuwach i jyub taʼaj; xak i qui rayijbal re mas i mer, mas i ubitak que, xak u rayijbal re i qui chʼacul, niʼpa tak ile xa cu chup i ʼutz laj tzij. Rumal-i, n-ta ca wachin chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.020', 'Xui-ri, je ʼo jujun quiqui ta u tzij i Dios, y quiqui cʼam piqui cʼux. Teʼuri, ca wachin chique pacha xoʼon i ija tictal pa ʼutz laj ulew. ʼO jujun chique je pacha i rakan chi xu ya tréinta u wach, xak ʼo jujun quiqui ya sesénta, xak ʼo jujun quiqui ya cien u wach —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_4_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xui-ri, je ʼo jujun quiqui ta u tzij i Dios, y quiqui cʼam piqui cʼux. Teʼuri, ca wachin chique pacha xoʼon i ija tictal pa ʼutz laj ulew. ʼO jujun chique je pacha i rakan chi xu ya tréinta u wach, xak ʼo jujun quiqui ya sesénta, xak ʼo jujun quiqui ya cien u wach —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.021', 'I Jesus xak xu bij chic jun tzij chique.
—Jun cantil, are qui cʼam lok, ¿xataba qui ya chuxeʼ jun cajon, o chuxeʼ jun chʼat? N-queje ta ile. I ʼaʼ-le ca yaʼ ʼan chicaj, man ca ʼe u tzunbal. ', '', '&lt;h2&gt;I tijojbal tzij re i cantil&lt;/h2&gt;&lt;span class="verse" id="Mark_4_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus xak xu bij chic jun tzij chique.&lt;p&gt; &lt;/p&gt;—Jun cantil, are qui cʼam lok, ¿xataba qui ya chuxeʼ jun cajon, o chuxeʼ jun chʼat? N-queje ta ile. I ʼaʼ-le ca yaʼ ʼan chicaj, man ca ʼe u tzunbal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.022', 'Xui-ri, niʼpa i n-ca ʼiltaj taj, ca ʼalijinsax na; xak ronojel niʼpa i uwam u wach ʼis quelsax na chi sak. ', '', '&lt;span class="verse" id="Mark_4_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xui-ri, niʼpa i n-ca ʼiltaj taj, ca ʼalijinsax na; xak ronojel niʼpa i uwam u wach ʼis quelsax na chi sak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.023', 'We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij chique.
', '', '&lt;span class="verse" id="Mark_4_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.024', 'I Jesus xak xu bij i tzij-i chique:
—Chi cojo retalil che wach quix tijin chu tayic. Ruʼ i pajbal pa quix pajan wi yix, xak ruʼ ile ca paj wi chiwe yix. (We lic qui coj retalil che i sak laj tzij re i Dios, ire cu ya ʼuri mas u nojbal chiwe. Y coʼon panok, mas cu ya na chic chiwe puwi ile.) ', '', '&lt;span class="verse" id="Mark_4_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I Jesus xak xu bij i tzij-i chique:&lt;p&gt; &lt;/p&gt;—Chi cojo retalil che wach quix tijin chu tayic. Ruʼ i pajbal pa quix pajan wi yix, xak ruʼ ile ca paj wi chiwe yix. (We lic qui coj retalil che i sak laj tzij re i Dios, ire cu ya ʼuri mas u nojbal chiwe. Y coʼon panok, mas cu ya na chic chiwe puwi ile.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.025', 'Jun, we ʼo na chic ʼo ruʼ, ca yaʼ na mas ʼuri che; xak queje i jun, we n-ta ʼo ruʼ, quelsax na i jubiʼ ʼo ruʼ —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_4_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Jun, we ʼo na chic ʼo ruʼ, ca yaʼ na mas ʼuri che; xak queje i jun, we n-ta ʼo ruʼ, quelsax na i jubiʼ ʼo ruʼ —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.026', 'I Jesus xu bij chic jun tijojbal tzij chique. Xu bij:
—I ʼatbal tzij re i Dios quin junamsaj ruʼ juban ija tictal na. Queʼelok pacha coʼon jun achi chi cu tic ija pulew. ', '', '&lt;h2&gt;I tijojbal tzij re u qʼuiyic jun ija&lt;/h2&gt;&lt;span class="verse" id="Mark_4_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chic jun tijojbal tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re i Dios quin junamsaj ruʼ juban ija tictal na. Queʼelok pacha coʼon jun achi chi cu tic ija pulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.027', 'Ire n-cretamaj taj wach i qʼuiyic coʼon u ticon; ire xa cwaric ʼab ʼab, xak cwalij ronojel u sakirbal. Queje coʼon ire ile, tzʼakat quicʼaw i tiémpo. I u ticon ca poʼic y ca qʼuiyic. Ire n-retaʼam taj wach mer rumal xpoʼ u ticon. ', '', '&lt;span class="verse" id="Mark_4_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Ire n-cretamaj taj wach i qʼuiyic coʼon u ticon; ire xa cwaric ʼab ʼab, xak cwalij ronojel u sakirbal. Queje coʼon ire ile, tzʼakat quicʼaw i tiémpo. I u ticon ca poʼic y ca qʼuiyic. Ire n-retaʼam taj wach mer rumal xpoʼ u ticon. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.028', 'Utuquel i ulew xoʼon che i ticon chi xqʼuiyic y xwachinic. Nabe, calaxic, teʼuri ca qʼuiy i rakan, ca pe i ochʼ che, y pa qʼuisbal-re, coʼon ʼan. ', '', '&lt;span class="verse" id="Mark_4_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Utuquel i ulew xoʼon che i ticon chi xqʼuiyic y xwachinic. Nabe, calaxic, teʼuri ca qʼuiy i rakan, ca pe i ochʼ che, y pa qʼuisbal-re, coʼon ʼan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.029', 'Are ya ʼan chic, ca jachʼic, man ya xu rik i ʼij molbal-re.
(Queje i tijojbal tzij ile xu bij i Jesus, wach coʼon i Dios chique i qui jachom chic quib puʼab i u ʼatbal tzij ire.)
', '', '&lt;span class="verse" id="Mark_4_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are ya ʼan chic, ca jachʼic, man ya xu rik i ʼij molbal-re.&lt;p&gt; &lt;/p&gt;(Queje i tijojbal tzij ile xu bij i Jesus, wach coʼon i Dios chique i qui jachom chic quib puʼab i u ʼatbal tzij ire.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.030', 'Teʼuri i Jesus xu bij chique:
—I ʼatbal tzij re i Dios, ¿wach nawi quin junamsaj ruʼ, sakirsabal-re chiwe wach usucʼ? ¿Wach i tijojbal tzij quin bij na chiwe? ', '', '&lt;h2&gt;I tijojbal tzij re ija mostas&lt;/h2&gt;&lt;span class="verse" id="Mark_4_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij re i Dios, ¿wach nawi quin junamsaj ruʼ, sakirsabal-re chiwe wach usucʼ? ¿Wach i tijojbal tzij quin bij na chiwe? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.031', 'Quin junamsaj ruʼ jun ija re i mostas. I ija-le ca tiquic, are mer chʼutik chuwach ronojel niʼpa ija. ', '', '&lt;span class="verse" id="Mark_4_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Quin junamsaj ruʼ jun ija re i mostas. I ija-le ca tiquic, are mer chʼutik chuwach ronojel niʼpa ija. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.032', 'Xui-ri, are tictal chic, ca qʼuiyic, y mas nim queʼel chiquiwach i rachiʼil. Ca pe i u ʼab lic nimak, teʼuri que cʼun i tzʼiquin y quiqui ʼan ni qui soc che u mujbal u ʼab.
(Queje i tijojbal tzij ile xu bij i Jesus chique, sakirsabal-re wach u qʼuiyaric i u ʼatbal tzij i Dios chique.)
', '', '&lt;span class="verse" id="Mark_4_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xui-ri, are tictal chic, ca qʼuiyic, y mas nim queʼel chiquiwach i rachiʼil. Ca pe i u ʼab lic nimak, teʼuri que cʼun i tzʼiquin y quiqui ʼan ni qui soc che u mujbal u ʼab.&lt;p&gt; &lt;/p&gt;(Queje i tijojbal tzij ile xu bij i Jesus chique, sakirsabal-re wach u qʼuiyaric i u ʼatbal tzij i Dios chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.033', 'Queje u cʼutic ile xoʼon i Jesus. Ruʼ uqʼuial ejémplo xe u tijoj che u tzij i Dios, niʼpa tak i tzij chi xe tiqui chu tayic u be. ', '', '&lt;h2&gt;I cʼutunic xoʼon i Jesus chique i winak xa ruʼ tijojbal tzij&lt;/h2&gt;&lt;span class="verse" id="Mark_4_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Queje u cʼutic ile xoʼon i Jesus. Ruʼ uqʼuial ejémplo xe u tijoj che u tzij i Dios, niʼpa tak i tzij chi xe tiqui chu tayic u be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.034', 'Are i cʼutunic xoʼon ire chique i winak, ʼis ruʼ tijojbal tzij xu bij chique, xui-ri xu sakij ni usucʼ chique u tijoxelab.
', '', '&lt;span class="verse" id="Mark_4_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Are i cʼutunic xoʼon ire chique i winak, ʼis ruʼ tijojbal tzij xu bij chique, xui-ri xu sakij ni usucʼ chique u tijoxelab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.035', 'Che i ʼij-le, are xoc aʼab, teʼuri i Jesus xu bij chique u tijoxelab:
—Coj ʼax chʼaka alagun —xu bij.
', '', '&lt;h2&gt;I Jesus xu ʼatij i tew, xak u choʼab i ya&lt;/h2&gt;&lt;span class="verse" id="Mark_4_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Che i ʼij-le, are xoc aʼab, teʼuri i Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Coj ʼax chʼaka alagun —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.036', 'Teʼuri xe qui canaj can i winak y xqui cʼam bi i Jesus chupam i bárco pa cul wi ire; queje ile xqui ticba ʼenam puwi i ya. Xak je ʼo juban chic winak xe ʼe cuʼ chupam juban chic bárco. ', '', '&lt;span class="verse" id="Mark_4_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Teʼuri xe qui canaj can i winak y xqui cʼam bi i Jesus chupam i bárco pa cul wi ire; queje ile xqui ticba ʼenam puwi i ya. Xak je ʼo juban chic winak xe ʼe cuʼ chupam juban chic bárco. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.037', 'Are que tijin chi be, xpe jun nim laj tew puwi i ya. I tew lic cu yac u wi i ya chicaj, y xoc chupam qui bárco, laj xnoj rumal. ', '', '&lt;span class="verse" id="Mark_4_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Are que tijin chi be, xpe jun nim laj tew puwi i ya. I tew lic cu yac u wi i ya chicaj, y xoc chupam qui bárco, laj xnoj rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.038', 'I Jesus xa cwar pan che u je i bárco, u yom u jolom pujaʼ u chʼaquet. Ique xe ʼe qui cʼastaj, y xqui bij che:
—Mayes, ¿yet n-ta ca chʼob che we coj muktaj pa ya? —xe cha.
', '', '&lt;span class="verse" id="Mark_4_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;I Jesus xa cwar pan che u je i bárco, u yom u jolom pujaʼ u chʼaquet. Ique xe ʼe qui cʼastaj, y xqui bij che:&lt;p&gt; &lt;/p&gt;—Mayes, ¿yet n-ta ca chʼob che we coj muktaj pa ya? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.039', 'I Jesus xwalijic y xu ʼatij i tew. Xak xu bij che i ya:
—¡Cha mayij! ¡Chat uxlanok! —xu bij che.
Teʼuri i tew juntir xuxlanic; xak i ya n-ca silab ta chic.
', '', '&lt;span class="verse" id="Mark_4_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;I Jesus xwalijic y xu ʼatij i tew. Xak xu bij che i ya:&lt;p&gt; &lt;/p&gt;—¡Cha mayij! ¡Chat uxlanok! —xu bij che.&lt;p&gt; &lt;/p&gt;Teʼuri i tew juntir xuxlanic; xak i ya n-ca silab ta chic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.040', 'Teʼuri xu bij chique u tijoxelab:
—¿Wuchac xi xij iwib? ¿Wuchac n-tu cubibal i cʼux chwe? —xu bij chique.
', '', '&lt;span class="verse" id="Mark_4_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—¿Wuchac xi xij iwib? ¿Wuchac n-tu cubibal i cʼux chwe? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '4.041', 'Ique lic je ocnak ʼil che. Xqui bij chiquiwach:
—¿Bi chi achial ile chi que cojon i ya che, xak i loʼlaj tew? —xqui bij chiquiwach.
', '', '&lt;span class="verse" id="Mark_4_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Ique lic je ocnak ʼil che. Xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Bi chi achial ile chi que cojon i ya che, xak i loʼlaj tew? —xqui bij chiquiwach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.001', 'Teʼuri xe upon chʼaka ya, pa qui jyub i aj Gadára. ', '', '&lt;h2&gt;Wach i relsaxic itzel tew che achi aj Gadára&lt;/h2&gt;&lt;span class="verse" id="Mark_5_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri xe upon chʼaka ya, pa qui jyub i aj Gadára. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.002', 'I Jesus, are xel pa bárco, juntir xcʼun jun achi ruʼ, chi ʼo itzel tew che. I achi-ri petnak pa tak pantion chuwi camnak. ', '', '&lt;span class="verse" id="Mark_5_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I Jesus, are xel pa bárco, juntir xcʼun jun achi ruʼ, chi ʼo itzel tew che. I achi-ri petnak pa tak pantion chuwi camnak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.003', 'Chila jekel wi pa tak pec que i camnak. Ire tijtal u ximic cumal i winak, xui-ri ne te tiquinak che, xak mi ne ruʼ carena. ', '', '&lt;span class="verse" id="Mark_5_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Chila jekel wi pa tak pec que i camnak. Ire tijtal u ximic cumal i winak, xui-ri ne te tiquinak che, xak mi ne ruʼ carena. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.004', 'Qʼuialmul qui yutʼum i rakan i u ʼab che carena, xui-ri ire ʼis xu tʼukij, xa xoʼon chʼakatak che. Niʼpa i chʼichʼ qui cojom che i rakan, ʼis ca tiqui chu ʼajic; mi jun winak ca tiqui che ire. ', '', '&lt;span class="verse" id="Mark_5_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Qʼuialmul qui yutʼum i rakan i u ʼab che carena, xui-ri ire ʼis xu tʼukij, xa xoʼon chʼakatak che. Niʼpa i chʼichʼ qui cojom che i rakan, ʼis ca tiqui chu ʼajic; mi jun winak ca tiqui che ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.005', 'Chi paʼij chi chaʼab, ire xmalcat tzam tak jyub xak chuwi camnak, toʼ ca siqʼuinic. Xak cu joy i rij che abaj. ', '', '&lt;span class="verse" id="Mark_5_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Chi paʼij chi chaʼab, ire xmalcat tzam tak jyub xak chuwi camnak, toʼ ca siqʼuinic. Xak cu joy i rij che abaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.006', 'Are xril u wach i Jesus cʼa naj panok, xa ʼe paʼanem, y xuqui chuwach. ', '', '&lt;span class="verse" id="Mark_5_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are xril u wach i Jesus cʼa naj panok, xa ʼe paʼanem, y xuqui chuwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.007', 'Teʼuri xsiqʼuin chu bixquil i tzij-i:
—Jesus, yet chi at u Cʼojol i Dios chi ʼatz u ʼij, ¿wuchac xat cʼun wuʼ? Chuwach i Dios lic quin tzʼonoj chawe, min a ya pa cʼax —xu bij. (Ile are itzel tew xchʼawic.)
', '', '&lt;span class="verse" id="Mark_5_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Teʼuri xsiqʼuin chu bixquil i tzij-i:&lt;p&gt; &lt;/p&gt;—Jesus, yet chi at u Cʼojol i Dios chi ʼatz u ʼij, ¿wuchac xat cʼun wuʼ? Chuwach i Dios lic quin tzʼonoj chawe, min a ya pa cʼax —xu bij. (Ile are itzel tew xchʼawic.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.008', 'Queje ile xu bij, man i Jesus ca tijin chu bixquil:
—Yet, at itzel tew, ¡chat el che i achi-ri!
', '', '&lt;span class="verse" id="Mark_5_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Queje ile xu bij, man i Jesus ca tijin chu bixquil:&lt;p&gt; &lt;/p&gt;—Yet, at itzel tew, ¡chat el che i achi-ri!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.009', 'I Jesus xu tzʼonoj che:
—¿Wach a bi? —xu bij che.
Ire xu bij:
—Are in bi, are “Legion”, man oj qʼui —xu bij.
', '', '&lt;span class="verse" id="Mark_5_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I Jesus xu tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wach a bi? —xu bij che.&lt;p&gt; &lt;/p&gt;Ire xu bij:&lt;p&gt; &lt;/p&gt;—Are in bi, are “Legion”, man oj qʼui —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.010', 'Ire lic xu tzʼonoj che i Jesus:
—Moj a tak bi naj che i jyub-i —xu bij.
', '', '&lt;span class="verse" id="Mark_5_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Ire lic xu tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;—Moj a tak bi naj che i jyub-i —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.011', 'Chu nakaj i jyub-le ʼo juban ak que tijin chi waʼim chuwach jun ral jyub. ', '', '&lt;span class="verse" id="Mark_5_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Chu nakaj i jyub-le ʼo juban ak que tijin chi waʼim chuwach jun ral jyub. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.012', 'Are ʼuri i itzel tak tew lic xqui tzʼonoj che i Jesus:
—Choj a taka bi cuʼ i ak-le; cha ya chake coj oc chique —que cha.
', '', '&lt;span class="verse" id="Mark_5_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Are ʼuri i itzel tak tew lic xqui tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;—Choj a taka bi cuʼ i ak-le; cha ya chake coj oc chique —que cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.013', 'Teʼuri i Jesus xu ya chique pacha i xqui tzʼonoj; xe ʼel ʼuri che achi y xe oc chique i ak. I uqʼuial ak-le, juntir xe ʼe paʼanem chu chiʼ jun siwan; xe tzak bi cʼa pa ya y xe jiʼic. Colo je queb mil i qui qʼuial i ak-le.
', '', '&lt;span class="verse" id="Mark_5_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu ya chique pacha i xqui tzʼonoj; xe ʼel ʼuri che achi y xe oc chique i ak. I uqʼuial ak-le, juntir xe ʼe paʼanem chu chiʼ jun siwan; xe tzak bi cʼa pa ya y xe jiʼic. Colo je queb mil i qui qʼuial i ak-le.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.014', 'I aj yuʼul ak lic xqui xij quib. Xe ʼe paʼanem chija, xak pa tak jyub, chu lapic wach i xquilo. Y xe cʼun i winak chi rilic wach i xa ʼani chila. ', '', '&lt;span class="verse" id="Mark_5_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I aj yuʼul ak lic xqui xij quib. Xe ʼe paʼanem chija, xak pa tak jyub, chu lapic wach i xquilo. Y xe cʼun i winak chi rilic wach i xa ʼani chila. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.015', 'Are xe upon ruʼ i Jesus, xquil i achi chi ʼo te itzel tew che. Xquilo chi toʼ chʼucul chubic, jic chic u nojbal; u cojom chic u ʼuʼ. Rumal-i, i winak-i lic xqui xij quib che i Jesus. ', '', '&lt;span class="verse" id="Mark_5_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are xe upon ruʼ i Jesus, xquil i achi chi ʼo te itzel tew che. Xquilo chi toʼ chʼucul chubic, jic chic u nojbal; u cojom chic u ʼuʼ. Rumal-i, i winak-i lic xqui xij quib che i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.016', 'Je niʼpa i quilom wach u ʼanom i Jesus che i achi-le, ique xqui lap chique i juban chi cʼa te xe cʼunic. Xqui bij chique wach i quelsaxic itzel tew che i achi, xak wach tak xqui ʼan i ak. ', '', '&lt;span class="verse" id="Mark_5_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Je niʼpa i quilom wach u ʼanom i Jesus che i achi-le, ique xqui lap chique i juban chi cʼa te xe cʼunic. Xqui bij chique wach i quelsaxic itzel tew che i achi, xak wach tak xqui ʼan i ak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.017', 'Teʼuri i winak xe oc chu tzʼonoxic pawor che i Jesus chi quel chila che i qui jyub.
', '', '&lt;span class="verse" id="Mark_5_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Teʼuri i winak xe oc chu tzʼonoxic pawor che i Jesus chi quel chila che i qui jyub.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.018', 'Are ca tijin roquic i Jesus pa bárco, i achi, chi ʼo te itzel tew che, lic craj te ʼenam ruʼ. ', '', '&lt;span class="verse" id="Mark_5_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Are ca tijin roquic i Jesus pa bárco, i achi, chi ʼo te itzel tew che, lic craj te ʼenam ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.019', 'I Jesus n-xu ya ta che, xui-ri, xu bij che:
—Jat che awachoch cuʼ awachalal. Ja lapa chique wach u ʼanom i Awajwal chawe; ja bij chique chi lic xril toʼbal a wach —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Mark_5_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Jesus n-xu ya ta che, xui-ri, xu bij che:&lt;p&gt; &lt;/p&gt;—Jat che awachoch cuʼ awachalal. Ja lapa chique wach u ʼanom i Awajwal chawe; ja bij chique chi lic xril toʼbal a wach —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.020', 'I achi-le xa ʼe chu bixquil wach i utzil chomal u ʼanom i Jesus che. Naj xa ʼec; xbin chupam i lajuj tinimit re i Decápolis chu bixquil. Conojel i winak chila lic xqui bisoj wach i cu bij i achi-ri.
', '', '&lt;span class="verse" id="Mark_5_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I achi-le xa ʼe chu bixquil wach i utzil chomal u ʼanom i Jesus che. Naj xa ʼec; xbin chupam i lajuj tinimit re i Decápolis chu bixquil. Conojel i winak chila lic xqui bisoj wach i cu bij i achi-ri.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.021', 'I Jesus, are xa ʼax chali ruʼ u bárco jewa, lic xe moltaj mas i winak ruʼ. Ire xcanaj can chu chiʼ i alagun. ', '', '&lt;h2&gt;Wach i rutzirsaxic u miʼal i ma Jáiro, xak i rutzirsaxic i ixok chi xu chap u ʼuʼ i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_5_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus, are xa ʼax chali ruʼ u bárco jewa, lic xe moltaj mas i winak ruʼ. Ire xcanaj can chu chiʼ i alagun. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.022', 'Are ʼo chila, xupon jun achi ruʼ, ma Jáiro u bi. Ire jun cajwal re i molbalʼib que i aj Israel. Are xril i Jesus, xuqui chuwach. ', '', '&lt;span class="verse" id="Mark_5_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are ʼo chila, xupon jun achi ruʼ, ma Jáiro u bi. Ire jun cajwal re i molbalʼib que i aj Israel. Are xril i Jesus, xuqui chuwach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.023', 'Lic xu tzʼonoj jun pawor che. Xu bij:
—I ral in miʼal ya ca camic —coʼono—. Cha ʼana utzil chomal chwe, jo wuʼ, que a ya a ʼab puwi, cawutzirsabej-re, ca yabej re u cʼaslemal —xu bij.
', '', '&lt;span class="verse" id="Mark_5_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Lic xu tzʼonoj jun pawor che. Xu bij:&lt;p&gt; &lt;/p&gt;—I ral in miʼal ya ca camic —coʼono—. Cha ʼana utzil chomal chwe, jo wuʼ, que a ya a ʼab puwi, cawutzirsabej-re, ca yabej re u cʼaslemal —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.024', 'Are ʼuri i Jesus xa ʼe ruʼ i achi. Mas je qʼui i winak je teren chirij; lic ca bajbat i Jesus chiquixol i winak. ', '', '&lt;span class="verse" id="Mark_5_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xa ʼe ruʼ i achi. Mas je qʼui i winak je teren chirij; lic ca bajbat i Jesus chiquixol i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.025', 'Xak chiquixol i winak-le, ʼo jun ixok lic iwab. ʼO chic cablajuj junab u jekanic i yobil ʼo che; n-ca tani ta chic u yobil. ', '', '&lt;span class="verse" id="Mark_5_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Xak chiquixol i winak-le, ʼo jun ixok lic iwab. ʼO chic cablajuj junab u jekanic i yobil ʼo che; n-ca tani ta chic u yobil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.026', 'Ire ʼenak chic cuʼ uqʼuial aj cun, lic u chʼijom i cʼax rumal wach ʼantal che cumal, xak ʼis u sachom chic u mer rumal; xui-ri n-ta xoʼon re che; xa mas cu tzʼabij u yobil. ', '', '&lt;span class="verse" id="Mark_5_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Ire ʼenak chic cuʼ uqʼuial aj cun, lic u chʼijom i cʼax rumal wach ʼantal che cumal, xak ʼis u sachom chic u mer rumal; xui-ri n-ta xoʼon re che; xa mas cu tzʼabij u yobil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.027', 'Ire u tom rason wach coʼon i Jesus, rumal-i xe u mina rib chiquixol i uqʼuial winak chirij i Jesus, y xu yin pan u ʼuʼ. ', '', '&lt;span class="verse" id="Mark_5_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Ire u tom rason wach coʼon i Jesus, rumal-i xe u mina rib chiquixol i uqʼuial winak chirij i Jesus, y xu yin pan u ʼuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.028', 'Queje xoʼon ire ile, man xu chʼob ire, “We xui quin chap jubiʼ u ʼuʼ i Jesus, quin utzir na.” ', '', '&lt;span class="verse" id="Mark_5_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Queje xoʼon ire ile, man xu chʼob ire, “We xui quin chap jubiʼ u ʼuʼ i Jesus, quin utzir na.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.029', 'Are xu chap u ʼuʼ, juntir xtan can u quiqʼuel. Xu na pu chʼacul chi ya xutzir i cʼax che. ', '', '&lt;span class="verse" id="Mark_5_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are xu chap u ʼuʼ, juntir xtan can u quiqʼuel. Xu na pu chʼacul chi ya xutzir i cʼax che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.030', 'Xak i Jesus xu naʼo chi xak teʼet, ruʼ u choʼab, rutzirsam jun iwab. Rumal-i, xtzun chirij.
—¿Pachin xu yin in ʼuʼ? —xu bij.
', '', '&lt;span class="verse" id="Mark_5_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xak i Jesus xu naʼo chi xak teʼet, ruʼ u choʼab, rutzirsam jun iwab. Rumal-i, xtzun chirij.&lt;p&gt; &lt;/p&gt;—¿Pachin xu yin in ʼuʼ? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.031', 'I u tijoxelab xqui bij che:
—Lic je qʼui i winak cat qui pitzʼo, y yet ca bij ¿pachin xu yin in ʼuʼ? ¿Wach retamaxic caka ʼan yoj ile? —xe cha.
', '', '&lt;span class="verse" id="Mark_5_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I u tijoxelab xqui bij che:&lt;p&gt; &lt;/p&gt;—Lic je qʼui i winak cat qui pitzʼo, y yet ca bij ¿pachin xu yin in ʼuʼ? ¿Wach retamaxic caka ʼan yoj ile? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.032', 'I Jesus n-xu mayij ti u tzucuxic chiquixol i winak pachin xyinawic. ', '', '&lt;span class="verse" id="Mark_5_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I Jesus n-xu mayij ti u tzucuxic chiquixol i winak pachin xyinawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.033', 'Rumal-i, i ixok lic xu xij rib, man retaʼam wach u ʼanom che. Ca barbatic xa ʼec, y xuqui chuwach i Jesus. Xu bij i sak laj tzij che, ronojel niʼpa i u ʼanom. ', '', '&lt;span class="verse" id="Mark_5_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Rumal-i, i ixok lic xu xij rib, man retaʼam wach u ʼanom che. Ca barbatic xa ʼec, y xuqui chuwach i Jesus. Xu bij i sak laj tzij che, ronojel niʼpa i u ʼanom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.034', 'I Jesus xu bij che:
—Wacʼal, at utzirnak chic rumal u cubibal a cʼux chwe. ʼUtz cat ʼec, mat cʼachir chic; ya xutzir i yobil chawij —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Mark_5_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—Wacʼal, at utzirnak chic rumal u cubibal a cʼux chwe. ʼUtz cat ʼec, mat cʼachir chic; ya xutzir i yobil chawij —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.035', 'Cʼa ca tijin i Jesus chi tzij, ʼo i xe cʼun pa rachoch i cajwal i molbalʼib. Xqui bij che i qui patron:
—I a miʼal ya xcamic. N-ta chic u chac we ca ʼe i Mayes awuʼ —xqui bij.
', '', '&lt;span class="verse" id="Mark_5_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Cʼa ca tijin i Jesus chi tzij, ʼo i xe cʼun pa rachoch i cajwal i molbalʼib. Xqui bij che i qui patron:&lt;p&gt; &lt;/p&gt;—I a miʼal ya xcamic. N-ta chic u chac we ca ʼe i Mayes awuʼ —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.036', 'Xui-ri i Jesus n-xoʼon ta cas chique, wach i xqui bij; xa xu bij che i cajwal i molbalʼib:
—Mat oc ʼil che, xui-ri, cha cuba a cʼux chwe —xu bij che.
', '', '&lt;span class="verse" id="Mark_5_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Xui-ri i Jesus n-xoʼon ta cas chique, wach i xqui bij; xa xu bij che i cajwal i molbalʼib:&lt;p&gt; &lt;/p&gt;—Mat oc ʼil che, xui-ri, cha cuba a cʼux chwe —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.037', 'I Jesus n-xu ya taj chi que rachiʼilaj bi i winak ruʼ. Xui i ma Pédro, i ma Jacóbo, xak i ma Wan chi u chaʼ i ma Jacóbo, xui ique xe u cʼam bi ruʼ. ', '', '&lt;span class="verse" id="Mark_5_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;I Jesus n-xu ya taj chi que rachiʼilaj bi i winak ruʼ. Xui i ma Pédro, i ma Jacóbo, xak i ma Wan chi u chaʼ i ma Jacóbo, xui ique xe u cʼam bi ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.038', 'Are xe upon ruʼ rachoch i cajwal i molbalʼib, xril i Jesus chi toʼ que chʼokowic; i winak toʼ que oʼic, toʼ que siqʼuinic. ', '', '&lt;span class="verse" id="Mark_5_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Are xe upon ruʼ rachoch i cajwal i molbalʼib, xril i Jesus chi toʼ que chʼokowic; i winak toʼ que oʼic, toʼ que siqʼuinic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.039', 'Are xoc pa ja, xu bij chique:
—¿Wuchac xa quix chʼokowic, xa quix oʼic? I ali n-camnak taj; xa cwaric —xu bij chique.
', '', '&lt;span class="verse" id="Mark_5_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Are xoc pa ja, xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac xa quix chʼokowic, xa quix oʼic? I ali n-camnak taj; xa cwaric —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.040', 'Xui-ri, i winak xa xqui tzeʼej che. Are ʼuri i Jesus ʼis xe resaj li chi sak conojel, teʼuri xe u cʼam bi i u kajaw i u chuch i ali, chilam i je rachiʼil bi ire, y xe oc pa ʼo wi ali. ', '', '&lt;span class="verse" id="Mark_5_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Xui-ri, i winak xa xqui tzeʼej che. Are ʼuri i Jesus ʼis xe resaj li chi sak conojel, teʼuri xe u cʼam bi i u kajaw i u chuch i ali, chilam i je rachiʼil bi ire, y xe oc pa ʼo wi ali. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.041', 'Teʼuri xu chap u ʼab i ali, y xu bij pa qui chʼawbal ique:
—Talíta cúmi —xu bij.
Are i usucʼ i tzij-le: Ali, quin bij chawe, chat walijok, coʼono.
', '', '&lt;span class="verse" id="Mark_5_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Teʼuri xu chap u ʼab i ali, y xu bij pa qui chʼawbal ique:&lt;p&gt; &lt;/p&gt;—Talíta cúmi —xu bij.&lt;p&gt; &lt;/p&gt;Are i usucʼ i tzij-le: Ali, quin bij chawe, chat walijok, coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.042', 'Are xu bij ile, i ali juntir xwalijic, y xoc chi binic; i je ʼo chila lic xqui bisoj che. I ali ʼo cablajuj junab che. ', '', '&lt;span class="verse" id="Mark_5_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are xu bij ile, i ali juntir xwalijic, y xoc chi binic; i je ʼo chila lic xqui bisoj che. I ali ʼo cablajuj junab che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '5.043', 'I Jesus lic xu bij chique chi mi ta jubiʼ quiqui chʼachʼa wach i xquilo. Xak xu bij chique chi quiqui ya u wa i ali.
', '', '&lt;span class="verse" id="Mark_5_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;I Jesus lic xu bij chique chi mi ta jubiʼ quiqui chʼachʼa wach i xquilo. Xak xu bij chique chi quiqui ya u wa i ali.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.001', 'Teʼuri, i Jesus xel che i tinimit-le cuʼ u tijoxelab, y xe ʼe pu tinimit ire. ', '', '&lt;h2&gt;I aj Nazaret xqui xutuj u cʼutunic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_6_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri, i Jesus xel che i tinimit-le cuʼ u tijoxelab, y xe ʼe pu tinimit ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.002', 'Are xu rik i uxlambal ʼij, xoc pa ja re i molbalʼib y xoc chu cʼutic chique i winak chila. Je qʼui i winak je ʼo chila; niʼpa i xqui ta u tzij lic xqui bisoj che.
—¿Pa xu tijoj wi rib i achi-le ronojel wach ca tijin chu ʼonquil? —xqui bij—. ¿Wach usucʼ i nojbal chi yatal che? ¿Pa xu nawsaj wi rib ire-le chu ʼonquil i milágro? ', '', '&lt;span class="verse" id="Mark_6_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are xu rik i uxlambal ʼij, xoc pa ja re i molbalʼib y xoc chu cʼutic chique i winak chila. Je qʼui i winak je ʼo chila; niʼpa i xqui ta u tzij lic xqui bisoj che.&lt;p&gt; &lt;/p&gt;—¿Pa xu tijoj wi rib i achi-le ronojel wach ca tijin chu ʼonquil? —xqui bij—. ¿Wach usucʼ i nojbal chi yatal che? ¿Pa xu nawsaj wi rib ire-le chu ʼonquil i milágro? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.003', '¿Xataba n-are ti ire-le i aj ʼanol mexa? Ile xa are i ralab i ati Mariy; xa are i catz a-Jacóbo, i aJose, i a-Júdas, i a-Simon. ¿Xataba i ranab ne te je jekel chi kuʼ yoj? Je jekelic pues. Queje ile xqui bij i winak. Xa xqui xutuj wach i xu bij, wach i xu ʼano.
', '', '&lt;span class="verse" id="Mark_6_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;¿Xataba n-are ti ire-le i aj ʼanol mexa? Ile xa are i ralab i ati Mariy; xa are i catz a-Jacóbo, i aJose, i a-Júdas, i a-Simon. ¿Xataba i ranab ne te je jekel chi kuʼ yoj? Je jekelic pues. Queje ile xqui bij i winak. Xa xqui xutuj wach i xu bij, wach i xu ʼano.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.004', 'I Jesus xu bij chique:
—Conojel quiqui coj u ʼij jun ajbil u tzij i Dios, xui i je rach aj tinimit n-quiqui coj tu ʼij. Xak queje i rachalal, tak i je aj pa rachoch, xak n-quiqui coj tu ʼij —xu bij chique.
', '', '&lt;span class="verse" id="Mark_6_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Conojel quiqui coj u ʼij jun ajbil u tzij i Dios, xui i je rach aj tinimit n-quiqui coj tu ʼij. Xak queje i rachalal, tak i je aj pa rachoch, xak n-quiqui coj tu ʼij —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.005', 'I winak chila n-xqui cuba ti qui cʼux che, rumal-i, ire n-xtiqui ta chu ʼanic i milágro. Xui je queb oxib winak xu ya u ʼab piquiwi y xe rutzirsaj. ', '', '&lt;span class="verse" id="Mark_6_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I winak chila n-xqui cuba ti qui cʼux che, rumal-i, ire n-xtiqui ta chu ʼanic i milágro. Xui je queb oxib winak xu ya u ʼab piquiwi y xe rutzirsaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.006', 'Lic xu bisoj man i winak n-xqui cuba ti qui cʼux che. Teʼuri xa ʼe pa tak aldéa nakaj chu cʼutic chique i winak.
', '', '&lt;span class="verse" id="Mark_6_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Lic xu bisoj man i winak n-xqui cuba ti qui cʼux che. Teʼuri xa ʼe pa tak aldéa nakaj chu cʼutic chique i winak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.007', 'Xoʼon panok xe u siqʼuij i cablajuj u tijoxelab ruʼ, y pa cacabil xu jek bi qui takic pa tak aldéa. Xu ya piquiʼab caquesaj itzel tak tew chique i winak. ', '', '&lt;h2&gt;I Jesus xe u tak bi u tijoxelab chu paxsaxic u tzij i Dios&lt;/h2&gt;&lt;span class="verse" id="Mark_6_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xoʼon panok xe u siqʼuij i cablajuj u tijoxelab ruʼ, y pa cacabil xu jek bi qui takic pa tak aldéa. Xu ya piquiʼab caquesaj itzel tak tew chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.008', 'Xak xu bij chique n-ta quiqui cʼam bic are quiqui maj qui be, mi tene qui teb, mi tene qui wa, mi tene qui mer; xui qui chʼamiy quiqui cʼam bic. ', '', '&lt;span class="verse" id="Mark_6_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xak xu bij chique n-ta quiqui cʼam bic are quiqui maj qui be, mi tene qui teb, mi tene qui wa, mi tene qui mer; xui qui chʼamiy quiqui cʼam bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.009', 'Xak xu bij chique chi toʼ xajab chiqui cojo, xak miqui cʼam chubi jumolaj qui ʼuʼ, xui i jumolaj chi qui cojom bic. ', '', '&lt;span class="verse" id="Mark_6_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak xu bij chique chi toʼ xajab chiqui cojo, xak miqui cʼam chubi jumolaj qui ʼuʼ, xui i jumolaj chi qui cojom bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.010', 'Xak xu bij chique:
—We ʼo jun winak cu ya i posar, chix ʼol na ruʼ; cʼa te qui canaj we quix el che i aldéale. ', '', '&lt;span class="verse" id="Mark_6_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—We ʼo jun winak cu ya i posar, chix ʼol na ruʼ; cʼa te qui canaj we quix el che i aldéale. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.011', 'We ʼo i winak n-quix qui cʼulaj taj, xak n-cacaj ti u tayic i tzij, chix el chila. Che i pu can i ulew che iwakan, cʼutbal-re chique chi cʼo can i qui mac chiquij. I tinimit-le, are ca cʼun i tojpen re qʼuisbal ʼij, sak laj tzij i quin bij chiwe, mas nim ni tojbal mac que ique chuwach i queb tinimit Sodóma y Gomórra —xu bij chique u tijoxelab.
', '', '&lt;span class="verse" id="Mark_6_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;We ʼo i winak n-quix qui cʼulaj taj, xak n-cacaj ti u tayic i tzij, chix el chila. Che i pu can i ulew che iwakan, cʼutbal-re chique chi cʼo can i qui mac chiquij. I tinimit-le, are ca cʼun i tojpen re qʼuisbal ʼij, sak laj tzij i quin bij chiwe, mas nim ni tojbal mac que ique chuwach i queb tinimit Sodóma y Gomórra —xu bij chique u tijoxelab.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.012', 'Teʼuri ique xe ʼe chu bixquil chique i winak chi ʼo u chac quiqui jalwachij qui nojbal chi n-ʼus taj. ', '', '&lt;span class="verse" id="Mark_6_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri ique xe ʼe chu bixquil chique i winak chi ʼo u chac quiqui jalwachij qui nojbal chi n-ʼus taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.013', 'Xak xquesaj je qʼui itzel tak tew chique i winak; xak je qʼui iwabib xqui coj acéite piquiwi y xe cutzirsaj.
', '', '&lt;span class="verse" id="Mark_6_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xak xquesaj je qʼui itzel tak tew chique i winak; xak je qʼui iwabib xqui coj acéite piquiwi y xe cutzirsaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.014', 'Xoʼon panok, xak i ma Heródes, chi ʼatol tzij piquiwi i aj Israel winak, xak xu ta u tzijol wach i nim laj ʼanic ca tijin i Jesus chu ʼonquil, man conojel i winak quiqui lapo. Ire xu bij:
—Are i ʼetz ma Wan aj kajsanel ya, walijnak chic chiquixol i camnak. Are i rumal ʼuri chi ʼo i choʼab ruʼ chu ʼonquil i milágro —xu bij.
', '', '&lt;h2&gt;Wach i camic ʼantal che i ma Wan chi aj kajsanel ya&lt;/h2&gt;&lt;span class="verse" id="Mark_6_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xak i ma Heródes, chi ʼatol tzij piquiwi i aj Israel winak, xak xu ta u tzijol wach i nim laj ʼanic ca tijin i Jesus chu ʼonquil, man conojel i winak quiqui lapo. Ire xu bij:&lt;p&gt; &lt;/p&gt;—Are i ʼetz ma Wan aj kajsanel ya, walijnak chic chiquixol i camnak. Are i rumal ʼuri chi ʼo i choʼab ruʼ chu ʼonquil i milágro —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.015', 'Xak je ʼo juban chic xqui bij:
—Are i ujer ajbil u tzij i Dios mam Elías —xe cha. Xak ʼo i que binic:
—Are jun cʼacʼ laj ajbil u tzij i Dios pacha i je re ujer —xe cha.
', '', '&lt;span class="verse" id="Mark_6_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xak je ʼo juban chic xqui bij:&lt;p&gt; &lt;/p&gt;—Are i ujer ajbil u tzij i Dios mam Elías —xe cha. Xak ʼo i que binic:&lt;p&gt; &lt;/p&gt;—Are jun cʼacʼ laj ajbil u tzij i Dios pacha i je re ujer —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.016', 'I ma Heródes, are xu ta, xu bij:
Ire-le are i ma Wan, chi xin tak u cʼatzixic u jolom; ya walijnak chic chiquixol i camnak —xu bij.
', '', '&lt;span class="verse" id="Mark_6_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I ma Heródes, are xu ta, xu bij:&lt;p&gt; &lt;/p&gt;Ire-le are i ma Wan, chi xin tak u cʼatzixic u jolom; ya walijnak chic chiquixol i camnak —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.017', 'I ma Heródes u camsam i ma Wan rumal i rixokil, chi ati Erodiys u bi. Are maja ca camsax i ma Wan, i ma Heródes xu maj i ati Erodiys che u chaʼ, chi ma Felípe. I ma Wan xu bij che:
—N-ʼus taj cat qʼuiji ruʼ i rixokil a chaʼ —xu bij.
Rumal-i, i ma Heródes xu tak u chapic i ma Wan, xu xim ruʼ carena, y xu tzʼapij pa cárcel.
', '', '&lt;span class="verse" id="Mark_6_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I ma Heródes u camsam i ma Wan rumal i rixokil, chi ati Erodiys u bi. Are maja ca camsax i ma Wan, i ma Heródes xu maj i ati Erodiys che u chaʼ, chi ma Felípe. I ma Wan xu bij che:&lt;p&gt; &lt;/p&gt;—N-ʼus taj cat qʼuiji ruʼ i rixokil a chaʼ —xu bij.&lt;p&gt; &lt;/p&gt;Rumal-i, i ma Heródes xu tak u chapic i ma Wan, xu xim ruʼ carena, y xu tzʼapij pa cárcel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.019', 'I ati Erodiys tzel cril i ma Wan. Craj cu camsaj, xui-ri n-cu rik taj wach u ʼanic cu ʼan che, ', '', '&lt;span class="verse" id="Mark_6_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I ati Erodiys tzel cril i ma Wan. Craj cu camsaj, xui-ri n-cu rik taj wach u ʼanic cu ʼan che, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.020', 'man i ma Heródes n-xu ya ta che. Retaʼam chi ma Wan, lic sucʼul i cu ʼano; lic chʼam u cʼaslemal. Rumal-i, xu xij rib che. Are i rumal ʼuri n-xu ya ta che ixok coʼon cʼax che. I ma Heródes, are xu ta i u tzij i ma Wan, lic ʼutz xu ta, xui-ri, ʼo ca tiʼan pu cʼux rumal; n-xu rik taj wach i cu ʼano. ', '', '&lt;span class="verse" id="Mark_6_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;man i ma Heródes n-xu ya ta che. Retaʼam chi ma Wan, lic sucʼul i cu ʼano; lic chʼam u cʼaslemal. Rumal-i, xu xij rib che. Are i rumal ʼuri n-xu ya ta che ixok coʼon cʼax che. I ma Heródes, are xu ta i u tzij i ma Wan, lic ʼutz xu ta, xui-ri, ʼo ca tiʼan pu cʼux rumal; n-xu rik taj wach i cu ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.021', 'I ati Erodiys lic xraj u camsaxic i ma Wan, y xoʼon panok xu rik i ʼij che. I ma Heródes xoʼon jun nimaʼij re u ʼij ire. Je qʼui i nimak tak winak xe cʼun chu tijawic cenar ruʼ. Xe cʼun i je rakan u ʼab, xak xe cʼun i je cajʼatzil i u soldádo, xak xe cʼun i nimak qui patan re ronojel i jyub Galiléa. ', '', '&lt;span class="verse" id="Mark_6_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I ati Erodiys lic xraj u camsaxic i ma Wan, y xoʼon panok xu rik i ʼij che. I ma Heródes xoʼon jun nimaʼij re u ʼij ire. Je qʼui i nimak tak winak xe cʼun chu tijawic cenar ruʼ. Xe cʼun i je rakan u ʼab, xak xe cʼun i je cajʼatzil i u soldádo, xak xe cʼun i nimak qui patan re ronojel i jyub Galiléa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.022', 'Are que tijin chi waʼim, xoc bi i ralit i ati Erodiys, y xoc chi xajawic chiquiwach. I ma Heródes, cuʼ i je ʼo ruʼ, kus xquilo wach i xajawic xoʼon i ali. Rumal-i xu bij che:
—Cha tzʼonoj chwe wach i cawaj quin ya chawe, y yin quin ya na ʼuri chawe —xu bij che ali. ', '', '&lt;span class="verse" id="Mark_6_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are que tijin chi waʼim, xoc bi i ralit i ati Erodiys, y xoc chi xajawic chiquiwach. I ma Heródes, cuʼ i je ʼo ruʼ, kus xquilo wach i xajawic xoʼon i ali. Rumal-i xu bij che:&lt;p&gt; &lt;/p&gt;—Cha tzʼonoj chwe wach i cawaj quin ya chawe, y yin quin ya na ʼuri chawe —xu bij che ali. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.023', 'Lic xu cuxtaj u bi i Dios chu bixquil.
—Yin lic quin ya na chawe pachique tak ca tzʼonoj chwe. N-ta coʼono we ca tzʼonoj i nicʼaj i uqʼuial tinimit chi ʼo pin ʼab —xu bij—, lic quin ya na chawe. Queje ile xu bij i ma Heródes.
', '', '&lt;span class="verse" id="Mark_6_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Lic xu cuxtaj u bi i Dios chu bixquil.&lt;p&gt; &lt;/p&gt;—Yin lic quin ya na chawe pachique tak ca tzʼonoj chwe. N-ta coʼono we ca tzʼonoj i nicʼaj i uqʼuial tinimit chi ʼo pin ʼab —xu bij—, lic quin ya na chawe. Queje ile xu bij i ma Heródes.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.024', 'I ali xe u tzʼonoj che u chuch:
—¿Wach quin tzʼonoj che, ca bij yet? —xu bij che.
—Cha tzʼonoj u jolom i ma Wan che, chi aj kajsanel ya —xu bij u chuch che.
', '', '&lt;span class="verse" id="Mark_6_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I ali xe u tzʼonoj che u chuch:&lt;p&gt; &lt;/p&gt;—¿Wach quin tzʼonoj che, ca bij yet? —xu bij che.&lt;p&gt; &lt;/p&gt;—Cha tzʼonoj u jolom i ma Wan che, chi aj kajsanel ya —xu bij u chuch che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.025', 'I ali juntir xtzalijic; xa ʼe ruʼ i ma Heródes y xu bij che:
—Woʼor tan cwaj i u jolom i ma Wan, aj kajsanel ya; cwaj ca ya li chwe chupam jun plato —xu bij.
', '', '&lt;span class="verse" id="Mark_6_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I ali juntir xtzalijic; xa ʼe ruʼ i ma Heródes y xu bij che:&lt;p&gt; &lt;/p&gt;—Woʼor tan cwaj i u jolom i ma Wan, aj kajsanel ya; cwaj ca ya li chwe chupam jun plato —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.026', 'I ʼatol tzij, are xu ta ile, lic xbison rumal. Xui-ri, ire u bim chic chiquiwach conojel, chi cu ya na che wach i ca tzʼonox che, xak u cuxtam u bi i Dios puwi u tzij. Rumal-i, n-xraj ta ire cu tzalij u tzij. ', '', '&lt;span class="verse" id="Mark_6_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I ʼatol tzij, are xu ta ile, lic xbison rumal. Xui-ri, ire u bim chic chiquiwach conojel, chi cu ya na che wach i ca tzʼonox che, xak u cuxtam u bi i Dios puwi u tzij. Rumal-i, n-xraj ta ire cu tzalij u tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.027', 'Are ʼuri xu tak-la bi jun soldádo chu cʼamic u jolom i ma Wan. ', '', '&lt;span class="verse" id="Mark_6_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Are ʼuri xu tak-la bi jun soldádo chu cʼamic u jolom i ma Wan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.028', 'I soldádo xa ʼe ruʼ i cárcel, y xresaj u jolom i ma Wan; xu ya ʼuri pa plato y xu cʼam bi che ali. Ire-le xe u jacha che u chuch.
', '', '&lt;span class="verse" id="Mark_6_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;I soldádo xa ʼe ruʼ i cárcel, y xresaj u jolom i ma Wan; xu ya ʼuri pa plato y xu cʼam bi che ali. Ire-le xe u jacha che u chuch.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.029', 'I u tijoxelab i ma Wan, are xqui ta wach xa ʼan che, xe qui cʼama u nimal y xe qui muku.
', '', '&lt;span class="verse" id="Mark_6_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I u tijoxelab i ma Wan, are xqui ta wach xa ʼan che, xe qui cʼama u nimal y xe qui muku.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.030', 'Xoʼon panok, i u tijoxelab i Jesus xe tzalij chic y xqui mol quib ruʼ i Jesus. Xe oc chu lapic che niʼpa i qui ʼanom y niʼpa i qui cʼutum chique i winak. ', '', '&lt;h2&gt;I qui tzukic joʼob mil winak&lt;/h2&gt;&lt;span class="verse" id="Mark_6_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i u tijoxelab i Jesus xe tzalij chic y xqui mol quib ruʼ i Jesus. Xe oc chu lapic che niʼpa i qui ʼanom y niʼpa i qui cʼutum chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.031', 'Teʼuri i Jesus xu bij chique:
—Joʼ. Jo che jun lugar pa n-ta wi winak; coj uxlan jubiʼ —coʼono.
Queje xraj ire ile, man i winak n-ta qui qʼuisic ruʼ. N-ca tijtaj ta chic u wa cumal. ', '', '&lt;span class="verse" id="Mark_6_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Joʼ. Jo che jun lugar pa n-ta wi winak; coj uxlan jubiʼ —coʼono.&lt;p&gt; &lt;/p&gt;Queje xraj ire ile, man i winak n-ta qui qʼuisic ruʼ. N-ca tijtaj ta chic u wa cumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.032', 'Rumal-i, i Jesus cuʼ u tijoxelab, quituquel xe ʼe chupam jun bárco che jun lugar pa n-ta wi winak. ', '', '&lt;span class="verse" id="Mark_6_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Rumal-i, i Jesus cuʼ u tijoxelab, quituquel xe ʼe chupam jun bárco che jun lugar pa n-ta wi winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.033', 'Xui-ri, are xe ʼec, je qʼui i winak xe ʼilawic y xquetamaj chi are i Jesus. Are ʼuri xe ʼe paʼanem chi tak u chiʼ i alagun; nabe xe upon ique chuwach i Jesus. ʼIs pa ronojel tak tinimit je petnak wi. ', '', '&lt;span class="verse" id="Mark_6_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xui-ri, are xe ʼec, je qʼui i winak xe ʼilawic y xquetamaj chi are i Jesus. Are ʼuri xe ʼe paʼanem chi tak u chiʼ i alagun; nabe xe upon ique chuwach i Jesus. ʼIs pa ronojel tak tinimit je petnak wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.034', 'I Jesus, are xel pa bárco, xe ril je uqʼuial winak je ʼo tan chic chila. Cʼax xu na u cʼux chique, man je pacha juban chij n-ta que chajiwic. Rumal-i, xoc chu bixquil uqʼuial cʼutunic chique. ', '', '&lt;span class="verse" id="Mark_6_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus, are xel pa bárco, xe ril je uqʼuial winak je ʼo tan chic chila. Cʼax xu na u cʼux chique, man je pacha juban chij n-ta que chajiwic. Rumal-i, xoc chu bixquil uqʼuial cʼutunic chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.035', 'Are mer xkaj i ʼij, i u tijoxelab xe tejeb ruʼ y xqui bij che:
—Ya xkaj i ʼij, y waral xa tzʼinilic jyub, n-ta winak chi. ', '', '&lt;span class="verse" id="Mark_6_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Are mer xkaj i ʼij, i u tijoxelab xe tejeb ruʼ y xqui bij che:&lt;p&gt; &lt;/p&gt;—Ya xkaj i ʼij, y waral xa tzʼinilic jyub, n-ta winak chi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.036', 'Cha bij chique i winak ʼutz que ʼe chu tzucuxic qui wa chi tak ja, xak pa tak aldéa nakaj —xqui bij che i Jesus.
', '', '&lt;span class="verse" id="Mark_6_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Cha bij chique i winak ʼutz que ʼe chu tzucuxic qui wa chi tak ja, xak pa tak aldéa nakaj —xqui bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.037', 'Xui-ri, ire xu bij chique:
—Yix chi ya chique wach quiqui tij na —coʼono.
—¿Cawaj bari que ʼe ka loʼo queb ciénto quetzal caxlanwa, tzukbal que i winak-i? —xqui bij che.
', '', '&lt;span class="verse" id="Mark_6_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Xui-ri, ire xu bij chique:&lt;p&gt; &lt;/p&gt;—Yix chi ya chique wach quiqui tij na —coʼono.&lt;p&gt; &lt;/p&gt;—¿Cawaj bari que ʼe ka loʼo queb ciénto quetzal caxlanwa, tzukbal que i winak-i? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.038', 'Ire xu tzʼonoj chique:
—¿Niʼpa i caxlanwa ʼo iwuʼ? Je iwilapeʼ.
Ique, are xquetamaj niʼpa ʼo cuʼ, xqui bij che:
—ʼO joʼob caxlanwa ruʼ queb car —xe cha.
', '', '&lt;span class="verse" id="Mark_6_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Ire xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Niʼpa i caxlanwa ʼo iwuʼ? Je iwilapeʼ.&lt;p&gt; &lt;/p&gt;Ique, are xquetamaj niʼpa ʼo cuʼ, xqui bij che:&lt;p&gt; &lt;/p&gt;—ʼO joʼob caxlanwa ruʼ queb car —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.039', 'Teʼuri i Jesus xe u tak i winak chi que cubi chuwa tak qʼuim pa tak tzobajil. ', '', '&lt;span class="verse" id="Mark_6_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u tak i winak chi que cubi chuwa tak qʼuim pa tak tzobajil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.040', 'Che tak i tzobajil-le, ʼo jujun chi ʼo i cincuénta chique, xak je ʼo jujun je ʼo jun ciénto chique. ', '', '&lt;span class="verse" id="Mark_6_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Che tak i tzobajil-le, ʼo jujun chi ʼo i cincuénta chique, xak je ʼo jujun je ʼo jun ciénto chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.041', 'Teʼuri i Jesus xu chap i joʼob caxlanwa, xak i queb car; are ʼuri xtzun chicaj xu cʼamwaj xu tioxij che i Dios. Teʼuri xu pir i caxlanwa, y xu ya chique u tijoxelab, quiqui jachbej-re chique i winak. Xak i queb car xu ya chique, man xak quiqui ya chique conojel i winak. ', '', '&lt;span class="verse" id="Mark_6_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu chap i joʼob caxlanwa, xak i queb car; are ʼuri xtzun chicaj xu cʼamwaj xu tioxij che i Dios. Teʼuri xu pir i caxlanwa, y xu ya chique u tijoxelab, quiqui jachbej-re chique i winak. Xak i queb car xu ya chique, man xak quiqui ya chique conojel i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.042', 'Conojel ʼis xe waʼic y xe nojic. ', '', '&lt;span class="verse" id="Mark_6_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Conojel ʼis xe waʼic y xe nojic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.043', 'Are xquelej, i u tijoxelab xqui mol u chiʼ niʼpa i chʼakatak caxlanwa, xak i car chi n-xqʼuis taj. Xnoj cablajuj coban che i chʼakatak chi xsobrinic. ', '', '&lt;span class="verse" id="Mark_6_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Are xquelej, i u tijoxelab xqui mol u chiʼ niʼpa i chʼakatak caxlanwa, xak i car chi n-xqʼuis taj. Xnoj cablajuj coban che i chʼakatak chi xsobrinic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.044', 'Niʼpa i achiab xe tzukic, je joʼob mil.
', '', '&lt;span class="verse" id="Mark_6_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Niʼpa i achiab xe tzukic, je joʼob mil.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.045', 'Are xutzin ile, i Jesus xu bij chique u tijoxelab chi que oc chic pa bárco, que ʼax chʼaka alagun cʼa pa tinimit Betsáida. Queje xu bij ile chi quiqui ʼan ique tzʼakat ire que u ʼijla bi i winak. ', '', '&lt;h2&gt;Wach i binic xoʼon i Jesus puwi ya&lt;/h2&gt;&lt;span class="verse" id="Mark_6_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Are xutzin ile, i Jesus xu bij chique u tijoxelab chi que oc chic pa bárco, que ʼax chʼaka alagun cʼa pa tinimit Betsáida. Queje xu bij ile chi quiqui ʼan ique tzʼakat ire que u ʼijla bi i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.046', 'Are xutzin qui ʼijlaxic i winak, xa ʼe che jun jyub chu tzʼonoxic che i Dios. ', '', '&lt;span class="verse" id="Mark_6_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Are xutzin qui ʼijlaxic i winak, xa ʼe che jun jyub chu tzʼonoxic che i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.047', 'Are xoc aʼab, i u tijoxelab ya que upon pa nicʼaj alagun; i Jesus utuquel ʼo che i jyub-le. ', '', '&lt;span class="verse" id="Mark_6_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Are xoc aʼab, i u tijoxelab ya que upon pa nicʼaj alagun; i Jesus utuquel ʼo che i jyub-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.048', 'Are ʼo chila, xe ril lok chi u tijoxelab uyej quiqui ʼan chu minic bi qui bárco chuwi ya, rumal i tew chi lic xpe chirij. Are ya ca pe u sakiric, i Jesus ya ca nakajab cuʼ, lic ca bin chuwi ya; ca tijin ricʼawic chiquiwach ique. ', '', '&lt;span class="verse" id="Mark_6_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Are ʼo chila, xe ril lok chi u tijoxelab uyej quiqui ʼan chu minic bi qui bárco chuwi ya, rumal i tew chi lic xpe chirij. Are ya ca pe u sakiric, i Jesus ya ca nakajab cuʼ, lic ca bin chuwi ya; ca tijin ricʼawic chiquiwach ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.049', 'I u tijoxelab, are xquilo chi ca bin chuwi ya, xqui chʼob ique, mok jun xibinel, y xe siqʼuin rumal. ', '', '&lt;span class="verse" id="Mark_6_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xquilo chi ca bin chuwi ya, xqui chʼob ique, mok jun xibinel, y xe siqʼuin rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.050', 'Queje xqui ʼan ile, man conojel xquilo, xak ʼis xe oc ʼil rumal. Xui-ri, ire xe u ʼijla; xu bij chique:
—¡Mi xij iwib! Xa yin. ¡Chi cowirsaj i cʼux! —coʼono.
', '', '&lt;span class="verse" id="Mark_6_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Queje xqui ʼan ile, man conojel xquilo, xak ʼis xe oc ʼil rumal. Xui-ri, ire xe u ʼijla; xu bij chique:&lt;p&gt; &lt;/p&gt;—¡Mi xij iwib! Xa yin. ¡Chi cowirsaj i cʼux! —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.051', 'Teʼuri xoc cuʼ pa bárco; are ʼuri i tew xuxlanic. I u tijoxelab lic xqui bisoj rumal. ', '', '&lt;span class="verse" id="Mark_6_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Teʼuri xoc cuʼ pa bárco; are ʼuri i tew xuxlanic. I u tijoxelab lic xqui bisoj rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.052', 'Ique n-qui tom tu be wach usucʼ i milágro xoʼon ire che i caxlanwa (chi are ire ʼo puwi ronojel niʼpa i ʼolic). Ique toʼ tzʼapil i qui nojbal.
', '', '&lt;span class="verse" id="Mark_6_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;Ique n-qui tom tu be wach usucʼ i milágro xoʼon ire che i caxlanwa (chi are ire ʼo puwi ronojel niʼpa i ʼolic). Ique toʼ tzʼapil i qui nojbal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.053', 'Are xe ʼax chʼakap, xe upon chu chiʼ i jyub Genesaret. Chila xqui yukba wi can i qui bárco. ', '', '&lt;h2&gt;I Jesus xe rutzirsaj i wabib pa Genesaret&lt;/h2&gt;&lt;span class="verse" id="Mark_6_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;Are xe ʼax chʼakap, xe upon chu chiʼ i jyub Genesaret. Chila xqui yukba wi can i qui bárco. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.054', 'Juntir, are xe ʼel pa bárco, i winak je ʼo chila xquilo chi are i Jesus. ', '', '&lt;span class="verse" id="Mark_6_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;Juntir, are xe ʼel pa bárco, i winak je ʼo chila xquilo chi are i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.055', 'Rumal-i, ʼis xe ʼe paʼanem chu cʼamic li qui iwabib chuwa chʼat pa ʼo wi i Jesus. ', '', '&lt;span class="verse" id="Mark_6_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;Rumal-i, ʼis xe ʼe paʼanem chu cʼamic li qui iwabib chuwa chʼat pa ʼo wi i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '6.056', 'Pa tak ca ʼe wi ire, pa tak tinimit, pa tak aldéa, pa tak jyub, ʼis que qui cʼam li iwabib ruʼ. Xe qui ya chi tak be, y lic xqui tzʼonoj che chi ca yaʼ chique quiqui chap jubiʼ u ʼuʼ. Conojel niʼpa i xqui chap u ʼuʼ, xe utziric.
', '', '&lt;span class="verse" id="Mark_6_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Pa tak ca ʼe wi ire, pa tak tinimit, pa tak aldéa, pa tak jyub, ʼis que qui cʼam li iwabib ruʼ. Xe qui ya chi tak be, y lic xqui tzʼonoj che chi ca yaʼ chique quiqui chap jubiʼ u ʼuʼ. Conojel niʼpa i xqui chap u ʼuʼ, xe utziric.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.001', 'ʼO jujun aj Fariséo winak xe tejeb pan ruʼ i Jesus, cucʼam quib cuʼ jujun aj tijonel re i ujer ʼatbal tzij. Ique je petnak pa Jerusalen. ', '', '&lt;h2&gt;Wach i ca ʼanaw n-chʼam taj chique i winak&lt;/h2&gt;&lt;span class="verse" id="Mark_7_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;ʼO jujun aj Fariséo winak xe tejeb pan ruʼ i Jesus, cucʼam quib cuʼ jujun aj tijonel re i ujer ʼatbal tzij. Ique je petnak pa Jerusalen. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.002', 'Are xe upon ruʼ, xquilo chi ʼo jujun chique u tijoxelab i Jesus chi que wa ruʼ qui ʼab tzʼil, quiqui bij ique, man n-quiqui coj ta retalil chu chʼajic qui ʼab pacha quiqui ʼan i aj Fariséo winak. ', '', '&lt;span class="verse" id="Mark_7_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are xe upon ruʼ, xquilo chi ʼo jujun chique u tijoxelab i Jesus chi que wa ruʼ qui ʼab tzʼil, quiqui bij ique, man n-quiqui coj ta retalil chu chʼajic qui ʼab pacha quiqui ʼan i aj Fariséo winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.003', '(I aj Fariséo, xak conojel i juban aj Israel winak quiqui ʼan pacha i qui costúmbre i catit qui mam; n-ca tijaw ti wa we n-quiqui chʼaj ti qui ʼab qʼuialmul. ', '', '&lt;span class="verse" id="Mark_7_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;(I aj Fariséo, xak conojel i juban aj Israel winak quiqui ʼan pacha i qui costúmbre i catit qui mam; n-ca tijaw ti wa we n-quiqui chʼaj ti qui ʼab qʼuialmul. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.004', 'Are que cʼun pa qʼuebal, n-quiqui tij ta tan i wa we n-qui chʼajom ti qui ʼab pacha cu bij i qui costúmbre ique. Xak ʼo chic juban uqʼuial costúmbre je teren chirij: ʼo rawasil che i qui bas, xak che i qui xar, xak queje che i qui pichel chʼichʼ, xak queje che i qui chʼat; ʼis ʼo rawasil che chi quiqui chʼajo.) ', '', '&lt;span class="verse" id="Mark_7_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Are que cʼun pa qʼuebal, n-quiqui tij ta tan i wa we n-qui chʼajom ti qui ʼab pacha cu bij i qui costúmbre ique. Xak ʼo chic juban uqʼuial costúmbre je teren chirij: ʼo rawasil che i qui bas, xak che i qui xar, xak queje che i qui pichel chʼichʼ, xak queje che i qui chʼat; ʼis ʼo rawasil che chi quiqui chʼajo.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.005', 'Rumal-i, i aj Fariséo-le, xak i aj tijonel re i ujer ʼatbal tzij xqui bij che i Jesus:
—I a tijoxelab, ¿wuchac n-quiqui ʼan ta ique pacha qui bim can i katit kamam ujer? ¿Wuchac quiqui tij qui wa, we n-chʼam ti qui ʼab chuwach i Dios? —xqui bij che.
', '', '&lt;span class="verse" id="Mark_7_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Rumal-i, i aj Fariséo-le, xak i aj tijonel re i ujer ʼatbal tzij xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—I a tijoxelab, ¿wuchac n-quiqui ʼan ta ique pacha qui bim can i katit kamam ujer? ¿Wuchac quiqui tij qui wa, we n-chʼam ti qui ʼab chuwach i Dios? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.006', 'I Jesus xu laʼ u wach chique:
—Lic katzij xu bij i mam Isaías ujer. Yix xa queb i palaj. Lic quix ʼanawic pacha chom i qui ʼano, xui-ri ire u bim canok wach quix tijin yix chu ʼanic woʼor. U bim canok chi Dios u bim na:
I winak-i lic quiqui coj in ʼij ruʼ qui chiʼ,
xui-ri, ruʼ qui cʼux n-quiqui ʼan taj.
', '', '&lt;span class="verse" id="Mark_7_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Lic katzij xu bij i mam Isaías ujer. Yix xa queb i palaj. Lic quix ʼanawic pacha chom i qui ʼano, xui-ri ire u bim canok wach quix tijin yix chu ʼanic woʼor. U bim canok chi Dios u bim na:
I winak-i lic quiqui coj in ʼij ruʼ qui chiʼ,
xui-ri, ruʼ qui cʼux n-quiqui ʼan taj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.007', 'I u cojic in ʼij quiqui ʼano, lic n-ta cu chacuj,
man i tzij que tijin chu cʼutic,
xa qui tzij i winak, xui-ri, quiqui ʼan che pacha tak u tzij i Dios.
(Queje ile u bim i Dios, xu bij i mam Isaías ujer.)
', '', '&lt;span class="verse" id="Mark_7_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I u cojic in ʼij quiqui ʼano, lic n-ta cu chacuj,
man i tzij que tijin chu cʼutic,
xa qui tzij i winak, xui-ri, quiqui ʼan che pacha tak u tzij i Dios.
(Queje ile u bim i Dios, xu bij i mam Isaías ujer.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.008', 'Yix n-qui coj ta chic retalil che wach u bim can i Dios chiwe —xu bij i Jesus chique—. Xui quix tijin chu cojic i cʼutunic chi xa ruʼ winak petnak wi. Xui qui coj retalil che wach tak u chʼajic i xar, i bas, xak juban chic ʼanic pacha ile —xu bij chique.
', '', '&lt;span class="verse" id="Mark_7_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Yix n-qui coj ta chic retalil che wach u bim can i Dios chiwe —xu bij i Jesus chique—. Xui quix tijin chu cojic i cʼutunic chi xa ruʼ winak petnak wi. Xui qui coj retalil che wach tak u chʼajic i xar, i bas, xak juban chic ʼanic pacha ile —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.009', 'Xak xu bij:
—I ʼatbal tzij chi u bim can i Dios chiwe, yix quix tijin chu ʼanic pacha n-ta chic u chac chiwe; are i cojom pu qʼuexel xa ruʼ winak petnak wi. ', '', '&lt;span class="verse" id="Mark_7_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak xu bij:&lt;p&gt; &lt;/p&gt;—I ʼatbal tzij chi u bim can i Dios chiwe, yix quix tijin chu ʼanic pacha n-ta chic u chac chiwe; are i cojom pu qʼuexel xa ruʼ winak petnak wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.010', 'I mam Moises u bim can u tzij u pixab i Dios chiwe: “Cha cojo qui ʼij a kajaw a chuch” xu bij; xak xu bij: “Pachin cʼax ca chʼaw chiquij u kajaw u chuch, cha camsaxok”; queje ile u bim can i mam Moises chi ajbil u tzij i Dios. ', '', '&lt;span class="verse" id="Mark_7_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I mam Moises u bim can u tzij u pixab i Dios chiwe: “Cha cojo qui ʼij a kajaw a chuch” xu bij; xak xu bij: “Pachin cʼax ca chʼaw chiquij u kajaw u chuch, cha camsaxok”; queje ile u bim can i mam Moises chi ajbil u tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.011', 'Xui-ri, yix quix tijin chu cʼutic, chi jun achi, ʼo puʼab cu bij che u kajaw xak che u chuch: “N-cat in to taj, man niʼpa i ʼo wuʼ in sujum chic che i Dios” mok cu bij che. ', '', '&lt;span class="verse" id="Mark_7_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xui-ri, yix quix tijin chu cʼutic, chi jun achi, ʼo puʼab cu bij che u kajaw xak che u chuch: “N-cat in to taj, man niʼpa i ʼo wuʼ in sujum chic che i Dios” mok cu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.012', 'Y we queje cu bij ire ile, n-ta chic chukul ʼuri que u to u kajaw u chuch, qui bij yix. ', '', '&lt;span class="verse" id="Mark_7_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Y we queje cu bij ire ile, n-ta chic chukul ʼuri que u to u kajaw u chuch, qui bij yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.013', 'Queje ile, yix i kajsam u ʼij i ʼatbal tzij chi u bim can i Dios. Y pu qʼuexel ile, i cojom wi i cʼutunic chi xa winak bin-nak che jun chic. N-xui ta la ile, xak qʼui na qui ʼan yix pacha ile —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_7_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Queje ile, yix i kajsam u ʼij i ʼatbal tzij chi u bim can i Dios. Y pu qʼuexel ile, i cojom wi i cʼutunic chi xa winak bin-nak che jun chic. N-xui ta la ile, xak qʼui na qui ʼan yix pacha ile —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.014', 'Teʼuri i Jesus xe u siqʼuij chic i winak ruʼ y xu bij chique:
—Chi ta na iwonojel wach i quin bij; chi bisoj u tayic u be. ', '', '&lt;span class="verse" id="Mark_7_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u siqʼuij chic i winak ruʼ y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ta na iwonojel wach i quin bij; chi bisoj u tayic u be. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.015', 'Wach ca ʼe chupam jun winak, n-are ta ca ʼanaw che chi n-chʼam ta ire. Xa are wach ca pe pu cʼux ire, are ca ʼanaw che chi n-chʼam taj. ', '', '&lt;span class="verse" id="Mark_7_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Wach ca ʼe chupam jun winak, n-are ta ca ʼanaw che chi n-chʼam ta ire. Xa are wach ca pe pu cʼux ire, are ca ʼanaw che chi n-chʼam taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.016', 'We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_7_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;We yix ʼo i xiquin chu tayic, chi ta u be ʼuri —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.017', 'Are xel i Jesus chiquixol i winak y xoc pa ja, i u tijoxelab xqui tzʼonoj che wach usucʼ i tzij-le chi xu bij chique i winak. ', '', '&lt;span class="verse" id="Mark_7_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are xel i Jesus chiquixol i winak y xoc pa ja, i u tijoxelab xqui tzʼonoj che wach usucʼ i tzij-le chi xu bij chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.018', 'Ire xu bij chique:
—¿Xak yix n-qui ta tu be? ¿N-qui ta tu be chi wach coc chupam jun winak n-are ta ca ʼanaw che chi n-chʼam taj? ', '', '&lt;span class="verse" id="Mark_7_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Ire xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Xak yix n-qui ta tu be? ¿N-qui ta tu be chi wach coc chupam jun winak n-are ta ca ʼanaw che chi n-chʼam taj? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.019', 'Man ile n-coc ta pa ranima. Xa che u pam ca ʼe wi, xa quicʼawic y quel tan chic —xu bij i Jesus chique.
Queje ile xu sakij chique chi ronojel niʼpa i u yijbam i Dios chi tzukbal-ke ʼis ʼutz, n-ta rawasil che. ', '', '&lt;span class="verse" id="Mark_7_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Man ile n-coc ta pa ranima. Xa che u pam ca ʼe wi, xa quicʼawic y quel tan chic —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;Queje ile xu sakij chique chi ronojel niʼpa i u yijbam i Dios chi tzukbal-ke ʼis ʼutz, n-ta rawasil che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.020', 'Xak xu bij chique:
—Xa are wach ca pe pu cʼux jun winak, are ca ʼanaw-i che chi n-chʼam taj. ', '', '&lt;span class="verse" id="Mark_7_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—Xa are wach ca pe pu cʼux jun winak, are ca ʼanaw-i che chi n-chʼam taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.021', 'Man ile, pa ranima ca pe wi. Piqui cʼux i winak ca pe wi i qui nojbal chi n-ʼus taj; are i rumal ʼuri ʼo i je cʼulanic quiqui kajsaj qui ʼij cuʼ i ne te cachiʼil. Are i rumal ʼuri xak ʼo i ne te cʼulanic quiqui kajsaj qui ʼij chiquiwach; are i rumal ʼuri, ʼo i winak je camsanel, ', '', '&lt;span class="verse" id="Mark_7_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Man ile, pa ranima ca pe wi. Piqui cʼux i winak ca pe wi i qui nojbal chi n-ʼus taj; are i rumal ʼuri ʼo i je cʼulanic quiqui kajsaj qui ʼij cuʼ i ne te cachiʼil. Are i rumal ʼuri xak ʼo i ne te cʼulanic quiqui kajsaj qui ʼij chiquiwach; are i rumal ʼuri, ʼo i winak je camsanel, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.022', 'xak ʼo i je iliʼomab, xak ʼo i je rayinel; xak ʼo i je aj ʼanol mal, xak ʼo i je aj tʼoronel; xak ʼo i je aj ʼanol n-ʼus taj; xak ʼo i ca titat qui pam chique i cachiʼil wach i ʼo cuʼ; xak ʼo i je aj nucʼul tzij; xak ʼo i quiqui ʼan nimak che quib; xak ʼo i n-quiqui chʼob ta bien wach quiqui ʼano. ', '', '&lt;span class="verse" id="Mark_7_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;xak ʼo i je iliʼomab, xak ʼo i je rayinel; xak ʼo i je aj ʼanol mal, xak ʼo i je aj tʼoronel; xak ʼo i je aj ʼanol n-ʼus taj; xak ʼo i ca titat qui pam chique i cachiʼil wach i ʼo cuʼ; xak ʼo i je aj nucʼul tzij; xak ʼo i quiqui ʼan nimak che quib; xak ʼo i n-quiqui chʼob ta bien wach quiqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.023', 'Je niʼpa i que ʼanaw queje ile, are i ne te chʼam-i chuwach i Dios, y ronojel i n-ʼus taj-le, xa piqui cʼux petnak wi; n-are ti wach quiqui tijo ca ʼanaw chique.
(Queje ile, i Jesus xu sakij i tzij chique u tijoxelab.)
', '', '&lt;span class="verse" id="Mark_7_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Je niʼpa i que ʼanaw queje ile, are i ne te chʼam-i chuwach i Dios, y ronojel i n-ʼus taj-le, xa piqui cʼux petnak wi; n-are ti wach quiqui tijo ca ʼanaw chique.&lt;p&gt; &lt;/p&gt;(Queje ile, i Jesus xu sakij i tzij chique u tijoxelab.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.024', 'Xoʼon panok, i Jesus xel che i jyub-le y xa ʼe che i jyub re i queb tinimit, Tíro y Sidon; chila xoc che jun ja. N-xraj ta ire we ca tatajic pa i ʼo wi, xui-ri, n-xtiqui ta chi ruwaxic rib chiquiwach i winak.
', '', '&lt;h2&gt;I Jesus xrutzirsaj i ralit jun ixok chi n-aj Israel taj&lt;/h2&gt;&lt;span class="verse" id="Mark_7_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus xel che i jyub-le y xa ʼe che i jyub re i queb tinimit, Tíro y Sidon; chila xoc che jun ja. N-xraj ta ire we ca tatajic pa i ʼo wi, xui-ri, n-xtiqui ta chi ruwaxic rib chiquiwach i winak.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.025', 'ʼO jun ixok, weʼ xu ta rason pa ʼo wi i Jesus. Ire ʼo jun ralit chi ʼo itzel tew che. I ixok-i xupon ruʼ i Jesus y xuqui che rakan. ', '', '&lt;span class="verse" id="Mark_7_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;ʼO jun ixok, weʼ xu ta rason pa ʼo wi i Jesus. Ire ʼo jun ralit chi ʼo itzel tew che. I ixok-i xupon ruʼ i Jesus y xuqui che rakan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.026', 'Ire n-aj Israel taj, xa are aj Sirofenis winak. Xtejeb pan ruʼ i Jesus y xu tzʼonoj pawor che chi quelsax ni itzel tew che i ralit. ', '', '&lt;span class="verse" id="Mark_7_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Ire n-aj Israel taj, xa are aj Sirofenis winak. Xtejeb pan ruʼ i Jesus y xu tzʼonoj pawor che chi quelsax ni itzel tew che i ralit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.027', 'I Jesus xu bij jun moxbal tzij che:
—Nabe ʼo u chac que tzuk ni acʼalab, man n-ʼus taj ca maj i wa chique i acʼalab y ca yaʼ chique i tzʼi. (Queje ile xu bij i Jesus che, chi queʼelok: “Nabe que in to ni wach aj Israel.”)
I ixok xu bij ʼuri:
', '', '&lt;span class="verse" id="Mark_7_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I Jesus xu bij jun moxbal tzij che:&lt;p&gt; &lt;/p&gt;—Nabe ʼo u chac que tzuk ni acʼalab, man n-ʼus taj ca maj i wa chique i acʼalab y ca yaʼ chique i tzʼi. (Queje ile xu bij i Jesus che, chi queʼelok: “Nabe que in to ni wach aj Israel.”)&lt;p&gt; &lt;/p&gt;I ixok xu bij ʼuri:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.028', '—Katzij i ca bij, Nim laj Winak; xui-ri, xak i ral tak tzʼi chi je ʼo chuxeʼ i mexa quiqui mol ni ucʼaj qui wa i acʼalab chi tzaknak bic —xu bij.
', '', '&lt;span class="verse" id="Mark_7_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;—Katzij i ca bij, Nim laj Winak; xui-ri, xak i ral tak tzʼi chi je ʼo chuxeʼ i mexa quiqui mol ni ucʼaj qui wa i acʼalab chi tzaknak bic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.029', 'Teʼuri i Jesus xu bij:
—ʼUtz i xa bij —coʼon che ixok—. Y woʼor, ʼutz cat ʼe che awachoch; i itzel tew ya ʼelnak chic che awalit —xu bij che.
', '', '&lt;span class="verse" id="Mark_7_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—ʼUtz i xa bij —coʼon che ixok—. Y woʼor, ʼutz cat ʼe che awachoch; i itzel tew ya ʼelnak chic che awalit —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.030', 'I ixok juntir xa ʼec. Are xupon ruʼ ja, xe u rika i acʼal cotzʼol chuwi chʼat. I mal laj tew ya ʼelnak chic che.
', '', '&lt;span class="verse" id="Mark_7_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;I ixok juntir xa ʼec. Are xupon ruʼ ja, xe u rika i acʼal cotzʼol chuwi chʼat. I mal laj tew ya ʼelnak chic che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.031', 'Teʼuri i Jesus xel che i jyub re i tinimit Tíro, xicʼaw pa tinimit Sidon; teʼuri xicʼaw pa jyub Decápolis, y xupon cʼa chu chiʼ i alagun Galiléa. ', '', '&lt;h2&gt;I Jesus xrutzirsaj jun tac chi n-kas ta quel u chʼawbal&lt;/h2&gt;&lt;span class="verse" id="Mark_7_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xel che i jyub re i tinimit Tíro, xicʼaw pa tinimit Sidon; teʼuri xicʼaw pa jyub Decápolis, y xupon cʼa chu chiʼ i alagun Galiléa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.032', 'Chila xqui cʼam li jun achi ruʼ, lic tac u xiquin, xak n-kas ta quel u chʼawbal. Xqui tzʼonoj che chi cu ya ni u ʼab puwi. ', '', '&lt;span class="verse" id="Mark_7_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Chila xqui cʼam li jun achi ruʼ, lic tac u xiquin, xak n-kas ta quel u chʼawbal. Xqui tzʼonoj che chi cu ya ni u ʼab puwi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.033', 'I Jesus xu cʼam bi i achi, y xe tejeb pan jubiʼ. Xu min u wi u ʼab pa tak u xiquin i achi; teʼuri xu chubaj pichʼ u cʼaxaj, y xu coj u wi u ʼab che u tzamraʼ i achi. ', '', '&lt;span class="verse" id="Mark_7_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I Jesus xu cʼam bi i achi, y xe tejeb pan jubiʼ. Xu min u wi u ʼab pa tak u xiquin i achi; teʼuri xu chubaj pichʼ u cʼaxaj, y xu coj u wi u ʼab che u tzamraʼ i achi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.034', 'Are ʼuri xtzun chicaj, y xu qʼuis u cʼux. Xu bij pa qui chʼawbal ique:
—¡Efáta! chi queʼelok: ¡cha jaktajok!
', '', '&lt;span class="verse" id="Mark_7_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Are ʼuri xtzun chicaj, y xu qʼuis u cʼux. Xu bij pa qui chʼawbal ique:&lt;p&gt; &lt;/p&gt;—¡Efáta! chi queʼelok: ¡cha jaktajok!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.035', 'Juntir, i u xiquin i achi xe jaktajic; xak xel u chʼawbal, n-ta chic cʼax che u tzamraʼ. ', '', '&lt;span class="verse" id="Mark_7_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Juntir, i u xiquin i achi xe jaktajic; xak xel u chʼawbal, n-ta chic cʼax che u tzamraʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.036', 'I Jesus xu bij chique i winak chi mi jun quiqui lap wi ile; xui-ri, are cu bij queje ile chique, xa mas quiqui paxsaj rason. ', '', '&lt;span class="verse" id="Mark_7_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique i winak chi mi jun quiqui lap wi ile; xui-ri, are cu bij queje ile chique, xa mas quiqui paxsaj rason. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '7.037', 'Conojel lic xqui bisoj che. Xqui bij:
—Ronojel niʼpa i cu ʼano, ʼis ʼutz; cu ʼan chique i je tac, chi que tanic; y xak i je mem, que chʼaw rumal —xe cha.
', '', '&lt;span class="verse" id="Mark_7_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Conojel lic xqui bisoj che. Xqui bij:&lt;p&gt; &lt;/p&gt;—Ronojel niʼpa i cu ʼano, ʼis ʼutz; cu ʼan chique i je tac, chi que tanic; y xak i je mem, que chʼaw rumal —xe cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.001', 'Xoʼon panok xe moltaj chic uqʼuial winak ruʼ; ique, xqʼuisic wach quiqui tijo. I Jesus xe u siqʼuij i u tijoxelab ruʼ y xu bij chique:
', '', '&lt;h2&gt;I Jesus xe u tzuk cajib mil winak&lt;/h2&gt;&lt;span class="verse" id="Mark_8_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok xe moltaj chic uqʼuial winak ruʼ; ique, xqʼuisic wach quiqui tijo. I Jesus xe u siqʼuij i u tijoxelab ruʼ y xu bij chique:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.002', '—Toʼ qui wach i winak-i, man are oxib ʼij iri chi je ʼo chi wuʼ; n-ta chic wach quiqui tijo. ', '', '&lt;span class="verse" id="Mark_8_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—Toʼ qui wach i winak-i, man are oxib ʼij iri chi je ʼo chi wuʼ; n-ta chic wach quiqui tijo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.003', 'Woʼor, we que in tak bi che cachoch, lic n-quiqui chʼij ti uponic we n-ta wa qui tijom bic. Man je ʼo jujun chique, naj je petnak wi. (Queje ile xu bij i Jesus chique u tijoxelab.)
', '', '&lt;span class="verse" id="Mark_8_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Woʼor, we que in tak bi che cachoch, lic n-quiqui chʼij ti uponic we n-ta wa qui tijom bic. Man je ʼo jujun chique, naj je petnak wi. (Queje ile xu bij i Jesus chique u tijoxelab.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.004', 'Ique xqui bij che:
—¿Wach qui tzukic i winak caka ʼan ʼuri waral? N-ta ca tijaw chi, che i lugar-i chi lic tzʼinilic —xqui bij u tijoxelab che.
', '', '&lt;span class="verse" id="Mark_8_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ique xqui bij che:&lt;p&gt; &lt;/p&gt;—¿Wach qui tzukic i winak caka ʼan ʼuri waral? N-ta ca tijaw chi, che i lugar-i chi lic tzʼinilic —xqui bij u tijoxelab che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.005', 'I Jesus xu tzʼonoj chique:
—¿Niʼpa i caxlanwa ʼo iwuʼ? —xu bij.
Ique xqui bij:
—Xa wukub —xe cha che.
', '', '&lt;span class="verse" id="Mark_8_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Niʼpa i caxlanwa ʼo iwuʼ? —xu bij.&lt;p&gt; &lt;/p&gt;Ique xqui bij:&lt;p&gt; &lt;/p&gt;—Xa wukub —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.006', 'Are ʼuri i Jesus xu bij chique i winak chi que cubi chuwach tak ulew. Teʼuri xu chap i wukub caxlanwa y xu tioxij che i Dios. Teʼuri xu piro, y xu ya chique u tijoxelab. Ique xqui jach chique i winak. ', '', '&lt;span class="verse" id="Mark_8_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique i winak chi que cubi chuwach tak ulew. Teʼuri xu chap i wukub caxlanwa y xu tioxij che i Dios. Teʼuri xu piro, y xu ya chique u tijoxelab. Ique xqui jach chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.007', 'Xak ʼo jubiʼ ral tak car cuʼ. I Jesus xu tioxij che i Dios rumal, y xu bij chique u tijoxelab chi que qui jacha chique i winak. ', '', '&lt;span class="verse" id="Mark_8_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xak ʼo jubiʼ ral tak car cuʼ. I Jesus xu tioxij che i Dios rumal, y xu bij chique u tijoxelab chi que qui jacha chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.008', 'Conojel i winak xqui tijo y xe nojic. Are xquelej ile, i u tijoxelab xqui mol niʼpa i chʼakatak chi xsobrinic, xnoj wukub chicach che. ', '', '&lt;span class="verse" id="Mark_8_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Conojel i winak xqui tijo y xe nojic. Are xquelej ile, i u tijoxelab xqui mol niʼpa i chʼakatak chi xsobrinic, xnoj wukub chicach che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.009', 'I winak chi xe nojic, colo je ʼo cajib mil chique. Teʼuri i Jesus xe u ʼijla canok, ', '', '&lt;span class="verse" id="Mark_8_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I winak chi xe nojic, colo je ʼo cajib mil chique. Teʼuri i Jesus xe u ʼijla canok, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.010', 'y xoc bi pa bárco cuʼ u tijoxelab; xe ʼax ʼuri chʼakap che i jyub Dalmanúta.
', '', '&lt;span class="verse" id="Mark_8_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;y xoc bi pa bárco cuʼ u tijoxelab; xe ʼax ʼuri chʼakap che i jyub Dalmanúta.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.011', 'Je ʼo jujun aj Fariséo winak xe upon ruʼ i Jesus y xe oc chu chapic quib chi tzij ruʼ. Xqui tzʼonoj che chi cu cʼut jun milágro chique, cʼutbal-re we ʼo u choʼab i Dios ruʼ; ique xa cʼambal u pam quiqui ʼano. ', '', '&lt;h2&gt;I aj Fariséo toʼ xqui tzʼonoj tak milágro che i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_8_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Je ʼo jujun aj Fariséo winak xe upon ruʼ i Jesus y xe oc chu chapic quib chi tzij ruʼ. Xqui tzʼonoj che chi cu cʼut jun milágro chique, cʼutbal-re we ʼo u choʼab i Dios ruʼ; ique xa cʼambal u pam quiqui ʼano. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.012', 'I Jesus, lic xu qʼuis u cʼux rumal; xu bij:
—¿Wuchac ix winak woʼor xa quiwaj quiwil ni jun milágro, cʼutbal re u choʼab i Dios? Lic quin bij chiwe, lic mi jun cʼutbal re u choʼab ca ʼan na chiwach —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_8_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I Jesus, lic xu qʼuis u cʼux rumal; xu bij:&lt;p&gt; &lt;/p&gt;—¿Wuchac ix winak woʼor xa quiwaj quiwil ni jun milágro, cʼutbal re u choʼab i Dios? Lic quin bij chiwe, lic mi jun cʼutbal re u choʼab ca ʼan na chiwach —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.013', 'Teʼuri xe u canaj canok y xoc pa bárco, y xe ʼax chic chʼaka alagun.
', '', '&lt;span class="verse" id="Mark_8_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Teʼuri xe u canaj canok y xoc pa bárco, y xe ʼax chic chʼaka alagun.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.014', 'I u tijoxelab i Jesus xsach i qui wa chi qui cʼux; n-xqui cʼam tu bic wach i rajwaxic quiqui tij pa be. Xa jun caxlanwa qui cʼamom bi cuʼ pa bárco. ', '', '&lt;h2&gt;Mi ʼan yix pacha quiqui ʼan i aj Fariséo&lt;/h2&gt;&lt;span class="verse" id="Mark_8_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I u tijoxelab i Jesus xsach i qui wa chi qui cʼux; n-xqui cʼam tu bic wach i rajwaxic quiqui tij pa be. Xa jun caxlanwa qui cʼamom bi cuʼ pa bárco. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.015', 'I Jesus xu bij jun moxbal tzij chique:
—Chitapeʼ, chi ʼana cwent che i cunbal re i caxlanwa que i aj Fariséo, xak i re i ma Heródes —xu bij chique.
', '', '&lt;span class="verse" id="Mark_8_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I Jesus xu bij jun moxbal tzij chique:&lt;p&gt; &lt;/p&gt;—Chitapeʼ, chi ʼana cwent che i cunbal re i caxlanwa que i aj Fariséo, xak i re i ma Heródes —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.016', 'I u tijoxelab xe oc chu bixquil chiquiwach:
—Xu bij ire ile man n-ta ka caxlanwa ka cʼamom lok —xe cha.
', '', '&lt;span class="verse" id="Mark_8_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I u tijoxelab xe oc chu bixquil chiquiwach:&lt;p&gt; &lt;/p&gt;—Xu bij ire ile man n-ta ka caxlanwa ka cʼamom lok —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.017', 'I Jesus xu naʼo wach quiqui bij; xu bij chique:
—¿Wuchac quix cʼachir rumal i caxlanwa? ¿Wuchac quix lapan chirij? ¿Maja ba qui ta u be ʼuri? ¿Xix naw ta bawa che, wach xin ʼano? ¿Lic co ba i jolom-i? ', '', '&lt;span class="verse" id="Mark_8_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I Jesus xu naʼo wach quiqui bij; xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchac quix cʼachir rumal i caxlanwa? ¿Wuchac quix lapan chirij? ¿Maja ba qui ta u be ʼuri? ¿Xix naw ta bawa che, wach xin ʼano? ¿Lic co ba i jolom-i? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.018', 'ʼO i baʼwach, ¿n-quix tzun ta bawa? ʼO i xiquin, ¿n-quix tan ta bawa? ¿N-ca cʼun ta bawa pi cʼux ', '', '&lt;span class="verse" id="Mark_8_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;ʼO i baʼwach, ¿n-quix tzun ta bawa? ʼO i xiquin, ¿n-quix tan ta bawa? ¿N-ca cʼun ta bawa pi cʼux &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.019', 'wach xiwilo are xin ya i joʼob pan chique i joʼob mil winak? ¿Niʼpa chicach i chʼakatak xsobrinic? —xu bij chique.
—Cablajuj —xqui bij che.
', '', '&lt;span class="verse" id="Mark_8_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;wach xiwilo are xin ya i joʼob pan chique i joʼob mil winak? ¿Niʼpa chicach i chʼakatak xsobrinic? —xu bij chique.&lt;p&gt; &lt;/p&gt;—Cablajuj —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.020', '—Xak are xin ya i wukub pan chique i cajib mil winak, ¿niʼpa chicach i chʼakatak xsobrinic? —xu bij chique.
—Wukub —xqui bij che.
', '', '&lt;span class="verse" id="Mark_8_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;—Xak are xin ya i wukub pan chique i cajib mil winak, ¿niʼpa chicach i chʼakatak xsobrinic? —xu bij chique.&lt;p&gt; &lt;/p&gt;—Wukub —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.021', 'Teʼuri xu bij:
—¿Maja qui ta ʼuri u be ile? —coʼono.
', '', '&lt;span class="verse" id="Mark_8_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij:&lt;p&gt; &lt;/p&gt;—¿Maja qui ta ʼuri u be ile? —coʼono.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.022', 'Are xe upon pa tinimit Betsáida, xcʼam li jun mawach ruʼ i Jesus, y xqui tzʼonoj che chi cu chap na ruʼ u ʼab. ', '', '&lt;h2&gt;Wach rutzirsaxic jun mawach pa Betsáida&lt;/h2&gt;&lt;span class="verse" id="Mark_8_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are xe upon pa tinimit Betsáida, xcʼam li jun mawach ruʼ i Jesus, y xqui tzʼonoj che chi cu chap na ruʼ u ʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.023', 'I Jesus xu chap bi che u ʼab, y xel pa tinimit ruʼ. Teʼuri i Jesus xu coj pichʼ u cʼaxaj che u baʼwach i mawach, y xu ya u ʼab puwi. Teʼuri xu tzʼonoj che we ca tzun jubiʼ. ', '', '&lt;span class="verse" id="Mark_8_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I Jesus xu chap bi che u ʼab, y xel pa tinimit ruʼ. Teʼuri i Jesus xu coj pichʼ u cʼaxaj che u baʼwach i mawach, y xu ya u ʼab puwi. Teʼuri xu tzʼonoj che we ca tzun jubiʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.024', 'I mawach xu bij:
—Que wil i winak —xu bij—. Pacha cheʼ, cwil yin, xui-ri que binic —xu bij.
', '', '&lt;span class="verse" id="Mark_8_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I mawach xu bij:&lt;p&gt; &lt;/p&gt;—Que wil i winak —xu bij—. Pacha cheʼ, cwil yin, xui-ri que binic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.025', 'I Jesus xu coj chic u ʼab che u baʼwach; teʼuri i achi xu coj retalil che u takenic, y xutziric. Are ʼuri lic xtzun bien. ', '', '&lt;span class="verse" id="Mark_8_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I Jesus xu coj chic u ʼab che u baʼwach; teʼuri i achi xu coj retalil che u takenic, y xutziric. Are ʼuri lic xtzun bien. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.026', 'Teʼuri i Jesus xu tak bi ruʼ rachoch, y xu bij che:
—Mat oc pa tinimit, xak ma bij iri chila —xu bij che.
', '', '&lt;span class="verse" id="Mark_8_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu tak bi ruʼ rachoch, y xu bij che:&lt;p&gt; &lt;/p&gt;—Mat oc pa tinimit, xak ma bij iri chila —xu bij che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.027', 'Teʼuri i Jesus xa ʼe cuʼ u tijoxelab che tak i aldéa re i tinimit Cesaréa Filípo. Are que tijin chi be, xu tzʼonoj chique u tijoxelab:
—I winak, are quin qui lapo, ¿wach quiqui bij ique chi in pachin yin? —xu bij.
', '', '&lt;h2&gt;I ma Pédro xu bij chi Jesus are i Crísto chi Tolque&lt;/h2&gt;&lt;span class="verse" id="Mark_8_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xa ʼe cuʼ u tijoxelab che tak i aldéa re i tinimit Cesaréa Filípo. Are que tijin chi be, xu tzʼonoj chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—I winak, are quin qui lapo, ¿wach quiqui bij ique chi in pachin yin? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.028', 'Ique xqui bij che:
—ʼO jujun quiqui bij chi yet at ʼetz ma Wan chi aj kajsanel ya, quiqui bij. Xak ʼo i que binic chi yet at ujer mam Elías. Xak je ʼo juban chic quiqui bij chi yet at jun chic ajbil u tzij i Dios, quiqui bij i winak chawij. Queje ile xqui bij u tijoxelab che.
', '', '&lt;span class="verse" id="Mark_8_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Ique xqui bij che:&lt;p&gt; &lt;/p&gt;—ʼO jujun quiqui bij chi yet at ʼetz ma Wan chi aj kajsanel ya, quiqui bij. Xak ʼo i que binic chi yet at ujer mam Elías. Xak je ʼo juban chic quiqui bij chi yet at jun chic ajbil u tzij i Dios, quiqui bij i winak chawij. Queje ile xqui bij u tijoxelab che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.029', 'Teʼuri i Jesus xu tzʼonoj chique u tijoxelab:
—Y yix, ¿wach i qui bij yix chi in pachin yin?
I ma Pédro xu bij:
—Yet at Crísto (chi Tolke koyʼem) —xu bij che. ', '', '&lt;span class="verse" id="Mark_8_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu tzʼonoj chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Y yix, ¿wach i qui bij yix chi in pachin yin?&lt;p&gt; &lt;/p&gt;I ma Pédro xu bij:&lt;p&gt; &lt;/p&gt;—Yet at Crísto (chi Tolke koyʼem) —xu bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.030', 'Xui-ri i Jesus xu bij chique chi mi jun quiqui lap wi pachin ire.
', '', '&lt;span class="verse" id="Mark_8_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xui-ri i Jesus xu bij chique chi mi jun quiqui lap wi pachin ire.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.031', 'Teʼuri i Jesus xoc chu bixquil chique chi ire, chi Achi aj Chicaj, lic cu tij ni uyej, xak ca xutux na cumal i nimak tak mamʼib, xak cumal i cajʼatzil i sacerdóte, xak cumal i aj tijonel re i ujer ʼatbal tzij. Xu bij chique chi ca camsax na, teʼuri churox ʼij ca walijic. ', '', '&lt;h2&gt;I Jesus xu bij chique wach i camic quicʼaw wi&lt;/h2&gt;&lt;span class="verse" id="Mark_8_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc chu bixquil chique chi ire, chi Achi aj Chicaj, lic cu tij ni uyej, xak ca xutux na cumal i nimak tak mamʼib, xak cumal i cajʼatzil i sacerdóte, xak cumal i aj tijonel re i ujer ʼatbal tzij. Xu bij chique chi ca camsax na, teʼuri churox ʼij ca walijic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.032', 'Ile lic sakil xu bij chique. Are ʼuri i ma Pédro xu siqʼuij pan jubiʼ ruʼ, y xoc chu ʼatixic wach i ca tijin chu bixquil. ', '', '&lt;span class="verse" id="Mark_8_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Ile lic sakil xu bij chique. Are ʼuri i ma Pédro xu siqʼuij pan jubiʼ ruʼ, y xoc chu ʼatixic wach i ca tijin chu bixquil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.033', 'I Jesus xu pisquilij rib, y xe u tzu u tijoxelab. Are ʼuri xu yaj i ma Pédro:
—¡Chat el chinwach, a-Satanas! A nojbal yet n-queje taj pacha u chʼobom i Dios. Ca chʼob yet pacha quiqui chʼob i toʼ winak —xu bij che.
', '', '&lt;span class="verse" id="Mark_8_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I Jesus xu pisquilij rib, y xe u tzu u tijoxelab. Are ʼuri xu yaj i ma Pédro:&lt;p&gt; &lt;/p&gt;—¡Chat el chinwach, a-Satanas! A nojbal yet n-queje taj pacha u chʼobom i Dios. Ca chʼob yet pacha quiqui chʼob i toʼ winak —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.034', 'Teʼuri i Jesus xe u siqʼuij u tijoxelab ruʼ, xak i uqʼuial winak, y xu bij chique:
—Pachin jun craj ca terej chwij, ʼo u chac mu chʼob chic rib, xui-ri charekaj bi u cruz, y cha terej na chwij, tupu ca cam na rumal. ', '', '&lt;span class="verse" id="Mark_8_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u siqʼuij u tijoxelab ruʼ, xak i uqʼuial winak, y xu bij chique:&lt;p&gt; &lt;/p&gt;—Pachin jun craj ca terej chwij, ʼo u chac mu chʼob chic rib, xui-ri charekaj bi u cruz, y cha terej na chwij, tupu ca cam na rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.035', 'Queje ile chu ʼana man pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, pachin jun ca sachsax nu wach u cʼaslemal waral xa wumal yin, xak rumal i ʼutz laj tzij re i Dios, cu rik ʼuri u cʼaslemal sakil. ', '', '&lt;span class="verse" id="Mark_8_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Queje ile chu ʼana man pachin jun xa cʼax cu na u cʼaslemal re waral, xa cu sach ʼuri u cʼaslemal chi sakil. Xui-ri, pachin jun ca sachsax nu wach u cʼaslemal waral xa wumal yin, xak rumal i ʼutz laj tzij re i Dios, cu rik ʼuri u cʼaslemal sakil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.036', 'We ʼo jun u chʼacom ronojel i beyomal re u wach i jyub taʼaj, xui-ri we cu sach u cʼaslemal sakil, ile n-ta queʼel wi, n-ta u chʼacom ʼuri. ', '', '&lt;span class="verse" id="Mark_8_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;We ʼo jun u chʼacom ronojel i beyomal re u wach i jyub taʼaj, xui-ri we cu sach u cʼaslemal sakil, ile n-ta queʼel wi, n-ta u chʼacom ʼuri. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.037', '¿Wach u tojic ʼuri coʼon na, chʼacbal re i u cʼaslemal sakil we u sachom? N-taj. ', '', '&lt;span class="verse" id="Mark_8_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;¿Wach u tojic ʼuri coʼon na, chʼacbal re i u cʼaslemal sakil we u sachom? N-taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '8.038', 'We ʼo jun ca qʼuix na chwe yin, xak che in tzij, chiquiwach i winak re woʼor chi lic je ajmaquib, xak yin quin qʼuix na che ire are quin cʼun tan chic. Yin, chi in Achi aj Chicaj, quin cʼun chic ruʼ u ʼij u chomal in Kajaw, xak cuʼ i loʼlaj ángel —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_8_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;We ʼo jun ca qʼuix na chwe yin, xak che in tzij, chiquiwach i winak re woʼor chi lic je ajmaquib, xak yin quin qʼuix na che ire are quin cʼun tan chic. Yin, chi in Achi aj Chicaj, quin cʼun chic ruʼ u ʼij u chomal in Kajaw, xak cuʼ i loʼlaj ángel —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.001', 'Xak xu bij chique:
—Katzij quin bij chiwe, je ʼo jujun waral chi n-que cam ta tan cʼa quiquil ni u jekanic i ʼatbal tzij re i Dios waral, chi nim u choʼab.
', '', '&lt;span class="verse" id="Mark_9_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij quin bij chiwe, je ʼo jujun waral chi n-que cam ta tan cʼa quiquil ni u jekanic i ʼatbal tzij re i Dios waral, chi nim u choʼab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.002', 'Wakib ʼij u ʼonquil ile, i Jesus xe u cʼam bi i ma Pédro, i ma Jacóbo, xak i ma Wan; y xe ʼe quituquel che jun nim laj jyub naj u wi. Are je ʼo chila, i u tzunbal i Jesus lic xyojtajic. ', '', '&lt;h2&gt;Wach u yojtajic u tzunbal i Jesus xoʼon chiquiwach u tijoxelab&lt;/h2&gt;&lt;span class="verse" id="Mark_9_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Wakib ʼij u ʼonquil ile, i Jesus xe u cʼam bi i ma Pédro, i ma Jacóbo, xak i ma Wan; y xe ʼe quituquel che jun nim laj jyub naj u wi. Are je ʼo chila, i u tzunbal i Jesus lic xyojtajic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.003', 'I u ʼuʼ lic ca walchʼin rumal u sakil; lic sak ʼotoʼoj ca tzunic. N-ta jun cʼul, tupu lic chʼajtalic, chi ca majaw u sakil ile. ', '', '&lt;span class="verse" id="Mark_9_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I u ʼuʼ lic ca walchʼin rumal u sakil; lic sak ʼotoʼoj ca tzunic. N-ta jun cʼul, tupu lic chʼajtalic, chi ca majaw u sakil ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.004', 'Xak xquilo chi xe winakir i mam Moises xak i mam Elías ruʼ i Jesus, que tijin chi lapanic. ', '', '&lt;span class="verse" id="Mark_9_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xak xquilo chi xe winakir i mam Moises xak i mam Elías ruʼ i Jesus, que tijin chi lapanic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.005', 'Are ʼuri i ma Pédro xu bij che i Jesus:
—Mayes, lic ʼutz na oj ʼo waral. ʼUtz caka ʼan oxib i mujbal: jun awe yet, jun re i mam Moises, xak jun re i mam Elías —xu bij che.
', '', '&lt;span class="verse" id="Mark_9_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—Mayes, lic ʼutz na oj ʼo waral. ʼUtz caka ʼan oxib i mujbal: jun awe yet, jun re i mam Moises, xak jun re i mam Elías —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.006', 'I u tijoxelab, lic je ocnak ʼil che wach xquilo, rumal-i n-xu na ti ma Pédro wach i xu bij. ', '', '&lt;span class="verse" id="Mark_9_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I u tijoxelab, lic je ocnak ʼil che wach xquilo, rumal-i n-xu na ti ma Pédro wach i xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.007', 'Teʼuri xkaj jutzobaj sutzʼ piquiwi y xqui ta jun nim laj chʼawbal xchʼaw chupam i sutzʼ:
—Iri, are in Cʼojol, chi lic cʼax quin naʼo; chi cojo retalil che, wach i cu bij.
', '', '&lt;span class="verse" id="Mark_9_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Teʼuri xkaj jutzobaj sutzʼ piquiwi y xqui ta jun nim laj chʼawbal xchʼaw chupam i sutzʼ:&lt;p&gt; &lt;/p&gt;—Iri, are in Cʼojol, chi lic cʼax quin naʼo; chi cojo retalil che, wach i cu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.008', 'Are xqui ta ile, juntir cʼula xe tzunic, péro n-ta xquilo, xui chic i Jesus utuquel.
', '', '&lt;span class="verse" id="Mark_9_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are xqui ta ile, juntir cʼula xe tzunic, péro n-ta xquilo, xui chic i Jesus utuquel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.009', 'Are xe kaj li che i jyub-le, i Jesus xu bij chique u tijoxelab:
—Niʼpa i xiwil yix, mi lapo cʼa cu rik ni ʼij chi yin, chi in i Achi aj Chicaj, in walijnak chic che i camic —xu bij chique.
', '', '&lt;span class="verse" id="Mark_9_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Are xe kaj li che i jyub-le, i Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Niʼpa i xiwil yix, mi lapo cʼa cu rik ni ʼij chi yin, chi in i Achi aj Chicaj, in walijnak chic che i camic —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.010', 'Rumal-i, n-xqui bij taj wach i xquilo; xui-ri, xqui tzʼonoj chiquiwach wach usucʼ i tzij-le xu bij, chi ca walij che i camic. ', '', '&lt;span class="verse" id="Mark_9_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Rumal-i, n-xqui bij taj wach i xquilo; xui-ri, xqui tzʼonoj chiquiwach wach usucʼ i tzij-le xu bij, chi ca walij che i camic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.011', 'Teʼuri xqui tzʼonoj che i Jesus:
—I aj tijonel re i ujer ʼatbal tzij, ¿wuchac quiqui bij ique chi nabe ca cʼun i ujer mam Elías? —xqui bij che.
', '', '&lt;span class="verse" id="Mark_9_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Teʼuri xqui tzʼonoj che i Jesus:&lt;p&gt; &lt;/p&gt;—I aj tijonel re i ujer ʼatbal tzij, ¿wuchac quiqui bij ique chi nabe ca cʼun i ujer mam Elías? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.012', 'I Jesus xu bij:
—Katzij i xqui bij-le, nabe ca cʼun i mam Elías, col u yijba ronojel; queje ile tzʼibtal canok. Xak tzʼibtal chic jun tzij chirij i Achi aj Chicaj: cu bij chi cu tij ni uyej xak ca xutux na. ', '', '&lt;span class="verse" id="Mark_9_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij i xqui bij-le, nabe ca cʼun i mam Elías, col u yijba ronojel; queje ile tzʼibtal canok. Xak tzʼibtal chic jun tzij chirij i Achi aj Chicaj: cu bij chi cu tij ni uyej xak ca xutux na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.013', 'Xui-ri, yin quin bij chiwe chi mam Elías ya cʼun-nak chic; i winak xqui ʼan che pacha cacaj ique; xeʼelok pacha i tzʼibtal can chupam u wuj i Dios —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_9_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xui-ri, yin quin bij chiwe chi mam Elías ya cʼun-nak chic; i winak xqui ʼan che pacha cacaj ique; xeʼelok pacha i tzʼibtal can chupam u wuj i Dios —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.014', 'Teʼuri xe tzalij chic chila pa je ʼo wi i juban u tijoxelab, y xquilo je qʼui i winak je ʼo chic chiquij chiquiwach. Xak je ʼo jujun tijonel chila re i ʼatbal tzij re i mam Moises; ique qui chapom quib chi tzij cuʼ u tijoxelab. ', '', '&lt;h2&gt;Wach i relsaxic itzel tew che jun ala&lt;/h2&gt;&lt;span class="verse" id="Mark_9_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri xe tzalij chic chila pa je ʼo wi i juban u tijoxelab, y xquilo je qʼui i winak je ʼo chic chiquij chiquiwach. Xak je ʼo jujun tijonel chila re i ʼatbal tzij re i mam Moises; ique qui chapom quib chi tzij cuʼ u tijoxelab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.015', 'I winak, are xquil i Jesus, xqui bisoj, are ʼuri, xe ʼe paʼanem chu yaʼic rutzil u wach. ', '', '&lt;span class="verse" id="Mark_9_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I winak, are xquil i Jesus, xqui bisoj, are ʼuri, xe ʼe paʼanem chu yaʼic rutzil u wach. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.016', 'Are ʼuri, ire xu tzʼonoj chique:
—¿Wach quix tijin chu lapic cuʼ ique?
', '', '&lt;span class="verse" id="Mark_9_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Are ʼuri, ire xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Wach quix tijin chu lapic cuʼ ique?&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.017', 'ʼO jun achi chiquixól xu bij:
—Mayes, yin xin cʼam li jun wacʼal awuʼ, ʼo itzel tew che chi u ʼanom mem che. ', '', '&lt;span class="verse" id="Mark_9_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;ʼO jun achi chiquixól xu bij:&lt;p&gt; &lt;/p&gt;—Mayes, yin xin cʼam li jun wacʼal awuʼ, ʼo itzel tew che chi u ʼanom mem che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.018', 'Xa ipa ʼo wi, ca chaptaj rumal itzel tew, y ca tzaksax pulew. Are ʼuri xa ca wokow u cʼaxaj pu chiʼ, xak cu kachʼachʼej uware rumal. Rumal-i, lic n-ta chic u choʼab. Xin tzʼonoj chique a tijoxelab chi caquesaj i mal laj tew che. Xui-ri, n-xe tiqui ta che —xu bij i achi che.
', '', '&lt;span class="verse" id="Mark_9_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xa ipa ʼo wi, ca chaptaj rumal itzel tew, y ca tzaksax pulew. Are ʼuri xa ca wokow u cʼaxaj pu chiʼ, xak cu kachʼachʼej uware rumal. Rumal-i, lic n-ta chic u choʼab. Xin tzʼonoj chique a tijoxelab chi caquesaj i mal laj tew che. Xui-ri, n-xe tiqui ta che —xu bij i achi che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.019', 'Are ʼuri i Jesus xu bij:
—¡Ay, yix chi n-tu cubibal i cʼux che i Dios! ¡ʼO tan chi ʼij in ʼo iwuʼ, y yix n-ta i nawic! ¿Niʼpa nawi chic ʼij cʼa craj? —xu bij—. Chi cʼama li acʼal waral —coʼono.
', '', '&lt;span class="verse" id="Mark_9_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—¡Ay, yix chi n-tu cubibal i cʼux che i Dios! ¡ʼO tan chi ʼij in ʼo iwuʼ, y yix n-ta i nawic! ¿Niʼpa nawi chic ʼij cʼa craj? —xu bij—. Chi cʼama li acʼal waral —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.020', 'Xqui cʼamlok. I itzel tew, are xril i Jesus, xu ya chic atac che i ala. Ire-le xtzak pulew, lic cu balʼatij chic rib chuwach ulew, xak ca wokow u cʼaxaj. ', '', '&lt;span class="verse" id="Mark_9_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xqui cʼamlok. I itzel tew, are xril i Jesus, xu ya chic atac che i ala. Ire-le xtzak pulew, lic cu balʼatij chic rib chuwach ulew, xak ca wokow u cʼaxaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.021', 'Are ʼuri i Jesus xu tzʼonoj che i achi:
—¿Niʼpa junab u jekanic i cʼax che? —xu bij.
I u kajaw xu bij:
—Ire cʼa chʼutin are xu jeko. ', '', '&lt;span class="verse" id="Mark_9_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu tzʼonoj che i achi:&lt;p&gt; &lt;/p&gt;—¿Niʼpa junab u jekanic i cʼax che? —xu bij.&lt;p&gt; &lt;/p&gt;I u kajaw xu bij:&lt;p&gt; &lt;/p&gt;—Ire cʼa chʼutin are xu jeko. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.022', 'I itzel tew-le, qʼuialmul u cʼakom pa ʼaʼ, xak pa ya; craj cu camsaj. ¡Chawila toʼbal ka wach! ¡Choj a to na, we cat tiqui che! —xu bij i achi.
', '', '&lt;span class="verse" id="Mark_9_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I itzel tew-le, qʼuialmul u cʼakom pa ʼaʼ, xak pa ya; craj cu camsaj. ¡Chawila toʼbal ka wach! ¡Choj a to na, we cat tiqui che! —xu bij i achi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.023', '—Yin quin tiqui che —xu bij i Jesus—, xui-ri we yet cat tiqui chu cubaxic a cʼux chwe. Ronojel ʼis coj tiqui che we ʼo u cubibal ka cʼux —xu bij.
', '', '&lt;span class="verse" id="Mark_9_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;—Yin quin tiqui che —xu bij i Jesus—, xui-ri we yet cat tiqui chu cubaxic a cʼux chwe. Ronojel ʼis coj tiqui che we ʼo u cubibal ka cʼux —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.024', 'Juntir i u kajaw i ala xsiqʼuin chu bixquil:
—Cul in cʼux chawe —xu bij—. ¡Chin a to na che, chi quin cuba na mas in cʼux chawe! —xu bij.
', '', '&lt;span class="verse" id="Mark_9_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Juntir i u kajaw i ala xsiqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—Cul in cʼux chawe —xu bij—. ¡Chin a to na che, chi quin cuba na mas in cʼux chawe! —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.025', 'I Jesus, are xrilo chi que tijin qui moltajic i winak ruʼ, xu yaj i itzel tew y xu bij che:
—Yet, at tac y at mem laj itzel tew, quin bij chawe, chat el che i ala-le. Chawoʼtaj, y mat oc chic ruʼ —xu bij che.
', '', '&lt;span class="verse" id="Mark_9_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I Jesus, are xrilo chi que tijin qui moltajic i winak ruʼ, xu yaj i itzel tew y xu bij che:&lt;p&gt; &lt;/p&gt;—Yet, at tac y at mem laj itzel tew, quin bij chawe, chat el che i ala-le. Chawoʼtaj, y mat oc chic ruʼ —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.026', 'I itzel tew siqʼuinic xoʼon chic jun atac che i ala, teʼuri xelic. Xcanaj can i ala lic pacha xcamic; rumal-i, conojel xqui bij chi lic xcamic. ', '', '&lt;span class="verse" id="Mark_9_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I itzel tew siqʼuinic xoʼon chic jun atac che i ala, teʼuri xelic. Xcanaj can i ala lic pacha xcamic; rumal-i, conojel xqui bij chi lic xcamic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.027', 'Xui-ri, i Jesus xu chap che u ʼab y xu yaco; i ala xtaqʼuiʼic.
', '', '&lt;span class="verse" id="Mark_9_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xui-ri, i Jesus xu chap che u ʼab y xu yaco; i ala xtaqʼuiʼic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.028', 'Teʼuri i Jesus xoc pa ja. Are ʼuri i u tojoxelab xqui tzʼonoj che:
—¿Wuchac n-xoj tiqui ti yoj chi relsaxic i itzel tew-le? —que cha.
', '', '&lt;span class="verse" id="Mark_9_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xoc pa ja. Are ʼuri i u tojoxelab xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wuchac n-xoj tiqui ti yoj chi relsaxic i itzel tew-le? —que cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.029', 'Ire xu bij chique:
—I mal laj itzel tew pacha i jun-le n-quel taj, xui-ri we lic qui tzʼonoj che i Dios, xak we qui mayij ni waʼim rumal —xu bij.
', '', '&lt;span class="verse" id="Mark_9_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Ire xu bij chique:&lt;p&gt; &lt;/p&gt;—I mal laj itzel tew pacha i jun-le n-quel taj, xui-ri we lic qui tzʼonoj che i Dios, xak we qui mayij ni waʼim rumal —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.030', 'Are xe ʼel chila, xe ʼax pa Galiléa. I Jesus n-xraj taj we caquetamaj i winak pa ʼo wi ire. ', '', '&lt;h2&gt;I Jesus xu bij tan chic chi ca camsax na&lt;/h2&gt;&lt;span class="verse" id="Mark_9_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Are xe ʼel chila, xe ʼax pa Galiléa. I Jesus n-xraj taj we caquetamaj i winak pa ʼo wi ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.031', 'N-xraj taj, man ire ca tijin chu cʼutic chique u tijoxelab; ca tijin chu bixquil chique chi i Achi aj Chicaj ca jachtaj na piquiʼab i winak, y ca camsax na cumal, xui-ri che oxib ʼij panok ca walij na. ', '', '&lt;span class="verse" id="Mark_9_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;N-xraj taj, man ire ca tijin chu cʼutic chique u tijoxelab; ca tijin chu bixquil chique chi i Achi aj Chicaj ca jachtaj na piquiʼab i winak, y ca camsax na cumal, xui-ri che oxib ʼij panok ca walij na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.032', 'Tupu xu bij ire ile chique, n-xqui ta tu be, xak xqui xij quib chu tzʼonoxic che wach usucʼ.
', '', '&lt;span class="verse" id="Mark_9_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Tupu xu bij ire ile chique, n-xqui ta tu be, xak xqui xij quib chu tzʼonoxic che wach usucʼ.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.033', 'Xoʼon chupanok, xe upon pa Capernaum. Are xe oc pa ja chila, i Jesus xu tzʼonoj chique utijoxelab:
—Are coj tijin te chi be, ¿wach i tzij i chapom wi iwib yix puwi? —xu bij chique.
', '', '&lt;h2&gt;Pachin mas nim u ʼij, are ajchac ʼuri chiquij i rachiʼil&lt;/h2&gt;&lt;span class="verse" id="Mark_9_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xoʼon chupanok, xe upon pa Capernaum. Are xe oc pa ja chila, i Jesus xu tzʼonoj chique utijoxelab:&lt;p&gt; &lt;/p&gt;—Are coj tijin te chi be, ¿wach i tzij i chapom wi iwib yix puwi? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.034', 'Ique lic n-xe chʼaw ta chic. Are i tzij chi qui chapom wi quib ique puwi pa be, are i pachin mas nim u ʼij chique ique. ', '', '&lt;span class="verse" id="Mark_9_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Ique lic n-xe chʼaw ta chic. Are i tzij chi qui chapom wi quib ique puwi pa be, are i pachin mas nim u ʼij chique ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.035', 'I Jesus xcubiʼic, y xe u siqʼuij i cablajuj u tijoxelab ruʼ. Are ʼuri xu bij chique:
—Pachin jun craj cu ya rib chu nabeal, chu ya na rib ʼuri pa qʼuisbal-re chique i conojel, xak chu ʼana che rib pacha mocom chiquij —xu bij.
', '', '&lt;span class="verse" id="Mark_9_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I Jesus xcubiʼic, y xe u siqʼuij i cablajuj u tijoxelab ruʼ. Are ʼuri xu bij chique:&lt;p&gt; &lt;/p&gt;—Pachin jun craj cu ya rib chu nabeal, chu ya na rib ʼuri pa qʼuisbal-re chique i conojel, xak chu ʼana che rib pacha mocom chiquij —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.036', 'Are ʼuri xu cʼam jun acʼal ruʼ y xu ya chiquiwach. Xu ʼaluj ʼanok y xu bij chique:
', '', '&lt;span class="verse" id="Mark_9_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Are ʼuri xu cʼam jun acʼal ruʼ y xu ya chiquiwach. Xu ʼaluj ʼanok y xu bij chique:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.037', '—Pachin jun cu cʼulaj jun acʼal ruʼ pacha iri rumal i cʼaxnabal u cʼux chwe yin, are yin ʼuri quin u cʼulaj ruʼ. Pachin jun quin u cʼulaj yin ruʼ, n-xui ti yin quin u cʼulaj ruʼ, xak xu cʼulaj ʼuri i Jun chi in takawnak lok —xu bij chique.
', '', '&lt;span class="verse" id="Mark_9_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;—Pachin jun cu cʼulaj jun acʼal ruʼ pacha iri rumal i cʼaxnabal u cʼux chwe yin, are yin ʼuri quin u cʼulaj ruʼ. Pachin jun quin u cʼulaj yin ruʼ, n-xui ti yin quin u cʼulaj ruʼ, xak xu cʼulaj ʼuri i Jun chi in takawnak lok —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.038', 'Teʼuri i ma Wan xu bij che:
—Mayes, xkil jun achi chi ca tijin chi relsaxic itzel tew ruʼ u cuxtaxic a bi yet. Yoj ʼut xka bij che chu mayij, man n-kachiʼil taj —xu bij che i Jesus.
', '', '&lt;h2&gt;Pachin n-coʼon ta chʼoʼoj chikij, u yom ʼuri u wach kuʼ&lt;/h2&gt;&lt;span class="verse" id="Mark_9_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Wan xu bij che:&lt;p&gt; &lt;/p&gt;—Mayes, xkil jun achi chi ca tijin chi relsaxic itzel tew ruʼ u cuxtaxic a bi yet. Yoj ʼut xka bij che chu mayij, man n-kachiʼil taj —xu bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.039', '—Ile mi ʼatij —xu bij i Jesus che—, man we ʼo jun coʼon milágro rumal u cuxtaxic in bi yin, n-cu bij ta ʼuri mal laj tak tzij chwij. ', '', '&lt;span class="verse" id="Mark_9_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;—Ile mi ʼatij —xu bij i Jesus che—, man we ʼo jun coʼon milágro rumal u cuxtaxic in bi yin, n-cu bij ta ʼuri mal laj tak tzij chwij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.040', 'Pachin jun n-coʼon ta chʼoʼoj chikij, ire-le ka cwenta yoj. ', '', '&lt;span class="verse" id="Mark_9_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Pachin jun n-coʼon ta chʼoʼoj chikij, ire-le ka cwenta yoj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.041', 'Pachin jun coʼon jun utzil chomal chiwe xa rumal ix terejnak chwij, ʼo ni sipon ʼuri cu chʼaco. Queje ile queʼel che, tupu xa jun bas i yaʼ cu ya chiwe wumal yin —xu abij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_9_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Pachin jun coʼon jun utzil chomal chiwe xa rumal ix terejnak chwij, ʼo ni sipon ʼuri cu chʼaco. Queje ile queʼel che, tupu xa jun bas i yaʼ cu ya chiwe wumal yin —xu abij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.042', 'Xak xu bij chique:
—Jun chʼutin chi u cubam u cʼux chwe, we ʼo jun cu ʼan che chi ca tzak chupam u mac, mas ʼutz queʼel che i jun-le we tene ca xim jun caʼ rij u kul y ca cʼak bi pa ya, jiʼbalre. I camic-le mas ʼutz chuwach i camic chi ca pe puwi we cu tzaksaj jun chʼutin chupam u mac. ', '', '&lt;h2&gt;Chawesaj wach tak i cat tzaksaw chupam i mac&lt;/h2&gt;&lt;span class="verse" id="Mark_9_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—Jun chʼutin chi u cubam u cʼux chwe, we ʼo jun cu ʼan che chi ca tzak chupam u mac, mas ʼutz queʼel che i jun-le we tene ca xim jun caʼ rij u kul y ca cʼak bi pa ya, jiʼbalre. I camic-le mas ʼutz chuwach i camic chi ca pe puwi we cu tzaksaj jun chʼutin chupam u mac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.043', 'We are a ʼab ca ʼanaw chawe chi cat tzak chupam a mac, suʼ cha cʼupij. Mas ʼutz ca sach a ʼab, tobal re a cʼaslemal, chuwach xa cat ʼe pa ʼaʼ chi n-ca chup taj, ruʼ i queb a ʼab tzʼakat. ', '', '&lt;span class="verse" id="Mark_9_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;We are a ʼab ca ʼanaw chawe chi cat tzak chupam a mac, suʼ cha cʼupij. Mas ʼutz ca sach a ʼab, tobal re a cʼaslemal, chuwach xa cat ʼe pa ʼaʼ chi n-ca chup taj, ruʼ i queb a ʼab tzʼakat. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.044', 'I amol chi cat qui tij chila lic n-que cam taj, xak i ʼaʼ mi jun ca chupuwic. ', '', '&lt;span class="verse" id="Mark_9_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;I amol chi cat qui tij chila lic n-que cam taj, xak i ʼaʼ mi jun ca chupuwic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.045', 'We are awakan ca ʼanaw chawe cat tzak chupam a mac, suʼ cha cʼupij bic. Mas ʼutz ca sach awakan, tobal re a cʼaslemal, chuwach i xa cat ʼe pa ʼaʼ chi n-ca chup taj, ruʼ i queb awakan tzʼakat. ', '', '&lt;span class="verse" id="Mark_9_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;We are awakan ca ʼanaw chawe cat tzak chupam a mac, suʼ cha cʼupij bic. Mas ʼutz ca sach awakan, tobal re a cʼaslemal, chuwach i xa cat ʼe pa ʼaʼ chi n-ca chup taj, ruʼ i queb awakan tzʼakat. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.046', 'I amol chi cat qui tij chila n-que cam taj, xak i ʼaʼ mi jun ca chupuwic. ', '', '&lt;span class="verse" id="Mark_9_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;I amol chi cat qui tij chila n-que cam taj, xak i ʼaʼ mi jun ca chupuwic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.047', 'Xak i a baʼwach, we are ca ʼanaw chawe cat tzak chupam a mac, suʼ chawesaj. Mas ʼutz cat oc pa cu ʼat wi tzij i Dios, tupu xa jun a baʼwach, chuwach i xa cat ʼe pa ʼaʼ ruʼ i queb a baʼwach tzʼakat. ', '', '&lt;span class="verse" id="Mark_9_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Xak i a baʼwach, we are ca ʼanaw chawe cat tzak chupam a mac, suʼ chawesaj. Mas ʼutz cat oc pa cu ʼat wi tzij i Dios, tupu xa jun a baʼwach, chuwach i xa cat ʼe pa ʼaʼ ruʼ i queb a baʼwach tzʼakat. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.048', 'I amol chi cat qui tij chila n-que cam taj, xak i ʼaʼ chila mi jun ca chupuwic.
', '', '&lt;span class="verse" id="Mark_9_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;I amol chi cat qui tij chila n-que cam taj, xak i ʼaʼ chila mi jun ca chupuwic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.049', 'Ronojel niʼpa i ca sujux chuwach i Dios, cocsax ʼaʼ che, xak cocsax atzʼam che; xak yix, ʼo u chac pacha cocsax ʼaʼ, pacha cocsax atzʼam chiwe, we ʼutz i yijbaxic chuwach i Dios. ', '', '&lt;span class="verse" id="Mark_9_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;Ronojel niʼpa i ca sujux chuwach i Dios, cocsax ʼaʼ che, xak cocsax atzʼam che; xak yix, ʼo u chac pacha cocsax ʼaʼ, pacha cocsax atzʼam chiwe, we ʼutz i yijbaxic chuwach i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '9.050', 'I atzʼam, lic cu rik u chac chake. Xui-ri, we xsach i u tzayil, ¿wach u ʼonquil ʼuri caka ʼan tan chic tza che? N-taj. Xak ʼo u chac chi yix ʼo i tzayil (pacha jun sipon sujtal chuwach i Dios; queje ile, ix chom na chiwach); chom chix ʼol chiwach.
', '', '&lt;span class="verse" id="Mark_9_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;I atzʼam, lic cu rik u chac chake. Xui-ri, we xsach i u tzayil, ¿wach u ʼonquil ʼuri caka ʼan tan chic tza che? N-taj. Xak ʼo u chac chi yix ʼo i tzayil (pacha jun sipon sujtal chuwach i Dios; queje ile, ix chom na chiwach); chom chix ʼol chiwach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.001', 'Xoʼon panok, i Jesus xel pa tinimit Capernaum, xa ʼe cʼa pa jyub Judéa; xu ʼaxuj i ya Jordan, y xupon che i jyub pa oriénte re i ya. Chila xe moltaj chic uqʼuial winak ruʼ, y ire xoc chic chu cʼutic chique pacha ʼax coʼon wi. ', '', '&lt;h2&gt;Wach xu bij i Jesus chirij u jachic quib i je cʼulanic&lt;/h2&gt;&lt;span class="verse" id="Mark_10_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Jesus xel pa tinimit Capernaum, xa ʼe cʼa pa jyub Judéa; xu ʼaxuj i ya Jordan, y xupon che i jyub pa oriénte re i ya. Chila xe moltaj chic uqʼuial winak ruʼ, y ire xoc chic chu cʼutic chique pacha ʼax coʼon wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.002', 'Are ʼuri, je ʼo jujun aj Fariséo xe upon ruʼ, xa quiqui tzucuj tzaksabal re. Xqui tzʼonoj che we i ʼatbal tzij cu ya che jun, we craj cu jach i rixokil. Queje ile quiqui cʼambej u pam, xqui bij ique. ', '', '&lt;span class="verse" id="Mark_10_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are ʼuri, je ʼo jujun aj Fariséo xe upon ruʼ, xa quiqui tzucuj tzaksabal re. Xqui tzʼonoj che we i ʼatbal tzij cu ya che jun, we craj cu jach i rixokil. Queje ile quiqui cʼambej u pam, xqui bij ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.003', 'Are ʼuri i Jesus xu bij chique:
—¿Wach xu bij i mam Moises ujer chi qui ʼano?
', '', '&lt;span class="verse" id="Mark_10_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wach xu bij i mam Moises ujer chi qui ʼano?&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.004', 'Ique xqui bij:
—I mam Moises u yom can chake chi jun achi xa cu tzʼibaj jun wuj y cu ya che i ixok, jachbal-re —xqui bij che.
', '', '&lt;span class="verse" id="Mark_10_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ique xqui bij:&lt;p&gt; &lt;/p&gt;—I mam Moises u yom can chake chi jun achi xa cu tzʼibaj jun wuj y cu ya che i ixok, jachbal-re —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.005', 'Teʼuri i Jesus xu bij:
—I mam Moises xu ya ile chiwe xa rumal n-quiwaj ta qui ʼan pacha craj i Dios. ', '', '&lt;span class="verse" id="Mark_10_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—I mam Moises xu ya ile chiwe xa rumal n-quiwaj ta qui ʼan pacha craj i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.006', 'Xui-ri chu xebal ʼij sak, i Dios, are xu yijba ronojel u wach i jyub taʼaj, xak xe u ʼan i winak, “jun achi, jun ixok xe u yijba”. (Queje ile cu bij u wuj i Dios.) ', '', '&lt;span class="verse" id="Mark_10_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xui-ri chu xebal ʼij sak, i Dios, are xu yijba ronojel u wach i jyub taʼaj, xak xe u ʼan i winak, “jun achi, jun ixok xe u yijba”. (Queje ile cu bij u wuj i Dios.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.007', '“Rumal ʼuri, jun achi ʼo u chac quel cuʼ u kajaw u chuch, man cacucʼaj quib ruʼ i rixokil. ', '', '&lt;span class="verse" id="Mark_10_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;“Rumal ʼuri, jun achi ʼo u chac quel cuʼ u kajaw u chuch, man cacucʼaj quib ruʼ i rixokil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.008', 'Ique che queb, xa jun chʼaculaj queʼelok.” (Queje ile cu bij u wuj i Dios.) Queʼel ʼuri are i je queb, queʼelok xa je jun. ', '', '&lt;span class="verse" id="Mark_10_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Ique che queb, xa jun chʼaculaj queʼelok.” (Queje ile cu bij u wuj i Dios.) Queʼel ʼuri are i je queb, queʼelok xa je jun. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.009', 'Rumal-i, i winak n-ta piquiʼab quiqui yoj ile chi u yijbam i Dios, chi xa jun u ʼanom chique —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_10_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Rumal-i, i winak n-ta piquiʼab quiqui yoj ile chi u yijbam i Dios, chi xa jun u ʼanom chique —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.010', 'Are xe oc pa ja, i u tijoxelab xqui tzʼonoj jubiʼ chic che, puwi i tzij-le. ', '', '&lt;span class="verse" id="Mark_10_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are xe oc pa ja, i u tijoxelab xqui tzʼonoj jubiʼ chic che, puwi i tzij-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.011', 'Rumal-i, i Jesus xu bij chique:
—Pachin jun cu jach i rixokil, we xqʼuiji ruʼ jun chic, queʼel ʼuri chi ʼo u mac i achi chuwach i Dios, xak chuwach i nabe rixokil. ', '', '&lt;span class="verse" id="Mark_10_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Rumal-i, i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Pachin jun cu jach i rixokil, we xqʼuiji ruʼ jun chic, queʼel ʼuri chi ʼo u mac i achi chuwach i Dios, xak chuwach i nabe rixokil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.012', 'Xak queje jun ixok, we cu jach i rachijil y ca qʼuiji chic ruʼ jun achi, ʼo ni u mac ʼuri chuwach i Dios, xak chuwach i nabe rachijil —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_10_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xak queje jun ixok, we cu jach i rachijil y ca qʼuiji chic ruʼ jun achi, ʼo ni u mac ʼuri chuwach i Dios, xak chuwach i nabe rachijil —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.013', 'Xoʼon panok, je ʼo jujun winak xe qui cʼam li cacʼal ruʼ i Jesus, cu yabej na re u ʼab piquiwi. Xui-ri, i u tijoxelab xqui jek qui ʼatixic i je ucʼawnak i acʼalab. ', '', '&lt;h2&gt;I Jesus que raj i acʼalab; xu ya u ʼab piquiwi&lt;/h2&gt;&lt;span class="verse" id="Mark_10_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xoʼon panok, je ʼo jujun winak xe qui cʼam li cacʼal ruʼ i Jesus, cu yabej na re u ʼab piquiwi. Xui-ri, i u tijoxelab xqui jek qui ʼatixic i je ucʼawnak i acʼalab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.014', 'I Jesus, are xrilo, xpe roywal y xu bij chique:
—Chi ya chique i acʼalab que cʼun wuʼ; me i ʼatij, man ʼo que ique che i ʼatbal tzij re i Dios, xak chique i je pacha acʼalab. ', '', '&lt;span class="verse" id="Mark_10_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I Jesus, are xrilo, xpe roywal y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ya chique i acʼalab que cʼun wuʼ; me i ʼatij, man ʼo que ique che i ʼatbal tzij re i Dios, xak chique i je pacha acʼalab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.015', 'Katzij i quin bij chiwe: pachin jun, we n-cu ya ta che i Dios cu ʼat ni tzij puwi, pacha coʼon jun acʼal, lic n-coc ta ʼuri pa cu ʼat wi tzij i Dios —xu bij i Jesus.
', '', '&lt;span class="verse" id="Mark_10_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe: pachin jun, we n-cu ya ta che i Dios cu ʼat ni tzij puwi, pacha coʼon jun acʼal, lic n-coc ta ʼuri pa cu ʼat wi tzij i Dios —xu bij i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.016', 'Teʼuri xe u tzʼulej i acʼalab; xu ya u ʼab piquiwi conojel, y xu tzʼonoj ʼutz piquiwi.
', '', '&lt;span class="verse" id="Mark_10_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Teʼuri xe u tzʼulej i acʼalab; xu ya u ʼab piquiwi conojel, y xu tzʼonoj ʼutz piquiwi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.017', 'I Jesus xu maj chubi u be; are ʼuri xcʼun jun achi paʼanem y xuqui chuwach. Xu bij che:
—ʼUtz laj Mayes, ¿wach quin ʼan yin, quin rikbej na re in cʼaslemal chi n-tu qʼuisic? —xu bij.
', '', '&lt;h2&gt;Jun achi beyom xu tzʼonoj wach u rikic u cʼaslemal chi n-tu qʼuisic&lt;/h2&gt;&lt;span class="verse" id="Mark_10_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;I Jesus xu maj chubi u be; are ʼuri xcʼun jun achi paʼanem y xuqui chuwach. Xu bij che:&lt;p&gt; &lt;/p&gt;—ʼUtz laj Mayes, ¿wach quin ʼan yin, quin rikbej na re in cʼaslemal chi n-tu qʼuisic? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.018', '—¿Wuchac ca bij “ʼutz” chwe yin? —xu bij i Jesus—. N-ta jun chi ʼutz, xui Dios —xu bij. ', '', '&lt;span class="verse" id="Mark_10_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;—¿Wuchac ca bij “ʼutz” chwe yin? —xu bij i Jesus—. N-ta jun chi ʼutz, xui Dios —xu bij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.019', 'Yet awetaʼam wach cu bij i lajuj u tzij u pixab i Dios: cu bij “Ma min awib ruʼ jun ixok chi n-awixokil taj; mat camsanic; ma ʼan elaʼ; ma bij mentir chirij jun winak; mat tʼoronic; cha cojo qui ʼij a kajaw a chuch.” Ile awetaʼam chic —xu bij i Jesus che i achi.
', '', '&lt;span class="verse" id="Mark_10_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Yet awetaʼam wach cu bij i lajuj u tzij u pixab i Dios: cu bij “Ma min awib ruʼ jun ixok chi n-awixokil taj; mat camsanic; ma ʼan elaʼ; ma bij mentir chirij jun winak; mat tʼoronic; cha cojo qui ʼij a kajaw a chuch.” Ile awetaʼam chic —xu bij i Jesus che i achi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.020', '—Mayes, ronojel ile ʼis quin tijin chu ʼanic; in jekom na lok cʼa are in chʼutin —xu bij i achi che.
', '', '&lt;span class="verse" id="Mark_10_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;—Mayes, ronojel ile ʼis quin tijin chu ʼanic; in jekom na lok cʼa are in chʼutin —xu bij i achi che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.021', 'I Jesus xu maj u takexic i achi; lic cʼax xu naʼo.
—Cʼo ni rajwaxic chawe —xu bij—: niʼpa i ʼo awuʼ, ja cʼayij, ca tobej que i nibaʼib. Ca rik na ʼuri a beyomal chicaj. Teʼuri chat petok y chat terej chwij, tupu cat cam na rumal —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Mark_10_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus xu maj u takexic i achi; lic cʼax xu naʼo.&lt;p&gt; &lt;/p&gt;—Cʼo ni rajwaxic chawe —xu bij—: niʼpa i ʼo awuʼ, ja cʼayij, ca tobej que i nibaʼib. Ca rik na ʼuri a beyomal chicaj. Teʼuri chat petok y chat terej chwij, tupu cat cam na rumal —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.022', 'Are xu ta i achi ile, lic xoc ʼil, y ca bisonic xa ʼec, man ire lic beyom.
', '', '&lt;span class="verse" id="Mark_10_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are xu ta i achi ile, lic xoc ʼil, y ca bisonic xa ʼec, man ire lic beyom.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.023', 'Teʼuri i Jesus cʼula xe u tzu u tijoxelab, y xu bij chique:
—I beyomab, cʼax coquic puʼab i Dios —xu bij.
', '', '&lt;span class="verse" id="Mark_10_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus cʼula xe u tzu u tijoxelab, y xu bij chique:&lt;p&gt; &lt;/p&gt;—I beyomab, cʼax coquic puʼab i Dios —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.024', 'I u tijoxelab, are xqui ta ile, lic xqui bisoj. Are ʼuri i Jesus xu bij chic chique:
—Ix wacʼal, niʼpa i qui cubam qui cʼux che i qui beyomal, cʼax coquic ʼuri pu ʼatbal tzij i Dios. ', '', '&lt;span class="verse" id="Mark_10_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xqui ta ile, lic xqui bisoj. Are ʼuri i Jesus xu bij chic chique:&lt;p&gt; &lt;/p&gt;—Ix wacʼal, niʼpa i qui cubam qui cʼux che i qui beyomal, cʼax coquic ʼuri pu ʼatbal tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.025', 'Cʼax u ʼanic chi jun beyom cu jach rib puʼab i Dios; cʼax ile chuwach i ricʼawic jun camey che i tel re acuxa —xu bij.
', '', '&lt;span class="verse" id="Mark_10_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Cʼax u ʼanic chi jun beyom cu jach rib puʼab i Dios; cʼax ile chuwach i ricʼawic jun camey che i tel re acuxa —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.026', 'I u tijoxelab, are xqui ta ile, lic xqui bisoj. Teʼuri xe oc chu chʼobic chiquiwach:
—¿Pachin quelsax na ʼuri chupam u mac?
', '', '&lt;span class="verse" id="Mark_10_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I u tijoxelab, are xqui ta ile, lic xqui bisoj. Teʼuri xe oc chu chʼobic chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Pachin quelsax na ʼuri chupam u mac?&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.027', 'I Jesus xu maj qui takexic y xu bij chique:
—I winak n-que tiqui ta chu ʼanic ile, xui-ri, i Dios ca tiqui che; ire, ronojel ʼis ca tiqui che —xu bij.
', '', '&lt;span class="verse" id="Mark_10_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I Jesus xu maj qui takexic y xu bij chique:&lt;p&gt; &lt;/p&gt;—I winak n-que tiqui ta chu ʼanic ile, xui-ri, i Dios ca tiqui che; ire, ronojel ʼis ca tiqui che —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.028', 'Are ʼuri i ma Pédro xu bij che:
—Kajwal, yoj, ronojel niʼpa i ʼo kuʼ xka canaj canok y oj terejnak chawij —xu bij.
', '', '&lt;span class="verse" id="Mark_10_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xu bij che:&lt;p&gt; &lt;/p&gt;—Kajwal, yoj, ronojel niʼpa i ʼo kuʼ xka canaj canok y oj terejnak chawij —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.029', 'I Jesus xu bij chique:
—Katzij i quin bij chiwe, pachin croʼtaj can u bitak-re wumal yin, nim na cu chʼac puwi ile. We ʼo jun cu canaj can i rachoch, xak i rulew, xak i ratz u chaʼ, xak u chuch u kajaw, xak i rixokil ralcʼwal, xa wumal yin, xak rumal u paxsaxic u tzij i Dios, ', '', '&lt;span class="verse" id="Mark_10_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, pachin croʼtaj can u bitak-re wumal yin, nim na cu chʼac puwi ile. We ʼo jun cu canaj can i rachoch, xak i rulew, xak i ratz u chaʼ, xak u chuch u kajaw, xak i rixokil ralcʼwal, xa wumal yin, xak rumal u paxsaxic u tzij i Dios, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.030', 'ʼo ni cu chʼac ʼuri woʼor chuwach i jyub taʼaj, xak ʼo ni cu chʼaco coʼon panok. Ca yaʼ na ʼuri jun ciénto che, puwi ile chi xa jun ʼo ruʼ; we are i rachoch xu canaj canok, we are i ratz u chaʼ, we are u chuch, we are i racʼal, we are i rulew xu canaj canok, ca yaʼ na chic jun ciénto puwi; xui-ri, xak cu tij uyej cumal i winak. Péro n-ta coʼono, man coʼon panok, ʼo ni u cʼaslemal chi n-ca qʼuis ta chic. ', '', '&lt;span class="verse" id="Mark_10_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;ʼo ni cu chʼac ʼuri woʼor chuwach i jyub taʼaj, xak ʼo ni cu chʼaco coʼon panok. Ca yaʼ na ʼuri jun ciénto che, puwi ile chi xa jun ʼo ruʼ; we are i rachoch xu canaj canok, we are i ratz u chaʼ, we are u chuch, we are i racʼal, we are i rulew xu canaj canok, ca yaʼ na chic jun ciénto puwi; xui-ri, xak cu tij uyej cumal i winak. Péro n-ta coʼono, man coʼon panok, ʼo ni u cʼaslemal chi n-ca qʼuis ta chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.031', 'Xui-ri, niʼpa i je ʼo pa nabeal, je qʼui na que canaj can pa qʼuisbal-re; xak je qʼui chi woʼor je ʼo pa qʼuisbal-re, are ique que nabeaj na —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_10_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xui-ri, niʼpa i je ʼo pa nabeal, je qʼui na que canaj can pa qʼuisbal-re; xak je qʼui chi woʼor je ʼo pa qʼuisbal-re, are ique que nabeaj na —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.032', 'Je ʼo pa be, ʼelic bi que pa Jerusalen, i Jesus nabe chiquiwach u tijoxelab. Ique lic xqui bisoj che, wach ca tijin chu ʼonquil, chi ca ʼe pa Jerusalen; lic qui xim quib xe terej bic. I Jesus xe u siqʼuij juyac chic i cablajuj u tijoxelab ruʼ. Xoc ʼuri chu bixquil chique wach i cʼax quicʼaw wi.
', '', '&lt;h2&gt;I Jesus, are uroxmul xu bij chic u camic&lt;/h2&gt;&lt;span class="verse" id="Mark_10_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Je ʼo pa be, ʼelic bi que pa Jerusalen, i Jesus nabe chiquiwach u tijoxelab. Ique lic xqui bisoj che, wach ca tijin chu ʼonquil, chi ca ʼe pa Jerusalen; lic qui xim quib xe terej bic. I Jesus xe u siqʼuij juyac chic i cablajuj u tijoxelab ruʼ. Xoc ʼuri chu bixquil chique wach i cʼax quicʼaw wi.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.033', '—Yix iwetaʼam ya xka maj bi ka be pa Jerusalen. Are coj upon chila, yin chi in Achi aj Chicaj, quin jachtaj na piquiʼab i cajʼatzil i sacerdóte, tak piquiʼab i tijonel re i ʼatbal tzij re i mam Moises. Quiqui ʼat na tzij pinwi, y quiqui bij na chi ʼo u chac quin camsaxic, xak quin qui jach na piquiʼab i ne te aj Israel. ', '', '&lt;span class="verse" id="Mark_10_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;—Yix iwetaʼam ya xka maj bi ka be pa Jerusalen. Are coj upon chila, yin chi in Achi aj Chicaj, quin jachtaj na piquiʼab i cajʼatzil i sacerdóte, tak piquiʼab i tijonel re i ʼatbal tzij re i mam Moises. Quiqui ʼat na tzij pinwi, y quiqui bij na chi ʼo u chac quin camsaxic, xak quin qui jach na piquiʼab i ne te aj Israel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.034', 'Quin yoʼyax na, quin qui chʼay na, xak quiqui chubaj qui cʼaxaj chwe, xak quin camsax na. Xui-ri, churox ʼij quin walij che i camic —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_10_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Quin yoʼyax na, quin qui chʼay na, xak quiqui chubaj qui cʼaxaj chwe, xak quin camsax na. Xui-ri, churox ʼij quin walij che i camic —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.035', 'Teʼuri i ma Jacóbo, i ma Wan chi je u cʼojol i mam Zebedéo, xe tejeb ruʼ i Jesus.
—Mayes —xqui bij che—, caka tzʼonoj jun pawor chawe —que cha.
', '', '&lt;h2&gt;Wach i xqui tzʼonoj i ma Jacóbo, i ma Wan&lt;/h2&gt;&lt;span class="verse" id="Mark_10_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Jacóbo, i ma Wan chi je u cʼojol i mam Zebedéo, xe tejeb ruʼ i Jesus.&lt;p&gt; &lt;/p&gt;—Mayes —xqui bij che—, caka tzʼonoj jun pawor chawe —que cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.036', '—¿Wach quiwaj quin ʼan chiwe? —xu bij i Jesus.
', '', '&lt;span class="verse" id="Mark_10_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;—¿Wach quiwaj quin ʼan chiwe? —xu bij i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.037', 'Ique xqui bij che:
—Are at ʼo chic che a ʼatbal tzij, chi nim u ʼij u chomal, cakaj yoj ca ya chake coj qʼuiji ni jujun pa tak a tzal: jun pa wikabim, jun pa moxim —xqui bij che.
', '', '&lt;span class="verse" id="Mark_10_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Ique xqui bij che:&lt;p&gt; &lt;/p&gt;—Are at ʼo chic che a ʼatbal tzij, chi nim u ʼij u chomal, cakaj yoj ca ya chake coj qʼuiji ni jujun pa tak a tzal: jun pa wikabim, jun pa moxim —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.038', '—Yix n-iwetaʼam taj wach qui tzʼonoj —xu bij i Jesus chique—. ¿Quix tiqui yix chu chʼijic i cʼax, pacha i cʼax quin icʼaw wi yin che? ¿Quix tiqui yix chu chʼijic uyej pacha uyej quin icʼaw wi yin? —xu bij chique.
', '', '&lt;span class="verse" id="Mark_10_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;—Yix n-iwetaʼam taj wach qui tzʼonoj —xu bij i Jesus chique—. ¿Quix tiqui yix chu chʼijic i cʼax, pacha i cʼax quin icʼaw wi yin che? ¿Quix tiqui yix chu chʼijic uyej pacha uyej quin icʼaw wi yin? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.039', '—Coj tiquiʼic —xe cha ique.
I Jesus xu bij:
—Katzij, yix quix icʼaw na chupam i cʼax-le; xak qui tij ni uyej-le, pacha quin icʼaw na wi yin. ', '', '&lt;span class="verse" id="Mark_10_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;—Coj tiquiʼic —xe cha ique.&lt;p&gt; &lt;/p&gt;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij, yix quix icʼaw na chupam i cʼax-le; xak qui tij ni uyej-le, pacha quin icʼaw na wi yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.040', 'Xui-ri, n-pinʼab ti yin ʼo wi u bixquil pachin que cubi pin wikabim, xak pin moxim. Ca yaʼ ni-le chique pachin u chʼobom i Dios cu ya chique —xu bij i Jesus.
', '', '&lt;span class="verse" id="Mark_10_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Xui-ri, n-pinʼab ti yin ʼo wi u bixquil pachin que cubi pin wikabim, xak pin moxim. Ca yaʼ ni-le chique pachin u chʼobom i Dios cu ya chique —xu bij i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.041', 'I je lajuj chic u tijoxelab, are xqui ta qui tzij i ma Jacóbo i ma Wan, xpe coywal chiquij. ', '', '&lt;span class="verse" id="Mark_10_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;I je lajuj chic u tijoxelab, are xqui ta qui tzij i ma Jacóbo i ma Wan, xpe coywal chiquij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.042', 'Are ʼuri i Jesus xe u siqʼuij ruʼ y xu bij chique:
—Iwetaʼam yix, niʼpa i je ʼatol tzij piquiwi i tinimit, quiqui ʼan che quib pacha je rajaw piquiwi; xak i winak chi nimak qui patan que rajawin piquiwi i cach winak. ', '', '&lt;span class="verse" id="Mark_10_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xe u siqʼuij ruʼ y xu bij chique:&lt;p&gt; &lt;/p&gt;—Iwetaʼam yix, niʼpa i je ʼatol tzij piquiwi i tinimit, quiqui ʼan che quib pacha je rajaw piquiwi; xak i winak chi nimak qui patan que rajawin piquiwi i cach winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.043', 'Xui-ri, yix n-ix ta queje ile. Are i qui ʼan ni yix, pachin craj queʼelok nim u ʼij, cha chacun ʼuri chiquij i rachiʼil. ', '', '&lt;span class="verse" id="Mark_10_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Xui-ri, yix n-ix ta queje ile. Are i qui ʼan ni yix, pachin craj queʼelok nim u ʼij, cha chacun ʼuri chiquij i rachiʼil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.044', 'Pachin craj cu ya rib chu nabeal, chu ʼana ʼuri che rib pacha mocom chiquij conojel. ', '', '&lt;span class="verse" id="Mark_10_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Pachin craj cu ya rib chu nabeal, chu ʼana ʼuri che rib pacha mocom chiquij conojel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.045', 'Queje chi ʼana ile, man i Achi aj Chicaj mi te ne ire xcʼun chu tzucuxic pachin ca chacun chirij; xcʼun ire pacha mocom chiquij conojel; xcʼun chu yaʼic rib che i camic, tojbal re i quelbal pa cʼax i je qʼui —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_10_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Queje chi ʼana ile, man i Achi aj Chicaj mi te ne ire xcʼun chu tzucuxic pachin ca chacun chirij; xcʼun ire pacha mocom chiquij conojel; xcʼun chu yaʼic rib che i camic, tojbal re i quelbal pa cʼax i je qʼui —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.046', 'Cʼa que tijin qui ʼenam pa Jerusalen, xe icʼaw pa Jerico. Are ca tijin quelic i Jesus cuʼ u tijoxelab, je qʼui i winak je teren chiquij. ʼO jun mawach, ma Bartiméo u bi, chi u cʼojol i ma Timéo; ire cul pa be, ca tijin chu tzʼonoxic u mer chique i winak. ', '', '&lt;h2&gt;I Jesus xu tzunsaj jun mawach, ma Bartiméo u bi&lt;/h2&gt;&lt;span class="verse" id="Mark_10_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Cʼa que tijin qui ʼenam pa Jerusalen, xe icʼaw pa Jerico. Are ca tijin quelic i Jesus cuʼ u tijoxelab, je qʼui i winak je teren chiquij. ʼO jun mawach, ma Bartiméo u bi, chi u cʼojol i ma Timéo; ire cul pa be, ca tijin chu tzʼonoxic u mer chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.047', 'Are xu ta chi are i Jesus aj Nazaret chi ca tijin ricʼawic, ire xsiqʼuin chu bixquil:
—¡Kajwal Jesus! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal in wach! —xu bij.
', '', '&lt;span class="verse" id="Mark_10_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Are xu ta chi are i Jesus aj Nazaret chi ca tijin ricʼawic, ire xsiqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Kajwal Jesus! ¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal in wach! —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.048', 'Je qʼui i winak xe yajawic man cacaj cu mayij siqʼuinic, xui-ri, ire xa xu cowij u bixquil che i Jesus:
—¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal in wach! —xu bij.
', '', '&lt;span class="verse" id="Mark_10_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Je qʼui i winak xe yajawic man cacaj cu mayij siqʼuinic, xui-ri, ire xa xu cowij u bixquil che i Jesus:&lt;p&gt; &lt;/p&gt;—¡Yet chi at umuk uxiquin can i mam David, chawila toʼbal in wach! —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.049', 'I Jesus, are xu ta, xtacʼ canic y xu bij:
—Chi siqʼuij.
Teʼuri xqui siqʼuij i mawach y xqui bij che:
—Cha cowij a cʼux. Chat walijok man ire cat u siqʼuij —xe cha che.
', '', '&lt;span class="verse" id="Mark_10_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;I Jesus, are xu ta, xtacʼ canic y xu bij:&lt;p&gt; &lt;/p&gt;—Chi siqʼuij.&lt;p&gt; &lt;/p&gt;Teʼuri xqui siqʼuij i mawach y xqui bij che:&lt;p&gt; &lt;/p&gt;—Cha cowij a cʼux. Chat walijok man ire cat u siqʼuij —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.050', 'Juntir xu cʼak bi i cʼul u licʼom chirij, y xsacʼlin chicaj. Teʼuri xtejeb pan ruʼ i Jesus. ', '', '&lt;span class="verse" id="Mark_10_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Juntir xu cʼak bi i cʼul u licʼom chirij, y xsacʼlin chicaj. Teʼuri xtejeb pan ruʼ i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.051', 'I Jesus xu tzʼonoj che:
—¿Wach cawaj quin ʼan chawe?
I mawach xu bij che:
—Mayes, cwaj yin quin a tzunsaj —coʼono.
', '', '&lt;span class="verse" id="Mark_10_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;I Jesus xu tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wach cawaj quin ʼan chawe?&lt;p&gt; &lt;/p&gt;I mawach xu bij che:&lt;p&gt; &lt;/p&gt;—Mayes, cwaj yin quin a tzunsaj —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '10.052', 'I Jesus xu bij che:
—ʼUtz cat ʼec; ya xat utziric, man xa cuba a cʼux chwe —xu bij.
I mawach juntir xtzunic, y xterej bi chirij i Jesus pa be.
', '', '&lt;span class="verse" id="Mark_10_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—ʼUtz cat ʼec; ya xat utziric, man xa cuba a cʼux chwe —xu bij.&lt;p&gt; &lt;/p&gt;I mawach juntir xtzunic, y xterej bi chirij i Jesus pa be.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.001', 'Ique, ya nakaj chic je ʼo wi pa Jerusalen; ya xe upon che i queb aldéa Betfage y Betánia, chi je ʼo chuwach i ral jyub Olívos. Are ʼuri i Jesus xe u siqʼuij queb u tijoxelab ruʼ y xu bij chique:
', '', '&lt;h2&gt;Wach u cojic u ʼij i Jesus are xoc pa Jerusalen&lt;/h2&gt;&lt;span class="verse" id="Mark_11_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Ique, ya nakaj chic je ʼo wi pa Jerusalen; ya xe upon che i queb aldéa Betfage y Betánia, chi je ʼo chuwach i ral jyub Olívos. Are ʼuri i Jesus xe u siqʼuij queb u tijoxelab ruʼ y xu bij chique:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.002', '—Jix che i aldéa-le chi ʼo chakawach; are quix uponic, qui rik ni jun buru yukul chila, chi cʼa maja cretamaj ca ʼan winak chirij. Chi toro y chi cʼama lok. ', '', '&lt;span class="verse" id="Mark_11_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;—Jix che i aldéa-le chi ʼo chakawach; are quix uponic, qui rik ni jun buru yukul chila, chi cʼa maja cretamaj ca ʼan winak chirij. Chi toro y chi cʼama lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.003', 'We ʼo jun ca tzʼonow chiwe wuchac quix tijin chu toric, chi bij che chi cu rik u chac che i Kajwal y weʼ col u ya chic, chix chok che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_11_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;We ʼo jun ca tzʼonow chiwe wuchac quix tijin chu toric, chi bij che chi cu rik u chac che i Kajwal y weʼ col u ya chic, chix chok che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.004', 'Ique xe ʼec, xe qui rika i buru yukul pa be chuwach jun ja, y xe oc chu quiric. ', '', '&lt;span class="verse" id="Mark_11_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Ique xe ʼec, xe qui rika i buru yukul pa be chuwach jun ja, y xe oc chu quiric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.005', 'Je ʼo jujun xe ʼilawic, y xqui bij chique:
—¿Wach quix tijin chu ʼanic? ¿Wuchac qui tor i buru? —xe cha.
', '', '&lt;span class="verse" id="Mark_11_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Je ʼo jujun xe ʼilawic, y xqui bij chique:&lt;p&gt; &lt;/p&gt;—¿Wach quix tijin chu ʼanic? ¿Wuchac qui tor i buru? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.006', 'Are ʼuri xqui bij chique pacha u bim bi Jesus chique ique; teʼuri xyaʼ li chique chi xqui cʼam li buru che i Jesus. ', '', '&lt;span class="verse" id="Mark_11_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are ʼuri xqui bij chique pacha u bim bi Jesus chique ique; teʼuri xyaʼ li chique chi xqui cʼam li buru che i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.007', 'Are xe uponic, xqui coj qui ʼuʼ chirij, teʼuri xa ʼan i Jesus chirij. ', '', '&lt;span class="verse" id="Mark_11_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Are xe uponic, xqui coj qui ʼuʼ chirij, teʼuri xa ʼan i Jesus chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.008', 'Je qʼui i winak xqui rip qui ʼuʼ pa be, xak je qʼui chic xqui cʼatzij u xak cheʼ y xqui coj pa be, wikbal-re.
', '', '&lt;span class="verse" id="Mark_11_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Je qʼui i winak xqui rip qui ʼuʼ pa be, xak je qʼui chic xqui cʼatzij u xak cheʼ y xqui coj pa be, wikbal-re.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.009', 'Je qʼui i winak je nabe chuwach i Jesus, xak je qʼui je teren li chirij; conojel xe siqʼuin chu bixquil:
—¡Hosánna! (¡Nim u ʼij i Dios!) —xe cha—. ¡Chu ya ni ʼutz i Dios puwi i Jun-i chi u takom li kuʼ! ', '', '&lt;span class="verse" id="Mark_11_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Je qʼui i winak je nabe chuwach i Jesus, xak je qʼui je teren li chirij; conojel xe siqʼuin chu bixquil:&lt;p&gt; &lt;/p&gt;—¡Hosánna! (¡Nim u ʼij i Dios!) —xe cha—. ¡Chu ya ni ʼutz i Dios puwi i Jun-i chi u takom li kuʼ! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.010', '¡Cʼamori che i Dios! Ya ca jekan u ʼatbal tzij chi coʼon ni Jun-i, chi u muk u xiquin can i ka mam, mam David. ¡Chocsax u ʼij i Dios! (Queje ile xe siqʼuin chu bixquil.)
', '', '&lt;span class="verse" id="Mark_11_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;¡Cʼamori che i Dios! Ya ca jekan u ʼatbal tzij chi coʼon ni Jun-i, chi u muk u xiquin can i ka mam, mam David. ¡Chocsax u ʼij i Dios! (Queje ile xe siqʼuin chu bixquil.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.011', 'Queje ile i roquic i Jesus pa Jerusalen. Are ʼuri xoc ire pa rachoch i Dios chila. Are xoc paja, cʼula xtzunic, y xtzalij chalok man ya xkaj i ʼij. Teʼuri xa ʼe chic pa Betánia cuʼ i cablajuj u tijoxelab.
', '', '&lt;span class="verse" id="Mark_11_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Queje ile i roquic i Jesus pa Jerusalen. Are ʼuri xoc ire pa rachoch i Dios chila. Are xoc paja, cʼula xtzunic, y xtzalij chalok man ya xkaj i ʼij. Teʼuri xa ʼe chic pa Betánia cuʼ i cablajuj u tijoxelab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.012', 'Chucab ʼij, are xe tzalij chali pa Betánia, i Jesus xpe numic che. ', '', '&lt;h2&gt;Wach xu bij i Jesus che i cheʼ chi n-tu wach&lt;/h2&gt;&lt;span class="verse" id="Mark_11_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Chucab ʼij, are xe tzalij chali pa Betánia, i Jesus xpe numic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.013', 'Are ʼuri xril jun u cheʼlal hígo. Tupu cʼa maja cu rik i tiémpo ca wachinic, chi naj panok xrilo chi chom chic u xak; rumal-i, xa ʼe i Jesus chi rilic we ʼo chic echaʼ che. Xui-ri, ile n-ta echaʼ che, toʼ u xak ʼolic. ', '', '&lt;span class="verse" id="Mark_11_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Are ʼuri xril jun u cheʼlal hígo. Tupu cʼa maja cu rik i tiémpo ca wachinic, chi naj panok xrilo chi chom chic u xak; rumal-i, xa ʼe i Jesus chi rilic we ʼo chic echaʼ che. Xui-ri, ile n-ta echaʼ che, toʼ u xak ʼolic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.014', 'Rumal-i xu bij i Jesus che:
—Queʼe ʼij chawij, mi jun col u tija chic jun rechaʼ chawe —xu bij che i cheʼ.
I u tijoxelab xak xqui ta wach xu bij.
', '', '&lt;span class="verse" id="Mark_11_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Rumal-i xu bij i Jesus che:&lt;p&gt; &lt;/p&gt;—Queʼe ʼij chawij, mi jun col u tija chic jun rechaʼ chawe —xu bij che i cheʼ.&lt;p&gt; &lt;/p&gt;I u tijoxelab xak xqui ta wach xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.015', 'Teʼuri xe upon pa Jerusalen. I Jesus, are xoc pa rachoch i Dios, xoc li chi quelsaxic niʼpa i aj cʼayinel xak i aj loʼonel je ʼo xe ja. Xe u balʼatij qui mexa i je aj jalwachil u wach qui mer i winak. Xak queje xoʼon che i qui tem i je aj cʼay palomax. ', '', '&lt;h2&gt;Wach i quelsaxic i aj cʼayinel pa rachoch i Dios&lt;/h2&gt;&lt;span class="verse" id="Mark_11_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Teʼuri xe upon pa Jerusalen. I Jesus, are xoc pa rachoch i Dios, xoc li chi quelsaxic niʼpa i aj cʼayinel xak i aj loʼonel je ʼo xe ja. Xe u balʼatij qui mexa i je aj jalwachil u wach qui mer i winak. Xak queje xoʼon che i qui tem i je aj cʼay palomax. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.016', 'Xak xe u ʼatij i winak chi n-cacaj ta quiqui sutij i qui be chirij i rachoch i Dios; xa que icʼaw chu nicʼajal uwaja ruʼ wach tak cucʼam. ', '', '&lt;span class="verse" id="Mark_11_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xak xe u ʼatij i winak chi n-cacaj ta quiqui sutij i qui be chirij i rachoch i Dios; xa que icʼaw chu nicʼajal uwaja ruʼ wach tak cucʼam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.017', 'Teʼuri xoc chu cʼutic chique i winak; xu bij:
—I Dios, chupam u wuj u bim can i tzij-i: “I wachoch, lic jun ja re qui tzʼonbal i winak chwe, i winak aj pa tak tinimit re ronojel u wach i jyub taʼaj.” Queje ile u bim i Dios, xui-ri, yix i ʼanom qui jul iliʼomab che —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_11_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Teʼuri xoc chu cʼutic chique i winak; xu bij:&lt;p&gt; &lt;/p&gt;—I Dios, chupam u wuj u bim can i tzij-i: “I wachoch, lic jun ja re qui tzʼonbal i winak chwe, i winak aj pa tak tinimit re ronojel u wach i jyub taʼaj.” Queje ile u bim i Dios, xui-ri, yix i ʼanom qui jul iliʼomab che —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.018', 'I cajʼatzil i sacerdóte xqui ta wach u ʼanom, xak xqui ta rason i je tijonel re i ʼatbal tzij re i mam Moises. Xe oc ʼuri chu tzucuxic wach u camsaxic quiqui ʼano. Queje ile xqui ʼano man xqui xij quib che, man conojel i winak xqui bisoj che, wach i cʼutunic ca tijin chu ʼonquil. ', '', '&lt;span class="verse" id="Mark_11_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I cajʼatzil i sacerdóte xqui ta wach u ʼanom, xak xqui ta rason i je tijonel re i ʼatbal tzij re i mam Moises. Xe oc ʼuri chu tzucuxic wach u camsaxic quiqui ʼano. Queje ile xqui ʼano man xqui xij quib che, man conojel i winak xqui bisoj che, wach i cʼutunic ca tijin chu ʼonquil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.019', 'I Jesus, are xoc aʼab, xel pa tinimit junam cuʼ u tijoxelab.
', '', '&lt;span class="verse" id="Mark_11_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Jesus, are xoc aʼab, xel pa tinimit junam cuʼ u tijoxelab.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.020', 'Sakir chucab ʼij xe ʼe chic pa Jerusalen. Are que tijin chi be, xe icʼaw pa ʼo wi i u cheʼlal hígo. Xquilo chi lic chakijnak chic i cheʼ, camnak chic i raʼ. ', '', '&lt;h2&gt;Xchakij i u cheʼlal hígo&lt;/h2&gt;&lt;span class="verse" id="Mark_11_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Sakir chucab ʼij xe ʼe chic pa Jerusalen. Are que tijin chi be, xe icʼaw pa ʼo wi i u cheʼlal hígo. Xquilo chi lic chakijnak chic i cheʼ, camnak chic i raʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.021', 'Are ʼuri i ma Pédro xcʼun chu cʼux wach u bim can i Jesus che i cheʼ-le, y xu bij che i Jesus:
—Mayes, wilapeʼ. I cheʼ chi xa cʼokoj, ya chakij chic —xu bij che.
', '', '&lt;span class="verse" id="Mark_11_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Are ʼuri i ma Pédro xcʼun chu cʼux wach u bim can i Jesus che i cheʼ-le, y xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—Mayes, wilapeʼ. I cheʼ chi xa cʼokoj, ya chakij chic —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.022', 'I Jesus xu bij:
—Pachin jun ʼo u cubibal u cʼux che i Dios, ʼis ca tiqui chu ʼonquil. ', '', '&lt;span class="verse" id="Mark_11_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Pachin jun ʼo u cubibal u cʼux che i Dios, ʼis ca tiqui chu ʼonquil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.023', 'Katzij i quin bij chiwe, we ʼo jun lic cul u cʼux che i Dios, we cu bij na che i nim laj jyub-i, “Chat el na chi, ja cʼaka awib pa mar”, cu bij che, i Dios coʼon na ʼuri ile che. Xui-ri, i jun, ʼo u chac lic n-ta cu bij u cʼux; ʼo u chac lic cul u cʼux che chi cril na pacha i u bim che. ', '', '&lt;span class="verse" id="Mark_11_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, we ʼo jun lic cul u cʼux che i Dios, we cu bij na che i nim laj jyub-i, “Chat el na chi, ja cʼaka awib pa mar”, cu bij che, i Dios coʼon na ʼuri ile che. Xui-ri, i jun, ʼo u chac lic n-ta cu bij u cʼux; ʼo u chac lic cul u cʼux che chi cril na pacha i u bim che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.024', 'Rumal-i, ʼo jun tzij quin bij na chiwe: ronojel niʼpa i qui tzʼonoj che i Dios, we lic cul i cʼux che chi yatal chic chiwe, ile lic quiwil na. ', '', '&lt;span class="verse" id="Mark_11_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Rumal-i, ʼo jun tzij quin bij na chiwe: ronojel niʼpa i qui tzʼonoj che i Dios, we lic cul i cʼux che chi yatal chic chiwe, ile lic quiwil na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.025', 'Are quix tijin chu tzʼonoxic che i Dios, we ʼo jun u ʼanom mal chiwe, chi cuyu ni u mac che, man i Ta chicaj xak cu cuy ni i mac yix. ', '', '&lt;span class="verse" id="Mark_11_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are quix tijin chu tzʼonoxic che i Dios, we ʼo jun u ʼanom mal chiwe, chi cuyu ni u mac che, man i Ta chicaj xak cu cuy ni i mac yix. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.026', 'Man yix, we n-qui cuy ti qui mac i juban, xak i Ta chicaj xak n-cu cuy ta i mac yix —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_11_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Man yix, we n-qui cuy ti qui mac i juban, xak i Ta chicaj xak n-cu cuy ta i mac yix —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.027', 'Teʼuri xe oc chic pa Jerusalen y xe oc pa rachoch i Dios. Are je ʼo chila, xe tejeb pan i cajʼatzil i sacerdóte ruʼ i Jesus, xak i je tijonel re i ujer ʼatbal tzij, xak i nimak tak mamʼib je ʼo cuʼ. ', '', '&lt;h2&gt;I nimak tak mamʼib xqui tzʼonoj che i Jesus pachin yawnak puʼab wach tak i cu ʼano&lt;/h2&gt;&lt;span class="verse" id="Mark_11_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Teʼuri xe oc chic pa Jerusalen y xe oc pa rachoch i Dios. Are je ʼo chila, xe tejeb pan i cajʼatzil i sacerdóte ruʼ i Jesus, xak i je tijonel re i ujer ʼatbal tzij, xak i nimak tak mamʼib je ʼo cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.028', 'Ique xqui tzʼonoj che:
—¿Bi chi ʼatbal tzijal ʼo paʼab chu ʼanic niʼpa ile? ¿Pachin xyaw ile chawe chi cat tijin chu ʼanic-le? —xqui bij che.
', '', '&lt;span class="verse" id="Mark_11_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Ique xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Bi chi ʼatbal tzijal ʼo paʼab chu ʼanic niʼpa ile? ¿Pachin xyaw ile chawe chi cat tijin chu ʼanic-le? —xqui bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.029', 'I Jesus xu laʼ u wach chique:
—Xak yin quin tzʼonoj jun tzij chiwe yix; we qui bij usucʼ chwe, teʼuri xak quin bij yin chiwe bi chi ʼatbal tzijal ʼo pinʼab. ', '', '&lt;span class="verse" id="Mark_11_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Xak yin quin tzʼonoj jun tzij chiwe yix; we qui bij usucʼ chwe, teʼuri xak quin bij yin chiwe bi chi ʼatbal tzijal ʼo pinʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.030', 'Chi bij chwe pachin xtakaw li ma Wan chu kajsaxic ya piquiwi i winak. ¿Are i Dios xtakaw lok? ¿O xa winak? chi bij ʼu chwe —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_11_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Chi bij chwe pachin xtakaw li ma Wan chu kajsaxic ya piquiwi i winak. ¿Are i Dios xtakaw lok? ¿O xa winak? chi bij ʼu chwe —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.031', 'Teʼuri ique xe oc chu chapic quib chiquiwach puwi i tzij. Xqui bij chiquiwach:
—Woʼor we caka bij chi Dios takawnak lok, ire cu bij ʼuri chake: “¿Wuchac ʼuri n-xi coj taj?” cu bij na chake. ', '', '&lt;span class="verse" id="Mark_11_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri ique xe oc chu chapic quib chiquiwach puwi i tzij. Xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—Woʼor we caka bij chi Dios takawnak lok, ire cu bij ʼuri chake: “¿Wuchac ʼuri n-xi coj taj?” cu bij na chake. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.032', '¿O xataba caka bij che chi winak takawnak lok? —xqui bij chiquiwach.
Queje ile xqui bij, man ique quiqui xij quib chique i winak; conojel i winak cul qui cʼux che chi ma Wan lic ajbil u tzij i Dios. ', '', '&lt;span class="verse" id="Mark_11_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;¿O xataba caka bij che chi winak takawnak lok? —xqui bij chiquiwach.&lt;p&gt; &lt;/p&gt;Queje ile xqui bij, man ique quiqui xij quib chique i winak; conojel i winak cul qui cʼux che chi ma Wan lic ajbil u tzij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '11.033', 'Rumal-i, xqui bij che i Jesus:
—N-ketaʼam taj —xe cha che.
Rumal-i, i Jesus xu laʼ u wach chique:
—Xak queje yin, n-quin bij ta chiwe wach i ʼatbal tzij ʼo pinʼab, chi quin ʼan tak iri —xu bij chique.
', '', '&lt;span class="verse" id="Mark_11_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Rumal-i, xqui bij che i Jesus:&lt;p&gt; &lt;/p&gt;—N-ketaʼam taj —xe cha che.&lt;p&gt; &lt;/p&gt;Rumal-i, i Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Xak queje yin, n-quin bij ta chiwe wach i ʼatbal tzij ʼo pinʼab, chi quin ʼan tak iri —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.001', 'Teʼuri i Jesus xu jek u bixquil i tijojbal tak tzij chique. Xu bij:
—ʼO jun achi xu tic juban u ticon, úva ca bixic, y xu coj coral chirij. Xu yijba pa quiqui yitzʼ wi u waʼal i úva, xak xu yijba jun ral ja tacʼtic chupam i ticon, chajbal re ronojel.
Teʼuri xe u coj jujun ajchaquib, je aj a média ruʼ, y xa ʼe ire naj che jun tinimit. ', '', '&lt;h2&gt;I tijojbal tzij re i mal laj tak ajchaquib&lt;/h2&gt;&lt;span class="verse" id="Mark_12_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu jek u bixquil i tijojbal tak tzij chique. Xu bij:&lt;p&gt; &lt;/p&gt;—ʼO jun achi xu tic juban u ticon, úva ca bixic, y xu coj coral chirij. Xu yijba pa quiqui yitzʼ wi u waʼal i úva, xak xu yijba jun ral ja tacʼtic chupam i ticon, chajbal re ronojel.&lt;p&gt; &lt;/p&gt;Teʼuri xe u coj jujun ajchaquib, je aj a média ruʼ, y xa ʼe ire naj che jun tinimit. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.002', 'Are xu rik i ʼij re i cosech, xu tak li jun u mocom chu tzʼonoxic i re ire chique i raj mediant. ', '', '&lt;span class="verse" id="Mark_12_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Are xu rik i ʼij re i cosech, xu tak li jun u mocom chu tzʼonoxic i re ire chique i raj mediant. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.003', 'Are xuponic, ique xqui chapo, xqui chʼayo, y xqui tzalijsaj chubic, n-ta xqui ya bi che. ', '', '&lt;span class="verse" id="Mark_12_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are xuponic, ique xqui chapo, xqui chʼayo, y xqui tzalijsaj chubic, n-ta xqui ya bi che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.004', 'Teʼuri i rajaw xu tak chal jun chic u mocom. Are xupon ire, xqui coj abaj che, xqui soc u jolom, y xqui yoʼya. ', '', '&lt;span class="verse" id="Mark_12_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Teʼuri i rajaw xu tak chal jun chic u mocom. Are xupon ire, xqui coj abaj che, xqui soc u jolom, y xqui yoʼya. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.005', 'I rajaw xu tak chal jun chic u mocom; ire lic xqui camsaj. Teʼuri xe u tak chal juban chic, je qʼui. ʼO jujun chique xqui ʼan cʼax chique; xak ʼo i xe qui camsaj.
', '', '&lt;span class="verse" id="Mark_12_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I rajaw xu tak chal jun chic u mocom; ire lic xqui camsaj. Teʼuri xe u tak chal juban chic, je qʼui. ʼO jujun chique xqui ʼan cʼax chique; xak ʼo i xe qui camsaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.006', 'Xeʼelok, n-ta chic que u tako, xui-ri cʼo jun, ile are u cʼojol chi lic cʼax cu naʼo. Xu bij ire, “Woʼor quin tak bi wacʼal cuʼ; que cojon na ʼuri che in cʼojol”, xu bij; y pa qʼuisbal-re, xu tak bi cuʼ. ', '', '&lt;span class="verse" id="Mark_12_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Xeʼelok, n-ta chic que u tako, xui-ri cʼo jun, ile are u cʼojol chi lic cʼax cu naʼo. Xu bij ire, “Woʼor quin tak bi wacʼal cuʼ; que cojon na ʼuri che in cʼojol”, xu bij; y pa qʼuisbal-re, xu tak bi cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.007', 'Xui-ri, are xquil u cʼojol i rajaw ulew, xqui bij chiquiwach: “Ire-le are i rajaw ulew coʼon panok”, xe cha. “Chaka camsaj, y yoj coj canajic oj rajaw”, xe cha. ', '', '&lt;span class="verse" id="Mark_12_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Xui-ri, are xquil u cʼojol i rajaw ulew, xqui bij chiquiwach: “Ire-le are i rajaw ulew coʼon panok”, xe cha. “Chaka camsaj, y yoj coj canajic oj rajaw”, xe cha. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.008', 'Are ʼuri xqui chapo, y xqui camsaj, y xqui cʼak bi u chʼacul rij coral —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_12_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are ʼuri xqui chapo, y xqui camsaj, y xqui cʼak bi u chʼacul rij coral —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.009', 'Teʼuri xu tzʼonoj chique:
—¿Wach coʼon i rajaw ulew ʼuri? Ire ca cʼunic, y que u camsaj i ajchaquib chi je ʼo chuwach ulew; teʼuri cu ya i ulew piquiʼab juban chic.
', '', '&lt;span class="verse" id="Mark_12_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Teʼuri xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Wach coʼon i rajaw ulew ʼuri? Ire ca cʼunic, y que u camsaj i ajchaquib chi je ʼo chuwach ulew; teʼuri cu ya i ulew piquiʼab juban chic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.010', '¿Xataba n-iwilom taj wach i tzʼibtal can chupam u wuj i Dios? Queje iri tzʼibtalic:
I abaj chi n-xcaj ti je aj ʼanol cimiénto re ja,
are i abaj-le chi xeʼelok lic nim ni u chac.
', '', '&lt;span class="verse" id="Mark_12_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;¿Xataba n-iwilom taj wach i tzʼibtal can chupam u wuj i Dios? Queje iri tzʼibtalic:
I abaj chi n-xcaj ti je aj ʼanol cimiénto re ja,
are i abaj-le chi xeʼelok lic nim ni u chac.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.011', 'Are i xeʼelok-le, are i Kajwal Dios xa ʼanawic,
y cakil yoj, lic ʼutz ile chi u ʼanom ire.
Queje ile tzʼibtal canok —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_12_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Are i xeʼelok-le, are i Kajwal Dios xa ʼanawic,
y cakil yoj, lic ʼutz ile chi u ʼanom ire.
Queje ile tzʼibtal canok —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.012', 'I nimak tak mamʼib, are xqui ta ile, laj xqui cʼam bi pa cárcel, man xqui ta ique chi u tzij i Jesus-le lic cojbal qui mac ique. Xui-ri, xqui xij quib chique i winak, rumal-i, xqui canaj canok, y xe ʼec.
', '', '&lt;span class="verse" id="Mark_12_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I nimak tak mamʼib, are xqui ta ile, laj xqui cʼam bi pa cárcel, man xqui ta ique chi u tzij i Jesus-le lic cojbal qui mac ique. Xui-ri, xqui xij quib chique i winak, rumal-i, xqui canaj canok, y xe ʼec.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.013', 'Xoʼon panok, ʼo jujun aj Fariséo, xak jujun aj u partído i ma Heródes, je taktal bi ruʼ i Jesus, que qui cʼambej u pam, tzaksabal re cacaj ique. ', '', '&lt;h2&gt;I aj Fariséo cacaj quiqui cʼam u pam i Jesus we ʼutz quiqui toj impuésto&lt;/h2&gt;&lt;span class="verse" id="Mark_12_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Xoʼon panok, ʼo jujun aj Fariséo, xak jujun aj u partído i ma Heródes, je taktal bi ruʼ i Jesus, que qui cʼambej u pam, tzaksabal re cacaj ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.014', 'Are xe upon ruʼ i Jesus, xqui bij i tzij-i che:
—Mayes, ketaʼam chi yet, wach i ca bij, lic sak laj tzij, man yet lic n-ca ʼan ta cas che, wach quiqui bij i winak chawij. Lic n-ta awe che, wach u patan jun winak, we nim, o we n-nim taj, lic ca bij i sak laj tzij che wach craj i Dios. Cha bij ʼu chake, ¿ʼutz nawi caka toj impuésto che i ʼatol tzij pa Róma, o n-ʼus taj? I ujer ʼatbal tzij, ¿cu ya nawi chake caka tojo, o n-taj? —xe cha.
', '', '&lt;span class="verse" id="Mark_12_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Are xe upon ruʼ i Jesus, xqui bij i tzij-i che:&lt;p&gt; &lt;/p&gt;—Mayes, ketaʼam chi yet, wach i ca bij, lic sak laj tzij, man yet lic n-ca ʼan ta cas che, wach quiqui bij i winak chawij. Lic n-ta awe che, wach u patan jun winak, we nim, o we n-nim taj, lic ca bij i sak laj tzij che wach craj i Dios. Cha bij ʼu chake, ¿ʼutz nawi caka toj impuésto che i ʼatol tzij pa Róma, o n-ʼus taj? I ujer ʼatbal tzij, ¿cu ya nawi chake caka tojo, o n-taj? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.015', 'Péro i Jesus retaʼam chi xa cʼambal u pam quiqui ʼano. Xu bij chique:
—¿Wuchas qui tzucuj tzaksabal we? —xu bij—. Chi cʼama li jun sak pwak, y chi cʼutu chwe —coʼono.
', '', '&lt;span class="verse" id="Mark_12_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Péro i Jesus retaʼam chi xa cʼambal u pam quiqui ʼano. Xu bij chique:&lt;p&gt; &lt;/p&gt;—¿Wuchas qui tzucuj tzaksabal we? —xu bij—. Chi cʼama li jun sak pwak, y chi cʼutu chwe —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.016', 'Xqui cʼam lok y xqui cʼut che. Are xrilo, i Jesus xu tzʼonoj chique:
—¿Pachin u qʼuexwach iri ʼo chuwach? ¿Pachin u bi iri tzʼibtal chuwach? —xu bij.
—Re i ʼatol tzij —xqui bij.
', '', '&lt;span class="verse" id="Mark_12_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xqui cʼam lok y xqui cʼut che. Are xrilo, i Jesus xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Pachin u qʼuexwach iri ʼo chuwach? ¿Pachin u bi iri tzʼibtal chuwach? —xu bij.&lt;p&gt; &lt;/p&gt;—Re i ʼatol tzij —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.017', 'Are ʼuri xu bij i Jesus:
—Chi ya che i ʼatol tzij wach ʼo re ire che; xak chi ya che i Dios wach ʼo re ire che —xu bij chique.
Ique lic xqui bisoj che, wach i xu bij.
', '', '&lt;span class="verse" id="Mark_12_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij i Jesus:&lt;p&gt; &lt;/p&gt;—Chi ya che i ʼatol tzij wach ʼo re ire che; xak chi ya che i Dios wach ʼo re ire che —xu bij chique.&lt;p&gt; &lt;/p&gt;Ique lic xqui bisoj che, wach i xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.018', 'Xoʼon panok, xe upon juban aj Saducéo ruʼ i Jesus. I aj Saducéo, ique quiqui bij chi i camnak n-que walij ta chic. Rumal-i xqui bij i tzij-i che i Jesus:
', '', '&lt;h2&gt;I aj Saducéo xcaj u cʼamic u pam i Jesus che i qui walijbal i camnak&lt;/h2&gt;&lt;span class="verse" id="Mark_12_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xe upon juban aj Saducéo ruʼ i Jesus. I aj Saducéo, ique quiqui bij chi i camnak n-que walij ta chic. Rumal-i xqui bij i tzij-i che i Jesus:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.019', '—Tijonel, u tzʼibam can i mam Moises, we ʼo jun achi xcamic, we ʼo can rixokil chi n-ta racʼal ruʼ, we je ʼo can ratz u chaʼ i achi, ʼo junok chique, ʼutz ca cʼuli ruʼ i ixok malcan man ca qʼuiji racʼal ruʼ, pu qʼuexel i achi xcamic. Queje ile u tzʼibam can i mam Moises. ', '', '&lt;span class="verse" id="Mark_12_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;—Tijonel, u tzʼibam can i mam Moises, we ʼo jun achi xcamic, we ʼo can rixokil chi n-ta racʼal ruʼ, we je ʼo can ratz u chaʼ i achi, ʼo junok chique, ʼutz ca cʼuli ruʼ i ixok malcan man ca qʼuiji racʼal ruʼ, pu qʼuexel i achi xcamic. Queje ile u tzʼibam can i mam Moises. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.020', 'Bay, oʼonom-lok, je ʼo wukub achiab catz qui chaʼ quib. Are i ʼatz xcʼuli nabe, y xcamic, n-ta racʼal xqʼuiji canok. ', '', '&lt;span class="verse" id="Mark_12_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Bay, oʼonom-lok, je ʼo wukub achiab catz qui chaʼ quib. Are i ʼatz xcʼuli nabe, y xcamic, n-ta racʼal xqʼuiji canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.021', 'I ucab chaʼaxel xcʼuli chic ruʼ ixok malcan, y xak xcamic, xak n-ta racʼal xu canaj can ruʼ ixok. Xak queje xoʼon ruʼ urox chaʼaxel. ', '', '&lt;span class="verse" id="Mark_12_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I ucab chaʼaxel xcʼuli chic ruʼ ixok malcan, y xak xcamic, xak n-ta racʼal xu canaj can ruʼ ixok. Xak queje xoʼon ruʼ urox chaʼaxel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.022', 'Queje xqui ʼan ile conojel je wukub, ʼis xe cʼuli ruʼ i ixok-le; n-ta jun cacʼal xqʼuiji ruʼ i ixok malcan. Pa qʼuisbal-re xcam i ixok. ', '', '&lt;span class="verse" id="Mark_12_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Queje xqui ʼan ile conojel je wukub, ʼis xe cʼuli ruʼ i ixok-le; n-ta jun cacʼal xqʼuiji ruʼ i ixok malcan. Pa qʼuisbal-re xcam i ixok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.023', 'Bay, are cu rik i ʼij piqui walijbal i camnak, are que walij niʼpa i camnak, ¿pachinok chique i wukub-le chi mer rachijil i ixok? Man conojel i je wukub-le xe cʼuli ruʼ —xe cha.
', '', '&lt;span class="verse" id="Mark_12_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Bay, are cu rik i ʼij piqui walijbal i camnak, are que walij niʼpa i camnak, ¿pachinok chique i wukub-le chi mer rachijil i ixok? Man conojel i je wukub-le xe cʼuli ruʼ —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.024', 'Teʼuri i Jesus xu laʼ u wach chique:
—Yix xa ix sachnak na. Ix sachnak, man n-iwetaʼam taj wach i tzʼibtal can chupam u wuj i Dios, xak n-iwetaʼam taj wach i nim laj u choʼab i Dios. ', '', '&lt;span class="verse" id="Mark_12_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu laʼ u wach chique:&lt;p&gt; &lt;/p&gt;—Yix xa ix sachnak na. Ix sachnak, man n-iwetaʼam taj wach i tzʼibtal can chupam u wuj i Dios, xak n-iwetaʼam taj wach i nim laj u choʼab i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.025', 'I camnak, are que walij na, queʼelok je pacha i ángel chi je ʼo chicaj; n-que cʼuli taj. I achiab, i ixokib n-que cʼuli ta chic. ', '', '&lt;span class="verse" id="Mark_12_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;I camnak, are que walij na, queʼelok je pacha i ángel chi je ʼo chicaj; n-que cʼuli taj. I achiab, i ixokib n-que cʼuli ta chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.026', 'I qui walijbal i camnak, katzij ʼolic. ¿Xataba n-iwilom ti yix chupam i wuj chi u tzʼibam can i mam Moises wach xu bij i Dios che? I Dios, are xu ʼijla i mam Moises chupam i ʼaʼ chi ca juluw che i ral cheʼ, xu bij che: “Yin cʼa in u Dios i mam Abraham, xak i mam Isaac, xak i mam Jacob”, xu bij che. I oxib-le je camnak chic are xu bij i Dios ile. Bay, chuwach i Dios, cʼa je cʼas na; ', '', '&lt;span class="verse" id="Mark_12_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;I qui walijbal i camnak, katzij ʼolic. ¿Xataba n-iwilom ti yix chupam i wuj chi u tzʼibam can i mam Moises wach xu bij i Dios che? I Dios, are xu ʼijla i mam Moises chupam i ʼaʼ chi ca juluw che i ral cheʼ, xu bij che: “Yin cʼa in u Dios i mam Abraham, xak i mam Isaac, xak i mam Jacob”, xu bij che. I oxib-le je camnak chic are xu bij i Dios ile. Bay, chuwach i Dios, cʼa je cʼas na; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.027', 'I Dios n-are ti qui Dios i je camnak, are i qui Dios i je cʼaslic —xu bij i Jesus chique—. Yix, ʼut, xa ix sachnak na. (Queje ile xu sakij chique chi ʼo ni qui walijbal i camnak.)
', '', '&lt;span class="verse" id="Mark_12_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I Dios n-are ti qui Dios i je camnak, are i qui Dios i je cʼaslic —xu bij i Jesus chique—. Yix, ʼut, xa ix sachnak na. (Queje ile xu sakij chique chi ʼo ni qui walijbal i camnak.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.028', 'Chila xak ʼo jun mayes re i ujer ʼatbal tzij. Ire ʼis xu ta ile chi xu bij i Jesus chique i aj Saducéo. Xu ta chi Jesus ʼis ʼutz u laʼic u wach xu ʼano. Rumal-i, xtejeb pan ruʼ, y xu tzʼonoj jun tzij che. Xu bij:
—I lajuj u tzij u pixab i Dios, ¿pachique chique mas ʼo u chac coj cojon che? —xu bij.
', '', '&lt;h2&gt;I lajuj u tzij u pixab i Dios, ¿pachique chique mas ʼo u ʼij?&lt;/h2&gt;&lt;span class="verse" id="Mark_12_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Chila xak ʼo jun mayes re i ujer ʼatbal tzij. Ire ʼis xu ta ile chi xu bij i Jesus chique i aj Saducéo. Xu ta chi Jesus ʼis ʼutz u laʼic u wach xu ʼano. Rumal-i, xtejeb pan ruʼ, y xu tzʼonoj jun tzij che. Xu bij:&lt;p&gt; &lt;/p&gt;—I lajuj u tzij u pixab i Dios, ¿pachique chique mas ʼo u chac coj cojon che? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.029', 'I Jesus xu bij che:
—I jun chique chi mas ʼo u chac are i jun chi queje iri cu bij: “Chitapeʼ, ix aj Israel, i Kajwal Dios, xui ire i Kajwal. ', '', '&lt;span class="verse" id="Mark_12_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I Jesus xu bij che:&lt;p&gt; &lt;/p&gt;—I jun chique chi mas ʼo u chac are i jun chi queje iri cu bij: “Chitapeʼ, ix aj Israel, i Kajwal Dios, xui ire i Kajwal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.030', 'Cʼax cha na i Awajwal Dios ronojel a cʼux, ronojel awanima, ronojel a nojbal, ronojel a choʼab”, cu bij. Are i jun-le chi mas ʼo u chac quix cojon che —xu bij i Jesus che—. ', '', '&lt;span class="verse" id="Mark_12_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Cʼax cha na i Awajwal Dios ronojel a cʼux, ronojel awanima, ronojel a nojbal, ronojel a choʼab”, cu bij. Are i jun-le chi mas ʼo u chac quix cojon che —xu bij i Jesus che—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.031', 'Teʼuri i ucab tzij (chi ʼo u chac coj cojon che) laj cumaj i jun-le: “Cʼax che a na a loʼcʼanij pacha cʼax u naʼic awib yet ca ʼano”, cu bij. N-ta chic jun chic chiquixol i lajuj tzij pixab nim ni u ʼij chuwach i queb-le —xu bij i Jesus che.
', '', '&lt;span class="verse" id="Mark_12_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Teʼuri i ucab tzij (chi ʼo u chac coj cojon che) laj cumaj i jun-le: “Cʼax che a na a loʼcʼanij pacha cʼax u naʼic awib yet ca ʼano”, cu bij. N-ta chic jun chic chiquixol i lajuj tzij pixab nim ni u ʼij chuwach i queb-le —xu bij i Jesus che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.032', 'Teʼuri i tijonel re i ujer ʼatbal tzij xu bij:
—Lic ʼutz i xa bij, Mayes. Lic katzij i xa bij chi xa jun i Dios, n-ta chic jun chic. ', '', '&lt;span class="verse" id="Mark_12_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Teʼuri i tijonel re i ujer ʼatbal tzij xu bij:&lt;p&gt; &lt;/p&gt;—Lic ʼutz i xa bij, Mayes. Lic katzij i xa bij chi xa jun i Dios, n-ta chic jun chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.033', 'We cʼax caka na i Dios ronojel ka cʼux, ronojel i ka nojbal, ronojel i kanima, ronojel i ka choʼab, xak we que kaj i kach loʼcʼanij pacha cakaj kib yoj, niʼpa tak ile mas ʼutz chuwach u poroxic tak chicop chuwach i Dios, xak u sujuxic tak sipon chuwach ire —xu bij che i Jesus.
', '', '&lt;span class="verse" id="Mark_12_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;We cʼax caka na i Dios ronojel ka cʼux, ronojel i ka nojbal, ronojel i kanima, ronojel i ka choʼab, xak we que kaj i kach loʼcʼanij pacha cakaj kib yoj, niʼpa tak ile mas ʼutz chuwach u poroxic tak chicop chuwach i Dios, xak u sujuxic tak sipon chuwach ire —xu bij che i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.034', 'I Jesus xu ta chi lic ʼo u noʼoj niʼpa i xu bij, rumal-i xu bij che:
—Yet, xa jubiʼ chic craj ca jach awib che u ʼatbal tzij i Dios —xu bij che. Teʼuri, mi jun xu cowij chic u cʼux chu tzʼonoxic mas tzij che i Jesus.
', '', '&lt;span class="verse" id="Mark_12_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I Jesus xu ta chi lic ʼo u noʼoj niʼpa i xu bij, rumal-i xu bij che:&lt;p&gt; &lt;/p&gt;—Yet, xa jubiʼ chic craj ca jach awib che u ʼatbal tzij i Dios —xu bij che. Teʼuri, mi jun xu cowij chic u cʼux chu tzʼonoxic mas tzij che i Jesus.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.035', 'Xoʼon chupan jubiʼ, i Jesus ca tijin chu cʼutic chique i winak pa rachoch i Dios. Xu bij chique:
—I je tijonel, re i ujer ʼatbal tzij, ¿wuchac quiqui bij chi i Tolque coyʼem, are na jun umuk uxiquin i mam David? ', '', '&lt;h2&gt;I mam David ujer ʼo u ʼij, xui-ri i Crísto ʼo pawi ire&lt;/h2&gt;&lt;span class="verse" id="Mark_12_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, i Jesus ca tijin chu cʼutic chique i winak pa rachoch i Dios. Xu bij chique:&lt;p&gt; &lt;/p&gt;—I je tijonel, re i ujer ʼatbal tzij, ¿wuchac quiqui bij chi i Tolque coyʼem, are na jun umuk uxiquin i mam David? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.036', '¿Wuchac quiqui bij ile? man lic are i mam David u bim can i tzij-i:
Xu bij i Kajwal Dios che i Wajwal Crísto:
“Chat cul na pin wikabim cʼa cu rik ni ʼij que in ya na pawakan niʼpa i aj chʼoʼoj chawij”, xu bij.
Queje ile u tzʼibam can i mam David ujer, y are u Tewal i Dios yawnak pu jolom chi xu bij i tzij-le. ', '', '&lt;span class="verse" id="Mark_12_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;¿Wuchac quiqui bij ile? man lic are i mam David u bim can i tzij-i:&lt;p&gt; &lt;/p&gt;Xu bij i Kajwal Dios che i Wajwal Crísto:
“Chat cul na pin wikabim cʼa cu rik ni ʼij que in ya na pawakan niʼpa i aj chʼoʼoj chawij”, xu bij.
Queje ile u tzʼibam can i mam David ujer, y are u Tewal i Dios yawnak pu jolom chi xu bij i tzij-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.037', '¿Wach u ʼonquil ʼuri chi i Crísto xa umuk uxiquin i mam David? ¿Wach u ʼonquil ile, man lic are i mam David xu bij “Wajwal” che i Crísto? —xcha.
I uqʼuial winak je ʼo chila lic que quicot chu tayic.
', '', '&lt;span class="verse" id="Mark_12_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;¿Wach u ʼonquil ʼuri chi i Crísto xa umuk uxiquin i mam David? ¿Wach u ʼonquil ile, man lic are i mam David xu bij “Wajwal” che i Crísto? —xcha.&lt;p&gt; &lt;/p&gt;I uqʼuial winak je ʼo chila lic que quicot chu tayic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.038', 'I Jesus, are ca tijin chu cʼutic chique, xe u pixbaj ruʼ tak i tzij-i:
—Chi ʼana cwent chique i tijonel re i ujer ʼatbal tzij chi xa kus quiquilo que malcat pa tak be ruʼ qui ʼuʼ naj rakan; xak kus quiqui ta are ca yaʼ rutzil qui wach cumal i winak, ocsabal re qui ʼij. ', '', '&lt;h2&gt;I Jesus xu sakij wach i qui mac i je tijonel re i ʼatbal tzij&lt;/h2&gt;&lt;span class="verse" id="Mark_12_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;I Jesus, are ca tijin chu cʼutic chique, xe u pixbaj ruʼ tak i tzij-i:&lt;p&gt; &lt;/p&gt;—Chi ʼana cwent chique i tijonel re i ujer ʼatbal tzij chi xa kus quiquilo que malcat pa tak be ruʼ qui ʼuʼ naj rakan; xak kus quiqui ta are ca yaʼ rutzil qui wach cumal i winak, ocsabal re qui ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.039', 'Are que oc pa ja re i molbalʼib, xa quiqui tzucuj qui cubibal chi ʼo u ʼij; are que ʼe pa waʼim re tak nimaʼij, xa quiqui tzucuj qui qʼuijibal ʼo u ʼij. ', '', '&lt;span class="verse" id="Mark_12_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Are que oc pa ja re i molbalʼib, xa quiqui tzucuj qui cubibal chi ʼo u ʼij; are que ʼe pa waʼim re tak nimaʼij, xa quiqui tzucuj qui qʼuijibal ʼo u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.040', 'Xak quiqui maj i cachoch ixokib malcanib; teʼuri nim i oracion quiqui ʼan chiquiwach i winak, sachsabal-re chique wach qui ʼanom. Ique ʼut, ca yaʼ jun tojpen chique lic nim na u tojic —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_12_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Xak quiqui maj i cachoch ixokib malcanib; teʼuri nim i oracion quiqui ʼan chiquiwach i winak, sachsabal-re chique wach qui ʼanom. Ique ʼut, ca yaʼ jun tojpen chique lic nim na u tojic —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.041', 'I Jesus cul pa rachoch i Dios chuwach li caxa, yabal re qui mer i winak. Are ʼo chila, que u takej i winak are que tijin chu yaʼic qui mer pa caxa. Je qʼui i beyomab, mas i mer xqui yaʼo. ', '', '&lt;h2&gt;I ixok malcan, nim i sipon xu ya chuwach i Dios&lt;/h2&gt;&lt;span class="verse" id="Mark_12_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;I Jesus cul pa rachoch i Dios chuwach li caxa, yabal re qui mer i winak. Are ʼo chila, que u takej i winak are que tijin chu yaʼic qui mer pa caxa. Je qʼui i beyomab, mas i mer xqui yaʼo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.042', 'Are ʼuri xupon jun ixok malcan lic nibaʼ, xu ya queb u mer chi n-ta kas rajil. ', '', '&lt;span class="verse" id="Mark_12_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are ʼuri xupon jun ixok malcan lic nibaʼ, xu ya queb u mer chi n-ta kas rajil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.043', 'Teʼuri i Jesus xe u siqʼuij u tijoxelab ruʼ, y xu bij chique:
—Katzij i quin bij chiwe, i ixok nibaʼ-i, i u mer xu yaʼo, mas nim chuwach ile chi qui yom conojel i juban pa caxa. ', '', '&lt;span class="verse" id="Mark_12_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xe u siqʼuij u tijoxelab ruʼ, y xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, i ixok nibaʼ-i, i u mer xu yaʼo, mas nim chuwach ile chi qui yom conojel i juban pa caxa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '12.044', 'Ique, wach i qui yom, xa are quesam che wach i sobrinak cuʼ; xui-ri, i ixok-le, chi lic nibaʼ, ronojel xu yaʼo, niʼpa i ʼo ruʼ; n-ta chic xu canaj re u cʼaslemal —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_12_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Ique, wach i qui yom, xa are quesam che wach i sobrinak cuʼ; xui-ri, i ixok-le, chi lic nibaʼ, ronojel xu yaʼo, niʼpa i ʼo ruʼ; n-ta chic xu canaj re u cʼaslemal —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.001', 'Are xel i Jesus pa rachoch i Dios, ʼo jun chique u tijoxelab xu bij che:
—Mayes, chawila tak i ja-ri re i rachoch i Dios, ¡lic chom! ¡Chawila i abaj ʼo che, lic chomik!
', '', '&lt;h2&gt;I Jesus xu bij chi i rachoch i Dios ca wulijsax na&lt;/h2&gt;&lt;span class="verse" id="Mark_13_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xel i Jesus pa rachoch i Dios, ʼo jun chique u tijoxelab xu bij che:&lt;p&gt; &lt;/p&gt;—Mayes, chawila tak i ja-ri re i rachoch i Dios, ¡lic chom! ¡Chawila i abaj ʼo che, lic chomik!&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.002', 'I Jesus xu laʼu wach che:
—¿Cawil yet niʼpa tak iri lic chom? Ronojel iri ca wulijsax na. Mi jun abaj ca canaj chic puwi i rachiʼil —xu bij che.
', '', '&lt;span class="verse" id="Mark_13_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼu wach che:&lt;p&gt; &lt;/p&gt;—¿Cawil yet niʼpa tak iri lic chom? Ronojel iri ca wulijsax na. Mi jun abaj ca canaj chic puwi i rachiʼil —xu bij che.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.003', 'Teʼuri ique xe ʼel bi chuwi jyub Olívos chi ʼo chuwach i rachoch i Dios; chila xcubi wi i Jesus. Are ʼuri je cajib i u tijoxelab xe tejeb pan ruʼ. Je are i ma Pédro, i ma Jacóbo, i ma Wan, i ma Andres. Quituquel xe ʼe chu tzʼonoxic che, aliʼal:
', '', '&lt;h2&gt;Wach i séña cakilo are ya ca cʼun i qʼuisbal ʼij&lt;/h2&gt;&lt;span class="verse" id="Mark_13_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Teʼuri ique xe ʼel bi chuwi jyub Olívos chi ʼo chuwach i rachoch i Dios; chila xcubi wi i Jesus. Are ʼuri je cajib i u tijoxelab xe tejeb pan ruʼ. Je are i ma Pédro, i ma Jacóbo, i ma Wan, i ma Andres. Quituquel xe ʼe chu tzʼonoxic che, aliʼal:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.004', '—¿Umpa cakil ile chi xa bij? ¿Wach i retalil ca yaʼ chake we ya mer cakil ronojel ile? —xqui bij.
', '', '&lt;span class="verse" id="Mark_13_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;—¿Umpa cakil ile chi xa bij? ¿Wach i retalil ca yaʼ chake we ya mer cakil ronojel ile? —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.005', 'I Jesus xu bij chique:
—Chi ʼana cwent iwib, mokxa ʼo quix qui tʼoro. ', '', '&lt;span class="verse" id="Mark_13_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi ʼana cwent iwib, mokxa ʼo quix qui tʼoro. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.006', 'Man je qʼui que cʼun na, quiqui bij na chi “Yin in i Toliwe”, que cha. Cumal ique-le je qʼui que tʼortaj na. ', '', '&lt;span class="verse" id="Mark_13_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Man je qʼui que cʼun na, quiqui bij na chi “Yin in i Toliwe”, que cha. Cumal ique-le je qʼui que tʼortaj na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.007', 'Yix, are qui ta rason we ʼo nimak tak chʼoʼoj jela, we ʼo nimak tak chʼoʼoj jewa, mix cʼachir rumal; ʼo ni u chac queje ile ca ʼantajic; xui-ri, are qui ta i tzij-le, maja na ca cʼun i qʼuisbal re tak i ʼij-i. ', '', '&lt;span class="verse" id="Mark_13_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Yix, are qui ta rason we ʼo nimak tak chʼoʼoj jela, we ʼo nimak tak chʼoʼoj jewa, mix cʼachir rumal; ʼo ni u chac queje ile ca ʼantajic; xui-ri, are qui ta i tzij-le, maja na ca cʼun i qʼuisbal re tak i ʼij-i. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.008', 'ʼO ni tinimit quiqui ʼan chʼoʼoj che chic jun tinimit; ʼo ni winak re jun ʼatol tzij quiqui ʼan chʼoʼoj chiquij i winak re chic jun ʼatol tzij. Xak i nimak tak ucabrakan xa ipa quicʼaw wi, xak queje i waʼjal; xak queje i chʼokowic, xa ipa ca ʼan na wi. Niʼpa ile xa are jekbal re i cʼax chi ca petic.
', '', '&lt;span class="verse" id="Mark_13_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;ʼO ni tinimit quiqui ʼan chʼoʼoj che chic jun tinimit; ʼo ni winak re jun ʼatol tzij quiqui ʼan chʼoʼoj chiquij i winak re chic jun ʼatol tzij. Xak i nimak tak ucabrakan xa ipa quicʼaw wi, xak queje i waʼjal; xak queje i chʼokowic, xa ipa ca ʼan na wi. Niʼpa ile xa are jekbal re i cʼax chi ca petic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.009', 'Chi na iwib yix, man ʼo ni winak quix qui cʼam bi chiquiwach i ʼatbal tzij; quiqui jic i loʼxic pa tak qui molbalʼib. Quix siqʼuix na chiquiwach i gobernador, xak chiquiwach i nimak tak ʼatol tzij xa wumal yin. Xui-ri, queje ile quix tiqui na chu bixquil chique wach iwilom, wach i tom, wach iwetaʼam chwij yin. ', '', '&lt;span class="verse" id="Mark_13_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Chi na iwib yix, man ʼo ni winak quix qui cʼam bi chiquiwach i ʼatbal tzij; quiqui jic i loʼxic pa tak qui molbalʼib. Quix siqʼuix na chiquiwach i gobernador, xak chiquiwach i nimak tak ʼatol tzij xa wumal yin. Xui-ri, queje ile quix tiqui na chu bixquil chique wach iwilom, wach i tom, wach iwetaʼam chwij yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.010', 'ʼO u chac ca paxsax ni ʼutz laj tzij re i Dios chique conojel i tinimit chuwach i jyub taʼaj, are cʼa maja ca cʼun i qʼuisbal ʼij. ', '', '&lt;span class="verse" id="Mark_13_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;ʼO u chac ca paxsax ni ʼutz laj tzij re i Dios chique conojel i tinimit chuwach i jyub taʼaj, are cʼa maja ca cʼun i qʼuisbal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.011', 'Are quix qui chapo, yabal na iwe chiquiwach i ʼatbal tzij, mix cʼachir che, wach qui bij na chiquiwach, xak mix oc chu chʼobic. Xui chi bij na wach cu ya i Dios pi chiʼ are ix ʼo chiquiwach, man n-are ti yix quix nojiw u bixquil, xa are u Tewal i Dios ca binic. ', '', '&lt;span class="verse" id="Mark_13_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Are quix qui chapo, yabal na iwe chiquiwach i ʼatbal tzij, mix cʼachir che, wach qui bij na chiquiwach, xak mix oc chu chʼobic. Xui chi bij na wach cu ya i Dios pi chiʼ are ix ʼo chiquiwach, man n-are ti yix quix nojiw u bixquil, xa are u Tewal i Dios ca binic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.012', 'ʼO ni jujun que qui jach ni catz qui chaʼ piquiʼab i je aj chʼoʼoj chiquij, camsabal na que; xak je ʼo ni kajawxel queje quiqui ʼan cuʼ i cacʼal. Xak queje i acʼalab, quiqui ʼan che quib je aj chʼoʼoj chiquij i qui kajaw i qui chuch, que qui jach na chique i que camsawic. ', '', '&lt;span class="verse" id="Mark_13_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;ʼO ni jujun que qui jach ni catz qui chaʼ piquiʼab i je aj chʼoʼoj chiquij, camsabal na que; xak je ʼo ni kajawxel queje quiqui ʼan cuʼ i cacʼal. Xak queje i acʼalab, quiqui ʼan che quib je aj chʼoʼoj chiquij i qui kajaw i qui chuch, que qui jach na chique i que camsawic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.013', 'Conojel i winak tzel quix quil na xa wumal yin; xui-ri, pachin i cu chʼijo cʼa pa qʼuisbal re u chac, quelsax na ʼuri pa cʼax.
', '', '&lt;span class="verse" id="Mark_13_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Conojel i winak tzel quix quil na xa wumal yin; xui-ri, pachin i cu chʼijo cʼa pa qʼuisbal re u chac, quelsax na ʼuri pa cʼax.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.014', 'I ajbil u tzij i Dios, mam Daniel, u tzʼibam canok wach ca cʼunic: xu bij ire chi cocsax na jun tzel laj kajsabal re u ʼij i rachoch i Dios che i lugar chi n-ca majaw ta che. Are quiwil yix ile tacʼal chila, niʼpa i je ʼo pa jyub. Niʼpa i cacajlaj i tzij-i, chiqui ta usucʼ. ', '', '&lt;span class="verse" id="Mark_13_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I ajbil u tzij i Dios, mam Daniel, u tzʼibam canok wach ca cʼunic: xu bij ire chi cocsax na jun tzel laj kajsabal re u ʼij i rachoch i Dios che i lugar chi n-ca majaw ta che. Are quiwil yix ile tacʼal chila, niʼpa i je ʼo pa jyub. Niʼpa i cacajlaj i tzij-i, chiqui ta usucʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.015', 'Chupam i ʼij-le, suʼ chix ʼanmajok. Niʼpa je ʼo ʼan puwija re i cachoch, suʼ che kaj la lok; me oc chic chupam, quiqui cʼambej re ubitak-que. ', '', '&lt;span class="verse" id="Mark_13_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Chupam i ʼij-le, suʼ chix ʼanmajok. Niʼpa je ʼo ʼan puwija re i cachoch, suʼ che kaj la lok; me oc chic chupam, quiqui cʼambej re ubitak-que. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.016', 'Xak niʼpa i que tijin chi chac pa tak ticon, me tzalij lok col qui cʼambej re qui ʼuʼ; ¡paʼanem che ʼanmajok! ', '', '&lt;span class="verse" id="Mark_13_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Xak niʼpa i que tijin chi chac pa tak ticon, me tzalij lok col qui cʼambej re qui ʼuʼ; ¡paʼanem che ʼanmajok! &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.017', 'Chupam i ʼij-le, toʼ qui wach ixokib chi ʼo i yobil chiquij, xak ique chi ʼo cacʼal chi cʼa que tzʼumanic; cʼax i qui ʼanmajic ique. ', '', '&lt;span class="verse" id="Mark_13_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Chupam i ʼij-le, toʼ qui wach ixokib chi ʼo i yobil chiquij, xak ique chi ʼo cacʼal chi cʼa que tzʼumanic; cʼax i qui ʼanmajic ique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.018', 'Chi tzʼonoj che i Dios chi n-joron ti tew are quix ʼanmaj chuwach ile, ', '', '&lt;span class="verse" id="Mark_13_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Chi tzʼonoj che i Dios chi n-joron ti tew are quix ʼanmaj chuwach ile, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.019', 'man i uyej che tak i ʼij-le, lic cʼax. Cʼa chu xebal ʼij sak, are xu yijba i Dios i jyub taʼaj, n-ta jun cʼax petnak lic cʼax chuwach ile; xak n-ta chic jun chic ca petic, lic cʼax chuwach ile. ', '', '&lt;span class="verse" id="Mark_13_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;man i uyej che tak i ʼij-le, lic cʼax. Cʼa chu xebal ʼij sak, are xu yijba i Dios i jyub taʼaj, n-ta jun cʼax petnak lic cʼax chuwach ile; xak n-ta chic jun chic ca petic, lic cʼax chuwach ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.020', 'Y we i Kajwal n-coʼon ta che, chi xa ʼatal ʼij ʼo ni cʼax-le, ʼis que cam na conojel. Xui-ri, ire u chʼobom chi cu ʼatij na; queje coʼon ire ile, xa rumal i cʼaxnabal u cʼux chique i je re ire, chique i je u chom ire.
', '', '&lt;span class="verse" id="Mark_13_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Y we i Kajwal n-coʼon ta che, chi xa ʼatal ʼij ʼo ni cʼax-le, ʼis que cam na conojel. Xui-ri, ire u chʼobom chi cu ʼatij na; queje coʼon ire ile, xa rumal i cʼaxnabal u cʼux chique i je re ire, chique i je u chom ire.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.021', 'Rumal-i, che tak i ʼij-le, we ʼo i que bin chiwe: “Chiwilapeʼ, chi ʼo wi i Tolke”, que cha, mi cojo; o we xak quiqui bij: “Le i ʼolic-le”, que cha, mi cojo. ', '', '&lt;span class="verse" id="Mark_13_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Rumal-i, che tak i ʼij-le, we ʼo i que bin chiwe: “Chiwilapeʼ, chi ʼo wi i Tolke”, que cha, mi cojo; o we xak quiqui bij: “Le i ʼolic-le”, que cha, mi cojo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.022', 'Man que cʼun ni aj tʼoronel chi quiqui ʼan che quib pacha je toliwe, xak que cʼun jujun quiqui ʼan che quib pacha je ajbil u tzij i Dios. Quiqui ʼan ni milágro, xak i nimak tak ʼanic, ʼilbal-re we que tiqui chi qui tʼoric niʼpa i je re i Dios, chi je u chom ire. ', '', '&lt;span class="verse" id="Mark_13_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Man que cʼun ni aj tʼoronel chi quiqui ʼan che quib pacha je toliwe, xak que cʼun jujun quiqui ʼan che quib pacha je ajbil u tzij i Dios. Quiqui ʼan ni milágro, xak i nimak tak ʼanic, ʼilbal-re we que tiqui chi qui tʼoric niʼpa i je re i Dios, chi je u chom ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.023', '¡Chi na iwib! Ya in bim chic ronojel chiwe, wach i qui cʼulmaj coʼon panok.
', '', '&lt;span class="verse" id="Mark_13_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;¡Chi na iwib! Ya in bim chic ronojel chiwe, wach i qui cʼulmaj coʼon panok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.024', 'Are quicʼaw i ʼij re niʼpa i cʼax-le, teʼuri i ʼij ca ʼekumaric, xak i icʼ n-ca tzun ta chic, ', '', '&lt;h2&gt;Wach u cʼunic tan chic i Crísto pa qʼuisbal ʼij&lt;/h2&gt;&lt;span class="verse" id="Mark_13_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Are quicʼaw i ʼij re niʼpa i cʼax-le, teʼuri i ʼij ca ʼekumaric, xak i icʼ n-ca tzun ta chic, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.025', 'xak i chʼumil que tzak na li chicaj, xak wach tak ʼo chicaj ca sicsax na. ', '', '&lt;span class="verse" id="Mark_13_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;xak i chʼumil que tzak na li chicaj, xak wach tak ʼo chicaj ca sicsax na. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.026', 'Are ʼuri quiwil ni Achi aj Chicaj; ca cʼun pa sutzʼ ruʼ i nim laj u choʼab, nim laj u chomal. ', '', '&lt;span class="verse" id="Mark_13_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Are ʼuri quiwil ni Achi aj Chicaj; ca cʼun pa sutzʼ ruʼ i nim laj u choʼab, nim laj u chomal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.027', 'Que u tak bi u ángel cʼa pa que ʼel wi li i cajib tew cʼulala, quiqui molbej-que niʼpa i je u chom i Dios, lic ronojel u wach i jyub taʼaj.
', '', '&lt;span class="verse" id="Mark_13_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Que u tak bi u ángel cʼa pa que ʼel wi li i cajib tew cʼulala, quiqui molbej-que niʼpa i je u chom i Dios, lic ronojel u wach i jyub taʼaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.028', 'Yix iwetaʼam wach i séña cu cʼut u cheʼlal hígo are ya ca cʼun i ʼalaj. Chix naw che i cheʼ-le. Ile, are quiwilo ca pe tan chic u tux, y coʼon rax, iwetaʼam ʼuri ya ca miʼ tan chic i ulew. ', '', '&lt;span class="verse" id="Mark_13_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Yix iwetaʼam wach i séña cu cʼut u cheʼlal hígo are ya ca cʼun i ʼalaj. Chix naw che i cheʼ-le. Ile, are quiwilo ca pe tan chic u tux, y coʼon rax, iwetaʼam ʼuri ya ca miʼ tan chic i ulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.029', 'Queje queʼel tak ile, chi cʼa te xin bij chiwe. Are quiwil yix niʼpa tak ile, chi ta u be ʼuri ya ca pe i qʼuisbal re tak i ʼij-i, ya jekanic coʼono. ', '', '&lt;span class="verse" id="Mark_13_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Queje queʼel tak ile, chi cʼa te xin bij chiwe. Are quiwil yix niʼpa tak ile, chi ta u be ʼuri ya ca pe i qʼuisbal re tak i ʼij-i, ya jekanic coʼono. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.030', 'Katzij i quin bij chiwe, ya ca cʼun ronojel niʼpa in bim-le; ca ʼantaj na, are maja que cam i winak re i woʼor. ', '', '&lt;span class="verse" id="Mark_13_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, ya ca cʼun ronojel niʼpa in bim-le; ca ʼantaj na, are maja que cam i winak re i woʼor. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.031', 'I caj, i ulew, ca sach na; xui-ri, in tzij yin n-ca sach taj; coʼon na ronojel niʼpa in bim chiwe.
', '', '&lt;span class="verse" id="Mark_13_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I caj, i ulew, ca sach na; xui-ri, in tzij yin n-ca sach taj; coʼon na ronojel niʼpa in bim chiwe.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.032', 'Xui-ri, mi jun retaʼam pachique ʼij, pachique ʼor, coʼon ile. Xak mi ne i ángel aj chicaj, xak mi ne yin chi in u Cʼojol i Dios n-wetaʼam taj; xui i Ta chicaj retaʼam.
', '', '&lt;span class="verse" id="Mark_13_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Xui-ri, mi jun retaʼam pachique ʼij, pachique ʼor, coʼon ile. Xak mi ne i ángel aj chicaj, xak mi ne yin chi in u Cʼojol i Dios n-wetaʼam taj; xui i Ta chicaj retaʼam.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.033', 'Xui-ri, yix chix cʼascʼatok, chi cʼacʼalij, y mi mayij i tzʼonoxic che i Dios, man n-iwetaʼam taj wach oʼonom ʼor que u rika. ', '', '&lt;span class="verse" id="Mark_13_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Xui-ri, yix chix cʼascʼatok, chi cʼacʼalij, y mi mayij i tzʼonoxic che i Dios, man n-iwetaʼam taj wach oʼonom ʼor que u rika. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.034', 'Queʼelok pacha jun achi chi ya ca ʼec, naj ca ʼe wi. Que u pixbaj i rajchaquib chi que chajiw can i rachoch. Chi qui jujunal ʼis cu ya can qui chac, xak i aj chajal ja, cu bij can che chi ca cʼascʼat na chu chajixic. ', '', '&lt;span class="verse" id="Mark_13_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Queʼelok pacha jun achi chi ya ca ʼec, naj ca ʼe wi. Que u pixbaj i rajchaquib chi que chajiw can i rachoch. Chi qui jujunal ʼis cu ya can qui chac, xak i aj chajal ja, cu bij can che chi ca cʼascʼat na chu chajixic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.035', 'Xak queje chi ʼana yix ile, chix cʼascʼatok, man n-iwetaʼam taj wach oʼonom ʼor ca cʼun i rajaw ja. N-iwetaʼam taj we coc aʼab ca cʼunic, o we tiqʼuil aʼab, o are ca chʼaw i acʼ, o we are quel li ʼij. ', '', '&lt;span class="verse" id="Mark_13_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Xak queje chi ʼana yix ile, chix cʼascʼatok, man n-iwetaʼam taj wach oʼonom ʼor ca cʼun i rajaw ja. N-iwetaʼam taj we coc aʼab ca cʼunic, o we tiqʼuil aʼab, o are ca chʼaw i acʼ, o we are quel li ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.036', 'Mok xak teʼet ca cʼunic, y quix u riko xa quix waric. ', '', '&lt;span class="verse" id="Mark_13_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Mok xak teʼet ca cʼunic, y quix u riko xa quix waric. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '13.037', 'Wach i quin tijin chu bixquil chiwe, xak quin bij chique conojel: “¡Chix cʼascʼatok!” (Queje xu bij i Jesus ile chique.)
', '', '&lt;span class="verse" id="Mark_13_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Wach i quin tijin chu bixquil chiwe, xak quin bij chique conojel: “¡Chix cʼascʼatok!” (Queje xu bij i Jesus ile chique.)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.001', 'Xa queb ʼij chic craj cu rik i nimaʼij páscua, iʼij chi ca tijaw wi i caxlanwa chi n-ta cunbal ocnak che. I je cajʼatzil i sacerdóte, xak i je tijonel re i ujer ʼatbal tzij, quiqui tzucuj wach i tzaksabal re i Jesus quiqui ʼano, chapbal na re aliʼal, xa camsabal-re. ', '', '&lt;h2&gt;Wach u chapic i Jesus xqui chʼob i aj chʼoʼoj chirij&lt;/h2&gt;&lt;span class="verse" id="Mark_14_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xa queb ʼij chic craj cu rik i nimaʼij páscua, iʼij chi ca tijaw wi i caxlanwa chi n-ta cunbal ocnak che. I je cajʼatzil i sacerdóte, xak i je tijonel re i ujer ʼatbal tzij, quiqui tzucuj wach i tzaksabal re i Jesus quiqui ʼano, chapbal na re aliʼal, xa camsabal-re. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.002', 'Xui-ri, xqui bij chiquiwach:
—Maka ʼan ile pa nimaʼij, mokxa que chʼokow i uqʼuial winak rumal —que cha.
', '', '&lt;span class="verse" id="Mark_14_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Xui-ri, xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—Maka ʼan ile pa nimaʼij, mokxa que chʼokow i uqʼuial winak rumal —que cha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.003', 'I Jesus ʼo pa aldéa Betánia ruʼ rachoch i achi Simon, chi “Lepróso” ca bix che. Are que tijin chi waʼim, xoc bi jun ixok cuʼ. Ire rucʼam jun chom laj baxo, nojnak che acéite lic muy, lic pakal rajil. I acéitele, ʼantal che i cotzʼij, “nárdo” u bi. Xu ʼaj u tzam i baxo, y xu jam i acéite che u jolom i Jesus. ', '', '&lt;h2&gt;Jun ixok xu coj cunbal muy, pakalic, che u jolom i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_14_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I Jesus ʼo pa aldéa Betánia ruʼ rachoch i achi Simon, chi “Lepróso” ca bix che. Are que tijin chi waʼim, xoc bi jun ixok cuʼ. Ire rucʼam jun chom laj baxo, nojnak che acéite lic muy, lic pakal rajil. I acéitele, ʼantal che i cotzʼij, “nárdo” u bi. Xu ʼaj u tzam i baxo, y xu jam i acéite che u jolom i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.004', 'ʼO jujun je ʼo chila xpe coywal; xqui bij chiquiwach:
—¿Wuchac queje xoʼon ile che i cunbal muy, chi toʼ xsachic? —xqui bij—. ', '', '&lt;span class="verse" id="Mark_14_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;ʼO jujun je ʼo chila xpe coywal; xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Wuchac queje xoʼon ile che i cunbal muy, chi toʼ xsachic? —xqui bij—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.005', 'Are i rajil ile, mas che oxib ciénto quetzal; mas ʼutz we ta xcʼayixic, cu tobej que i nibaʼib —xqui bij.
Y xa que chʼachʼat chirij ixok.
', '', '&lt;span class="verse" id="Mark_14_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Are i rajil ile, mas che oxib ciénto quetzal; mas ʼutz we ta xcʼayixic, cu tobej que i nibaʼib —xqui bij.&lt;p&gt; &lt;/p&gt;Y xa que chʼachʼat chirij ixok.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.006', 'Péro i Jesus xu bij:
—Coʼon ile. ¿Wuchac quix chʼachʼat chirij? Ile chi xoʼon ire chwe, lic ʼutz. ', '', '&lt;span class="verse" id="Mark_14_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Péro i Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Coʼon ile. ¿Wuchac quix chʼachʼat chirij? Ile chi xoʼon ire chwe, lic ʼutz. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.007', 'I nibaʼib, queʼe ʼij sak je ʼo na iwuʼ; we quiwaj que i to, pachique ʼij ʼis ʼutz. Xui-ri, yin, n-ronojel ta ʼij in ʼo iwuʼ. ', '', '&lt;span class="verse" id="Mark_14_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;I nibaʼib, queʼe ʼij sak je ʼo na iwuʼ; we quiwaj que i to, pachique ʼij ʼis ʼutz. Xui-ri, yin, n-ronojel ta ʼij in ʼo iwuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.008', 'I ixok-i xoʼon chwe niʼpa i xtiqui che. Are cʼa maja cu rik i ʼij, xu coj i cunbal muy re i mukbal re in chʼacul. ', '', '&lt;span class="verse" id="Mark_14_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I ixok-i xoʼon chwe niʼpa i xtiqui che. Are cʼa maja cu rik i ʼij, xu coj i cunbal muy re i mukbal re in chʼacul. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.009', 'Katzij i quin bij chiwe, xa ipa ca paxsax wi i tzij re i ʼelsabal re qui mac i winak, xak ca tataj ni rason wach u ʼanom i ixok-i chwe. Queje coʼon ile ronojel u wach i jyub taʼaj, cʼunsabal re i ixok-i chi qui cʼux i winak —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_14_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, xa ipa ca paxsax wi i tzij re i ʼelsabal re qui mac i winak, xak ca tataj ni rason wach u ʼanom i ixok-i chwe. Queje coʼon ile ronojel u wach i jyub taʼaj, cʼunsabal re i ixok-i chi qui cʼux i winak —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.010', 'Teʼuri i ma Júdas Iscarióte, jun chique i cablajuj u tijoxelab, xa ʼe cuʼ i je cajʼatzil i sacerdóte, chu sujuxic i Jesus piquiʼab. ', '', '&lt;h2&gt;I ma Júdas xu sujuj i Jesus piquiʼab i aj chʼoʼoj chirij&lt;/h2&gt;&lt;span class="verse" id="Mark_14_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Júdas Iscarióte, jun chique i cablajuj u tijoxelab, xa ʼe cuʼ i je cajʼatzil i sacerdóte, chu sujuxic i Jesus piquiʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.011', 'Ique, are xqui ta, lic xe quicotic, y xqui bij che chi quiqui ya mer che. Teʼuri i ma Júdas xoc chu tzucuxic wach u jachic i Jesus coʼon piquiʼab ique.
', '', '&lt;span class="verse" id="Mark_14_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Ique, are xqui ta, lic xe quicotic, y xqui bij che chi quiqui ya mer che. Teʼuri i ma Júdas xoc chu tzucuxic wach u jachic i Jesus coʼon piquiʼab ique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.012', 'Xcʼun i nabe ʼij re i nimaʼij chi ca tijaw wi i caxlanwa chi n-ta cunbal che. Are i ʼij-le chi que camsax ni ral tak chij re i ni-maʼij páscua. Chupam i ʼij-le, i u tijoxelab i Jesus xqui bij che:
—¿Pa cawaj wi yet caka yijba wi i a waʼim re i nimaʼij páscua? —xe cha che. ', '', '&lt;h2&gt;I Jesus xoʼon waʼim cuʼ u tijoxelab re i nimaʼij páscua&lt;/h2&gt;&lt;span class="verse" id="Mark_14_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xcʼun i nabe ʼij re i nimaʼij chi ca tijaw wi i caxlanwa chi n-ta cunbal che. Are i ʼij-le chi que camsax ni ral tak chij re i ni-maʼij páscua. Chupam i ʼij-le, i u tijoxelab i Jesus xqui bij che:&lt;p&gt; &lt;/p&gt;—¿Pa cawaj wi yet caka yijba wi i a waʼim re i nimaʼij páscua? —xe cha che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.013', 'Ire xe u tak bi queb chique chu yijbaxic. Xu bij chique:
—Jix pa tinimit. Chila qui cʼulaj wi jun achi rekam jun ʼekbala ʼo ya chupam. Chix terej chirij. ', '', '&lt;span class="verse" id="Mark_14_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ire xe u tak bi queb chique chu yijbaxic. Xu bij chique:&lt;p&gt; &lt;/p&gt;—Jix pa tinimit. Chila qui cʼulaj wi jun achi rekam jun ʼekbala ʼo ya chupam. Chix terej chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.014', 'Pa coc wi ire ruʼ ja, chi bij iri che i rajaw ja chila: “Cu bij li ka Mayes, ¿Pa ʼo wi i cuárto pa quin tij wi i waʼim re i páscua cuʼ in tijoxelab? xu bij lok”, chix chok che. ', '', '&lt;span class="verse" id="Mark_14_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Pa coc wi ire ruʼ ja, chi bij iri che i rajaw ja chila: “Cu bij li ka Mayes, ¿Pa ʼo wi i cuárto pa quin tij wi i waʼim re i páscua cuʼ in tijoxelab? xu bij lok”, chix chok che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.015', 'Teʼuri ire cu cʼut jun nim laj cuárto chiwe, cʼa pu caleja, yij chic; chila chi yijba wi ka waʼim yoj —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_14_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Teʼuri ire cu cʼut jun nim laj cuárto chiwe, cʼa pu caleja, yij chic; chila chi yijba wi ka waʼim yoj —xu bij i Jesus chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.016', 'I u tijoxelab xe ʼec; are xe upon pa tinimit ʼis xqui riko pacha u bim i Jesus chique. Teʼuri xqui yijba i waʼim re i nimaʼij páscua.
', '', '&lt;span class="verse" id="Mark_14_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;I u tijoxelab xe ʼec; are xe upon pa tinimit ʼis xqui riko pacha u bim i Jesus chique. Teʼuri xqui yijba i waʼim re i nimaʼij páscua.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.017', 'Are xoc aʼab xupon i Jesus cuʼ i cablajuj u tijoxelab. ', '', '&lt;span class="verse" id="Mark_14_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are xoc aʼab xupon i Jesus cuʼ i cablajuj u tijoxelab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.018', 'Are je ʼo chi mexa, que tijin chi waʼim, i Jesus xu bij chique:
—Katzij i quin bij chiwe, ʼo jun chiwe yix, ca tijin chi waʼim wuʼ, chi quin u jach na piquiʼab i je aj chʼoʼoj chwij —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Are je ʼo chi mexa, que tijin chi waʼim, i Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chiwe, ʼo jun chiwe yix, ca tijin chi waʼim wuʼ, chi quin u jach na piquiʼab i je aj chʼoʼoj chwij —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.019', 'Ique, are xqui ta ile, lic xe oc chi bis. Chi qui jujunal xe oc chu tzʼonoxic che:
—¿Mokxa yin?
Y xak xu bij jun chic:
—¿Mokxa yin? —xu bij.
', '', '&lt;span class="verse" id="Mark_14_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Ique, are xqui ta ile, lic xe oc chi bis. Chi qui jujunal xe oc chu tzʼonoxic che:&lt;p&gt; &lt;/p&gt;—¿Mokxa yin?&lt;p&gt; &lt;/p&gt;Y xak xu bij jun chic:&lt;p&gt; &lt;/p&gt;—¿Mokxa yin? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.020', 'I Jesus xu bij chique:
—Are jun chiwe yix ix cablajuj, jun chi cu mu u wa pa lak wuʼ yin. ', '', '&lt;span class="verse" id="Mark_14_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Are jun chiwe yix ix cablajuj, jun chi cu mu u wa pa lak wuʼ yin. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.021', 'Katzij, yin chi in Achi aj Chicaj, quin camsax na, pacha tzʼibtal can chupam u wuj i Dios. Xui-ri, toʼwach i achi chi quin jachaw na piquiʼab i aj chʼoʼoj chwij. Mas ʼutz queʼel che i achi-le ma ta xalaxic —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Katzij, yin chi in Achi aj Chicaj, quin camsax na, pacha tzʼibtal can chupam u wuj i Dios. Xui-ri, toʼwach i achi chi quin jachaw na piquiʼab i aj chʼoʼoj chwij. Mas ʼutz queʼel che i achi-le ma ta xalaxic —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.022', 'Are que tijin chi waʼim, i Jesus xu chap i caxlanwa y xu tioxij che i Dios, teʼuri xu piro. Xu ya chique u tijoxelab y xu bij chique:
—Chi chapa; are iri are in chʼacul.
', '', '&lt;span class="verse" id="Mark_14_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are que tijin chi waʼim, i Jesus xu chap i caxlanwa y xu tioxij che i Dios, teʼuri xu piro. Xu ya chique u tijoxelab y xu bij chique:&lt;p&gt; &lt;/p&gt;—Chi chapa; are iri are in chʼacul.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.023', 'Teʼuri xu chap i tuʼbal víno, y xak xu tioxij che i Dios. Teʼuri xu ya chique, y conojel xqui tuʼ che. ', '', '&lt;span class="verse" id="Mark_14_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Teʼuri xu chap i tuʼbal víno, y xak xu tioxij che i Dios. Teʼuri xu ya chique, y conojel xqui tuʼ che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.024', 'Xu bij chique:
Are iri are in quiqʼuel chi ca yijbaw ni trat cʼacʼ ruʼ i Dios. Ca turuw na, ʼanbal ni ʼutz chique uqʼuial winak. ', '', '&lt;span class="verse" id="Mark_14_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;Are iri are in quiqʼuel chi ca yijbaw ni trat cʼacʼ ruʼ i Dios. Ca turuw na, ʼanbal ni ʼutz chique uqʼuial winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.025', 'Katzij i quin bij chiwe, n-quin tuʼ ta chic u waʼal úva, cʼa te tan chic quin tuʼ cʼacʼ pa cu ʼat wi tzij i Dios —xu bij.
', '', '&lt;span class="verse" id="Mark_14_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Katzij i quin bij chiwe, n-quin tuʼ ta chic u waʼal úva, cʼa te tan chic quin tuʼ cʼacʼ pa cu ʼat wi tzij i Dios —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.026', 'Teʼuri xqui bixaj jun bix, cojbal re u ʼij i Dios. Are xe utzinic, xe ʼe chuwi jyub Olívos. ', '', '&lt;h2&gt;I Jesus xu bij wach i ruwaxic u wach ca ʼan na rumal i ma Pédro&lt;/h2&gt;&lt;span class="verse" id="Mark_14_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Teʼuri xqui bixaj jun bix, cojbal re u ʼij i Dios. Are xe utzinic, xe ʼe chuwi jyub Olívos. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.027', 'I Jesus xu bij chique:
—Woʼor chaʼab, iwonojel yix quin iwoʼtaj na canok. Wetaʼam queje ile queʼelok, man queje iri u bim can i Dios chupam u wuj: “Quin ya na, chi ca camsax ni aj yuʼul chij”, xu bij, “teʼuri i chij que jacher bic.” (Queje ile tzʼibtal canok.) ', '', '&lt;span class="verse" id="Mark_14_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chique:&lt;p&gt; &lt;/p&gt;—Woʼor chaʼab, iwonojel yix quin iwoʼtaj na canok. Wetaʼam queje ile queʼelok, man queje iri u bim can i Dios chupam u wuj: “Quin ya na, chi ca camsax ni aj yuʼul chij”, xu bij, “teʼuri i chij que jacher bic.” (Queje ile tzʼibtal canok.) &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.028', 'Xui-ri, are in walijnak chic che i camic, quin nabeaj na chiwach pa Galiléa —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Xui-ri, are in walijnak chic che i camic, quin nabeaj na chiwach pa Galiléa —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.029', 'Are ʼuri xu bij i ma Pédro:
—We conojel ʼis cat coʼtaj canok, yin ʼut n-cat woʼtaj taj —xu bij.
', '', '&lt;span class="verse" id="Mark_14_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Are ʼuri xu bij i ma Pédro:&lt;p&gt; &lt;/p&gt;—We conojel ʼis cat coʼtaj canok, yin ʼut n-cat woʼtaj taj —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.030', 'I Jesus xu bij chic che:
—Katzij i quin bij chawe, che i aʼab-i, are maja ca chʼaw ucamul acʼ, quin awuwaj ni oxmul.
', '', '&lt;span class="verse" id="Mark_14_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;I Jesus xu bij chic che:&lt;p&gt; &lt;/p&gt;—Katzij i quin bij chawe, che i aʼab-i, are maja ca chʼaw ucamul acʼ, quin awuwaj ni oxmul.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.031', 'I ma Pédro xu camulij u bixquil; xu bij chic:
—Yin lic n-cat wuwaj taj, n-ta coʼono we junam awuʼ coj camic —xu bij. Xak queje ile xqui bij conojel.
', '', '&lt;span class="verse" id="Mark_14_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;I ma Pédro xu camulij u bixquil; xu bij chic:&lt;p&gt; &lt;/p&gt;—Yin lic n-cat wuwaj taj, n-ta coʼono we junam awuʼ coj camic —xu bij. Xak queje ile xqui bij conojel.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.032', 'Teʼuri xe ʼe chic che jun lugar, Getsemani u bi. I Jesus xu bij chique u tijoxelab:
—Chix cul na can chi, tzʼakat quin ʼe yin chu tzʼonoxic che i Dios —xu bij.
', '', '&lt;h2&gt;Wach u tzʼonoxic xoʼon i Jesus che i Dios pa Getsemani&lt;/h2&gt;&lt;span class="verse" id="Mark_14_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Teʼuri xe ʼe chic che jun lugar, Getsemani u bi. I Jesus xu bij chique u tijoxelab:&lt;p&gt; &lt;/p&gt;—Chix cul na can chi, tzʼakat quin ʼe yin chu tzʼonoxic che i Dios —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.033', 'Teʼuri xe u cʼam bi i ma Pédro, i ma Jacóbo, i ma Wan ruʼ, y xpe jun nim laj bis, jun nim laj cʼax pu cʼux. ', '', '&lt;span class="verse" id="Mark_14_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Teʼuri xe u cʼam bi i ma Pédro, i ma Jacóbo, i ma Wan ruʼ, y xpe jun nim laj bis, jun nim laj cʼax pu cʼux. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.034', 'Xu bij chique:
—I bis ʼo pa wanima, laj quin cam rumal, quin na yin. Chix canaj ni yix waral y chix cʼascʼatok —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—I bis ʼo pa wanima, laj quin cam rumal, quin na yin. Chix canaj ni yix waral y chix cʼascʼatok —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.035', 'I Jesus xa ʼe chupan jubiʼ, xu jupij rib pulew, y xoc chu tzʼonoxic che i Dios. Xu bij che we ta ʼo módo, craj quelsax na chuwach i cʼax chi ya cu cʼulmaj.
', '', '&lt;span class="verse" id="Mark_14_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I Jesus xa ʼe chupan jubiʼ, xu jupij rib pulew, y xoc chu tzʼonoxic che i Dios. Xu bij che we ta ʼo módo, craj quelsax na chuwach i cʼax chi ya cu cʼulmaj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.036', '—Ta —xu bij che—, yet ronojel ʼis cat tiqui che. Chawesaj i cʼax ʼo chinwach. Xui-ri, ma ya chwe pacha i toʼ cwaj yin; are, cha ʼana pacha i cawaj yet —xu bij.
', '', '&lt;span class="verse" id="Mark_14_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;—Ta —xu bij che—, yet ronojel ʼis cat tiqui che. Chawesaj i cʼax ʼo chinwach. Xui-ri, ma ya chwe pacha i toʼ cwaj yin; are, cha ʼana pacha i cawaj yet —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.037', 'Teʼuri xtzalij chic cuʼ u tijoxelab, xe u riko que waric. Xu ʼijla i ma Pédro y xu bij che:
—A-Simon, ¿cat waric? ¿N-xa chʼij ta bari cat cʼascʼat jun ʼor? ', '', '&lt;span class="verse" id="Mark_14_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri xtzalij chic cuʼ u tijoxelab, xe u riko que waric. Xu ʼijla i ma Pédro y xu bij che:&lt;p&gt; &lt;/p&gt;—A-Simon, ¿cat waric? ¿N-xa chʼij ta bari cat cʼascʼat jun ʼor? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.038', 'Chix cʼascʼat na y chi tzʼonoj che i Dios, man n-quix tzak ta chupam i mac. Katzij, yix piwanima quiwaj quix cʼascʼatic, xui-ri i chʼacul n-cu chʼij taj —xu bij.
', '', '&lt;span class="verse" id="Mark_14_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Chix cʼascʼat na y chi tzʼonoj che i Dios, man n-quix tzak ta chupam i mac. Katzij, yix piwanima quiwaj quix cʼascʼatic, xui-ri i chʼacul n-cu chʼij taj —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.039', 'Teʼuri xtzalij chic ucamul chu tzʼonoxic che i Dios; xak jun chi tzij xu bij chic che. ', '', '&lt;span class="verse" id="Mark_14_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Teʼuri xtzalij chic ucamul chu tzʼonoxic che i Dios; xak jun chi tzij xu bij chic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.040', 'Are xtzalij chalok, xe u rik tan chic u tijoxelab que war tan chic; i qui baʼwach lic waram craj. Ique n-xqui rik taj wach quiqui bij che. ', '', '&lt;span class="verse" id="Mark_14_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Are xtzalij chalok, xe u rik tan chic u tijoxelab que war tan chic; i qui baʼwach lic waram craj. Ique n-xqui rik taj wach quiqui bij che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.041', 'Are xtzalij chal che uroxmul, xu bij chique:
—Bay, chix warok; chix uxlanok. Ya coʼon ile. Ya xu rik i ʼor chi yin, chi in Achi aj Chicaj, quin jachtaj na piquiʼab i ajmaquib. ', '', '&lt;span class="verse" id="Mark_14_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Are xtzalij chal che uroxmul, xu bij chique:&lt;p&gt; &lt;/p&gt;—Bay, chix warok; chix uxlanok. Ya coʼon ile. Ya xu rik i ʼor chi yin, chi in Achi aj Chicaj, quin jachtaj na piquiʼab i ajmaquib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.042', 'Chix walijok. Joʼ. Ya xcʼun ire chi quin jachaw na piquiʼab i aj chʼoʼoj chwij —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Chix walijok. Joʼ. Ya xcʼun ire chi quin jachaw na piquiʼab i aj chʼoʼoj chwij —xu bij chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.043', 'I Jesus cʼa ca tijin chi tzij are xupon i ma Júdas, chi jun chique i cablajuj u tijoxelab. Ire je rucʼam li uqʼuial winak; ʼo jujun cucʼam chʼichʼ, xak ʼo i cucʼam cheʼ. Ique je taktal li cumal i cajʼatzil i sacerdóte, xak i tijonel re i ujer ʼatbal tzij, xak i nimak tak mamʼib. ', '', '&lt;h2&gt;Wach u chaptajic bi i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_14_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;I Jesus cʼa ca tijin chi tzij are xupon i ma Júdas, chi jun chique i cablajuj u tijoxelab. Ire je rucʼam li uqʼuial winak; ʼo jujun cucʼam chʼichʼ, xak ʼo i cucʼam cheʼ. Ique je taktal li cumal i cajʼatzil i sacerdóte, xak i tijonel re i ujer ʼatbal tzij, xak i nimak tak mamʼib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.044', 'I ma Júdas chi aj jachanel re i Jesus u bim can chique wach i séña coʼon na chique, cʼutbal re i Jesus:
—Are i jun chi quin tzʼubaj, are ile —u bim can chique—. Chi chapa, ʼutz u cʼamic bic chi ʼana —u bim chique.
', '', '&lt;span class="verse" id="Mark_14_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;I ma Júdas chi aj jachanel re i Jesus u bim can chique wach i séña coʼon na chique, cʼutbal re i Jesus:&lt;p&gt; &lt;/p&gt;—Are i jun chi quin tzʼubaj, are ile —u bim can chique—. Chi chapa, ʼutz u cʼamic bic chi ʼana —u bim chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.045', 'Are xuponic, xtejeb pan ruʼ i Jesus y xu bij che:
—¡Mayes, Mayes! —xu bij che.
Y xu tzʼubaj. ', '', '&lt;span class="verse" id="Mark_14_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Are xuponic, xtejeb pan ruʼ i Jesus y xu bij che:&lt;p&gt; &lt;/p&gt;—¡Mayes, Mayes! —xu bij che.&lt;p&gt; &lt;/p&gt;Y xu tzʼubaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.046', 'Are ʼuri xqui chapo, y xqui cʼam bi chuwach i ʼatz laj sacerdóte.
', '', '&lt;span class="verse" id="Mark_14_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Are ʼuri xqui chapo, y xqui cʼam bi chuwach i ʼatz laj sacerdóte.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.047', 'Xui-ri, ʼo jun ʼo chila xresaj li u chʼichʼ y xresaj jun u xiquin i aj takon re i ʼatz laj sacerdóte. ', '', '&lt;span class="verse" id="Mark_14_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Xui-ri, ʼo jun ʼo chila xresaj li u chʼichʼ y xresaj jun u xiquin i aj takon re i ʼatz laj sacerdóte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.048', 'Are ʼuri i Jesus xchʼawic; xu tzʼonoj chique i winak:
—¿Wuchac xix cʼun yix ruʼ i chʼichʼ, ruʼ i cheʼ, qui cʼambej bi we, pacha qui ʼan che jun iliʼom? ', '', '&lt;span class="verse" id="Mark_14_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Are ʼuri i Jesus xchʼawic; xu tzʼonoj chique i winak:&lt;p&gt; &lt;/p&gt;—¿Wuchac xix cʼun yix ruʼ i chʼichʼ, ruʼ i cheʼ, qui cʼambej bi we, pacha qui ʼan che jun iliʼom? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.049', 'ʼIj ʼij in ʼo chixol, pa rachoch i Dios, are quin tijin chu cʼutic chique i winak, y n-xin i chap taj. Xui-ri, ʼo ni u chac ca ʼan na iri chwe pacha i tzʼibtal can chupam u wuj i Dios —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;ʼIj ʼij in ʼo chixol, pa rachoch i Dios, are quin tijin chu cʼutic chique i winak, y n-xin i chap taj. Xui-ri, ʼo ni u chac ca ʼan na iri chwe pacha i tzʼibtal can chupam u wuj i Dios —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.050', 'Are ʼuri conojel u tijoxelab i Jesus xqui canaj canok y xe ʼanmajic.
', '', '&lt;span class="verse" id="Mark_14_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Are ʼuri conojel u tijoxelab i Jesus xqui canaj canok y xe ʼanmajic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.051', 'Xui-ri, xak ʼo jun ala teren bi chirij, xui jun sawan u licʼom chirij. Ire xchaptajic. ', '', '&lt;span class="verse" id="Mark_14_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Xui-ri, xak ʼo jun ala teren bi chirij, xui jun sawan u licʼom chirij. Ire xchaptajic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.052', 'Are xqui chapo, xu cʼak can i sawan chique y chʼanalic xa ʼanmajic.
', '', '&lt;span class="verse" id="Mark_14_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;Are xqui chapo, xu cʼak can i sawan chique y chʼanalic xa ʼanmajic.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.053', 'Teʼuri xqui cʼam bi i Jesus chuwach i ʼatz laj sacerdóte. Chila xqui mol wi quib i je ajwab sacerdóte, xak i nimak tak mamʼib, xak i je tijonel re i ujer ʼatbal tzij. ', '', '&lt;h2&gt;Wach xqui ʼan i aj nucʼbal tʼisbal che i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_14_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;Teʼuri xqui cʼam bi i Jesus chuwach i ʼatz laj sacerdóte. Chila xqui mol wi quib i je ajwab sacerdóte, xak i nimak tak mamʼib, xak i je tijonel re i ujer ʼatbal tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.054', 'I ma Pédro toʼ chi naj xterej bic, cʼa xupon chuwaja re i rachoch i ʼatz laj sacerdóte. Chila xchʼuqui wi chi ʼaʼ cuʼ i policía, cu miʼsaj rib.
', '', '&lt;span class="verse" id="Mark_14_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;I ma Pédro toʼ chi naj xterej bic, cʼa xupon chuwaja re i rachoch i ʼatz laj sacerdóte. Chila xchʼuqui wi chi ʼaʼ cuʼ i policía, cu miʼsaj rib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.055', 'I je cajʼatzil i sacerdóte junam cuʼ i je nimak tak aj nucʼbal tʼisbal xqui tzucuj wach u cojic u mac i Jesus quiqui ʼano, camsabal-re, xui-ri, n-xqui rik taj. ', '', '&lt;span class="verse" id="Mark_14_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;I je cajʼatzil i sacerdóte junam cuʼ i je nimak tak aj nucʼbal tʼisbal xqui tzucuj wach u cojic u mac i Jesus quiqui ʼano, camsabal-re, xui-ri, n-xqui rik taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.056', 'Je qʼui xqui ʼan mentir chirij, xui-ri niʼpa i xqui bij, n-xak jun tu wach i qui tzij. ', '', '&lt;span class="verse" id="Mark_14_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Je qʼui xqui ʼan mentir chirij, xui-ri niʼpa i xqui bij, n-xak jun tu wach i qui tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.057', 'Je ʼo jujun xe walijic y xqui bij i mentir-i chirij i Jesus:
', '', '&lt;span class="verse" id="Mark_14_57"&gt;&lt;strong class="verseno"&gt;57&lt;/strong&gt;&amp;nbsp;Je ʼo jujun xe walijic y xqui bij i mentir-i chirij i Jesus:&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.058', '—Lic ka tom yoj chi xu bij ire chi “Yin quin wulijsaj i rachoch i Dios-i, chi qui ʼanom i winak; teʼuri churox ʼij quin yac jun chic rachoch i Dios chi n-winak ta que yacawic”, xu bij —xqui bij ique.
', '', '&lt;span class="verse" id="Mark_14_58"&gt;&lt;strong class="verseno"&gt;58&lt;/strong&gt;&amp;nbsp;—Lic ka tom yoj chi xu bij ire chi “Yin quin wulijsaj i rachoch i Dios-i, chi qui ʼanom i winak; teʼuri churox ʼij quin yac jun chic rachoch i Dios chi n-winak ta que yacawic”, xu bij —xqui bij ique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.059', 'Queje xqui ʼan ile pacha i qui chʼobom, xui-ri, n-xak ta jun u wach i qui tzij.
', '', '&lt;span class="verse" id="Mark_14_59"&gt;&lt;strong class="verseno"&gt;59&lt;/strong&gt;&amp;nbsp;Queje xqui ʼan ile pacha i qui chʼobom, xui-ri, n-xak ta jun u wach i qui tzij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.060', 'Teʼuri i ʼatz laj sacerdóte xwalij chiquixol y xu bij che i Jesus:
—¿Yet, wuchac n-ca laʼ tu wach chique? ¿Wach usucʼ ile quiqui bij chawij? —xu bij.
', '', '&lt;span class="verse" id="Mark_14_60"&gt;&lt;strong class="verseno"&gt;60&lt;/strong&gt;&amp;nbsp;Teʼuri i ʼatz laj sacerdóte xwalij chiquixol y xu bij che i Jesus:&lt;p&gt; &lt;/p&gt;—¿Yet, wuchac n-ca laʼ tu wach chique? ¿Wach usucʼ ile quiqui bij chawij? —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.061', 'I Jesus lic n-xchʼaw taj; n-xu laʼ tu wach. Are ʼuri i ʼatz laj sacerdóte xu jek chic u tzʼonoxic che. Xu bij che:
—¿Yet nawi at Crísto, chi u Cʼojol i loʼlaj Dios? —xu bij che.
', '', '&lt;span class="verse" id="Mark_14_61"&gt;&lt;strong class="verseno"&gt;61&lt;/strong&gt;&amp;nbsp;I Jesus lic n-xchʼaw taj; n-xu laʼ tu wach. Are ʼuri i ʼatz laj sacerdóte xu jek chic u tzʼonoxic che. Xu bij che:&lt;p&gt; &lt;/p&gt;—¿Yet nawi at Crísto, chi u Cʼojol i loʼlaj Dios? —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.062', 'I Jesus xu bij:
—Katzij, yin. Xak quin iwil ni yin chi in Achi aj Chicaj, in cul na pu wikabim i Dios chi ʼatz u choʼab; xak quin iwil na are quin cʼun pa sutzʼ chicaj —xu bij chique.
', '', '&lt;span class="verse" id="Mark_14_62"&gt;&lt;strong class="verseno"&gt;62&lt;/strong&gt;&amp;nbsp;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Katzij, yin. Xak quin iwil ni yin chi in Achi aj Chicaj, in cul na pu wikabim i Dios chi ʼatz u choʼab; xak quin iwil na are quin cʼun pa sutzʼ chicaj —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.063', 'Are ʼuri, i ʼatz laj sacerdóte xu rakchʼij u ʼuʼ, cʼutbal re i roywal. Xu bij ʼuri chique i je moltajnak:
—Ya coʼon ile. N-ta chic u chac we ʼo juban chic col qui bij u mac. ', '', '&lt;span class="verse" id="Mark_14_63"&gt;&lt;strong class="verseno"&gt;63&lt;/strong&gt;&amp;nbsp;Are ʼuri, i ʼatz laj sacerdóte xu rakchʼij u ʼuʼ, cʼutbal re i roywal. Xu bij ʼuri chique i je moltajnak:&lt;p&gt; &lt;/p&gt;—Ya coʼon ile. N-ta chic u chac we ʼo juban chic col qui bij u mac. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.064', 'Ya xi ta yix u tzij, chi tzel cu ta i Dios. ¿Wach qui chʼob yix che? —xu bij chique.
Ique conojel xqui bij chi ca camsax na.
', '', '&lt;span class="verse" id="Mark_14_64"&gt;&lt;strong class="verseno"&gt;64&lt;/strong&gt;&amp;nbsp;Ya xi ta yix u tzij, chi tzel cu ta i Dios. ¿Wach qui chʼob yix che? —xu bij chique.&lt;p&gt; &lt;/p&gt;Ique conojel xqui bij chi ca camsax na.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.065', 'Are ʼuri ʼo jujun chique xqui jek u chubaxic qui cʼaxaj che, xak ʼo i xqui tzʼapij u palaj ruʼ cʼul, y xqui chʼayo. Xqui bij che:
—Cha bij pachin xat chʼayawic —xe cha che.
Xak i policía xqui chʼay che u palaj.
', '', '&lt;span class="verse" id="Mark_14_65"&gt;&lt;strong class="verseno"&gt;65&lt;/strong&gt;&amp;nbsp;Are ʼuri ʼo jujun chique xqui jek u chubaxic qui cʼaxaj che, xak ʼo i xqui tzʼapij u palaj ruʼ cʼul, y xqui chʼayo. Xqui bij che:&lt;p&gt; &lt;/p&gt;—Cha bij pachin xat chʼayawic —xe cha che.&lt;p&gt; &lt;/p&gt;Xak i policía xqui chʼay che u palaj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.066', 'Tzʼakat queje quiqui ʼan ile, i ma Pédro ʼo kaj chuwaja. Are ʼuri xcʼun jun ali chi aj chac pa rachoch i ʼatz laj sacerdóte. ', '', '&lt;h2&gt;Wach i ruwaxic u wach i Jesus xoʼon i ma Pédro&lt;/h2&gt;&lt;span class="verse" id="Mark_14_66"&gt;&lt;strong class="verseno"&gt;66&lt;/strong&gt;&amp;nbsp;Tzʼakat queje quiqui ʼan ile, i ma Pédro ʼo kaj chuwaja. Are ʼuri xcʼun jun ali chi aj chac pa rachoch i ʼatz laj sacerdóte. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.067', 'Are xrilo chi ma Pédro ca tijin chu miʼsaxic rib chi ʼaʼ, xu cʼam u takexic y xu bij che:
—Xak yet at rachiʼil i ma Jesus aj Nazaret —xu bij che.
', '', '&lt;span class="verse" id="Mark_14_67"&gt;&lt;strong class="verseno"&gt;67&lt;/strong&gt;&amp;nbsp;Are xrilo chi ma Pédro ca tijin chu miʼsaxic rib chi ʼaʼ, xu cʼam u takexic y xu bij che:&lt;p&gt; &lt;/p&gt;—Xak yet at rachiʼil i ma Jesus aj Nazaret —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.068', 'I ma Pédro xruwaj u wach:
—Yin n-wetaʼam tu wach; n-quin ta tu be wach i ca bij —xu bij che.
Teʼuri xu jach rib; xa ʼe pan chibe pa ʼelbal bic; are ʼuri xchʼaw jun teren. ', '', '&lt;span class="verse" id="Mark_14_68"&gt;&lt;strong class="verseno"&gt;68&lt;/strong&gt;&amp;nbsp;I ma Pédro xruwaj u wach:&lt;p&gt; &lt;/p&gt;—Yin n-wetaʼam tu wach; n-quin ta tu be wach i ca bij —xu bij che.&lt;p&gt; &lt;/p&gt;Teʼuri xu jach rib; xa ʼe pan chibe pa ʼelbal bic; are ʼuri xchʼaw jun teren. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.069', 'I ali xu takej chic ucamul, y xu bij chic chique i je ʼo chila:
—I achi-ri, lic cachiʼil i juban —xu bij.
', '', '&lt;span class="verse" id="Mark_14_69"&gt;&lt;strong class="verseno"&gt;69&lt;/strong&gt;&amp;nbsp;I ali xu takej chic ucamul, y xu bij chic chique i je ʼo chila:&lt;p&gt; &lt;/p&gt;—I achi-ri, lic cachiʼil i juban —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.070', 'I ma Pédro xruwaj chic u wach ire. Xoʼon chupan jubiʼ, i je ʼo chila xqui bij chic che i ma Pédro:
—Katzij, yet lic at cachiʼil ique-le, man at aj Galiléa, xak i a chʼawbal lic pacha ique-le —xqui bij.
', '', '&lt;span class="verse" id="Mark_14_70"&gt;&lt;strong class="verseno"&gt;70&lt;/strong&gt;&amp;nbsp;I ma Pédro xruwaj chic u wach ire. Xoʼon chupan jubiʼ, i je ʼo chila xqui bij chic che i ma Pédro:&lt;p&gt; &lt;/p&gt;—Katzij, yet lic at cachiʼil ique-le, man at aj Galiléa, xak i a chʼawbal lic pacha ique-le —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.071', 'I ma Pédro xoc chu cuxtaxic u bi i Dios:
—I Dios chin u ya na pa tojpen we n-katzij ti quin bij. Lic n-wetaʼam tu wach i achi-le, chi qui chʼachʼa chwe —xu bij.
', '', '&lt;span class="verse" id="Mark_14_71"&gt;&lt;strong class="verseno"&gt;71&lt;/strong&gt;&amp;nbsp;I ma Pédro xoc chu cuxtaxic u bi i Dios:&lt;p&gt; &lt;/p&gt;—I Dios chin u ya na pa tojpen we n-katzij ti quin bij. Lic n-wetaʼam tu wach i achi-le, chi qui chʼachʼa chwe —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '14.072', 'Are ʼuri, xchʼaw ucamul acʼ. Teʼuri i ma Pédro xcʼun chu cʼux wach i bital che rumal i Jesus, man u bim che: “Are maja ca chʼaw ucamul acʼ, quin awuwaj oxmul”, u bim che. Are xcʼun chu cʼux ile, xoc che oʼej.
', '', '&lt;span class="verse" id="Mark_14_72"&gt;&lt;strong class="verseno"&gt;72&lt;/strong&gt;&amp;nbsp;Are ʼuri, xchʼaw ucamul acʼ. Teʼuri i ma Pédro xcʼun chu cʼux wach i bital che rumal i Jesus, man u bim che: “Are maja ca chʼaw ucamul acʼ, quin awuwaj oxmul”, u bim che. Are xcʼun chu cʼux ile, xoc che oʼej.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.001', 'Are xsakiric, are ʼuri i cajʼatzil i sacerdóte xqui mol quib cuʼ i nimak tak mamʼib, xak cuʼ i tijonel re i ujer ʼatbal tzij, xak cuʼ conojel i nimak tak aj nucʼbal tʼisbal. Xqui xim i Jesus y xqui cʼam bi ruʼ i ma Piláto, y xqui jach puʼab. ', '', '&lt;h2&gt;Wach xoʼon i Jesus chuwach i ma Piláto&lt;/h2&gt;&lt;span class="verse" id="Mark_15_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xsakiric, are ʼuri i cajʼatzil i sacerdóte xqui mol quib cuʼ i nimak tak mamʼib, xak cuʼ i tijonel re i ujer ʼatbal tzij, xak cuʼ conojel i nimak tak aj nucʼbal tʼisbal. Xqui xim i Jesus y xqui cʼam bi ruʼ i ma Piláto, y xqui jach puʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.002', 'I ma Piláto xu tzʼonoj che:
—¿Yet at ʼAtol Tzij que i aj Israel winak? —xu bij.
I Jesus xu bij:
—Are ile, pacha i xa bij yet —xu bij che.
', '', '&lt;span class="verse" id="Mark_15_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;I ma Piláto xu tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Yet at ʼAtol Tzij que i aj Israel winak? —xu bij.&lt;p&gt; &lt;/p&gt;I Jesus xu bij:&lt;p&gt; &lt;/p&gt;—Are ile, pacha i xa bij yet —xu bij che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.003', 'I je cajʼatzil i sacerdóte, qʼui u mac que tijin chu cojic. ', '', '&lt;span class="verse" id="Mark_15_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I je cajʼatzil i sacerdóte, qʼui u mac que tijin chu cojic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.004', 'I ma Piláto xoc chic chu tzʼonoxic che. Xu bij:
—¿Yet, wuchac n-ca laʼ tu wach chique? Chatapeʼ, qʼui a mac que tijin chu cojic —coʼono.
', '', '&lt;span class="verse" id="Mark_15_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I ma Piláto xoc chic chu tzʼonoxic che. Xu bij:&lt;p&gt; &lt;/p&gt;—¿Yet, wuchac n-ca laʼ tu wach chique? Chatapeʼ, qʼui a mac que tijin chu cojic —coʼono.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.005', 'I Jesus n-xu laʼ tu wach; rumal-i, i ma Piláto lic xu bisoj.
', '', '&lt;span class="verse" id="Mark_15_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;I Jesus n-xu laʼ tu wach; rumal-i, i ma Piláto lic xu bisoj.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.006', 'Junab chi junab, are ʼo i nimaʼij páscua, i ma Piláto ʼo puʼab croʼtaj bi jun prex, pachique cacaj i winak. ', '', '&lt;h2&gt;Xqui bij chi ʼo u chac ca camsax ni Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_15_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Junab chi junab, are ʼo i nimaʼij páscua, i ma Piláto ʼo puʼab croʼtaj bi jun prex, pachique cacaj i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.007', 'ʼO jun achi, ma Barabas u bi, ʼo pa cárcel cuʼ i rachiʼil man xqui mol qui chiʼ i winak chu ʼonquil chʼoʼoj chirij i ʼatol tzij, xak xqui ʼan i camic rumal ile. ', '', '&lt;span class="verse" id="Mark_15_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;ʼO jun achi, ma Barabas u bi, ʼo pa cárcel cuʼ i rachiʼil man xqui mol qui chiʼ i winak chu ʼonquil chʼoʼoj chirij i ʼatol tzij, xak xqui ʼan i camic rumal ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.008', 'Are xe cʼun i winak ruʼ i ma Piláto, xe oc chu tzʼonoxic che chi croʼtaj bi jun prex, pacha ʼax coʼon wi. ', '', '&lt;span class="verse" id="Mark_15_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are xe cʼun i winak ruʼ i ma Piláto, xe oc chu tzʼonoxic che chi croʼtaj bi jun prex, pacha ʼax coʼon wi. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.009', 'Ire xu tzʼonoj chique:
—¿Pachique quiwaj cwoʼtaj bic? ¿Quiwaj cwoʼtaj bi i ʼAtol Tzij yix, ix aj Israel winak? —xu bij chique.
', '', '&lt;span class="verse" id="Mark_15_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Ire xu tzʼonoj chique:&lt;p&gt; &lt;/p&gt;—¿Pachique quiwaj cwoʼtaj bic? ¿Quiwaj cwoʼtaj bi i ʼAtol Tzij yix, ix aj Israel winak? —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.010', 'Queje ile xu bij chique man retaʼam chi i je cajʼatzil i sacerdóte xa ca titat qui pam che i Jesus; are ne rumal-i qui jachom puʼab ire. ', '', '&lt;span class="verse" id="Mark_15_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Queje ile xu bij chique man retaʼam chi i je cajʼatzil i sacerdóte xa ca titat qui pam che i Jesus; are ne rumal-i qui jachom puʼab ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.011', 'Xui-ri, i cajʼatzil i sacerdóte je qui tunum i uqʼuial winak, chi are i ma Barabas coʼtax bic. ', '', '&lt;span class="verse" id="Mark_15_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xui-ri, i cajʼatzil i sacerdóte je qui tunum i uqʼuial winak, chi are i ma Barabas coʼtax bic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.012', 'I ma Piláto xu tzʼonoj chic chique:
—¿Wach i quiwaj yix ʼut quin ʼan che i jun-le chi “qui ʼAtol Tzij i aj Israel” ca bix che?
', '', '&lt;span class="verse" id="Mark_15_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I ma Piláto xu tzʼonoj chic chique:&lt;p&gt; &lt;/p&gt;—¿Wach i quiwaj yix ʼut quin ʼan che i jun-le chi “qui ʼAtol Tzij i aj Israel” ca bix che?&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.013', 'Ique xe siqʼuin chu laʼic u wach y xqui bij:
—¡Cha camsaj chuwach cruz! —xe cha.
', '', '&lt;span class="verse" id="Mark_15_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ique xe siqʼuin chu laʼic u wach y xqui bij:&lt;p&gt; &lt;/p&gt;—¡Cha camsaj chuwach cruz! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.014', 'I ma Piláto xu bij tan chic chique:
—¿Wach u mac u ʼanom? —coʼono.
Ique xa xe siqʼuin tan chic:
—¡Cha camsax chuwach cruz! —xqui bij.
', '', '&lt;span class="verse" id="Mark_15_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;I ma Piláto xu bij tan chic chique:&lt;p&gt; &lt;/p&gt;—¿Wach u mac u ʼanom? —coʼono.&lt;p&gt; &lt;/p&gt;Ique xa xe siqʼuin tan chic:&lt;p&gt; &lt;/p&gt;—¡Cha camsax chuwach cruz! —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.015', 'Teʼuri i ma Piláto xroʼtaj bi ma Barabas, man n-craj ti ire chi ca pe i coywal i winak chirij. Teʼuri xu tak u yaʼic u loʼxic i Jesus, y xu jach bi piquiʼab i que camsaw chuwach cruz.
', '', '&lt;span class="verse" id="Mark_15_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Teʼuri i ma Piláto xroʼtaj bi ma Barabas, man n-craj ti ire chi ca pe i coywal i winak chirij. Teʼuri xu tak u yaʼic u loʼxic i Jesus, y xu jach bi piquiʼab i que camsaw chuwach cruz.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.016', 'Teʼuri i soldádo xqui cʼam bi chuwaja re i rachoch i gobernador. Chila xqui mol wi quib conojel i soldádo cuʼ. ', '', '&lt;span class="verse" id="Mark_15_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Teʼuri i soldádo xqui cʼam bi chuwaja re i rachoch i gobernador. Chila xqui mol wi quib conojel i soldádo cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.017', 'Xqui coj jun u ʼuʼ i Jesus, cakcoj ca tzunic, xak xqui coj jun sotosic qʼuix che u jolom. ', '', '&lt;span class="verse" id="Mark_15_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Xqui coj jun u ʼuʼ i Jesus, cakcoj ca tzunic, xak xqui coj jun sotosic qʼuix che u jolom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.018', 'Teʼuri xe oc chi sicʼ:
—¡Naj u ʼij i ʼAtol Tzij que i aj Israel! —xe cha.
', '', '&lt;span class="verse" id="Mark_15_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Teʼuri xe oc chi sicʼ:&lt;p&gt; &lt;/p&gt;—¡Naj u ʼij i ʼAtol Tzij que i aj Israel! —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.019', 'Xe oc chu chʼayic che u jolom ruʼ jun cheʼ; xak xqui chubaj qui cʼaxaj che, y xe xuqui chuwach, ʼanbal-re pacha quiqui coj u ʼij. ', '', '&lt;span class="verse" id="Mark_15_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xe oc chu chʼayic che u jolom ruʼ jun cheʼ; xak xqui chubaj qui cʼaxaj che, y xe xuqui chuwach, ʼanbal-re pacha quiqui coj u ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.020', 'Are xe utzin chu yoʼyaxic, xquesaj i cʼul cakcoj chi qui cojom che, y xqui coj i u ʼuʼ ire. Teʼuri xqui cʼam bic, y xe ʼe chu camsaxic chuwach cruz.
', '', '&lt;span class="verse" id="Mark_15_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are xe utzin chu yoʼyaxic, xquesaj i cʼul cakcoj chi qui cojom che, y xqui coj i u ʼuʼ ire. Teʼuri xqui cʼam bic, y xe ʼe chu camsaxic chuwach cruz.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.021', 'Are que tijin chi be, xqui cʼulaj jun achi aj Ciréne, ma Simon u bi, chi qui kajaw i ma Alejándro, i ma Rúfo. I achi-le cʼunic-re pa tak jyub. Are xqui cʼulaj, xqui ʼan pen che cu cʼam bi u crusil i Jesus.
', '', '&lt;h2&gt;Wach u ripic i Jesus chuwach cruz&lt;/h2&gt;&lt;span class="verse" id="Mark_15_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Are que tijin chi be, xqui cʼulaj jun achi aj Ciréne, ma Simon u bi, chi qui kajaw i ma Alejándro, i ma Rúfo. I achi-le cʼunic-re pa tak jyub. Are xqui cʼulaj, xqui ʼan pen che cu cʼam bi u crusil i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.022', 'Xqui cʼam bi Jesus che jun ral jyub, Gólgota u bi (i usucʼ i tzij-le queʼel pa ka chʼawbal: “jyub pacha u jolom anma”). ', '', '&lt;span class="verse" id="Mark_15_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Xqui cʼam bi Jesus che jun ral jyub, Gólgota u bi (i usucʼ i tzij-le queʼel pa ka chʼawbal: “jyub pacha u jolom anma”). &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.023', 'Are ʼuri xe oc chu sujuxic víno che, chi ʼo cunbal mírra che (icʼawsabal re u cʼaxcʼol); xui-ri i Jesus n-xu tuʼ taj. ', '', '&lt;span class="verse" id="Mark_15_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are ʼuri xe oc chu sujuxic víno che, chi ʼo cunbal mírra che (icʼawsabal re u cʼaxcʼol); xui-ri i Jesus n-xu tuʼ taj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.024', 'Teʼuri xqui rip chuwach cruz. Are ʼuri i soldádo xe oc chi saquinic chirij u ʼuʼ i Jesus, ʼilbal-re pachin ca chʼacaw bi chique.
', '', '&lt;span class="verse" id="Mark_15_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Teʼuri xqui rip chuwach cruz. Are ʼuri i soldádo xe oc chi saquinic chirij u ʼuʼ i Jesus, ʼilbal-re pachin ca chʼacaw bi chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.025', 'Are i ʼor xqui rip i Jesus chuwach i cruz, are a las nuéve re i raʼanbal ʼij. ', '', '&lt;span class="verse" id="Mark_15_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;Are i ʼor xqui rip i Jesus chuwach i cruz, are a las nuéve re i raʼanbal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.026', 'Pujaʼ ʼan i cruz xqui tzʼibaj juban tzij, ʼalijinsabal-re wach rumal xcamsaxic; queje iri cu bij i tzʼib: “ʼAtol Tzij que i aj Israel winak.” ', '', '&lt;span class="verse" id="Mark_15_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Pujaʼ ʼan i cruz xqui tzʼibaj juban tzij, ʼalijinsabal-re wach rumal xcamsaxic; queje iri cu bij i tzʼib: “ʼAtol Tzij que i aj Israel winak.” &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.027', 'Xak je ʼo queb iliʼomab xe rip chuwach qui cruz junam ruʼ i Jesus: jun pu wikabim, jun pu moxim. ', '', '&lt;span class="verse" id="Mark_15_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xak je ʼo queb iliʼomab xe rip chuwach qui cruz junam ruʼ i Jesus: jun pu wikabim, jun pu moxim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.028', 'Pacha i tzʼibtal can ujer, queje ile xeʼelok, man u bim can i ajbil u tzij i Dios ujer chi “Xqui ʼan che chi xak jun cuʼ i je aj ʼanol mal”. (Queje ile tzʼibtalic.)
', '', '&lt;span class="verse" id="Mark_15_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Pacha i tzʼibtal can ujer, queje ile xeʼelok, man u bim can i ajbil u tzij i Dios ujer chi “Xqui ʼan che chi xak jun cuʼ i je aj ʼanol mal”. (Queje ile tzʼibtalic.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.029', 'Niʼpa i winak que icʼaw chila quiqui sutsa qui jolom che, y quiqui yoʼya. Xqui bij che:
—Yet a bim na ca wulijsaj ni rachoch i Dios, y ca walijsaj chic churox ʼij. ', '', '&lt;span class="verse" id="Mark_15_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Niʼpa i winak que icʼaw chila quiqui sutsa qui jolom che, y quiqui yoʼya. Xqui bij che:&lt;p&gt; &lt;/p&gt;—Yet a bim na ca wulijsaj ni rachoch i Dios, y ca walijsaj chic churox ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.030', 'Bay, ¡cha to ʼuri awib! ¡Cha kajsaj li awib chuwach i cruz! —xe cha che.
', '', '&lt;span class="verse" id="Mark_15_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Bay, ¡cha to ʼuri awib! ¡Cha kajsaj li awib chuwach i cruz! —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.031', 'Xak queje xqui ʼan i cajʼatzil i sacerdóte tak i je tijonel re i ujer ʼatbal tzij; xa xqui yoʼya. Xqui bij chiquiwach:
—Ire-le xe u to i juban, xui-ri n-ca tiqui ta chu toʼic rib —xe cha—. ', '', '&lt;span class="verse" id="Mark_15_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Xak queje xqui ʼan i cajʼatzil i sacerdóte tak i je tijonel re i ujer ʼatbal tzij; xa xqui yoʼya. Xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—Ire-le xe u to i juban, xui-ri n-ca tiqui ta chu toʼic rib —xe cha—. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.032', 'Chu cʼutu na chake we ire are i Crísto, i ka ʼAtol Tzij yoj oj aj Israel. Chu kajsaj li rib woʼor chuwach i cruz, cu cʼutbej-re chake, teʼuri caka cojo —xqui bij.
Xak i iliʼomab chi je riptal ruʼ ire, xak ique xqui yoʼya panok.
', '', '&lt;span class="verse" id="Mark_15_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Chu cʼutu na chake we ire are i Crísto, i ka ʼAtol Tzij yoj oj aj Israel. Chu kajsaj li rib woʼor chuwach i cruz, cu cʼutbej-re chake, teʼuri caka cojo —xqui bij.&lt;p&gt; &lt;/p&gt;Xak i iliʼomab chi je riptal ruʼ ire, xak ique xqui yoʼya panok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.033', 'Are tiqʼuil ʼij, xa ʼekumar ronojel u wach i jyub taʼaj; xu chʼij oxib ʼor re u kajbal ʼij. ', '', '&lt;h2&gt;Wach i camic xoʼon i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_15_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Are tiqʼuil ʼij, xa ʼekumar ronojel u wach i jyub taʼaj; xu chʼij oxib ʼor re u kajbal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.034', 'Teʼuri, co xchʼaw i Jesus:
—Elói, Elói, ¿láma sabactáni? —xu bij.
I usucʼ i tzij-i queʼelok: Wajwal Dios, Wajwal Dios, ¿wuchac in awoʼtam canok? (Queje usucʼ ile i u tzij pa ka chʼawbal.)
', '', '&lt;span class="verse" id="Mark_15_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;Teʼuri, co xchʼaw i Jesus:&lt;p&gt; &lt;/p&gt;—Elói, Elói, ¿láma sabactáni? —xu bij.&lt;p&gt; &lt;/p&gt;I usucʼ i tzij-i queʼelok: Wajwal Dios, Wajwal Dios, ¿wuchac in awoʼtam canok? (Queje usucʼ ile i u tzij pa ka chʼawbal.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.035', 'Je ʼo jujun chila xqui ta; ique xqui bij:
—Chitapeʼ, ire cu siqʼuij i ujer ajbil u tzij i Dios, mam Elías —xe cha.
', '', '&lt;span class="verse" id="Mark_15_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Je ʼo jujun chila xqui ta; ique xqui bij:&lt;p&gt; &lt;/p&gt;—Chitapeʼ, ire cu siqʼuij i ujer ajbil u tzij i Dios, mam Elías —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.036', 'Are ʼuri ʼo jun chique xa ʼe paʼanem y xu mu jun espónja chupam juban víno chʼam. Teʼuri xu chʼap tzam cheʼ, y xu ya ʼan che, man cu tzʼub-bej re. Xu bij:
—Chiwoyʼej we kas ca cʼun i mam Elías chu kajsaxic —xu bij.
', '', '&lt;span class="verse" id="Mark_15_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Are ʼuri ʼo jun chique xa ʼe paʼanem y xu mu jun espónja chupam juban víno chʼam. Teʼuri xu chʼap tzam cheʼ, y xu ya ʼan che, man cu tzʼub-bej re. Xu bij:&lt;p&gt; &lt;/p&gt;—Chiwoyʼej we kas ca cʼun i mam Elías chu kajsaxic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.037', 'Teʼuri i Jesus co xsiqʼuinic, y xcamic. ', '', '&lt;span class="verse" id="Mark_15_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus co xsiqʼuinic, y xcamic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.038', 'Are ʼuri i nim laj cortin pim chi ʼo pa rachoch i Dios xel pa nicʼaj, majan li pujaʼ, cʼa pulew. ', '', '&lt;span class="verse" id="Mark_15_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Are ʼuri i nim laj cortin pim chi ʼo pa rachoch i Dios xel pa nicʼaj, majan li pujaʼ, cʼa pulew. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.039', 'ʼO jun qui capitan i soldádo ʼo chi, chuwach i Jesus. Ire, are xu ta wach i sicʼ xoʼon i Jesus, xak xrilo wach u camic, xu bij:
—Katzij, i achi-ri, lic are u Cʼojol i Dios —xu bij.
', '', '&lt;span class="verse" id="Mark_15_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;ʼO jun qui capitan i soldádo ʼo chi, chuwach i Jesus. Ire, are xu ta wach i sicʼ xoʼon i Jesus, xak xrilo wach u camic, xu bij:&lt;p&gt; &lt;/p&gt;—Katzij, i achi-ri, lic are u Cʼojol i Dios —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.040', 'Xak je ʼo jujun ixokib que taken li cʼa naj; chiquixol ique je ʼo i ati Mariy Magdaléna, xak i ati Mariy chi qui chuch i ma Jose, xak i ma Jacóbo chi chaʼxel; xak ʼo i ati Salome cuʼ. ', '', '&lt;span class="verse" id="Mark_15_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Xak je ʼo jujun ixokib que taken li cʼa naj; chiquixol ique je ʼo i ati Mariy Magdaléna, xak i ati Mariy chi qui chuch i ma Jose, xak i ma Jacóbo chi chaʼxel; xak ʼo i ati Salome cuʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.041', 'I ixokib-i, je terej nak li chirij i Jesus cʼa pa Galiléa, quiqui tobej na re. Xak je qʼui ixokib je ʼo cuʼ chi je upon-nak ruʼ pa Jerusalen.
', '', '&lt;span class="verse" id="Mark_15_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;I ixokib-i, je terej nak li chirij i Jesus cʼa pa Galiléa, quiqui tobej na re. Xak je qʼui ixokib je ʼo cuʼ chi je upon-nak ruʼ pa Jerusalen.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.042', 'ʼO jun achi, ma Jose u bi, aj tinimit Arimatéa. Ire, nim u ʼij chiquixol i je aj nucʼanel aj tʼisanel que i aj Israel winak; ire lic craj cril u jekic i ʼatbal tzij re i Dios. Lic xu cowij u cʼux, xoc bi ruʼ i ma Piláto, y xu tzʼonoj u chʼacul i Jesus, man ya xoc aʼab re i qʼuisbal ʼij chuxeʼ i uxlambal ʼij. Are i ʼij wi chi quiqui yijba qui uxlambal ʼij. ', '', '&lt;h2&gt;Wach u mukic i Jesus&lt;/h2&gt;&lt;span class="verse" id="Mark_15_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;ʼO jun achi, ma Jose u bi, aj tinimit Arimatéa. Ire, nim u ʼij chiquixol i je aj nucʼanel aj tʼisanel que i aj Israel winak; ire lic craj cril u jekic i ʼatbal tzij re i Dios. Lic xu cowij u cʼux, xoc bi ruʼ i ma Piláto, y xu tzʼonoj u chʼacul i Jesus, man ya xoc aʼab re i qʼuisbal ʼij chuxeʼ i uxlambal ʼij. Are i ʼij wi chi quiqui yijba qui uxlambal ʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.044', 'I ma Piláto, are xu ta chi ya camnak chic i Jesus, lic xu bisoj che man lic weʼ xcamic; rumal-i xu siqʼuij i capitan y xu tzʼonoj che we katzij camnak chic. ', '', '&lt;span class="verse" id="Mark_15_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;I ma Piláto, are xu ta chi ya camnak chic i Jesus, lic xu bisoj che man lic weʼ xcamic; rumal-i xu siqʼuij i capitan y xu tzʼonoj che we katzij camnak chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.045', 'Are xu ta wach xu bij i capitan, chi katzij, teʼuri xu ya che i ma Jose que u kajsaj li u chʼacul i Jesus. ', '', '&lt;span class="verse" id="Mark_15_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;Are xu ta wach xu bij i capitan, chi katzij, teʼuri xu ya che i ma Jose que u kajsaj li u chʼacul i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.046', 'I ma Jose xu loʼ jun chom laj sawan, xu kajsaj li u chʼacul i Jesus, y xu pasij chupam. Xe u ya chupam jun jul worom pa abaj; teʼuri xu tzʼapij u chiʼ i jul ruʼ jun abaj. ', '', '&lt;span class="verse" id="Mark_15_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;I ma Jose xu loʼ jun chom laj sawan, xu kajsaj li u chʼacul i Jesus, y xu pasij chupam. Xe u ya chupam jun jul worom pa abaj; teʼuri xu tzʼapij u chiʼ i jul ruʼ jun abaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '15.047', 'I ati Mariy Magdaléna ruʼ i ati Mariy chi u chuch i ma Jose, xquilo pa xqui muk wi.
', '', '&lt;span class="verse" id="Mark_15_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;I ati Mariy Magdaléna ruʼ i ati Mariy chi u chuch i ma Jose, xquilo pa xqui muk wi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.001', 'Are xicʼaw i uxlambal ʼij, i ati Mariy Magdaléna, cuʼ i ati Mariy chi u chuch i ma Jacóbo, xak i ati Salome, xqui loʼ i cunbal muy, quiqui cojbej che i u chʼacul i Jesus. ', '', '&lt;h2&gt;Wach i walijic xoʼon i Jesus chiquixol i camnak&lt;/h2&gt;&lt;span class="verse" id="Mark_16_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Are xicʼaw i uxlambal ʼij, i ati Mariy Magdaléna, cuʼ i ati Mariy chi u chuch i ma Jacóbo, xak i ati Salome, xqui loʼ i cunbal muy, quiqui cojbej che i u chʼacul i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.002', 'Re u sakirbal domíngo, are xel li ʼij, xe upon pa muktal wi i Jesus. ', '', '&lt;span class="verse" id="Mark_16_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Re u sakirbal domíngo, are xel li ʼij, xe upon pa muktal wi i Jesus. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.003', 'I ixokib que tijin chu bixquil chiquiwach:
—¿Pachin nawi coj u to chi relsaxic i abaj chu chiʼ i jul-le? —que cha.
', '', '&lt;span class="verse" id="Mark_16_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;I ixokib que tijin chu bixquil chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Pachin nawi coj u to chi relsaxic i abaj chu chiʼ i jul-le? —que cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.004', 'Xui-ri, are xqui takej, xquil i nim laj abaj chi cojtal can chu chiʼ i jul, n-ta chic chila pu qʼuijibal. ', '', '&lt;span class="verse" id="Mark_16_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xui-ri, are xqui takej, xquil i nim laj abaj chi cojtal can chu chiʼ i jul, n-ta chic chila pu qʼuijibal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.005', 'Teʼuri xe oc pa jul y xquil jun achi, cʼa ala, cul pa qui wikabim; i u ʼuʼ naj rakan, lic sak. Ique lic xqui xij quib.
', '', '&lt;span class="verse" id="Mark_16_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri xe oc pa jul y xquil jun achi, cʼa ala, cul pa qui wikabim; i u ʼuʼ naj rakan, lic sak. Ique lic xqui xij quib.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.006', '—Mi xij iwib —xu bij chique—. Yix quix tijin chu tzucuxic i Jesus aj Nazaret chi xrip chuwach cruz. Ire n-ta chic chi, walijnak chubic; chiwilape pa yatal wi canok. ', '', '&lt;span class="verse" id="Mark_16_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;—Mi xij iwib —xu bij chique—. Yix quix tijin chu tzucuxic i Jesus aj Nazaret chi xrip chuwach cruz. Ire n-ta chic chi, walijnak chubic; chiwilape pa yatal wi canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.007', 'Ji bij chique u tijoxelab, y xak che i ma Pédro, chi i Jesus ya ca ʼe pa Galiléa. “Ire ca ʼe pa Galiléa nabe chiwach yix”, chix chok chique. “Chila quiwil wi pacha u bim can chiwe”, chi bij chique —xu bij.
', '', '&lt;span class="verse" id="Mark_16_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Ji bij chique u tijoxelab, y xak che i ma Pédro, chi i Jesus ya ca ʼe pa Galiléa. “Ire ca ʼe pa Galiléa nabe chiwach yix”, chix chok chique. “Chila quiwil wi pacha u bim can chiwe”, chi bij chique —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.008', 'Are ʼuri i ixokib paʼanem xe ʼel ruʼ i jul; lic que barbatic rumal i qui xibriquil. Lic n-ta jun tzij xqui bij chirij, man lic qui xim quib.
', '', '&lt;span class="verse" id="Mark_16_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Are ʼuri i ixokib paʼanem xe ʼel ruʼ i jul; lic que barbatic rumal i qui xibriquil. Lic n-ta jun tzij xqui bij chirij, man lic qui xim quib.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.009', 'I Jesus, are xwalij chiquixol i camnak, are u sakirbal re i domíngo. Nabe xu cʼut rib che i ati Mariy Magdaléna, chi Jesus resam wukub mal laj tak tew che, oʼonom lok. ', '', '&lt;h2&gt;I Jesus xu cʼut rib che i ati Mariy Magdaléna&lt;/h2&gt;&lt;span class="verse" id="Mark_16_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;I Jesus, are xwalij chiquixol i camnak, are u sakirbal re i domíngo. Nabe xu cʼut rib che i ati Mariy Magdaléna, chi Jesus resam wukub mal laj tak tew che, oʼonom lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.010', 'Teʼuri i ixok xa ʼe chu bixquil chique i je terejnak chirij i Jesus. Are xupon cuʼ, xe u riko lic que bisonic, xak que oʼic. ', '', '&lt;span class="verse" id="Mark_16_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Teʼuri i ixok xa ʼe chu bixquil chique i je terejnak chirij i Jesus. Are xupon cuʼ, xe u riko lic que bisonic, xak que oʼic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.011', 'Are xqui ta chi cʼaslic, xak ʼiltal u wach rumal i ixok-le, n-xqui coj taj.
', '', '&lt;span class="verse" id="Mark_16_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Are xqui ta chi cʼaslic, xak ʼiltal u wach rumal i ixok-le, n-xqui coj taj.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.012', 'Teʼuri i Jesus, jun wi tan chic u cʼutic rib xoʼon chique i queb chi je cachiʼil ique. Xu cʼut rib chique i queb-le are que tijin chi be ʼenam-que pa jyub. ', '', '&lt;span class="verse" id="Mark_16_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus, jun wi tan chic u cʼutic rib xoʼon chique i queb chi je cachiʼil ique. Xu cʼut rib chique i queb-le are que tijin chi be ʼenam-que pa jyub. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.013', 'Ique xe tzalij chic; xe ʼe chu bixquil chique i cachiʼil, xui-ri xak chique i queb-le, i juban n-xqui coj taj, wach i xqui bij.
', '', '&lt;span class="verse" id="Mark_16_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ique xe tzalij chic; xe ʼe chu bixquil chique i cachiʼil, xui-ri xak chique i queb-le, i juban n-xqui coj taj, wach i xqui bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.014', 'Teʼuri i Jesus xu cʼut rib chique i je julajuj u tijoxelab are que tijin chi waʼim. Xe u yaj jubiʼ man n-tu cubibal qui cʼux che we ca walij na; xak co qui jolom, man n-xqui coj taj wach i xbix chique cumal i xe ʼilaw u wach, are walijnak chic. ', '', '&lt;h2&gt;I Jesus xu ya can i chac chique i julajuj u tijoxelab&lt;/h2&gt;&lt;span class="verse" id="Mark_16_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Teʼuri i Jesus xu cʼut rib chique i je julajuj u tijoxelab are que tijin chi waʼim. Xe u yaj jubiʼ man n-tu cubibal qui cʼux che we ca walij na; xak co qui jolom, man n-xqui coj taj wach i xbix chique cumal i xe ʼilaw u wach, are walijnak chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.015', 'Xak xu bij chique:
—Jix, ji paxsaj u tzijol ronojel u wach i jyub taʼaj wach i ʼelbal chi sak; ji bij chique conojel. ', '', '&lt;span class="verse" id="Mark_16_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Xak xu bij chique:&lt;p&gt; &lt;/p&gt;—Jix, ji paxsaj u tzijol ronojel u wach i jyub taʼaj wach i ʼelbal chi sak; ji bij chique conojel. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.016', 'Pachin ca cojow i tzij, xak xkajsax ni ya puwi, quel na ʼuri chupam u mac. Xui-ri, pachin i n-cu coj ta i tzij, ca ʼe na ʼuri pa ʼaʼ. ', '', '&lt;span class="verse" id="Mark_16_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Pachin ca cojow i tzij, xak xkajsax ni ya puwi, quel na ʼuri chupam u mac. Xui-ri, pachin i n-cu coj ta i tzij, ca ʼe na ʼuri pa ʼaʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.017', 'Ique chi quiqui coj na, ʼo ni ʼalijinsabal re u choʼab i Dios cuʼ: ique que quesaj ni itzel tew ruʼ u cuxtaxic in bi yin, xak quiqui rik juban chʼawbal cʼacʼ; ', '', '&lt;span class="verse" id="Mark_16_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Ique chi quiqui coj na, ʼo ni ʼalijinsabal re u choʼab i Dios cuʼ: ique que quesaj ni itzel tew ruʼ u cuxtaxic in bi yin, xak quiqui rik juban chʼawbal cʼacʼ; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.018', 'xak we quiqui chap jun cumatz, o we quiqui tuʼ jun venen, n-ta coʼon chique; xak quiqui ya na qui ʼab piquiwi iwabib, y que utziric —xu bij i Jesus chique.
', '', '&lt;span class="verse" id="Mark_16_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;xak we quiqui chap jun cumatz, o we quiqui tuʼ jun venen, n-ta coʼon chique; xak quiqui ya na qui ʼab piquiwi iwabib, y que utziric —xu bij i Jesus chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.019', 'I Kajwal Jesus, are xutzin che u tzij cuʼ, xcʼam bi chicaj, y xcubi pu wikabim i Dios. ', '', '&lt;h2&gt;Wach i ʼelic bic xoʼon i Jesus chicaj&lt;/h2&gt;&lt;span class="verse" id="Mark_16_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I Kajwal Jesus, are xutzin che u tzij cuʼ, xcʼam bi chicaj, y xcubi pu wikabim i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Mark', '16.020', 'I u tijoxelab xe ʼe chu bixquil u tzij i Dios pa ronojel tak tinimit. I Kajwal xe u to chu bixquil, xak xu ʼan tak i milágro chiquiwach i que tawic, ʼalijinsabal-re chique chi katzij i qui tzij. Amen (katzij).
', '', '&lt;span class="verse" id="Mark_16_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I u tijoxelab xe ʼe chu bixquil u tzij i Dios pa ronojel tak tinimit. I Kajwal xe u to chu bixquil, xak xu ʼan tak i milágro chiquiwach i que tawic, ʼalijinsabal-re chique chi katzij i qui tzij. Amen (katzij).
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.001', 'Quin tzʼibaj i tzij-i chawe yet, at nim laj winak mam Teófilo. ʼUtz xin chʼob yin, quin tzʼibaj pusucʼ niʼpa iri; cwaj cawetamaj chi lic katzij wach a tom yet chirij i Jesucrísto. Je qʼui qui ʼanom pen u tzʼibaxic pusucʼ wach xa ʼani che tak can i ʼij-i. Qui tzʼibam wach i cʼut-tal chake cumal i xe ʼilaw li cʼa chu xebal, xak cumal i je aj paxsanel re u tzijol. Yin xin taqʼui na pa rakan, cʼa chu xebal lok, wach coj tijin chu tayic; ʼutz u ʼonquil xin ʼan che, rumal-i quin bij yin, ʼutz quin tzʼibaj ni usucʼ chawe yet.
', '', '&lt;h2&gt;I ʼUTZ LAJ TZIJ CHIRIJ I JESUCRISTO U TZʼIBAM&lt;/h2&gt;&lt;h1&gt;I SAN LUCAS&lt;/h1&gt;&lt;span class="verse" id="Luke_1_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Quin tzʼibaj i tzij-i chawe yet, at nim laj winak mam Teófilo. ʼUtz xin chʼob yin, quin tzʼibaj pusucʼ niʼpa iri; cwaj cawetamaj chi lic katzij wach a tom yet chirij i Jesucrísto. Je qʼui qui ʼanom pen u tzʼibaxic pusucʼ wach xa ʼani che tak can i ʼij-i. Qui tzʼibam wach i cʼut-tal chake cumal i xe ʼilaw li cʼa chu xebal, xak cumal i je aj paxsanel re u tzijol. Yin xin taqʼui na pa rakan, cʼa chu xebal lok, wach coj tijin chu tayic; ʼutz u ʼonquil xin ʼan che, rumal-i quin bij yin, ʼutz quin tzʼibaj ni usucʼ chawe yet.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.005', 'Are i ma Heródes are i ʼatol tzij che i jyub Judéa, are ʼuri ʼo jun achi, mam Zacarías u bi. Ire sacerdóte, aj cojol qui tzij i winak chuwach i Dios. Ire-le, qui cwenta i jutzobaj sacerdóte re i mam Abías. I rixokil i mam Zacarías, chi ati Wet, lic u muk u xiquin can i nim laj sacerdóte mam Aaron. ', '', '&lt;h2&gt;Wach i xu bij i ángel che i ralaxbal i ma Wan&lt;/h2&gt;&lt;span class="verse" id="Luke_1_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Are i ma Heródes are i ʼatol tzij che i jyub Judéa, are ʼuri ʼo jun achi, mam Zacarías u bi. Ire sacerdóte, aj cojol qui tzij i winak chuwach i Dios. Ire-le, qui cwenta i jutzobaj sacerdóte re i mam Abías. I rixokil i mam Zacarías, chi ati Wet, lic u muk u xiquin can i nim laj sacerdóte mam Aaron. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.006', 'I mam Zacarías, i ati Wet, je ʼutz chuwach i Dios. Lic xe cojon che ronojel u tzij u pixab i Kajwal Dios, lic n-ti qui mac che ile. ', '', '&lt;span class="verse" id="Luke_1_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;I mam Zacarías, i ati Wet, je ʼutz chuwach i Dios. Lic xe cojon che ronojel u tzij u pixab i Kajwal Dios, lic n-ti qui mac che ile. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.007', 'Ique n-ta cacʼal, man i ati Wet n-calan taj; y n-xui ta la ile, xak che queb, je nimak winak chic.
', '', '&lt;span class="verse" id="Luke_1_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Ique n-ta cacʼal, man i ati Wet n-calan taj; y n-xui ta la ile, xak che queb, je nimak winak chic.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.008', 'Xoʼon panok, i mam Zacarías ca tijin chu ʼonquil u patan chuwach i Dios man ya xu rik i ʼij que i jutzobaj re ire. ', '', '&lt;span class="verse" id="Luke_1_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i mam Zacarías ca tijin chu ʼonquil u patan chuwach i Dios man ya xu rik i ʼij que i jutzobaj re ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.009', 'Niʼpa tak i jutzobaj sacerdóte, are i qui módo quiqui saquij na, quiquilbej-re pachin chirij ca tzak wi, chi cu poroj inciénso chuwach i Dios. Che i ʼij-le, i patan xtzak chirij i mam Zacarías, chi are ire coc pa cuárto naj u ʼij pa rachoch i Kajwal Dios. ', '', '&lt;span class="verse" id="Luke_1_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Niʼpa tak i jutzobaj sacerdóte, are i qui módo quiqui saquij na, quiquilbej-re pachin chirij ca tzak wi, chi cu poroj inciénso chuwach i Dios. Che i ʼij-le, i patan xtzak chirij i mam Zacarías, chi are ire coc pa cuárto naj u ʼij pa rachoch i Kajwal Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.010', 'Xoc i mam chila y are ca tijin chu poroxic i inciénso, i uqʼuial winak chi xcoyʼej can chi sak ʼis que tijin chu tzʼonoxic che i Dios. ', '', '&lt;span class="verse" id="Luke_1_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Xoc i mam chila y are ca tijin chu poroxic i inciénso, i uqʼuial winak chi xcoyʼej can chi sak ʼis que tijin chu tzʼonoxic che i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.011', 'Xak teʼet, ʼo jun ángel re i Kajwal xwinakir chuwach i mam Zacarías chila paja. I ángel-le tacʼal pa wikabim re i altar, pa ca porox wi i inciénso. ', '', '&lt;span class="verse" id="Luke_1_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xak teʼet, ʼo jun ángel re i Kajwal xwinakir chuwach i mam Zacarías chila paja. I ángel-le tacʼal pa wikabim re i altar, pa ca porox wi i inciénso. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.012', 'I mam Zacarías, are xrilo, lic xoc ʼil che; lic xu xij rib che. ', '', '&lt;span class="verse" id="Luke_1_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;I mam Zacarías, are xrilo, lic xoc ʼil che; lic xu xij rib che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.013', 'I ángel xu bij che:
—A-Zacarías, ma xij awib. I Dios u tom wach a tzʼonom che. Yet ruʼ awixokil ati Wet ca qʼuiji jun iwacʼal. Are qui coj u bi, chi ya pu bi, a-Wan. ', '', '&lt;span class="verse" id="Luke_1_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I ángel xu bij che:&lt;p&gt; &lt;/p&gt;—A-Zacarías, ma xij awib. I Dios u tom wach a tzʼonom che. Yet ruʼ awixokil ati Wet ca qʼuiji jun iwacʼal. Are qui coj u bi, chi ya pu bi, a-Wan. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.014', 'Yet cat quicot na; lic ʼo ni quicotemal awuʼ. Xak je qʼui i winak que quicot na are calaxic, ', '', '&lt;span class="verse" id="Luke_1_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Yet cat quicot na; lic ʼo ni quicotemal awuʼ. Xak je qʼui i winak que quicot na are calaxic, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.015', 'man i a cʼojol-le, nim u patan che i Dios. Ire-le n-cu tij ti víno, n-cu tij ti awarient. Ire, lic pa ralaxbal lok ucʼam na rumal u Tewal i Dios. ', '', '&lt;span class="verse" id="Luke_1_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;man i a cʼojol-le, nim u patan che i Dios. Ire-le n-cu tij ti víno, n-cu tij ti awarient. Ire, lic pa ralaxbal lok ucʼam na rumal u Tewal i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.016', 'Rumal u cʼutunic ire, je qʼui aj Israel winak quiqui jek tan chic qui cojonic che i Dios; quiqui ʼan tan chic Cajwal che. ', '', '&lt;span class="verse" id="Luke_1_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Rumal u cʼutunic ire, je qʼui aj Israel winak quiqui jek tan chic qui cojonic che i Dios; quiqui ʼan tan chic Cajwal che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.017', 'Ire ca nabeaj na chuwach i Tolque chi ca cʼunic. Pacha u nojbal i mam Elías ujer, queje ni u nojbal ire; xak pacha i ʼo puʼab i mam Elías, xak queje i ʼo na puʼab ire. Ire cu chomarsaj ni qui nojbal i achiab cuʼ i cacʼal. Rumal ire, niʼpa i n-quiqui coj ti Dios, quiqui jek ni u tayic qui tzij i ʼo qui noʼoj rumal i Dios. Queje cu ʼan ire ile, yijbal que i winak, are ya ca cʼun i Cajwal. (Queje ile xu bij i ángel.)
', '', '&lt;span class="verse" id="Luke_1_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Ire ca nabeaj na chuwach i Tolque chi ca cʼunic. Pacha u nojbal i mam Elías ujer, queje ni u nojbal ire; xak pacha i ʼo puʼab i mam Elías, xak queje i ʼo na puʼab ire. Ire cu chomarsaj ni qui nojbal i achiab cuʼ i cacʼal. Rumal ire, niʼpa i n-quiqui coj ti Dios, quiqui jek ni u tayic qui tzij i ʼo qui noʼoj rumal i Dios. Queje cu ʼan ire ile, yijbal que i winak, are ya ca cʼun i Cajwal. (Queje ile xu bij i ángel.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.018', 'I mam Zacarías xu bij che:
—¿Wach i retamaxic quin ʼan yin? Man yin in rij chic; xak i wixokil rij chic —xu bij.
', '', '&lt;span class="verse" id="Luke_1_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;I mam Zacarías xu bij che:&lt;p&gt; &lt;/p&gt;—¿Wach i retamaxic quin ʼan yin? Man yin in rij chic; xak i wixokil rij chic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.019', 'I ángel xu laʼ u wach che:
—Yin in ángel Gabriel, chi in u takon i Dios. Lic ruʼ ire in petnak wi. Xin u tak lok, col in bij i ʼutz laj tzij-i chawe. ', '', '&lt;span class="verse" id="Luke_1_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I ángel xu laʼ u wach che:&lt;p&gt; &lt;/p&gt;—Yin in ángel Gabriel, chi in u takon i Dios. Lic ruʼ ire in petnak wi. Xin u tak lok, col in bij i ʼutz laj tzij-i chawe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.020', 'Xui-ri, yet n-ca coj taj wach i quin bij chawe, rumal-i cat memer na. N-cat chʼaw ta chic cʼa calax ni awacʼal. Wach in bim chawe, yet cawil na are cu rik i ʼij chi u chʼobom i Dios —xu bij i ángel che.
', '', '&lt;span class="verse" id="Luke_1_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Xui-ri, yet n-ca coj taj wach i quin bij chawe, rumal-i cat memer na. N-cat chʼaw ta chic cʼa calax ni awacʼal. Wach in bim chawe, yet cawil na are cu rik i ʼij chi u chʼobom i Dios —xu bij i ángel che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.021', 'I winak coyʼem i relic i mam Zacarías pa rachoch i Dios; lic xqui bisoj man naj xbaytajic. ', '', '&lt;span class="verse" id="Luke_1_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I winak coyʼem i relic i mam Zacarías pa rachoch i Dios; lic xqui bisoj man naj xbaytajic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.022', 'Are xel lok, n-quel ta chic u chʼawbal. Are ʼuri ique xquetamaj chi rilom jun nim laj ʼil chila pa cuárto naj u ʼij, pa rachoch i Dios. Ire toʼ u ʼab chic cu cʼut chique, man n-quel tu chʼawbal.
', '', '&lt;span class="verse" id="Luke_1_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are xel lok, n-quel ta chic u chʼawbal. Are ʼuri ique xquetamaj chi rilom jun nim laj ʼil chila pa cuárto naj u ʼij, pa rachoch i Dios. Ire toʼ u ʼab chic cu cʼut chique, man n-quel tu chʼawbal.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.023', 'Are xqʼuis i ʼij re u patan, xa ʼe chi rachoch. ', '', '&lt;span class="verse" id="Luke_1_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Are xqʼuis i ʼij re u patan, xa ʼe chi rachoch. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.024', 'Xoʼon panok, xqʼuiji i yobil chirij i ati Wet chi rixokil; i ixok, joʼob icʼ n-xel ta chi rachoch; lic ca quicotic xu bij pu cʼux: ', '', '&lt;span class="verse" id="Luke_1_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Xoʼon panok, xqʼuiji i yobil chirij i ati Wet chi rixokil; i ixok, joʼob icʼ n-xel ta chi rachoch; lic ca quicotic xu bij pu cʼux: &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.025', '“I Kajwal Dios cu ya na jun wacʼal chwe. Queje xu ʼan ni ile chwe tobal we. Xresaj in qʼuix chiquiwach i winak,” xu bij.
', '', '&lt;span class="verse" id="Luke_1_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;“I Kajwal Dios cu ya na jun wacʼal chwe. Queje xu ʼan ni ile chwe tobal we. Xresaj in qʼuix chiquiwach i winak,” xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.026', 'Che i wakib icʼ, i Dios xu tak li ángel Gabriel che i tinimit Nazaret chi u cwenta i jyub Galiléa. ', '', '&lt;h2&gt;Wach i xu bij i ángel che i ralaxbal i Jesus&lt;/h2&gt;&lt;span class="verse" id="Luke_1_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Che i wakib icʼ, i Dios xu tak li ángel Gabriel che i tinimit Nazaret chi u cwenta i jyub Galiléa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.027', 'Xu tak bi ruʼ jun ʼapoj ali, al-Mariy u bi, que u bij che wach cu ʼan ni Dios che. I ali-le, ya u yom chic u tzij che jun achi, ma Jose u bi, chi ca cʼuli ruʼ. I ma Jose-le, u muk u xiquin can i ujer ʼatol tzij mam David. ', '', '&lt;span class="verse" id="Luke_1_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;Xu tak bi ruʼ jun ʼapoj ali, al-Mariy u bi, que u bij che wach cu ʼan ni Dios che. I ali-le, ya u yom chic u tzij che jun achi, ma Jose u bi, chi ca cʼuli ruʼ. I ma Jose-le, u muk u xiquin can i ujer ʼatol tzij mam David. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.028', 'Xoc i ángel pa ʼo wi i al-Mariy, y xu ya rutzil u wach. Xu bij che:
—¡Chat quicotok! ¡ʼUtz awe yet! I kajwal Dios lic ʼo awuʼ. Nim ni ʼutz cu ya i Dios pawi yet chiquiwach i juban ixokib —xu bij i ángel che.
', '', '&lt;span class="verse" id="Luke_1_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Xoc i ángel pa ʼo wi i al-Mariy, y xu ya rutzil u wach. Xu bij che:&lt;p&gt; &lt;/p&gt;—¡Chat quicotok! ¡ʼUtz awe yet! I kajwal Dios lic ʼo awuʼ. Nim ni ʼutz cu ya i Dios pawi yet chiquiwach i juban ixokib —xu bij i ángel che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.029', 'I al-Mariy, are xril i ángel, lic xu bisoj i tzij chi xbix che; xu chʼob pu cʼux wuchac queje xu bij i ángel ile che. ', '', '&lt;span class="verse" id="Luke_1_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;I al-Mariy, are xril i ángel, lic xu bisoj i tzij chi xbix che; xu chʼob pu cʼux wuchac queje xu bij i ángel ile che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.030', 'Rumal-i, i ángel xu bij:
—Ma xij awib al-Mariy; i Dios, nim ni rutzil u cʼux chawe. ', '', '&lt;span class="verse" id="Luke_1_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Rumal-i, i ángel xu bij:&lt;p&gt; &lt;/p&gt;—Ma xij awib al-Mariy; i Dios, nim ni rutzil u cʼux chawe. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.031', 'Chatapeʼ, ca pe i yobil chawij; ca qʼuiji jun awacʼal; y ca ya pu bi: A-JESUS. ', '', '&lt;span class="verse" id="Luke_1_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Chatapeʼ, ca pe i yobil chawij; ca qʼuiji jun awacʼal; y ca ya pu bi: A-JESUS. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.032', 'Coʼon panok, nim ni u ʼij; ca bix na che chi u Cʼojol i Dios chi ʼAtz u ʼIj. I Kajwal Dios cu ya puʼab pacha u ʼatbal tzij i u mam, mam David ujer. ', '', '&lt;span class="verse" id="Luke_1_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;Coʼon panok, nim ni u ʼij; ca bix na che chi u Cʼojol i Dios chi ʼAtz u ʼIj. I Kajwal Dios cu ya puʼab pacha u ʼatbal tzij i u mam, mam David ujer. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.033', 'Ire cu ʼat na tzij piquiwi i aj Israel winak queʼe ʼij sak chirij; i u ʼatbal tzij n-tu qʼuisic. (Queje ile xu bij i ángel.)
', '', '&lt;span class="verse" id="Luke_1_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;Ire cu ʼat na tzij piquiwi i aj Israel winak queʼe ʼij sak chirij; i u ʼatbal tzij n-tu qʼuisic. (Queje ile xu bij i ángel.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.034', 'I al-Mariy xu bij che:
—¿Wach u ʼonquil ʼuri? Yin n-ta achi qʼuijinak wuʼ —xu bij.
', '', '&lt;span class="verse" id="Luke_1_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I al-Mariy xu bij che:&lt;p&gt; &lt;/p&gt;—¿Wach u ʼonquil ʼuri? Yin n-ta achi qʼuijinak wuʼ —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.035', 'I ángel xu bij che:
—I u Tewal i Dios ca cʼun na pawi yet; i u choʼab i Dios chi ʼAtz u ʼIj ca kaj nali pawi. Rumal ʼuri i ral acʼal chi calaxic, lic ʼo na re i Dios che. Lic u Cʼojol i Dios ca bix na che. ', '', '&lt;span class="verse" id="Luke_1_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;I ángel xu bij che:&lt;p&gt; &lt;/p&gt;—I u Tewal i Dios ca cʼun na pawi yet; i u choʼab i Dios chi ʼAtz u ʼIj ca kaj nali pawi. Rumal ʼuri i ral acʼal chi calaxic, lic ʼo na re i Dios che. Lic u Cʼojol i Dios ca bix na che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.036', 'Xak i awachalal, ati Wet, i Dios u ʼanom jun milágro che; ya xqʼuiji yobil chirij tupu rij chic. Quiqui bij i winak chi n-calan taj, xui-ri, wakib icʼ ʼo chic chirij. ', '', '&lt;span class="verse" id="Luke_1_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Xak i awachalal, ati Wet, i Dios u ʼanom jun milágro che; ya xqʼuiji yobil chirij tupu rij chic. Quiqui bij i winak chi n-calan taj, xui-ri, wakib icʼ ʼo chic chirij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.037', 'Queje ile u ʼanom i Dios che, man i Dios ʼis ronojel ca tiqui che —xu bij i ángel.
', '', '&lt;span class="verse" id="Luke_1_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Queje ile u ʼanom i Dios che, man i Dios ʼis ronojel ca tiqui che —xu bij i ángel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.038', 'I al-Mariy xu bij:
—Lic in ʼo puʼab i Kajwal Dios; chu ʼana na chwe pacha a bim chwe —xu bij.
Are ʼuri xa ʼe i ángel.
', '', '&lt;span class="verse" id="Luke_1_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;I al-Mariy xu bij:&lt;p&gt; &lt;/p&gt;—Lic in ʼo puʼab i Kajwal Dios; chu ʼana na chwe pacha a bim chwe —xu bij.&lt;p&gt; &lt;/p&gt;Are ʼuri xa ʼe i ángel.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.039', 'Xoʼon chupan jubiʼ, che tak i ʼij-le, i al-Mariy xa ʼec; xu bisoj bi u be, xa ʼe che jun tinimit chujyub, u cwenta i Judéa. ', '', '&lt;h2&gt;I al-Mariy xa ʼe bin ruʼ i tit Wet&lt;/h2&gt;&lt;span class="verse" id="Luke_1_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;Xoʼon chupan jubiʼ, che tak i ʼij-le, i al-Mariy xa ʼec; xu bisoj bi u be, xa ʼe che jun tinimit chujyub, u cwenta i Judéa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.040', 'Are xupon chila, xoc pa rachoch i mam Zacarías y xu tioxij i tit Wet. ', '', '&lt;span class="verse" id="Luke_1_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Are xupon chila, xoc pa rachoch i mam Zacarías y xu tioxij i tit Wet. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.041', 'Ire, are xu ta chi xtioxix rumal i al-Mariy, i acʼal xu yin rib pu chʼacul, xak xcʼun i u Tewal i Dios puwi i tit Wet. ', '', '&lt;span class="verse" id="Luke_1_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;Ire, are xu ta chi xtioxix rumal i al-Mariy, i acʼal xu yin rib pu chʼacul, xak xcʼun i u Tewal i Dios puwi i tit Wet. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.042', 'Are ʼuri, co xchʼawic y xu bij che i al-Mariy:
—I Dios, nim ni ʼutz u yom pawi chiquiwach niʼpa ixokib, xak nim i ʼutz cu ya na puwi awacʼal. ', '', '&lt;span class="verse" id="Luke_1_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are ʼuri, co xchʼawic y xu bij che i al-Mariy:&lt;p&gt; &lt;/p&gt;—I Dios, nim ni ʼutz u yom pawi chiquiwach niʼpa ixokib, xak nim i ʼutz cu ya na puwi awacʼal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.043', 'Nim ni a ʼij yet chin wach yin, n-ca majaw ta chwe chi yet xat cʼun chin solixic, man at u chuch i Kajwal. ', '', '&lt;span class="verse" id="Luke_1_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Nim ni a ʼij yet chin wach yin, n-ca majaw ta chwe chi yet xat cʼun chin solixic, man at u chuch i Kajwal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.044', 'Wetaʼam, ʼo ni u ʼij awacʼal, man are xin a tioxij, i wacʼal chi ʼo pin chʼacul, xu yin rib rumal i quicotemal. ', '', '&lt;span class="verse" id="Luke_1_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;Wetaʼam, ʼo ni u ʼij awacʼal, man are xin a tioxij, i wacʼal chi ʼo pin chʼacul, xu yin rib rumal i quicotemal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.045', 'ʼUtz awe yet man xa cojo chi i Kajwal Dios cu ʼan na pacha u bim chawe —xu bij.
', '', '&lt;span class="verse" id="Luke_1_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;ʼUtz awe yet man xa cojo chi i Kajwal Dios cu ʼan na pacha u bim chawe —xu bij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.046', 'Teʼuri, xu bij i al-Mariy:
—Yin, ronojel in cʼux, lic quin coj u ʼij i Kajwal Dios.
', '', '&lt;span class="verse" id="Luke_1_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Teʼuri, xu bij i al-Mariy:
—Yin, ronojel in cʼux, lic quin coj u ʼij i Kajwal Dios.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.047', 'I wanima yin lic ca quicot rumal i Dios Tolwe.
', '', '&lt;span class="verse" id="Luke_1_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;I wanima yin lic ca quicot rumal i Dios Tolwe.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.048', 'Yin n-nim ti in ʼij, xui-ri i Dios xu ya i ʼutz pinwi, tupu xa in rajchac ire.
Are ʼo wi woʼor, ronojel ʼij sak, conojel i winak quiqui bij na chi Dios nim i ʼutz u yom chwe.
', '', '&lt;span class="verse" id="Luke_1_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;Yin n-nim ti in ʼij, xui-ri i Dios xu ya i ʼutz pinwi, tupu xa in rajchac ire.
Are ʼo wi woʼor, ronojel ʼij sak, conojel i winak quiqui bij na chi Dios nim i ʼutz u yom chwe.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.049', 'Queje quiqui bij ni ique ile, man i Dios chi ʼAtz u Choʼab, nim ni ʼutz u ʼanom chwe.
Lic nim u ʼij u bi.
', '', '&lt;span class="verse" id="Luke_1_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;Queje quiqui bij ni ique ile, man i Dios chi ʼAtz u Choʼab, nim ni ʼutz u ʼanom chwe.
Lic nim u ʼij u bi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.050', 'Ire nic are wi cril toʼbal qui wach niʼpa i winak quiqui xij quib chuwach; queje u ʼanom ile chique i katit ka mam;
xak queje cu ʼan ile chique i ka muk ka xiquin, we quiqui xij quib chuwach.
', '', '&lt;span class="verse" id="Luke_1_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Ire nic are wi cril toʼbal qui wach niʼpa i winak quiqui xij quib chuwach; queje u ʼanom ile chique i katit ka mam;
xak queje cu ʼan ile chique i ka muk ka xiquin, we quiqui xij quib chuwach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.051', 'Ruʼ i nim laj u choʼab, nim i u ʼanom;
u kajsam qui ʼij niʼpa i quiqui ʼan nim che quib, man quiqui na ique ʼo qui nojbal.
', '', '&lt;span class="verse" id="Luke_1_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Ruʼ i nim laj u choʼab, nim i u ʼanom;
u kajsam qui ʼij niʼpa i quiqui ʼan nim che quib, man quiqui na ique ʼo qui nojbal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.052', 'Ire resam i nimak tak ʼatol tzij che qui patan;
i niʼpa i n-ti qui ʼij, ire xu ya qui ʼij.
', '', '&lt;span class="verse" id="Luke_1_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;Ire resam i nimak tak ʼatol tzij che qui patan;
i niʼpa i n-ti qui ʼij, ire xu ya qui ʼij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.053', 'Niʼpa i que numic, nim i ʼutz u yom chique;
xui-ri i beyomab, lic n-ta xu ya chique.
', '', '&lt;span class="verse" id="Luke_1_53"&gt;&lt;strong class="verseno"&gt;53&lt;/strong&gt;&amp;nbsp;Niʼpa i que numic, nim i ʼutz u yom chique;
xui-ri i beyomab, lic n-ta xu ya chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.054', 'Xoj u to yoj oj aj Israel, chi oj rajchac ire; xcʼun chu cʼux chi cril toʼbal ka wach.
', '', '&lt;span class="verse" id="Luke_1_54"&gt;&lt;strong class="verseno"&gt;54&lt;/strong&gt;&amp;nbsp;Xoj u to yoj oj aj Israel, chi oj rajchac ire; xcʼun chu cʼux chi cril toʼbal ka wach.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.055', 'Xu ʼan na pacha u bim can chique i katit ka mam, che mam Abraham, xak chique i u muk u xiquin queʼe ʼij sak chiquij.
(Queje ile xu bij i al-Mariy.)
', '', '&lt;span class="verse" id="Luke_1_55"&gt;&lt;strong class="verseno"&gt;55&lt;/strong&gt;&amp;nbsp;Xu ʼan na pacha u bim can chique i katit ka mam, che mam Abraham, xak chique i u muk u xiquin queʼe ʼij sak chiquij.
(Queje ile xu bij i al-Mariy.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.056', 'Ire xcanaj ruʼ i tit Wet colo oxib icʼ, teʼuri xtzalij chi rachoch.
', '', '&lt;span class="verse" id="Luke_1_56"&gt;&lt;strong class="verseno"&gt;56&lt;/strong&gt;&amp;nbsp;Ire xcanaj ruʼ i tit Wet colo oxib icʼ, teʼuri xtzalij chi rachoch.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.057', 'I tit Wet, are xu rik u ʼij, xiwajic; xqʼuiji jun racʼal, ral ala. ', '', '&lt;h2&gt;Wach i ralaxbal i a-Wan&lt;/h2&gt;&lt;span class="verse" id="Luke_1_57"&gt;&lt;strong class="verseno"&gt;57&lt;/strong&gt;&amp;nbsp;I tit Wet, are xu rik u ʼij, xiwajic; xqʼuiji jun racʼal, ral ala. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.058', 'Niʼpa i je ʼo chirij chuwach, xak niʼpa i rachalal, ique xqui ta chi nim ni rutzil u cʼux i Kajwal Dios oʼonom che; ʼis xe ʼe quicot ruʼ. ', '', '&lt;span class="verse" id="Luke_1_58"&gt;&lt;strong class="verseno"&gt;58&lt;/strong&gt;&amp;nbsp;Niʼpa i je ʼo chirij chuwach, xak niʼpa i rachalal, ique xqui ta chi nim ni rutzil u cʼux i Kajwal Dios oʼonom che; ʼis xe ʼe quicot ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.059', 'Are xtzʼakat i wajxakib ʼij, xe ʼe chu cojic i retal que i aj Israel winak che u chʼacul i acʼal. Cacaj ique quiqui ya pu bi, a-Zacarías, pacha i u bi u kajaw. ', '', '&lt;span class="verse" id="Luke_1_59"&gt;&lt;strong class="verseno"&gt;59&lt;/strong&gt;&amp;nbsp;Are xtzʼakat i wajxakib ʼij, xe ʼe chu cojic i retal que i aj Israel winak che u chʼacul i acʼal. Cacaj ique quiqui ya pu bi, a-Zacarías, pacha i u bi u kajaw. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.060', 'I u chuch xu bij:
—N-are taj. I u bi: a-Wan —xu bij.
', '', '&lt;span class="verse" id="Luke_1_60"&gt;&lt;strong class="verseno"&gt;60&lt;/strong&gt;&amp;nbsp;I u chuch xu bij:&lt;p&gt; &lt;/p&gt;—N-are taj. I u bi: a-Wan —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.061', '—¿Wuchac ʼuri? —xqui bij che—. Mi jun chique awachalal ʼo i u bi queje ile —xe cha.
', '', '&lt;span class="verse" id="Luke_1_61"&gt;&lt;strong class="verseno"&gt;61&lt;/strong&gt;&amp;nbsp;—¿Wuchac ʼuri? —xqui bij che—. Mi jun chique awachalal ʼo i u bi queje ile —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.062', 'Ique teʼuri xqui tzʼonoj che u kajaw ruʼ qui ʼab wach u bi craj cu ya che. ', '', '&lt;span class="verse" id="Luke_1_62"&gt;&lt;strong class="verseno"&gt;62&lt;/strong&gt;&amp;nbsp;Ique teʼuri xqui tzʼonoj che u kajaw ruʼ qui ʼab wach u bi craj cu ya che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.063', 'Are ʼuri i u kajaw xu tzʼonoj chʼakap wuj chique, man cu tzʼibaj chuwach. “A-Wan u bi,” xu tzʼibaj. Conojel xqui bisoj che, wach i quilom. ', '', '&lt;span class="verse" id="Luke_1_63"&gt;&lt;strong class="verseno"&gt;63&lt;/strong&gt;&amp;nbsp;Are ʼuri i u kajaw xu tzʼonoj chʼakap wuj chique, man cu tzʼibaj chuwach. “A-Wan u bi,” xu tzʼibaj. Conojel xqui bisoj che, wach i quilom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.064', 'Are ʼuri i mam Zacarías juntir xel chic u chʼawbal; xu jek chʼaʼatic y xu bij u ʼij i Dios. ', '', '&lt;span class="verse" id="Luke_1_64"&gt;&lt;strong class="verseno"&gt;64&lt;/strong&gt;&amp;nbsp;Are ʼuri i mam Zacarías juntir xel chic u chʼawbal; xu jek chʼaʼatic y xu bij u ʼij i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.065', 'Niʼpa i je aj chirij chuwach lic xqui bisoj. Xel u tzijol chuwi tak jyub re i Judéa: i winak ʼis xqui lapo wach i xa ʼanic. ', '', '&lt;span class="verse" id="Luke_1_65"&gt;&lt;strong class="verseno"&gt;65&lt;/strong&gt;&amp;nbsp;Niʼpa i je aj chirij chuwach lic xqui bisoj. Xel u tzijol chuwi tak jyub re i Judéa: i winak ʼis xqui lapo wach i xa ʼanic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.066', 'Niʼpa i winak chi xe taw ile, xe oc chi chʼobonic che, y xqui tzʼonoj chiquiwach:
—¿Bi chi acʼalil ile? ¿Wach nawi u patan?
—xqui bij.
Queje ile xqui bij, man ʼalaj chi i Kajwal Dios lic ʼo ruʼ.
', '', '&lt;span class="verse" id="Luke_1_66"&gt;&lt;strong class="verseno"&gt;66&lt;/strong&gt;&amp;nbsp;Niʼpa i winak chi xe taw ile, xe oc chi chʼobonic che, y xqui tzʼonoj chiquiwach:&lt;p&gt; &lt;/p&gt;—¿Bi chi acʼalil ile? ¿Wach nawi u patan?
—xqui bij.
Queje ile xqui bij, man ʼalaj chi i Kajwal Dios lic ʼo ruʼ.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.067', 'I mam Zacarías, chi u kajaw i a-Wan, xcʼun i u Tewal i Dios puwi, y xu jek ʼuri u bixquil i tzij-i:
', '', '&lt;h2&gt;Wach u cojic u ʼij i Dios xu ʼan i mam Zacarías&lt;/h2&gt;&lt;span class="verse" id="Luke_1_67"&gt;&lt;strong class="verseno"&gt;67&lt;/strong&gt;&amp;nbsp;I mam Zacarías, chi u kajaw i a-Wan, xcʼun i u Tewal i Dios puwi, y xu jek ʼuri u bixquil i tzij-i:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.068', 'Chaka bij u ʼij i Kajwal Dios, chi ka Dios oj aj Israel winak;
ire xoj ol u to ni yoj, chi oj u winak ire; u tojom i kelsaxic chi sak.
', '', '&lt;span class="verse" id="Luke_1_68"&gt;&lt;strong class="verseno"&gt;68&lt;/strong&gt;&amp;nbsp;Chaka bij u ʼij i Kajwal Dios, chi ka Dios oj aj Israel winak;
ire xoj ol u to ni yoj, chi oj u winak ire; u tojom i kelsaxic chi sak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.069', 'U yom jun Tolke chi nim u choʼab; ire-le, u muk u xiquin can i mam David, chi lic rajchac i Dios ujer.
', '', '&lt;span class="verse" id="Luke_1_69"&gt;&lt;strong class="verseno"&gt;69&lt;/strong&gt;&amp;nbsp;U yom jun Tolke chi nim u choʼab; ire-le, u muk u xiquin can i mam David, chi lic rajchac i Dios ujer.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.070', 'Queje ile u bim canok chi cu ʼan na; lic ujer queje ile xu bij chique i je u loʼlaj ajbil u tzij.
', '', '&lt;span class="verse" id="Luke_1_70"&gt;&lt;strong class="verseno"&gt;70&lt;/strong&gt;&amp;nbsp;Queje ile u bim canok chi cu ʼan na; lic ujer queje ile xu bij chique i je u loʼlaj ajbil u tzij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.071', 'Ire xu bij chi coj u to na chiquiwach i je aj chʼoʼoj chikij, coj u to na chiquiwach niʼpa i tzel coj quilo.
', '', '&lt;span class="verse" id="Luke_1_71"&gt;&lt;strong class="verseno"&gt;71&lt;/strong&gt;&amp;nbsp;Ire xu bij chi coj u to na chiquiwach i je aj chʼoʼoj chikij, coj u to na chiquiwach niʼpa i tzel coj quilo.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.072', 'Queje ile u ʼanom chake, man queje ile u bim can chique i katit ka mam rumal i rutzil u cʼux.
Queje ile u ʼanom chake man n-cu sach ta chu cʼux wach i trat u ʼanom can cuʼ ique ujer.
', '', '&lt;span class="verse" id="Luke_1_72"&gt;&lt;strong class="verseno"&gt;72&lt;/strong&gt;&amp;nbsp;Queje ile u ʼanom chake, man queje ile u bim can chique i katit ka mam rumal i rutzil u cʼux.
Queje ile u ʼanom chake man n-cu sach ta chu cʼux wach i trat u ʼanom can cuʼ ique ujer.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.073', 'I trat-le, i Dios lic xu bij che i ka mam, mam Abraham, chi cu ʼan na.
', '', '&lt;span class="verse" id="Luke_1_73"&gt;&lt;strong class="verseno"&gt;73&lt;/strong&gt;&amp;nbsp;I trat-le, i Dios lic xu bij che i ka mam, mam Abraham, chi cu ʼan na.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.074', 'Xu bij chi coj resaj piquiʼab i je aj chʼoʼoj chikij man ʼutz n-caka xij ta kib chu ʼanic ka patan che ire;
', '', '&lt;span class="verse" id="Luke_1_74"&gt;&lt;strong class="verseno"&gt;74&lt;/strong&gt;&amp;nbsp;Xu bij chi coj resaj piquiʼab i je aj chʼoʼoj chikij man ʼutz n-caka xij ta kib chu ʼanic ka patan che ire;
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.075', 'xak caka ʼan ka patan chirij, lic oj chʼam, lic oj ʼutz chuwach, niʼpa i ʼij re i ka cʼaslemal.
(Queje ile xu bij i mam Zacarías; teʼuri xu bij che i acʼal:)
', '', '&lt;span class="verse" id="Luke_1_75"&gt;&lt;strong class="verseno"&gt;75&lt;/strong&gt;&amp;nbsp;xak caka ʼan ka patan chirij, lic oj chʼam, lic oj ʼutz chuwach, niʼpa i ʼij re i ka cʼaslemal.
(Queje ile xu bij i mam Zacarías; teʼuri xu bij che i acʼal:)
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.076', 'Xak yet, ral wacʼal, quiqui bij ni winak chi yet at ajbil u tzij i Dios chi ʼAtz u ʼIj.
Queje quiqui bij na chawe, man cat nabeaj chuwach i Kajwal, que a yijba i winak chu tayic u tzij.
', '', '&lt;span class="verse" id="Luke_1_76"&gt;&lt;strong class="verseno"&gt;76&lt;/strong&gt;&amp;nbsp;Xak yet, ral wacʼal, quiqui bij ni winak chi yet at ajbil u tzij i Dios chi ʼAtz u ʼIj.
Queje quiqui bij na chawe, man cat nabeaj chuwach i Kajwal, que a yijba i winak chu tayic u tzij.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.077', 'Yet ca cʼut na chique i je re ire chi ʼo ni sachbal qui mac, ʼelsabal-que chi sak.
', '', '&lt;span class="verse" id="Luke_1_77"&gt;&lt;strong class="verseno"&gt;77&lt;/strong&gt;&amp;nbsp;Yet ca cʼut na chique i je re ire chi ʼo ni sachbal qui mac, ʼelsabal-que chi sak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.078', 'I ka Dios cʼax coj u naʼo, lic cril toʼbal ka wach;
rumal ile, cu tak na li jun aj chicaj; i u cʼunic, pacha coʼon i sak re u sakirbal.
', '', '&lt;span class="verse" id="Luke_1_78"&gt;&lt;strong class="verseno"&gt;78&lt;/strong&gt;&amp;nbsp;I ka Dios cʼax coj u naʼo, lic cril toʼbal ka wach;
rumal ile, cu tak na li jun aj chicaj; i u cʼunic, pacha coʼon i sak re u sakirbal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.079', 'Ire cu cʼam na li sak chique i je ʼo pa ʼekum;
cu cʼam li sak chique i quiqui xij quib che i camic. Coj u cʼam bi pa ʼo wi i quicotemal.
(Queje ile xu bij i mam Zacarías.)
', '', '&lt;span class="verse" id="Luke_1_79"&gt;&lt;strong class="verseno"&gt;79&lt;/strong&gt;&amp;nbsp;Ire cu cʼam na li sak chique i je ʼo pa ʼekum;
cu cʼam li sak chique i quiqui xij quib che i camic. Coj u cʼam bi pa ʼo wi i quicotemal.
(Queje ile xu bij i mam Zacarías.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '1.080', 'I acʼal, chi a-Wan, ca tijin u qʼuiyic, xak ca tijin u qʼuiyic u cubibal u cʼux che i Dios. Are achi chic, xa ʼe jekel che i jyub tzʼinilic. Chila xqʼuiji wi cʼa xu rik ni ʼij re u cʼutic rib chique i aj Israel winak.
', '', '&lt;span class="verse" id="Luke_1_80"&gt;&lt;strong class="verseno"&gt;80&lt;/strong&gt;&amp;nbsp;I acʼal, chi a-Wan, ca tijin u qʼuiyic, xak ca tijin u qʼuiyic u cubibal u cʼux che i Dios. Are achi chic, xa ʼe jekel che i jyub tzʼinilic. Chila xqʼuiji wi cʼa xu rik ni ʼij re u cʼutic rib chique i aj Israel winak.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.001', 'Che tak i ʼij-le, are i ʼatz laj ʼatol tzij are i ma Augústo César. Ire xresaj li jun ʼatbal tzij chi ʼis ca tzʼibax qui bi i winak pa qui tinimit, ronojel tak i tinimit chi ʼo puʼab ire. ', '', '&lt;h2&gt;I ralaxbal i Jesus&lt;/h2&gt;&lt;span class="verse" id="Luke_2_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Che tak i ʼij-le, are i ʼatz laj ʼatol tzij are i ma Augústo César. Ire xresaj li jun ʼatbal tzij chi ʼis ca tzʼibax qui bi i winak pa qui tinimit, ronojel tak i tinimit chi ʼo puʼab ire. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.002', 'Ile are i nabe rakan chi xu ʼan i ʼatbal tzij-i. Are xu ʼan ile, are i ma Cirénio are i gobernador pa jyub Síria. ', '', '&lt;span class="verse" id="Luke_2_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Ile are i nabe rakan chi xu ʼan i ʼatbal tzij-i. Are xu ʼan ile, are i ma Cirénio are i gobernador pa jyub Síria. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.003', 'Conojel i winak ʼis que ʼe ʼuri chu tzʼibaxic qui bi pa tak tinimit pa je petnak wi i catit qui mam. ', '', '&lt;span class="verse" id="Luke_2_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Conojel i winak ʼis que ʼe ʼuri chu tzʼibaxic qui bi pa tak tinimit pa je petnak wi i catit qui mam. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.004', 'I ma Jose, jekel pa tinimit Nazaret, chi u cwenta i Galiléa. Ire, u muk u xiquin can i rey mam David. Rumal i ʼatbal tzij chi xel lok, ʼo ni u chac ca ʼe pu tinimit pa alaxnak wi i mam David ujer. I tinimit-le are i Belen, u cwenta i jyub Judéa. Xe ʼe ʼuri i ma Jose pa Belen junam ruʼ i al-Mariy chi yatal chic che chi rixokil. Are xe ʼec, i al-Mariy iwab winak chic. ', '', '&lt;span class="verse" id="Luke_2_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I ma Jose, jekel pa tinimit Nazaret, chi u cwenta i Galiléa. Ire, u muk u xiquin can i rey mam David. Rumal i ʼatbal tzij chi xel lok, ʼo ni u chac ca ʼe pu tinimit pa alaxnak wi i mam David ujer. I tinimit-le are i Belen, u cwenta i jyub Judéa. Xe ʼe ʼuri i ma Jose pa Belen junam ruʼ i al-Mariy chi yatal chic che chi rixokil. Are xe ʼec, i al-Mariy iwab winak chic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.006', 'Are je ʼo pa tinimit Belen, xu rik u ʼij chirij i ali-le; ', '', '&lt;span class="verse" id="Luke_2_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Are je ʼo pa tinimit Belen, xu rik u ʼij chirij i ali-le; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.007', 'chila xqʼuiji wi i nabe racʼal, ral ala. Ique n-xqui rik ti qui qʼuijibal pa meson, rumal-i i al-Mariy xa xu pasij i racʼal pa tak cʼul, y xu cotzʼba pu waybal chicop.
', '', '&lt;span class="verse" id="Luke_2_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;chila xqʼuiji wi i nabe racʼal, ral ala. Ique n-xqui rik ti qui qʼuijibal pa meson, rumal-i i al-Mariy xa xu pasij i racʼal pa tak cʼul, y xu cotzʼba pu waybal chicop.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.008', 'Nakaj che i tinimit Belen, je ʼo juban aj chajil chij pa tak ʼes, que tijin chu chajixic qui chij chaʼab. ', '', '&lt;h2&gt;Wach xu bij i ángel chique i je aj yuʼul chij&lt;/h2&gt;&lt;span class="verse" id="Luke_2_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;Nakaj che i tinimit Belen, je ʼo juban aj chajil chij pa tak ʼes, que tijin chu chajixic qui chij chaʼab. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.009', 'Xak teʼet, jun ángel re i Kajwal Dios xwinakir chiquiwach. Xak i u chomal i Kajwal Dios lic ca walchʼin chiquij chiquiwach. Ique lic xqui xij quib. ', '', '&lt;span class="verse" id="Luke_2_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Xak teʼet, jun ángel re i Kajwal Dios xwinakir chiquiwach. Xak i u chomal i Kajwal Dios lic ca walchʼin chiquij chiquiwach. Ique lic xqui xij quib. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.010', 'I ángel xu bij chique:
—Mi xij iwib. Yin xa col in bij jun ʼutz laj tzij chiwe chi ʼis que quicot ni winak rumal. ', '', '&lt;span class="verse" id="Luke_2_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;I ángel xu bij chique:&lt;p&gt; &lt;/p&gt;—Mi xij iwib. Yin xa col in bij jun ʼutz laj tzij chiwe chi ʼis que quicot ni winak rumal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.011', 'Woʼor xalax jun Toliwe pa Belen, chi u tinimit can i mam David ujer. Are i chatal rumal i Dios: i CRISTO, chi Kajwal. ', '', '&lt;span class="verse" id="Luke_2_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Woʼor xalax jun Toliwe pa Belen, chi u tinimit can i mam David ujer. Are i chatal rumal i Dios: i CRISTO, chi Kajwal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.012', 'Are i cʼutbal-re chiwe chi katzij i quin bij, we qui tzucuj, qui rik ni acʼal cotzʼbam pa jun u waybal chicop, pasim pa tak cʼul. (Queje ile xu bij i ángel chique.)
', '', '&lt;span class="verse" id="Luke_2_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Are i cʼutbal-re chiwe chi katzij i quin bij, we qui tzucuj, qui rik ni acʼal cotzʼbam pa jun u waybal chicop, pasim pa tak cʼul. (Queje ile xu bij i ángel chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.013', 'Are xoʼon ile, xak teʼet xe winakir uqʼuial ángel aj chicaj ruʼ i jun. Ique, que tijin chu cojic u ʼij i Dios; xqui bij:
', '', '&lt;span class="verse" id="Luke_2_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Are xoʼon ile, xak teʼet xe winakir uqʼuial ángel aj chicaj ruʼ i jun. Ique, que tijin chu cojic u ʼij i Dios; xqui bij:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.014', '“¡Chocsax u ʼij i Dios chila chicaj!
Chuxlan qui cʼux i winak chuwach i jyub taʼaj,
chi je u chom ire,” xqui bij i ángel.
', '', '&lt;span class="verse" id="Luke_2_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;“¡Chocsax u ʼij i Dios chila chicaj!
Chuxlan qui cʼux i winak chuwach i jyub taʼaj,
chi je u chom ire,” xqui bij i ángel.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.015', 'Are xqui bij ile, xe tzalij chic chicaj; teʼuri i aj chajil chij xqui bij chiquiwach:
—Jo pa Belen, cakilbej re wach i xa ʼanic chi u ʼalijinsam i Kajwal Dios chake —xqui bij.
', '', '&lt;span class="verse" id="Luke_2_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;Are xqui bij ile, xe tzalij chic chicaj; teʼuri i aj chajil chij xqui bij chiquiwach:&lt;p&gt; &lt;/p&gt;—Jo pa Belen, cakilbej re wach i xa ʼanic chi u ʼalijinsam i Kajwal Dios chake —xqui bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.016', 'Teʼuri i je aj chajil chij xe ʼe paʼanem cʼa Belen. Are xe uponic, xqui rik i al-Mariy ruʼ i ma Jose, xak i acʼal cotzʼbam pu waybal chicop. ', '', '&lt;span class="verse" id="Luke_2_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Teʼuri i je aj chajil chij xe ʼe paʼanem cʼa Belen. Are xe uponic, xqui rik i al-Mariy ruʼ i ma Jose, xak i acʼal cotzʼbam pu waybal chicop. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.017', 'Are xquilo xqui bij chique i je ʼo chila wach i u bim i ángel chirij i acʼal-le. ', '', '&lt;span class="verse" id="Luke_2_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Are xquilo xqui bij chique i je ʼo chila wach i u bim i ángel chirij i acʼal-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.018', 'Conojel i xe tawic lic xqui bisoj wach i xbix chique cumal i je aj chajil chij. ', '', '&lt;span class="verse" id="Luke_2_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Conojel i xe tawic lic xqui bisoj wach i xbix chique cumal i je aj chajil chij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.019', 'I al-Mariy toʼ xu ta wach i xqui bij. Xu cʼol i qui tzij pu cʼux; toʼ xu chʼobo. ', '', '&lt;span class="verse" id="Luke_2_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;I al-Mariy toʼ xu ta wach i xqui bij. Xu cʼol i qui tzij pu cʼux; toʼ xu chʼobo. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.020', 'I je aj chajil chij xe tzalij chic pa i je petnak wi. Are xe ʼec, que tijin chu cojic u ʼij i Dios; xqui bij chi lic nim u ʼij, rumal ronojel i qui tom, xak i quilom. Xeʼelok lic katzij pacha i u bim i ángel chique.
', '', '&lt;span class="verse" id="Luke_2_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;I je aj chajil chij xe tzalij chic pa i je petnak wi. Are xe ʼec, que tijin chu cojic u ʼij i Dios; xqui bij chi lic nim u ʼij, rumal ronojel i qui tom, xak i quilom. Xeʼelok lic katzij pacha i u bim i ángel chique.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.021', 'Xel wajxakib ʼij, xqui coj i retal que i aj Israel winak che u chʼacul i acʼal; are ʼuri xqui ya pu bi A-JESUS. Are u bi ile chi bital che i al-Mariy rumal i ángel, are cʼa maja ca qʼuiji i yobil chirij.
', '', '&lt;h2&gt;Wach u jachic i acʼal Jesus che i Dios&lt;/h2&gt;&lt;span class="verse" id="Luke_2_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;Xel wajxakib ʼij, xqui coj i retal que i aj Israel winak che u chʼacul i acʼal; are ʼuri xqui ya pu bi A-JESUS. Are u bi ile chi bital che i al-Mariy rumal i ángel, are cʼa maja ca qʼuiji i yobil chirij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.022', 'Are xtzʼakat u ʼij i ixok Mariy, xe ʼe pa Jerusalen, quiqui ʼanbej rawasil re u walijic, pacha cu bij i ujer ʼatbal tzij re i mam Moises. Xe ʼe chu jachic i acʼal che i Kajwal Dios. ', '', '&lt;span class="verse" id="Luke_2_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;Are xtzʼakat u ʼij i ixok Mariy, xe ʼe pa Jerusalen, quiqui ʼanbej rawasil re u walijic, pacha cu bij i ujer ʼatbal tzij re i mam Moises. Xe ʼe chu jachic i acʼal che i Kajwal Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.023', 'Queje xqui ʼan ique ile rumal i tzij-i chi tzʼibtal can chupam u ʼatbal tzij i Kajwal Dios; queje iri cu bij: “Are calax i nabe acʼal, we ala, ʼo u chac ca jach na puʼab i Dios.” Queje ile tzʼibtal canok. ', '', '&lt;span class="verse" id="Luke_2_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;Queje xqui ʼan ique ile rumal i tzij-i chi tzʼibtal can chupam u ʼatbal tzij i Kajwal Dios; queje iri cu bij: “Are calax i nabe acʼal, we ala, ʼo u chac ca jach na puʼab i Dios.” Queje ile tzʼibtal canok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.024', 'Rumal ʼuri, xe ʼe chu sujuxic queb palomax o xi n-taj, queb ixcucu, pacha i cu bij u ʼatbal tzij i Kajwal Dios.
', '', '&lt;span class="verse" id="Luke_2_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;Rumal ʼuri, xe ʼe chu sujuxic queb palomax o xi n-taj, queb ixcucu, pacha i cu bij u ʼatbal tzij i Kajwal Dios.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.025', 'ʼO jun mam jekel chila pa Jerusalen, mam Simeon u bi. Ire ʼutz laj mam, lic u yom rib puʼab i Dios. Ire royʼem chic i tobal que i aj Israel winak. I mam-le, lic ʼo u Tewal i Dios ruʼ. ', '', '&lt;span class="verse" id="Luke_2_25"&gt;&lt;strong class="verseno"&gt;25&lt;/strong&gt;&amp;nbsp;ʼO jun mam jekel chila pa Jerusalen, mam Simeon u bi. Ire ʼutz laj mam, lic u yom rib puʼab i Dios. Ire royʼem chic i tobal que i aj Israel winak. I mam-le, lic ʼo u Tewal i Dios ruʼ. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.026', 'Rumal u Tewal i Dios, retaʼam chi n-ca cam ta tan, cʼa cril ni u wach i Tolque, i jun chi u bim i Kajwal Dios cu tak na lok. ', '', '&lt;span class="verse" id="Luke_2_26"&gt;&lt;strong class="verseno"&gt;26&lt;/strong&gt;&amp;nbsp;Rumal u Tewal i Dios, retaʼam chi n-ca cam ta tan, cʼa cril ni u wach i Tolque, i jun chi u bim i Kajwal Dios cu tak na lok. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.027', 'I mam-le xoc pa rachoch i Dios, ucʼam rumal u Tewal i Dios. Are ʼuri i ma Jose i ati Mariy xak qui cʼamom bi i a-Jesus pa rachoch i Dios, chu ʼonquil che pacha i tzʼibtal can chupam u ʼatbal tzij i mam Moises. ', '', '&lt;span class="verse" id="Luke_2_27"&gt;&lt;strong class="verseno"&gt;27&lt;/strong&gt;&amp;nbsp;I mam-le xoc pa rachoch i Dios, ucʼam rumal u Tewal i Dios. Are ʼuri i ma Jose i ati Mariy xak qui cʼamom bi i a-Jesus pa rachoch i Dios, chu ʼonquil che pacha i tzʼibtal can chupam u ʼatbal tzij i mam Moises. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.028', 'Are ʼuri i mam Simeon xu ʼaluj i acʼal pu ʼab, xu cʼamwaj che i Dios, y xu bij:
', '', '&lt;span class="verse" id="Luke_2_28"&gt;&lt;strong class="verseno"&gt;28&lt;/strong&gt;&amp;nbsp;Are ʼuri i mam Simeon xu ʼaluj i acʼal pu ʼab, xu cʼamwaj che i Dios, y xu bij:
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.029', 'Woʼor, Wajwal Dios, xuxlan in cʼux; a ʼanom na, pacha a bim chwe; we cawaj, chin camok.
', '', '&lt;span class="verse" id="Luke_2_29"&gt;&lt;strong class="verseno"&gt;29&lt;/strong&gt;&amp;nbsp;Woʼor, Wajwal Dios, xuxlan in cʼux; a ʼanom na, pacha a bim chwe; we cawaj, chin camok.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.030', 'Xuxlan in cʼux man wilom chic wach i tobal-ke a yijbam.
', '', '&lt;span class="verse" id="Luke_2_30"&gt;&lt;strong class="verseno"&gt;30&lt;/strong&gt;&amp;nbsp;Xuxlan in cʼux man wilom chic wach i tobal-ke a yijbam.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.031', 'Conojel i winak chuwach i jyub taʼaj, a yijbam wach i tobal-que.
', '', '&lt;span class="verse" id="Luke_2_31"&gt;&lt;strong class="verseno"&gt;31&lt;/strong&gt;&amp;nbsp;Conojel i winak chuwach i jyub taʼaj, a yijbam wach i tobal-que.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.032', 'I tobal-que chi a yijbam, queʼel chique i ne te aj Israel winak pacha sak petnak piquiwi.
Y yoj, chi oj aj Israel, oj a winak yet, cocsax ni ka ʼij rumal wach a ʼanom.
(Queje ile xu bij i mam Simeon.)
', '', '&lt;span class="verse" id="Luke_2_32"&gt;&lt;strong class="verseno"&gt;32&lt;/strong&gt;&amp;nbsp;I tobal-que chi a yijbam, queʼel chique i ne te aj Israel winak pacha sak petnak piquiwi.
Y yoj, chi oj aj Israel, oj a winak yet, cocsax ni ka ʼij rumal wach a ʼanom.
(Queje ile xu bij i mam Simeon.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.033', 'I ma Jose, xak i u chuch i a-Jesus, xqui bisoj wach i xqui ta, chi xbix chirij i acʼal. ', '', '&lt;span class="verse" id="Luke_2_33"&gt;&lt;strong class="verseno"&gt;33&lt;/strong&gt;&amp;nbsp;I ma Jose, xak i u chuch i a-Jesus, xqui bisoj wach i xqui ta, chi xbix chirij i acʼal. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.034', 'I mam Simeon xu tzʼonoj i ʼutz re i Dios piquiwi. Are ʼuri xu bij jun tzij che ixok Mariy, chi u chuch i acʼal. Xu bij:
—I acʼal-i, yatal chakaxol man cu ʼalijinsaj na pachin je re i Dios, xak pachin ne te re. Rumal ire, je qʼui ni aj Israel que totaj na, xak je qʼui xa que tzak pa tojpen rumal. I acʼal-i col u cʼutu u choʼab i Dios, tobal na que i winak, xui-ri, je qʼui quiqui xutuj ni tobal-que le. ', '', '&lt;span class="verse" id="Luke_2_34"&gt;&lt;strong class="verseno"&gt;34&lt;/strong&gt;&amp;nbsp;I mam Simeon xu tzʼonoj i ʼutz re i Dios piquiwi. Are ʼuri xu bij jun tzij che ixok Mariy, chi u chuch i acʼal. Xu bij:&lt;p&gt; &lt;/p&gt;—I acʼal-i, yatal chakaxol man cu ʼalijinsaj na pachin je re i Dios, xak pachin ne te re. Rumal ire, je qʼui ni aj Israel que totaj na, xak je qʼui xa que tzak pa tojpen rumal. I acʼal-i col u cʼutu u choʼab i Dios, tobal na que i winak, xui-ri, je qʼui quiqui xutuj ni tobal-que le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.035', 'Queje ile, quiqui ʼalijinsaj na wach i mer qui nojbal. Xui-ri yet, coc i bis pa cʼux pacha chʼichʼ coc ruʼ awanima. (Queje ile xu bij i mam Simeon che.)
', '', '&lt;span class="verse" id="Luke_2_35"&gt;&lt;strong class="verseno"&gt;35&lt;/strong&gt;&amp;nbsp;Queje ile, quiqui ʼalijinsaj na wach i mer qui nojbal. Xui-ri yet, coc i bis pa cʼux pacha chʼichʼ coc ruʼ awanima. (Queje ile xu bij i mam Simeon che.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.036', 'Xak ʼo jun tit ʼo chila, tit An u bi, chi ajbil u tzij i Dios. Ire u miʼal i ma Fanuel, chi u muk u xiquin can i mam Aser. I tit An-le, nim laj winak chic. Are cʼa ali, xcʼuliʼic, y xcucʼaj quib wukub junab ruʼ i rachijil. ', '', '&lt;span class="verse" id="Luke_2_36"&gt;&lt;strong class="verseno"&gt;36&lt;/strong&gt;&amp;nbsp;Xak ʼo jun tit ʼo chila, tit An u bi, chi ajbil u tzij i Dios. Ire u miʼal i ma Fanuel, chi u muk u xiquin can i mam Aser. I tit An-le, nim laj winak chic. Are cʼa ali, xcʼuliʼic, y xcucʼaj quib wukub junab ruʼ i rachijil. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.037', 'Teʼuri, xmalcanij canok, y ʼo chic ochénta y cuátro junab che. Ire n-kas ta xel pa rachoch i Dios, ca tijin chu cojic u ʼij i Dios chi paʼij, chi chaʼab; ʼolic n-cu tij ti u wa xa rumal u tzʼonoxic che i Dios. ', '', '&lt;span class="verse" id="Luke_2_37"&gt;&lt;strong class="verseno"&gt;37&lt;/strong&gt;&amp;nbsp;Teʼuri, xmalcanij canok, y ʼo chic ochénta y cuátro junab che. Ire n-kas ta xel pa rachoch i Dios, ca tijin chu cojic u ʼij i Dios chi paʼij, chi chaʼab; ʼolic n-cu tij ti u wa xa rumal u tzʼonoxic che i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.038', 'Are ʼuri ire-le xtejeb pan cuʼ i u kajaw i u chuch i a-Jesus, y xu tioxij che i Dios rumal i acʼal-le. Teʼuri xu paxsaj u tzijol i acʼal chique conojel niʼpa i aj Israel winak chi coyʼem i quelsaxic piquiʼab i aj Róma.
', '', '&lt;span class="verse" id="Luke_2_38"&gt;&lt;strong class="verseno"&gt;38&lt;/strong&gt;&amp;nbsp;Are ʼuri ire-le xtejeb pan cuʼ i u kajaw i u chuch i a-Jesus, y xu tioxij che i Dios rumal i acʼal-le. Teʼuri xu paxsaj u tzijol i acʼal chique conojel niʼpa i aj Israel winak chi coyʼem i quelsaxic piquiʼab i aj Róma.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.039', 'I ixok Mariy, i ma Jose, are xe utzin chu ʼonquil pacha usucʼ u ʼatbal tzij i Kajwal Dios, xe tzalij ʼuri pa qui tinimit Nazaret, chi pa jyub Galiléa. ', '', '&lt;span class="verse" id="Luke_2_39"&gt;&lt;strong class="verseno"&gt;39&lt;/strong&gt;&amp;nbsp;I ixok Mariy, i ma Jose, are xe utzin chu ʼonquil pacha usucʼ u ʼatbal tzij i Kajwal Dios, xe tzalij ʼuri pa qui tinimit Nazaret, chi pa jyub Galiléa. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.040', 'Chila xqʼuiy wi i acʼal; xqʼuiji u choʼab, xqʼuiji u nojbal, xak i rutzil i Dios lic ʼo puwi.
', '', '&lt;span class="verse" id="Luke_2_40"&gt;&lt;strong class="verseno"&gt;40&lt;/strong&gt;&amp;nbsp;Chila xqʼuiy wi i acʼal; xqʼuiji u choʼab, xqʼuiji u nojbal, xak i rutzil i Dios lic ʼo puwi.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.041', 'I u kajaw i u chuch i a-Jesus, ronojel junab que ʼe pa Jerusalen chi rilic i nimaʼij páscua. ', '', '&lt;h2&gt;Wach xu ʼan i a-Jesus chiquixol i mamʼib pa rachoch i Dios&lt;/h2&gt;&lt;span class="verse" id="Luke_2_41"&gt;&lt;strong class="verseno"&gt;41&lt;/strong&gt;&amp;nbsp;I u kajaw i u chuch i a-Jesus, ronojel junab que ʼe pa Jerusalen chi rilic i nimaʼij páscua. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.042', 'Are ʼo cablajuj junab che i a-Jesus, xe ʼe pa Jerusalen, pacha ʼax quiqui ʼan wi pa nimaʼij. ', '', '&lt;span class="verse" id="Luke_2_42"&gt;&lt;strong class="verseno"&gt;42&lt;/strong&gt;&amp;nbsp;Are ʼo cablajuj junab che i a-Jesus, xe ʼe pa Jerusalen, pacha ʼax quiqui ʼan wi pa nimaʼij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.043', 'Xqʼuis i nimaʼij, xe tzalij lok, xui-ri, xcanaj i a-Jesus pa Jerusalen. I u kajaw u chuch n-xqui na taj we xcanaj canok; ', '', '&lt;span class="verse" id="Luke_2_43"&gt;&lt;strong class="verseno"&gt;43&lt;/strong&gt;&amp;nbsp;Xqʼuis i nimaʼij, xe tzalij lok, xui-ri, xcanaj i a-Jesus pa Jerusalen. I u kajaw u chuch n-xqui na taj we xcanaj canok; &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.044', 'xqui bij ique, xa teren li cuʼ i rachiʼil. Xqui chʼij jun ʼij, toʼ queje ile xqui chʼob ique. Teʼuri xqui jek u tzucuxic chiquixol i rachalal, tak i je rachiʼil, ', '', '&lt;span class="verse" id="Luke_2_44"&gt;&lt;strong class="verseno"&gt;44&lt;/strong&gt;&amp;nbsp;xqui bij ique, xa teren li cuʼ i rachiʼil. Xqui chʼij jun ʼij, toʼ queje ile xqui chʼob ique. Teʼuri xqui jek u tzucuxic chiquixol i rachalal, tak i je rachiʼil, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.045', 'y n-xqui rik taj. Rumal-i xe tzalij chic chu tzucuxic pa Jerusalen. ', '', '&lt;span class="verse" id="Luke_2_45"&gt;&lt;strong class="verseno"&gt;45&lt;/strong&gt;&amp;nbsp;y n-xqui rik taj. Rumal-i xe tzalij chic chu tzucuxic pa Jerusalen. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.046', 'Xicʼaw urox ʼij, teʼuri xqui rik pa rachoch i Dios. Are xqui riko, ire cul cuʼ i je tijonel re i ujer tzij pixab, ca tijin chu tayic qui tzij, xak ʼo i tzij cu tzʼonoj ire chique. ', '', '&lt;span class="verse" id="Luke_2_46"&gt;&lt;strong class="verseno"&gt;46&lt;/strong&gt;&amp;nbsp;Xicʼaw urox ʼij, teʼuri xqui rik pa rachoch i Dios. Are xqui riko, ire cul cuʼ i je tijonel re i ujer tzij pixab, ca tijin chu tayic qui tzij, xak ʼo i tzij cu tzʼonoj ire chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.047', 'Conojel i xqui ta wach i xu bij, lic xqui bisoj u nojbal, wach u tzalixic u wach xoʼon che i qui tzij. ', '', '&lt;span class="verse" id="Luke_2_47"&gt;&lt;strong class="verseno"&gt;47&lt;/strong&gt;&amp;nbsp;Conojel i xqui ta wach i xu bij, lic xqui bisoj u nojbal, wach u tzalixic u wach xoʼon che i qui tzij. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.048', 'I u kajaw i u chuch, are xquil u wach, lic xqui bisoj. I u chuch xu bij che:
—Wacʼal, ¿wuchac queje xa ʼan ile chake? Yin, ruʼ a kajaw, lic xoj cʼachir cha tzucuxic —xu bij.
', '', '&lt;span class="verse" id="Luke_2_48"&gt;&lt;strong class="verseno"&gt;48&lt;/strong&gt;&amp;nbsp;I u kajaw i u chuch, are xquil u wach, lic xqui bisoj. I u chuch xu bij che:&lt;p&gt; &lt;/p&gt;—Wacʼal, ¿wuchac queje xa ʼan ile chake? Yin, ruʼ a kajaw, lic xoj cʼachir cha tzucuxic —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.049', '—¿Wuchac xa ipa xin i tzucuj wi? —xu bij i a-Jesus chique. ¿N-iwetaʼam ta ba ri, chi yin ʼo i quin ʼan pa rachoch in Kajaw? —xcha.
', '', '&lt;span class="verse" id="Luke_2_49"&gt;&lt;strong class="verseno"&gt;49&lt;/strong&gt;&amp;nbsp;—¿Wuchac xa ipa xin i tzucuj wi? —xu bij i a-Jesus chique. ¿N-iwetaʼam ta ba ri, chi yin ʼo i quin ʼan pa rachoch in Kajaw? —xcha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.050', 'Ique n-xqui ta ti usucʼ wach i xu bij chique. ', '', '&lt;span class="verse" id="Luke_2_50"&gt;&lt;strong class="verseno"&gt;50&lt;/strong&gt;&amp;nbsp;Ique n-xqui ta ti usucʼ wach i xu bij chique. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.051', 'Teʼuri i a-Jesus xa ʼe cuʼ pa tinimit Nazaret; chila xcojon chique pacha ʼax coʼon wi. I u chuch xu cʼol pu cʼux ronojel wach i ʼantalic. ', '', '&lt;span class="verse" id="Luke_2_51"&gt;&lt;strong class="verseno"&gt;51&lt;/strong&gt;&amp;nbsp;Teʼuri i a-Jesus xa ʼe cuʼ pa tinimit Nazaret; chila xcojon chique pacha ʼax coʼon wi. I u chuch xu cʼol pu cʼux ronojel wach i ʼantalic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '2.052', 'I a-Jesus ca tijin u qʼuiyic, xak ca tijin u qʼuiyic u nojbal; wach i xu ʼano, i Dios ʼutz xrilo; xak i winak, ʼutz xquilo.
', '', '&lt;span class="verse" id="Luke_2_52"&gt;&lt;strong class="verseno"&gt;52&lt;/strong&gt;&amp;nbsp;I a-Jesus ca tijin u qʼuiyic, xak ca tijin u qʼuiyic u nojbal; wach i xu ʼano, i Dios ʼutz xrilo; xak i winak, ʼutz xquilo.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.001', 'Xoʼon panok, i Dios xu ʼijla i ma Wan, chi u cʼojol i mam Zacarías. Are xu ʼan i Dios ile, are i ma Tibério ʼatz laj ʼatol tzij, ʼo chic jolajuj junab che u ʼatbal tzij. Xak i ma Póncio Piláto are i gobernador re i jyub Judéa. Xak che i junab-le, are i ma Heródes are i cajʼatzil i aj Galiléa; xak i u chaʼ chi ma Felípe, are i cajʼatzil i aj Ituréa xak i aj Traconíte; xak i ma Lisánias are i cajʼatzil i aj Abilínia. Xak che i junab-le, i mam Anas, i mam Caifas, che queb, je ʼatz laj sacerdóte.
Chupam i junab-le, i Dios xu ʼijla i ma Wan, chi ʼo pa jyub tzʼinilic. ', '', '&lt;h2&gt;Wach u pixbanic i ma Wan chique i winak&lt;/h2&gt;&lt;span class="verse" id="Luke_3_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;Xoʼon panok, i Dios xu ʼijla i ma Wan, chi u cʼojol i mam Zacarías. Are xu ʼan i Dios ile, are i ma Tibério ʼatz laj ʼatol tzij, ʼo chic jolajuj junab che u ʼatbal tzij. Xak i ma Póncio Piláto are i gobernador re i jyub Judéa. Xak che i junab-le, are i ma Heródes are i cajʼatzil i aj Galiléa; xak i u chaʼ chi ma Felípe, are i cajʼatzil i aj Ituréa xak i aj Traconíte; xak i ma Lisánias are i cajʼatzil i aj Abilínia. Xak che i junab-le, i mam Anas, i mam Caifas, che queb, je ʼatz laj sacerdóte.&lt;p&gt; &lt;/p&gt;Chupam i junab-le, i Dios xu ʼijla i ma Wan, chi ʼo pa jyub tzʼinilic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.003', 'Xu tak bi che i jyub chu chiʼ i ya Jordan. I ma Wan, are xupon chila, xoc chu bixquil chique i winak chi ʼo u chac quiqui jalwachij qui noʼoj chi n-ʼus taj chuwach i Dios. Teʼuri, ca kajsax ni ya piquiwi, cʼutbal-re chi qui jalwachim qui noʼoj chuwach i Dios. Queje u rikic ile i cuybal qui mac, xu bij i ma Wan chique i winak. ', '', '&lt;span class="verse" id="Luke_3_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Xu tak bi che i jyub chu chiʼ i ya Jordan. I ma Wan, are xupon chila, xoc chu bixquil chique i winak chi ʼo u chac quiqui jalwachij qui noʼoj chi n-ʼus taj chuwach i Dios. Teʼuri, ca kajsax ni ya piquiwi, cʼutbal-re chi qui jalwachim qui noʼoj chuwach i Dios. Queje u rikic ile i cuybal qui mac, xu bij i ma Wan chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.004', 'Xeʼelok pacha u bim can i ajbil u tzij i Dios, mam Isaías, chupam u wuj ujer. Ire u tzʼibam can i tzij-i:
Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.
', '', '&lt;span class="verse" id="Luke_3_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;Xeʼelok pacha u bim can i ajbil u tzij i Dios, mam Isaías, chupam u wuj ujer. Ire u tzʼibam can i tzij-i:
Ca tataj u chʼawbal jun ca siqʼuin che i jyub tzʼinilic; cu bij:
“¡Ya ca pe i Kajwal! Chi yijba u be chuwach.
Chi sucʼba niʼpa i jul, niʼpa i tanalak pu be i Kajwal.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.005', 'Niʼpa u xol tak jyub, ca nojsaxic;
niʼpa i u tzam tak jyub, ca kajsaxic; xak i ral tak jyub ca kajsax na.
Niʼpa i be cotcak, ca sucʼbax na;
niʼpa i be tanalak, ca jos na.
', '', '&lt;span class="verse" id="Luke_3_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Niʼpa u xol tak jyub, ca nojsaxic;
niʼpa i u tzam tak jyub, ca kajsaxic; xak i ral tak jyub ca kajsax na.
Niʼpa i be cotcak, ca sucʼbax na;
niʼpa i be tanalak, ca jos na.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.006', 'Teʼuri conojel i winak ʼis quiquil na wach i tobal-que u yijbam i Dios.”
(Are i tzij ile u tzʼibam can i mam Isaías ujer, y queje ile xeʼelok.)
', '', '&lt;span class="verse" id="Luke_3_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Teʼuri conojel i winak ʼis quiquil na wach i tobal-que u yijbam i Dios.”
(Are i tzij ile u tzʼibam can i mam Isaías ujer, y queje ile xeʼelok.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.007', 'Je qʼui i winak xe cʼun ruʼ i ma Wan, quiqui tzʼonoj u kajsaxic ya piquiwi. Ire xu bij chique:
—¡Yix xa ix racʼal cumatz! ¿Pachin xbin chiwe chi quix totaj chuwach i tojpen chi ca tijin nali piwi? ', '', '&lt;span class="verse" id="Luke_3_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;Je qʼui i winak xe cʼun ruʼ i ma Wan, quiqui tzʼonoj u kajsaxic ya piquiwi. Ire xu bij chique:&lt;p&gt; &lt;/p&gt;—¡Yix xa ix racʼal cumatz! ¿Pachin xbin chiwe chi quix totaj chuwach i tojpen chi ca tijin nali piwi? &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.008', 'N-ʼus taj we toʼ ca kajsax ya piwi. Chi ʼana i ʼutz, i chom, ʼalijinsabal-re we i jalwachim chic i nojbal chi n-ʼus taj. Mi chʼob yix pi jolom, “Lic oj ʼutz man oj u muk u xiquin can i mam Abraham,” quix cha. Ile n-ta coʼono. Chiwila i abaj-i; i Dios, we ta craj, ca tiqui chu yijbaxic je qʼui u muk u xiquin i mam Abraham che. N-are ti rumal i mam, mam Abraham, quix totajic. ', '', '&lt;span class="verse" id="Luke_3_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;N-ʼus taj we toʼ ca kajsax ya piwi. Chi ʼana i ʼutz, i chom, ʼalijinsabal-re we i jalwachim chic i nojbal chi n-ʼus taj. Mi chʼob yix pi jolom, “Lic oj ʼutz man oj u muk u xiquin can i mam Abraham,” quix cha. Ile n-ta coʼono. Chiwila i abaj-i; i Dios, we ta craj, ca tiqui chu yijbaxic je qʼui u muk u xiquin i mam Abraham che. N-are ti rumal i mam, mam Abraham, quix totajic. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.009', 'Chitapeʼ, i Dios yij chic chu cojic pa tojpen niʼpa i u muk u xiquin i mam Abraham chi n-ʼus ti quiqui ʼano. Lic yij chic chu petsaxic i tojpen-le pacha cu ʼan jun achi yij chic ruʼ i ricaj cʼatzbal-re jun cheʼ. Niʼpa i cheʼ chi n-cu ya ti ʼutz laj echaʼ, ca cʼatzix na; teʼuri cocsax pa ʼaʼ. (Queje ile xu bij i ma Wan chique i winak.)
', '', '&lt;span class="verse" id="Luke_3_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Chitapeʼ, i Dios yij chic chu cojic pa tojpen niʼpa i u muk u xiquin i mam Abraham chi n-ʼus ti quiqui ʼano. Lic yij chic chu petsaxic i tojpen-le pacha cu ʼan jun achi yij chic ruʼ i ricaj cʼatzbal-re jun cheʼ. Niʼpa i cheʼ chi n-cu ya ti ʼutz laj echaʼ, ca cʼatzix na; teʼuri cocsax pa ʼaʼ. (Queje ile xu bij i ma Wan chique i winak.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.010', 'Are ʼuri i winak xqui tzʼonoj che:
—¿Wach caka ʼan ʼut? —xe cha.
', '', '&lt;span class="verse" id="Luke_3_10"&gt;&lt;strong class="verseno"&gt;10&lt;/strong&gt;&amp;nbsp;Are ʼuri i winak xqui tzʼonoj che:&lt;p&gt; &lt;/p&gt;—¿Wach caka ʼan ʼut? —xe cha.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.011', 'Xu bij chique:
—We ʼo queb a camix, cha sipaj jun re i n-tu camix. We ʼo ca tijo, cha ya re i n-ti cu tijo —xu bij chique.
', '', '&lt;span class="verse" id="Luke_3_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Xu bij chique:&lt;p&gt; &lt;/p&gt;—We ʼo queb a camix, cha sipaj jun re i n-tu camix. We ʼo ca tijo, cha ya re i n-ti cu tijo —xu bij chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.012', 'Xak ʼo i aj tzʼonol alcawal xe cʼun ruʼ i ma Wan, quiqui tzʼonoj u kajsaxic ya piquiwi. Xqui bij che:
—At tijonel, cha bij chake wach nawi caka ʼan yoj —xe cha che.
', '', '&lt;span class="verse" id="Luke_3_12"&gt;&lt;strong class="verseno"&gt;12&lt;/strong&gt;&amp;nbsp;Xak ʼo i aj tzʼonol alcawal xe cʼun ruʼ i ma Wan, quiqui tzʼonoj u kajsaxic ya piquiwi. Xqui bij che:&lt;p&gt; &lt;/p&gt;—At tijonel, cha bij chake wach nawi caka ʼan yoj —xe cha che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.013', 'Ire xu bij chique:
—Mi tzʼonoj mas mer chique i winak, xui chi tzʼonoj chique pacha cu bij i ʼatbal tzij —xu bij.
', '', '&lt;span class="verse" id="Luke_3_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;Ire xu bij chique:&lt;p&gt; &lt;/p&gt;—Mi tzʼonoj mas mer chique i winak, xui chi tzʼonoj chique pacha cu bij i ʼatbal tzij —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.014', 'Xak ʼo i soldádo xe cʼun ruʼ, quiqui tzʼonoj che.
—¿Wach caka ʼan yoj, oj soldádo? —xqui bij.
—Me i xibij i winak —xu bij chique— xak mi maj qui mer; mi coj qui mac we n-ti qui mac; chix quicot ruʼ iwajil chi qui chʼaco; mi rayij mas. (Queje xu bij i ma Wan ile chique.)
', '', '&lt;span class="verse" id="Luke_3_14"&gt;&lt;strong class="verseno"&gt;14&lt;/strong&gt;&amp;nbsp;Xak ʼo i soldádo xe cʼun ruʼ, quiqui tzʼonoj che.&lt;p&gt; &lt;/p&gt;—¿Wach caka ʼan yoj, oj soldádo? —xqui bij.&lt;p&gt; &lt;/p&gt;—Me i xibij i winak —xu bij chique— xak mi maj qui mer; mi coj qui mac we n-ti qui mac; chix quicot ruʼ iwajil chi qui chʼaco; mi rayij mas. (Queje xu bij i ma Wan ile chique.)&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.015', 'I aj Israel winak, ʼis coyʼem chic u cʼunic i Tolque; xqui jek u chʼobic piqui cʼux chi i ma Wan mok are i Tolque-le. ', '', '&lt;span class="verse" id="Luke_3_15"&gt;&lt;strong class="verseno"&gt;15&lt;/strong&gt;&amp;nbsp;I aj Israel winak, ʼis coyʼem chic u cʼunic i Tolque; xqui jek u chʼobic piqui cʼux chi i ma Wan mok are i Tolque-le. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.016', 'Ire xu bij ʼuri chique conojel:
—Yin n-in ti Toliwe chi iwoyʼem. Yin, katzij quin kajsaj ya piwi, xui-ri, i jun chi ca cʼun chwijil yin cu kajsaj li u Tewal i Dios piwi; xak cu kajsaj li ʼaʼ piquiwi i je mal. Ire mas nim ni u ʼij chinwach yin; n-ca majaw ta chwe quin ʼan mocom che wib chirij, mi tene quin tor u wach u xajab, rumal ni u ʼij nim. ', '', '&lt;span class="verse" id="Luke_3_16"&gt;&lt;strong class="verseno"&gt;16&lt;/strong&gt;&amp;nbsp;Ire xu bij ʼuri chique conojel:&lt;p&gt; &lt;/p&gt;—Yin n-in ti Toliwe chi iwoyʼem. Yin, katzij quin kajsaj ya piwi, xui-ri, i jun chi ca cʼun chwijil yin cu kajsaj li u Tewal i Dios piwi; xak cu kajsaj li ʼaʼ piquiwi i je mal. Ire mas nim ni u ʼij chinwach yin; n-ca majaw ta chwe quin ʼan mocom che wib chirij, mi tene quin tor u wach u xajab, rumal ni u ʼij nim. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.017', 'Col u ʼata tzij piquiwi i winak, pachin i je ʼutz, xak pachin i ne te ʼutz. Ire yij chic pacha coʼon jun ruʼ u pubal u wach trígo. Are i ʼutz quel che u trígo, cu molo y cu ya pu cʼolbal. I rij, chi n-ʼus taj, cu poroj pa ʼaʼ chi n-tu chupic —xu bij i ma Wan chique.
', '', '&lt;span class="verse" id="Luke_3_17"&gt;&lt;strong class="verseno"&gt;17&lt;/strong&gt;&amp;nbsp;Col u ʼata tzij piquiwi i winak, pachin i je ʼutz, xak pachin i ne te ʼutz. Ire yij chic pacha coʼon jun ruʼ u pubal u wach trígo. Are i ʼutz quel che u trígo, cu molo y cu ya pu cʼolbal. I rij, chi n-ʼus taj, cu poroj pa ʼaʼ chi n-tu chupic —xu bij i ma Wan chique.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.018', 'Ruʼ tak i tzij-le, xe u pixbaj i winak, xak ruʼ juban chic pixbanic. Queje xoʼon ire ile, are ca tijin chu bixquil i ʼutz laj tzij re i Dios chique i winak. ', '', '&lt;span class="verse" id="Luke_3_18"&gt;&lt;strong class="verseno"&gt;18&lt;/strong&gt;&amp;nbsp;Ruʼ tak i tzij-le, xe u pixbaj i winak, xak ruʼ juban chic pixbanic. Queje xoʼon ire ile, are ca tijin chu bixquil i ʼutz laj tzij re i Dios chique i winak. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.019', 'Xui-ri, i ma Heródes, chi cajʼatzil i aj Galiléa, xu tzʼapij i ma Wan pa cárcel. Xu tzʼapij, man i ma Wan xu bij che chi n-ʼus ti ca tijin chu ʼanic. Ire u majom i ati Erodiys, chi rixokil u chaʼ, chi ma Felípe; xak ʼo juban chic u mac u ʼanom. ', '', '&lt;span class="verse" id="Luke_3_19"&gt;&lt;strong class="verseno"&gt;19&lt;/strong&gt;&amp;nbsp;Xui-ri, i ma Heródes, chi cajʼatzil i aj Galiléa, xu tzʼapij i ma Wan pa cárcel. Xu tzʼapij, man i ma Wan xu bij che chi n-ʼus ti ca tijin chu ʼanic. Ire u majom i ati Erodiys, chi rixokil u chaʼ, chi ma Felípe; xak ʼo juban chic u mac u ʼanom. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.020', 'Are xyaj rumal i ma Wan, xpe roywal; rumal-i xu min pa cárcel. Are jun chic u mac-i xu ʼano.
', '', '&lt;span class="verse" id="Luke_3_20"&gt;&lt;strong class="verseno"&gt;20&lt;/strong&gt;&amp;nbsp;Are xyaj rumal i ma Wan, xpe roywal; rumal-i xu min pa cárcel. Are jun chic u mac-i xu ʼano.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.021', 'I Jesus, are xkajsax ya piquiwi conojel i winak, xak xkajsax ya puwi ire. Are ʼuri, are ca tijin chu tzʼonoxic che i Dios, xjaktaj i caj, ', '', '&lt;h2&gt;Xkajsax ya puwi i Jesus&lt;/h2&gt;&lt;span class="verse" id="Luke_3_21"&gt;&lt;strong class="verseno"&gt;21&lt;/strong&gt;&amp;nbsp;I Jesus, are xkajsax ya piquiwi conojel i winak, xak xkajsax ya puwi ire. Are ʼuri, are ca tijin chu tzʼonoxic che i Dios, xjaktaj i caj, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.022', 'y xkaj li u Tewal i Dios puwi, pacha u qʼuexwach palomax. Xtataj jun chʼawbal chicaj chi xu bij:
—Yet lic at in Cʼojol; cʼax cat in naʼo. Lic quin quicot awumal —xcha.
', '', '&lt;span class="verse" id="Luke_3_22"&gt;&lt;strong class="verseno"&gt;22&lt;/strong&gt;&amp;nbsp;y xkaj li u Tewal i Dios puwi, pacha u qʼuexwach palomax. Xtataj jun chʼawbal chicaj chi xu bij:&lt;p&gt; &lt;/p&gt;—Yet lic at in Cʼojol; cʼax cat in naʼo. Lic quin quicot awumal —xcha.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.023', 'I Jesus, are xu jek u chac, colo ʼo chic tréinta junab che. Ire, are u cʼojol i ma Jose, quiqui bij i winak. I ma Jose-le, are u cʼojol i ma Eli.
', '', '&lt;h2&gt;I qui bi i ratit u mam i Jesus&lt;/h2&gt;&lt;span class="verse" id="Luke_3_23"&gt;&lt;strong class="verseno"&gt;23&lt;/strong&gt;&amp;nbsp;I Jesus, are xu jek u chac, colo ʼo chic tréinta junab che. Ire, are u cʼojol i ma Jose, quiqui bij i winak. I ma Jose-le, are u cʼojol i ma Eli.
&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '3.024', 'I ma Eli are u cʼojol i ma Matat.
I ma Matat are u cʼojol i ma Levi.
I ma Levi are u cʼojol i ma Mélqui.
I ma Mélqui are u cʼojol i ma Jána.
I ma Jána are u cʼojol i ma Jose.
', '', '&lt;span class="verse" id="Luke_3_24"&gt;&lt;strong class="verseno"&gt;24&lt;/strong&gt;&amp;nbsp;I ma Eli are u cʼojol i ma Matat.
I ma Matat are u cʼojol i ma Levi.
