INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.001', 'I Jesus xel chila che i ya Jordan, lic ʼo u Tewal i Dios ruʼ. Teʼuri xcʼam bi che jun jyub tzʼinilic rumal u Tewal i Dios. ', '', '&lt;h2&gt;I itzel craj cu cʼam u pam i Jesus&lt;/h2&gt;&lt;span class="verse" id="Luke_4_1"&gt;&lt;strong class="verseno"&gt;1&lt;/strong&gt;&amp;nbsp;I Jesus xel chila che i ya Jordan, lic ʼo u Tewal i Dios ruʼ. Teʼuri xcʼam bi che jun jyub tzʼinilic rumal u Tewal i Dios. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.002', 'Chila xu chʼij cuarénta ʼij, lic n-ta xu tijo. Are ʼo chila, xupon itzel ruʼ; xraj ire cu ʼan che i Jesus chi cu ʼan jun u mac. Are xicʼaw i cuarénta ʼij, xpe i numic che. ', '', '&lt;span class="verse" id="Luke_4_2"&gt;&lt;strong class="verseno"&gt;2&lt;/strong&gt;&amp;nbsp;Chila xu chʼij cuarénta ʼij, lic n-ta xu tijo. Are ʼo chila, xupon itzel ruʼ; xraj ire cu ʼan che i Jesus chi cu ʼan jun u mac. Are xicʼaw i cuarénta ʼij, xpe i numic che. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.003', 'Are ʼuri, i itzel xu bij che:
—We yet at u Cʼojol i Dios, cha bij che i abaj-i coʼon wa che rib —xu bij.
', '', '&lt;span class="verse" id="Luke_4_3"&gt;&lt;strong class="verseno"&gt;3&lt;/strong&gt;&amp;nbsp;Are ʼuri, i itzel xu bij che:&lt;p&gt; &lt;/p&gt;—We yet at u Cʼojol i Dios, cha bij che i abaj-i coʼon wa che rib —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.004', 'I Jesus xu laʼ u wach che:
—N-quin ʼan taj —xu bij—. Man chupam u wuj i Dios, queje iri tzʼibtalic: “N-xui ta rumal i wa je cʼas i winak. Xui-ri are ronojel u tzij i Dios je cʼasbawnak” —xu bij i Jesus.
', '', '&lt;span class="verse" id="Luke_4_4"&gt;&lt;strong class="verseno"&gt;4&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ u wach che:&lt;p&gt; &lt;/p&gt;—N-quin ʼan taj —xu bij—. Man chupam u wuj i Dios, queje iri tzʼibtalic: “N-xui ta rumal i wa je cʼas i winak. Xui-ri are ronojel u tzij i Dios je cʼasbawnak” —xu bij i Jesus.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.005', 'Teʼuri i itzel xu cʼam bi che jun nim laj jyub. Juntir xu cʼut che i Jesus ronojel i nimak tak tinimit ʼo chuwach i jyub taʼaj. ', '', '&lt;span class="verse" id="Luke_4_5"&gt;&lt;strong class="verseno"&gt;5&lt;/strong&gt;&amp;nbsp;Teʼuri i itzel xu cʼam bi che jun nim laj jyub. Juntir xu cʼut che i Jesus ronojel i nimak tak tinimit ʼo chuwach i jyub taʼaj. &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.006', 'Teʼuri xu bij chic che:
—Ronojel iri ʼis ʼo pinʼab yin. Xak ʼo pinʼab quin sipaj che, pachin che cwaj yin. Ronojel iri, ruʼ u chomal, quin sipaj chawe, ', '', '&lt;span class="verse" id="Luke_4_6"&gt;&lt;strong class="verseno"&gt;6&lt;/strong&gt;&amp;nbsp;Teʼuri xu bij chic che:&lt;p&gt; &lt;/p&gt;—Ronojel iri ʼis ʼo pinʼab yin. Xak ʼo pinʼab quin sipaj che, pachin che cwaj yin. Ronojel iri, ruʼ u chomal, quin sipaj chawe, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.007', 'xui-ri we yet cat xuqui chinwach, y ca coj in ʼij —xu bij itzel che.
', '', '&lt;span class="verse" id="Luke_4_7"&gt;&lt;strong class="verseno"&gt;7&lt;/strong&gt;&amp;nbsp;xui-ri we yet cat xuqui chinwach, y ca coj in ʼij —xu bij itzel che.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.008', 'I Jesus xu laʼ chic u wach che:
—Chat el chinwach, a-Satanas, man chupam u wuj i Dios, queje iri tzʼibtalic: “Xui che Awajwal Dios cha cojo u ʼij; xui che ire ca ʼan wi a patan,” queje ile cu bij —xu bij.
', '', '&lt;span class="verse" id="Luke_4_8"&gt;&lt;strong class="verseno"&gt;8&lt;/strong&gt;&amp;nbsp;I Jesus xu laʼ chic u wach che:&lt;p&gt; &lt;/p&gt;—Chat el chinwach, a-Satanas, man chupam u wuj i Dios, queje iri tzʼibtalic: “Xui che Awajwal Dios cha cojo u ʼij; xui che ire ca ʼan wi a patan,” queje ile cu bij —xu bij.&lt;p&gt; &lt;/p&gt;&lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.009', 'Teʼuri i itzel xu cʼam bi i Jesus pa Jerusalen. Xe u ya chila cʼa chu tzam i rachoch i Dios. Teʼuri xu bij che:
—We yet at u Cʼojol i Dios, cha cʼaka bi awib cʼa pulew. N-cat cam taj, ', '', '&lt;span class="verse" id="Luke_4_9"&gt;&lt;strong class="verseno"&gt;9&lt;/strong&gt;&amp;nbsp;Teʼuri i itzel xu cʼam bi i Jesus pa Jerusalen. Xe u ya chila cʼa chu tzam i rachoch i Dios. Teʼuri xu bij che:&lt;p&gt; &lt;/p&gt;—We yet at u Cʼojol i Dios, cha cʼaka bi awib cʼa pulew. N-cat cam taj, &lt;/span&gt;');
INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.010', 'man chupam u wuj i Dios, queje iri tzʼibtalic:
