INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.011', 'Ique lic cat cucʼaj che qui ʼab;
mi tene quiqui ya chawe que ʼana cʼax che awakan che abaj, cu bij.
(Queje ile xu bij itzel che.)
', '', '&lt;span class="verse" id="Luke_4_11"&gt;&lt;strong class="verseno"&gt;11&lt;/strong&gt;&amp;nbsp;Ique lic cat cucʼaj che qui ʼab;
