INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.012', 'I Jesus xu laʼ chic u wach che:
—Xak tzʼibtal i tzij-i chupam u wuj i Dios: “Ma cʼam u pam Awajwal Dios,” cu bij. Rumal-i, n-quin ʼan taj —xu bij che.
