INSERT INTO "reader_bible_versedata" ("version", "book", "verse", "unformatted", "idxfti", "basichtml") VALUES ('x-acc', 'Luke', '4.013', 'I itzel, ya n-cu rik ta chic wach cu tʼorbej re i Jesus, are ʼuri, xel jun tiémpo chuwach.
', '', '&lt;span class="verse" id="Luke_4_13"&gt;&lt;strong class="verseno"&gt;13&lt;/strong&gt;&amp;nbsp;I itzel, ya n-cu rik ta chic wach cu tʼorbej re i Jesus, are ʼuri, xel jun tiémpo chuwach.
